INSERT INTO `t_poem` VALUES ('1', 'poem', '唐', '李白', '黄鹤楼送孟浩然之广陵', '故人西辞黄鹤楼，烟花三月下扬州。\\n孤帆远影碧空尽，唯见长江天际流。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('2', 'poem', '唐', '李白', '将进酒·君不见', '君不见，黄河之水天上来，奔流到海不复回。\\n君不见，高堂明镜悲白发，朝如青丝暮成雪。\\n人生得意须尽欢，莫使金樽空对月。\\n天生我材必有用，千金散尽还复来。\\n烹羊宰牛且为乐，会须一饮三百杯。\\n岑夫子，丹丘生，将进酒，杯莫停。\\n与君歌一曲，请君为我倾耳听。\\n钟鼓馔玉不足贵，但愿长醉不复醒。\\n古来圣贤皆寂寞，惟有饮者留其名。\\n陈王昔时宴平乐，斗酒十千恣欢谑。\\n主人何为言少钱，径须沽取对君酌。\\n五花马，千金裘，呼儿将出换美酒，与尔同销万古愁。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('3', 'poem', '唐', '王维', '渭城曲 / 送元二使安西', '渭城朝雨浥轻尘，客舍青青柳色新。\\n劝君更尽一杯酒，西出阳关无故人。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('4', 'poem', '唐', '贺知章', '咏柳', '碧玉妆成一树高，万条垂下绿丝绦。\\n不知细叶谁裁出，二月春风似剪刀。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('5', 'poem', '唐', '杜甫', '春望', '国破山河在，城春草木深。\\n感时花溅泪，恨别鸟惊心。\\n烽火连三月，家书抵万金。\\n白头搔更短，浑欲不胜簪。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('6', 'poem', '唐', '李白', '望庐山瀑布', '日照香炉生紫烟，遥看瀑布挂前川。\\n飞流直下三千尺，疑是银河落九天。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('7', 'poem', '唐', '杜牧', '清明', '清明时节雨纷纷，路上行人欲断魂。\\n借问酒家何处有？牧童遥指杏花村。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('8', 'poem', '唐', '李白', '静夜思', '床前明月光，疑是地上霜。\\n举头望明月，低头思故乡。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('9', 'poem', '唐', '李绅', '古风二首 / 悯农二首', '春种一粒粟，秋收万颗子。\\n四海无闲田，农夫犹饿死。\\n锄禾日当午，汗滴禾下土。\\n谁知盘中餐，粒粒皆辛苦。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('10', 'poem', '唐', '杜甫', '春夜喜雨', '好雨知时节，当春乃发生。\\n随风潜入夜，润物细无声。\\n野径云俱黑，江船火独明。\\n晓看红湿处，花重锦官城。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('11', 'poem', '唐', '王昌龄', '芙蓉楼送辛渐', '寒雨连江夜入吴，平明送客楚山孤。\\n洛阳亲友如相问，一片冰心在玉壶。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('12', 'poem', '唐', '杜甫', '绝句', '两个黄鹂鸣翠柳，一行白鹭上青天。\\n窗含西岭千秋雪，门泊东吴万里船。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('13', 'poem', '唐', '王维', '九月九日忆山东兄弟', '独在异乡为异客，每逢佳节倍思亲。\\n遥知兄弟登高处，遍插茱萸少一人。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('14', 'poem', '唐', '柳宗元', '江雪', '千山鸟飞绝，万径人踪灭。\\n孤舟蓑笠翁，独钓寒江雪。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('15', 'poem', '唐', '李商隐', '夜雨寄北', '君问归期未有期，巴山夜雨涨秋池。\\n何当共剪西窗烛，却话巴山夜雨时。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('16', 'poem', '唐', '李白', '早发白帝城 / 白帝下江陵', '朝辞白帝彩云间，千里江陵一日还。\\n两岸猿声啼不住，轻舟已过万重山。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('17', 'poem', '唐', '张继', '枫桥夜泊 / 夜泊枫江', '月落乌啼霜满天，江枫渔火对愁眠。\\n姑苏城外寒山寺，夜半钟声到客船。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('18', 'poem', '唐', '崔颢', '黄鹤楼', '昔人已乘黄鹤去，此地空余黄鹤楼。\\n黄鹤一去不复返，白云千载空悠悠。\\n晴川历历汉阳树，芳草萋萋鹦鹉洲。\\n日暮乡关何处是？烟波江上使人愁。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('19', 'poem', '唐', '王之涣', '登鹳雀楼', '白日依山尽，黄河入海流。\\n欲穷千里目，更上一层楼。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('20', 'poem', '唐', '李商隐', '无题·相见时难别亦难', '相见时难别亦难，东风无力百花残。\\n春蚕到死丝方尽，蜡炬成灰泪始干。\\n晓镜但愁云鬓改，夜吟应觉月光寒。\\n蓬山此去无多路，青鸟殷勤为探看。', '2018-03-09 14:55:33');
INSERT INTO `t_poem` VALUES ('21', 'poem', '唐', '韩愈', '早春呈水部张十八员外（其一）', '天街小雨润如酥，草色遥看近却无。\\n最是一年春好处，绝胜烟柳满皇都。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('22', 'poem', '唐', '孟浩然', '春晓', '春眠不觉晓，处处闻啼鸟。\\n夜来风雨声，花落知多少。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('23', 'poem', '唐', '杜牧', '山行', '远上寒山石径斜，白云生处有人家。\\n停车坐爱枫林晚，霜叶红于二月花。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('24', 'poem', '唐', '王昌龄', '出塞二首·其一', '秦时明月汉时关，万里长征人未还。\\n但使龙城飞将在，不教胡马度阴山。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('25', 'poem', '唐', '李白', '赠汪伦', '李白乘舟将欲行，忽闻岸上踏歌声。\\n桃花潭水深千尺，不及汪伦送我情。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('26', 'poem', '唐', '白居易', '钱塘湖春行', '孤山寺北贾亭西，水面初平云脚低。\\n几处早莺争暖树，谁家新燕啄春泥。\\n乱花渐欲迷人眼，浅草才能没马蹄。\\n最爱湖东行不足，绿杨阴里白沙堤。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('27', 'poem', '唐', '杜牧', '泊秦淮', '烟笼寒水月笼沙，夜泊秦淮近酒家。\\n商女不知亡国恨，隔江犹唱后庭花。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('28', 'poem', '唐', '李商隐', '锦瑟', '锦瑟无端五十弦，一弦一柱思华年。\\n庄生晓梦迷蝴蝶，望帝春心托杜鹃。\\n沧海月明珠有泪，蓝田日暖玉生烟。\\n此情可待成追忆？只是当时已惘然。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('29', 'poem', '唐', '李商隐', '嫦娥', '云母屏风烛影深，长河渐落晓星沉。\\n嫦娥应悔偷灵药，碧海青天夜夜心。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('30', 'poem', '唐', '韦应物', '滁州西涧', '独怜幽草涧边生，上有黄鹂深树鸣。\\n春潮带雨晚来急，野渡无人舟自横。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('31', 'poem', '唐', '王维', '山居秋暝', '空山新雨后，天气晚来秋。\\n明月松间照，清泉石上流。\\n竹喧归浣女，莲动下渔舟。\\n随意春芳歇，王孙自可留。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('32', 'poem', '唐', '贺知章', '回乡偶书二首·其一', '少小离家老大回，乡音无改鬓毛衰。\\n儿童相见不相识，笑问客从何处来。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('33', 'poem', '唐', '杜甫', '望岳', '岱宗夫如何？齐鲁青未了。\\n造化钟神秀，阴阳割昏晓。\\n荡胸生层云，决眦入归鸟。\\n会当凌绝顶，一览众山小。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('34', 'poem', '唐', '王翰', '凉州词二首·其一', '葡萄美酒夜光杯，欲饮琵琶马上催。\\n醉卧沙场君莫笑，古来征战几人回？', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('35', 'poem', '唐', '王勃', '送杜少府之任蜀州', '城阙辅三秦，风烟望五津。\\n与君离别意，同是宦游人。\\n海内存知己，天涯若比邻。\\n无为在歧路，儿女共沾巾。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('36', 'poem', '唐', '杜牧', '江南春·千里莺啼绿映红', '千里莺啼绿映红，水村山郭酒旗风。\\n南朝四百八十寺，多少楼台烟雨中。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('37', 'poem', '唐', '岑参', '白雪歌送武判官归京', '北风卷地白草折，胡天八月即飞雪。\\n忽如一夜春风来，千树万树梨花开。\\n散入珠帘湿罗幕，狐裘不暖锦衾薄。\\n将军角弓不得控，都护铁衣冷难着。\\n瀚海阑干百丈冰，愁云惨淡万里凝。\\n中军置酒饮归客，胡琴琵琶与羌笛。\\n纷纷暮雪下辕门，风掣红旗冻不翻。\\n轮台东门送君去，去时雪满天山路。\\n山回路转不见君，雪上空留马行处。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('38', 'poem', '唐', '贾岛', '寻隐者不遇 / 孙革访羊尊师诗', '松下问童子，言师采药去。\\n只在此山中，云深不知处。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('39', 'poem', '唐', '王维', '相思', '红豆生南国，春来发几枝。\\n愿君多采撷，此物最相思。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('40', 'poem', '唐', '孟浩然', '过故人庄', '故人具鸡黍，邀我至田家。\\n绿树村边合，青山郭外斜。\\n开轩面场圃，把酒话桑麻。\\n待到重阳日，还来就菊花。', '2018-03-09 14:55:39');
INSERT INTO `t_poem` VALUES ('41', 'poem', '唐', '王维', '鸟鸣涧', '人闲桂花落，夜静春山空。\\n月出惊山鸟，时鸣春涧中。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('42', 'poem', '唐', '白居易', '草 / 赋得古原草送别', '离离原上草，一岁一枯荣。\\n野火烧不尽，春风吹又生。\\n远芳侵古道，晴翠接荒城。\\n又送王孙去，萋萋满别情。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('43', 'poem', '唐', '王维', '使至塞上', '单车欲问边，属国过居延。\\n征蓬出汉塞，归雁入胡天。\\n大漠孤烟直，长河落日圆。\\n萧关逢候骑，都护在燕然。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('44', 'poem', '唐', '杜甫', '茅屋为秋风所破歌', '八月秋高风怒号，卷我屋上三重茅。茅飞渡江洒江郊，高者挂罥长林梢，下者飘转沉塘坳。\\n南村群童欺我老无力，忍能对面为盗贼。公然抱茅入竹去，唇焦口燥呼不得，归来倚杖自叹息。\\n俄顷风定云墨色，秋天漠漠向昏黑。布衾多年冷似铁，娇儿恶卧踏里裂。床头屋漏无干处，雨脚如麻未断绝。自经丧乱少睡眠，长夜沾湿何由彻！\\n安得广厦千万间，大庇天下寒士俱欢颜！风雨不动安如山。呜呼！何时眼前突兀见此屋，吾庐独破受冻死亦足！', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('45', 'poem', '唐', '孟郊', '游子吟', '慈母手中线，游子身上衣。\\n临行密密缝，意恐迟迟归。\\n谁言寸草心，报得三春晖。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('46', 'poem', '唐', '元稹', '离思五首·其四', '曾经沧海难为水，除却巫山不是云。\\n取次花丛懒回顾，半缘修道半缘君。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('47', 'poem', '唐', '刘长卿', '逢雪宿芙蓉山主人', '日暮苍山远，天寒白屋贫。\\n柴门闻犬吠，风雪夜归人。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('48', 'poem', '唐', '李白', '望天门山', '天门中断楚江开，碧水东流至此回。\\n两岸青山相对出，孤帆一片日边来。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('49', 'poem', '唐', '王之涣', '凉州词二首·其一', '黄河远上白云间，一片孤城万仞山。\\n羌笛何须怨杨柳，春风不度玉门关。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('50', 'poem', '唐', '刘禹锡', '酬乐天扬州初逢席上见赠', '巴山楚水凄凉地，二十三年弃置身。\\n怀旧空吟闻笛赋，到乡翻似烂柯人。\\n沉舟侧畔千帆过，病树前头万木春。\\n今日听君歌一曲，暂凭杯酒长精神。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('51', 'poem', '唐', '杜甫', '江南逢李龟年', '岐王宅里寻常见，崔九堂前几度闻。\\n正是江南好风景，落花时节又逢君。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('52', 'poem', '唐', '白居易', '长恨歌', '汉皇重色思倾国，御宇多年求不得。\\n杨家有女初长成，养在深闺人未识。\\n天生丽质难自弃，一朝选在君王侧。\\n回眸一笑百媚生，六宫粉黛无颜色。\\n春寒赐浴华清池，温泉水滑洗凝脂。\\n侍儿扶起娇无力，始是新承恩泽时。\\n云鬓花颜金步摇，芙蓉帐暖度春宵。\\n春宵苦短日高起，从此君王不早朝。\\n承欢侍宴无闲暇，春从春游夜专夜。\\n后宫佳丽三千人，三千宠爱在一身。\\n金屋妆成娇侍夜，玉楼宴罢醉和春。\\n姊妹弟兄皆列土，可怜光彩生门户。\\n遂令天下父母心，不重生男重生女。\\n骊宫高处入青云，仙乐风飘处处闻。\\n缓歌谩舞凝丝竹，尽日君王看不足。\\n渔阳鼙鼓动地来，惊破霓裳羽衣曲。\\n九重城阙烟尘生，千乘万骑西南行。\\n翠华摇摇行复止，西出都门百余里。\\n六军不发无奈何，宛转蛾眉马前死。\\n花钿委地无人收，翠翘金雀玉搔头。\\n君王掩面救不得，回看血泪相和流。\\n黄埃散漫风萧索，云栈萦纡登剑阁。\\n峨嵋山下少人行，旌旗无光日色薄。\\n蜀江水碧蜀山青，圣主朝朝暮暮情。\\n行宫见月伤心色，夜雨闻铃肠断声。\\n天旋地转回龙驭，到此踌躇不能去。\\n马嵬坡下泥土中，不见玉颜空死处。\\n君臣相顾尽沾衣，东望都门信马归。\\n归来池苑皆依旧，太液芙蓉未央柳。\\n芙蓉如面柳如眉，对此如何不泪垂。\\n春风桃李花开日，秋雨梧桐叶落时。\\n西宫南内多秋草，落叶满阶红不扫。\\n梨园弟子白发新，椒房阿监青娥老。\\n夕殿萤飞思悄然，孤灯挑尽未成眠。\\n迟迟钟鼓初长夜，耿耿星河欲曙天。\\n鸳鸯瓦冷霜华重，翡翠衾寒谁与共。\\n悠悠生死别经年，魂魄不曾来入梦。\\n临邛道士鸿都客，能以精诚致魂魄。\\n为感君王辗转思，遂教方士殷勤觅。\\n排空驭气奔如电，升天入地求之遍。\\n上穷碧落下黄泉，两处茫茫皆不见。\\n忽闻海上有仙山，山在虚无缥渺间。\\n楼阁玲珑五云起，其中绰约多仙子。\\n中有一人字太真，雪肤花貌参差是。\\n金阙西厢叩玉扃，转教小玉报双成。\\n闻道汉家天子使，九华帐里梦魂惊。\\n揽衣推枕起徘徊，珠箔银屏迤逦开。\\n云鬓半偏新睡觉，花冠不整下堂来。\\n风吹仙袂飘飘举，犹似霓裳羽衣舞。\\n玉容寂寞泪阑干，梨花一枝春带雨。\\n含情凝睇谢君王，一别音容两渺茫。\\n昭阳殿里恩爱绝，蓬莱宫中日月长。\\n回头下望人寰处，不见长安见尘雾。\\n惟将旧物表深情，钿合金钗寄将去。\\n钗留一股合一扇，钗擘黄金合分钿。\\n但教心似金钿坚，天上人间会相见。\\n临别殷勤重寄词，词中有誓两心知。\\n七月七日长生殿，夜半无人私语时。\\n在天愿作比翼鸟，在地愿为连理枝。\\n天长地久有时尽，此恨绵绵无绝期。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('53', 'poem', '唐', '崔护', '题都城南庄', '去年今日此门中，人面桃花相映红。\\n人面不知何处去，桃花依旧笑春风。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('54', 'poem', '唐', '杜甫', '闻官军收河南河北', '剑外忽传收蓟北，初闻涕泪满衣裳。\\n却看妻子愁何在，漫卷诗书喜欲狂。\\n白日放歌须纵酒，青春作伴好还乡。\\n即从巴峡穿巫峡，便下襄阳向洛阳。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('55', 'poem', '唐', '杜牧', '秋夕', '银烛秋光冷画屏，轻罗小扇扑流萤。\\n天阶夜色凉如水，坐看牵牛织女星。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('56', 'poem', '唐', '刘禹锡', '望洞庭', '湖光秋月两相和，潭面无风镜未磨。\\n遥望洞庭山水翠，白银盘里一青螺。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('57', 'poem', '唐', '李白', '闻王昌龄左迁龙标遥有此寄', '杨花落尽子规啼，闻道龙标过五溪。\\n我寄愁心与明月，随风直到夜郎西。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('58', 'poem', '唐', '王维', '画', '远看山有色，近听水无声。\\n春去花还在，人来鸟不惊。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('59', 'poem', '唐', '王维', '竹里馆', '独坐幽篁里，弹琴复长啸。\\n深林人不知，明月来相照。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('60', 'poem', '唐', '李商隐', '乐游原 / 登乐游原', '向晚意不适，驱车登古原。\\n夕阳无限好，只是近黄昏。', '2018-03-09 14:55:43');
INSERT INTO `t_poem` VALUES ('61', 'poem', '唐', '张若虚', '春江花月夜', '春江潮水连海平，海上明月共潮生。\\n滟滟随波千万里，何处春江无月明！\\n江流宛转绕芳甸，月照花林皆似霰；\\n空里流霜不觉飞，汀上白沙看不见。\\n江天一色无纤尘，皎皎空中孤月轮。\\n江畔何人初见月？江月何年初照人？\\n人生代代无穷已，江月年年只相似。\\n不知江月待何人，但见长江送流水。\\n白云一片去悠悠，青枫浦上不胜愁。\\n谁家今夜扁舟子？何处相思明月楼？\\n可怜楼上月徘徊，应照离人妆镜台。\\n玉户帘中卷不去，捣衣砧上拂还来。\\n此时相望不相闻，愿逐月华流照君。\\n鸿雁长飞光不度，鱼龙潜跃水成文。\\n昨夜闲潭梦落花，可怜春半不还家。\\n江水流春去欲尽，江潭落月复西斜。\\n斜月沉沉藏海雾，碣石潇湘无限路。\\n不知乘月几人归，落月摇情满江树。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('62', 'poem', '唐', '李白', '独坐敬亭山', '众鸟高飞尽，孤云独去闲。\\n相看两不厌，只有敬亭山。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('63', 'poem', '唐', '白居易', '暮江吟', '一道残阳铺水中，半江瑟瑟半江红。\\n可怜九月初三夜，露似真珠月似弓。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('64', 'poem', '唐', '张九龄', '望月怀远 / 望月怀古', '海上生明月，天涯共此时。\\n情人怨遥夜，竟夕起相思。\\n灭烛怜光满，披衣觉露滋。\\n不堪盈手赠，还寝梦佳期。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('65', 'poem', '唐', '刘禹锡', '乌衣巷', '朱雀桥边野草花，乌衣巷口夕阳斜。\\n旧时王谢堂前燕，飞入寻常百姓家。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('66', 'poem', '唐', '刘禹锡', '秋词', '自古逢秋悲寂寥，我言秋日胜春朝。\\n晴空一鹤排云上，便引诗情到碧霄。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('67', 'poem', '唐', '李贺', '雁门太守行', '黑云压城城欲摧，甲光向日金鳞开。\\n角声满天秋色里，塞上燕脂凝夜紫。\\n半卷红旗临易水，霜重鼓寒声不起。\\n报君黄金台上意，提携玉龙为君死。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('68', 'poem', '唐', '杜甫', '登高', '风急天高猿啸哀，渚清沙白鸟飞回。\\n无边落木萧萧下，不尽长江滚滚来。\\n万里悲秋常作客，百年多病独登台。\\n艰难苦恨繁霜鬓，潦倒新停浊酒杯。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('69', 'poem', '唐', '高适', '别董大二首', '千里黄云白日曛，北风吹雁雪纷纷。\\n莫愁前路无知己，天下谁人不识君。\\n六翮飘飖私自怜，一离京洛十馀年。\\n丈夫贫践应未足，今日相逢无酒钱。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('70', 'poem', '唐', '李白', '送友人', '青山横北郭，白水绕东城。\\n此地一为别，孤蓬万里征。\\n浮云游子意，落日故人情。\\n挥手自兹去，萧萧班马鸣。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('71', 'poem', '唐', '王维', '鹿柴', '空山不见人，但闻人语响。\\n返景入深林，复照青苔上。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('72', 'poem', '唐', '刘禹锡', '浪淘沙·九曲黄河万里沙', '九曲黄河万里沙，浪淘风簸自天涯。\\n如今直上银河去，同到牵牛织女家。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('73', 'poem', '唐', '刘禹锡', '竹枝词二首·其一', '杨柳青青江水平，闻郎江上踏歌声。\\n东边日出西边雨，道是无晴却有晴。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('74', 'poem', '唐', '李白', '春夜洛城闻笛 / 春夜洛阳城闻笛', '谁家玉笛暗飞声，散入春风满洛城。\\n此夜曲中闻折柳，何人不起故园情。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('75', 'poem', '唐', '胡令能', '小儿垂钓', '蓬头稚子学垂纶，侧坐莓苔草映身。\\n路人借问遥招手，怕得鱼惊不应人。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('76', 'poem', '唐', '孟浩然', '宿建德江', '移舟泊烟渚，日暮客愁新。\\n野旷天低树，江清月近人。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('77', 'poem', '唐', '杜甫', '江畔独步寻花·其六', '黄四娘家花满蹊，千朵万朵压枝低。\\n留连戏蝶时时舞，自在娇莺恰恰啼。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('78', 'poem', '唐', '李峤', '风', '解落三秋叶，能开二月花。\\n过江千尺浪，入竹万竿斜。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('79', 'poem', '唐', '李白', '峨眉山月歌', '峨眉山月半轮秋，影入平羌江水流。\\n夜发清溪向三峡，思君不见下渝州。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('80', 'poem', '唐', '白居易', '池上', '小娃撑小艇，偷采白莲回。\\n不解藏踪迹，浮萍一道开。', '2018-03-09 14:55:56');
INSERT INTO `t_poem` VALUES ('81', 'poem', '唐', '李白', '月下独酌四首·其一', '花间一壶酒，独酌无相亲。\\n举杯邀明月，对影成三人。\\n月既不解饮，影徒随我身。\\n暂伴月将影，行乐须及春。\\n我歌月徘徊，我舞影零乱。\\n醒时相交欢，醉后各分散。\\n永结无情游，相期邈云汉。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('82', 'poem', '唐', '黄巢', '不第后赋菊', '待到秋来九月八，我花开后百花杀。\\n冲天香阵透长安，满城尽带黄金甲。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('83', 'poem', '唐', '李白', '行路难（其一）', '金樽清酒斗十千，玉盘珍羞直万钱。\\n停杯投箸不能食，拔剑四顾心茫然。\\n欲渡黄河冰塞川，将登太行雪满山。\\n闲来垂钓碧溪上，忽复乘舟梦日边。\\n行路难！行路难！多歧路，今安在？\\n长风破浪会有时，直挂云帆济沧海。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('84', 'poem', '唐', '罗隐', '蜂', '不论平地与山尖，无限风光尽被占。\\n采得百花成蜜后，为谁辛苦为谁甜。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('85', 'poem', '唐', '李白', '夜宿山寺', '危楼高百尺，手可摘星辰。\\n不敢高声语，恐惊天上人。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('86', 'poem', '唐', '林杰', '乞巧', '七夕今宵看碧霄，牵牛织女渡河桥。\\n家家乞巧望秋月，穿尽红丝几万条。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('87', 'poem', '唐', '杜甫', '月夜忆舍弟', '戍鼓断人行，秋边一雁声。\\n露从今夜白，月是故乡明。\\n有弟皆分散，无家问死生。\\n寄书长不达，况乃未休兵。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('88', 'poem', '唐', '王昌龄', '从军行七首·其四', '青海长云暗雪山，孤城遥望玉门关。\\n黄沙百战穿金甲，不破楼兰终不还。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('89', 'poem', '唐', '刘方平', '月夜 / 夜月', '更深月色半人家，北斗阑干南斗斜。\\n今夜偏知春气暖，虫声新透绿窗纱。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('90', 'poem', '唐', '白居易', '问刘十九', ' 绿蚁新醅酒，红泥小火炉。\\n晚来天欲雪，能饮一杯无？', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('91', 'poem', '唐', '孟郊', '登科后', '昔日龌龊不足夸，今朝放荡思无涯。\\n春风得意马蹄疾，一日看尽长安花。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('92', 'poem', '唐', '白居易', '大林寺桃花', '人间四月芳菲尽，山寺桃花始盛开。\\n长恨春归无觅处，不知转入此中来。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('93', 'poem', '唐', '杜甫', '蜀相', '丞相祠堂何处寻，锦官城外柏森森。\\n映阶碧草自春色，隔叶黄鹂空好音。\\n三顾频烦天下计，两朝开济老臣心。\\n出师未捷身先死，长使英雄泪满襟。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('94', 'poem', '唐', '杜牧', '赤壁', '折戟沉沙铁未销，自将磨洗认前朝。\\n东风不与周郎便，铜雀春深锁二乔。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('95', 'poem', '唐', '李白', '蜀道难', '噫吁嚱，危乎高哉！蜀道之难，难于上青天！蚕丛及鱼凫，开国何茫然！尔来四万八千岁，不与秦塞通人烟。西当太白有鸟道，可以横绝峨眉巅。地崩山摧壮士死，然后天梯石栈相钩连。上有六龙回日之高标，下有冲波逆折之回川。黄鹤之飞尚不得过，猿猱欲度愁攀援。青泥何盘盘，百步九折萦岩峦。扪参历井仰胁息，以手抚膺坐长叹。\\n问君西游何时还？畏途巉岩不可攀。但见悲鸟号古木，雄飞雌从绕林间。又闻子规啼夜月，愁空山。蜀道之难，难于上青天，使人听此凋朱颜！连峰去天不盈尺，枯松倒挂倚绝壁。飞湍瀑流争喧豗，砯崖转石万壑雷。其险也如此，嗟尔远道之人胡为乎来哉！\\n剑阁峥嵘而崔嵬，一夫当关，万夫莫开。所守或匪亲，化为狼与豺。朝避猛虎，夕避长蛇；磨牙吮血，杀人如麻。锦城虽云乐，不如早还家。蜀道之难，难于上青天，侧身西望长咨嗟！', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('96', 'poem', '唐', '李白', '渡荆门送别', '渡远荆门外，来从楚国游。\\n山随平野尽，江入大荒流。\\n月下飞天镜，云生结海楼。\\n仍怜故乡水，万里送行舟。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('97', 'poem', '唐', '岑参', '逢入京使', '故园东望路漫漫，双袖龙钟泪不干。\\n马上相逢无纸笔，凭君传语报平安。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('98', 'poem', '唐', '韩翃', '寒食 / 寒食日即事', '春城无处不飞花，寒食东风御柳斜。\\n日暮汉宫传蜡烛，轻烟散入五侯家。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('99', 'poem', '唐', '李白', '古朗月行', '小时不识月，呼作白玉盘。\\n又疑瑶台镜，飞在青云端。\\n仙人垂两足，桂树何团团。\\n白兔捣药成，问言与谁餐。\\n蟾蜍蚀圆影，大明夜已残。\\n羿昔落九乌，天人清且安。\\n阴精此沦惑，去去不足观。\\n忧来其如何，凄怆摧心肝。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('100', 'poem', '唐', '白居易', '琵琶行 / 琵琶引', '浔阳江头夜送客，枫叶荻花秋瑟瑟。主人下马客在船，举酒欲饮无管弦。醉不成欢惨将别，别时茫茫江浸月。\\n忽闻水上琵琶声，主人忘归客不发。寻声暗问弹者谁？琵琶声停欲语迟。移船相近邀相见，添酒回灯重开宴。千呼万唤始出来，犹抱琵琶半遮面。转轴拨弦三两声，未成曲调先有情。弦弦掩抑声声思，似诉平生不得志。低眉信手续续弹，说尽心中无限事。轻拢慢捻抹复挑，初为霓裳后六幺。大弦嘈嘈如急雨，小弦切切如私语。嘈嘈切切错杂弹，大珠小珠落玉盘。间关莺语花底滑，幽咽泉流冰下难。冰泉冷涩弦凝绝，凝绝不通声暂歇。别有幽愁暗恨生，此时无声胜有声。银瓶乍破水浆迸，铁骑突出刀枪鸣。曲终收拨当心画，四弦一声如裂帛。东船西舫悄无言，唯见江心秋月白。\\n沉吟放拨插弦中，整顿衣裳起敛容。自言本是京城女，家在虾蟆陵下住。十三学得琵琶成，名属教坊第一部。曲罢曾教善才服，妆成每被秋娘妒。五陵年少争缠头，一曲红绡不知数。钿头银篦击节碎，血色罗裙翻酒污。今年欢笑复明年，秋月春风等闲度。弟走从军阿姨死，暮去朝来颜色故。门前冷落鞍马稀，老大嫁作商人妇。商人重利轻别离，前月浮梁买茶去。去来江口守空船，绕船月明江水寒。夜深忽梦少年事，梦啼妆泪红阑干。\\n我闻琵琶已叹息，又闻此语重唧唧。同是天涯沦落人，相逢何必曾相识！我从去年辞帝京，谪居卧病浔阳城。浔阳地僻无音乐，终岁不闻丝竹声。住近湓江地低湿，黄芦苦竹绕宅生。其间旦暮闻何物？杜鹃啼血猿哀鸣。春江花朝秋月夜，往往取酒还独倾。岂无山歌与村笛？呕哑嘲哳难为听。今夜闻君琵琶语，如听仙乐耳暂明。莫辞更坐弹一曲，为君翻作《琵琶行》。感我此言良久立，却坐促弦弦转急。凄凄不似向前声，满座重闻皆掩泣。座中泣下谁最多？江州司马青衫湿。', '2018-03-09 14:56:06');
INSERT INTO `t_poem` VALUES ('101', 'poem', '唐', '卢纶', '和张仆射塞下曲·其三', '月黑雁飞高，单于夜遁逃。\\n欲将轻骑逐，大雪满弓刀。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('102', 'poem', '唐', '吕洞宾', '牧童', '草铺横野六七里，笛弄晚风三四声。\\n归来饱饭黄昏后，不脱蓑衣卧月明。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('103', 'poem', '唐', '颜真卿', '劝学诗', '三更灯火五更鸡，正是男儿读书时。\\n黑发不知勤学早，白首方悔读书迟。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('104', 'poem', '唐', '白居易', '观刈麦', '田家少闲月，五月人倍忙。\\n夜来南风起，小麦覆陇黄。\\n妇姑荷箪食，童稚携壶浆，\\n相随饷田去，丁壮在南冈。\\n足蒸暑土气，背灼炎天光，\\n力尽不知热，但惜夏日长。\\n复有贫妇人，抱子在其旁，\\n右手秉遗穗，左臂悬敝筐。\\n听其相顾言，闻者为悲伤。\\n家田输税尽，拾此充饥肠。\\n今我何功德，曾不事农桑。\\n吏禄三百石，岁晏有余粮。\\n念此私自愧，尽日不能忘。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('105', 'poem', '唐', '杜牧', '寄扬州韩绰判官', '青山隐隐水迢迢，秋尽江南草木凋。\\n二十四桥明月夜，玉人何处教吹箫。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('106', 'poem', '唐', '高骈', '山亭夏日', '绿树阴浓夏日长，楼台倒影入池塘。\\n水晶帘动微风起，满架蔷薇一院香。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('107', 'poem', '唐', '杜甫', '绝句二首', '迟日江山丽，春风花草香。\\n泥融飞燕子，沙暖睡鸳鸯。\\n江碧鸟逾白，山青花欲燃。\\n今春看又过，何日是归年。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('108', 'poem', '唐', '元稹', '菊花', '秋丛绕舍似陶家，遍绕篱边日渐斜。\\n不是花中偏爱菊，此花开尽更无花。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('109', 'poem', '唐', '王维', '终南别业', '中岁颇好道，晚家南山陲。\\n兴来每独往，胜事空自知。\\n行到水穷处，坐看云起时。\\n偶然值林叟，谈笑无还期。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('110', 'poem', '唐', '孟浩然', '望洞庭湖赠张丞相', '八月湖水平，涵虚混太清。\\n气蒸云梦泽，波撼岳阳城。\\n欲济无舟楫，端居耻圣明。\\n坐观垂钓者，徒有羡鱼情。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('111', 'poem', '唐', '韩愈', '春雪', '新年都未有芳华，二月初惊见草芽。\\n白雪却嫌春色晚，故穿庭树作飞花。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('112', 'poem', '唐', '贾岛', '剑客 / 述剑', '十年磨一剑，霜刃未曾试。\\n今日把示君，谁有不平事。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('113', 'poem', '唐', '李贺', '马诗二十三首·其五', '大漠沙如雪，燕山月似钩。\\n何当金络脑，快走踏清秋。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('114', 'poem', '唐', '刘长卿', '送灵澈上人', '苍苍竹林寺，杳杳钟声晚。\\n荷笠带斜阳，青山独归远。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('115', 'poem', '唐', '王维', '山中', '荆溪白石出，天寒红叶稀。\\n山路元无雨，空翠湿人衣。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('116', 'poem', '唐', '杜甫', '秋兴八首（其一）', '玉露凋伤枫树林，巫山巫峡气萧森。\\n江间波浪兼天涌，塞上风云接地阴。\\n丛菊两开他日泪，孤舟一系故园心。\\n寒衣处处催刀尺，白帝城高急暮砧。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('117', 'poem', '唐', '杜甫', '八阵图', '功盖三分国，名成八阵图。\\n江流石不转，遗恨失吞吴。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('118', 'poem', '唐', '张籍', '秋思', '洛阳城里见秋风，欲作家书意万重。\\n复恐匆匆说不尽，行人临发又开封。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('119', 'poem', '唐', '李商隐', '贾生', '宣室求贤访逐臣，贾生才调更无伦。\\n可怜夜半虚前席，不问苍生问鬼神。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('120', 'poem', '唐', '温庭筠', '商山早行', '晨起动征铎，客行悲故乡。\\n鸡声茅店月，人迹板桥霜。\\n槲叶落山路，枳花明驿墙。\\n因思杜陵梦，凫雁满回塘。', '2018-03-09 14:56:18');
INSERT INTO `t_poem` VALUES ('121', 'poem', '唐', '虞世南', '蝉', '垂緌饮清露，流响出疏桐。\\n居高声自远，非是藉秋风。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('122', 'poem', '唐', '杜甫', '石壕吏', '暮投石壕村，有吏夜捉人。老翁逾墙走，老妇出门看。\\n吏呼一何怒！妇啼一何苦！\\n听妇前致词：三男邺城戍。一男附书至，二男新战死。存者且偷生，死者长已矣！室中更无人，惟有乳下孙。有孙母未去，出入无完裙。老妪力虽衰，请从吏夜归，急应河阳役，犹得备晨炊。\\n夜久语声绝，如闻泣幽咽。天明登前途，独与老翁别。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('123', 'poem', '唐', '贾岛', '题李凝幽居', '闲居少邻并，草径入荒园。\\n鸟宿池边树，僧敲月下门。\\n过桥分野色，移石动云根。\\n暂去还来此，幽期不负言。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('124', 'poem', '唐', '王维', '辛夷坞', '木末芙蓉花，山中发红萼。\\n涧户寂无人，纷纷开且落。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('125', 'poem', '唐', '杜甫', '赠花卿', '锦城丝管日纷纷，半入江风半入云。\\n此曲只应天上有，人间能得几回闻。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('126', 'poem', '唐', '李白', '子夜吴歌·秋歌', '长安一片月，万户捣衣声。\\n秋风吹不尽，总是玉关情。\\n何日平胡虏，良人罢远征。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('127', 'poem', '唐', '宋之问', '渡汉江', '岭外音书断，经冬复历春。\\n近乡情更怯，不敢问来人。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('128', 'poem', '唐', '李白', '登金陵凤凰台', '凤凰台上凤凰游，凤去台空江自流。\\n吴宫花草埋幽径，晋代衣冠成古丘。\\n三山半落青天外，二水中分白鹭洲。\\n总为浮云能蔽日，长安不见使人愁。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('129', 'poem', '唐', '杜甫', '登岳阳楼', '昔闻洞庭水，今上岳阳楼。\\n吴楚东南坼，乾坤日夜浮。\\n亲朋无一字，老病有孤舟。\\n戎马关山北，凭轩涕泗流。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('130', 'poem', '唐', '李白', '客中行 / 客中作', '兰陵美酒郁金香，玉碗盛来琥珀光。\\n但使主人能醉客，不知何处是他乡。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('131', 'poem', '唐', '李商隐', '无题·昨夜星辰昨夜风', '昨夜星辰昨夜风，画楼西畔桂堂东。\\n身无彩凤双飞翼，心有灵犀一点通。\\n隔座送钩春酒暖，分曹射覆蜡灯红。\\n嗟余听鼓应官去，走马兰台类转蓬。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('132', 'poem', '唐', '杜甫', '旅夜书怀', '细草微风岸，危樯独夜舟。\\n星垂平野阔，月涌大江流。\\n名岂文章著，官应老病休。\\n飘飘何所似，天地一沙鸥。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('133', 'poem', '唐', '白居易', '卖炭翁', '卖炭翁，伐薪烧炭南山中。\\n满面尘灰烟火色，两鬓苍苍十指黑。\\n卖炭得钱何所营？身上衣裳口中食。\\n可怜身上衣正单，心忧炭贱愿天寒。\\n夜来城外一尺雪，晓驾炭车辗冰辙。\\n牛困人饥日已高，市南门外泥中歇。\\n翩翩两骑来是谁？黄衣使者白衫儿。\\n手把文书口称敕，回车叱牛牵向北。\\n一车炭，千余斤，宫使驱将惜不得。\\n半匹红绡一丈绫，系向牛头充炭直。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('134', 'poem', '唐', '李白', '关山月', '明月出天山，苍茫云海间。\\n长风几万里，吹度玉门关。\\n汉下白登道，胡窥青海湾。\\n由来征战地，不见有人还。\\n戍客望边色，思归多苦颜。\\n高楼当此夜，叹息未应闲。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('135', 'poem', '唐', '李白', '宣州谢朓楼饯别校书叔云', '弃我去者，昨日之日不可留；\\n乱我心者，今日之日多烦忧。\\n长风万里送秋雁，对此可以酣高楼。\\n蓬莱文章建安骨，中间小谢又清发。\\n俱怀逸兴壮思飞，欲上青天览明月。\\n抽刀断水水更流，举杯消愁愁更愁。\\n人生在世不称意，明朝散发弄扁舟。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('136', 'poem', '唐', '韩愈', '左迁至蓝关示侄孙湘', '一封朝奏九重天，夕贬潮阳路八千。\\n欲为圣明除弊事，肯将衰朽惜残年！\\n云横秦岭家何在？雪拥蓝关马不前。\\n知汝远来应有意，好收吾骨瘴江边。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('137', 'poem', '唐', '元稹', '闻乐天授江州司马', '残灯无焰影幢幢，此夕闻君谪九江。\\n垂死病中惊坐起，暗风吹雨入寒窗。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('138', 'poem', '唐', '温庭筠', '南歌子词二首 / 新添声杨柳枝词', '一尺深红胜曲尘，天生旧物不如新。\\n合欢桃核终堪恨，里许元来别有人。\\n井底点灯深烛伊，共郎长行莫围棋。\\n玲珑骰子安红豆，入骨相思知不知。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('139', 'poem', '唐', '杜牧', '题乌江亭', '胜败兵家事不期，包羞忍耻是男儿。\\n江东子弟多才俊，卷土重来未可知。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('140', 'poem', '唐', '皮日休', '天竺寺八月十五日夜桂子', '玉颗珊珊下月轮，殿前拾得露华新。\\n至今不会天中事，应是嫦娥掷与人。', '2018-03-09 14:56:24');
INSERT INTO `t_poem` VALUES ('141', 'poem', '唐', '李白', '行路难·其二', '大道如青天，我独不得出。\\n羞逐长安社中儿，赤鸡白雉赌梨栗。\\n弹剑作歌奏苦声，曳裾王门不称情。\\n淮阴市井笑韩信，汉朝公卿忌贾生。\\n君不见昔时燕家重郭隗，拥篲折节无嫌猜。\\n剧辛、乐毅感恩分，输肝剖胆效英才。\\n昭王白骨萦蔓草，谁人更扫黄金台？', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('142', 'poem', '唐', '罗隐', '自遣', '得即高歌失即休，多愁多恨亦悠悠。\\n今朝有酒今朝醉，明日愁来明日愁。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('143', 'poem', '唐', '刘禹锡', '赏牡丹', '庭前芍药妖无格，池上芙蕖净少情。\\n唯有牡丹真国色，花开时节动京城。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('144', 'poem', '唐', '王绩', '野望', '东皋薄暮望，徙倚欲何依。\\n树树皆秋色，山山唯落晖。\\n牧人驱犊返，猎马带禽归。\\n相顾无相识，长歌怀采薇。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('145', 'poem', '唐', '韩愈', '晚春二首·其一', '草树知春不久归，百般红紫斗芳菲。\\n杨花榆荚无才思，惟解漫天作雪飞。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('146', 'poem', '唐', '王维', '杂诗三首·其二', '君自故乡来，应知故乡事。\\n来日绮窗前，寒梅著花未？', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('147', 'poem', '唐', '郑谷', '鹧鸪', '暖戏烟芜锦翼齐，品流应得近山鸡。\\n雨昏青草湖边过，花落黄陵庙里啼。\\n游子乍闻征袖湿，佳人才唱翠眉低。\\n相呼相应湘江阔，苦竹丛深日向西。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('148', 'poem', '唐', '杜甫', '江畔独步寻花·其五', '黄师塔前江水东，春光懒困倚微风。\\n桃花一簇开无主，可爱深红爱浅红。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('149', 'poem', '唐', '崔郊', '赠去婢', '公子王孙逐后尘，绿珠垂泪滴罗巾。\\n侯门一入深如海，从此萧郎是路人。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('150', 'poem', '唐', '许浑', '咸阳城东楼 / 咸阳城西楼晚眺 / 西门', '一上高城万里愁，蒹葭杨柳似汀洲。\\n溪云初起日沉阁，山雨欲来风满楼。\\n鸟下绿芜秦苑夕，蝉鸣黄叶汉宫秋。\\n行人莫问当年事，故国东来渭水流。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('151', 'poem', '唐', '杜牧', '过华清宫绝句三首·其一', '长安回望绣成堆，山顶千门次第开。\\n一骑红尘妃子笑，无人知是荔枝来。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('152', 'poem', '唐', '杜牧', '紫薇花', '晓迎秋露一枝新，不占园中最上春。\\n桃李无言又何在，向风偏笑艳阳人。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('153', 'poem', '唐', '刘禹锡', '金陵五题·石头城', '山围故国周遭在，潮打空城寂寞回。\\n淮水东边旧时月，夜深还过女墙来。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('154', 'poem', '唐', '李商隐', '赠荷花', '世间花叶不相伦，花入金盆叶作尘。\\n惟有绿荷红菡萏，卷舒开合任天真。\\n此花此叶常相映，翠减红衰愁杀人。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('155', 'poem', '唐', '杨巨源', '城东早春', '诗家清景在新春，绿柳才黄半未匀。\\n若待上林花似锦，出门俱是看花人。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('156', 'poem', '唐', '张旭', '桃花溪', '隐隐飞桥隔野烟，石矶西畔问渔船。\\n桃花尽日随流水，洞在清溪何处边。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('157', 'poem', '唐', '王勃', '山中', '长江悲已滞，万里念将归。\\n况属高风晚，山山黄叶飞。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('158', 'poem', '唐', '杜甫', '登楼', '花近高楼伤客心，万方多难此登临。\\n锦江春色来天地，玉垒浮云变古今。\\n北极朝廷终不改，西山寇盗莫相侵。\\n可怜后主还祠庙，日暮聊为《梁父吟》。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('159', 'poem', '唐', '李商隐', '瑶池', '瑶池阿母绮窗开，黄竹歌声动地哀。\\n八骏日行三万里，穆王何事不重来。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('160', 'poem', '唐', '王建', '雨过山村', '雨里鸡鸣一两家，竹溪村路板桥斜。\\n妇姑相唤浴蚕去，闲看中庭栀子花。', '2018-03-09 14:56:33');
INSERT INTO `t_poem` VALUES ('161', 'poem', '唐', '李贺', '南园十三首·其五', '男儿何不带吴钩，收取关山五十州。\\n请君暂上凌烟阁，若个书生万户侯？', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('162', 'poem', '唐', '王昌龄', '闺怨', '闺中少妇不知愁，春日凝妆上翠楼。\\n忽见陌头杨柳色，悔教夫婿觅封侯。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('163', 'poem', '唐', '刘希夷', '代悲白头翁 / 白头吟 / 有所思', '洛阳城东桃李花，飞来飞去落谁家？\\n洛阳女儿惜颜色，坐见落花长叹息。\\n今年花落颜色改，明年花开复谁在？\\n已见松柏摧为薪，更闻桑田变成海。\\n古人无复洛城东，今人还对落花风。\\n年年岁岁花相似，岁岁年年人不同。\\n寄言全盛红颜子，应怜半死白头翁。\\n此翁白头真可怜，伊昔红颜美少年。\\n公子王孙芳树下，清歌妙舞落花前。\\n光禄池台文锦绣，将军楼阁画神仙。\\n一朝卧病无相识，三春行乐在谁边？\\n宛转蛾眉能几时？须臾鹤发乱如丝。\\n但看古来歌舞地，唯有黄昏鸟雀悲。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('164', 'poem', '唐', '杜甫', '客至', '舍南舍北皆春水，但见群鸥日日来。\\n花径不曾缘客扫，蓬门今始为君开。\\n盘餐市远无兼味，樽酒家贫只旧醅。\\n肯与邻翁相对饮，隔篱呼取尽馀杯。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('165', 'poem', '唐', '王贞白', '白鹿洞二首·其一', '读书不觉已春深，一寸光阴一寸金。\\n不是道人来引笑，周情孔思正追寻。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('166', 'poem', '唐', '李白', '从军行·其二', '百战沙场碎铁衣，城南已合数重围。\\n突营射杀呼延将，独领残兵千骑归。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('167', 'poem', '唐', '杜甫', '春远 / 春运', '肃肃花絮晚，菲菲红素轻。\\n日长唯鸟雀，春远独柴荆。\\n数有关中乱，何曾剑外清。\\n故乡归不得，地入亚夫营。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('168', 'poem', '唐', '岑参', '走马川行奉送出师西征 / 走马川行奉送封大夫出师西征', '君不见走马川行雪海边，平沙莽莽黄入天。\\n轮台九月风夜吼，一川碎石大如斗，随风满地石乱走。\\n匈奴草黄马正肥，金山西见烟尘飞，汉家大将西出师。\\n将军金甲夜不脱，半夜军行戈相拨，风头如刀面如割。\\n马毛带雪汗气蒸，五花连钱旋作冰，幕中草檄砚水凝。\\n虏骑闻之应胆慑，料知短兵不敢接，车师西门伫献捷。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('169', 'poem', '唐', '杜甫', '绝句二首·其二', '江碧鸟逾白，山青花欲燃。\\n今春看又过，何日是归年？', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('170', 'poem', '唐', '白居易', '望月有感', '自河南经乱，关内阻饥，兄弟离散，各在一处。因望月有感，聊书所怀，寄上浮梁大兄、於潜七兄、乌江十五兄，兼示符离及下邽弟妹。\\n时难年荒世业空，弟兄羁旅各西东。\\n田园寥落干戈后，骨肉流离道路中。\\n吊影分为千里雁，辞根散作九秋蓬。\\n共看明月应垂泪，一夜乡心五处同。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('171', 'poem', '唐', '李白', '秋浦歌十七首·其十四', '炉火照天地，红星乱紫烟。\\n赧郎明月夜，歌曲动寒川。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('172', 'poem', '唐', '惠能', '菩提偈', '菩提本无树，明镜亦非台。\\n佛性常清净，何处有尘埃！\\n心是菩提树，身为明镜台。\\n明镜本清净，何处染尘埃！\\n菩提本无树，明镜亦非台。\\n本来无一物，何处惹尘埃！\\n菩提只向心觅，何劳向外求玄？\\n听说依此修行，西方只在目前！', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('173', 'poem', '唐', '白居易', '花非花', '花非花，雾非雾。夜半来，天明去。来如春梦不多时，去似朝云无觅处。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('174', 'poem', '唐', '杜牧', '遣怀', '落魄江南载酒行，楚腰肠断掌中轻。\\n十年一觉扬州梦，赢得青楼薄幸名。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('175', 'poem', '唐', '李益', '夜上受降城闻笛', '回乐峰前沙似雪，受降城外月如霜。\\n不知何处吹芦管，一夜征人尽望乡。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('176', 'poem', '唐', '陈陶', '陇西行四首·其二', '誓扫匈奴不顾身，五千貂锦丧胡尘。\\n可怜无定河边骨，犹是春闺梦里人！', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('177', 'poem', '唐', '朱庆馀', '近试上张籍水部 / 近试上张水部 / 闺意献张水部', '洞房昨夜停红烛，待晓堂前拜舅姑。\\n妆罢低声问夫婿，画眉深浅入时无。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('178', 'poem', '唐', '王昌龄', '采莲曲', '荷叶罗裙一色裁，芙蓉向脸两边开。\\n乱入池中看不见，闻歌始觉有人来。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('179', 'poem', '唐', '杜甫', '绝句四首', '堂西长笋别开门，堑北行椒却背村。\\n梅熟许同朱老吃，松高拟对阮生论。\\n欲作鱼梁云复湍，因惊四月雨声寒。\\n青溪先有蛟龙窟，竹石如山不敢安。\\n两个黄鹂鸣翠柳，一行白鹭上青天。\\n窗含西岭千秋雪，门泊东吴万里船。\\n药条药甲润青青，色过棕亭入草亭。\\n苗满空山惭取誉，根居隙地怯成形。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('180', 'poem', '唐', '杜甫', '前出塞九首·其六', '挽弓当挽强，用箭当用长。\\n射人先射马，擒贼先擒王。\\n杀人亦有限，列国自有疆。\\n苟能制侵陵，岂在多杀伤。', '2018-03-09 14:56:38');
INSERT INTO `t_poem` VALUES ('181', 'poem', '唐', '杜甫', '梦李白二首·其二', '浮云终日行，游子久不至。\\n三夜频梦君，情亲见君意。\\n告归常局促，苦道来不易。\\n江湖多风波，舟楫恐失坠。\\n出门搔白首，若负平生志。\\n冠盖满京华，斯人独憔悴。\\n孰云网恢恢，将老身反累。\\n千秋万岁名，寂寞身后事。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('182', 'poem', '唐', '杜甫', '兵车行', '车辚辚，马萧萧，行人弓箭各在腰。\\n爷娘妻子走相送，尘埃不见咸阳桥。\\n牵衣顿足拦道哭，哭声直上干云霄。\\n道旁过者问行人，行人但云点行频。\\n或从十五北防河，便至四十西营田。\\n去时里正与裹头，归来头白还戍边。\\n边庭流血成海水，武皇开边意未已。\\n君不闻汉家山东二百州，千村万落生荆杞。\\n纵有健妇把锄犁，禾生陇亩无东西。\\n况复秦兵耐苦战，被驱不异犬与鸡。\\n长者虽有问，役夫敢申恨？\\n且如今年冬，未休关西卒。\\n县官急索租，租税从何出？\\n信知生男恶，反是生女好。\\n生女犹得嫁比邻，生男埋没随百草。\\n君不见，青海头，古来白骨无人收。\\n新鬼烦冤旧鬼哭，天阴雨湿声啾啾！', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('183', 'poem', '唐', '孟浩然', '早寒江上有怀 / 早寒有怀 / 江上思归', '木落雁南度，北风江上寒。\\n我家襄水曲，遥隔楚云端。\\n乡泪客中尽，孤帆天际看。\\n迷津欲有问，平海夕漫漫。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('184', 'poem', '唐', '李贺', '李凭箜篌引', '吴丝蜀桐张高秋，空山凝云颓不流。\\n江娥啼竹素女愁，李凭中国弹箜篌。\\n昆山玉碎凤凰叫，芙蓉泣露香兰笑。\\n十二门前融冷光，二十三丝动紫皇。\\n女娲炼石补天处，石破天惊逗秋雨。\\n梦入神山教神妪，老鱼跳波瘦蛟舞。\\n吴质不眠倚桂树，露脚斜飞湿寒兔。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('185', 'poem', '唐', '杜甫', '咏怀古迹（其三）', '群山万壑赴荆门，生长明妃尚有村。\\n一去紫台连朔漠，独留青冢向黄昏。\\n画图省识春风面，环珮空归夜月魂。\\n千载琵琶作胡语，分明怨恨曲中论。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('186', 'poem', '唐', '刘禹锡', '西塞山怀古', '王濬楼船下益州，金陵王气黯然收。\\n千寻铁锁沉江底，一片降幡出石头。\\n人世几回伤往事，山形依旧枕寒流。\\n今逢四海为家日，故垒萧萧芦荻秋。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('187', 'poem', '唐', '颜仁郁', '农家', '半夜呼儿趁晓耕，羸牛无力渐艰行。\\n时人不识农家苦，将谓田中谷自生。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('188', 'poem', '唐', '杜荀鹤', '小松', '自小刺头深草里，而今渐觉出蓬蒿。\\n时人不识凌云木，直待凌云始道高。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('189', 'poem', '唐', '柳宗元', '渔翁', '渔翁夜傍西岩宿，晓汲清湘燃楚竹。\\n烟销日出不见人，欸乃一声山水绿。\\n回看天际下中流，岩上无心云相逐。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('190', 'poem', '唐', '骆宾王', '咏蝉 / 在狱咏蝉', '西陆蝉声唱，南冠客思侵。\\n那堪玄鬓影，来对白头吟。\\n露重飞难进，风多响易沉。\\n无人信高洁，谁为表予心。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('191', 'poem', '唐', '王维', '积雨辋川庄作 / 秋归辋川庄作', '积雨空林烟火迟，蒸藜炊黍饷东菑。\\n漠漠水田飞白鹭，阴阴夏木啭黄鹂。\\n山中习静观朝槿，松下清斋折露葵。\\n野老与人争席罢，海鸥何事更相疑。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('192', 'poem', '唐', '戴叔伦', '兰溪棹歌', '凉月如眉挂柳湾，越中山色镜中看。\\n兰溪三日桃花雨，半夜鲤鱼来上滩。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('193', 'poem', '唐', '卢纶', '和张仆射塞下曲·其二', '林暗草惊风，将军夜引弓。\\n平明寻白羽，没在石棱中。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('194', 'poem', '唐', '杜甫', '饮中八仙歌', '知章骑马似乘船，眼花落井水底眠。汝阳三斗始朝天，\\n道逢麹车口流涎，恨不移封向酒泉。左相日兴费万钱，\\n饮如长鲸吸百川，衔杯乐圣称世贤。宗之潇洒美少年，\\n举觞白眼望青天，皎如玉树临风前。苏晋长斋绣佛前，\\n醉中往往爱逃禅。李白一斗诗百篇，长安市上酒家眠。\\n天子呼来不上船，自称臣是酒中仙。张旭三杯草圣传，\\n脱帽露顶王公前，挥毫落纸如云烟。焦遂五斗方卓然，\\n高谈雄辨惊四筵。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('195', 'poem', '唐', '祖咏', '终南望余雪', '终南阴岭秀，积雪浮云端。\\n林表明霁色，城中增暮寒。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('196', 'poem', '唐', '白居易', '村夜', '霜草苍苍虫切切，村南村北行人绝。\\n独出前门望野田，月明荞麦花如雪。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('197', 'poem', '唐', '郑畋', '马嵬坡', '玄宗回马杨妃死，云雨虽亡日月新。\\n终是圣明天子事，景阳宫井又何人。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('198', 'poem', '唐', '元稹', '遣悲怀三首·其二', '昔日戏言身后事，今朝都到眼前来。\\n衣裳已施行看尽，针线犹存未忍开。\\n尚想旧情怜婢仆，也曾因梦送钱财。\\n诚知此恨人人有，贫贱夫妻百事哀。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('199', 'poem', '唐', '李白', '山中问答', '问余何意栖碧山，笑而不答心自闲。\\n桃花流水窅然去，别有天地非人间。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('200', 'poem', '唐', '杜甫', '孤雁 / 后飞雁', '孤雁不饮啄，飞鸣声念群。\\n谁怜一片影，相失万重云？\\n望尽似犹见，哀多如更闻。\\n野鸦无意绪，鸣噪自纷纷。', '2018-03-09 14:56:44');
INSERT INTO `t_poem` VALUES ('201', 'poem', '唐', '李白', '春思', '燕草如碧丝，秦桑低绿枝。\\n当君怀归日，是妾断肠时。\\n春风不相识，何事入罗帏。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('202', 'poem', '唐', '杜牧', '赠别二首', '娉娉袅袅十三馀，豆蔻梢头二月初。\\n春风十里扬州路，卷上珠帘总不如。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('203', 'poem', '唐', '李白', '南陵别儿童入京', '白酒新熟山中归，黄鸡啄黍秋正肥。\\n呼童烹鸡酌白酒，儿女嬉笑牵人衣。\\n高歌取醉欲自慰，起舞落日争光辉。\\n游说万乘苦不早，著鞭跨马涉远道。\\n会稽愚妇轻买臣，余亦辞家西入秦。\\n仰天大笑出门去，我辈岂是蓬蒿人。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('204', 'poem', '唐', '李商隐', '霜月', '初闻征雁已无蝉，百尺楼高水接天。\\n青女素娥俱耐冷，月中霜里斗婵娟。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('205', 'poem', '唐', '王勃', '滕王阁诗', '滕王高阁临江渚，佩玉鸣鸾罢歌舞。\\n画栋朝飞南浦云，珠帘暮卷西山雨。\\n闲云潭影日悠悠，物换星移几度秋。\\n阁中帝子今何在？槛外长江空自流。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('206', 'poem', '唐', '郑谷', '淮上与友人别', '扬子江头杨柳春，杨花愁杀渡江人。\\n数声风笛离亭晚，君向潇湘我向秦。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('207', 'poem', '唐', '曹松', '己亥岁二首·僖宗广明元年', '泽国江山入战图，生民何计乐樵苏。\\n凭君莫话封侯事，一将功成万骨枯。\\n传闻一战百神愁，两岸强兵过未休。\\n谁道沧江总无事，近来长共血争流。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('208', 'poem', '唐', '杜牧', '鹭鸶', '雪衣雪发青玉觜，群捕鱼儿溪影中。\\n惊飞远映碧山去，一树梨花落晚风。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('209', 'poem', '唐', '刘禹锡', '竹枝词九首·其九', '山上层层桃李花，云间烟火是人家。\\n银钏金钗来负水，长刀短笠去烧畲。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('210', 'poem', '唐', '李白', '塞下曲六首·其一', '五月天山雪，无花只有寒。\\n笛中闻折柳，春色未曾看。\\n晓战随金鼓，宵眠抱玉鞍。\\n愿将腰下剑，直为斩楼兰。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('211', 'poem', '唐', '李白', '上李邕', '大鹏一日同风起，扶摇直上九万里。\\n假令风歇时下来，犹能簸却沧溟水。\\n世人见我恒殊调，闻余大言皆冷笑。\\n宣父犹能畏后生，丈夫未可轻年少。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('212', 'poem', '唐', '李贺', '金铜仙人辞汉歌', '茂陵刘郎秋风客，夜闻马嘶晓无迹。\\n画栏桂树悬秋香，三十六宫土花碧。\\n魏官牵车指千里，东关酸风射眸子。\\n空将汉月出宫门，忆君清泪如铅水。\\n衰兰送客咸阳道，天若有情天亦老。\\n携盘独出月荒凉，渭城已远波声小。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('213', 'poem', '唐', '杜牧', '赠别', '多情却似总无情，唯觉樽前笑不成。\\n蜡烛有心还惜别，替人垂泪到天明。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('214', 'poem', '唐', '白居易', '南浦别', '南浦凄凄别，西风袅袅秋。\\n一看肠一断，好去莫回头。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('215', 'poem', '唐', '杜牧', '过华清宫绝句三首', '长安回望绣成堆，山顶千门次第开。\\n一骑红尘妃子笑，无人知是荔枝来。\\n新丰绿树起黄埃，数骑渔阳探使回。\\n霓裳一曲千峰上，舞破中原始下来。\\n万国笙歌醉太平，倚天楼殿月分明。\\n云中乱拍禄山舞，风过重峦下笑声。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('216', 'poem', '唐', '杜甫', '佳人', '绝代有佳人，幽居在空谷。\\n自云良家子，零落依草木。\\n关中昔丧败，兄弟遭杀戮。\\n官高何足论，不得收骨肉。\\n世情恶衰歇，万事随转烛。\\n夫婿轻薄儿，新人已如玉。\\n合昏尚知时，鸳鸯不独宿。\\n但见新人笑，那闻旧人哭。\\n在山泉水清，出山泉水浊。\\n侍婢卖珠回，牵萝补茅屋。\\n摘花不插发，采柏动盈掬。\\n天寒翠袖薄，日暮倚修竹。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('217', 'poem', '唐', '高适', '除夜作', '旅馆寒灯独不眠，客心何事转凄然。\\n故乡今夜思千里，愁鬓明朝又一年。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('218', 'poem', '唐', '王维', '汉江临泛 / 汉江临眺', '楚塞三湘接，荆门九派通。\\n江流天地外，山色有无中。\\n郡邑浮前浦，波澜动远空。\\n襄阳好风日，留醉与山翁。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('219', 'poem', '唐', '白居易', '夜雪', '已讶衾枕冷，复见窗户明。\\n夜深知雪重，时闻折竹声。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('220', 'poem', '唐', '杜甫', '赠卫八处士', '人生不相见，动如参与商。\\n今夕复何夕，共此灯烛光。\\n少壮能几时，鬓发各已苍。\\n访旧半为鬼，惊呼热中肠。\\n焉知二十载，重上君子堂。\\n昔别君未婚，儿女忽成行。\\n怡然敬父执，问我来何方。\\n问答乃未已，儿女罗酒浆。\\n夜雨翦春韭，新炊间黄粱。\\n主称会面难，一举累十觞。\\n十觞亦不醉，感子故意长。\\n明日隔山岳，世事两茫茫。', '2018-03-09 14:56:48');
INSERT INTO `t_poem` VALUES ('221', 'poem', '唐', '李商隐', '晚晴', '深居俯夹城，春去夏犹清。\\n天意怜幽草，人间重晚晴。\\n并添高阁迥，微注小窗明。\\n越鸟巢干后，归飞体更轻。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('222', 'poem', '唐', '西鄙人', '哥舒歌', '北斗七星高，哥舒夜带刀。\\n至今窥牧马，不敢过临洮。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('223', 'poem', '唐', '杨炯', '从军行', '烽火照西京，心中自不平。\\n牙璋辞凤阙，铁骑绕龙城。\\n雪暗凋旗画，风多杂鼓声。\\n宁为百夫长，胜作一书生。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('224', 'poem', '唐', '王维', '辋川闲居赠裴秀才迪', '寒山转苍翠，秋水日潺湲。\\n倚杖柴门外，临风听暮蝉。\\n渡头馀落日，墟里上孤烟。\\n复值接舆醉，狂歌五柳前。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('225', 'poem', '唐', '李白', '清平调·其二', '一枝红艳露凝香，云雨巫山枉断肠。\\n借问汉宫谁得似，可怜飞燕倚新妆。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('226', 'poem', '唐', '元稹', '行宫', '寥落古行宫，宫花寂寞红。\\n白头宫女在，闲坐说玄宗。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('227', 'poem', '唐', '王维', '少年行四首', '新丰美酒斗十千，咸阳游侠多少年。\\n相逢意气为君饮，系马高楼垂柳边。\\n汉家君臣欢宴终，高议云台论战功。\\n天子临轩赐侯印，将军佩出明光宫。\\n出身仕汉羽林郎，初随骠骑战渔阳。\\n孰知不向边庭苦，纵死犹闻侠骨香。\\n一身能臂两雕弧，虏骑千群只似无。\\n偏坐金鞍调白羽，纷纷射杀五单于。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('228', 'poem', '唐', '黄巢', '题菊花', '飒飒西风满院栽，蕊寒香冷蝶难来。\\n他年我若为青帝，报与桃花一处开。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('229', 'poem', '唐', '李商隐', '落花', '高阁客竟去，小园花乱飞。\\n参差连曲陌，迢递送斜晖。\\n肠断未忍扫，眼穿仍欲归。\\n芳心向春尽，所得是沾衣。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('230', 'poem', '唐', '杜甫', '春日忆李白', '白也诗无敌，飘然思不群。\\n清新庾开府，俊逸鲍参军。\\n渭北春天树，江东日暮云。\\n何时一尊酒，重与细论文。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('231', 'poem', '唐', '李白', '菩萨蛮·平林漠漠烟如织', '平林漠漠烟如织，寒山一带伤心碧。暝色入高楼，有人楼上愁。\\n玉阶空伫立，宿鸟归飞急。何处是归程？长亭连短亭。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('232', 'poem', '唐', '王建', '十五夜望月寄杜郎中', '中庭地白树栖鸦，冷露无声湿桂花。\\n今夜月明人尽望，不知秋思落谁家。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('233', 'poem', '唐', '孟郊', '劝学', '击石乃有火，不击元无烟。\\n人学始知道，不学非自然。\\n万事须己运，他得非我贤。\\n青春须早为，岂能长少年。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('234', 'poem', '唐', '李隆基', '端午', '端午临中夏，时清日复长。\\n盐梅已佐鼎，曲糵且传觞。\\n事古人留迹，年深缕积长。\\n当轩知槿茂，向水觉芦香。\\n亿兆同归寿，群公共保昌。\\n忠贞如不替，贻厥后昆芳。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('235', 'poem', '唐', '张九龄', '春江晚景', '江林多秀发，云日复相鲜。\\n征路那逢此，春心益渺然。\\n兴来只自得，佳气莫能传。\\n薄暮津亭下，馀花满客船。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('236', 'poem', '唐', '白居易', '遗爱寺', '弄日临溪坐，寻花绕寺行。\\n时时闻鸟语，处处是泉声。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('237', 'poem', '唐', '元稹', '一字至七字诗·茶', '茶，香叶，嫩芽。慕诗客，爱僧家。碾雕白玉，\\n罗织红纱。铫煎黄蕊色，碗转麹尘花。夜后邀陪明月，\\n晨前命对朝霞。洗尽古今人不倦，将知醉后岂堪夸。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('238', 'poem', '唐', '王驾', '社日', '鹅湖山下稻粱肥，豚栅鸡栖半掩扉。\\n桑柘影斜春社散，家家扶得醉人归。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('239', 'poem', '唐', '张籍', '节妇吟·寄东平李司空师道', '君知妾有夫，赠妾双明珠。\\n感君缠绵意，系在红罗襦。\\n妾家高楼连苑起，良人执戟明光里。\\n知君用心如日月，事夫誓拟同生死。\\n还君明珠双泪垂，恨不相逢未嫁时。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('240', 'poem', '唐', '王驾', '雨晴', '雨前初见花间蕊，雨后兼无叶里花。\\n蛱蝶飞来过墙去，却疑春色在邻家。', '2018-03-09 14:56:53');
INSERT INTO `t_poem` VALUES ('241', 'poem', '唐', '王维', '观猎', '风劲角弓鸣，将军猎渭城。\\n草枯鹰眼疾，雪尽马蹄轻。\\n忽过新丰市，还归细柳营。\\n回看射雕处，千里暮云平。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('242', 'poem', '唐', '郑谷', '莲叶', '移舟水溅差差绿，倚槛风摇柄柄香。\\n多谢浣溪人不折，雨中留得盖鸳鸯。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('243', 'poem', '唐', '李白', '访戴天山道士不遇', '犬吠水声中，桃花带雨浓。\\n树深时见鹿，溪午不闻钟。\\n野竹分青霭，飞泉挂碧峰。\\n无人知所去，愁倚两三松。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('244', 'poem', '唐', '孟浩然', '夜归鹿门山歌 / 夜归鹿门歌', '山寺钟鸣昼已昏，渔梁渡头争渡喧。\\n人随沙路向江村，余亦乘舟归鹿门。\\n鹿门月照开烟树，忽到庞公栖隐处。\\n岩扉松径长寂寥，惟有幽人夜来去。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('245', 'poem', '唐', '许浑', '谢亭送别', '劳歌一曲解行舟，红叶青山水急流。\\n日暮酒醒人已远，满天风雨下西楼。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('246', 'poem', '唐', '李白', '秋浦歌十七首', '秋浦长似秋，萧条使人愁。\\n客愁不可度，行上东大楼。\\n正西望长安，下见江水流。\\n寄言向江水，汝意忆侬不。\\n遥传一掬泪，为我达扬州。\\n秋浦猿夜愁，黄山堪白头。\\n清溪非陇水，翻作断肠流。\\n欲去不得去，薄游成久游。\\n何年是归日，雨泪下孤舟。\\n秋浦锦驼鸟，人间天上稀。\\n山鸡羞渌水，不敢照毛衣。\\n两鬓入秋浦，一朝飒已衰。\\n猿声催白发，长短尽成丝。\\n秋浦多白猿，超腾若飞雪。\\n牵引条上儿，饮弄水中月。\\n愁作秋浦客，强看秋浦花。\\n山川如剡县，风日似长沙。\\n醉上山公马，寒歌甯戚牛。\\n空吟白石烂，泪满黑貂裘。\\n秋浦千重岭，水车岭最奇。\\n天倾欲堕石，水拂寄生枝。\\n江祖一片石，青天扫画屏。\\n题诗留万古，绿字锦苔生。\\n千千石楠树，万万女贞林。\\n山山白鹭满，涧涧白猿吟。\\n君莫向秋浦，猿声碎客心。\\n逻人横鸟道，江祖出鱼梁。\\n水急客舟疾，山花拂面香。\\n水如一匹练，此地即平天。\\n耐可乘明月，看花上酒船。\\n渌水净素月，月明白鹭飞。\\n郎听采菱女，一道夜歌归。\\n炉火照天地，红星乱紫烟。\\n赧郎明月夜，歌曲动寒川。\\n白发三千丈，缘愁似个长。\\n不知明镜里，何处得秋霜。\\n秋浦田舍翁，采鱼水中宿。\\n妻子张白鹇，结罝映深竹。\\n桃波一步地，了了语声闻。\\n暗与山僧别，低头礼白云。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('247', 'poem', '唐', '柳宗元', '登柳州城楼寄漳汀封连四州', '城上高楼接大荒，海天愁思正茫茫。\\n惊风乱飐芙蓉水，密雨斜侵薜荔墙。\\n岭树重遮千里目，江流曲似九回肠。\\n共来百越文身地，犹自音书滞一乡。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('248', 'poem', '唐', '李白', '把酒问月·故人贾淳令予问之', '青天有月来几时，我今停杯一问之。\\n人攀明月不可得，月行却与人相随。\\n皎如飞镜临丹阙，绿烟灭尽清辉发。\\n但见宵从海上来，宁知晓向云间没。\\n白兔捣药秋复春，嫦娥孤栖与谁邻。\\n今人不见古时月，今月曾经照古人。\\n古人今人若流水，共看明月皆如此。\\n唯愿当歌对酒时，月光长照金樽里。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('249', 'poem', '唐', '杜甫', '羌村三首·其三', '群鸡正乱叫，客至鸡斗争。\\n驱鸡上树木，始闻叩柴荆。\\n父老四五人，问我久远行。\\n手中各有携，倾榼浊复清。\\n“莫辞酒味薄，黍地无人耕。\\n兵戈既未息，儿童尽东征。”\\n请为父老歌，艰难愧深情。\\n歌罢仰天叹，四座泪纵横。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('250', 'poem', '唐', '李白', '与史郎中钦听黄鹤楼上吹笛 / 黄鹤楼闻笛', '一为迁客去长沙，西望长安不见家。\\n黄鹤楼中吹玉笛，江城五月落梅花。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('251', 'poem', '唐', '王之涣', '送别', '杨柳东风树，青青夹御河。\\n近来攀折苦，应为别离多。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('252', 'poem', '唐', '李约', '过华清宫', '君王游乐万机轻，一曲霓裳四海兵。\\n玉辇升天人已尽，故宫犹有树长生。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('253', 'poem', '唐', '贺知章', '回乡偶书二首', '少小离乡老大回，乡音难改鬓毛衰。\\n儿童相见不相识，笑问客从何处来。\\n离别家乡岁月多，近来人事半销磨。\\n唯有门前镜湖水，春风不改旧时波。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('254', 'poem', '唐', '王维', '送梓州李使君', '万壑树参天，千山响杜鹃。\\n山中一夜雨，树杪百重泉。\\n汉女输橦布，巴人讼芋田。\\n文翁翻教授，不敢倚先贤。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('255', 'poem', '唐', '韦庄', '稻田', '绿波春浪满前陂，极目连云罢亚肥。\\n更被鹭鹚千点雪，破烟来入画屏飞。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('256', 'poem', '唐', '江采萍', '谢赐珍珠', '桂叶双眉久不描，残妆和泪污红绡。\\n长门尽日无梳洗，何必珍珠慰寂寥。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('257', 'poem', '唐', '杜甫', '曲江二首', '一片花飞减却春，风飘万点正愁人。\\n且看欲尽花经眼，莫厌伤多酒入唇。\\n江上小堂巢翡翠，花边高冢卧麒麟。\\n细推物理须行乐，何用浮名绊此身。\\n朝回日日典春衣，每日江头尽醉归。\\n酒债寻常行处有，人生七十古来稀。\\n穿花蛱蝶深深见，点水蜻蜓款款飞。\\n传语风光共流转，暂时相赏莫相违。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('258', 'poem', '唐', '牟融', '写意二首', '寂寥荒馆闭闲门，苔径阴阴屐少痕。白发颠狂尘梦断，\\n青毡泠落客心存。高山流水琴三弄，明月清风酒一樽。\\n醉后曲肱林下卧，此生荣辱不须论。\\n萧萧华发满头生，深远蓬门倦送迎。独喜冥心无外慕，\\n自怜知命不求荣。闲情欲赋思陶令。卧病何人问马卿。\\n林下贫居甘困守，尽教城市不知名。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('259', 'poem', '唐', '王之涣', '凉州词二首', '黄河远上白云间，一片孤城万仞山。\\n羌笛何须怨杨柳，春风不度玉门关。\\n单于北望拂云堆，杀马登坛祭几回。\\n汉家天子今神武，不肯和亲归去来。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('260', 'poem', '唐', '李益', '写情', '水纹珍簟思悠悠，千里佳期一夕休。\\n从此无心爱良夜，任他明月下西楼。', '2018-03-09 14:57:02');
INSERT INTO `t_poem` VALUES ('261', 'poem', '唐', '杜甫', '江畔独步寻花七绝句', '江上被花恼不彻，无处告诉只颠狂。\\n走觅南邻爱酒伴，经旬出饮独空床。\\n稠花乱蕊畏江滨，行步欹危实怕春。\\n诗酒尚堪驱使在，未须料理白头人。\\n江深竹静两三家，多事红花映白花。\\n报答春光知有处，应须美酒送生涯。\\n东望少城花满烟，百花高楼更可怜。\\n谁能载酒开金盏，唤取佳人舞绣筵。\\n黄师塔前江水东，春光懒困倚微风。\\n桃花一簇开无主，可爱深红爱浅红。\\n黄四娘家花满蹊，千朵万朵压枝低。\\n留连戏蝶时时舞，自在娇莺恰恰啼。\\n不是爱花即肯死，只恐花尽老相催。\\n繁枝容易纷纷落，嫩叶商量细细开。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('262', 'poem', '唐', '李商隐', '马嵬（其二）', '海外徒闻更九州，他生未卜此生休。\\n空闻虎旅传宵柝，无复鸡人报晓筹。\\n此日六军同驻马，当时七夕笑牵牛。\\n如何四纪为天子，不及卢家有莫愁。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('263', 'poem', '唐', '杜牧', '九日齐山登高', '江涵秋影雁初飞，与客携壶上翠微。\\n尘世难逢开口笑，菊花须插满头归。\\n但将酩酊酬佳节，不用登临恨落晖。\\n古往今来只如此，牛山何必独沾衣。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('264', 'poem', '唐', '韦庄', '台城', '江雨霏霏江草齐，六朝如梦鸟空啼。\\n无情最是台城柳，依旧烟笼十里堤。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('265', 'poem', '唐', '王建', '新嫁娘词', '三日入厨下，洗手作羹汤。\\n未谙姑食性，先遣小姑尝。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('266', 'poem', '唐', '李商隐', '无题·飒飒东风细雨来', '飒飒东风细雨来，芙蓉塘外有轻雷。\\n金蟾啮锁烧香入，玉虎牵丝汲井回。\\n贾氏窥帘韩掾少，宓妃留枕魏王才。\\n春心莫共花争发，一寸相思一寸灰！', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('267', 'poem', '唐', '李商隐', '无题二首', '昨夜星辰昨夜风，画楼西畔桂堂东。\\n身无彩凤双飞翼，心有灵犀一点通。\\n隔座送钩春酒暖，分曹射覆蜡灯红。\\n嗟余听鼓应官去，走马兰台类转蓬。\\n闻道阊门萼绿华，昔年相望抵天涯。\\n岂知一夜秦楼客，偷看吴王苑内花。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('268', 'poem', '唐', '郑谷', '菊', '王孙莫把比蓬蒿，九日枝枝近鬓毛。\\n露湿秋香满池岸，由来不羡瓦松高。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('269', 'poem', '唐', '宣宗宫人', '题红叶', '流水何太急，深宫尽日闲。\\n殷勤谢红叶，好去到人间。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('270', 'poem', '唐', '李白', '夏日山中', '懒摇白羽扇，裸体青林中。\\n脱巾挂石壁，露顶洒松风。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('271', 'poem', '唐', '李白', '宣城见杜鹃花 / 子规', '蜀国曾闻子规鸟，宣城还见杜鹃花。\\n一叫一回肠一断，三春三月忆三巴。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('272', 'poem', '唐', '王维', '送别', '下马饮君酒，问君何所之？\\n君言不得意，归卧南山陲。\\n但去莫复问，白云无尽时。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('273', 'poem', '唐', '卢照邻', '长安古意', '长安大道连狭斜，青牛白马七香车。\\n玉辇纵横过主第，金鞭络绎向侯家。\\n龙衔宝盖承朝日，凤吐流苏带晚霞。\\n百尺游丝争绕树，一群娇鸟共啼花。\\n游蜂戏蝶千门侧，碧树银台万种色。\\n复道交窗作合欢，双阙连甍垂凤翼。\\n梁家画阁中天起，汉帝金茎云外直。\\n楼前相望不相知，陌上相逢讵相识？\\n借问吹箫向紫烟，曾经学舞度芳年。\\n得成比目何辞死，愿作鸳鸯不羡仙。\\n比目鸳鸯真可羡，双去双来君不见？\\n生憎帐额绣孤鸾，好取门帘帖双燕。\\n双燕双飞绕画梁，罗帷翠被郁金香。\\n片片行云着蝉鬓，纤纤初月上鸦黄。\\n鸦黄粉白车中出，含娇含态情非一。\\n妖童宝马铁连钱，娼妇盘龙金屈膝。\\n御史府中乌夜啼，廷尉门前雀欲栖。\\n隐隐朱城临玉道，遥遥翠幰没金堤。\\n挟弹飞鹰杜陵北，探丸借客渭桥西。\\n俱邀侠客芙蓉剑，共宿娼家桃李蹊。\\n娼家日暮紫罗裙，清歌一啭口氛氲。\\n北堂夜夜人如月，南陌朝朝骑似云。\\n南陌北堂连北里，五剧三条控三市。\\n弱柳青槐拂地垂，佳气红尘暗天起。\\n汉代金吾千骑来，翡翠屠苏鹦鹉杯。\\n罗襦宝带为君解，燕歌赵舞为君开。\\n别有豪华称将相，转日回天不相让。\\n意气由来排灌夫，专权判不容萧相。\\n专权意气本豪雄，青虬紫燕坐春风。\\n自言歌舞长千载，自谓骄奢凌五公。\\n节物风光不相待，桑田碧海须臾改。\\n昔时金阶白玉堂，即今惟见青松在。\\n寂寂寥寥扬子居，年年岁岁一床书。\\n独有南山桂花发，飞来飞去袭人裾。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('274', 'poem', '唐', '李商隐', '春雨', '怅卧新春白袷衣，白门寥落意多违。\\n红楼隔雨相望冷，珠箔飘灯独自归。\\n远路应悲春晼晚，残宵犹得梦依稀。\\n玉珰缄札何由达，万里云罗一雁飞。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('275', 'poem', '唐', '杜甫', '江汉', '江汉思归客，乾坤一腐儒。\\n片云天共远，永夜月同孤。\\n落日心犹壮，秋风病欲疏。\\n古来存老马，不必取长途。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('276', 'poem', '唐', '无名氏', '白雪歌', '皇穹何处飞琼屑，散下人间作春雪。\\n五花马踏白云衢，七香车碾瑶墀月。\\n苏岩乳洞拥山家，涧藤古栗盘银蛇。\\n寒郊复叠铺柳絮，古碛烂熳吹芦花。\\n流泉不下孤汀咽，断臂老猿声欲绝。\\n鸟啄冰潭玉镜开，风敲檐溜水晶折。\\n拂户初疑粉蝶飞，看山又讶白鸥归。\\n孙康冻死读书闱，火井不暖温泉微。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('277', 'poem', '唐', '罗隐', '春居', '春风百卉摇，旧国路迢迢。\\n偶病成疏散，因贫得寂寥。\\n倚帘高柳弱，乘露小桃夭。\\n春色常无处，村醪更一瓢。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('278', 'poem', '唐', '孟浩然', '与诸子登岘山', '人事有代谢，往来成古今。\\n江山留胜迹，我辈复登临。\\n水落鱼梁浅，天寒梦泽深。\\n羊公碑字在，读罢泪沾襟。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('279', 'poem', '唐', '杜甫', '自京赴奉先县咏怀五百字 / 自京赴奉先咏怀五百字', '杜陵有布衣，老大意转拙。\\n许身一何愚，窃比稷与契。\\n居然成濩落，白首甘契阔。\\n盖棺事则已，此志常觊豁。\\n穷年忧黎元，叹息肠内热。\\n取笑同学翁，浩歌弥激烈。\\n非无江海志，潇洒送日月。\\n生逢尧舜君，不忍便永诀。\\n当今廊庙具，构厦岂云缺。\\n葵藿倾太阳，物性固莫夺。\\n顾惟蝼蚁辈，但自求其穴。\\n胡为慕大鲸，辄拟偃溟渤。\\n以兹悟生理，独耻事干谒。\\n兀兀遂至今，忍为尘埃没。\\n终愧巢与由，未能易其节。\\n沈饮聊自适，放歌颇愁绝。\\n岁暮百草零，疾风高冈裂。\\n天衢阴峥嵘，客子中夜发。\\n霜严衣带断，指直不得结。\\n凌晨过骊山，御榻在嵽嵲。\\n蚩尤塞寒空，蹴蹋崖谷滑。\\n瑶池气郁律，羽林相摩戛。\\n君臣留欢娱，乐动殷樛嶱。\\n赐浴皆长缨，与宴非短褐。\\n彤庭所分帛，本自寒女出。\\n鞭挞其夫家，聚敛贡城阙。\\n圣人筐篚恩，实欲邦国活。\\n臣如忽至理，君岂弃此物。\\n多士盈朝廷，仁者宜战栗。\\n况闻内金盘，尽在卫霍室。\\n中堂舞神仙，烟雾散玉质。\\n暖客貂鼠裘，悲管逐清瑟。\\n劝客驼蹄羹，霜橙压香橘。\\n朱门酒肉臭，路有冻死骨。\\n荣枯咫尺异，惆怅难再述。\\n北辕就泾渭，官渡又改辙。\\n群冰从西下，极目高崒兀。\\n疑是崆峒来，恐触天柱折。\\n河梁幸未坼，枝撑声窸窣。\\n行旅相攀援，川广不可越。\\n老妻寄异县，十口隔风雪。\\n谁能久不顾，庶往共饥渴。\\n入门闻号咷，幼子饥已卒。\\n吾宁舍一哀，里巷亦呜咽。\\n所愧为人父，无食致夭折。\\n岂知秋未登，贫窭有仓卒。\\n生常免租税，名不隶征伐。\\n抚迹犹酸辛，平人固骚屑。\\n默思失业徒，因念远戍卒。\\n忧端齐终南，澒洞不可掇。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('280', 'poem', '唐', '李白', '公无渡河', '黄河西来决昆仑，咆哮万里触龙门。\\n波滔天，尧咨嗟。\\n大禹理百川，儿啼不窥家。\\n杀湍湮洪水，九州始蚕麻。\\n其害乃去，茫然风沙。\\n被发之叟狂而痴，清晨临流欲奚为。\\n旁人不惜妻止之，公无渡河苦渡之。\\n虎可搏，河难凭，公果溺死流海湄。\\n有长鲸白齿若雪山，公乎公乎挂罥于其间。\\n箜篌所悲竟不还。', '2018-03-09 14:57:06');
INSERT INTO `t_poem` VALUES ('281', 'poem', '唐', '李白', '玉阶怨', '玉阶生白露，夜久侵罗袜。\\n却下水晶帘，玲珑望秋月。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('282', 'poem', '唐', '李商隐', '流莺', '流莺漂荡复参差，渡陌临流不自持。\\n巧啭岂能无本意，良辰未必有佳期。\\n风朝露夜阴晴里，万户千门开闭时。\\n曾苦伤春不忍听，凤城何处有花枝。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('283', 'poem', '唐', '李白', '山中与幽人对酌', '两人对酌山花开，一杯一杯复一杯。\\n我醉欲眠卿且去，明朝有意抱琴来。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('284', 'poem', '唐', '李白', '长干行·其一', '妾发初覆额，折花门前剧。\\n郎骑竹马来，绕床弄青梅。\\n同居长干里，两小无嫌猜，\\n十四为君妇，羞颜未尝开。\\n低头向暗壁，千唤不一回。\\n十五始展眉，愿同尘与灰。\\n常存抱柱信，岂上望夫台。\\n十六君远行，瞿塘滟滪堆。\\n五月不可触，猿声天上哀。\\n门前迟行迹，一一生绿苔。\\n苔深不能扫，落叶秋风早。\\n八月蝴蝶黄，双飞西园草。\\n感此伤妾心，坐愁红颜老。\\n早晚下三巴，预将书报家。\\n相迎不道远，直至长风沙。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('285', 'poem', '唐', '徐凝', '春雨', '花时闷见联绵雨，云入人家水毁堤。\\n昨日春风源上路，可怜红锦枉抛泥。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('286', 'poem', '唐', '孟浩然', '清明即事', '帝里重清明，人心自愁思。\\n车声上路合，柳色东城翠。\\n花落草齐生，莺飞蝶双戏。\\n空堂坐相忆，酌茗聊代醉。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('287', 'poem', '唐', '刘禹锡', '再游玄都观', '百亩庭中半是苔，桃花净尽菜花开。\\n种桃道士归何处，前度刘郎今又来。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('288', 'poem', '唐', '刘禹锡', '元和十年自朗州至京戏赠看花诸君子 / 玄都观桃花', '紫陌红尘拂面来，无人不道看花回。\\n玄都观里桃千树，尽是刘郎去后栽。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('289', 'poem', '唐', '李商隐', '一片', '一片非烟隔九枝，蓬峦仙仗俨云旗。\\n天泉水暖龙吟细，露畹春多凤舞迟。\\n榆荚散来星斗转，桂花寻去月轮移。\\n人间桑海朝朝变，莫遣佳期更后期。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('290', 'poem', '唐', '李颀', '古从军行', '白日登山望烽火，黄昏饮马傍交河。\\n行人刁斗风沙暗，公主琵琶幽怨多。\\n野云万里无城郭，雨雪纷纷连大漠。\\n胡雁哀鸣夜夜飞，胡儿眼泪双双落。\\n闻道玉门犹被遮，应将性命逐轻车。\\n年年战骨埋荒外，空见蒲桃入汉家。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('291', 'poem', '唐', '张谓', '早梅', '一树寒梅白玉条，迥临林村傍溪桥。\\n不知近水花先发，疑是经冬雪未销。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('292', 'poem', '唐', '刘禹锡', '秋词二首', '自古逢秋悲寂寥，我言秋日胜春朝。\\n晴空一鹤排云上，便引诗情到碧霄。\\n山明水净夜来霜，数树深红出浅黄。\\n试上高楼清入骨，岂如春色嗾人狂。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('293', 'poem', '唐', '杜甫', '天末怀李白', '凉风起天末，君子意如何。\\n鸿雁几时到，江湖秋水多。\\n文章憎命达，魑魅喜人过。\\n应共冤魂语，投诗赠汨罗。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('294', 'poem', '唐', '武则天', '如意娘', '看朱成碧思纷纷，憔悴支离为忆君。\\n不信比来长下泪，开箱验取石榴裙。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('295', 'poem', '唐', '罗隐', '雪', '尽道丰年瑞，丰年事若何。\\n长安有贫者，为瑞不宜多。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('296', 'poem', '唐', '杜牧', '金谷园', '繁华事散逐香尘，流水无情草自春。\\n日暮东风怨啼鸟，落花犹似堕楼人。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('297', 'poem', '唐', '刘方平', '春怨', '纱窗日落渐黄昏，金屋无人见泪痕。\\n寂寞空庭春欲晚，梨花满地不开门。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('298', 'poem', '唐', '李白', '秋登宣城谢脁北楼', '江城如画里，山晓望晴空。\\n两水夹明镜，双桥落彩虹。\\n人烟寒橘柚，秋色老梧桐。\\n谁念北楼上，临风怀谢公。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('299', 'poem', '唐', '骆宾王', '于易水送人 / 于易水送别', '此地别燕丹，壮士发冲冠。\\n昔时人已没，今日水犹寒。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('300', 'poem', '唐', '杜甫', '戏为六绝句·其二', '王杨卢骆当时体，轻薄为文哂未休。\\n尔曹身与名俱灭，不废江河万古流。', '2018-03-09 14:57:10');
INSERT INTO `t_poem` VALUES ('301', 'poem', '唐', '李白', '金陵酒肆留别', '风吹柳花满店香，吴姬压酒唤客尝。\\n金陵子弟来相送，欲行不行各尽觞。\\n请君试问东流水，别意与之谁短长。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('302', 'poem', '唐', '文秀', '端午', '节分端午自谁言，万古传闻为屈原。\\n堪笑楚江空渺渺，不能洗得直臣冤。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('303', 'poem', '唐', '刘禹锡', '秋风引', '何处秋风至？萧萧送雁群。\\n朝来入庭树，孤客最先闻。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('304', 'poem', '唐', '杜甫', '月夜', '今夜鄜州月，闺中只独看。\\n遥怜小儿女，未解忆长安。\\n香雾云鬟湿，清辉玉臂寒。\\n何时倚虚幌，双照泪痕干。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('305', 'poem', '唐', '徐凝', '牡丹', '何人不爱牡丹花，占断城中好物华。\\n疑是洛川神女作，千娇万态破朝霞。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('306', 'poem', '唐', '温庭筠', '梦江南·千万恨', '千万恨，恨极在天涯。山月不知心里事，水风空落眼前花，摇曳碧云斜。\\n梳洗罢，独倚望江楼。过尽千帆皆不是，斜晖脉脉水悠悠，肠断白苹洲。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('307', 'poem', '唐', '张旭', '山中留客', '山光物态弄春晖，莫为轻阴便拟归。\\n纵使晴明无雨色，入云深处亦沾衣。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('308', 'poem', '唐', '李白', '怨情', '美人卷珠帘，深坐颦蛾眉。\\n但见泪痕湿，不知心恨谁。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('309', 'poem', '唐', '杜甫', '江村', '清江一曲抱村流，长夏江村事事幽。\\n自去自来堂上燕，相亲相近水中鸥。\\n老妻画纸为棋局，稚子敲针作钓钩。\\n多病所须唯药物，微躯此外更何求。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('310', 'poem', '唐', '贾岛', '题诗后', '两句三年得，一吟双泪流。\\n知音如不赏，归卧故山秋。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('311', 'poem', '唐', '高适', '塞上听吹笛', '雪净胡天牧马还，月明羌笛戍楼间。\\n借问梅花何处落，风吹一夜满关山。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('312', 'poem', '唐', '赵嘏', '江楼旧感 / 江楼感旧', '独上江楼思渺然，月光如水水如天。\\n同来望月人何处，风影依稀似去年。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('313', 'poem', '唐', '皎然', '寻陆鸿渐不遇', '移家虽带郭，野径入桑麻。\\n近种篱边菊，秋来未著花。\\n扣门无犬吠，欲去问西家。\\n报道山中去，归时每日斜。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('314', 'poem', '唐', '崔道融', '溪居即事', '篱外谁家不系船，春风吹入钓鱼湾。\\n小童疑是有村客，急向柴门去却关。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('315', 'poem', '唐', '元稹', '酬乐天频梦微之', '山水万重书断绝，念君怜我梦相闻。\\n我今因病魂颠倒，唯梦闲人不梦君。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('316', 'poem', '唐', '李白', '赠孟浩然', '吾爱孟夫子，风流天下闻。\\n红颜弃轩冕，白首卧松云。\\n醉月频中圣，迷花不事君。\\n高山安可仰，徒此揖清芬。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('317', 'poem', '唐', '杜甫', '赠李白', '秋来相顾尚飘蓬，未就丹砂愧葛洪。\\n痛饮狂歌空度日，飞扬跋扈为谁雄。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('318', 'poem', '唐', '元稹', '离思五首', '自爱残妆晓镜中，环钗漫篸绿丝丛。\\n须臾日射燕脂颊，一朵红苏旋欲融。\\n山泉散漫绕阶流，万树桃花映小楼。\\n闲读道书慵未起，水晶帘下看梳头。\\n红罗著压逐时新，吉了花纱嫩麹尘。\\n第一莫嫌材地弱，些些纰缦最宜人。\\n曾经沧海难为水，除却巫山不是云。\\n取次花丛懒回顾，半缘修道半缘君。\\n寻常百种花齐发，偏摘梨花与白人。\\n今日江头两三树，可怜和叶度残春。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('319', 'poem', '唐', '杜甫', '阁夜', '岁暮阴阳催短景，天涯霜雪霁寒宵。\\n五更鼓角声悲壮，三峡星河影动摇。\\n野哭几家闻战伐，夷歌数处起渔樵。\\n卧龙跃马终黄土，人事依依漫寂寥。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('320', 'poem', '唐', '李白', '听蜀僧濬弹琴 / 听蜀僧浚弹琴', '蜀僧抱绿绮，西下峨眉峰。\\n为我一挥手，如听万壑松。\\n客心洗流水，馀响入霜钟。\\n不觉碧山暮，秋云暗几重。', '2018-03-09 14:57:22');
INSERT INTO `t_poem` VALUES ('321', 'poem', '唐', '杜甫', '江上值水如海势聊短述', '为人性僻耽佳句，语不惊人死不休。\\n老去诗篇浑漫与，春来花鸟莫深愁。\\n新添水槛供垂钓，故着浮槎替入舟。\\n焉得思如陶谢手，令渠述作与同游。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('322', 'poem', '唐', '张九龄', '感遇十二首·其一', '兰叶春葳蕤，桂华秋皎洁。\\n欣欣此生意，自尔为佳节。\\n谁知林栖者，闻风坐相悦。\\n草木有本心，何求美人折！', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('323', 'poem', '唐', '孟浩然', '岁暮归南山 / 归故园作 / 归终南山', '北阙休上书，南山归敝庐。\\n不才明主弃，多病故人疏。\\n白发催年老，青阳逼岁除。\\n永怀愁不寐，松月夜窗虚。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('324', 'poem', '唐', '陆龟蒙', '别离', '丈夫非无泪，不洒离别间。\\n杖剑对尊酒，耻为游子颜。\\n蝮蛇一螫手，壮士即解腕。\\n所志在功名，离别何足叹。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('325', 'poem', '唐', '戴叔伦', '泛舟', '风软扁舟稳，行依绿水堤。\\n孤尊秋露滑，短棹晚烟迷。\\n夜静月初上，江空天更低。\\n飘飘信流去，误过子猷溪。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('326', 'poem', '唐', '岑参', '行军九日思长安故园', '强欲登高去，无人送酒来。\\n遥怜故园菊，应傍战场开。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('327', 'poem', '唐', '李贺', '苏小小墓', '幽兰露，如啼眼。无物结同心，烟花不堪剪。草如茵，\\n松如盖。风为裳，水为珮。油壁车，夕相待。冷翠烛，\\n劳光彩。西陵下，风吹雨。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('328', 'poem', '唐', '王维', '渭川田家', '斜阳照墟落，穷巷牛羊归。\\n野老念牧童，倚杖候荆扉。\\n雉雊麦苗秀，蚕眠桑叶稀。\\n田夫荷锄至，相见语依依。\\n即此羡闲逸，怅然吟式微。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('329', 'poem', '唐', '李商隐', '安定城楼', '迢递高城百尺楼，绿杨枝外尽汀洲。\\n贾生年少虚垂泪，王粲春来更远游。\\n永忆江湖归白发，欲回天地入扁舟。\\n不知腐鼠成滋味，猜意鹓雏竟未休。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('330', 'poem', '唐', '白居易', '句', '慈恩塔下题名处，十七人中最少年。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('331', 'poem', '唐', '元稹', '说剑', '吾友有宝剑，密之如密友。\\n我实胶漆交，中堂共杯酒。\\n酒酣肝胆露，恨不眼前剖。\\n高唱荆卿歌，乱击相如缶。\\n更击复更唱，更酌亦更寿。\\n白虹坐上飞，青蛇匣中吼。\\n我闻音响异，疑是干将偶。\\n为君再拜言，神物可见不。\\n君言我所重，我自为君取。\\n迎箧已焚香，近鞘先泽手。\\n徐抽寸寸刃，渐屈弯弯肘。\\n杀杀霜在锋，团团月临纽。\\n逡巡潜虬跃，郁律惊左右。\\n霆电满室光，蛟龙绕身走。\\n我为捧之泣，此剑别来久。\\n铸时近山破，藏在松桂朽。\\n幽匣狱底埋，神人水心守。\\n本是稽泥淬，果非雷焕有。\\n我欲评剑功，愿君良听受。\\n剑可剸犀兕，剑可切琼玖。\\n剑决天外云，剑冲日中斗。\\n剑隳妖蛇腹，剑拂佞臣首。\\n太古初断鳌，武王亲击纣。\\n燕丹卷地图，陈平绾花绶。\\n曾被桂树枝，寒光射林薮。\\n曾经铸农器，利用翦稂莠。\\n神物终变化，复为龙牝牡。\\n晋末武库烧，脱然排户牖。\\n为欲扫群胡，散作弥天帚。\\n自兹失所往，豪英共为诟。\\n今复谁人铸，挺然千载后。\\n既非古风胡，无乃近鸦九。\\n自我与君游，平生益自负。\\n况擎宝剑出，重以雄心扣。\\n此剑何太奇，此心何太厚。\\n劝君慎所用，所用无或苟。\\n潜将辟魑魅，勿但防妾妇。\\n留斩泓下蛟，莫试街中狗。\\n君今困泥滓，我亦坌尘垢。\\n俗耳惊大言，逢人少开口。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('332', 'poem', '唐', '王昌龄', '从军行七首', '烽火城西百尺楼，黄昏独上海风秋。\\n更吹羌笛关山月，无那金闺万里愁。\\n琵琶起舞换新声，总是关山旧别情。\\n撩乱边愁听不尽，高高秋月照长城。\\n关城榆叶早疏黄，日暮云沙古战场。\\n表请回军掩尘骨，莫教兵士哭龙荒。\\n青海长云暗雪山，孤城遥望玉门关。\\n黄沙百战穿金甲，不破楼兰终不还。\\n大漠风尘日色昏，红旗半卷出辕门。\\n前军夜战洮河北，已报生擒吐谷浑。\\n胡瓶落膊紫薄汗，碎叶城西秋月团。\\n明敕星驰封宝剑，辞君一夜取楼兰。\\n玉门山嶂几千重，山北山南总是烽。\\n人依远戍须看火，马踏深山不见踪。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('333', 'poem', '唐', '白居易', '邯郸冬至夜思家', '邯郸驿里逢冬至，抱膝灯前影伴身。\\n想得家中夜深坐，还应说着远行人。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('334', 'poem', '唐', '高适', '送李少府贬峡中王少府贬长沙', '嗟君此别意何如，驻马衔杯问谪居。\\n巫峡啼猿数行泪，衡阳归雁几封书。\\n青枫江上秋帆远，白帝城边古木疏。\\n圣代即今多雨露，暂时分手莫踌躇。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('335', 'poem', '唐', '白居易', '放言五首·其三', '赠君一法决狐疑，不用钻龟与祝蓍。\\n试玉要烧三日满，辨材须待七年期。\\n周公恐惧流言日，王莽谦恭未篡时。\\n向使当初身便死，一生真伪复谁知？', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('336', 'poem', '唐', '韩愈', '山石', '山石荦确行径微，黄昏到寺蝙蝠飞。\\n升堂坐阶新雨足，芭蕉叶大支子肥。\\n僧言古壁佛画好，以火来照所见稀。\\n铺床拂席置羹饭，疏粝亦足饱我饥。\\n夜深静卧百虫绝，清月出岭光入扉。\\n天明独去无道路，出入高下穷烟霏。\\n山红涧碧纷烂漫，时见松枥皆十围。\\n当流赤足踏涧石，水声激激风吹衣。\\n人生如此自可乐，岂必局束为人鞿。\\n嗟哉吾党二三子，安得至老不更归。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('337', 'poem', '唐', '白居易', '采莲曲', '菱叶萦波荷飐风，荷花深处小船通。\\n逢郎欲语低头笑，碧玉搔头落水中。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('338', 'poem', '唐', '李商隐', '悼伤后赴东蜀辟至散关遇雪', '剑外从军远，无家与寄衣。\\n散关三尺雪，回梦旧鸳机。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('339', 'poem', '唐', '李白', '庐山谣寄卢侍御虚舟', '我本楚狂人，凤歌笑孔丘。\\n手持绿玉杖，朝别黄鹤楼。\\n五岳寻仙不辞远，一生好入名山游。\\n庐山秀出南斗傍，屏风九叠云锦张。\\n影落明湖青黛光，金阙前开二峰长，银河倒挂三石梁。\\n香炉瀑布遥相望，回崖沓嶂凌苍苍。\\n翠影红霞映朝日，鸟飞不到吴天长。\\n登高壮观天地间，大江茫茫去不还。\\n黄云万里动风色，白波九道流雪山。\\n好为庐山谣，兴因庐山发。\\n闲窥石镜清我心，谢公行处苍苔没。\\n早服还丹无世情，琴心三叠道初成。\\n遥见仙人彩云里，手把芙蓉朝玉京。\\n先期汗漫九垓上，愿接卢敖游太清。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('340', 'poem', '唐', '黄櫱禅师', '上堂开示颂', '尘劳迥脱事非常，紧把绳头做一场。\\n不经一番寒彻骨，怎得梅花扑鼻香。', '2018-03-09 14:57:32');
INSERT INTO `t_poem` VALUES ('341', 'poem', '唐', '刘长卿', '新年作', '乡心新岁切，天畔独潸然。\\n老至居人下，春归在客先。\\n岭猿同旦暮，江柳共风烟。\\n已似长沙傅，从今又几年。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('342', 'poem', '唐', '韦应物', '秋夜寄邱员外 / 秋夜寄丘二十二员外', '怀君属秋夜，散步咏凉天。\\n空山松子落，幽人应未眠。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('343', 'poem', '唐', '李商隐', '无题·重帏深下莫愁堂', '重帏深下莫愁堂，卧后清宵细细长。\\n神女生涯原是梦，小姑居处本无郎。\\n风波不信菱枝弱，月露谁教桂叶香。\\n直道相思了无益，未妨惆怅是清狂。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('344', 'poem', '唐', '杜甫', '水槛遣心二首', '去郭轩楹敞，无村眺望赊。\\n澄江平少岸，幽树晚多花。\\n细雨鱼儿出，微风燕子斜。\\n城中十万户，此地两三家。\\n蜀天常夜雨，江槛已朝晴。\\n叶润林塘密，衣干枕席清。\\n不堪祗老病，何得尚浮名。\\n浅把涓涓酒，深凭送此生。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('345', 'poem', '唐', '苏味道', '正月十五夜', '火树银花合，星桥铁锁开。\\n暗尘随马去，明月逐人来。\\n游伎皆秾李，行歌尽落梅。\\n金吾不禁夜，玉漏莫相催。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('346', 'poem', '唐', '李贺', '梦天', '老兔寒蟾泣天色，云楼半开壁斜白。\\n玉轮轧露湿团光，鸾珮相逢桂香陌。\\n黄尘清水三山下，更变千年如走马。\\n遥望齐州九点烟，一泓海水杯中泻。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('347', 'poem', '唐', '杜甫', '狂夫', '万里桥西一草堂，百花潭水即沧浪。\\n风含翠篠娟娟净，雨裛红蕖冉冉香。\\n厚禄故人书断绝，恒饥稚子色凄凉。\\n欲填沟壑唯疏放，自笑狂夫老更狂。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('348', 'poem', '唐', '李商隐', '寄令狐郎中', '嵩云秦树久离居，双鲤迢迢一纸书。\\n休问梁园旧宾客，茂陵秋雨病相如。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('349', 'poem', '唐', '李贺', '苦昼短', '飞光飞光，劝尔一杯酒。吾不识青天高，黄地厚，\\n唯见月寒日暖，来煎人寿。食熊则肥，食蛙则瘦。\\n神君何在，太一安有。天东有若木，下置衔烛龙。\\n吾将斩龙足，嚼龙肉。使之朝不得回，夜不得伏。\\n自然老者不死，少者不哭。何为服黄金，吞白玉。\\n谁似任公子，云中骑碧驴。刘彻茂陵多滞骨，\\n嬴政梓棺费鲍鱼。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('350', 'poem', '唐', '杜甫', '秋兴八首', '玉露凋伤枫树林，巫山巫峡气萧森。\\n江间波浪兼天涌，塞上风云接地阴。\\n丛菊两开他日泪，孤舟一系故园心。\\n寒衣处处催刀尺，白帝城高急暮砧。\\n夔府孤城落日斜，每依南斗望京华。\\n听猿实下三声泪，奉使虚随八月查。\\n画省香炉违伏枕，山楼粉堞隐悲笳。\\n请看石上藤萝月，已映洲前芦荻花。\\n千家山郭静朝晖，日日江楼坐翠微。\\n信宿渔人还泛泛，清秋燕子故飞飞。\\n匡衡抗疏功名薄，刘向传经心事违。\\n同学少年多不贱，五陵衣马自轻肥。\\n闻道长安似弈棋，百年世事不胜悲。\\n王侯第宅皆新主，文武衣冠异昔时。\\n直北关山金鼓振，征西车马羽书迟。\\n鱼龙寂寞秋江冷，故国平居有所思。\\n蓬莱宫阙对南山，承露金茎霄汉间。\\n西望瑶池降王母，东来紫气满函关。\\n云移雉尾开宫扇，日绕龙鳞识圣颜。\\n一卧沧江惊岁晚，几回青琐照朝班。\\n瞿唐峡口曲江头，万里风烟接素秋。\\n花萼夹城通御气，芙蓉小苑入边愁。\\n朱帘绣柱围黄鹤，锦缆牙樯起白鸥。\\n回首可怜歌舞地，秦中自古帝王州。\\n昆明池水汉时功，武帝旌旗在眼中。\\n织女机丝虚月夜，石鲸鳞甲动秋风。\\n波漂菰米沈云黑，露冷莲房坠粉红。\\n关塞极天唯鸟道，江湖满地一渔翁。\\n昆吾御宿自逶迤，紫阁峰阴入渼陂。\\n香稻啄余鹦鹉粒，碧梧栖老凤凰枝。\\n佳人拾翠春相问，仙侣同舟晚更移。\\n彩笔昔游干气象，白头吟望苦低垂。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('351', 'poem', '唐', '李颀', '送魏万之京', '朝闻游子唱离歌，昨夜微霜初渡河。\\n鸿雁不堪愁里听，云山况是客中过。\\n关城树色催寒近，御苑砧声向晚多。\\n莫见长安行乐处，空令岁月易蹉跎。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('352', 'poem', '唐', '秦韬玉', '贫女', '蓬门未识绮罗香，拟托良媒益自伤。\\n谁爱风流高格调，共怜时世俭梳妆。\\n敢将十指夸针巧，不把双眉斗画长。\\n苦恨年年压金线，为他人作嫁衣裳。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('353', 'poem', '唐', '司空曙', '江村即事', '钓罢归来不系船，江村月落正堪眠。\\n纵然一夜风吹去，只在芦花浅水边。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('354', 'poem', '唐', '李白', '越中览古', '越王勾践破吴归，义士还乡尽锦衣。\\n宫女如花满春殿，只今惟有鹧鸪飞。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('355', 'poem', '唐', '李商隐', '宿骆氏亭寄怀崔雍崔衮', '竹坞无尘水槛清，相思迢递隔重城。\\n秋阴不散霜飞晚，留得枯荷听雨声。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('356', 'poem', '唐', '李白', '长相思·其一', '长相思，在长安。\\n络纬秋啼金井阑，微霜凄凄簟色寒。\\n孤灯不明思欲绝，卷帷望月空长叹。\\n美人如花隔云端！\\n上有青冥之长天，下有渌水之波澜。\\n天长路远魂飞苦，梦魂不到关山难。\\n长相思，摧心肝！', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('357', 'poem', '唐', '李白', '侠客行', '赵客缦胡缨，吴钩霜雪明。\\n银鞍照白马，飒沓如流星。\\n十步杀一人，千里不留行。\\n事了拂衣去，深藏身与名。\\n闲过信陵饮，脱剑膝前横。\\n将炙啖朱亥，持觞劝侯嬴。\\n三杯吐然诺，五岳倒为轻。\\n眼花耳热后，意气素霓生。\\n救赵挥金槌，邯郸先震惊。\\n千秋二壮士，烜赫大梁城。\\n纵死侠骨香，不惭世上英。\\n谁能书阁下，白首太玄经。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('358', 'poem', '唐', '王昌龄', '送魏二', '醉别江楼橘柚香，江风引雨入舟凉。\\n忆君遥在潇湘月，愁听清猿梦里长。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('359', 'poem', '唐', '李白', '江上吟', '木兰之枻沙棠舟，玉箫金管坐两头。\\n美酒樽中置千斛，载妓随波任去留。\\n仙人有待乘黄鹤，海客无心随白鸥。\\n屈平辞赋悬日月，楚王台榭空山丘。\\n兴酣落笔摇五岳，诗成笑傲凌沧洲。\\n功名富贵若长在，汉水亦应西北流。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('360', 'poem', '唐', '王维', '过香积寺', '不知香积寺，数里入云峰。\\n古木无人径，深山何处钟。\\n泉声咽危石，日色冷青松。\\n薄暮空潭曲，安禅制毒龙。', '2018-03-09 14:57:38');
INSERT INTO `t_poem` VALUES ('361', 'poem', '唐', '孟浩然', '田家元日', '昨夜斗回北，今朝岁起东。\\n我年已强仕，无禄尚忧农。\\n桑野就耕父，荷锄随牧童。\\n田家占气候，共说此年丰。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('362', 'poem', '唐', '杜牧', '长安秋望', '楼倚霜树外，镜天无一毫。\\n南山与秋色，气势两相高。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('363', 'poem', '唐', '王昌龄', '采莲曲二首', '吴姬越艳楚王妃，争弄莲舟水湿衣。\\n来时浦口花迎入，采罢江头月送归。\\n荷叶罗裙一色裁，芙蓉向脸两边开。\\n乱入池中看不见，闻歌始觉有人来。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('364', 'poem', '唐', '刘长卿', '听弹琴', '泠泠七丝上，静听松风寒。\\n古调虽自爱，今人多不弹。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('365', 'poem', '唐', '白居易', '李白墓', '采石江边李白坟，绕田无限草连云。\\n可怜荒垄穷泉骨，曾有惊天动地文。\\n但是诗人多薄命，就中沦落不过君。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('366', 'poem', '唐', '吴融', '秋色', '染不成乾画未销，霏霏拂拂又迢迢。\\n曾从建业城边路，蔓草寒烟锁六朝。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('367', 'poem', '唐', '杜牧', '愁', '聚散竟无形，回肠自结成。\\n古今留不得，离别又潜生。\\n降虏将军思，穷秋远客情。\\n何人更憔悴，落第泣秦京。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('368', 'poem', '唐', '张籍', '秋山', '秋山无云复无风，溪头看月出深松。\\n草堂不闭石床静，叶间坠露声重重。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('369', 'poem', '唐', '李白', '九日', '今日云景好，水绿秋山明。\\n携壶酌流霞，搴菊泛寒荣。\\n地远松石古，风扬弦管清。\\n窥觞照欢颜，独笑还自倾。\\n落帽醉山月，空歌怀友生。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('370', 'poem', '唐', '温庭筠', '过分水岭', '溪水无情似有情，入山三日得同行。\\n岭头便是分头处，惜别潺湲一夜声。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('371', 'poem', '唐', '修睦', '落叶', '雨过闲田地，重重落叶红。\\n翻思向春日，肯信有秋风。\\n几处随流水，河边乱暮空。\\n只应松自立，而不与君同。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('372', 'poem', '唐', '李白', '清平调·名花倾国两相欢', '名花倾国两相欢，常得君王带笑看。\\n解释春风无限恨，沉香亭北倚栏杆。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('373', 'poem', '唐', '于良史', '春山夜月', '春山多胜事，赏玩夜忘归。\\n掬水月在手，弄花香满衣。\\n兴来无远近，欲去惜芳菲。\\n南望鸣钟处，楼台深翠微。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('374', 'poem', '唐', '李白', '沙丘城下寄杜甫', '我来竟何事，高卧沙丘城。\\n城边有古树，日夕连秋声。\\n鲁酒不可醉，齐歌空复情。\\n思君若汶水，浩荡寄南征。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('375', 'poem', '唐', '韦应物', '西楼', '高阁一长望，故园何日归。\\n烟尘拥函谷，秋雁过来稀。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('376', 'poem', '唐', '孟浩然', '送杜十四之江南 / 送杜晃进士之东吴', '荆吴相接水为乡，君去春江正淼茫。\\n日暮征帆何处泊，天涯一望断人肠。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('377', 'poem', '唐', '柳中庸', '征人怨 / 征怨', '岁岁金河复玉关，朝朝马策与刀环。\\n三春白雪归青冢，万里黄河绕黑山。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('378', 'poem', '唐', '李白', '下终南山过斛斯山人宿置酒', '暮从碧山下，山月随人归。\\n却顾所来径，苍苍横翠微。\\n相携及田家，童稚开荆扉。\\n绿竹入幽径，青萝拂行衣。\\n欢言得所憩，美酒聊共挥。\\n长歌吟松风，曲尽河星稀。\\n我醉君复乐，陶然共忘机。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('379', 'poem', '唐', '顾况', '乐府', '暖谷春光至，宸游近甸荣。\\n云随天仗转，风入御帘轻。\\n翠盖浮佳气，朱楼倚太清。\\n朝臣冠剑退，宫女管弦迎。\\n细草承雕辇，繁花入幔城。\\n文房开圣藻，武卫宿天营。\\n玉醴随觞至，铜壶逐漏行。\\n五星含土德，万姓彻中声。\\n亲祀先崇典，躬推示劝耕。\\n国风新正乐，农器近消兵。\\n道德关河固，刑章日月明。\\n野人同鸟兽，率舞感升平。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('380', 'poem', '唐', '韦应物', '闻雁', '故园渺何处，归思方悠哉。\\n淮南秋雨夜，高斋闻雁来。', '2018-03-09 14:57:48');
INSERT INTO `t_poem` VALUES ('381', 'poem', '唐', '韩愈', '早春呈水部张十八员外二首', '天街小雨润如酥，草色遥看近却无。\\n最是一年春好处，绝胜烟柳满皇都。\\n莫道官忙身老大，即无年少逐春心。\\n凭君先到江头看，柳色如今深未深。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('382', 'poem', '唐', '杜甫', '丹青引赠曹将军霸 / 丹青引赠曹霸将军', '将军魏武之子孙，于今为庶为清门。\\n英雄割据虽已矣，文采风流今尚存。\\n学书初学卫夫人，但恨无过王右军。\\n丹青不知老将至，富贵于我如浮云。\\n开元之中常引见，承恩数上南薰殿。\\n凌烟功臣少颜色，将军下笔开生面。\\n良相头上进贤冠，猛将腰间大羽箭。\\n褒公鄂公毛发动，英姿飒爽来酣战。\\n先帝御马五花骢，画工如山貌不同。\\n是日牵来赤墀下，迥立阊阖生长风。\\n诏谓将军拂绢素，意匠惨淡经营中。\\n斯须九重真龙出，一洗万古凡马空。\\n玉花却在御榻上，榻上庭前屹相向。\\n至尊含笑催赐金，圉人太仆皆惆怅。\\n弟子韩干早入室，亦能画马穷殊相。\\n干惟画肉不画骨，忍使骅骝气凋丧。\\n将军画善盖有神，必逢佳士亦写真。\\n即今漂泊干戈际，屡貌寻常行路人。\\n途穷反遭俗眼白，世上未有如公贫。\\n但看古来盛名下，终日坎壈缠其身。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('383', 'poem', '唐', '王勃', '蜀中九日 / 九日登高', '九月九日望乡台，他席他乡送客杯。\\n人情已厌南中苦，鸿雁那从北地来。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('384', 'poem', '唐', '李白', '与夏十二登岳阳楼', '楼观岳阳尽，川迥洞庭开。\\n雁引愁心去，山衔好月来。\\n云间连下榻，天上接行杯。\\n醉后凉风起，吹人舞袖回。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('385', 'poem', '唐', '高适', '燕歌行', '汉家烟尘在东北，汉将辞家破残贼。\\n男儿本自重横行，天子非常赐颜色。\\n摐金伐鼓下榆关，旌旆逶迤碣石间。\\n校尉羽书飞瀚海，单于猎火照狼山。\\n山川萧条极边土，胡骑凭陵杂风雨。\\n战士军前半死生，美人帐下犹歌舞。\\n大漠穷秋塞草腓，孤城落日斗兵稀。\\n身当恩遇恒轻敌，力尽关山未解围。\\n铁衣远戍辛勤久，玉箸应啼别离后。\\n少妇城南欲断肠，征人蓟北空回首。\\n边庭飘飖那可度，绝域苍茫更何有。\\n杀气三时作阵云，寒声一夜传刁斗。\\n相看白刃血纷纷，死节从来岂顾勋。\\n君不见沙场征战苦，至今犹忆李将军。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('386', 'poem', '唐', '岑参', '山房春事二首', '风恬日暖荡春光，戏蝶游蜂乱入房。\\n数枝门柳低衣桁，一片山花落笔床。\\n梁园日暮乱飞鸦，极目萧条三两家。\\n庭树不知人去尽，春来还发旧时花。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('387', 'poem', '唐', '刘昚虚', '阙题', '道由白云尽，春与青溪长。\\n时有落花至，远随流水香。\\n闲门向山路，深柳读书堂。\\n幽映每白日，清辉照衣裳。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('388', 'poem', '唐', '郑谷', '鹭鸶', '闲立春塘烟淡淡，静眠寒苇雨飕飕。\\n渔翁归后汀沙晚，飞下滩头更自由。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('389', 'poem', '唐', '杨炯', '巫峡', '三峡七百里，唯言巫峡长。\\n重岩窅不极，叠嶂凌苍苍。\\n绝壁横天险，莓苔烂锦章。\\n入夜分明见，无风波浪狂。\\n忠信吾所蹈，泛舟亦何伤。\\n可以涉砥柱，可以浮吕梁。\\n美人今何在，灵芝徒有芳。\\n山空夜猿啸，征客泪沾裳。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('390', 'poem', '唐', '崔液', '上元夜六首·其一', '玉漏银壶且莫催，铁关金锁彻明开。\\n谁家见月能闲坐？何处闻灯不看来？', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('391', 'poem', '唐', '裴迪', '崔九欲往南山马上口号与别 / 留别王维 / 送崔九', '归山深浅去，须尽丘壑美。\\n莫学武陵人，暂游桃源里。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('392', 'poem', '唐', '贯休', '献钱尚父', '贵逼人来不自由，龙骧凤翥势难收。\\n满堂花醉三千客，一剑霜寒十四州。\\n鼓角揭天嘉气冷，风涛动地海山秋。\\n东南永作金天柱，谁羡当时万户侯。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('393', 'poem', '唐', '刘长卿', '寻南溪常山道人隐居 / 寻南溪常道士', '一路经行处，莓苔见履痕。\\n白云依静渚，春草闭闲门。\\n过雨看松色，随山到水源。\\n溪花与禅意，相对亦忘言。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('394', 'poem', '唐', '王维', '田园乐七首·其六 / 闲居', '桃红复含宿雨，柳绿更带朝烟。\\n花落家童未扫，莺啼山客犹眠。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('395', 'poem', '唐', '李商隐', '为有', '为有云屏无限娇，凤城寒尽怕春宵。\\n无端嫁得金龟婿，辜负香衾事早朝。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('396', 'poem', '唐', '白居易', '梦微之', '夜来携手梦同游，晨起盈巾泪莫收。\\n漳浦老身三度病，咸阳草树八回秋。\\n君埋泉下泥销骨，我寄人间雪满头。\\n阿卫韩郎相次去，夜台茫昧得知不。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('397', 'poem', '唐', '王昌龄', '送柴侍御', '沅水通波接武冈，送君不觉有离伤。\\n青山一道同云雨，明月何曾是两乡。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('398', 'poem', '唐', '戎昱', '移家别湖上亭', '好是春风湖上亭，柳条藤蔓系离情。\\n黄莺久住浑相识，欲别频啼四五声。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('399', 'poem', '唐', '李商隐', '风雨', '凄凉宝剑篇，羁泊欲穷年。\\n黄叶仍风雨，青楼自管弦。\\n新知遭薄俗，旧好隔良缘。\\n心断新丰酒，销愁斗几千。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('400', 'poem', '唐', '李益', '塞下曲·其一', '蕃州部落能结束，朝暮驰猎黄河曲。\\n燕歌未断塞鸿飞，牧马群嘶边草绿。', '2018-03-09 14:57:54');
INSERT INTO `t_poem` VALUES ('401', 'poem', '唐', '孟浩然', '宿业师山房期丁大不至 / 宿业师山房待丁大不至', '夕阳度西岭，群壑倏已暝。\\n松月生夜凉，风泉满清听。\\n樵人归欲尽，烟鸟栖初定。\\n之子期宿来，孤琴候萝径。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('402', 'poem', '唐', '聂夷中', '田家', '父耕原上田，子劚山下荒。\\n六月禾未秀，官家已修仓。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('403', 'poem', '唐', '刘禹锡', '酬乐天咏老见示', '人谁不顾老，老去有谁怜。\\n身瘦带频减，发稀冠自偏。\\n废书缘惜眼，多炙为随年。\\n经事还谙事，阅人如阅川。\\n细思皆幸矣，下此便翛然。\\n莫道桑榆晚，为霞尚满天。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('404', 'poem', '唐', '白居易', '白云泉', '天平山上白云泉，云自无心水自闲。\\n何必奔冲山下去，更添波浪向人间。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('405', 'poem', '唐', '张泌', '寄人', '别梦依依到谢家，小廊回合曲阑斜。\\n多情只有春庭月，犹为离人照落花。\\n酷怜风月为多情，还到春时别恨生。\\n倚柱寻思倍惆怅，一场春梦不分明。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('406', 'poem', '唐', '李白', '望月有怀', '清泉映疏松，不知几千古。\\n寒月摇清波，流光入窗户。\\n对此空长吟，思君意何深。\\n无因见安道，兴尽愁人心。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('407', 'poem', '唐', '戴叔伦', '相思曲', '高楼重重闭明月，肠断仙郎隔年别。\\n紫萧横笛寂无声，独向瑶窗坐愁绝。\\n鱼沈雁杳天涯路，始信人间别离苦。\\n恨满牙床翡翠衾，怨折金钗凤凰股。\\n井深辘轳嗟绠短，衣带相思日应缓。\\n将刀斫水水复连，挥刃割情情不断。\\n落红乱逐东流水，一点芳心为君死。\\n妾身愿作巫山云，飞入仙郎梦魂里。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('408', 'poem', '唐', '李白', '行路难三首', '金樽清酒斗十千，玉盘珍羞直万钱。\\n停杯投箸不能食，拔剑四顾心茫然。\\n欲渡黄河冰塞川，将登太行雪满山。\\n闲来垂钓碧溪上，忽复乘舟梦日边。\\n行路难！行路难！多歧路，今安在？\\n长风破浪会有时，直挂云帆济沧海。\\n大道如青天，我独不得出。\\n羞逐长安社中儿，赤鸡白雉赌梨栗。\\n弹剑作歌奏苦声，曳裾王门不称情。\\n淮阴市井笑韩信，汉朝公卿忌贾生。\\n君不见昔时燕家重郭隗，拥簪折节无嫌猜。\\n剧辛乐毅感恩分，输肝剖胆效英才。\\n昭王白骨萦蔓草，谁人更扫黄金台？\\n行路难，归去来！\\n有耳莫洗颍川水，有口莫食首阳蕨。\\n含光混世贵无名，何用孤高比云月？\\n吾观自古贤达人，功成不退皆殒身。\\n子胥既弃吴江上，屈原终投湘水滨。\\n陆机雄才岂自保？李斯税驾苦不早。\\n华亭鹤唳讵可闻？上蔡苍鹰何足道？\\n君不见吴中张翰称达生，秋风忽忆江东行。\\n且乐生前一杯酒，何须身后千载名？', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('409', 'poem', '唐', '罗隐', '塞外', '塞外偷儿塞内兵，圣君宵旰望升平。\\n碧幢未作朝廷计，白梃犹驱妇女行。\\n可使御戎无上策，只应忧国是虚声。\\n汉王第宅秦田土，今日将军已自荣。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('410', 'poem', '唐', '王勃', '春游', '客念纷无极，春泪倍成行。\\n今朝花树下，不觉恋年光。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('411', 'poem', '唐', '白居易', '寒食野望吟', '乌啼鹊噪昏乔木，清明寒食谁家哭。\\n风吹旷野纸钱飞，古墓垒垒春草绿。\\n棠梨花映白杨树，尽是死生别离处。\\n冥冥重泉哭不闻，萧萧暮雨人归去。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('412', 'poem', '唐', '韦应物', '寄李儋元锡', '去年花里逢君别，今日花开已一年。\\n世事茫茫难自料，春愁黯黯独成眠。\\n身多疾病思田里，邑有流亡愧俸钱。\\n闻道欲来相问讯，西楼望月几回圆。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('413', 'poem', '唐', '白居易', '池鹤二首', '高竹笼前无伴侣，乱鸡群里有风标。\\n低头乍恐丹砂落，晒翅常疑白雪消。\\n转觉鸬鹚毛色下，苦嫌鹦鹉语声娇。\\n临风一唳思何事，怅望青田云水遥。\\n池中此鹤鹤中稀，恐是辽东老令威。\\n带雪松枝翘膝胫，放花菱片缀毛衣。\\n低回且向林间宿，奋迅终须天外飞。\\n若问故巢知处在，主人相恋未能归。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('414', 'poem', '唐', '元稹', '樱桃花', '樱桃花，一枝两枝千万朵。花砖曾立摘花人，\\n窣破罗裙红似火。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('415', 'poem', '唐', '杜甫', '捣衣', '亦知戍不返，秋至拭清砧。\\n已近苦寒月，况经长别心。\\n宁辞捣熨倦，一寄塞垣深。\\n用尽闺中力，君听空外音。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('416', 'poem', '唐', '王维', '红牡丹', '绿艳闲且静，红衣浅复深。\\n花心愁欲断，春色岂知心。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('417', 'poem', '唐', '杜荀鹤', '题弟侄书堂', '何事居穷道不穷，乱时还与静时同。\\n家山虽在干戈地，弟侄常修礼乐风。\\n窗竹影摇书案上，野泉声入砚池中。\\n少年辛苦终身事，莫向光阴惰寸功。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('418', 'poem', '唐', '张祜', '宫词·故国三千里', '故国三千里，深宫二十年。\\n一声何满子，双泪落君前。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('419', 'poem', '唐', '王维', '老将行', '少年十五二十时，步行夺得胡马骑。\\n射杀中山白额虎，肯数邺下黄须儿！\\n一身转战三千里，一剑曾当百万师。\\n汉兵奋迅如霹雳，虏骑崩腾畏蒺藜。\\n卫青不败由天幸，李广无功缘数奇。\\n自从弃置便衰朽，世事蹉跎成白首。\\n昔时飞箭无全目，今日垂杨生左肘。\\n路旁时卖故侯瓜，门前学种先生柳。\\n苍茫古木连穷巷，寥落寒山对虚牖。\\n誓令疏勒出飞泉，不似颍川空使酒。\\n贺兰山下阵如云，羽檄交驰日夕闻。\\n节使三河募年少，诏书五道出将军。\\n试拂铁衣如雪色，聊持宝剑动星文。\\n愿得燕弓射大将，耻令越甲鸣吾军。\\n莫嫌旧日云中守，犹堪一战取功勋。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('420', 'poem', '唐', '李商隐', '忆梅', '定定住天涯，依依向物华。\\n寒梅最堪恨，常作去年花。', '2018-03-09 14:57:58');
INSERT INTO `t_poem` VALUES ('421', 'poem', '唐', '武元衡', '春兴', '杨柳阴阴细雨晴，残花落尽见流莺。\\n春风一夜吹乡梦，又逐春风到洛城。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('422', 'poem', '唐', '王维', '送沈子归江东 / 送沈子福之江东', '杨柳渡头行客稀，罟师荡桨向临圻。\\n唯有相思似春色，江南江北送君归。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('423', 'poem', '唐', '曹邺', '官仓鼠', '官仓老鼠大如斗，见人开仓亦不走。\\n健儿无粮百姓饥，谁遣朝朝入君口。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('424', 'poem', '唐', '杜甫', '春水', '三月桃花浪，江流复旧痕。\\n朝来没沙尾，碧色动柴门。\\n接缕垂芳饵，连筒灌小园。\\n已添无数鸟，争浴故相喧。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('425', 'poem', '唐', '白居易', '浪淘沙·借问江潮与海水', '借问江潮与海水，何似君情与妾心？相恨不如潮有信，相思始觉海非深。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('426', 'poem', '唐', '牛殳', '琵琶行', '何人劚得一片木，三尺春冰五音足。一弹决破真珠囊，\\n迸落金盘声断续。飘飘飖飖寒丁丁，虫豸出蛰神鬼惊。\\n秋鸿叫侣代云黑，猩猩夜啼蛮月明。潏潏汩汩声不定，\\n胡雏学汉语未正。若似长安月蚀时，满城敲鼓声噒噒，\\n青山飞起不压物，野水流来欲湿人。伤心忆得陈后主，\\n春殿半酣细腰舞。黄莺百舌正相呼，玉树后庭花带雨。\\n二妃哭处山重重，二妃没后云溶溶。夜深霜露锁空庙，\\n零落一丛斑竹风。金谷园中草初绿，石崇一弄思归曲。\\n当时二十四友人，手把金杯听不足。又似贾客蜀道间，\\n千铎万磬鸣空山。未若此调呦呦兮啁啁，嘈嘈兮啾啾。\\n引之于山，兽不能走。吹之于水，鱼不能游。\\n方知此艺不可有，人间万事凭双手。若何为我再三弹，\\n送却花前一尊酒。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('427', 'poem', '唐', '孟浩然', '春中喜王九相寻 / 晚春', '二月湖水清，家家春鸟鸣。\\n林花扫更落，径草踏还生。\\n酒伴来相命，开尊共解酲。\\n当杯已入手，歌妓莫停声。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('428', 'poem', '唐', '李商隐', '柳', '曾逐东风拂舞筵，乐游春苑断肠天。\\n如何肯到清秋日，已带斜阳又带蝉。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('429', 'poem', '唐', '严武', '军城早秋', '昨夜秋风入汉关，朔云边月满西山。\\n更催飞将追骄虏，莫遣沙场匹马还。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('430', 'poem', '唐', '韦庄', '秦妇吟', '中和癸卯春三月，洛阳城外花如雪。东西南北路人绝，绿杨悄悄香尘灭。路旁忽见如花人，独向绿杨阴下歇。凤侧鸾欹鬓脚斜，红攒黛敛眉心折。借问女郎何处来？含颦欲语声先咽。回头敛袂谢行人，丧乱漂沦何堪说！三年陷贼留秦地，依稀记得秦中事。君能为妾解金鞍，妾亦与君停玉趾。\\n前年庚子腊月五，正闭金笼教鹦鹉。斜开鸾镜懒梳头，闲凭雕栏慵不语。忽看门外起红尘，已见街中擂金鼓。居人走出半仓惶，朝士归来尚疑误。是时西面官军入，拟向潼关为警急。皆言博野自相持，尽道贼军来未及。须臾主父乘奔至，下马入门痴似醉。适逢紫盖去蒙尘，已见白旗来匝地。\\n扶羸携幼竞相呼，上屋缘墙不知次。南邻走入北邻藏，东邻走向西邻避。北邻诸妇咸相凑，户外崩腾如走兽。轰轰混混乾坤动，万马雷声从地涌。火迸金星上九天，十二官街烟烘烔。日轮西下寒光白，上帝无言空脉脉。阴云晕气若重围，宦者流星如血色。紫气潜随帝座移，妖光暗射台星拆。家家流血如泉沸，处处冤声声动地。舞伎歌姬尽暗捐，婴儿稚女皆生弃。\\n东邻有女眉新画，倾国倾城不知价。长戈拥得上戎车，回首香闺泪盈把。旋抽金线学缝旗，才上雕鞍教走马。有时马上见良人，不敢回眸空泪下；西邻有女真仙子，一寸横波剪秋水。妆成只对镜中春，年幼不知门外事。一夫跳跃上金阶，斜袒半肩欲相耻。牵衣不肯出朱门，红粉香脂刀下死。南邻有女不记姓，昨日良媒新纳聘。琉璃阶上不闻行，翡翠帘间空见影。忽看庭际刀刃鸣，身首支离在俄顷。仰天掩面哭一声，女弟女兄同入井；北邻少妇行相促，旋拆云鬟拭眉绿。已闻击托坏高门，不觉攀缘上重屋。须臾四面火光来，欲下回梯梯又摧。烟中大叫犹求救，梁上悬尸已作灰。\\n妾身幸得全刀锯，不敢踟蹰久回顾。旋梳蝉鬓逐军行，强展蛾眉出门去。旧里从兹不得归，六亲自此无寻处。一从陷贼经三载，终日惊忧心胆碎。夜卧千重剑戟围，朝餐一味人肝脍。鸳帏纵入岂成欢？宝货虽多非所爱。蓬头垢面眉犹赤，几转横波看不得。衣裳颠倒语言异，面上夸功雕作字。柏台多半是狐精，兰省诸郎皆鼠魅。还将短发戴华簪，不脱朝衣缠绣被。翻持象笏作三公，倒佩金鱼为两史。朝闻奏对入朝堂，暮见喧呼来酒市。\\n一朝五鼓人惊起，叫啸喧呼如窃语。夜来探马入皇城，昨日官军收赤水。赤水去城一百里，朝若来兮暮应至。凶徒马上暗吞声，女伴闺中潜生喜。皆言冤愤此时销，必谓妖徒今日死。逡巡走马传声急，又道官军全阵入。大彭小彭相顾忧，二郎四郎抱鞍泣。沉沉数日无消息，必谓军前已衔璧。簸旗掉剑却来归，又道官军悉败绩。\\n四面从兹多厄束，一斗黄金一斗粟。尚让厨中食木皮，黄巢机上刲人肉。东南断绝无粮道，沟壑渐平人渐少。六军门外倚僵尸，七架{寨}营中填饿殍。长安寂寂今何有？废市荒街麦苗秀。采樵斫尽杏园花，修寨诛残御沟柳。华轩绣毂皆销散，甲第朱门无一半。含元殿上狐兔行，花萼楼前荆棘满。昔时繁盛皆埋没，举目凄凉无故物。内库烧为锦绣灰，天街踏尽公卿骨！\\n来时晓出城东陌，城外风烟如塞色。路旁时见游奕军，坡下寂无迎送客。霸陵东望人烟绝，树锁骊山金翠灭。大道俱成棘子林，行人夜宿墙匡月。明朝晓至三峰路，百万人家无一户。破落田园但有蒿，摧残竹树皆无主。路旁试问金天神，金天无语愁于人。庙前古柏有残枿，殿上金炉生暗尘。一从狂寇陷中国，天地晦冥风雨黑。案前神水咒不成，壁上阴兵驱不得。闲日徒歆奠飨恩，危时不助神通力。我今愧恧拙为神，且向山中深避匿。寰中箫管不曾闻，筵上牺牲无处觅。旋教魔鬼傍乡村，诛剥生灵过朝夕。妾闻此语愁更愁，天遣时灾非自由。神在山中犹避难，何须责望东诸侯！\\n前年又出扬震关，举头云际见荆山。如从地府到人间，顿觉时清天地闲。陕州主帅忠且贞，不动干戈唯守城。蒲津主帅能戢兵，千里晏然无犬声。朝携宝货无人问，暮插金钗唯独行。明朝又过新安东，路上乞浆逢一翁。苍苍面带苔藓色，隐隐身藏蓬荻中。问翁本是何乡曲？底事寒天霜露宿？老翁暂起欲陈辞，却坐支颐仰天哭。乡园本贯东畿县，岁岁耕桑临近甸。岁种良田二百廛，年输户税三千万。小姑惯织褐絁袍，中妇能炊红黍饭。千间仓兮万丝箱，黄巢过后犹残半。自从洛下屯师旅，日夜巡兵入村坞。匣中秋水拔青蛇，旗上高风吹白虎。入门下马若旋风，罄室倾囊如卷土。家财既尽骨肉离，今日垂年一身苦。一身苦兮何足嗟，山中更有千万家，朝饥山上寻蓬子，夜宿霜中卧荻花！\\n妾闻此老伤心语，竟日阑干泪如雨。出门惟见乱枭鸣，更欲东奔何处所？仍闻汴路舟车绝，又道彭门自相杀。野宿徒销战士魂，河津半是冤人血。适闻有客金陵至，见说江南风景异。自从大寇犯中原，戎马不曾生四鄙。诛锄窃盗若神功，惠爱生灵如赤子。城壕固护教{斆、敩、效}金汤，赋税如云送军垒。奈何四海尽滔滔，湛然一境平如砥。避难徒为阙下人，怀安却羡江南鬼。愿君举棹东复东，咏此长歌献相公。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('431', 'poem', '唐', '杜甫', '房兵曹胡马诗', '胡马大宛名，锋棱瘦骨成。\\n竹批双耳峻，风入四蹄轻。\\n所向无空阔，真堪托死生。\\n骁腾有如此，万里可横行。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('432', 'poem', '唐', '杜甫', '新婚别', '兔丝附蓬麻，引蔓故不长。\\n嫁女与征夫，不如弃路旁。\\n结发为妻子，席不暖君床。\\n暮婚晨告别，无乃太匆忙。\\n君行虽不远，守边赴河阳。\\n妾身未分明，何以拜姑嫜。\\n父母养我时，日夜令我藏。\\n生女有所归，鸡狗亦得将。\\n君今往死地，沈痛迫中肠。\\n誓欲随君去，形势反苍黄。\\n勿为新婚念，努力事戎行。\\n妇人在军中，兵气恐不扬。\\n自嗟贫家女，久致罗襦裳。\\n罗襦不复施，对君洗红妆。\\n仰视百鸟飞，大小必双翔。\\n人事多错迕，与君永相望。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('433', 'poem', '唐', '王昌龄', '芙蓉楼送辛渐二首', '寒雨连天夜入湖，平明送客楚山孤。\\n洛阳亲友如相问，一片冰心在玉壶。\\n丹阳城南秋海阴，丹阳城北楚云深。\\n高楼送客不能醉，寂寂寒江明月心。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('434', 'poem', '唐', '金昌绪', '春怨 / 伊州歌', '打起黄莺儿，莫教枝上啼。\\n啼时惊妾梦，不得到辽西。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('435', 'poem', '唐', '杜甫', '又呈吴郎', '堂前扑枣任西邻，无食无儿一妇人。\\n不为困穷宁有此？只缘恐惧转须亲。\\n即防远客虽多事，便插疏篱却甚真。\\n已诉征求贫到骨，正思戎马泪盈巾。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('436', 'poem', '唐', '杜甫', '奉赠韦左丞丈二十二韵', '纨绔不饿死，儒冠多误身。丈人试静听，贱子请具陈。\\n甫昔少年日，早充观国宾。读书破万卷，下笔如有神。\\n赋料扬雄敌，诗看子建亲。李邕求识面，王翰愿卜邻。\\n自谓颇挺出，立登要路津。致君尧舜上，再使风俗淳。\\n此意竟萧条，行歌非隐沦。骑驴十三载，旅食京华春。\\n朝扣富儿门，暮随肥马尘。残杯与冷炙，到处潜悲辛。\\n主上顷见征，欻然欲求伸。青冥却垂翅，蹭蹬无纵鳞。\\n甚愧丈人厚，甚知丈人真。每于百僚上，猥颂佳句新。\\n窃效贡公喜，难甘原宪贫。焉能心怏怏，只是走踆踆。\\n今欲东入海，即将西去秦。尚怜终南山，回首清渭滨。\\n常拟报一饭，况怀辞大臣。白鸥没浩荡，万里谁能驯？', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('437', 'poem', '唐', '王维', '洛阳女儿行', '洛阳女儿对门居，才可颜容十五余。\\n良人玉勒乘骢马，侍女金盘脍鲤鱼。\\n画阁朱楼尽相望，红桃绿柳垂檐向。\\n罗帷送上七香车，宝扇迎归九华帐。\\n狂夫富贵在青春，意气骄奢剧季伦。\\n自怜碧玉亲教舞，不惜珊瑚持与人。\\n春窗曙灭九微火，九微片片飞花琐。\\n戏罢曾无理曲时，妆成祗是熏香坐。\\n城中相识尽繁华，日夜经过赵李家。\\n谁怜越女颜如玉，贫贱江头自浣纱。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('438', 'poem', '唐', '杜甫', '不见', '不见李生久，佯狂真可哀。\\n世人皆欲杀，吾意独怜才。\\n敏捷诗千首，飘零酒一杯。\\n匡山读书处，头白好归来。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('439', 'poem', '唐', '韩愈', '同水部张员外籍曲江春游寄白二十二舍人', '漠漠轻阴晚自开，青天白日映楼台。\\n曲江水满花千树，有底忙时不肯来。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('440', 'poem', '唐', '许浑', '塞下曲', '夜战桑乾北，秦兵半不归。\\n朝来有乡信，犹自寄寒衣。', '2018-03-09 14:58:03');
INSERT INTO `t_poem` VALUES ('441', 'poem', '唐', '李白', '子夜吴歌·夏歌', '镜湖三百里，菡萏发荷花。\\n五月西施采，人看隘若耶。\\n回舟不待月，归去越王家。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('442', 'poem', '唐', '薛逢', '长安夜雨', '滞雨通宵又彻明，百忧如草雨中生。\\n心关桂玉天难晓，运落风波梦亦惊。\\n压树早鸦飞不散，到窗寒鼓湿无声。\\n当年志气俱消尽，白发新添四五茎。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('443', 'poem', '唐', '孟浩然', '宿桐庐江寄广陵旧游', '山暝闻猿愁，沧江急夜流。\\n风鸣两岸叶，月照一孤舟。\\n建德非吾土，维扬忆旧游。\\n还将两行泪，遥寄海西头。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('444', 'poem', '唐', '神秀', '偈一', '身是菩提树，心如明镜台；时时勤拂拭，莫使有尘埃。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('445', 'poem', '唐', '吕岩', '七言', '金丹一粒定长生，须得真铅炼甲庚。\\n火取南方赤凤髓，水求北海黑龟精。\\n鼎追四季中央合，药遣三元八卦行。\\n斋戒兴功成九转，定应入口鬼神惊。\\n功满来来际会难，又闻东去上仙坛。\\n杖头春色一壶酒，顶上云攒五岳冠。\\n饮酒龟儿人不识，烧山符子鬼难看。\\n先生去后身须老，乞与贫儒换骨丹。\\n碧潭深处一真人，貌似桃花体似银。\\n鬓发未斑缘有术，红颜不老为通神。\\n蓬莱要去如今去，架上黄衣化作云。\\n任彼桑田变沧海，一丸丹药定千春。\\n炉养丹砂鬓不斑，假将名利住人间。\\n已逢志士传神药，又喜同流动笑颜。\\n老子道经分付得，少微星许共相攀。\\n幸蒙上士甘捞摝，处世输君一个闲。\\n谁解长生似我哉，炼成真气在三台。\\n尽知白日升天去，刚逐红尘下世来。\\n黑虎行时倾雨露，赤龙耕处产琼瑰。\\n只吞一粒金丹药，飞入青霄更不回。\\n乱云堆里表星都，认得深藏大丈夫。\\n绿酒醉眠闲日月，白蘋风定钓江湖。\\n长将气度随天道，不把言词问世徒。\\n山水路遥人不到，茅君消息近知无。\\n鹤为车驾酒为粮，为恋长生不死乡。\\n地脉尚能缩得短，人年岂不展教长。\\n星辰往往壶中见，日月时时衲里藏。\\n若欲时流亲得见，朝朝不离水银行。\\n灵芝无种亦无根，解饮能餐自返魂。\\n但得烟霞供岁月，任他乌兔走乾坤。\\n婴儿只恋阳中母，姹女须朝顶上尊。\\n一得不回千古内，更无冢墓示儿孙。\\n世上何人会此言，休将名利挂心田。\\n等闲倒尽十分酒，遇兴高吟一百篇。\\n物外烟霞为伴侣，壶中日月任婵娟。\\n他时功满归何处，直驾云车入洞天。\\n玄门帝子坐中央，得算明长感玉皇。\\n枕上山河和雨露，笛中日月混潇湘。\\n坎男会遇逢金女，离女交腾嫁木郎。\\n真个夫妻齐守志，立教牵惹在阴阳。\\n遥指高峰笑一声，红霞紫雾面前生。\\n每于廛市无人识，长到山中有鹤行。\\n时弄玉蟾驱鬼魅，夜煎金鼎煮琼英。\\n他时若赴蓬莱洞，知我仙家有姓名。\\n堪笑时人问我家，杖担云物惹烟霞。\\n眉藏火电非他说，手种金莲不自夸。\\n三尺焦桐为活计，一壶美酒是生涯。\\n骑龙远出游三岛，夜久无人玩月华。\\n九曲江边坐卧看，一条长路入天端。\\n庆云捧拥朝丹阙，瑞气裴回起白烟。\\n铅汞此时为至药，坎离今日结神丹。\\n功能济命长无老，只在人心不是难。\\n玄门玄理又玄玄，不死根元在汞铅。\\n知是一般真个术，调和六一也同天。\\n玉京山上羊儿闹，金水河中石虎眠。\\n妙要能生觉本体，勤心到处自如然。\\n公卿虽贵不曾酬，说著仙乡便去游。\\n为讨石肝逢蜃海，因寻甜雪过瀛洲。\\n山川醉后壶中放，神鬼闲来匣里收。\\n据见目前无个识，不如杯酒混凡流。\\n曾邀相访到仙家，忽上昆仑宴月华。\\n玉女控拢苍獬豸，山童提挈白虾蟆。\\n时斟海内千年酒，惯摘壶中四序花。\\n今在人寰人不识，看看挥袖入烟霞。\\n火种丹田金自生，重重楼阁自分明。\\n三千功行百旬见，万里蓬莱一日程。\\n羽化自应无鬼录，玉都长是有仙名。\\n今朝得赴瑶池会，九节幢幡洞里迎。\\n因看崔公入药镜，令人心地转分明。\\n阳龙言向离宫出，阴虎还于坎位生。\\n二物会时为道本，五方行尽得丹名。\\n修真道士如知此，定跨赤龙归玉清。\\n浮生不实为轻忽，衲服深藏奇异骨。\\n非是尘中不染尘，焉得物外通无物。\\n共语难兮情兀兀，独自行时轻拂拂。\\n一点刀圭五彩生，飞丹走入神仙窟。\\n莫怪爱吟天上诗，盖缘吟得世间稀。\\n惯餐玉帝宫中饭，曾著蓬莱洞里衣。\\n马踏日轮红露卷，凤衔月角擘云飞。\\n何时再控青丝辔，又掉金鞭入紫微。\\n黄芽白雪两飞金，行即高歌醉即吟。\\n日月暗扶君甲子，乾坤自与我知音。\\n精灵灭迹三清剑，风雨腾空一弄琴。\\n的当南游归甚处，莫交鹤去上天寻。\\n云鬓双明骨更轻，自言寻鹤到蓬瀛。\\n日论药草皆知味，问著神仙自得名。\\n簪冷夜龙穿碧洞，枕寒晨虎卧银城。\\n来春又拟携筇去，为忆轩辕海上行。\\n龙精龟眼两相和，丈六男儿不奈何。\\n九盏水中煎赤子，一轮火内养黄婆。\\n月圆自觉离天网，功满方知出地罗。\\n半醉好吞龙凤髓，劝君休更认弥陀。\\n强居此境绝知音，野景虽多不合吟。\\n诗句若喧卿相口，姓名还动帝王心。\\n道袍薜带应慵挂，隐帽皮冠尚懒簪。\\n除此更无馀个事，一壶村酒一张琴。\\n华阳山里多芝田，华阳山叟复延年。\\n青松岩畔攀高干，白云堆里饮飞泉。\\n不寒不热神荡荡，东来西去气绵绵。\\n三千功满好归去，休与时人说洞天。\\n天生不散自然心，成败从来古与今。\\n得路应知能出世，迷途终是任埋沈。\\n身边至药堪攻炼，物外丹砂且细寻。\\n咫尺洞房仙景在，莫随波浪没光阴。\\n自隐玄都不记春，几回沧海变成尘。\\n玉京殿里朝元始，金阙宫中拜老君。\\n闷即驾乘千岁鹤，闲来高卧九重云。\\n我今学得长生法，未肯轻传与世人。\\n北帝南辰掌内观，潜通造化暗相传。\\n金槌袖里居元宅，玉户星宫降上玄。\\n举世尽皆寻此道，谁人空里得玄关。\\n明明道在堪消息，日月滩头去又还。\\n日影元中合自然，奔雷走电入中原。\\n长驱赤马居东殿，大启朱门泛碧泉。\\n怒拔昆吾歌圣化，喜陪孤月贺新年。\\n方知此是生生物，得在仁人始受传。\\n六龙齐驾得升乾，须觉潜通造化权。\\n真道每吟秋月澹，至言长运碧波寒。\\n昼乘白虎游三岛，夜顶金冠立古坛。\\n一载已成千岁药，谁人将袖染尘寰。\\n五岳滩头景象新，仁人方达杳冥身。\\n天纲运转三元净，地脉通来万物生。\\n自晓谷神通此道，谁将理性欲修真。\\n明明说向中黄路，霹雳声中自得神。\\n欲陪仙侣得身轻，飞过蓬莱彻上清。\\n朱顶鹤来云外接，紫鳞鱼向海中迎。\\n姮娥月桂花先吐，王母仙桃子渐成。\\n下瞰日轮天欲晓，定知人世久长生。\\n四海皆忙几个闲，时人口内说尘缘。\\n知君有道来山上，何似无名住世间。\\n十二楼台藏秘诀，五千言内隐玄关。\\n方知鼎贮神仙药，乞取刀圭一粒看。\\n割断繁华掉却荣，便从初得是长生。\\n曾于锦水为蝉蜕，又向蓬莱别姓名。\\n三住住来无否泰，一尘尘在世人情。\\n不知功满归何处，直跨虬龙上玉京。\\n当年诗价满皇都，掉臂西归是丈夫。\\n万顷白云独自有，一枝丹桂阿谁无。\\n闲寻渭曲渔翁引，醉上莲峰道士扶。\\n他日与君重际会，竹溪茅舍夜相呼。\\n金锤灼灼舞天阶，独自骑龙去又来。\\n高卧白云观日窟，闲眠秋月擘天开。\\n离花片片乾坤产，坎蕊翻翻造化栽。\\n晚醉九岩回首望，北邙山下骨皑皑。\\n结交常与道情深，日日随他出又沈。\\n若要自通云外鹤，直须勤炼水中金。\\n丹成只恐乾坤窄，饵了宁忧疾患侵。\\n未去瑶台犹混世，不妨杯酒喜闲吟。\\n因携琴剑下烟萝，何幸今朝喜暂过。\\n貌相本来犹自可，针医偏更效无多。\\n仙经已读三千卷，古法曾持十二科。\\n些小道功如不信，金阶舍手试看么。\\n倾侧华阳醉再三，骑龙遇晚下南岩。\\n眉因拍剑留星电，衣为眠云惹碧岚。\\n金液变来成雨露，玉都归去老松杉。\\n曾将铁镜照神鬼，霹雳搜寻火满潭。\\n铁镜烹金火满空，碧潭龙卧夕阳中。\\n麒麟意合乾坤地，獬豸机关日月东。\\n三尺剑横双水岸，五丁冠顶百神宫。\\n闲铺羽服居仙窟，自著金莲造化功。\\n随缘信业任浮沈，似水如云一片心。\\n两卷道经三尺剑，一条藜杖七弦琴。\\n壶中有药逢人施，腹内新诗遇客吟。\\n一嚼永添千载寿，一丸丹点一斤金。\\n琴剑酒棋龙鹤虎，逍遥落托永无忧。\\n闲骑白鹿游三岛，闷驾青牛看十洲。\\n碧洞远观明月上，青山高隐彩云流。\\n时人若要还如此，名利浮华即便休。\\n紫极宫中我自知，亲磨神剑剑还飞。\\n先差玉子开南殿，后遣青龙入紫微。\\n九鼎黄芽栖瑞凤，一躯仙骨养灵芝。\\n蓬莱不是凡人处，只怕愚人泄世机。\\n向身方始出埃尘，造化功夫只在人。\\n早使亢龙抛地网，岂知白虎出天真。\\n绵绵有路谁留我，默默忘言自合神。\\n击剑夜深归甚处，披星带月折麒麟。\\n春尽闲闲过落花，一回舞剑一吁嗟。\\n常忧白日光阴促，每恨青天道路赊。\\n本志不求名与利，元心只慕水兼霞。\\n世间万种浮沉事，达理谁能似我家。\\n日为和解月呼丹，华夏诸侯肉眼看。\\n仁义异如胡越异，世情难似泰衡难。\\n八仙炼后钟神异，四海磨成照胆寒。\\n笑指不平千万万，骑龙抚剑九重关。\\n别来洛汭六东风，醉眼吟情慵不慵。\\n摆撼乾坤金剑吼，烹煎日月玉炉红。\\n杖摇楚甸三千里，鹤翥秦烟几万重。\\n为报晋成仙子道，再期春色会稽峰。\\n发头滴血眼如镮，吐气云生怒世间。\\n争耐不平千古事，须期一诀荡凶顽。\\n蛟龙斩处翻沧海，暴虎除时拔远山。\\n为灭世情兼负义，执剑腥染点痕斑。\\n雨雪霏霏天已暮。\\n金钟满劝抚焦桐。\\n诗吟席上未移刻，剑舞筵前疾似风。\\n何事行杯当午夜，忽然怒目便腾空。\\n不知谁是亏忠孝，携个人头入坐中。\\n未炼还丹且炼心，丹成方觉道元深。\\n每留客有钱酤酒，谁信君无药点金。\\n洞里风雷归掌握，壶中日月在胸襟。\\n神仙事业人难会，养性长生自意吟。\\n铁牛耕地种金钱，刻石时童把贯穿。\\n一粒粟中藏世界，二升铛内煮山川。\\n白头老子眉垂地，碧眼胡儿手指天。\\n若向此中玄会得，此玄玄外更无玄。\\n箕星昴宿下长天，凡景宁教不愕然。\\n龙出水来鳞甲就，鹤冲天气羽毛全。\\n尘中教化千人眼，世上人知尔雅篇。\\n自是凡流福命薄，忍教微妙略轻传。\\n闲来掉臂入天门，拂袂徐徐撮彩云。\\n无语下窥黄谷子，破颜平揖紫霞君。\\n拟登瑶殿参金母，回访瀛洲看日轮。\\n恰值嫦娥排宴会，瑶浆新熟味氤氲。\\n曾随刘阮醉桃源，未省人间欠酒钱。\\n一领布裘权且当，九天回日却归还。\\n凤茸袄子非为贵，狐白裘裳欲比难。\\n只此世间无价宝，不凭火里试烧看。\\n因思往事却成憨，曾读仙经第十三。\\n武氏死时应室女，陈王没后是童男。\\n两轮日月从他载，九个山河一担担。\\n尽日无人话消息，一壶春酒且醺酣。\\n垂袖腾腾傲世尘，葫芦携却数游巡。\\n利名身外终非道，龙虎门前辨取真。\\n一觉梦魂朝紫府，数年踪迹隐埃尘。\\n华阴市内才相见，不是寻常卖药人。\\n万卷仙经三尺琴，刘安闻说是知音。\\n杖头春色一壶酒，炉内丹砂万点金。\\n闷里醉眠三路口，闲来游钓洞庭心。\\n相逢相遇人谁识，只恐冲天没处寻。\\n曾战蚩尤玉座前，六龙高驾振鸣銮。\\n如来车后随金鼓，黄帝旂傍戴铁冠。\\n醉捋黑须三岛黯，怒抽霜剑十洲寒。\\n轩辕世代横行后，直隐深岩久觅难。\\n头角苍浪声似钟，貌如冰雪骨如松。\\n匣中宝剑时频吼，袖里金锤逞露风。\\n会饮酒时为伴侣，能行诗句便参同。\\n来年定赴蓬莱会，骑个生狞九色龙。\\n神仙暮入黄金阙，将相门关白玉京。\\n可是洞中无好景，为怜天下有众生。\\n心琴际会闲随鹤，匣剑时磨待断鲸。\\n进退两楹俱未应，凭君与我指前程。\\n九鼎烹煎一味砂，自然火候放童花。\\n星辰照出青莲颗，日月能藏白马牙。\\n七返返成生碧雾，九还还就吐红霞。\\n有人夺得玄珠饵，三岛途中路不赊。\\n天生一物变三才，交感阴阳结圣胎。\\n龙虎顺行阴鬼去，龟蛇逆往火龙来。\\n婴儿日吃黄婆髓，姹女时餐白玉杯。\\n功满自然居物外，人间寒暑任轮回。\\n星辰聚会入离乡，日月盈亏助药王。\\n三候火烧金鼎宝，五符水炼玉壶浆。\\n乾坤反覆龙收雾，卯酉相吞虎放光。\\n入室用机擒捉取，一丸丹点体纯阳。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('446', 'poem', '唐', '陈子昂', '送魏大从军', '匈奴犹未灭，魏绛复从戎。\\n怅别三河道，言追六郡雄。\\n雁山横代北，狐塞接云中。\\n勿使燕然上，惟留汉将功。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('447', 'poem', '唐', '杜甫', '八月十五夜月二首', '满月飞明镜，归心折大刀。\\n转蓬行地远，攀桂仰天高。\\n水路疑霜雪，林栖见羽毛。\\n此时瞻白兔，直欲数秋毫。\\n稍下巫山峡，犹衔白帝城。\\n气沈全浦暗，轮仄半楼明。\\n刁斗皆催晓，蟾蜍且自倾。\\n张弓倚残魄，不独汉家营。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('448', 'poem', '唐', '李白', '北风行', '烛龙栖寒门，光曜犹旦开。\\n日月照之何不及此？惟有北风号怒天上来。\\n燕山雪花大如席，片片吹落轩辕台。\\n幽州思妇十二月，停歌罢笑双蛾摧。\\n倚门望行人，念君长城苦寒良可哀。\\n别时提剑救边去，遗此虎文金鞞靫。\\n中有一双白羽箭，蜘蛛结网生尘埃。\\n箭空在，人今战死不复回。\\n不忍见此物，焚之已成灰。\\n黄河捧土尚可塞，北风雨雪恨难裁。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('449', 'poem', '唐', '柳宗元', '溪居', '久为簪组累，幸此南夷谪。\\n闲依农圃邻，偶似山林客。\\n晓耕翻露草，夜榜响溪石。\\n来往不逢人，长歌楚天碧。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('450', 'poem', '唐', '李白', '鹦鹉洲', '鹦鹉来过吴江水，江上洲传鹦鹉名。\\n鹦鹉西飞陇山去，芳洲之树何青青。\\n烟开兰叶香风暖，岸夹桃花锦浪生。\\n迁客此时徒极目，长洲孤月向谁明。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('451', 'poem', '唐', '殷尧藩', '端午日', '少年佳节倍多情，老去谁知感慨生；不效艾符趋习俗，但祈蒲酒话升平。\\n鬓丝日日添白头，榴锦年年照眼明；千载贤愚同瞬息，几人湮没几垂名。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('452', 'poem', '唐', '崔涂', '除夜 / 巴山道中除夜书怀 / 除夜有怀', '迢递三巴路，羁危万里身。\\n乱山残雪夜，孤烛异乡人。\\n渐与骨肉远，转于僮仆亲。\\n那堪正飘泊，明日岁华新。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('453', 'poem', '唐', '岑参', '碛中作', '走马西来欲到天，辞家见月两回圆。\\n今夜不知何处宿，平沙万里绝人烟。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('454', 'poem', '唐', '曹邺', '古词', '高阙碍飞鸟，人言是君家。\\n经年不归去，爱妾面上花。\\n妾面虽有花，妾心非女萝。\\n郎妻自不重，于妾欲如何。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('455', 'poem', '唐', '张祜', '送杨秀才游蜀', '鄂渚逢游客，瞿塘上去船。\\n峡深明月夜，江静碧云天。\\n旧俗巴渝舞，新声蜀国弦。\\n不堪挥惨恨，一涕自潸然。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('456', 'poem', '唐', '李华', '春行即兴', '宜阳城下草萋萋，涧水东流复向西。\\n芳树无人花自落，春山一路鸟空啼。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('457', 'poem', '唐', '白居易', '清明夜', '好风胧月清明夜，碧砌红轩刺史家。\\n独绕回廊行复歇，遥听弦管暗看花。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('458', 'poem', '唐', '孟浩然', '送朱大入秦', '游人五陵去，宝剑值千金。\\n分手脱相赠，平生一片心。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('459', 'poem', '唐', '李白', '王右军', '右军本清真，潇洒出风尘。\\n山阴过羽客，爱此好鹅宾。\\n扫素写道经，笔精妙入神。\\n书罢笼鹅去，何曾别主人。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('460', 'poem', '唐', '李商隐', '无题·八岁偷照镜', '八岁偷照镜，长眉已能画。\\n十岁去踏青，芙蓉作裙衩。\\n十二学弹筝，银甲不曾卸。\\n十四藏六亲，悬知犹未嫁。\\n十五泣春风，背面秋千下。', '2018-03-09 14:58:09');
INSERT INTO `t_poem` VALUES ('461', 'poem', '唐', '司空图', '乐府', '五更窗下簇妆台，已怕堂前阿母催。\\n满鸭香薰鹦鹉睡，隔帘灯照牡丹开。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('462', 'poem', '唐', '杜甫', '对雪', '战哭多新鬼，愁吟独老翁。\\n乱云低薄暮，急雪舞回风。\\n瓢弃尊无绿，炉存火似红。\\n数州消息断，愁坐正书空。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('463', 'poem', '唐', '沈佺期', '夜宿七盘岭', '独游千里外，高卧七盘西。\\n晓月临窗近，天河入户低。\\n芳春平仲绿，清夜子规啼。\\n浮客空留听，褒城闻曙鸡。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('464', 'poem', '唐', '李商隐', '北青萝', '残阳西入崦，茅屋访孤僧。\\n落叶人何在，寒云路几层。\\n独敲初夜磬，闲倚一枝藤。\\n世界微尘里，吾宁爱与憎。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('465', 'poem', '唐', '王维', '归嵩山作', '清川带长薄，车马去闲闲。\\n流水如有意，暮禽相与还。\\n荒城临古渡，落日满秋山。\\n迢递嵩高下，归来且闭关。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('466', 'poem', '唐', '王维', '酌酒与裴迪', '酌酒与君君自宽，人情翻覆似波澜。\\n白首相知犹按剑，朱门先达笑弹冠。\\n草色全经细雨湿，花枝欲动春风寒。\\n世事浮云何足问，不如高卧且加餐。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('467', 'poem', '唐', '王维', '青溪 / 过青溪水作', '言入黄花川，每逐清溪水。\\n随山将万转，趣途无百里。\\n声喧乱石中，色静深松里。\\n漾漾泛菱荇，澄澄映葭苇。\\n我心素已闲，清川澹如此。\\n请留盘石上，垂钓将已矣。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('468', 'poem', '唐', '刘长卿', '饯别王十一南游', '望君烟水阔，挥手泪沾巾。飞鸟没何处，青山空向人。\\n长江一帆远，落日五湖春。谁见汀洲上，相思愁白蘋，', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('469', 'poem', '唐', '戴叔伦', '苏溪亭', '苏溪亭上草漫漫，谁倚东风十二阑。\\n燕子不归春事晚，一汀烟雨杏花寒。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('470', 'poem', '唐', '王勃', '江亭夜月送别二首', '江送巴南水，山横塞北云。\\n津亭秋月夜，谁见泣离群。\\n乱烟笼碧砌，飞月向南端。\\n寂寞离亭掩，江山此夜寒。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('471', 'poem', '唐', '贾至', '出塞曲', '万里平沙一聚尘，南飞羽檄北来人。\\n传道五原烽火急，单于昨夜寇新秦。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('472', 'poem', '唐', '王涯', '秋夜曲', '桂魄初生秋露微，轻罗已薄未更衣。\\n银筝夜久殷勤弄，心怯空房不忍归。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('473', 'poem', '唐', '马戴', '落日怅望', '孤云与归鸟，千里片时间。\\n念我一何滞，辞家久未还。\\n微阳下乔木，远色隐秋山。\\n临水不敢照，恐惊平昔颜。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('474', 'poem', '唐', '柳宗元', '与浩初上人同看山寄京华亲故', '海畔尖山似剑铓，秋来处处割愁肠。\\n若为化得身千亿，散上峰头望故乡。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('475', 'poem', '唐', '孟浩然', '秋登兰山寄张五', '北山白云里，隐者自怡悦。\\n相望试登高，心飞逐鸟灭。\\n愁因薄暮起，兴是清秋发。\\n时见归村人，沙行渡头歇。\\n天边树若荠，江畔洲如月。\\n何当载酒来，共醉重阳节。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('476', 'poem', '唐', '李涉', '题鹤林寺僧舍', '终日昏昏醉梦间，忽闻春尽强登山。\\n因过竹院逢僧话，又得浮生半日闲。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('477', 'poem', '唐', '杜甫', '哀江头', '少陵野老吞声哭，春日潜行曲江曲。\\n江头宫殿锁千门，细柳新蒲为谁绿？\\n忆昔霓旌下南苑，苑中万物生颜色。\\n昭阳殿里第一人，同辇随君侍君侧。\\n辇前才人带弓箭，白马嚼啮黄金勒。\\n翻身向天仰射云，一笑正坠双飞翼。\\n明眸皓齿今何在？血污游魂归不得。\\n清渭东流剑阁深，去住彼此无消息。\\n人生有情泪沾臆，江水江花岂终极！\\n黄昏胡骑尘满城，欲往城南望城北。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('478', 'poem', '唐', '白居易', '同李十一醉忆元九', '花时同醉破春愁，醉折花枝作酒筹。\\n忽忆故人天际去，计程今日到梁州。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('479', 'poem', '唐', '杜甫', '日暮', '牛羊下来久，各已闭柴门。\\n风月自清夜，江山非故园。\\n石泉流暗壁，草露滴秋根。\\n头白灯明里，何须花烬繁。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('480', 'poem', '唐', '刘禹锡', '竹枝词·山桃红花满上头', '山桃红花满上头，蜀江春水拍山流。\\n花红易衰似郎意，水流无限似侬愁。', '2018-03-09 14:58:18');
INSERT INTO `t_poem` VALUES ('481', 'poem', '唐', '吕岩', '警世', '二八佳人体似酥，腰间仗剑斩凡夫。\\n虽然不见人头落，暗里教君骨髓枯。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('482', 'poem', '唐', '李贺', '七夕', '别浦今朝暗，罗帷午夜愁。\\n鹊辞穿线月，花入曝衣楼。\\n天上分金镜，人间望玉钩。\\n钱塘苏小小，更值一年秋。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('483', 'poem', '唐', '罗隐', '杏花', '暖气潜催次第春，梅花已谢杏花新。\\n半开半落闲园里，何异荣枯世上人。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('484', 'poem', '唐', '张祜', '正月十五夜灯', '千门开锁万灯明，正月中旬动帝京。\\n三百内人连袖舞，一时天上著词声。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('485', 'poem', '唐', '韩偓', '与僧', '江海扁舟客，云山一衲僧。\\n相逢两无语，若个是南能。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('486', 'poem', '唐', '李白', '金陵望汉江', '汉江回万里，派作九龙盘。\\n横溃豁中国，崔嵬飞迅湍。\\n六帝沦亡后，三吴不足观。\\n我君混区宇，垂拱众流安。\\n今日任公子，沧浪罢钓竿。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('487', 'poem', '唐', '卢照邻', '曲池荷', '浮香绕曲岸，圆影覆华池。\\n常恐秋风早，飘零君不知。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('488', 'poem', '唐', '李商隐', '蝉', '本以高难饱，徒劳恨费声。\\n五更疏欲断，一树碧无情。\\n薄宦梗犹泛，故园芜已平。\\n烦君最相警，我亦举家清。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('489', 'poem', '唐', '杜甫', '梦李白二首·其一', '死别已吞声，生别常恻恻。\\n江南瘴疠地，逐客无消息。\\n故人入我梦，明我长相忆。\\n恐非平生魂，路远不可测。\\n魂来枫叶青，魂返关塞黑。\\n君今在罗网，何以有羽翼。\\n落月满屋梁，犹疑照颜色。\\n水深波浪阔，无使皎龙得。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('490', 'poem', '唐', '韦庄', '章台夜思', '清瑟怨遥夜，绕弦风雨哀。\\n孤灯闻楚角，残月下章台。\\n芳草已云暮，故人殊未来。\\n乡书不可寄，秋雁又南回。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('491', 'poem', '唐', '卢纶', '长安春望', '东风吹雨过青山，却望千门草色闲。\\n家在梦中何日到，春生江上几人还。\\n川原缭绕浮云外，宫阙参差落照间。\\n谁念为儒逢世难，独将衰鬓客秦关。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('492', 'poem', '唐', '刘长卿', '送方外上人 / 送上人', '孤云将野鹤，岂向人间住。\\n莫买沃洲山，时人已知处。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('493', 'poem', '唐', '司空图', '诗品二十四则·雄浑', '大用外腓，真体内充。\\n返虚入浑，积健为雄。\\n具备万物，横绝太空。\\n荒荒油云，寥寥长风。\\n超以象外，得其环中。\\n持之匪强，来之无穷。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('494', 'poem', '唐', '沈佺期', '古意呈补阙乔知之 / 古意 / 独不见', '卢家少妇郁金堂，海燕双栖玳瑁梁。\\n九月寒砧催木叶，十年征戍忆辽阳。\\n白狼河北音书断，丹凤城南秋夜长。\\n谁谓含愁独不见，更教明月照流黄。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('495', 'poem', '唐', '元稹', '遣悲怀三首·其三', '闲坐悲君亦自悲，百年多是几多时。\\n邓攸无子寻知命，潘岳悼亡犹费词。\\n同穴窅冥何所望，他生缘会更难期。\\n惟将终夜常开眼，报答平生未展眉。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('496', 'poem', '唐', '张旭', '山行留客', '山光物态弄春辉，莫为轻阴便拟归。\\n纵使晴明无雨色，入云深处亦沾衣。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('497', 'poem', '唐', '白居易', '八月十五日夜湓亭望月', '昔年八月十五夜，曲江池畔杏园边。\\n今年八月十五夜，湓浦沙头水馆前。\\n西北望乡何处是，东南见月几回圆。\\n昨风一吹无人会，今夜清光似往年。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('498', 'poem', '唐', '王之涣', '宴词', '长堤春水绿悠悠，畎入漳河一道流。\\n莫听声声催去棹，桃溪浅处不胜舟。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('499', 'poem', '唐', '马戴', '灞上秋居', '灞原风雨定，晚见雁行频。\\n落叶他乡树，寒灯独夜人。\\n空园白露滴，孤壁野僧邻。\\n寄卧郊扉久，何门致此身。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('500', 'poem', '唐', '知玄', '五岁咏花', '花开满树红，花落万枝空。\\n唯馀一孕在，明日定随风。', '2018-03-09 14:58:25');
INSERT INTO `t_poem` VALUES ('501', 'poem', '唐', '杜甫', '春归', '苔径临江竹，茅檐覆地花。\\n别来频甲子，倏忽又春华。\\n倚杖看孤石，倾壶就浅沙。\\n远鸥浮水静，轻燕受风斜。\\n世路虽多梗，吾生亦有涯。\\n此身醒复醉，乘兴即为家。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('502', 'poem', '唐', '张九龄', '湖口望庐山瀑布泉 / 湖口望庐山瀑布水', '万丈红泉落，迢迢半紫氛。\\n奔流下杂树，洒落出重云。\\n日照虹霓似，天清风雨闻。\\n灵山多秀色，空水共氤氲。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('503', 'poem', '唐', '刘禹锡', '竹枝词二首·其二', '楚水巴山江雨多，巴人能唱本乡歌。\\n今朝北客思归去，回入纥那披绿罗。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('504', 'poem', '唐', '白居易', '早出晚归', '早起或因携酒出，晚归多是看花回。\\n若抛风景长闲坐，自问东京作底来。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('505', 'poem', '唐', '韩愈', '题张十一旅舍三咏榴花 / 题榴花', '五月榴花照眼明，枝间时见子初成。\\n可怜此地无车马，颠倒青苔落绛英。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('506', 'poem', '唐', '韦应物', '寄全椒山中道士', '今朝郡斋冷，忽念山中客。\\n涧底束荆薪，归来煮白石。\\n欲持一瓢酒，远慰风雨夕。\\n落叶满空山，何处寻行迹。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('507', 'poem', '唐', '李白', '九月十日即事', '昨日登高罢，今朝更举觞。\\n菊花何太苦，遭此两重阳？', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('508', 'poem', '唐', '陆畅', '惊雪', '怪得北风急，前庭如月辉。\\n天人宁许巧，剪水作花飞。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('509', 'poem', '唐', '杜甫', '观公孙大娘弟子舞剑器行', '大历二年十月十九日，夔府别驾元持宅，见临颍李十二娘舞剑器，壮其蔚跂，问其所师，曰：“余公孙大娘弟子也。”开元三载，余尚童稚，记于郾城观公孙氏，舞剑器浑脱，浏漓顿挫，独出冠时，自高头宜春梨园二伎坊内人洎外供奉，晓是舞者，圣文神武皇帝初，公孙一人而已。玉貌锦衣，况余白首，今兹弟子，亦非盛颜。既辨其由来，知波澜莫二，抚事慷慨，聊为《剑器行》。昔者吴人张旭，善草书帖，数常于邺县见公孙大娘舞西河剑器，自此草书长进，豪荡感激，即公孙可知矣。\\n昔有佳人公孙氏，一舞剑器动四方。\\n观者如山色沮丧，天地为之久低昂。\\n霍如羿射九日落，矫如群帝骖龙翔。\\n来如雷霆收震怒，罢如江海凝清光。\\n绛唇珠袖两寂寞，晚有弟子传芬芳。\\n临颍美人在白帝，妙舞此曲神扬扬。\\n与余问答既有以，感时抚事增惋伤。\\n先帝侍女八千人，公孙剑器初第一。\\n五十年间似反掌，风尘澒洞昏王室。\\n梨园弟子散如烟，女乐馀姿映寒日。\\n金粟堆南木已拱，瞿唐石城草萧瑟。\\n玳筵急管曲复终，乐极哀来月东出。\\n老夫不知其所往，足茧荒山转愁疾。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('510', 'poem', '唐', '白居易', '醉赠刘二十八使君', '为我引杯添酒饮，与君把箸击盘歌。\\n诗称国手徒为尔，命压人头不奈何。\\n举眼风光长寂寞，满朝官职独蹉跎。\\n亦知合被才名折，二十三年折太多。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('511', 'poem', '唐', '白居易', '观游鱼', '绕池闲步看鱼游，正值儿童弄钓舟。\\n一种爱鱼心各异，我来施食尔垂钩。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('512', 'poem', '唐', '白居易', '春题湖上', '湖上春来似画图，乱峰围绕水平铺。\\n松排山面千重翠，月点波心一颗珠。\\n碧毯线头抽早稻，青罗裙带展新蒲。\\n未能抛得杭州去，一半勾留是此湖。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('513', 'poem', '唐', '李建枢', '咏月', '昨夜圆非今夜圆，却疑圆处减婵娟。\\n一年十二度圆缺，能得几多时少年。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('514', 'poem', '唐', '武则天', '唐享昊天乐·第七', '尊浮九酝，礼备三周。\\n陈诚菲奠，契福神猷。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('515', 'poem', '唐', '杜甫', '花鸭', '花鸭无泥滓，阶前每缓行。\\n羽毛知独立，黑白太分明。\\n不觉群心妒，休牵众眼惊。\\n稻粱沾汝在，作意莫先鸣。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('516', 'poem', '唐', '刘长卿', '长沙过贾谊宅', '三年谪宦此栖迟，万古惟留楚客悲。\\n秋草独寻人去后，寒林空见日斜时。\\n汉文有道恩犹薄，湘水无情吊岂知。\\n寂寂江山摇落处，怜君何事到天涯。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('517', 'poem', '唐', '白居易', '望驿台', '靖安宅里当窗柳，望驿台前扑地花。\\n两处春光同日尽，居人思客客思家。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('518', 'poem', '唐', '高蟾', '金陵晚望', '曾伴浮云归晚翠，犹陪落日泛秋声。\\n世间无限丹青手，一片伤心画不成。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('519', 'poem', '唐', '佚名', '杂诗·旧山虽在不关身', '旧山虽在不关身，且向长安过暮春。\\n一树梨花一溪月，不知今夜属何人？', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('520', 'poem', '唐', '李煜', '感怀', '又见桐花发旧枝，一楼烟雨暮凄凄。\\n凭阑惆怅人谁会，不觉潸然泪眼低。\\n层城无复见娇姿，佳节缠哀不自持。\\n空有当年旧烟月，芙蓉城上哭蛾眉。', '2018-03-09 14:58:36');
INSERT INTO `t_poem` VALUES ('521', 'poem', '唐', '王维', '酬张少府', '晚年唯好静，万事不关心。\\n自顾无长策，空知返旧林。\\n松风吹解带，山月照弹琴。\\n君问穷通理，渔歌入浦深。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('522', 'poem', '唐', '蔡孚', '郊庙歌辞·享龙池乐章·第二章', '帝宅王家大道边，神马龙龟涌圣泉。\\n昔日昔时经此地，看来看去渐成川。\\n歌台舞榭宜正月，柳岸梅洲胜往年。\\n莫言波上春云少，只为从龙直上天。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('523', 'poem', '唐', '韩偓', '夜深 / 寒食夜', '恻恻轻寒翦翦风，小梅飘雪杏花红。\\n夜深斜搭秋千索，楼阁朦胧烟雨中。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('524', 'poem', '唐', '杜甫', '绝句', '迟日江山丽，春风花草香。\\n泥融飞燕子，沙暖睡鸳鸯。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('525', 'poem', '唐', '李白', '夜泊牛渚怀古', '牛渚西江夜，青天无片云。\\n登舟望秋月，空忆谢将军。\\n余亦能高咏，斯人不可闻。\\n明朝挂帆席，枫叶落纷纷。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('526', 'poem', '唐', '温庭筠', '苏武庙', '苏武魂销汉使前，古祠高树两茫然。\\n云边雁断胡天月，陇上羊归塞草烟。\\n回日楼台非甲帐，去时冠剑是丁年。\\n茂陵不见封侯印，空向秋波哭逝川。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('527', 'poem', '唐', '张祜', '题金陵渡', '金陵津渡小山楼，一宿行人自可愁。\\n潮落夜江斜月里，两三星火是瓜州。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('528', 'poem', '唐', '李商隐', '暮秋独游曲江', '荷叶生时春恨生，荷叶枯时秋恨成。\\n深知身在情长在，怅望江头江水声。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('529', 'poem', '唐', '韩偓', '晓日', '天际霞光入水中，水中天际一时红。\\n直须日观三更后，首送金乌上碧空。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('530', 'poem', '唐', '韦应物', '淮上喜会梁川故人 / 淮上喜会梁州故人', '江汉曾为客，相逢每醉还。\\n浮云一别后，流水十年间。\\n欢笑情如旧，萧疏鬓已斑。\\n何因北归去，淮上对秋山。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('531', 'poem', '唐', '刘长卿', '重送裴郎中贬吉州', '猿啼客散暮江头，人自伤心水自流。\\n同作逐臣君更远，青山万里一孤舟。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('532', 'poem', '唐', '武则天', '唐享昊天乐·第六', '昭昭上帝，穆穆下临。\\n礼崇备物，乐奏锵金。\\n兰羞委荐，桂醑盈斟。\\n敢希明德，幸罄庄心。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('533', 'poem', '唐', '李贺', '致酒行', '零落栖迟一杯酒，主人奉觞客长寿。主父西游困不归，\\n家人折断门前柳。吾闻马周昔作新丰客，天荒地老无人识。\\n空将笺上两行书，直犯龙颜请恩泽。我有迷魂招不得，\\n雄鸡一声天下白。少年心事当拏云，谁念幽寒坐呜呃。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('534', 'poem', '唐', '刘皂', '旅次朔方 / 渡桑干', '客舍并州数十霜，归心日夜忆咸阳。\\n无端又渡桑干水，却望并州似故乡。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('535', 'poem', '唐', '杜甫', '野望', '西山白雪三城戍，南浦清江万里桥。\\n海内风尘诸弟隔，天涯涕泪一身遥。\\n惟将迟暮供多病，未有涓埃答圣朝。\\n跨马出郊时极目，不堪人事日萧条。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('536', 'poem', '唐', '韦庄', '思归', '暖丝无力自悠扬，牵引东风断客肠。\\n外地见花终寂寞，异乡闻乐更凄凉。\\n红垂野岸樱还熟，绿染回汀草又芳。\\n旧里若为归去好，子期凋谢吕安亡。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('537', 'poem', '唐', '郎士元', '柏林寺南望', '溪上遥闻精舍钟，泊舟微径度深松。\\n青山霁后云犹在，画出东南四五峰。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('538', 'poem', '唐', '李商隐', '菊花', '暗暗淡淡紫，融融冶冶黄。\\n陶令篱边色，罗含宅里香。\\n几时禁重露，实是怯残阳。\\n愿泛金鹦鹉，升君白玉堂。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('539', 'poem', '唐', '李白', '长歌行', '桃李待日开，荣华照当年。\\n东风动百物，草木尽欲言。\\n枯枝无丑叶，涸水吐清泉。\\n大力运天地，羲和无停鞭。\\n功名不早著，竹帛将何宣。\\n桃李务青春，谁能贯白日。\\n富贵与神仙，蹉跎成两失。\\n金石犹销铄，风霜无久质。\\n畏落日月后，强欢歌与酒。\\n秋霜不惜人，倏忽侵蒲柳。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('540', 'poem', '唐', '王勃', '咏风', '肃肃凉景生，加我林壑清。\\n驱烟寻涧户，卷雾出山楹。\\n去来固无迹，动息如有情。\\n日落山水静，为君起松声。', '2018-03-09 14:58:48');
INSERT INTO `t_poem` VALUES ('541', 'poem', '唐', '鱼玄机', '江陵愁望寄子安 / 江陵愁望有寄', '枫叶千枝复万枝，江桥掩映暮帆迟。\\n忆君心似西江水，日夜东流无歇时。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('542', 'poem', '唐', '张籍', '酬朱庆馀', '越女新妆出镜心，自知明艳更沉吟。\\n齐纨未是人间贵，一曲菱歌敌万金。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('543', 'poem', '唐', '李商隐', '无题·来是空言去绝踪', '来是空言去绝踪，月斜楼上五更钟。\\n梦为远别啼难唤，书被催成墨未浓。\\n蜡照半笼金翡翠，麝熏微度绣芙蓉。\\n刘郎已恨蓬山远，更隔蓬山一万重！', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('544', 'poem', '唐', '刘禹锡', '和乐天春词 / 春词', '新妆宜面下朱楼，深锁春光一院愁。\\n行到中庭数花朵，蜻蜓飞上玉搔头。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('545', 'poem', '唐', '白居易', '咏菊', '一夜新霜著瓦轻，芭蕉新折败荷倾。\\n耐寒唯有东篱菊，金粟初开晓更清。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('546', 'poem', '唐', '裴士淹', '白牡丹', '长安年少惜春残，争认慈恩紫牡丹。\\n别有玉盘乘露冷，无人起就月中看。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('547', 'poem', '唐', '吕洞宾', '五言', '悟了长生理，秋莲处处开。\\n金童登锦帐，玉女下香阶。\\n虎啸天魂住，龙吟地魄来。\\n有人明此道，立使返婴孩。\\n姹女住南方，身边产太阳。\\n蟾宫烹玉液，坎户炼琼浆。\\n过去神仙饵，今来到我尝。\\n一杯延万纪，物外任翱翔。\\n顿悟黄芽理，阴阳禀自然。\\n乾坤炉里炼，日月鼎中煎。\\n木产长生汞，金烹续命铅。\\n世人明此道，立便返童颜。\\n宇宙产黄芽，经炉煅作砂。\\n阴阳烹五彩，水火炼三花。\\n鼎内龙降虎，壶中龟遣蛇。\\n功成归物外，自在乐烟霞。\\n要觅长生路，除非认本元。\\n都来一味药，刚道数千般。\\n丹鼎烹成汞，炉中炼就铅。\\n依时服一粒，白日上冲天。\\n姹女住瑶台，仙花满地开。\\n金苗从此出，玉蕊自天来。\\n凤舞长生曲，鸾歌续命杯。\\n有人明此道，海变已千回。\\n古往诸仙子，根元占甲庚。\\n水中闻虎啸，火里见龙行。\\n进退穷三候，相吞用八纮。\\n冲天功行满，寒暑不能争。\\n我悟长生理，太阳伏太阴。\\n离宫生白玉，坎户产黄金。\\n要主君臣义，须存子母心。\\n九重神室内，虎啸与龙吟。\\n灵丹产太虚，九转入重炉。\\n浴就红莲颗，烧成白玉珠。\\n水中铅一两，火内汞三铢。\\n吃了瑶台宝，升天任海枯。\\n姹女住离宫，身边产雌雄。\\n炉中七返毕，鼎内九还终。\\n悟了鱼投水，迷因鸟在笼。\\n耄年服一粒，立地变冲童。\\n盗得乾坤祖，阴阳是本宗。\\n天魂生白虎，地魄产青龙。\\n运宝泥丸在，搬精入上宫。\\n有人明此法，万载貌如童。\\n要觅金丹理，根元不易逢。\\n三才七返足，四象九还终。\\n浴就微微白，烧成渐渐红。\\n一丸延万纪，物外去冲冲。\\n个个觅长生，根元不易寻。\\n要贪天上宝，须去世间琛。\\n炼就水中火，烧成阳内阴。\\n祖师亲有语，一味水中金。\\n万物皆生土，如人得本元。\\n青龙精是汞，白虎水为铅。\\n悟者子投母，迷应地是天。\\n将来物外客，个个补丹田。\\n二十四神清，三千功行成。\\n寒云连地转，圣日满天明。\\n玉子偏宜种，金田岂在耕。\\n此中真妙理，谁道不长生。\\n妙妙妙中妙，玄玄玄更玄。\\n动言俱演道，语默尽神仙。\\n在掌如珠异，当空似月圆。\\n他时功满后，直入大罗天。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('548', 'poem', '唐', '李益', '喜见外弟又言别', '十年离乱后，长大一相逢。\\n问姓惊初见，称名忆旧容。\\n别来沧海事，语罢暮天钟。\\n明日巴陵道，秋山又几重。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('549', 'poem', '唐', '赵嘏', '长安晚秋 / 秋望 / 秋夕', '云物凄凉拂曙流，汉家宫阙动高秋。\\n残星几点雁横塞，长笛一声人倚楼。\\n紫艳半开篱菊静，红衣落尽渚莲愁。\\n鲈鱼正美不归去，空戴南冠学楚囚。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('550', 'poem', '唐', '李端', '听筝', '鸣筝金粟柱，素手玉房前。\\n欲得周郎顾，时时误拂弦。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('551', 'poem', '唐', '白居易', '南湖早春', '风回云断雨初晴，返照湖边暖复明。\\n乱点碎红山杏发，平铺新绿水蘋生。\\n翅低白雁飞仍重，舌涩黄鹂语未成。\\n不道江南春不好，年年衰病减心情。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('552', 'poem', '唐', '权德舆', '岭上逢久别者又别', '十年曾一别，征路此相逢。\\n马首向何处，夕阳千万峰。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('553', 'poem', '唐', '李白', '经乱离后天恩流夜郎忆旧游书怀赠江夏韦太守良宰', '天上白玉京，十二楼五城。\\n仙人抚我顶，结发受长生。\\n误逐世间乐，颇穷理乱情。\\n九十六圣君，浮云挂空名。\\n天地赌一掷，未能忘战争。\\n试涉霸王略，将期轩冕荣。\\n时命乃大谬，弃之海上行。\\n学剑翻自哂，为文竟何成。\\n剑非万人敌，文窃四海声。\\n儿戏不足道，五噫出西京。\\n临当欲去时，慷慨泪沾缨。\\n叹君倜傥才，标举冠群英。\\n开筵引祖帐，慰此远徂征。\\n鞍马若浮云，送余骠骑亭。\\n歌钟不尽意，白日落昆明。\\n十月到幽州，戈鋋若罗星。\\n君王弃北海，扫地借长鲸。\\n呼吸走百川，燕然可摧倾。\\n心知不得语，却欲栖蓬瀛。\\n弯弧惧天狼，挟矢不敢张。\\n揽涕黄金台，呼天哭昭王。\\n无人贵骏骨，騄耳空腾骧。\\n乐毅倘再生，于今亦奔亡。\\n蹉跎不得意，驱马还贵乡。\\n逢君听弦歌，肃穆坐华堂。\\n百里独太古，陶然卧羲皇。\\n征乐昌乐馆，开筵列壶觞。\\n贤豪间青娥，对烛俨成行。\\n醉舞纷绮席，清歌绕飞梁。\\n欢娱未终朝，秩满归咸阳。\\n祖道拥万人，供帐遥相望。\\n一别隔千里，荣枯异炎凉。\\n炎凉几度改，九土中横溃。\\n汉甲连胡兵，沙尘暗云海。\\n草木摇杀气，星辰无光彩。\\n白骨成丘山，苍生竟何罪。\\n函关壮帝居，国命悬哥舒。\\n长戟三十万，开门纳凶渠。\\n公卿如犬羊，忠谠醢与菹。\\n二圣出游豫，两京遂丘墟。\\n帝子许专征，秉旄控强楚。\\n节制非桓文，军师拥熊虎。\\n人心失去就，贼势腾风雨。\\n惟君固房陵，诚节冠终古。\\n仆卧香炉顶，餐霞漱瑶泉。\\n门开九江转，枕下五湖连。\\n半夜水军来，浔阳满旌旃。\\n空名适自误，迫胁上楼船。\\n徒赐五百金，弃之若浮烟。\\n辞官不受赏，翻谪夜郎天。\\n夜郎万里道，西上令人老。\\n扫荡六合清，仍为负霜草。\\n日月无偏照，何由诉苍昊。\\n良牧称神明，深仁恤交道。\\n一忝青云客，三登黄鹤楼。\\n顾惭祢处士，虚对鹦鹉洲。\\n樊山霸气尽，寥落天地秋。\\n江带峨眉雪，川横三峡流。\\n万舸此中来，连帆过扬州。\\n送此万里目，旷然散我愁。\\n纱窗倚天开，水树绿如发。\\n窥日畏衔山，促酒喜得月。\\n吴娃与越艳，窈窕夸铅红。\\n呼来上云梯，含笑出帘栊。\\n对客小垂手，罗衣舞春风。\\n宾跪请休息，主人情未极。\\n览君荆山作，江鲍堪动色。\\n清水出芙蓉，天然去雕饰。\\n逸兴横素襟，无时不招寻。\\n朱门拥虎士，列戟何森森。\\n剪凿竹石开，萦流涨清深。\\n登台坐水阁，吐论多英音。\\n片辞贵白璧，一诺轻黄金。\\n谓我不愧君，青鸟明丹心。\\n五色云间鹊，飞鸣天上来。\\n传闻赦书至，却放夜郎回。\\n暖气变寒谷，炎烟生死灰。\\n君登凤池去，忽弃贾生才。\\n桀犬尚吠尧，匈奴笑千秋。\\n中夜四五叹，常为大国忧。\\n旌旆夹两山，黄河当中流。\\n连鸡不得进，饮马空夷犹。\\n安得羿善射，一箭落旄头。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('554', 'poem', '唐', '杜甫', '咏怀古迹五首·其五', '诸葛大名垂宇宙，宗臣遗像肃清高。\\n三分割据纡筹策，万古云霄一羽毛。\\n伯仲之间见伊吕，指挥若定失萧曹。\\n运移汉祚终难复，志决身歼军务劳。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('555', 'poem', '唐', '李益', '游子吟', '女羞夫婿薄，客耻主人贱。\\n遭遇同众流，低回愧相见。\\n君非青铜镜，何事空照面。\\n莫以衣上尘，不谓心如练。\\n人生当荣盛，待士勿言倦。\\n君看白日驰，何异弦上箭。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('556', 'poem', '唐', '司空图', '秋景', '景物皆难驻，伤春复怨秋。\\n旋书红叶落，拟画碧云收。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('557', 'poem', '唐', '李端', '拜新月·开帘见新月', '开帘见新月，便即下阶拜。\\n细语人不闻，北风吹裙带。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('558', 'poem', '唐', '李白', '游洞庭湖五首·其二', '南湖秋水夜无烟，耐可乘流直上天。\\n且就洞庭赊月色，将船买酒白云边。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('559', 'poem', '唐', '杜甫', '新安吏', '客行新安道，喧呼闻点兵。\\n借问新安吏：“县小更无丁？”\\n“府帖昨夜下，次选中男行。”\\n“中男绝短小，何以守王城？”\\n肥男有母送，瘦男独伶俜。\\n白水暮东流，青山犹哭声。\\n“莫自使眼枯，收汝泪纵横。\\n眼枯即见骨，天地终无情！\\n我军取相州，日夕望其平。\\n岂意贼难料，归军星散营。\\n就粮近故垒，练卒依旧京。\\n掘壕不到水，牧马役亦轻。\\n况乃王师顺，抚养甚分明。\\n送行勿泣血，仆射如父兄。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('560', 'poem', '唐', '王维', '皇甫岳云溪杂题五首·莲花坞', '日日采莲去，洲长多暮归。\\n弄篙莫溅水，畏湿红莲衣。', '2018-03-09 14:58:58');
INSERT INTO `t_poem` VALUES ('561', 'poem', '唐', '杜甫', '春宿左省', '花隐掖垣暮，啾啾栖鸟过。\\n星临万户动，月傍九霄多。\\n不寝听金钥，因风想玉珂。\\n明朝有封事，数问夜如何。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('562', 'poem', '唐', '杜甫', '古柏行', '孔明庙前有老柏，柯如青铜根如石。\\n霜皮溜雨四十围，黛色参天二千尺。\\n君臣已与时际会，树木犹为人爱惜。\\n云来气接巫峡长，月出寒通雪山白。\\n忆昨路绕锦亭东，先主武侯同閟宫。\\n崔嵬枝干郊原古，窈窕丹青户牖空。\\n落落盘踞虽得地，冥冥孤高多烈风。\\n扶持自是神明力，正直原因造化功。\\n大厦如倾要梁栋，万牛回首丘山重。\\n不露文章世已惊，未辞翦伐谁能送？\\n苦心岂免容蝼蚁，香叶终经宿鸾凤。\\n志士幽人莫怨嗟：古来材大难为用。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('563', 'poem', '唐', '王昌龄', '长信秋词五首', '金井梧桐秋叶黄，珠帘不卷夜来霜。\\n熏笼玉枕无颜色，卧听南宫清漏长。\\n高殿秋砧响夜阑，霜深犹忆御衣寒。\\n银灯青琐裁缝歇，还向金城明主看。\\n奉帚平明金殿开，且将团扇暂裴回。\\n玉颜不及寒鸦色，犹带昭阳日影来。\\n真成薄命久寻思，梦见君王觉后疑。\\n火照西宫知夜饮，分明复道奉恩时。\\n长信宫中秋月明，昭阳殿下捣衣声。\\n白露堂中细草迹，红罗帐里不胜情。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('564', 'poem', '唐', '李世民', '咏雨', '罩云飘远岫，喷雨泛长河。\\n低飞昏岭腹，斜足洒岩阿。\\n泫丛珠缔叶，起溜镜图波。\\n濛柳添丝密，含吹织空罗。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('565', 'poem', '唐', '白居易', '春江', '炎凉昏晓苦推迁，不觉忠州已二年。\\n闭阁只听朝暮鼓，上楼空望往来船。\\n莺声诱引来花下，草色句留坐水边。\\n唯有春江看未厌，萦砂绕石渌潺湲。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('566', 'poem', '唐', '崔道融', '田上', '雨足高田白，披蓑半夜耕。\\n人牛力俱尽，东方殊未明。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('567', 'poem', '唐', '杜荀鹤', '泾溪', '泾溪石险人兢慎，终岁不闻倾覆人。\\n却是平流无石处，时时闻说有沈沦。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('568', 'poem', '唐', '张敬忠', '边词', '五原春色旧来迟，二月垂杨未挂丝。\\n即今河畔冰开日，正是长安花落时。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('569', 'poem', '唐', '李世民', '赋萧瑀', '疾风知劲草，板荡识诚臣。\\n勇夫安识义，智者必怀仁。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('570', 'poem', '唐', '韦应物', '赋得暮雨送李胄 / 赋得暮雨送李曹', '楚江微雨里，建业暮钟时。\\n漠漠帆来重，冥冥鸟去迟。\\n海门深不见，浦树远含滋。\\n相送情无限，沾襟比散丝。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('571', 'poem', '唐', '魏承班', '渔歌子·柳如眉', '柳如眉，云似发，鲛绡雾縠笼香雪。梦魂惊，钟漏歇，窗外晓莺残月。\\n几多情，无处说，落花飞絮清明节。少年郎，容易别，一去音书断绝。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('572', 'poem', '唐', '白居易', '夜雨', '我有所念人，隔在远远乡。\\n我有所感事，结在深深肠。\\n乡远去不得，无日不瞻望。\\n肠深解不得，无夕不思量。\\n况此残灯夜，独宿在空堂。\\n秋天殊未晓，风雨正苍苍。\\n不学头陀法，前心安可忘。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('573', 'poem', '唐', '李群玉', '书院二小松', '一双幽色出凡尘，数粒秋烟二尺鳞。\\n从此静窗闻细韵，琴声长伴读书人。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('574', 'poem', '唐', '卢纶', '李端公 / 送李端', '故关衰草遍，离别正堪悲。\\n路出寒云外，人归暮雪时。\\n少孤为客早，多难识君迟。\\n掩泪空相向，风尘何处期。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('575', 'poem', '唐', '杜甫', '山寺', '野寺残僧少，山园细路高。\\n麝香眠石竹，鹦鹉啄金桃。\\n乱石通人过，悬崖置屋牢。\\n上方重阁晚，百里见秋毫。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('576', 'poem', '唐', '张籍', '成都曲', '锦江近西烟水绿，新雨山头荔枝熟。\\n万里桥边多酒家，游人爱向谁家宿。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('577', 'poem', '唐', '李贺', '马诗二十三首·其四', '此马非凡马，房星本是星。\\n向前敲瘦骨，犹自带铜声。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('578', 'poem', '唐', '杜荀鹤', '春宫怨', '早被婵娟误，欲妆临镜慵。\\n承恩不在貌，教妾若为容。\\n风暖鸟声碎，日高花影重。\\n年年越溪女，相忆采芙蓉。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('579', 'poem', '唐', '杜甫', '南征', '春岸桃花水，云帆枫树林。\\n偷生长避地，适远更沾襟。\\n老病南征日，君恩北望心。\\n百年歌自苦，未见有知音。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('580', 'poem', '唐', '于鹄', '古词三首', '素丝带金地，窗间掬飞尘。\\n偷得凤凰钗，门前乞行人。\\n新长青丝发，哑哑言语黠。\\n随人敲铜镜，街头救明月。\\n东家新长儿，与妾同时生。\\n并长两心熟，到大相呼名。', '2018-03-09 14:59:02');
INSERT INTO `t_poem` VALUES ('581', 'poem', '唐', '白居易', '简简吟', '苏家小女名简简，芙蓉花腮柳叶眼。\\n十一把镜学点妆，十二抽针能绣裳。\\n十三行坐事调品，不肯迷头白地藏。\\n玲珑云髻生花样，飘飖风袖蔷薇香。\\n殊姿异态不可状，忽忽转动如有光。\\n二月繁霜杀桃李，明年欲嫁今年死。\\n丈人阿母勿悲啼，此女不是凡夫妻。\\n恐是天仙谪人世，只合人间十三岁。\\n大都好物不坚牢，彩云易散琉璃脆。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('582', 'poem', '唐', '郑愔', '塞外三首', '塞外萧条望，征人此路赊。\\n边声乱朔马，秋色引胡笳。\\n遥嶂侵归日，长城带晚霞。\\n断蓬飞古戍，连雁聚寒沙。\\n海暗云无叶，山春雪作花。\\n丈夫期报主，万里独辞家。\\n荒垒三秋夕，穷郊万里平。\\n海阴凝独树，日气下连营。\\n戎旆霜旋重，边裘夜更轻。\\n将军犹转战，都尉不成名。\\n折柳悲春曲，吹笳断夜声。\\n明年汉使返，须筑受降城。\\n阳鸟南飞夜，阴山北地寒。\\n汉家征戍客，年岁在楼兰。\\n玉塞朔风起，金河秋月团。\\n边声入鼓吹，霜气下旌竿。\\n海外归书断，天涯旅鬓残。\\n子卿犹奉使，常向节旄看。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('583', 'poem', '唐', '白居易', '后宫词', '泪湿罗巾梦不成，夜深前殿按歌声。\\n红颜未老恩先断，斜倚薰笼坐到明。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('584', 'poem', '唐', '李涉', '牧童词', '远牧牛，绕村四面禾黍稠。\\n陂中饥乌啄牛背，令我不得戏垅头。\\n半陂草多牛散行，白犊时向芦中鸣。\\n隔堤吹叶应同伴，还鼓长鞭三四声。\\n牛群食草莫相触，官家截尔头上角。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('585', 'poem', '唐', '杜甫', '白小', '白小群分命，天然二寸鱼。\\n细微沾水族，风俗当园蔬。\\n入肆银花乱，倾箱雪片虚。\\n生成犹拾卵，尽取义何如。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('586', 'poem', '唐', '李商隐', '端居', '远书归梦两悠悠，只有空床敌素秋。\\n阶下青苔与红树，雨中寥落月中愁。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('587', 'poem', '唐', '温庭筠', '惜春词', '百舌问花花不语，低回似恨横塘雨。\\n蜂争粉蕊蝶分香，不似垂杨惜金缕。\\n愿君留得长妖韶，莫逐东风还荡摇。\\n秦女含颦向烟月，愁红带露空迢迢。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('588', 'poem', '唐', '柳宗元', '中夜起望西园值月上', '觉闻繁露坠，开户临西园。\\n寒月上东岭，泠泠疏竹根。\\n石泉远逾响，山鸟时一喧。\\n倚楹遂至旦，寂寞将何言。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('589', 'poem', '唐', '王维', '新晴野望', '新晴原野旷，极目无氛垢。\\n郭门临渡头，村树连溪口。\\n白水明田外，碧峰出山后。\\n农月无闲人，倾家事南亩。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('590', 'poem', '唐', '韦元甫', '木兰歌', '木兰抱杼嗟，借问复为谁。欲闻所戚戚，感激强其颜。\\n老父隶兵籍，气力日衰耗。岂足万里行，有子复尚少。\\n胡沙没马足，朔风裂人肤。老父旧羸病，何以强自扶。\\n木兰代父去，秣马备戎行。易却纨绮裳，洗却铅粉妆。\\n驰马赴军幕，慷慨携干将。朝屯雪山下，暮宿青海傍。\\n夜袭燕支虏，更携于阗羌。将军得胜归，士卒还故乡。\\n父母见木兰，喜极成悲伤。木兰能承父母颜，\\n却卸巾鞲理丝簧。昔为烈士雄，今为娇子容。\\n亲戚持酒贺父母，始知生女与男同。门前旧军都，\\n十年共崎岖。本结弟兄交，死战誓不渝。今者见木兰，\\n言声虽是颜貌殊。惊愕不敢前，叹息徒嘻吁。世有臣子心，\\n能如木兰节。忠孝两不渝，千古之名焉可灭。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('591', 'poem', '唐', '温庭筠', '杨柳枝·御柳如丝映九重', '御柳如丝映九重，凤凰窗映绣芙蓉。\\n景阳楼畔千条路，一面新妆待晓风。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('592', 'poem', '唐', '杜甫', '夏夜叹', '永日不可暮，炎蒸毒我肠。\\n安得万里风，飘飖吹我裳。\\n昊天出华月，茂林延疏光。\\n仲夏苦夜短，开轩纳微凉。\\n虚明见纤毫，羽虫亦飞扬。\\n物情无巨细，自适固其常。\\n念彼荷戈士，穷年守边疆。\\n何由一洗濯，执热互相望。\\n竟夕击刁斗，喧声连万方。\\n青紫虽被体，不如早还乡。\\n北城悲笳发，鹳鹤号且翔。\\n况复烦促倦，激烈思时康。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('593', 'poem', '唐', '岑参', '送李副使赴碛西官军', '火山六月应更热，赤亭道口行人绝。\\n知君惯度祁连城，岂能愁见轮台月。\\n脱鞍暂入酒家垆，送君万里西击胡。\\n功名只向马上取，真是英雄一丈夫。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('594', 'poem', '唐', '白居易', '秋雨夜眠', '凉冷三秋夜，安闲一老翁。\\n卧迟灯灭后，睡美雨声中。\\n灰宿温瓶火，香添暖被笼。\\n晓晴寒未起，霜叶满阶红。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('595', 'poem', '唐', '杜甫', '潼关吏', '士卒何草草，筑城潼关道。\\n大城铁不如，小城万丈馀。\\n借问潼关吏，修关还备胡。\\n要我下马行，为我指山隅。\\n连云列战格，飞鸟不能逾。\\n胡来但自守，岂复忧西都。\\n丈人视要处，窄狭容单车。\\n艰难奋长戟，万古用一夫。\\n哀哉桃林战，百万化为鱼。\\n请嘱防关将，慎勿学哥舒。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('596', 'poem', '唐', '李冶', '明月夜留别', '离人无语月无声，明月有光人有情。\\n别后相思人似月，云间水上到层城。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('597', 'poem', '唐', '李白', '独酌', '春草如有意，罗生玉堂阴。\\n东风吹愁来，白发坐相侵。\\n独酌劝孤影，闲歌面芳林。\\n长松尔何知，萧瑟为谁吟。\\n手舞石上月，膝横花间琴。\\n过此一壶外，悠悠非我心。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('598', 'poem', '唐', '李白', '行路难·其三', '有耳莫洗颍川水，有口莫食首阳蕨。\\n含光混世贵无名，何用孤高比云月？\\n吾观自古贤达人，功成不退皆殒身。\\n子胥既弃吴江上，屈原终投湘水滨。\\n陆机雄才岂自保？李斯税驾苦不早。\\n华亭鹤唳讵可闻？上蔡苍鹰何足道？\\n君不见吴中张翰称达生，秋风忽忆江东行。\\n且乐生前一杯酒，何须身后千载名？', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('599', 'poem', '唐', '钱珝', '未展芭蕉', '冷烛无烟绿蜡干，芳心犹卷怯春寒。\\n一缄书札藏何事，会被东风暗拆看。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('600', 'poem', '唐', '刘禹锡', '蜀先主庙', '天地英雄气，千秋尚凛然。\\n势分三足鼎，业复五铢钱。\\n得相能开国，生儿不象贤。\\n凄凉蜀故妓，来舞魏宫前。', '2018-03-09 14:59:10');
INSERT INTO `t_poem` VALUES ('601', 'poem', '唐', '王维', '终南山', '太乙近天都，连山接海隅。\\n白云回望合，青霭入看无。\\n分野中峰变，阴晴众壑殊。\\n欲投人处宿，隔水问樵夫。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('602', 'poem', '唐', '韦庄', '思帝乡·云髻坠', '云髻坠，凤钗垂。髻坠钗垂无力，枕函欹。\\n翡翠屏深月落，漏依依。说尽人间天上，两心知。\\n春日游，杏花吹满头。陌上谁家年少，足风流。\\n妾拟将身嫁与，一生休。纵被无情弃，不能羞。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('603', 'poem', '唐', '陆龟蒙', '奉和袭美太湖诗二十首·石板', '一片倒山屏，何时隳洞门。屹然空阔中，万古波涛痕。\\n我意上帝命，持来压泉源。恐为庚辰官，囚怪力所掀。\\n又疑广袤次，零落潜惊奔。不然遭霹雳，强半沈无垠。\\n如何造化首，便截秋云根。往事不足问，奇踪安可论。\\n吾今病烦暑，据簟常昏昏。欲从石公乞，莹理平如璊，\\n前后植桂桧，东西置琴尊。尽携天壤徒，浩唱羲皇言。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('604', 'poem', '唐', '李白', '清溪行 / 宣州清溪', '清溪清我心，水色异诸水。\\n借问新安江，见底何如此。\\n人行明镜中，鸟度屏风里。\\n向晚猩猩啼，空悲远游子。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('605', 'poem', '唐', '张九龄', '感遇·江南有丹橘', '江南有丹橘，经冬犹绿林。\\n岂伊地气暖？自有岁寒心。\\n可以荐嘉客，奈何阻重深。\\n运命唯所遇，循环不可寻。\\n徒言树桃李，此木岂无阴？', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('606', 'poem', '唐', '温庭筠', '咸阳值雨', '咸阳桥上雨如悬，万点空濛隔钓船。\\n还似洞庭春水色，晓云将入岳阳天。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('607', 'poem', '唐', '杜甫', '垂老别', '四郊未宁静，垂老不得安。\\n子孙阵亡尽，焉用身独完。\\n投杖出门去，同行为辛酸。\\n幸有牙齿存，所悲骨髓干。\\n男儿既介胄，长揖别上官。\\n老妻卧路啼，岁暮衣裳单。\\n孰知是死别，且复伤其寒。\\n此去必不归，还闻劝加餐。\\n土门壁甚坚，杏园度亦难。\\n势异邺城下，纵死时犹宽。\\n人生有离合，岂择衰老端。\\n忆昔少壮日，迟回竟长叹。\\n万国尽征戍，烽火被冈峦。\\n积尸草木腥，流血川原丹。\\n何乡为乐土，安敢尚盘桓。\\n弃绝蓬室居，塌然摧肺肝。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('608', 'poem', '唐', '苏拯', '药草', '天子恤疲瘵，坤灵奉其职。\\n年年济世功，贵贱相兼植。\\n因产众草中，所希采者识。\\n一枝当若神，千金亦何直。\\n生草不生药，无以彰士德。\\n生药不生草，无以彰奇特。\\n国忠在臣贤，民患凭药力。\\n灵草犹如此，贤人岂多得。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('609', 'poem', '唐', '岑参', '发临洮将赴北庭留别', '闻说轮台路，连年见雪飞。\\n春风曾不到，汉使亦应稀。\\n白草通疏勒，青山过武威。\\n勤王敢道远，私向梦中归。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('610', 'poem', '唐', '杜甫', '戏为六绝句', '庾信文章老更成，凌云健笔意纵横。\\n今人嗤点流传赋，不觉前贤畏后生。\\n王杨卢骆当时体，轻薄为文哂未休。\\n尔曹身与名俱灭，不废江河万古流。\\n纵使卢王操翰墨，劣于汉魏近风骚。\\n龙文虎脊皆君驭，历块过都见尔曹。\\n才力应难夸数公，凡今谁是出群雄。\\n或看翡翠兰苕上，未掣鲸鱼碧海中。\\n不薄今人爱古人，清词丽句必为邻。\\n窃攀屈宋宜方驾，恐与齐梁作后尘。\\n未及前贤更勿疑，递相祖述复先谁。\\n别裁伪体亲风雅，转益多师是汝师。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('611', 'poem', '唐', '卢照邻', '十五夜观灯', '锦里开芳宴，兰缸艳早年。\\n缛彩遥分地，繁光远缀天。\\n接汉疑星落，依楼似月悬。\\n别有千金笑，来映九枝前。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('612', 'poem', '唐', '陆龟蒙', '和袭美木兰后池三咏·白莲', '素花多蒙别艳欺，此花真合在瑶池。\\n还应有恨无人觉，月晓风清欲堕时。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('613', 'poem', '唐', '雍陶', '孤桐', '疏桐馀一干，风雨日萧条。\\n岁晚琴材老，天寒桂叶凋。\\n已悲根半死，复恐尾全焦。\\n幸在龙门下，知音肯寂寥。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('614', 'poem', '唐', '綦毋潜', '春泛若耶溪', '幽意无断绝，此去随所偶。\\n晚风吹行舟，花路入溪口。\\n际夜转西壑，隔山望南斗。\\n潭烟飞溶溶，林月低向后。\\n生事且弥漫，愿为持竿叟。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('615', 'poem', '唐', '李益', '江南曲', '嫁得瞿塘贾，朝朝误妾期。\\n早知潮有信，嫁与弄潮儿。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('616', 'poem', '唐', '李商隐', '板桥晓别', '回望高城落晓河，长亭窗户压微波。\\n水仙欲上鲤鱼去，一夜芙蓉红泪多。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('617', 'poem', '唐', '杜甫', '百忧集行', '忆年十五心尚孩，健如黄犊走复来。\\n庭前八月梨枣熟，一日上树能千回。\\n即今倏忽已五十，坐卧只多少行立。\\n强将笑语供主人，悲见生涯百忧集。\\n入门依旧四壁空，老妻睹我颜色同。\\n痴儿未知父子礼，叫怒索饭啼门东。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('618', 'poem', '唐', '白居易', '买花 / 牡丹', '帝城春欲暮，喧喧车马度。\\n共道牡丹时，相随买花去。\\n贵贱无常价，酬直看花数。\\n灼灼百朵红，戋戋五束素。\\n上张幄幕庇，旁织巴篱护。\\n水洒复泥封，移来色如故。\\n家家习为俗，人人迷不悟。\\n有一田舍翁，偶来买花处。\\n低头独长叹，此叹无人喻。\\n一丛深色花，十户中人赋。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('619', 'poem', '唐', '章碣', '焚书坑', '竹帛烟销帝业虚，关河空锁祖龙居。\\n坑灰未冷山东乱，刘项原来不读书。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('620', 'poem', '唐', '杜牧', '秋浦途中', '萧萧山路穷秋雨，淅淅溪风一岸蒲。\\n为问寒沙新到雁，来时还下杜陵无。', '2018-03-09 14:59:21');
INSERT INTO `t_poem` VALUES ('621', 'poem', '唐', '王维', '晓行巴峡', '际晓投巴峡，馀春忆帝京。\\n晴江一女浣，朝日众鸡鸣。\\n水国舟中市，山桥树杪行。\\n登高万井出，眺迥二流明。\\n人作殊方语，莺为故国声。\\n赖多山水趣，稍解别离情。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('622', 'poem', '唐', '朱庆馀', '宫词 / 宫中词', '寂寂花时闭院门，美人相并立琼轩。\\n含情欲说宫中事，鹦鹉前头不敢言。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('623', 'poem', '唐', '周昙', '三代门·太公', '昌猎关西纣猎东，纣怜崇虎弃非熊。\\n危邦自谓多麟凤，肯把王纲取钓翁。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('624', 'poem', '唐', '韦应物', '夕次盱眙县', '落帆逗淮镇，停舫临孤驿。\\n浩浩风起波，冥冥日沉夕。\\n人归山郭暗，雁下芦洲白。\\n独夜忆秦关，听钟未眠客。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('625', 'poem', '唐', '杜甫', '咏怀古迹五首·其二', '摇落深知宋玉悲，风流儒雅亦吾师。\\n怅望千秋一洒泪，萧条异代不同时。\\n江山故宅空文藻，云雨荒台岂梦思。\\n最是楚宫俱泯灭，舟人指点到今疑。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('626', 'poem', '唐', '张籍', '和韦开州盛山十二首·梅溪', '自爱新梅好，行寻一径斜。\\n不教人扫石，恐损落来花。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('627', 'poem', '唐', '韦应物', '东郊', '吏舍跼终年，出郊旷清曙。\\n杨柳散和风，青山澹吾虑。\\n依丛适自憩，缘涧还复去。\\n微雨霭芳原，春鸠鸣何处。\\n乐幽心屡止，遵事迹犹遽。\\n终罢斯结庐，慕陶真可庶。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('628', 'poem', '唐', '李白', '折杨柳·垂杨拂绿水', '垂杨拂绿水，摇艳东风年。\\n花明玉关雪，叶暖金窗烟。\\n美人结长想，对此心凄然。\\n攀条折春色，远寄龙庭前。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('629', 'poem', '唐', '周昙', '春秋战国门·赵简子', '简子雄心蓄霸机，贤愚聊欲试诸儿。\\n假言藏宝非真宝，不是生知焉得知。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('630', 'poem', '唐', '杜牧', '将赴吴兴登乐游原一绝', '清时有味是无能，闲爱孤云静爱僧。\\n欲把一麾江海去，乐游原上望昭陵。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('631', 'poem', '唐', '常建', '塞下曲四首·其一', '玉帛朝回望帝乡，乌孙归去不称王。\\n天涯静处无征战，兵气销为日月光。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('632', 'poem', '唐', '韦庄', '送日本国僧敬龙归', '扶桑已在渺茫中，家在扶桑东更东。\\n此去与师谁共到，一船明月一帆风。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('633', 'poem', '唐', '杜牧', '齐安郡后池绝句', '菱透浮萍绿锦池，夏莺千啭弄蔷薇。\\n尽日无人看微雨，鸳鸯相对浴红衣。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('634', 'poem', '唐', '皮日休', '奉和鲁望四明山九题·青棂子', '山风熟异果，应是供真仙。\\n味似云腴美，形如玉脑圆。\\n衔来多野鹤，落处半灵泉。\\n必共玄都奈，花开不记年。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('635', 'poem', '唐', '温庭筠', '瑶瑟怨', '冰簟银床梦不成，碧天如水夜云轻。\\n雁声远过潇湘去，十二楼中月自明。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('636', 'poem', '唐', '杜甫', '忆昔二首', '忆昔先皇巡朔方，千乘万骑入咸阳。阴山骄子汗血马，\\n长驱东胡胡走藏。邺城反覆不足怪，关中小儿坏纪纲，\\n张后不乐上为忙。至今今上犹拨乱，劳身焦思补四方。\\n我昔近侍叨奉引，出兵整肃不可当。为留猛士守未央，\\n致使岐雍防西羌。犬戎直来坐御林，百官跣足随天王。\\n愿见北地傅介子，老儒不用尚书郎。\\n忆昔开元全盛日，小邑犹藏万家室。稻米流脂粟米白，\\n公私仓廪俱丰实。九州道路无豺虎，远行不劳吉日出。\\n齐纨鲁缟车班班，男耕女桑不相失。宫中圣人奏云门，\\n天下朋友皆胶漆。百馀年间未灾变，叔孙礼乐萧何律。\\n岂闻一绢直万钱，有田种谷今流血。洛阳宫殿烧焚尽，\\n宗庙新除狐兔穴。伤心不忍问耆旧，复恐初从乱离说。\\n小臣鲁钝无所能，朝廷记识蒙禄秩。周宣中兴望我皇，\\n洒血江汉身衰疾。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('637', 'poem', '唐', '宋之问', '题大庾岭北驿', '阳月南飞雁，传闻至此回。\\n我行殊未已，何日复归来。\\n江静潮初落，林昏瘴不开。\\n明朝望乡处，应见陇头梅。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('638', 'poem', '唐', '崔颢', '长干行·君家何处住', '君家何处住，妾住在横塘。\\n停船暂借问，或恐是同乡。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('639', 'poem', '唐', '李白', '苏台览古', '旧苑荒台杨柳新，菱歌清唱不胜春。\\n只今惟有西江月，曾照吴王宫里人。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('640', 'poem', '唐', '白居易', '人定', '人定月胧明，香消枕簟清。\\n翠屏遮烛影，红袖下帘声。\\n坐久吟方罢，眠初梦未成。\\n谁家教鹦鹉，故故语相惊。', '2018-03-09 14:59:30');
INSERT INTO `t_poem` VALUES ('641', 'poem', '唐', '张说', '蜀道后期', '客心争日月，来往预期程。\\n秋风不相待，先至洛阳城。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('642', 'poem', '唐', '王维', '送綦毋潜落第还乡 / 送别', '圣代无隐者，英灵尽来归。\\n遂令东山客，不得顾采薇。\\n既至君门远，孰云吾道非。\\n江淮度寒食，京洛缝春衣。\\n置酒临长道，同心与我违。\\n行当浮桂棹，未几拂荆扉。\\n远树带行客，孤村当落晖。\\n吾谋适不用，勿谓知音稀。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('643', 'poem', '唐', '白居易', '秦中吟十首。轻肥（一作江南旱）', '意气骄满路，鞍马光照尘。\\n借问何为者，人称是内臣。\\n朱绂皆大夫，紫绶或将军。\\n夸赴军中宴，走马去如云。\\n尊罍溢九酝，水陆罗八珍。\\n果擘洞庭橘，脍切天池鳞。\\n食饱心自若，酒酣气益振。\\n是岁江南旱，衢州人食人。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('644', 'poem', '唐', '张仲素', '秋夜曲', '丁丁漏水夜何长，漫漫轻云露月光。\\n秋壁暗虫通夕响，寒衣未寄莫飞霜。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('645', 'poem', '唐', '齐己', '早梅', '万木冻欲折，孤根暖独回。\\n前村深雪里，昨夜一枝开。\\n风递幽香去，禽窥素艳来。\\n明年如应律，先发映春台。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('646', 'poem', '唐', '李白', '寻雍尊师隐居', '群峭碧摩天，逍遥不记年。\\n拨云寻古道，倚石听流泉。\\n花暖青牛卧，松高白鹤眠。\\n语来江色暮，独自下寒烟。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('647', 'poem', '唐', '沈佺期', '郊庙歌辞·享龙池乐章·第三章', '龙池跃龙龙已飞，龙德光天天不违。\\n池开天汉分黄道，龙向天门入紫微。\\n邸第楼台多气色，君王凫雁有光辉。\\n为报寰中百川水，来朝此地莫东归。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('648', 'poem', '唐', '祖咏', '望蓟门', '燕台一望客心惊，箫鼓喧喧汉将营。\\n万里寒光生积雪，三边曙色动危旌。\\n沙场烽火连胡月，海畔云山拥蓟城。\\n少小虽非投笔吏，论功还欲请长缨。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('649', 'poem', '唐', '白居易', '阴雨', '岚雾今朝重，江山此地深。\\n滩声秋更急，峡气晓多阴。\\n望阙云遮眼，思乡雨滴心。\\n将何慰幽独，赖此北窗琴。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('650', 'poem', '唐', '令狐楚', '相思河', '谁把相思号此河，塞垣车马往来多。\\n只应自古征人泪，洒向空洲作碧波。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('651', 'poem', '唐', '司空曙', '云阳馆与韩绅宿别', '故人江海别，几度隔山川。\\n乍见翻疑梦，相悲各问年。\\n孤灯寒照雨，湿竹暗浮烟。\\n更有明朝恨，离杯惜共传。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('652', 'poem', '唐', '卢纶', '晚次鄂州', '云开远见汉阳城，犹是孤帆一日程。\\n估客昼眠知浪静，舟人夜语觉潮生。\\n三湘衰鬓逢秋色，万里归心对月明。\\n旧业已随征战尽，更堪江上鼓鼙声。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('653', 'poem', '唐', '胡曾', '咏史诗·汴水', '千里长河一旦开，亡隋波浪九天来。\\n锦帆未落干戈起，惆怅龙舟更不回。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('654', 'poem', '唐', '崔道融', '梅花', '数萼初含雪，孤标画本难。\\n香中别有韵，清极不知寒。\\n横笛和愁听，斜枝倚病看。\\n朔风如解意，容易莫摧残。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('655', 'poem', '唐', '李白', '妾薄命', '汉帝重阿娇，贮之黄金屋。\\n咳唾落九天，随风生珠玉。\\n宠极爱还歇，妒深情却疏。\\n长门一步地，不肯暂回车。\\n雨落不上天，水覆难再收。\\n君情与妾意，各自东西流。\\n昔日芙蓉花，今成断根草。\\n以色事他人，能得几时好。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('656', 'poem', '唐', '刘禹锡', '杨柳枝/柳枝词', '清江一曲柳千条，二十年前旧板桥。\\n曾与美人桥上别，恨无消息到今朝。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('657', 'poem', '唐', '李白', '感遇其二', '可叹东篱菊。\\n茎疏叶且微。\\n虽言异兰蕙。\\n亦自有芳菲。\\n未泛盈樽酒。\\n徒沾清露辉。\\n当荣君不采。\\n飘落欲何依。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('658', 'poem', '唐', '唐彦谦', '穆天子传', '王母清歌玉琯悲，瑶台应有再来期。\\n穆王不得重相见，恐为无端哭盛姬。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('659', 'poem', '唐', '崔道融', '牧竖', '牧竖持蓑笠，逢人气傲然。\\n卧牛吹短笛，耕却傍溪田。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('660', 'poem', '唐', '翁宏', '春残', '又是春残也，如何出翠帏。\\n落花人独立，微雨燕双飞。\\n寓目魂将断，经年梦亦非。\\n那堪向愁夕，萧飒暮蝉辉。', '2018-03-09 14:59:40');
INSERT INTO `t_poem` VALUES ('661', 'poem', '唐', '李群玉', '石头城', '伯业随流水，寒芜上古城。\\n长空横海色，断岸落潮声。\\n八极悲扶拄，五湖来止倾。\\n东南天子气，扫地入函京。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('662', 'poem', '唐', '杜甫', '促织', '促织甚微细，哀音何动人。\\n草根吟不稳，床下夜相亲。\\n久客得无泪，放妻难及晨。\\n悲丝与急管，感激异天真。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('663', 'poem', '唐', '李商隐', '隋宫 / 隋堤', '乘兴南游不戒严，九重谁省谏书函。\\n春风举国裁宫锦，半作障泥半作帆。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('664', 'poem', '唐', '杜甫', '寄李十二白二十韵', '昔年有狂客，号尔谪仙人。\\n笔落惊风雨，诗成泣鬼神。\\n声名从此大，汩没一朝伸。\\n文彩承殊渥，流传必绝伦。\\n龙舟移棹晚，兽锦夺袍新。\\n白日来深殿，青云满后尘。\\n乞归优诏许，遇我宿心亲。\\n未负幽栖志，兼全宠辱身。\\n剧谈怜野逸，嗜酒见天真。\\n醉舞梁园夜，行歌泗水春。\\n才高心不展，道屈善无邻。\\n处士祢衡俊，诸生原宪贫。\\n稻粱求未足，薏苡谤何频。\\n五岭炎蒸地，三危放逐臣。\\n几年遭鵩鸟，独泣向麒麟。\\n苏武先还汉，黄公岂事秦。\\n楚筵辞醴日，梁狱上书辰。\\n已用当时法，谁将此义陈。\\n老吟秋月下，病起暮江滨。\\n莫怪恩波隔，乘槎与问津。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('665', 'poem', '唐', '李白', '金陵城西楼月下吟', '金陵夜寂凉风发，独上高楼望吴越。\\n白云映水摇空城，白露垂珠滴秋月。\\n月下沉吟久不归，古来相接眼中稀。\\n解道澄江净如练，令人长忆谢玄晖。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('666', 'poem', '唐', '高适', '重阳', '节物惊心两鬓华，东篱空绕未开花。\\n百年将半仕三已，五亩就荒天一涯。\\n岂有白衣来剥啄，一从乌帽自欹斜。\\n真成独坐空搔首，门柳萧萧噪暮鸦。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('667', 'poem', '唐', '王昌龄', '送郭司仓', '映门淮水绿，留骑主人心。\\n明月随良掾，春潮夜夜深。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('668', 'poem', '唐', '聂夷中', '公子家 / 公子行 / 长安花', '种花满西园，花发青楼道。\\n花下一禾生，去之为恶草。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('669', 'poem', '唐', '李世民', '初夏', '一朝春夏改，隔夜鸟花迁。\\n阴阳深浅叶，晓夕重轻烟。\\n哢莺犹响殿，横丝正网天。\\n珮高兰影接，绶细草纹连。\\n碧鳞惊棹侧，玄燕舞檐前。\\n何必汾阳处，始复有山泉。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('670', 'poem', '唐', '李世民', '守岁', '暮景斜芳殿，年华丽绮宫。\\n寒辞去冬雪，暖带入春风。\\n阶馥舒梅素，盘花卷烛红。\\n共欢新故岁，迎送一宵中。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('671', 'poem', '唐', '杜甫', '公安县怀古', '野旷吕蒙营，江深刘备城。\\n寒天催日短，风浪与云平。\\n洒落君臣契，飞腾战伐名。\\n维舟倚前浦，长啸一含情。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('672', 'poem', '唐', '王建', '短歌行', '人初生，日初出。上山迟，下山疾。百年三万六千朝，\\n夜里分将强半日。有歌有舞须早为，昨日健于今日时。\\n人家见生男女好，不知男女催人老。短歌行，无乐声。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('673', 'poem', '唐', '孟云卿', '寒食', '二月江南花满枝，他乡寒食远堪悲。\\n贫居往往无烟火，不独明朝为子推。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('674', 'poem', '唐', '佚名', '送兄', '别路云初起，离亭叶正稀。\\n所嗟人异雁，不作一行归。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('675', 'poem', '唐', '李白', '塞下曲六首', '五月天山雪，无花只有寒。\\n笛中闻折柳，春色未曾看。\\n晓战随金鼓，宵眠抱玉鞍。\\n愿将腰下剑，直为斩楼兰。\\n天兵下北荒，胡马欲南饮。\\n横戈从百战，直为衔恩甚。\\n握雪海上餐，拂沙陇头寝。\\n何当破月氏，然后方高枕。\\n骏马似风飙，鸣鞭出渭桥。\\n弯弓辞汉月，插羽破天骄。\\n阵解星芒尽，营空海雾消。\\n功成画麟阁，独有霍嫖姚。\\n白马黄金塞，云砂绕梦思。\\n那堪愁苦节，远忆边城儿。\\n萤飞秋窗满，月度霜闺迟。\\n摧残梧桐叶，萧飒沙棠枝。\\n无时独不见，流泪空自知。\\n塞虏乘秋下，天兵出汉家。\\n将军分虎竹，战士卧龙沙。\\n边月随弓影，胡霜拂剑花。\\n玉关殊未入，少妇莫长嗟。\\n烽火动沙漠，连照甘泉云。\\n汉皇按剑起，还召李将军。\\n兵气天上合，鼓声陇底闻。\\n横行负勇气，一战净妖氛。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('676', 'poem', '唐', '白居易', '奉和令公绿野堂种花', '绿野堂开占物华，路人指道令公家。\\n令公桃李满天下，何用堂前更种花。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('677', 'poem', '唐', '李世民', '元日', '高轩暧春色，邃阁媚朝光。\\n彤庭飞彩旆，翠幌曜明珰。\\n恭己临四极，垂衣驭八荒。\\n霜戟列丹陛，丝竹韵长廊。\\n穆矣熏风茂，康哉帝道昌。\\n继文遵后轨，循古鉴前王。\\n草秀故春色，梅艳昔年妆。\\n巨川思欲济，终以寄舟航。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('678', 'poem', '唐', '丘为', '寻西山隐者不遇 / 山行寻隐者不遇', '绝顶一茅茨，直上三十里。\\n扣关无僮仆，窥室唯案几。\\n若非巾柴车，应是钓秋水。\\n差池不相见，黾勉空仰止。\\n草色新雨中，松声晚窗里。\\n及兹契幽绝，自足荡心耳。\\n虽无宾主意，颇得清净理。\\n兴尽方下山，何必待之子。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('679', 'poem', '唐', '杜甫', '岁暮', '岁暮远为客，边隅还用兵。\\n烟尘犯雪岭，鼓角动江城。\\n天地日流血，朝廷谁请缨。\\n济时敢爱死，寂寞壮心惊。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('680', 'poem', '唐', '吴融', '杨花', '不斗秾华不占红，自飞晴野雪濛濛。\\n百花长恨风吹落，唯有杨花独爱风。', '2018-03-09 14:59:45');
INSERT INTO `t_poem` VALUES ('681', 'poem', '唐', '杜甫', '无家别', '寂寞天宝后，园庐但蒿藜。我里百馀家，世乱各东西。\\n存者无消息，死者为尘泥。贱子因阵败，归来寻旧蹊。\\n人行见空巷，日瘦气惨凄。但对狐与狸，竖毛怒我啼。\\n四邻何所有，一二老寡妻。宿鸟恋本枝，安辞且穷栖。\\n方春独荷锄，日暮还灌畦。县吏知我至，召令习鼓鞞，\\n虽从本州役，内顾无所携。近行止一身，远去终转迷。\\n家乡既荡尽，远近理亦齐。永痛长病母，五年委沟溪，\\n生我不得力，终身两酸嘶。人生无家别，何以为烝黎。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('682', 'poem', '唐', '王维', '陇西行', '十里一走马，五里一扬鞭。\\n都护军书至，匈奴围酒泉。\\n关山正飞雪，烽戍断无烟。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('683', 'poem', '唐', '卢纶', '和张仆射塞下曲', '鹫翎金仆姑，燕尾绣蝥弧。\\n独立扬新令，千营共一呼。\\n林暗草惊风，将军夜引弓。\\n平明寻白羽，没在石棱中。\\n月黑雁飞高，单于夜遁逃。\\n欲将轻骑逐，大雪满弓刀。\\n野幕敞琼筵，羌戎贺劳旋。\\n醉和金甲舞，雷鼓动山川。\\n调箭又呼鹰，俱闻出世能。\\n奔狐将迸雉，扫尽古丘陵。\\n亭亭七叶贵，荡荡一隅清。\\n他日题麟阁，唯应独不名。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('684', 'poem', '唐', '畅当', '登鹳雀楼', '迥临飞鸟上，高出世尘间。\\n天势围平野，河流入断山。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('685', 'poem', '唐', '王昌龄', '春宫曲', '昨夜风开露井桃，未央前殿月轮高。\\n平阳歌舞新承宠，帘外春寒赐锦袍。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('686', 'poem', '唐', '韩偓', '夏日', '庭树新阴叶未成，玉阶人静一蝉声。\\n相风不动乌龙睡，时有娇莺自唤名。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('687', 'poem', '唐', '李绅', '悯农', '春种一粒粟，秋收万颗子。\\n四海无闲田，农夫犹饿死。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('688', 'poem', '唐', '白居易', '夜筝', '紫袖红弦明月中，自弹自感暗低容。\\n弦凝指咽声停处，别有深情一万重。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('689', 'poem', '唐', '赵嘏', '宿楚国寺有怀', '风动衰荷寂寞香，断烟残月共苍苍。\\n寒生晚寺波摇壁，红堕疏林叶满床。\\n起雁似惊南浦棹，阴云欲护北楼霜。\\n江边松菊荒应尽，八月长安夜正长。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('690', 'poem', '唐', '吴融', '子规', '举国繁华委逝川，羽毛飘荡一年年。\\n他山叫处花成血，旧苑春来草似烟。\\n雨暗不离浓绿树，月斜长吊欲明天。\\n湘江日暮声凄切，愁杀行人归去船。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('691', 'poem', '唐', '刘长卿', '送李中丞之襄州 / 送李中丞归汉阳别业', '流落征南将，曾驱十万师。\\n罢归无旧业，老去恋明时。\\n独立三边静，轻生一剑知。\\n茫茫江汉上，日暮欲何之。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('692', 'poem', '唐', '王维', '登裴秀才迪小台', '端居不出户，满目望云山。\\n落日鸟边下，秋原人外闲。\\n遥知远林际，不见此檐间。\\n好客多乘月，应门莫上关。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('693', 'poem', '唐', '长孙姓', '春游曲', '上苑桃花朝日明，兰闺艳妾动春情。\\n井上新桃偷面色，檐边嫩柳学身轻。\\n花中来去看舞蝶，树上长短听啼莺。\\n林下何须远借问，出众风流旧有名。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('694', 'poem', '唐', '韦应物', '简卢陟', '可怜白雪曲，未遇知音人。\\n恓惶戎旅下，蹉跎淮海滨。\\n涧树含朝雨，山鸟哢馀春。\\n我有一瓢酒，可以慰风尘。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('695', 'poem', '唐', '孟郊', '寒溪', '霜洗水色尽，寒溪见纤鳞。幸临虚空镜，照此残悴身。\\n潜滑不自隐，露底莹更新。豁如君子怀，曾是危陷人。\\n始明浅俗心，夜结朝已津。净漱一掬碧，远消千虑尘。\\n始知泥步泉，莫与山源邻。\\n洛阳岸边道，孟氏庄前溪。舟行素冰折，声作青瑶嘶。\\n绿水结绿玉，白波生白珪。明明宝镜中，物物天照齐。\\n仄步下危曲，攀枯闻孀啼。霜芬稍消歇，凝景微茫齐。\\n痴坐直视听，戆行失踪蹊。岸童劚棘劳，语言多悲凄。\\n晓饮一杯酒，踏雪过清溪。波澜冻为刀，剸割凫与鹥，\\n宿羽皆翦弃，血声沉沙泥。独立欲何语，默念心酸嘶。\\n冻血莫作春，作春生不齐。冻血莫作花，作花发孀啼。\\n幽幽棘针村，冻死难耕犁。\\n篙工磓玉星，一路随迸萤。朔冻哀彻底，獠馋咏潜鯹，\\n冰齿相磨啮，风音酸铎铃。清悲不可逃，洗出纤悉听。\\n碧潋卷已尽，彩缕飞飘零。下蹑滑不定，上栖折难停。\\n哮嘐呷喢冤，仰诉何时宁。\\n一曲一直水，白龙何鳞鳞。冻飙杂碎号，齑音坑谷辛。\\n柧椾吃无力，飞走更相仁。猛弓一折弦，馀喘争来宾。\\n大严此之立，小杀不复陈。皎皎何皎皎，氤氲复氤氲。\\n瑞晴刷日月，高碧开星辰。独立两脚雪，孤吟千虑新。\\n天欃徒昭昭，箕舌虚龂龂。尧圣不听汝，孔微亦有臣。\\n谏书竟成章，古义终难陈。\\n因冻死得食，杀风仍不休。以兵为仁义，仁义生刀头。\\n刀头仁义腥，君子不可求。波澜抽剑冰，相劈如仇雠。\\n尖雪入鱼心，鱼心明愀愀。怳如罔两说，似诉割切由。\\n谁使异方气，入此中土流。翦尽一月春，闭为百谷幽。\\n仰怀新霁光，下照疑忧愁。\\n溪老哭甚寒，涕泗冰珊珊。飞死走死形，雪裂纷心肝。\\n剑刃冻不割，弓弦强难弹。常闻君子武，不食天杀残。\\n劚玉掩骼胔，吊琼哀阑干。\\n溪风摆馀冻，溪景衔明春。玉消花滴滴，虬解光鳞鳞。\\n悬步下清曲，消期濯芳津。千里冰裂处，一勺暖亦仁。\\n凝精互相洗，漪涟竞将新。忽如剑疮尽，初起百战身。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('696', 'poem', '唐', '王昌龄', '塞下曲', '饮马渡秋水，水寒风似刀。\\n平沙日未没，黯黯见临洮。\\n昔日长城战，咸言意气高。\\n黄尘足今古，白骨乱蓬蒿。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('697', 'poem', '唐', '李白', '古风·其十九', '西上莲花山，迢迢见明星。\\n素手把芙蓉，虚步蹑太清。\\n霓裳曳广带，飘拂升天行。\\n邀我登云台，高揖卫叔卿。\\n恍恍与之去，驾鸿凌紫冥。\\n俯视洛阳川，茫茫走胡兵。\\n流血涂野草，豺狼尽冠缨。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('698', 'poem', '唐', '韦应物', '初发扬子寄元大校书', '凄凄去亲爱，泛泛入烟雾。\\n归棹洛阳人，残钟广陵树。\\n今朝此为别，何处还相遇。\\n世事波上舟，沿洄安得住。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('699', 'poem', '唐', '严维', '丹阳送韦参军', '丹阳郭里送行舟，一别心知两地秋。\\n日晚江南望江北，寒鸦飞尽水悠悠。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('700', 'poem', '唐', '张九龄', '西江夜行', '遥夜人何在，澄潭月里行。\\n悠悠天宇旷，切切故乡情。\\n外物寂无扰，中流澹自清。\\n念归林叶换，愁坐露华生。\\n犹有汀洲鹤，宵分乍一鸣。', '2018-03-09 14:59:55');
INSERT INTO `t_poem` VALUES ('701', 'poem', '唐', '李商隐', '咏史二首·其二', '历览前贤国与家，成由勤俭破由奢。\\n何须琥珀方为枕，岂得真珠始是车。\\n运去不逢青海马，力穷难拔蜀山蛇。\\n几人曾预南薰曲，终古苍梧哭翠华。', '2018-03-09 15:00:07');
INSERT INTO `t_poem` VALUES ('702', 'poem', '唐', '李白', '广陵赠别', '玉瓶沽美酒，数里送君还。\\n系马垂杨下，衔杯大道间。\\n天边看渌水，海上见青山。\\n兴罢各分袂，何须醉别颜。', '2018-03-09 15:00:07');
INSERT INTO `t_poem` VALUES ('703', 'poem', '唐', '施肩吾', '夏雨后题青荷兰若', '僧舍清凉竹树新，初经一雨洗诸尘。\\n微风忽起吹莲叶，青玉盘中泻水银。', '2018-03-09 15:00:07');
INSERT INTO `t_poem` VALUES ('704', 'poem', '唐', '汪遵', '密县', '百里能将济猛宽，飞蝗不到邑人安。\\n至今闾里逢灾沴，犹祝当时卓长官。', '2018-03-09 15:00:07');
INSERT INTO `t_poem` VALUES ('705', 'poem', '唐', '韩偓', '残春旅舍', '旅舍残春宿雨晴，恍然心地忆咸京。\\n树头蜂抱花须落，池面鱼吹柳絮行。\\n禅伏诗魔归净域，酒冲愁阵出奇兵。\\n两梁免被尘埃污，拂拭朝簪待眼明。', '2018-03-09 15:00:07');
INSERT INTO `t_poem` VALUES ('706', 'poem', '唐', '杜牧', '初冬夜饮', '淮阳多病偶求欢，客袖侵霜与烛盘。\\n砌下梨花一堆雪，明年谁此凭阑干。', '2018-03-09 15:00:07');
INSERT INTO `t_poem` VALUES ('707', 'poem', '唐', '高骈', '对雪', '六出飞花入户时，坐看青竹变琼枝。\\n如今好上高楼望，盖尽人间恶路岐。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('708', 'poem', '唐', '杜甫', '暮春', '卧病拥塞在峡中，潇湘洞庭虚映空。\\n楚天不断四时雨，巫峡常吹千里风。\\n沙上草阁柳新暗，城边野池莲欲红。\\n暮春鸳鹭立洲渚，挟子翻飞还一丛。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('709', 'poem', '唐', '王维', '田园乐七首·其四', '萋萋春草秋绿，落落长松夏寒。\\n牛羊自归村巷，童稚不识衣冠。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('710', 'poem', '唐', '杜甫', '晨雨', '小雨晨光内，初来叶上闻。\\n雾交才洒地，风逆旋随云。\\n暂起柴荆色，轻沾鸟兽群。\\n麝香山一半，亭午未全分。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('711', 'poem', '唐', '李建勋', '春水', '万派争流雨过时，晚来春静更逶迤。\\n轻鸥散绕夫差国，远树微分夏禹祠。\\n青岸渐平濡柳带，旧溪应暖负莼丝。\\n风鬟倚楫谁家子，愁看鸳鸯望所之。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('712', 'poem', '唐', '王昌龄', '从军行二首·其一', '大将军出战，白日暗榆关。\\n三面黄金甲，单于破胆还。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('713', 'poem', '唐', '杜牧', '旅宿', '旅馆无良伴，凝情自悄然。\\n寒灯思旧事，断雁警愁眠。\\n远梦归侵晓，家书到隔年。\\n沧江好烟月，门系钓鱼船。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('714', 'poem', '唐', '孟浩然', '留别王侍御维 / 留别王维', '寂寂竟何待，朝朝空自归。\\n欲寻芳草去，惜与故人违。\\n当路谁相假，知音世所稀。\\n只应守寂寞，还掩故园扉。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('715', 'poem', '唐', '白居易', '句', '樱桃樊素口，杨柳小蛮腰。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('716', 'poem', '唐', '卢怀慎', '郊庙歌辞·享龙池乐章·第四章', '代邸东南龙跃泉，清漪碧浪远浮天。\\n楼台影就波中出，日月光疑镜里悬。\\n雁沼回流成舜海，龟书荐祉应尧年。\\n大川既济惭为楫，报德空思奉细涓。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('717', 'poem', '唐', '韩愈', '调张籍', '李杜文章在，光焰万丈长。\\n不知群儿愚，那用故谤伤。\\n蚍蜉撼大树，可笑不自量。\\n伊我生其后，举颈遥相望。\\n夜梦多见之，昼思反微茫。\\n徒观斧凿痕，不瞩治水航。\\n想当施手时，巨刃磨天扬。\\n垠崖划崩豁，乾坤摆雷硠。\\n惟此两夫子，家居率荒凉。\\n帝欲长吟哦，故遣起且僵。\\n剪翎送笼中，使看百鸟翔。\\n平生千万篇，金薤垂琳琅。\\n仙官敕六丁，雷电下取将。\\n流落人间者，太山一毫芒。\\n我愿生两翅，捕逐出八荒。\\n精诚忽交通，百怪入我肠。\\n刺手拔鲸牙，举瓢酌天浆。\\n腾身跨汗漫，不著织女襄。\\n顾语地上友，经营无太忙。\\n乞君飞霞佩，与我高颉颃。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('718', 'poem', '唐', '杜甫', '奉陪郑驸马韦曲二首', '韦曲花无赖，家家恼杀人。\\n绿尊虽尽日，白发好禁春。\\n石角钩衣破，藤枝刺眼新。\\n何时占丛竹，头戴小乌巾。\\n野寺垂杨里，春畦乱水间。\\n美花多映竹，好鸟不归山。\\n城郭终何事，风尘岂驻颜。\\n谁能共公子，薄暮欲俱还。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('719', 'poem', '唐', '李白', '送友人入蜀', '见说蚕丛路，崎岖不易行。\\n山从人面起，云傍马头生。\\n芳树笼秦栈，春流绕蜀城。\\n升沉应已定，不必问君平。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('720', 'poem', '唐', '刘禹锡', '始闻秋风', '昔看黄菊与君别，今听玄蝉我却回。\\n五夜飕飗枕前觉，一年颜状镜中来。\\n马思边草拳毛动，雕眄青云睡眼开。\\n天地肃清堪四望，为君扶病上高台。', '2018-03-09 15:00:08');
INSERT INTO `t_poem` VALUES ('721', 'poem', '唐', '唐彦谦', '春草', '天北天南绕路边，托根无处不延绵。\\n萋萋总是无情物，吹绿东风又一年。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('722', 'poem', '唐', '王昌龄', '青楼怨', '香帏风动花入楼，高调鸣筝缓夜愁。\\n肠断关山不解说，依依残月下帘钩。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('723', 'poem', '唐', '李商隐', '鸳鸯', '雌去雄飞万里天，云罗满眼泪潸然。\\n不须长结风波愿，锁向金笼始两全。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('724', 'poem', '唐', '李白', '永王东巡歌十一首', '永王正月东出师，天子遥分龙虎旗。\\n楼船一举风波静，江汉翻为雁鹜池。\\n三川北虏乱如麻，四海南奔似永嘉。\\n但用东山谢安石，为君谈笑静胡沙。\\n雷鼓嘈嘈喧武昌，云旗猎猎过寻阳。\\n秋毫不犯三吴悦，春日遥看五色光。\\n龙蟠虎踞帝王州，帝子金陵访古丘。\\n春风试暖昭阳殿，明月还过鳷鹊楼。\\n二帝巡游俱未回，五陵松柏使人哀。\\n诸侯不救河南地，更喜贤王远道来。\\n丹阳北固是吴关，画出楼台云水间。\\n千岩烽火连沧海，两岸旌旗绕碧山。\\n王出三山按五湖，楼船跨海次陪都。\\n战舰森森罗虎士，征帆一一引龙驹。\\n长风挂席势难回，海动山倾古月摧。\\n君看帝子浮江日，何似龙骧出峡来。\\n祖龙浮海不成桥，汉武寻阳空射蛟。\\n我王楼舰轻秦汉，却似文皇欲渡辽。\\n帝宠贤王入楚关，扫清江汉始应还。\\n初从云梦开朱邸，更取金陵作小山。\\n试借君王玉马鞭，指挥戎虏坐琼筵。\\n南风一扫胡尘静，西入长安到日边。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('725', 'poem', '唐', '杜审言', '渡湘江', '迟日园林悲昔游，今春花鸟作边愁。\\n独怜京国人南窜，不似湘江水北流。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('726', 'poem', '唐', '李商隐', '无题四首', '来是空言去绝踪，月斜楼上五更钟。梦为远别啼难唤，\\n书被催成墨未浓。蜡照半笼金翡翠，麝熏微度绣芙蓉。\\n刘郎已恨蓬山远，更隔蓬山一万重。\\n飒飒东风细雨来，芙蓉塘外有轻雷。金蟾啮锁烧香入，\\n玉虎牵丝汲井回。贾氏窥帘韩掾少，宓妃留枕魏王才。\\n春心莫共花争发，一寸相思一寸灰。\\n含情春晼晚，暂见夜阑干。楼响将登怯，帘烘欲过难。\\n多羞钗上燕，真愧镜中鸾。归去横塘晓，华星送宝鞍。\\n何处哀筝随急管，樱花永巷垂杨岸。东家老女嫁不售，\\n白日当天三月半。溧阳公主年十四，清明暖后同墙看。\\n归来展转到五更，梁间燕子闻长叹。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('727', 'poem', '唐', '白居易', '夏日', '东窗晚无热，北户凉有风。\\n尽日坐复卧，不离一室中。\\n中心本无系，亦与出门同。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('728', 'poem', '唐', '鱼玄机', '赠邻女 / 寄李亿员外', '羞日遮罗袖，愁春懒起妆。\\n易求无价宝，难得有心郎。\\n枕上潜垂泪，花间暗断肠。\\n自能窥宋玉，何必恨王昌。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('729', 'poem', '唐', '白居易', '编集拙诗成一十五卷因题卷末戏赠元九、李二十', '一篇长恨有风情，十首秦吟近正声。\\n每被老元偷格律，苦教短李伏歌行。\\n世间富贵应无分，身后文章合有名。\\n莫怪气粗言语大，新排十五卷诗成。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('730', 'poem', '唐', '白居易', '夜归', '半醉闲行湖岸东，马鞭敲镫辔珑璁。\\n万株松树青山上，十里沙堤明月中。\\n楼角渐移当路影，潮头欲过满江风。\\n归来未放笙歌散，画戟门开蜡烛红。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('731', 'poem', '唐', '高蟾', '下第后上永崇高侍郎', '天上碧桃和露种，日边红杏倚云栽。\\n芙蓉生在秋江上，不向东风怨未开。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('732', 'poem', '唐', '李商隐', '北齐二首', '一笑相倾国便亡，何劳荆棘始堪伤。\\n小怜玉体横陈夜，已报周师入晋阳。\\n巧笑知堪敌万几，倾城最在著戎衣。\\n晋阳已陷休回顾，更请君王猎一围。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('733', 'poem', '唐', '孟郊', '列女操 / 烈女操', '梧桐相待老，鸳鸯会双死。\\n贞女贵徇夫，舍生亦如此。\\n波澜誓不起，妾心井中水。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('734', 'poem', '唐', '韦应物', '夏至避暑北池', '昼晷已云极，宵漏自此长。\\n未及施政教，所忧变炎凉。\\n公门日多暇，是月农稍忙。\\n高居念田里，苦热安可当。\\n亭午息群物，独游爱方塘。\\n门闭阴寂寂，城高树苍苍。\\n绿筠尚含粉，圆荷始散芳。\\n于焉洒烦抱，可以对华觞。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('735', 'poem', '唐', '崔涂', '春夕', '水流花谢两无情，送尽东风过楚城。\\n胡蝶梦中家万里，子规枝上月三更。\\n故园书动经年绝，华发春唯满镜生。\\n自是不归归便得，五湖烟景有谁争。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('736', 'poem', '唐', '柳宗元', '早梅', '早梅发高树，迥映楚天碧。\\n朔吹飘夜香，繁霜滋晓白。\\n欲为万里赠，杳杳山水隔。\\n寒英坐销落，何用慰远客。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('737', 'poem', '唐', '司空图', '重阳', '菊开犹阻雨，蝶意切于人。\\n亦应知暮节，不比惜残春。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('738', 'poem', '唐', '李白', '送贺宾客归越', '镜湖流水漾清波，狂客归舟逸兴多。\\n山阴道士如相见，应写黄庭换白鹅。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('739', 'poem', '唐', '宋之问', '度大庾岭', '度岭方辞国，停轺一望家。\\n魂随南翥鸟，泪尽北枝花。\\n山雨初含霁，江云欲变霞。\\n但令归有日，不敢恨长沙。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('740', 'poem', '唐', '白居易', '杭州春望', '望海楼明照曙霞，护江堤白踏晴沙。\\n涛声夜入伍员庙，柳色春藏苏小家。\\n红袖织绫夸柿蒂，青旗沽酒趁梨花。\\n谁开湖寺西南路，草绿裙腰一道斜。', '2018-03-09 15:00:17');
INSERT INTO `t_poem` VALUES ('741', 'poem', '唐', '崔涂', '孤雁二首·其二', '湘浦离应晚，边城去已孤。\\n如何万里计，只在一枝芦。\\n迥起波摇楚，寒栖月映蒲。\\n不知天畔侣，何处下平芜。\\n几行归去尽，片影独何之。\\n暮雨相呼失，寒塘欲下迟。\\n渚云低暗度，关月冷遥随。\\n未必逢矰缴，孤飞自可疑。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('742', 'poem', '唐', '曹松', '南海旅次', '忆归休上越王台，归思临高不易裁。\\n为客正当无雁处，故园谁道有书来。\\n城头早角吹霜尽，郭里残潮荡月回。\\n心似百花开未得，年年争发被春催。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('743', 'poem', '唐', '罗隐', '筹笔驿', '抛掷南阳为主忧，北征东讨尽良筹。\\n时来天地皆同力，运去英雄不自由。\\n千里山河轻孺子，两朝冠剑恨谯周。\\n唯馀岩下多情水，犹解年年傍驿流。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('744', 'poem', '唐', '骆宾王', '夏日游山家同夏少府', '返照下层岑，物外狎招寻。\\n兰径薰幽珮，槐庭落暗金。\\n谷静风声彻，山空月色深。\\n一遣樊笼累，唯馀松桂心。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('745', 'poem', '唐', '郑谷', '淮上渔者', '白头波上白头翁，家逐船移浦浦风。\\n一尺鲈鱼新钓得，儿孙吹火荻花中。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('746', 'poem', '唐', '白居易', '种荔枝', '红颗珍珠诚可爱，白须太守亦何痴。\\n十年结子知谁在，自向庭中种荔枝。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('747', 'poem', '唐', '李益', '塞下曲', '伏波惟愿裹尸还，定远何须生入关。\\n莫遣只轮归海窟，仍留一箭射天山。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('748', 'poem', '唐', '黄巢', '自题像', '记得当年草上飞，铁衣著尽著僧衣。\\n天津桥上无人识，独倚栏干看落晖。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('749', 'poem', '唐', '杜甫', '贫交行', '翻手作云覆手雨，纷纷轻薄何须数。\\n君不见管鲍贫时交，此道今人弃如土。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('750', 'poem', '唐', '皮日休', '汴河怀古二首', '万艘龙舸绿丝间，载到扬州尽不还。\\n应是天教开汴水，一千余里地无山。\\n尽道隋亡为此河，至今千里赖通波。\\n若无水殿龙舟事，共禹论功不较多。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('751', 'poem', '唐', '刘长卿', '寻洪尊师不遇', '古木无人地，来寻羽客家。\\n道书堆玉案，仙帔叠青霞。\\n鹤老难知岁，梅寒未作花。\\n山中不相见，何处化丹砂。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('752', 'poem', '唐', '白居易', '夜雨', '早蛩啼复歇，残灯灭又明。\\n隔窗知夜雨，芭蕉先有声。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('753', 'poem', '唐', '刘长卿', '秋日登吴公台上寺远眺', '古台摇落后，秋日望乡心。\\n野寺人来少，云峰水隔深。\\n夕阳依旧垒，寒磬满空林。\\n惆怅南朝事，长江独至今。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('754', 'poem', '唐', '李世民', '饮马长城窟行', '塞外悲风切，交河冰已结。\\n瀚海百重波，阴山千里雪。\\n迥戍危烽火，层峦引高节。\\n悠悠卷旆旌，饮马出长城。\\n寒沙连骑迹，朔吹断边声。\\n胡尘清玉塞，羌笛韵金钲。\\n绝漠干戈戢，车徒振原隰。\\n都尉反龙堆，将军旋马邑。\\n扬麾氛雾静，纪石功名立。\\n荒裔一戎衣，灵台凯歌入。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('755', 'poem', '唐', '郑概', '状江南·孟秋', '江南孟秋天，稻花白如毡。\\n素腕惭新藕，残妆妒晚莲。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('756', 'poem', '唐', '王维', '桃源行', '渔舟逐水爱山春，两岸桃花夹古津。\\n坐看红树不知远，行尽青溪不见人。\\n山口潜行始隈隩，山开旷望旋平陆。\\n遥看一处攒云树，近入千家散花竹。\\n樵客初传汉姓名，居人未改秦衣服。\\n居人共住武陵源，还从物外起田园。\\n月明松下房栊静，日出云中鸡犬喧。\\n惊闻俗客争来集，竞引还家问都邑。\\n平明闾巷扫花开，薄暮渔樵乘水入。\\n初因避地去人间，及至成仙遂不还。\\n峡里谁知有人事，世中遥望空云山。\\n不疑灵境难闻见，尘心未尽思乡县。\\n出洞无论隔山水，辞家终拟长游衍。\\n自谓经过旧不迷，安知峰壑今来变。\\n当时只记入山深，青溪几度到云林。\\n春来遍是桃花水，不辨仙源何处寻。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('757', 'poem', '唐', '白居易', '江楼夕望招客', '海天东望夕茫茫，山势川形阔复长。\\n灯火万家城四畔，星河一道水中央。\\n风吹古木晴天雨，月照平沙夏夜霜。\\n能就江楼消暑否？比君茅舍较清凉。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('758', 'poem', '唐', '李贺', '南园十三首', '花枝草蔓眼中开，小白长红越女腮。\\n可怜日暮嫣香落，嫁与春风不用媒。\\n宫北田塍晓气酣，黄桑饮露窣宫帘。\\n长腰健妇偷攀折，将喂吴王八茧蚕。\\n竹里缲丝挑网车，青蝉独噪日光斜。\\n桃胶迎夏香琥珀，自课越佣能种瓜。\\n三十未有二十馀，白日长饥小甲蔬。\\n桥头长老相哀念，因遗戎韬一卷书。\\n男儿何不带吴钩，收取关山五十州。\\n请君暂上凌烟阁，若个书生万户侯。\\n寻章摘句老雕虫，晓月当帘挂玉弓。\\n不见年年辽海上，文章何处哭秋风。\\n长卿牢落悲空舍，曼倩诙谐取自容。\\n见买若耶溪水剑，明朝归去事猿公。\\n春水初生乳燕飞，黄蜂小尾扑花归。\\n窗含远色通书幌，鱼拥香钩近石矶。\\n泉沙软卧鸳鸯暖，曲岸回篙舴艋迟。\\n泻酒木栏椒叶盖，病容扶起种菱丝。\\n边让今朝忆蔡邕，无心裁曲卧春风。\\n舍南有竹堪书字，老去溪头作钓翁。\\n长峦谷口倚嵇家，白昼千峰老翠华。\\n自履藤鞋收石蜜，手牵苔絮长莼花。\\n松溪黑水新龙卵，桂洞生硝旧马牙。\\n谁遣虞卿裁道帔，轻绡一匹染朝霞。\\n小树开朝径，长茸湿夜烟。柳花惊雪浦，麦雨涨溪田。\\n古刹疏钟度，遥岚破月悬。沙头敲石火，烧竹照渔船。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('759', 'poem', '唐', '马戴', '楚江怀古三首·其一', '露气寒光集，微阳下楚丘。\\n猿啼洞庭树，人在木兰舟。\\n广泽生明月，苍山夹乱流。\\n云中君不见，竟夕自悲秋。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('760', 'poem', '唐', '李商隐', '花下醉', '寻芳不觉醉流霞，倚树沉眠日已斜。\\n客散酒醒深夜后，更持红烛赏残花。', '2018-03-09 15:00:27');
INSERT INTO `t_poem` VALUES ('761', 'poem', '唐', '施肩吾', '瀑布', '豁开青冥颠，写出万丈泉。\\n如裁一条素，白日悬秋天。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('762', 'poem', '唐', '令狐楚', '少年行四首·其三', '弓背霞明剑照霜，秋风走马出咸阳。\\n未收天子河湟地，不拟回头望故乡。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('763', 'poem', '唐', '杜甫', '宿府', '清秋幕府井梧寒，独宿江城蜡炬残。\\n永夜角声悲自语，中天月色好谁看。\\n风尘荏苒音书绝，关塞萧条行路难。\\n已忍伶俜十年事，强移栖息一枝安。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('764', 'poem', '唐', '王建', '落叶', '陈绿向参差，初红已重叠。\\n中庭初扫地，绕树三两叶。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('765', 'poem', '唐', '徐惠', '长门怨', '旧爱柏梁台，新宠昭阳殿。\\n守分辞芳辇，含情泣团扇。\\n一朝歌舞荣，夙昔诗书贱。\\n颓恩诚已矣，覆水难重荐。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('766', 'poem', '唐', '罗邺', '早发', '一点灯残鲁酒醒，已携孤剑事离程。\\n愁看飞雪闻鸡唱，独向长空背雁行。\\n白草近关微有路，浊河连底冻无声。\\n此中来往本迢递，况是驱羸客塞城。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('767', 'poem', '唐', '白居易', '偶作寄朗之', '历想为官日，无如刺史时。\\n欢娱接宾客，饱暖及妻儿。\\n自到东都后，安闲更得宜。\\n分司胜刺史，致仕胜分司。\\n何况园林下，欣然得朗之。\\n仰名同旧识，为乐即新知。\\n有雪先相访，无花不作期。\\n斗醲干酿酒，夸妙细吟诗。\\n里巷千来往，都门五别离。\\n岐分两回首，书到一开眉。\\n叶落槐亭院，冰生竹阁池。\\n雀罗谁问讯，鹤氅罢追随。\\n身与心俱病，容将力共衰。\\n老来多健忘，唯不忘相思。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('768', 'poem', '唐', '李商隐', '乐游原', '万树鸣蝉隔岸虹，乐游原上有西风。\\n羲和自趁虞泉宿，不放斜阳更向东。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('769', 'poem', '唐', '吴筠', '高士咏·高凤', '吾观时人趣，矫迹务驰声。\\n独有高文通，讼田求翳名。\\n公车徒见累，爵禄非所荣。\\n隐身乐鱼钓，世网不可撄。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('770', 'poem', '唐', '权德舆', '小言', '醯鸡伺晨驾蚊翼，毫端棘刺分畛域。\\n蛛丝结构聊荫息，蚁垤崔嵬不可陟。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('771', 'poem', '唐', '戴叔伦', '湘南即事', '卢橘花开枫叶衰，出门何处望京师。\\n沅湘日夜东流去，不为愁人住少时。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('772', 'poem', '唐', '岑参', '戏问花门酒家翁', '老人七十仍沽酒，千壶百瓮花门口。\\n道傍榆荚仍似钱，摘来沽酒君肯否。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('773', 'poem', '唐', '缪氏子', '赋新月', '初月如弓未上弦，分明挂在碧霄边。\\n时人莫道蛾眉小，三五团圆照满天。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('774', 'poem', '唐', '杜荀鹤', '送友游吴越', '去越从吴过，吴疆与越连。\\n有园多种桔，无水不生莲。\\n夜市桥边火，春风寺外船。\\n此中偏重客，君去必经年。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('775', 'poem', '唐', '白居易', '杨柳枝词', '一树春风千万枝，嫩于金色软于丝。\\n永丰西角荒园里，尽日无人属阿谁。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('776', 'poem', '唐', '王维', '息夫人', '莫以今时宠，难忘旧日恩。\\n看花满眼泪，不共楚王言。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('777', 'poem', '唐', '白居易', '惜牡丹花二首', '惆怅阶前红牡丹，晚来唯有两枝残。\\n明朝风起应吹尽，夜惜衰红把火看。\\n寂寞萎红低向雨，离披破艳散随风。\\n晴明落地犹惆怅，何况飘零泥土中。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('778', 'poem', '唐', '李商隐', '梦泽', '梦泽悲风动白茅，楚王葬尽满城娇。\\n未知歌舞能多少，虚减宫厨为细腰。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('779', 'poem', '唐', '钱起', '省试湘灵鼓瑟', '善鼓云和瑟，常闻帝子灵。\\n冯夷空自舞，楚客不堪听。\\n苦调凄金石，清音入杳冥。\\n苍梧来怨慕，白芷动芳馨。\\n流水传潇浦，悲风过洞庭。\\n曲终人不见，江上数峰青。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('780', 'poem', '唐', '皮日休', '正乐府十篇·橡媪叹', '秋深橡子熟，散落榛芜冈。\\n伛伛黄发媪，拾之践晨霜。\\n移时始盈掬，尽日方满筐。\\n几嚗复几蒸，用作三冬粮。\\n山前有熟稻，紫穗袭人香。\\n细获又精舂，粒粒如玉珰。\\n持之纳于官，私室无仓箱。\\n如何一石馀，只作五斗量。\\n狡吏不畏刑，贪官不避赃。\\n农时作私债，农毕归官仓。\\n自冬及于春，橡实诳饥肠。\\n吾闻田成子，诈仁犹自王。\\n吁嗟逢橡媪，不觉泪沾裳。', '2018-03-09 15:00:33');
INSERT INTO `t_poem` VALUES ('781', 'poem', '唐', '柳宗元', '别舍弟宗一', '零落残红倍黯然，双垂别泪越江边。\\n一身去国六千里，万死投荒十二年。\\n桂岭瘴来云似墨，洞庭春尽水如天。\\n欲知此后相思梦，长在荆门郢树烟。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('782', 'poem', '唐', '齐己', '春雨', '欲布如膏势，先闻动地雷。\\n云龙相得起，风电一时来。\\n霢霂农桑野，冥濛杨柳台。\\n何人待晴暖，庭有牡丹开。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('783', 'poem', '唐', '王维', '辋川别业', '不到东山向一年，归来才及种春田。\\n雨中草色绿堪染，水上桃花红欲然。\\n优娄比丘经论学，伛偻丈人乡里贤。\\n披衣倒屣且相见，相欢语笑衡门前。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('784', 'poem', '唐', '白居易', '春风', '春风先发苑中梅，樱杏桃梨次第开。\\n荠花榆荚深村里，亦道春风为我来。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('785', 'poem', '唐', '卢肇', '竞渡诗 / 及第后江宁观竞渡寄袁州剌史成应元', '石溪久住思端午，馆驿楼前看发机。\\n鼙鼓动时雷隐隐，兽头凌处雪微微。\\n冲波突出人齐譀，跃浪争先鸟退飞。\\n向道是龙刚不信，果然夺得锦标归。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('786', 'poem', '唐', '李商隐', '隋宫', '紫泉宫殿锁烟霞，欲取芜城作帝家。\\n玉玺不缘归日角，锦帆应是到天涯。\\n于今腐草无萤火，终古垂杨有暮鸦。\\n地下若逢陈后主，岂宜重问后庭花。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('787', 'poem', '唐', '杜牧', '叹花 / 怅诗', '自是寻春去校迟，不须惆怅怨芳时。\\n狂风落尽深红色，绿叶成阴子满枝。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('788', 'poem', '唐', '白居易', '山泉煎茶有怀', '坐酌泠泠水，看煎瑟瑟尘。\\n无由持一碗，寄与爱茶人。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('789', 'poem', '唐', '唐彦谦', '春日偶成', '琴筝箫管和琵琶，兴满金尊酒量赊。\\n歌舞留春春似海，美人颜色正如花。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('790', 'poem', '唐', '刘禹锡', '杨柳枝词九首·其一', '塞北梅花羌笛吹，淮南桂树小山词。\\n请君莫奏前朝曲，听唱新翻杨柳枝。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('791', 'poem', '唐', '姜皎', '郊庙歌辞·享龙池乐章·第五章', '龙池初出此龙山，常经此地谒龙颜。\\n日日芙蓉生夏水，年年杨柳变春湾。\\n尧坛宝匣馀烟雾，舜海渔舟尚往还。\\n愿似飘飖五云影，从来从去九天间。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('792', 'poem', '唐', '元稹', '杏花', '常年出入右银台，每怪春光例早回。\\n惭愧杏园行在景，同州园里也先开。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('793', 'poem', '唐', '王维', '白石滩', '清浅白石滩，绿蒲向堪把。\\n家住水东西，浣纱明月下。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('794', 'poem', '唐', '杜甫', '咏怀古迹五首·其一', '支离东北风尘际，漂泊西南天地间。\\n三峡楼台淹日月，五溪衣服共云山。\\n羯胡事主终无赖，词客哀时且未还。\\n庾信平生最萧瑟，暮年诗赋动江关。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('795', 'poem', '唐', '张九龄', '送韦城李少府', '送客南昌尉，离亭西候春。\\n野花看欲尽，林鸟听犹新。\\n别酒青门路，归轩白马津。\\n相知无远近，万里尚为邻。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('796', 'poem', '唐', '李白', '江夏别宋之悌', '楚水清若空，遥将碧海通。\\n人分千里外，兴在一杯中。\\n谷鸟吟晴日，江猿啸晚风。\\n平生不下泪，于此泣无穷。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('797', 'poem', '唐', '杜甫', '奉济驿重送严公四韵', '远送从此别，青山空复情。\\n几时杯重把，昨夜月同行。\\n列郡讴歌惜，三朝出入荣。\\n江村独归处，寂寞养残生。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('798', 'poem', '唐', '杜甫', '野老', '野老篱前江岸回，柴门不正逐江开。\\n渔人网集澄潭下，贾客船随返照来。\\n长路关心悲剑阁，片云何意傍琴台。\\n王师未报收东郡，城阙秋生画角哀。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('799', 'poem', '唐', '王勃', '早春野望', '江旷春潮白，山长晓岫青。\\n他乡临睨极，花柳映边亭。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('800', 'poem', '唐', '李白', '鲁郡东石门送杜二甫', '醉别复几日，登临遍池台。\\n何时石门路，重有金樽开。\\n秋波落泗水，海色明徂徕。\\n飞蓬各自远，且尽手中杯。', '2018-03-09 15:00:37');
INSERT INTO `t_poem` VALUES ('801', 'poem', '唐', '裴迪', '辋川集二十首·华子冈', '日落松风起，还家草露晞。\\n云光侵履迹，山翠拂人衣。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('802', 'poem', '唐', '司空曙', '峡口送友人', '峡口花飞欲尽春，天涯去住泪沾巾。\\n来时万里同为客，今日翻成送故人。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('803', 'poem', '唐', '贯休', '古战处', '鬼气苍黄棘叶红，昔时人血此时风。\\n相怜极目无疆地，曾落将军一阵中。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('804', 'poem', '唐', '刘禹锡', '杨柳枝词九首', '塞北梅花羌笛吹，淮南桂树小山词。\\n请君莫奏前朝曲，听唱新翻杨柳枝。\\n南陌东城春早时，相逢何处不依依。\\n桃红李白皆夸好，须得垂杨相发挥。\\n凤阙轻遮翡翠帏，龙池遥望麹尘丝。\\n御沟春水相辉映，狂杀长安年少儿。\\n金谷园中莺乱飞，铜驼陌上好风吹。\\n城中桃李须臾尽，争似垂杨无限时。\\n花萼楼前初种时，美人楼上斗腰肢。\\n如今抛掷长街里，露叶如啼欲向谁。\\n炀帝行宫汴水滨，数枝杨柳不胜春。\\n晚来风起花如雪，飞入宫墙不见人。\\n御陌青门拂地垂，千条金缕万条丝。\\n如今绾作同心结，将赠行人知不知。\\n城外春风吹酒旗，行人挥袂日西时。\\n长安陌上无穷树，唯有垂杨管别离。\\n轻盈袅娜占年华，舞榭妆楼处处遮。\\n春尽絮花留不得，随风好去落谁家。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('805', 'poem', '唐', '皇甫松', '采莲子·船动湖光滟滟秋', '船动湖光滟滟秋，贪看年少信船流。\\n无端隔水抛莲子，遥被人知半日羞。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('806', 'poem', '唐', '杜甫', '武侯庙', '遗庙丹青落，空山草木长。\\n犹闻辞后主，不复卧南阳。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('807', 'poem', '唐', '施肩吾', '幼女词', '幼女才六岁，未知巧与拙。\\n向夜在堂前，学人拜新月。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('808', 'poem', '唐', '杜甫', '悲秋', '凉风动万里，群盗尚纵横。\\n家远传书日，秋来为客情。\\n愁窥高鸟过，老逐众人行。\\n始欲投三峡，何由见两京。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('809', 'poem', '唐', '李益', '行舟', '柳花飞入正行舟，卧引菱花信碧流。\\n闻道风光满扬子，天晴共上望乡楼。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('810', 'poem', '唐', '杜甫', '归雁', '东来万里客，乱定几年归。\\n肠断江城雁，高高正北飞。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('811', 'poem', '唐', '白居易', '燕诗示刘叟', '梁上有双燕，翩翩雄与雌。\\n衔泥两椽间，一巢生四儿。\\n四儿日夜长，索食声孜孜。\\n青虫不易捕，黄口无饱期。\\n觜爪虽欲敝，心力不知疲。\\n须臾十来往，犹恐巢中饥。\\n辛勤三十日，母瘦雏渐肥。\\n喃喃教言语，一一刷毛衣。\\n一旦羽翼成，引上庭树枝。\\n举翅不回顾，随风四散飞。\\n雌雄空中鸣，声尽呼不归。\\n却入空巢里，啁啾终夜悲。\\n燕燕尔勿悲，尔当返自思。\\n思尔为雏日，高飞背母时。\\n当时父母念，今日尔应知。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('812', 'poem', '唐', '姚合', '独居', '深闭柴门长不出，功夫自课少闲时。\\n翻音免问他人字，覆局何劳对手棋。\\n生计如云无定所，穷愁似影每相随。\\n到头归向青山是，尘路茫茫欲告谁。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('813', 'poem', '唐', '李商隐', '乐游原', '春梦乱不记，春原登已重。\\n青门弄烟柳，紫阁舞云松。\\n拂砚轻冰散，开尊绿酎浓。\\n无悰托诗遣，吟罢更无悰。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('814', 'poem', '唐', '周昙', '春秋战国门·顷襄王', '秦陷荆王死不还，只缘偏听子兰言。\\n顷襄还信子兰语，忍使江鱼葬屈原。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('815', 'poem', '唐', '崔季卿', '晴江秋望', '八月长江万里晴，千帆一道带风轻。\\n尽日不分天水色，洞庭南是岳阳城。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('816', 'poem', '唐', '温庭筠', '利州南渡', '澹然空水对斜晖，曲岛苍茫接翠微。\\n波上马嘶看棹去，柳边人歇待船归。\\n数丛沙草群鸥散，万顷江田一鹭飞。\\n谁解乘舟寻范蠡，五湖烟水独忘机。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('817', 'poem', '唐', '杜甫', '九日蓝田崔氏庄', '老去悲秋强自宽，兴来今日尽君欢。\\n羞将短发还吹帽，笑倩旁人为正冠。\\n蓝水远从千涧落，玉山高并两峰寒。\\n明年此会知谁健？醉把茱萸仔细看。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('818', 'poem', '唐', '陈子昂', '春夜别友人二首·其一', '银烛吐青烟，金樽对绮筵。\\n离堂思琴瑟，别路绕山川。\\n明月隐高树，长河没晓天。\\n悠悠洛阳道，此会在何年。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('819', 'poem', '唐', '杜甫', '清明二首', '朝来新火起新烟，湖色春光净客船。\\n绣羽衔花他自得，红颜骑竹我无缘。\\n胡童结束还难有，楚女腰肢亦可怜。\\n不见定王城旧处，长怀贾傅井依然。\\n虚沾焦举为寒食，实藉严君卖卜钱。\\n钟鼎山林各天性，浊醪粗饭任吾年。\\n此身飘泊苦西东，右臂偏枯半耳聋。\\n寂寂系舟双下泪，悠悠伏枕左书空。\\n十年蹴踘将雏远，万里秋千习俗同。\\n旅雁上云归紫塞，家人钻火用青枫。\\n秦城楼阁烟花里，汉主山河锦锈中。\\n春去春来洞庭阔，白苹愁杀白头翁。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('820', 'poem', '唐', '李白', '子夜吴歌·冬歌', '明朝驿使发，一夜絮征袍。\\n素手抽针冷，那堪把剪刀。\\n裁缝寄远道，几日到临洮。', '2018-03-09 15:00:46');
INSERT INTO `t_poem` VALUES ('821', 'poem', '唐', '白居易', '采地黄者', '麦死春不雨，禾损秋早霜。\\n岁晏无口食，田中采地黄。\\n采之将何用，持以易餱粮。\\n凌晨荷锄去，薄暮不盈筐。\\n携来朱门家，卖与白面郎。\\n与君啖肥马，可使照地光。\\n愿易马残粟，救此苦饥肠。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('822', 'poem', '唐', '薛能', '荔枝诗', '颗如松子色如樱，未识蹉跎欲半生。\\n岁杪监州曾见树，时新入座久闻名。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('823', 'poem', '唐', '杜甫', '丽春', '百草竞春华，丽春应最胜。\\n少须颜色好，多漫枝条剩。\\n纷纷桃李枝，处处总能移。\\n如何此贵重？却怕有人知。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('824', 'poem', '唐', '杜甫', '吹笛', '吹笛秋山风月清，谁家巧作断肠声。\\n风飘律吕相和切，月傍关山几处明。\\n胡骑中宵堪北走，武陵一曲想南征。\\n故园杨柳今摇落，何得愁中曲尽生。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('825', 'poem', '唐', '权德舆', '玉台体', '昨夜裙带解，今朝蟢子飞。\\n铅华不可弃，莫是藁砧归。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('826', 'poem', '唐', '李商隐', '春游', '桥峻斑骓疾，川长白鸟高。\\n烟轻惟润柳，风滥欲吹桃。\\n徙倚三层阁，摩挲七宝刀。\\n庾郎年最少，青草妒春袍。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('827', 'poem', '唐', '李乂', '郊庙歌辞·享龙池乐章·第八章', '星分邑里四人居，水洊源流万顷馀。\\n魏国君王称象处，晋家藩邸化龙初。\\n青蒲暂似游梁马，绿藻还疑宴镐鱼。\\n自有神灵滋液地，年年云物史官书。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('828', 'poem', '唐', '无名氏', '骊龙', '有美为鳞族，潜蟠得所从。\\n标奇初韫宝，表智即称龙。\\n大壑长千里，深泉固九重。\\n奋髯云乍起，矫首浪还冲。\\n荀氏传高誉，庄生冀绝踪。\\n仍知流泪在，何幸此相逢。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('829', 'poem', '唐', '杜甫', '泊岳阳城下 / 泊岳阳楼下', '江国逾千里，山城仅百层。\\n岸风翻夕浪，舟雪洒寒灯。\\n留滞才难尽，艰危气益增。\\n图南未可料，变化有鲲鹏。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('830', 'poem', '唐', '白居易', '与梦得沽酒闲饮且约后期', '少时犹不忧生计，老后谁能惜酒钱。\\n共把十千沽一斗，相看七十欠三年。\\n闲征雅令穷经史，醉听清吟胜管弦。\\n更待菊黄家酝熟，共君一醉一陶然。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('831', 'poem', '唐', '杜甫', '画鹰', '素练风霜起，苍鹰画作殊。\\n身思狡兔，侧目似愁胡。\\n绦镟光堪擿，轩楹势可呼。\\n何当击凡鸟，毛血洒平芜。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('832', 'poem', '唐', '岑参', '寄左省杜拾遗', '联步趋丹陛，分曹限紫微。\\n晓随天仗入，暮惹御香归。\\n白发悲花落，青云羡鸟飞。\\n圣朝无阙事，自觉谏书稀。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('833', 'poem', '唐', '韩琮', '晚春江晴寄友人', '晚日低霞绮，晴山远画眉。\\n春青河畔草，不是望乡时。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('834', 'poem', '唐', '杜牧', '南陵道中 / 寄远', '南陵水面漫悠悠，风紧云轻欲变秋。\\n正是客心孤回处，谁家红袖凭江楼。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('835', 'poem', '唐', '岑参', '送崔子还京', '匹马西从天外归，扬鞭只共鸟争飞。\\n送君九月交河北，雪里题诗泪满衣。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('836', 'poem', '唐', '杜荀鹤', '送人游吴', '君到姑苏见，人家尽枕河。\\n古宫闲地少，水港小桥多。\\n夜市卖菱藕，春船载绮罗。\\n遥知未眠月，乡思在渔歌。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('837', 'poem', '唐', '杜甫', '绝句漫兴九首·其五', '肠断春江欲尽头，杖藜徐步立芳洲。\\n颠狂柳絮随风去，轻薄桃花逐水流。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('838', 'poem', '唐', '骆宾王', '在军登城楼', '城上风威冷，江中水气寒。\\n戎衣何日定，歌舞入长安。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('839', 'poem', '唐', '李白', '长门怨二首', '天回北斗挂西楼，金屋无人萤火流。\\n月光欲到长门殿，别作深宫一段愁。\\n桂殿长愁不记春，黄金四屋起秋尘。\\n夜悬明镜青天上，独照长门宫里人。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('840', 'poem', '唐', '李商隐', '二月二日', '二月二日江上行，东风日暖闻吹笙。\\n花须柳眼各无赖，紫蝶黄蜂俱有情。\\n万里忆归元亮井，三年从事亚夫营。\\n新滩莫悟游人意，更作风檐夜雨声。', '2018-03-09 15:00:51');
INSERT INTO `t_poem` VALUES ('841', 'poem', '唐', '武则天', '唐享昊天乐·第十一', '礼终肆类，乐阕九成。\\n仰惟明德，敢荐非馨。\\n顾惭菲奠，久驰云輧。\\n瞻荷灵泽，悚恋兼盈。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('842', 'poem', '唐', '李商隐', '夕阳楼', '花明柳暗绕天愁，上尽重城更上楼。\\n欲问孤鸿向何处，不知身世自悠悠。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('843', 'poem', '唐', '李商隐', '槿花', '风露凄凄秋景繁，可怜荣落在朝昏。\\n未央宫里三千女，但保红颜莫保恩。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('844', 'poem', '唐', '苏颋', '郊庙歌辞·享龙池乐章·第七章', '西京凤邸跃龙泉，佳气休光镇在天。\\n轩后雾图今已得，秦王水剑昔常传。\\n恩鱼不似昆明钓，瑞鹤长如太液仙。\\n愿侍巡游同旧里，更闻箫鼓济楼船。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('845', 'poem', '唐', '罗隐', '魏城逢故人 / 绵谷回寄蔡氏昆仲', '一年两度锦城游，前值东风后值秋。\\n芳草有情皆碍马，好云无处不遮楼。\\n山将别恨和心断，水带离声入梦流。\\n今日因君试回首，淡烟乔木隔绵州。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('846', 'poem', '唐', '杜牧', '题扬州禅智寺', '雨过一蝉噪，飘萧松桂秋。\\n青苔满阶砌，白鸟故迟留。\\n暮霭生深树，斜阳下小楼。\\n谁知竹西路，歌吹是扬州。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('847', 'poem', '唐', '李商隐', '对雪二首', '寒气先侵玉女扉，清光旋透省郎闱。\\n梅花大庾岭头发，柳絮章台街里飞。\\n欲舞定随曹植马，有情应湿谢庄衣。\\n龙山万里无多远，留待行人二月归。\\n旋扑珠帘过粉墙，轻于柳絮重于霜。\\n已随江令夸琼树，又入卢家妒玉堂。\\n侵夜可能争桂魄，忍寒应欲试梅妆。\\n关河冻合东西路，肠断斑骓送陆郎。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('848', 'poem', '唐', '孟浩然', '凉州词', '浑成紫檀金屑文，作得琵琶声入云。\\n胡地迢迢三万里，那堪马上送明君。\\n异方之乐令人悲，羌笛胡笳不用吹。\\n坐看今夜关山月，思杀边城游侠儿。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('849', 'poem', '唐', '李白', '乌栖曲', '姑苏台上乌栖时，吴王宫里醉西施。\\n吴歌楚舞欢未毕，青山欲衔半边日。\\n银箭金壶漏水多，起看秋月坠江波。\\n东方渐高奈乐何！', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('850', 'poem', '唐', '戴叔伦', '题稚川山水', '松下茅亭五月凉，汀沙云树晚苍苍。\\n行人无限秋风思，隔水青山似故乡。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('851', 'poem', '唐', '柳中庸', '凉州曲二首', '关山万里远征人，一望关山泪满巾。\\n青海戍头空有月，黄沙碛里本无春。\\n高槛连天望武威，穷阴拂地戍金微。\\n九城弦管声遥发，一夜关山雪满飞。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('852', 'poem', '唐', '李商隐', '无题·凤尾香罗薄几重', '凤尾香罗薄几重，碧文圆顶夜深缝。\\n扇裁月魄羞难掩，车走雷声语未通。\\n曾是寂寥金烬暗，断无消息石榴红。\\n斑骓只系垂杨岸，何处西南任好风。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('853', 'poem', '唐', '杜甫', '奉和贾至舍人早朝大明宫 / 和贾至舍人早朝大明宫之作', '五夜漏声催晓箭，九重春色醉仙桃。\\n旌旗日暖龙蛇动，宫殿风微燕雀高。\\n朝罢香烟携满袖，诗成珠玉在挥毫。\\n欲知世掌丝纶美，池上于今有凤毛。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('854', 'poem', '唐', '皎然', '送灵澈', '我欲长生梦，无心解伤别。\\n千里万里心，只似眼前月。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('855', 'poem', '唐', '李白', '忆东山二首', '不向东山久，蔷薇几度花。\\n白云还自散，明月落谁家。\\n我今携谢妓，长啸绝人群。\\n欲报东山客，开关扫白云。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('856', 'poem', '唐', '李贺', '将进酒·琉璃钟', '琉璃钟，琥珀浓，小槽酒滴真珠红。烹龙炮凤玉脂泣，\\n罗屏绣幕围香风。吹龙笛，击鼍鼓。皓齿歌，细腰舞。\\n况是青春日将暮，桃花乱落如红雨。劝君终日酩酊醉，\\n酒不到刘伶坟上土。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('857', 'poem', '唐', '钱起', '归雁', '潇湘何事等闲回，水碧沙明两岸苔。\\n二十五弦弹夜月，不胜清怨却飞来。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('858', 'poem', '唐', '司空曙', '喜外弟卢纶见宿', '静夜四无邻，荒居旧业贫。\\n雨中黄叶树，灯下白头人。\\n以我独沈久，愧君相见频。\\n平生自有分，况是蔡家亲。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('859', 'poem', '唐', '李白', '别储邕之剡中', '借问剡中道，东南指越乡。\\n舟从广陵去，水入会稽长。\\n竹色溪下绿，荷花镜里香。\\n辞君向天姥，拂石卧秋霜。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('860', 'poem', '唐', '罗隐', '菊', '篱落岁云暮，数枝聊自芳。\\n雪裁纤蕊密，金拆小苞香。\\n千载白衣酒，一生青女霜。\\n春丛莫轻薄，彼此有行藏。', '2018-03-09 15:00:57');
INSERT INTO `t_poem` VALUES ('861', 'poem', '唐', '李冶', '相思怨', '人道海水深，不抵相思半。\\n海水尚有涯，相思渺无畔。\\n携琴上高楼，楼虚月华满。\\n弹著相思曲，弦肠一时断。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('862', 'poem', '唐', '李白', '赤壁歌送别', '二龙争战决雌雄，赤壁楼船扫地空。\\n烈火张天照云海，周瑜于此破曹公。\\n君去沧江望澄碧，鲸鲵唐突留馀迹。\\n一一书来报故人，我欲因之壮心魄。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('863', 'poem', '唐', '李白', '夜下征虏亭', '船下广陵去，月明征虏亭。\\n山花如绣颊，江火似流萤。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('864', 'poem', '唐', '戴叔伦', '除夜宿石头驿', '旅馆谁相问，寒灯独可亲。\\n一年将尽夜，万里未归人。\\n寥落悲前事，支离笑此身。\\n愁颜与衰鬓，明日又逢春。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('865', 'poem', '唐', '韦庄', '金陵图', '谁谓伤心画不成，画人心逐世人情。\\n君看六幅南朝事，老木寒云满故城。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('866', 'poem', '唐', '李白', '长相思·其二', '日色欲尽花含烟，月明欲素愁不眠。\\n赵瑟初停凤凰柱，蜀琴欲奏鸳鸯弦。\\n此曲有意无人传，愿随春风寄燕然。\\n忆君迢迢隔青天，昔日横波目，今作流泪泉。\\n不信妾断肠，归来看取明镜前。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('867', 'poem', '唐', '孔绍安', '侍宴咏石榴', '可惜庭中树，移根逐汉臣。\\n只为来时晚，花开不及春。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('868', 'poem', '唐', '鲍溶', '岐路', '北风送微寒，徒侣勤远征。\\n忧人席不暖，残月马上明。\\n飘飘岐路间，长见日初生。\\n重嶂晓色浅，疏猿寒啼清。\\n人间多岐路，常恐终身行。\\n回见四方人，车轮无留声。\\n空谷亦堪隐，下田非懒耕。\\n古人有遗训，饱食非亲荣。\\n我生礼义乡，少小见太平。\\n圣贤犹羁旅，况复非其名。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('869', 'poem', '唐', '聂夷中', '咏田家 / 伤田家', '二月卖新丝，五月粜新谷。\\n医得眼前疮，剜却心头肉。\\n我愿君王心，化作光明烛。\\n不照绮罗筵，只照逃亡屋。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('870', 'poem', '唐', '李白', '九日龙山饮', '九日龙山饮，黄花笑逐臣。\\n醉看风落帽，舞爱月留人。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('871', 'poem', '唐', '孟郊', '洛桥晚望', '天津桥下冰初结，洛阳陌上人行绝。\\n榆柳萧疏楼阁闲，月明直见嵩山雪。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('872', 'poem', '唐', '苏拯', '鸱枭', '为害未为害，其如污物类。\\n斯言之一玷，流传极天地。\\n良木不得栖，清波不得戏。\\n曾戏水堪疑，曾栖树终弃。\\n天不歼尔族，与夫恶相济。\\n地若默尔声，与夫妖为讳。\\n一时怀害心，千古不能替。\\n伤哉丑行人，兹禽亦为譬。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('873', 'poem', '唐', '司空图', '杏花', '诗家偏为此伤情，品韵由来莫与争。\\n解笑亦应兼解语，只应慵语倩莺声。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('874', 'poem', '唐', '杜审言', '送崔融', '君王行出将，书记远从征。\\n祖帐连河阙，军麾动洛城。\\n旌旃朝朔气，笳吹夜边声。\\n坐觉烟尘扫，秋风古北平。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('875', 'poem', '唐', '李白', '秋下荆门', '霜落荆门江树空，布帆无恙挂秋风。\\n此行不为鲈鱼鲙，自爱名山入剡中。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('876', 'poem', '唐', '杜甫', '九日五首·其一', '重阳独酌杯中酒，抱病起登江上台。\\n竹叶于人既无分，菊花从此不须开。\\n殊方日落玄猿哭，旧国霜前白雁来。\\n弟妹萧条各何在，干戈衰谢两相催！', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('877', 'poem', '唐', '杜牧', '早雁', '金河秋半虏弦开，云外惊飞四散哀。\\n仙掌月明孤影过，长门灯暗数声来。\\n须知胡骑纷纷在，岂逐春风一一回？\\n莫厌潇湘少人处，水多菰米岸莓苔。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('878', 'poem', '唐', '李白', '宿五松山下荀媪家', '我宿五松下，寂寥无所欢。\\n田家秋作苦，邻女夜舂寒。\\n跪进雕胡饭，月光明素盘。\\n令人惭漂母，三谢不能餐。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('879', 'poem', '唐', '杜牧', '题齐安城楼', '呜轧江楼角一声，微阳潋潋落寒汀。\\n不用凭栏苦回首，故乡七十五长亭。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('880', 'poem', '唐', '唐彦谦', '采桑女', '春风吹蚕细如蚁，桑芽才努青鸦嘴。\\n侵晨探采谁家女，手挽长条泪如雨。\\n去岁初眠当此时，今岁春寒叶放迟。\\n愁听门外催里胥，官家二月收新丝。', '2018-03-09 15:01:08');
INSERT INTO `t_poem` VALUES ('881', 'poem', '唐', '杜甫', '田舍', '田舍清江曲，柴门古道旁。\\n草深迷市井，地僻懒衣裳。\\n榉柳枝枝弱，枇杷树树香。\\n鸬鹚西日照，晒翅满鱼梁。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('882', 'poem', '唐', '王建', '野池', '野池水满连秋堤，菱花结实蒲叶齐。\\n川口雨晴风复止，蜻蜓上下鱼东西。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('883', 'poem', '唐', '许浑', '早秋三首·其一', '遥夜泛清瑟，西风生翠萝。\\n残萤栖玉露，早雁拂金河。\\n高树晓还密，远山晴更多。\\n淮南一叶下，自觉洞庭波。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('884', 'poem', '唐', '韦庄', '忆昔', '昔年曾向五陵游，子夜歌清月满楼。\\n银烛树前长似昼，露桃花里不知秋。\\n西园公子名无忌，南国佳人号莫愁。\\n今日乱离俱是梦，夕阳唯见水东流！', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('885', 'poem', '唐', '施肩吾', '杂曲歌辞·古曲五首', '可怜江北女，惯唱江南曲。\\n摇荡木兰舟，双凫不成浴。\\n郎为匕上香，妾为笼上灰。\\n归时虽暖热，去罢生尘埃。\\n夜裁鸳鸯绮，朝织蒲桃绫。\\n欲试一寸心，待缝三尺冰。\\n怜时鱼得水，怨罢商与参。\\n不如山支子，却解结同心。\\n红颜感暮花，白日同流水。\\n思君如孤灯，一夜一心死。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('886', 'poem', '唐', '吴叔达', '言行相顾', '圣人垂政教，万古请常传。\\n立志言为本，修身行乃先。\\n相须宁得阙，相顾在无偏。\\n荣辱当于己，忠贞必动天。\\n大名如副宝，至道亦通玄。\\n千里犹能应，何云迩者焉。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('887', 'poem', '唐', '白居易', '禁中', '门严九重静，窗幽一室闲。\\n好是修心处，何必在深山。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('888', 'poem', '唐', '李绅', '答章孝标', '假金只用真金镀，若是真金不镀金。\\n十载长安得一第，何须空腹用高心。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('889', 'poem', '唐', '王昌龄', '送狄宗亨', '秋在水清山暮蝉，洛阳树色鸣皋烟。\\n送君归去愁不尽，又惜空度凉风天。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('890', 'poem', '唐', '元稹', '遣悲怀三首·其一', '谢公最小偏怜女，嫁与黔娄百事乖。\\n顾我无衣搜画箧，泥他沽酒拔金钗。\\n野蔬充膳甘长藿，落叶添薪仰古槐。\\n今日俸钱过十万，与君营奠复营斋。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('891', 'poem', '唐', '柳宗元', '柳州二月榕叶落尽偶题', '宦情羁思共凄凄，春半如秋意转迷。\\n山城过雨百花尽，榕叶满庭莺乱啼。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('892', 'poem', '唐', '陈陶', '游子吟', '栖乌喜林曙，惊蓬伤岁阑。\\n关河三尺雪，何处是天山。\\n朔风无重衣，仆马饥且寒。\\n惨戚别妻子，迟回出门难。\\n男儿值休明，岂是长泥蟠。\\n何者为木偶，何人侍金銮。\\n郁郁守贫贱，悠悠亦无端。\\n进不图功名，退不处岩峦。\\n穷通在何日，光景如跳丸。\\n富贵苦不早，令人摧心肝。\\n誓期春之阳，一振摩霄翰。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('893', 'poem', '唐', '孟翱', '言行相顾', '将使言堪复，常闻行欲先。\\n比珪斯不玷，修己直如弦。\\n跬步非全进，吹嘘禀自然。\\n当令夫子察，无宿仲由贤。\\n正遇兴邦际，因怀入署年。\\n坐知清监下，相顾有人焉。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('894', 'poem', '唐', '贾岛', '忆江上吴处士', '闽国扬帆去，蟾蜍亏复圆。\\n秋风生渭水，落叶满长安。\\n此地聚会夕，当时雷雨寒。\\n兰桡殊未返，消息海云端。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('895', 'poem', '唐', '韦庄', '西塞山下作', '西塞山前水似蓝，乱云如絮满澄潭。\\n孤峰渐映湓城北，片月斜生梦泽南。\\n爨动晓烟烹紫蕨，露和香蒂摘黄柑。\\n他年却棹扁舟去，终傍芦花结一庵。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('896', 'poem', '唐', '贾岛', '暮过山村', '数里闻寒水，山家少四邻。\\n怪禽啼旷野，落日恐行人。\\n初月未终夕，边烽不过秦。\\n萧条桑柘外，烟火渐相亲。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('897', 'poem', '唐', '罗隐', '鹭鸶', '斜阳澹澹柳阴阴，风袅寒丝映水深。\\n不要向人夸素白，也知常有羡鱼心。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('898', 'poem', '唐', '杜甫', '寄韩谏议 / 寄韩谏议注', '今我不乐思岳阳，身欲奋飞病在床。\\n美人娟娟隔秋水，濯足洞庭望八荒。\\n鸿飞冥冥日月白，青枫叶赤天雨霜。\\n玉京群帝集北斗，或骑骐驎翳凤凰。\\n芙蓉旌旗烟雾乐，影动倒景摇潇湘。\\n星宫之君醉琼浆，羽人稀少不在旁。\\n似闻昨者赤松子，恐是汉代韩张良。\\n昔随刘氏定长安，帷幄未改神惨伤。\\n国家成败吾岂敢，色难腥腐餐风香。\\n周南留滞古所惜，南极老人应寿昌。\\n美人胡为隔秋水，焉得置之贡玉堂。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('899', 'poem', '唐', '李益', '春夜闻笛', '寒山吹笛唤春归，迁客相看泪满衣。\\n洞庭一夜无穷雁，不待天明尽北飞。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('900', 'poem', '唐', '杜甫', '曲江对酒', '苑外江头坐不归，水精宫殿转霏微。\\n桃花细逐杨花落，黄鸟时兼白鸟飞。\\n纵饮久判人共弃，懒朝真与世相违。\\n吏情更觉沧洲远，老大徒伤未拂衣。', '2018-03-09 15:01:17');
INSERT INTO `t_poem` VALUES ('901', 'poem', '唐', '李白', '陪侍郎叔游洞庭醉后三首', '今日竹林宴，我家贤侍郎。\\n三杯容小阮，醉后发清狂。\\n船上齐桡乐，湖心泛月归。\\n白鸥闲不去，争拂酒筵飞。\\n刬却君山好，平铺湘水流。\\n巴陵无限酒，醉杀洞庭秋。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('902', 'poem', '唐', '陆龟蒙', '春晓', '春庭晓景别，清露花逦迤。\\n黄蜂一过慵，夜夜栖香蕊。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('903', 'poem', '唐', '郑谷', '柳', '半烟半雨江桥畔，映杏映桃山路中。\\n会得离人无限意，千丝万絮惹春风。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('904', 'poem', '唐', '储光羲', '钓鱼湾', '垂钓绿湾春，春深杏花乱。\\n潭清疑水浅，荷动知鱼散。\\n日暮待情人，维舟绿杨岸。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('905', 'poem', '唐', '李白', '拟古', '融融白玉辉，映我青蛾眉。\\n宝镜似空水，落花如风吹。\\n出门望帝子，荡漾不可期。\\n安得黄鹤羽，一报佳人知。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('906', 'poem', '唐', '王维', '送别', '山中相送罢，日暮掩柴扉。\\n春草年年绿，王孙归不归。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('907', 'poem', '唐', '刘叉', '偶书', '日出扶桑一丈高，人间万事细如毛。\\n野夫怒见不平处，磨损胸中万古刀。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('908', 'poem', '唐', '李白', '金乡送韦八之西京', '客自长安来，还归长安去。\\n狂风吹我心，西挂咸阳树。\\n此情不可道，此别何时遇。\\n望望不见君，连山起烟雾。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('909', 'poem', '唐', '韦应物', '长安遇冯著', '客从东方来，衣上灞陵雨。\\n问客何为来，采山因买斧。\\n冥冥花正开，飏飏燕新乳。\\n昨别今已春，鬓丝生几缕。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('910', 'poem', '唐', '李商隐', '楚宫', '湘波如泪色漻漻，楚厉迷魂逐恨遥。\\n枫树夜猿愁自断，女萝山鬼语相邀。\\n空归腐败犹难复，更困腥臊岂易招？\\n但使故乡三户在，彩丝谁惜惧长蛟。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('911', 'poem', '唐', '施肩吾', '秋夜山居二首', '幽居正想餐霞客，夜久月寒珠露滴。\\n千年独鹤两三声，飞下岩前一枝柏。\\n去雁声遥人语绝，谁家素机织新雪。\\n秋山野客醉醒时，百尺老松衔半月。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('912', 'poem', '唐', '白居易', '鸟', '谁道群生性命微，一般骨肉一般皮。\\n劝君莫打枝头鸟，子在巢中望母归。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('913', 'poem', '唐', '沈彬', '再过金陵', '玉树歌终王气收，雁行高送石城秋。\\n江山不管兴亡事，一任斜阳伴客愁。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('914', 'poem', '唐', '薛能', '行径', '盘径入依依，旋惊幽鸟飞。\\n寻多苔色古，踏碎箨声微。\\n鞭节横妨户，枝梢动拂衣。\\n前溪闻到处，应接钓鱼矶。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('915', 'poem', '唐', '顾况', '竹枝曲', '帝子苍梧不复归，洞庭叶下荆云飞。\\n巴人夜唱竹枝后，肠断晓猿声渐稀。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('916', 'poem', '唐', '张建封', '竞渡歌', '五月五日天晴明，杨花绕江啼晓莺。\\n使君未出郡斋外，江上早闻齐和声。\\n使君出时皆有准，马前已被红旗引。\\n两岸罗衣破晕香，银钗照日如霜刃。\\n鼓声三下红旗开，两龙跃出浮水来。\\n棹影斡波飞万剑，鼓声劈浪鸣千雷。\\n鼓声渐急标将近，两龙望标目如瞬。\\n坡上人呼霹雳惊，竿头彩挂虹蜺晕。\\n前船抢水已得标，后船失势空挥桡。\\n疮眉血首争不定，输岸一朋心似烧。\\n只将输赢分罚赏，两岸十舟五来往。\\n须臾戏罢各东西，竞脱文身请书上。\\n吾今细观竞渡儿，何殊当路权相持。\\n不思得岸各休去，会到摧车折楫时。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('917', 'poem', '唐', '韩愈', '石鼓歌', '张生手持石鼓文，劝我试作石鼓歌。\\n少陵无人谪仙死，才薄将奈石鼓何。\\n周纲凌迟四海沸，宣王愤起挥天戈。\\n大开明堂受朝贺，诸侯剑佩鸣相磨。\\n蒐于岐阳骋雄俊，万里禽兽皆遮罗。\\n镌功勒成告万世，凿石作鼓隳嵯峨。\\n从臣才艺咸第一，拣选撰刻留山阿。\\n雨淋日灸野火燎，鬼物守护烦撝呵。\\n公从何处得纸本，毫发尽备无差讹。\\n辞严义密读难晓，字体不类隶与蝌。\\n年深岂免有缺画，快剑斫断生蛟鼍。\\n鸾翔凤翥众仙下，珊瑚碧树交枝柯。\\n金绳铁索锁钮壮，古鼎跃水龙腾梭。\\n陋儒编诗不收入，二雅褊迫无委蛇。\\n孔子西行不到秦，掎摭星宿遗羲娥。\\n嗟余好古生苦晚，对此涕泪双滂沱。\\n忆昔初蒙博士征，其年始改称元和。\\n故人从军在右辅，为我度量掘臼科。\\n濯冠沐浴告祭酒，如此至宝存岂多。\\n毡包席裹可立致，十鼓只载数骆驼。\\n荐诸太庙比郜鼎，光价岂止百倍过。\\n圣恩若许留太学，诸生讲解得切磋。\\n观经鸿都尚填咽，坐见举国来奔波。\\n剜苔剔藓露节角，安置妥帖平不颇。\\n大厦深檐与盖覆，经历久远期无佗。\\n中朝大官老于事，讵肯感激徒媕娿。\\n牧童敲火牛砺角，谁复著手为摩挲。\\n日销月铄就埋没，六年西顾空吟哦。\\n羲之俗书趁姿媚，数纸尚可博白鹅。\\n继周八代争战罢，无人收拾理则那。\\n方今太平日无事，柄任儒术崇丘轲。\\n安能以此尚论列，愿借辩口如悬河。\\n石鼓之歌止于此，呜呼吾意其蹉跎。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('918', 'poem', '唐', '李白', '赠裴十四', '朝见裴叔则，朗如行玉山。\\n黄河落天走东海，万里写入胸怀间。\\n身骑白鼋不敢度，金高南山买君顾。\\n徘徊六合无相知，飘若浮云且西去！', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('919', 'poem', '唐', '刘禹锡', '罢郡归洛阳寄友人', '远谪年犹少，初归鬓已衰。\\n门闲故吏去，室静老僧期。\\n不见蜘蛛集，频为佝偻欺。\\n颖微囊未出，寒甚谷难吹。\\n濩落唯心在，平生有己知。\\n商歌夜深后，听者竟为谁。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('920', 'poem', '唐', '杜甫', '别房太尉墓', '他乡复行役，驻马别孤坟。\\n近泪无干土，低空有断云。\\n对棋陪谢傅，把剑觅徐君。\\n唯见林花落，莺啼送客闻。', '2018-03-09 15:01:22');
INSERT INTO `t_poem` VALUES ('921', 'poem', '唐', '张祜', '集灵台·其二', '虢国夫人承主恩，平明骑马入宫门。\\n却嫌脂粉污颜色，淡扫峨眉朝至尊。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('922', 'poem', '唐', '李益', '从军北征', '天山雪后海风寒，横笛偏吹行路难。\\n碛里征人三十万，一时回向月明看。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('923', 'poem', '唐', '崔珏', '哭李商隐', '成纪星郎字义山，适归高壤抱长叹。\\n词林枝叶三春尽，学海波澜一夜干。\\n风雨已吹灯烛灭，姓名长在齿牙寒。\\n只应物外攀琪树，便著霓裳上绛坛。\\n虚负凌云万丈才，一生襟抱未曾开。\\n鸟啼花落人何在，竹死桐枯凤不来。\\n良马足因无主踠，旧交心为绝弦哀。\\n九泉莫叹三光隔，又送文星入夜台。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('924', 'poem', '唐', '李建勋', '春雨二首', '春霖未免妨游赏，唯到诗家自有情。花径不通新草合，\\n兰舟初动曲池平。净缘高树莓苔色，饥集虚廊燕雀声。\\n闲忆昔年为客处，闷留山馆阻行行。\\n萧萧春雨密还疏，景象三时固不如，寒入远林莺翅重，\\n暖抽新麦土膏虚。细蒙台榭微兼日，潜涨涟漪欲动鱼。\\n唯称乖慵多睡者，掩门中酒览闲书。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('925', 'poem', '唐', '储光羲', '江南曲四首', '绿江深见底，高浪直翻空。\\n惯是湖边住，舟轻不畏风。\\n逐流牵荇叶，缘岸摘芦苗。\\n为惜鸳鸯鸟，轻轻动画桡。\\n日暮长江里，相邀归渡头。\\n落花如有意，来去逐船流。\\n隔江看树色，沿月听歌声。\\n不是长干住，那从此路行。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('926', 'poem', '唐', '刘长卿', '送李判官之润州行营', '万里辞家事鼓鼙，金陵驿路楚云西。\\n江春不肯留归客，草色青青送马蹄。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('927', 'poem', '唐', '李白', '哭晁卿衡', '日本晁卿辞帝都，征帆一片绕蓬壶。\\n明月不归沉碧海，白云愁色满苍梧。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('928', 'poem', '唐', '岑参', '早上五盘岭', '平旦驱驷马，旷然出五盘。\\n江回两崖斗，日隐群峰攒。\\n苍翠烟景曙，森沉云树寒。\\n松疏露孤驿，花密藏回滩。\\n栈道谿雨滑，畬田原草干。\\n此行为知己，不觉蜀道难。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('929', 'poem', '唐', '罗隐', '江南', '玉树歌声泽国春，累累辎重忆亡陈。\\n垂衣端拱浑闲事，忍把江山乞与人。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('930', 'poem', '唐', '储光羲', '咏山泉 / 山中流泉', '山中有流水，借问不知名。\\n映地为天色，飞空作雨声。\\n转来深涧满，分出小池平。\\n恬澹无人见，年年长自清。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('931', 'poem', '唐', '杜甫', '小至', '天时人事日相催，冬至阳生春又来。\\n刺绣五纹添弱线，吹葭六琯动浮灰。\\n岸容待腊将舒柳，山意冲寒欲放梅。\\n云物不殊乡国异，教儿且覆掌中杯。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('932', 'poem', '唐', '唐彦谦', '春风四首', '春风吹愁端，散漫不可收。\\n不如古溪水，只望乡江流。\\n新花红烁烁，旧花满山白。\\n昔日金张门，狼藉馀废宅。\\n回头语春风，莫向新花丛。\\n我见朱颜人，多金亦成翁。\\n多金不足惜，丹砂亦何益。\\n更种明年花，春风自相识。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('933', 'poem', '唐', '罗隐', '春风', '也知有意吹嘘切，争奈人间善恶分。\\n但是秕糠微细物，等闲抬举到青云。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('934', 'poem', '唐', '李商隐', '风', '撩钗盘孔雀，恼带拂鸳鸯。\\n罗荐谁教近，斋时锁洞房。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('935', 'poem', '唐', '白居易', '初出城留别', '朝从紫禁归，暮出青门去。\\n勿言城东陌，便是江南路。\\n扬鞭簇车马，挥手辞亲故。\\n我生本无乡，心安是归处。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('936', 'poem', '唐', '杜荀鹤', '蚕妇', '粉色全无饥色加，岂知人世有荣华。\\n年年道我蚕辛苦，底事浑身着苎麻。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('937', 'poem', '唐', '韦庄', '与小女', '见人初解语呕哑，不肯归眠恋小车。\\n一夜娇啼缘底事，为嫌衣少缕金华。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('938', 'poem', '唐', '杜甫', '晚晴', '村晚惊风度，庭幽过雨沾。\\n夕阳薰细草，江色映疏帘。\\n书乱谁能帙，怀干可自添。\\n时闻有馀论，未怪老夫潜。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('939', 'poem', '唐', '郑谷', '短褐', '闲披短褐杖山藤，头不是僧心是僧。\\n坐睡觉来清夜半，芭蕉影动道场灯。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('940', 'poem', '唐', '崔道融', '溪上遇雨二首', '回塘雨脚如缫丝，野禽不起沈鱼飞。\\n耕蓑钓笠取未暇，秋田有望从淋漓。\\n坐看黑云衔猛雨，喷洒前山此独晴。\\n忽惊云雨在头上，却是山前晚照明。', '2018-03-09 15:01:30');
INSERT INTO `t_poem` VALUES ('941', 'poem', '唐', '王维', '田园乐七首·其一', '厌见千门万户，经过北里南邻。\\n官府鸣珂有底，崆峒散发何人。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('942', 'poem', '唐', '张九龄', '赋得自君之出矣', '自君之出矣，不复理残机。\\n思君如满月，夜夜减清辉。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('943', 'poem', '唐', '李约', '观祈雨', '桑条无叶土生烟，箫管迎龙水庙前。\\n朱门几处看歌舞，犹恐春阴咽管弦。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('944', 'poem', '唐', '李商隐', '商於', '商於朝雨霁，归路有秋光。\\n背坞猿收果，投岩麝退香。\\n建瓴真得势，横戟岂能当。\\n割地张仪诈，谋身绮季长。\\n清渠州外月，黄叶庙前霜。\\n今日看云意，依依入帝乡。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('945', 'poem', '唐', '曹邺', '北郭闲思', '山前山后是青草，尽日出门还掩门。\\n每思骨肉在天畔，来看野翁怜子孙。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('946', 'poem', '唐', '李白', '子夜吴歌·春歌', '秦地罗敷女，采桑绿水边。\\n素手青条上，红妆白日鲜。\\n蚕饥妾欲去，五马莫留连。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('947', 'poem', '唐', '刘长卿', '别严士元', '春风倚棹阖闾城，水国春寒阴复晴。\\n细雨湿衣看不见，闲花落地听无声。\\n日斜江上孤帆影，草绿湖南万里情。\\n东道若逢相识问，青袍今日误儒生。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('948', 'poem', '唐', '王维', '田园乐七首·其五', '山下孤烟远村，天边独树高原。\\n一瓢颜回陋巷，五柳先生对门。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('949', 'poem', '唐', '李白', '采莲曲', '若耶溪傍采莲女，笑隔荷花共人语。\\n日照新妆水底明，风飘香袂空中举。\\n岸上谁家游冶郎，三三五五映垂杨。\\n紫骝嘶入落花去，见此踟蹰空断肠。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('950', 'poem', '唐', '许棠', '秋江霁望', '高秋偏入望，霁景倍关情。\\n落木满江水，离人怀渭城。\\n山高孤戍断，野极暮天平。\\n渔父时相问，羞真道姓名。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('951', 'poem', '唐', '柳宗元', '饮酒', '今夕少愉乐，起坐开清尊。\\n举觞酹先酒，为我驱忧烦。\\n须臾心自殊，顿觉天地暄。\\n连山变幽晦，绿水函晏温。\\n蔼蔼南郭门，树木一何繁。\\n清阴可自庇，竟夕闻佳言。\\n尽醉无复辞，偃卧有芳荪。\\n彼哉晋楚富，此道未必存。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('952', 'poem', '唐', '佚名', '杂曲歌辞·盖罗缝', '秦时明月汉时关，万里征人尚未还。\\n但愿龙庭神将在，不教胡马渡阴山。\\n音书杜绝白狼西，桃李无颜黄鸟啼。\\n寒鸟春深归去尽，出门肠断草萋萋。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('953', 'poem', '唐', '徐凝', '汉宫曲', '水色帘前流玉霜，赵家飞燕侍昭阳。\\n掌中舞罢箫声绝，三十六宫秋夜长。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('954', 'poem', '唐', '戎昱', '八月十五日', '忆昔千秋节，欢娱万国同。\\n今来六亲远，此日一悲风。\\n年少逢胡乱，时平似梦中。\\n梨园几人在，应是涕无穷。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('955', 'poem', '唐', '罗隐', '西施', '家国兴亡自有时，吴人何苦怨西施。\\n西施若解倾吴国，越国亡来又是谁。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('956', 'poem', '唐', '储嗣宗', '孤雁', '孤雁暮飞急，萧萧天地秋。\\n关河正黄叶，消息断青楼。\\n湘渚烟波远，骊山风雨愁。\\n此时万里道，魂梦绕沧洲。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('957', 'poem', '唐', '张乔', '书边事', '调角断清秋，征人倚戍楼。\\n春风对青冢，白日落梁州。\\n大汉无兵阻，穷边有客游。\\n蕃情似此水，长愿向南流。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('958', 'poem', '唐', '韩偓', '草书屏风', '何处一屏风，分明怀素踪。\\n虽多尘色染，犹见墨痕浓。\\n怪石奔秋涧，寒藤挂古松。\\n若教临水畔，字字恐成龙。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('959', 'poem', '唐', '许浑', '秋日赴阙题潼关驿楼 / 行次潼关逢魏扶东归', '红叶晚萧萧，长亭酒一瓢。\\n残云归太华，疏雨过中条。\\n树色随山迥，河声入海遥。\\n帝乡明日到，犹自梦渔樵。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('960', 'poem', '唐', '韩偓', '已凉', '碧阑干外绣帘垂，猩色屏风画折枝。\\n八尺龙须方锦褥，已凉天气未寒时。', '2018-03-09 15:01:43');
INSERT INTO `t_poem` VALUES ('961', 'poem', '唐', '王昌龄', '浣纱女', '钱塘江畔是谁家，江上女儿全胜花。\\n吴王在时不得出，今日公然来浣纱。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('962', 'poem', '唐', '王昌龄', '同从弟南斋玩月忆山阴崔少府', '高卧南斋时，开帷月初吐。\\n清辉澹水木，演漾在窗户。\\n荏苒几盈虚，澄澄变今古。\\n美人清江畔，是夜越吟苦。\\n千里共如何，微风吹兰杜。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('963', 'poem', '唐', '崔郊', '赠婢', '公子王孙逐后尘，绿珠垂泪滴罗巾。\\n侯门一入深似海，从此萧郎是路人。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('964', 'poem', '唐', '岑参', '轮台歌奉送封大夫出师西征', '轮台城头夜吹角，轮台城北旄头落。\\n羽书昨夜过渠黎，单于已在金山西。\\n戍楼西望烟尘黑，汉兵屯在轮台北。\\n上将拥旄西出征，平明吹笛大军行。\\n四边伐鼓雪海涌，三军大呼阴山动。\\n虏塞兵气连云屯，战场白骨缠草根。\\n剑河风急雪片阔，沙口石冻马蹄脱。\\n亚相勤王甘苦辛，誓将报主静边尘。\\n古来青史谁不见，今见功名胜古人。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('965', 'poem', '唐', '王维', '和贾舍人早朝大明宫之作', '绛帻鸡人报晓筹，尚衣方进翠云裘。\\n九天阊阖开宫殿，万国衣冠拜冕旒。\\n日色才临仙掌动，香烟欲傍衮龙浮。\\n朝罢须裁五色诏，佩声归到凤池头。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('966', 'poem', '唐', '陆龟蒙', '离骚', '天问复招魂，无因彻帝阍。\\n岂知千丽句，不敌一谗言。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('967', 'poem', '唐', '杜甫', '前出塞九首', '戚戚去故里，悠悠赴交河。\\n公家有程期，亡命婴祸罗。\\n君已富土境，开边一何多。\\n弃绝父母恩，吞声行负戈。\\n出门日已远，不受徒旅欺。\\n骨肉恩岂断，男儿死无时。\\n走马脱辔头，手中挑青丝。\\n捷下万仞冈，俯身试搴旗。\\n磨刀呜咽水，水赤刃伤手。\\n欲轻肠断声，心绪乱已久。\\n丈夫誓许国，愤惋复何有。\\n功名图骐驎，战骨当速朽。\\n送徒既有长，远戍亦有身。\\n生死向前去，不劳吏怒嗔。\\n路逢相识人，附书与六亲。\\n哀哉两决绝，不复同苦辛。\\n迢迢万馀里，领我赴三军。\\n军中异苦乐，主将宁尽闻。\\n隔河见胡骑，倏忽数百群。\\n我始为奴仆，几时树功勋。\\n挽弓当挽强，用箭当用长。\\n射人先射马，擒贼先擒王。\\n杀人亦有限，列国自有疆。\\n苟能制侵陵，岂在多杀伤。\\n驱马天雨雪，军行入高山。\\n径危抱寒石，指落曾冰间。\\n已去汉月远，何时筑城还。\\n浮云暮南征，可望不可攀。\\n单于寇我垒，百里风尘昏。\\n雄剑四五动，彼军为我奔。\\n虏其名王归，系颈授辕门。\\n潜身备行列，一胜何足论。\\n从军十年馀，能无分寸功。\\n众人贵苟得，欲语羞雷同。\\n中原有斗争，况在狄与戎。\\n丈夫四方志，安可辞固穷。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('968', 'poem', '唐', '韦庄', '含山店梦觉作', '曾为流离惯别家，等闲挥袂客天涯。\\n灯前一觉江南梦，惆怅起来山月斜。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('969', 'poem', '唐', '李商隐', '无题', '紫府仙人号宝灯，云浆未饮结成冰。\\n如何雪月交光夜，更在瑶台十二层。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('970', 'poem', '唐', '杜甫', '咏怀古迹五首·其四', '蜀主窥吴幸三峡，崩年亦在永安宫。\\n翠华想像空山里，玉殿虚无野寺中。\\n古庙杉松巢水鹤，岁时伏腊走村翁。\\n武侯祠堂常邻近，一体君臣祭祀同。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('971', 'poem', '唐', '裴迪', '辋川集二十首·竹里馆', '来过竹里馆，日与道相亲。\\n出入唯山鸟，幽深无世人。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('972', 'poem', '唐', '元稹', '连昌宫词', '连昌宫中满宫竹，岁久无人森似束。又有墙头千叶桃，\\n风动落花红蔌蔌。宫边老翁为余泣，小年进食曾因入。\\n上皇正在望仙楼，太真同凭阑干立。楼上楼前尽珠翠，\\n炫转荧煌照天地。归来如梦复如痴，何暇备言宫里事。\\n初过寒食一百六，店舍无烟宫树绿。夜半月高弦索鸣，\\n贺老琵琶定场屋。力士传呼觅念奴，念奴潜伴诸郎宿。\\n须臾觅得又连催，特敕街中许然烛。春娇满眼睡红绡，\\n掠削云鬟旋装束。飞上九天歌一声，二十五郎吹管逐。\\n逡巡大遍凉州彻，色色龟兹轰录续。李谟擫笛傍宫墙，\\n偷得新翻数般曲。平明大驾发行宫，万人歌舞涂路中。\\n百官队仗避岐薛，杨氏诸姨车斗风。明年十月东都破，\\n御路犹存禄山过。驱令供顿不敢藏，万姓无声泪潜堕。\\n两京定后六七年，却寻家舍行宫前。庄园烧尽有枯井，\\n行宫门闭树宛然。尔后相传六皇帝，不到离宫门久闭。\\n往来年少说长安，玄武楼成花萼废。去年敕使因斫竹，\\n偶值门开暂相逐。荆榛栉比塞池塘，狐兔骄痴缘树木。\\n舞榭欹倾基尚在，文窗窈窕纱犹绿。尘埋粉壁旧花钿，\\n乌啄风筝碎珠玉。上皇偏爱临砌花，依然御榻临阶斜。\\n蛇出燕巢盘斗栱，菌生香案正当衙。寝殿相连端正楼，\\n太真梳洗楼上头。晨光未出帘影黑，至今反挂珊瑚钩。\\n指似傍人因恸哭，却出宫门泪相续。自从此后还闭门，\\n夜夜狐狸上门屋。我闻此语心骨悲，太平谁致乱者谁。\\n翁言野父何分别，耳闻眼见为君说。姚崇宋璟作相公，\\n劝谏上皇言语切。燮理阴阳禾黍丰，调和中外无兵戎。\\n长官清平太守好，拣选皆言由相公。开元之末姚宋死，\\n朝廷渐渐由妃子。禄山宫里养作儿，虢国门前闹如市。\\n弄权宰相不记名，依稀忆得杨与李。庙谟颠倒四海摇，\\n五十年来作疮痏，今皇神圣丞相明，诏书才下吴蜀平。\\n官军又取淮西贼，此贼亦除天下宁。年年耕种宫前道，\\n今年不遣子孙耕。老翁此意深望幸，努力庙谋休用兵。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('973', 'poem', '唐', '沈佺期', '杂诗三首·其三', '闻道黄龙戍，频年不解兵。\\n可怜闺里月，长在汉家营。\\n少妇今春意，良人昨夜情。\\n谁能将旗鼓，一为取龙城。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('974', 'poem', '唐', '李白', '劳劳亭', '天下伤心处，劳劳送客亭。\\n春风知别苦，不遣柳条青。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('975', 'poem', '唐', '卢仝', '白鹭鸶', '刻成片玉白鹭鸶，欲捉纤鳞心自急。\\n翘足沙头不得时，傍人不知谓闲立。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('976', 'poem', '唐', '王维', '待储光羲不至', '重门朝已启，起坐听车声。\\n要欲闻清佩，方将出户迎。\\n晚钟鸣上苑，疏雨过春城。\\n了自不相顾，临堂空复情。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('977', 'poem', '唐', '齐己', '落叶', '落多秋亦晚，窗外见诸邻。\\n世上谁惊尽，林间独扫频。\\n萧骚微月夜，重叠早霜晨。\\n昨日繁阴在，莺声树树春。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('978', 'poem', '唐', '韩偓', '凄凄', '深将宠辱齐，往往亦凄凄。\\n白日知丹抱，青云有旧蹊。\\n嗜咸凌鲁济，恶洁助泾泥。\\n风雨今如晦，堪怜报晓鸡。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('979', 'poem', '唐', '韩愈', '示儿', '始我来京师，止携一束书。\\n辛勤三十年，以有此屋庐。\\n此屋岂为华，于我自有馀。\\n中堂高且新，四时登牢蔬。\\n前荣馔宾亲，冠婚之所于。\\n庭内无所有，高树八九株。\\n有藤娄络之，春华夏阴敷。\\n东堂坐见山，云风相吹嘘。\\n松果连南亭，外有瓜芋区。\\n西偏屋不多，槐榆翳空虚。\\n山鸟旦夕鸣，有类涧谷居。\\n主妇治北堂，膳服适戚疏。\\n恩封高平君，子孙从朝裾。\\n开门问谁来，无非卿大夫。\\n不知官高卑，玉带悬金鱼。\\n问客之所为，峨冠讲唐虞。\\n酒食罢无为，棋槊以相娱。\\n凡此座中人，十九持钧枢。\\n又问谁与频，莫与张樊如。\\n来过亦无事，考评道精粗。\\n跹跹媚学子，墙屏日有徒。\\n以能问不能，其蔽岂可祛。\\n嗟我不修饰，事与庸人俱。\\n安能坐如此，比肩于朝儒。\\n诗以示儿曹，其无迷厥初。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('980', 'poem', '唐', '李贺', '秋来', '桐风惊心壮士苦，衰灯络纬啼寒素。\\n谁看青简一编书，不遣花虫粉空蠹。\\n思牵今夜肠应直，雨冷香魂吊书客。\\n秋坟鬼唱鲍家诗，恨血千年土中碧。', '2018-03-09 15:01:49');
INSERT INTO `t_poem` VALUES ('981', 'poem', '唐', '张若虚', '代答闺梦还', '关塞年华早，楼台别望违。\\n试衫著暖气，开镜觅春晖。\\n燕入窥罗幕，蜂来上画衣。\\n情催桃李艳，心寄管弦飞。\\n妆洗朝相待，风花暝不归。\\n梦魂何处入，寂寂掩重扉。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('982', 'poem', '唐', '孟郊', '酒德', '酒是古明镜，辗开小人心。\\n醉见异举止，醉闻异声音。\\n酒功如此多，酒屈亦以深。\\n罪人免罪酒，如此可为箴。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('983', 'poem', '唐', '李白', '送蔡山人', '我本不弃世，世人自弃我。\\n一乘无倪舟，八极纵远舵。\\n燕客期跃马，唐生安敢讥。\\n采珠勿惊龙，大道可暗归。\\n故山有松月，迟尔玩清晖。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('984', 'poem', '唐', '许浑', '夜归丁卯桥村舍', '月凉风静夜，归客泊岩前。\\n桥响犬遥吠，庭空人散眠。\\n紫蒲低水槛，红叶半江船。\\n自有还家计，南湖二顷田。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('985', 'poem', '唐', '王维', '田园乐七首·其三', '采菱渡头风急，策杖林西日斜。\\n杏树坛边渔父，桃花源里人家。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('986', 'poem', '唐', '白居易', '长安春', '青门柳枝软无力，东风吹作黄金色。\\n街东酒薄醉易醒，满眼春愁销不得。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('987', 'poem', '唐', '崔颢', '杂曲歌辞·长干曲四首', '君家何处住，妾住在横塘。\\n停舟暂借问，或恐是同乡。\\n家临九江水，来去九江侧。\\n同是长干人，生小不相识。\\n下渚多风浪，莲舟渐觉稀。\\n那能不相待，独自逆潮归。\\n三江潮水急，五湖风浪涌。\\n由来花性轻，莫畏莲舟重。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('988', 'poem', '唐', '李珣', '巫山一段云·古庙依青嶂', '古庙依青嶂，行宫枕碧流。水声山色锁妆楼。往事思悠悠。\\n云雨朝还暮，烟花春复秋。啼猿何必近孤舟。行客自多愁。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('989', 'poem', '唐', '杜甫', '蒹葭', '摧折不自守，秋风吹若何。\\n暂时花戴雪，几处叶沉波。\\n体弱春风早，丛长夜露多。\\n江湖后摇落，亦恐岁蹉跎。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('990', 'poem', '唐', '薛涛', '风', '猎蕙微风远，飘弦唳一声。\\n林梢鸣淅沥，松径夜凄清。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('991', 'poem', '唐', '白居易', '花下自劝酒', '酒盏酌来须满满，花枝看即落纷纷。\\n莫言三十是年少，百岁三分已一分。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('992', 'poem', '唐', '戴叔伦', '松鹤', '雨湿松阴凉，风落松花细。\\n独鹤爱清幽，飞来不飞去。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('993', 'poem', '唐', '杜甫', '严郑公宅同咏竹', '绿竹半含箨，新梢才出墙。\\n色侵书帙晚，阴过酒樽凉。\\n雨洗娟娟净，风吹细细香。\\n但令无剪伐，会见拂云长。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('994', 'poem', '唐', '王维', '春中田园作', '屋上春鸠鸣，村边杏花白。\\n持斧伐远扬，荷锄觇泉脉。\\n归燕识故巢，旧人看新历。\\n临觞忽不御，惆怅远行客。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('995', 'poem', '唐', '戴叔伦', '客夜与故人偶集 / 江乡故人偶集客舍', '天秋月又满，城阙夜千重。\\n还作江南会，翻疑梦里逢。\\n风枝惊暗鹊，露草覆寒蛩。\\n羁旅长堪醉，相留畏晓钟。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('996', 'poem', '唐', '刘方平', '新春', '南陌春风早，东邻曙色斜。\\n一花开楚国，双燕入卢家。\\n眠罢梳云髻，妆成上锦车。\\n谁知如昔日，更浣越溪纱。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('997', 'poem', '唐', '殷尧藩', '同州端午', '鹤发垂肩尺许长，离家三十五端阳。\\n儿童见说深惊讶，却问何方是故乡。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('998', 'poem', '唐', '王维', '送杨少府贬郴州', '明到衡山与洞庭，若为秋月听猿声。\\n愁看北渚三湘远，恶说南风五两轻。\\n青草瘴时过夏口，白头浪里出湓城。\\n长沙不久留才子，贾谊何须吊屈平。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('999', 'poem', '唐', '孟浩然', '秦中感秋寄远上人 / 秦中寄远上人', '一丘常欲卧，三径苦无资。\\n北土非吾愿，东林怀我师。\\n黄金然桂尽，壮志逐年衰。\\n日夕凉风至，闻蝉但益悲。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('1000', 'poem', '唐', '钱起', '谷口书斋寄杨补阙', '泉壑带茅茨，云霞生薜帷。\\n竹怜新雨后，山爱夕阳时。\\n闲鹭栖常早，秋花落更迟。\\n家童扫萝径，昨与故人期。', '2018-03-09 15:02:01');
INSERT INTO `t_poem` VALUES ('1001', 'poem', '唐', '白居易', '池上早秋', '荷芰绿参差，新秋水满池。\\n早凉生北槛，残照下东篱。\\n露饱蝉声懒，风干柳意衰。\\n过潘二十岁，何必更愁悲。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1002', 'poem', '唐', '王昌龄', '西宫春怨', '西宫夜静百花香，欲卷珠帘春恨长。\\n斜抱云和深见月，朦胧树色隐昭阳。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1003', 'poem', '唐', '李商隐', '乐游原', '万树鸣蝉隔岸虹，乐游原上有西风。\\n羲和自趁虞泉宿，不放斜阳更向东。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1004', 'poem', '唐', '李白', '答王十二寒夜独酌有怀', '昨夜吴中雪，子猷佳兴发。万里浮云卷碧山，\\n青天中道流孤月。孤月沧浪河汉清，北斗错落长庚明。\\n怀余对酒夜霜白，玉床金井水峥嵘。人生飘忽百年内，\\n且须酣畅万古情。君不能狸膏金距学斗鸡，\\n坐令鼻息吹虹霓。君不能学哥舒横行青海夜带刀，\\n西屠石堡取紫袍。吟诗作赋北窗里，万言不直一杯水。\\n世人闻此皆掉头，有如东风射马耳。鱼目亦笑我，\\n请与明月同。骅骝拳跼不能食，蹇驴得志鸣春风。\\n折杨皇华合流俗，晋君听琴枉清角。巴人谁肯和阳春。\\n楚地由来贱奇璞。黄金散尽交不成，白首为儒身被轻。\\n一谈一笑失颜色，苍蝇贝锦喧谤声。曾参岂是杀人者，\\n谗言三及慈母惊。与君论心握君手，荣辱于余亦何有。\\n孔圣犹闻伤凤麟，董龙更是何鸡狗。一生傲岸苦不谐，\\n恩疏媒劳志多乖。严陵高揖汉天子，\\n何必长剑拄颐事玉阶。达亦不足贵，穷亦不足悲。\\n韩信羞将绛灌比，祢衡耻逐屠沽儿。君不见李北海，\\n英风豪气今何在。君不见裴尚书，土坟三尺蒿棘居。\\n少年早欲五湖去，见此弥将钟鼎疏。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1005', 'poem', '唐', '王维', '山居即事', '寂寞掩柴扉，苍茫对落晖。\\n鹤巢松树遍，人访荜门稀。\\n绿竹含新粉，红莲落故衣。\\n渡头烟火起，处处采菱归。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1006', 'poem', '唐', '杜甫', '望岳三首·其三', '南岳配朱鸟，秩礼自百王。\\n欻吸领地灵，鸿洞半炎方。\\n邦家用祀典，在德非馨香。\\n巡守何寂寥，有虞今则亡。\\n洎吾隘世网，行迈越潇湘。\\n渴日绝壁出，漾舟清光旁。\\n祝融五峰尊，峰峰次低昂。\\n紫盖独不朝，争长嶪相望。\\n恭闻魏夫人，群仙夹翱翔。\\n有时五峰气，散风如飞霜。\\n牵迫限修途，未暇杖崇冈。\\n归来觊命驾，沐浴休玉堂。\\n三叹问府主，曷以赞我皇。\\n牲璧忍衰俗，神其思降祥。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1007', 'poem', '唐', '元稹', '松鹤', '渚宫本坳下，佛庙有台阁。\\n台下三四松，低昂势前却。\\n是时晴景丽，松梢残雪薄。\\n日色相玲珑，纤云映罗幕。\\n逡巡九霄外，似振风中铎。\\n渐见尺帛光，孤飞唳空鹤。\\n裴回耀霜雪，顾慕下寥廓。\\n蹋动樛盘枝，龙蛇互跳跃。\\n俯瞰九江水，旁瞻万里壑。\\n无心眄乌鸢，有字悲城郭。\\n清角已沉绝，虞韶亦冥寞。\\n鶱翻勿重留，幸及钧天作。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1008', 'poem', '唐', '杜牧', '齐安郡晚秋', '柳岸风来影渐疏，使君家似野人居。\\n云容水态还堪赏，啸志歌怀亦自如。\\n雨暗残灯棋散后，酒醒孤枕雁来初。\\n可怜赤壁争雄渡，唯有蓑翁坐钓鱼。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1009', 'poem', '唐', '柳宗元', '重别梦得', '二十年来万事同，今朝岐路忽西东。\\n皇恩若许归田去，晚岁当为邻舍翁。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1010', 'poem', '唐', '韦应物', '郡斋雨中与诸文士燕集', '兵卫森画戟，宴寝凝清香。\\n海上风雨至，逍遥池阁凉。\\n烦疴近消散，嘉宾复满堂。\\n自惭居处崇，未睹斯民康。\\n理会是非遣，性达形迹忘。\\n鲜肥属时禁，蔬果幸见尝。\\n俯饮一杯酒，仰聆金玉章。\\n神欢体自轻，意欲凌风翔。\\n吴中盛文史，群彦今汪洋。\\n方知大藩地，岂曰财赋疆。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1011', 'poem', '唐', '刘复', '夏日', '映日纱窗深且闲，含桃红日石榴殷。\\n银瓶绠转桐花井，沉水烟销金博山。\\n文簟象床娇倚瑟，彩奁铜镜懒拈环。\\n明朝戏去谁相伴，年少相逢狭路间。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1012', 'poem', '唐', '杜甫', '江亭', '坦腹江亭暖，长吟野望时。\\n水流心不竞，云在意俱迟。\\n寂寂春将晚，欣欣物自私。\\n故林归未得，排闷强裁诗。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1013', 'poem', '唐', '白居易', '池畔二首', '结构池西廊，疏理池东树。\\n此意人不知，欲为待月处。\\n持刀间密竹，竹少风来多。\\n此意人不会，欲令池有波。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1014', 'poem', '唐', '李白', '白马篇', '龙马花雪毛，金鞍五陵豪。\\n秋霜切玉剑，落日明珠袍。\\n斗鸡事万乘，轩盖一何高。\\n弓摧南山虎，手接太行猱。\\n酒后竞风采，三杯弄宝刀。\\n杀人如剪草，剧孟同游遨。\\n发愤去函谷，从军向临洮。\\n叱咤经百战，匈奴尽奔逃。\\n归来使酒气，未肯拜萧曹。\\n羞入原宪室，荒淫隐蓬蒿。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1015', 'poem', '唐', '钱珝', '江行无题一百首', '倾酒向涟漪，乘流欲去时。寸心同尺璧，投此报冯夷。\\n江曲全萦楚，云飞半自秦。岘山回首望，如别故乡人。\\n浦烟含夜色，冷日转秋旻，自有沈碑在，清光不照人。\\n楚岸云空合，楚城人不来。只今谁善舞，莫恨废章台。\\n行背青山郭，吟当白露秋。风流无屈宋，空咏古荆州。\\n晚来渔父喜，网重欲收迟。恐有长江使，金钱愿赎龟。\\n去指龙沙路，徒悬象阙心。夜凉无远梦，不为偶闻砧。\\n雾云疏有叶，雨浪细无花。稳放扁舟去，江天自有涯。\\n好日当秋半，层波动旅肠。已行千里外，谁与共秋光。\\n润色非东里，官曹更建章。宦游难自定，来唤棹船郎。\\n夜江清未晓，徒惜月光沉。不是因行乐，堪伤老大心。\\n翳日多乔木，维舟取束薪。静听江叟语，尽是厌兵人。\\n箭漏日初短，汀烟草未衰。雨馀虽更绿，不是采蘋时。\\n山雨夜来涨，喜鱼跳满江。岸沙平欲尽，垂蓼入船窗。\\n渚边新雁下，舟上独凄凉。俱是南来客，怜君缀一行。\\n云密连江暗，风斜著物鸣。一杯真战将，笑尔作愁兵。\\n柳拂斜开路，篱边数户村。可能还有意，不掩向江门。\\n不识相如渴，徒吟子美诗。江清惟独看，心外更谁知。\\n牵路沿江狭，沙崩岸不平。尽知行处险，谁肯载时轻。\\n憔悴异灵均，非谗作逐臣。如逢渔父问，未是独醒人。\\n水含秋夜静，云带夕阳高。诗癖非吾病，何妨吮短豪。\\n带舟维古岸，还似阻西陵。箕伯无多怒，回头讵不能。\\n秋云久无雨，江燕社犹飞。却笑舟中客，今年未得归。\\n帆翅初张处，云鹏怒翼同。莫愁千里路，自有到来风。\\n佳节虽逢菊，浮生正似萍。故山何处望，荒岸小长亭。\\n月下江流静，村荒人语稀。鹭鸳虽有伴，仍共影双飞。\\n斗转月未落，舟行夜已深。有村知不远，风便数声砧。\\n棹惊沙鸟迅，飞溅夕阳波。不顾鱼多处，应防一目罗。\\n行到楚江岸，苍茫人正迷。只如秦塞远，格磔鹧鸪啼。\\n渐觉江天远，难逢故国书。可能无往事，空食鼎中鱼。\\n岸草连荒色，村声乐稔年。晚晴贪获稻，闲却彩菱船。\\n滩浅多游鹭，江清易见鱼。怪来吟未足，秋物欠红蕖。\\n蛩响依沙草，萤飞透水烟。夜凉谁咏史，空泊运租船。\\n睡稳叶舟轻，风微浪不惊。人居芦苇岸，终夜动秋声。\\n自念平生意，曾期一郡符。可知因谪宦，斑鬓入江湖。\\n水天凉夜月，不是少清光。好景随人物，秦淮忆建康。\\n古来多思客，摇落恨江潭。今日秋风至，萧疏过沔南。\\n映竹疑村好，穿芦觉渚幽。渐安无旷土，姜芋当农收。\\n烟渚复烟渚，画屏休画屏。引愁天末去，数点暮山青。\\n秋风动客心，寂寂不成吟。飞上危樯立，莺啼报好音。\\n见底高秋水，开怀万里天。旅吟还有伴，沙柳数枝蝉。\\n九日自佳节，扁舟无一杯。曹园旧樽酒，戏马忆高台。\\n兵火有馀烬，贫村才数家。无人争晓渡，残月下寒沙。\\n渚禽菱芡足，不向稻粱争。静宿凉湾月，应无失侣声。\\n轻云未扑霜，树杪橘初黄。行是知名物，过风过水香。\\n土旷深耕少，江平远钓多。平生皆弃本，金革竟如何。\\n海月非常物，等闲不可寻。披沙应有地，浅处定无金。\\n风晚冷飕飕，芦花已白头。旧来红叶寺，堪忆玉京秋。\\n渺渺望天涯，清涟浸赤霞。难逢星汉使，乌鹊日乘槎。\\n风好来无阵，云开去有踪。钓歌无远近，应喜罢艨艟。\\n吴疆连楚甸，楚俗异吴乡。谩把樽中物，无人啄蟹黄。\\n岸绿野烟远，江红斜照微。撑开小渔艇，应到月明归。\\n雨馀江始涨，漾漾见流薪。曾叹河中木，斯言忆古人。\\n垂露晚犹浓，清风不易逢。涉江虽已晚，高树搴芙蓉。\\n乘舟维夏口，烟野独行时。不见头陀寺，空怀幼妇碑。\\n晚泊武昌岸，津亭疏柳风。数株曾手植，好事忆陶公。\\n舟航依浦定，星斗满江寒。若比阴霾日，何妨夜未阑。\\n近戍离金落，孤岑望火门。惟将知命意，潇洒向乾坤。\\n丛菊生堤上，此花长后时。有人还采掇，何必在春期。\\n景夕残霞落，秋寒细雨晴。短缨何用濯，舟在月中行。\\n堤坏漏江水，地坳成野塘。晚荷人不折，留取作秋香。\\n左宦终何路，摅怀亦自宽。襞笺嘲白鹭，无意喻枭鸾。\\n楼空人不归，云白去时衣。黄鹤无心下，长应笑令威。\\n白帝朝惊浪，阳台暮映云。等闲生险易，世路只如君。\\n橹慢生轻浪，帆虚带白云。客船虽狭小，容得瘦将军。\\n静看秋江水，风渐浪微平。人间驰竞处，尘土自波成。\\n风借帆方疾，风回棹却迟。较量人世事，不校一毫厘。\\n咫尺愁风雨，匡庐不可登。只疑云雾窟，犹有六朝僧。\\n江草何多思，冬青尚满洲。谁能惊鵩鸟，作赋为沙鸥。\\n幸有烟波兴，宁辞笔砚劳。缘情无怨刺，却似反离骚。\\n沙上独行时，高吟到楚词。难将垂岸蓼，盈把当江蓠。\\n秋寒鹰隼健，逐雀下云空。知是江湖客，无心击塞鸿。\\n幽怀念烟水，长恨隔龙沙。今日滕王阁，分明见落霞。\\n江流何渺渺，怀古独依依。渔父非贤者，芦中但有矶。\\n风雨正甘寝，云霓忽晓晴。放歌虽自遣，一岁又峥嵘。\\n幽思正迟迟，沙边濯弄时。自怜非博物，犹未识凫葵。\\n曾有烟波客，能歌西塞山。落花惟待月，一钓紫菱湾。\\n千顷水纹细，一拳岚影孤。君山寒树绿，曾过洞庭湖。\\n光阔重湖水，低斜远雁行。未曾无兴咏，多谢沈东阳。\\n晚菊绕江垒，忽如开古屏。莫言时节过，白日有馀馨。\\n日落长亭晚，山门步障青。可怜无酒分，处处有旗亭。\\n远岸无行树，经霜有伴红。停船搜好句，题叶赠江枫。\\n身世比行舟，无风亦暂休。敢言终破浪，惟愿稳乘流。\\n数亩苍苔石，烟濛鹤卵洲。定因词客遇，名字始风流。\\n兴闲停桂楫，路好过松门。不负佳山水，还开酒一樽。\\n短楫休敲桂，孤根自驻萍。自怜非剑气，空向斗牛星。\\n高浪如银屋，江风一发时。笔端降太白，才大语终奇。\\n细竹渔家路，晴阳看结罾。喜来邀客坐，分与折腰菱。\\n平湖五百里，江水想通波。不奈扁舟去，其如决计何。\\n数逢云断处，去岸映高山。身到章江日，应犹未得闲。\\n一湾斜照水，三版顺风船。未敢相邀钓，劳生只自怜。\\n江雨正霏微，江村晚渡稀。何曾妨钓艇，更待得鱼归。\\n新野旧楼名，浔阳胜赏情。照人长一色，江月共凄清。\\n愿饮西江水，那吟北渚愁。莫教留滞迹，远比蔡昭侯。\\n湖口分江水，东流独有情。常时好风物，谁伴谢宣城。\\n浔阳江畔菊，应似古来秋。为问幽栖客，吟时得酒不。\\n高峰有佳号，千尺倚寒风。若使炉烟在，犹应为上公。\\n万木已清霜，江边村事忙。故溪黄稻熟，一夜梦中香。\\n楚水苦萦回，征帆落又开。可缘非直路，却有好风来。\\n远谪岁时晏，暮江风雨寒。仍愁系舟处，惊梦近长滩。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1016', 'poem', '唐', '郑谷', '海棠', '春风用意匀颜色，销得携觞与赋诗。\\n秾丽最宜新著雨，娇饶全在欲开时。\\n莫愁粉黛临窗懒，梁广丹青点笔迟。\\n朝醉暮吟看不足，羡他蝴蝶宿深枝。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1017', 'poem', '唐', '韩翃', '宿石邑山中', '浮云不共此山齐，山霭苍苍望转迷。\\n晓月暂飞高树里，秋河隔在数峰西。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1018', 'poem', '唐', '赵鸾鸾', '酥乳', '粉香汗湿瑶琴轸，春逗酥融绵雨膏。\\n浴罢檀郎扪弄处，灵华凉沁紫葡萄。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1019', 'poem', '唐', '高适', '营州歌', '营州少年厌原野，狐裘蒙茸猎城下。\\n虏酒千钟不醉人，胡儿十岁能骑马。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1020', 'poem', '唐', '曹邺', '送进士下第归南海', '数片红霞映夕阳，揽君衣袂更移觞。\\n行人莫叹碧云晚，上国每年春草芳。\\n雪过蓝关寒气薄，雁回湘浦怨声长。\\n应无惆怅沧波远，十二玉楼非我乡。', '2018-03-09 15:02:10');
INSERT INTO `t_poem` VALUES ('1021', 'poem', '唐', '卢象', '竹里馆', '江南冰不闭，山泽气潜通。\\n腊月闻山鸟，寒崖见蛰熊。\\n柳林春半合，荻笋乱无丛。\\n回首金陵岸，依依向北风。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1022', 'poem', '唐', '司空图', '中秋', '闲吟秋景外，万事觉悠悠。\\n此夜若无月，一年虚过秋。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1023', 'poem', '唐', '唐彦谦', '咏月', '阴盛此宵中，多为雨与风。\\n坐无风雨至，看与雪霜同。\\n抱湿离遥海，倾寒向远空。\\n年年不相值，还似道难通。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1024', 'poem', '唐', '杜甫', '曲江对雨', '城上春云覆苑墙，江亭晚色静年芳。\\n林花著雨燕脂落，水荇牵风翠带长。\\n龙武新军深驻辇，芙蓉别殿谩焚香。\\n何时诏此金钱会，暂醉佳人锦瑟旁。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1025', 'poem', '唐', '陆龟蒙', '奉和袭美茶具十咏·茶人', '天赋识灵草，自然钟野姿。\\n闲来北山下，似与东风期。\\n雨后探芳去，云间幽路危。\\n唯应报春鸟，得共斯人知。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1026', 'poem', '唐', '李商隐', '凉思', '客去波平槛，蝉休露满枝。\\n永怀当此节，倚立自移时。\\n北斗兼春远，南陵寓使迟。\\n天涯占梦数，疑误有新知。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1027', 'poem', '唐', '王驾', '春晴', '雨前初见花间蕊，雨后全无叶底花。\\n蜂蝶纷纷过墙去，却疑春色在邻家。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1028', 'poem', '唐', '王维', '杂诗三首 / 杂咏三首', '家住孟津河，门对孟津口。\\n常有江南船，寄书家中否。\\n君自故乡来，应知故乡事。\\n来日绮窗前，寒梅著花未。\\n已见寒梅发，复闻啼鸟声。\\n心心视春草，畏向阶前生。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1029', 'poem', '唐', '崔颢', '行经华阴', '岧峣太华俯咸京，天外三峰削不成。\\n武帝祠前云欲散，仙人掌上雨初晴。\\n河山北枕秦关险，驿树西连汉畤平。\\n借问路傍名利客，无如此处学长生。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1030', 'poem', '唐', '张籍', '送安西将', '万里海西路，茫茫边草秋。\\n计程沙塞口，望伴驿峰头。\\n雪暗非时宿，沙深独去愁。\\n塞乡人易老，莫住近蕃州。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1031', 'poem', '唐', '陈羽', '从军行', '海畔风吹冻泥裂，枯桐叶落枝梢折。\\n横笛闻声不见人，红旗直上天山雪。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1032', 'poem', '唐', '任翻', '惜花', '无语与花别，细看枝上红。\\n明年又相见，还恐是愁中。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1033', 'poem', '唐', '李白', '太原早秋', '岁落众芳歇，时当大火流。\\n霜威出塞早，云色渡河秋。\\n梦绕边城月，心飞故国楼。\\n思归若汾水，无日不悠悠。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1034', 'poem', '唐', '徐夤', '鹧鸪', '绣仆梅兼羽翼全，楚鸡非瑞莫争先。\\n啼归明月落边树，飞入百花深处烟。\\n避烧几曾遗远岫，引雏时见饮晴川。\\n荔枝初熟无人际，啄破红苞坠野田。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1035', 'poem', '唐', '杜甫', '小寒食舟中作', '佳辰强饮食犹寒，隐几萧条戴鹖冠。\\n春水船如天上坐，老年花似雾中看。\\n娟娟戏蝶过闲幔，片片轻鸥下急湍。\\n云白山青万余里，愁看直北是长安。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1036', 'poem', '唐', '张蠙', '抒怀', '几出东堂谢不才，便甘闲望故山回。\\n翻思未是离家久，更有人从外国来。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1037', 'poem', '唐', '李德裕', '思平泉树石杂咏一十首·舴艋舟', '无轻舴艋舟，始自鸱夷子。\\n双阙挂朝衣，五湖极烟水。\\n时游杏坛下，乍入湘川里。\\n永日歌濯缨，超然谢尘滓。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1038', 'poem', '唐', '李涉', '竹里', '竹里编茅倚石根，竹茎疏处见前村。\\n闲眠尽日无人到，自有春风为扫门。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1039', 'poem', '唐', '李贺', '天上谣', '天河夜转漂回星，银浦流云学水声。\\n玉宫桂树花未落，仙妾采香垂珮缨。\\n秦妃卷帘北窗晓，窗前植桐青凤小。\\n王子吹笙鹅管长，呼龙耕烟种瑶草。\\n粉霞红绶藕丝裙，青洲步拾兰苕春。\\n东指羲和能走马，海尘新生石山下。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1040', 'poem', '唐', '齐己', '剑客', '拔剑绕残樽，歌终便出门。\\n西风满天雪，何处报人恩。\\n勇死寻常事，轻雠不足论。\\n翻嫌易水上，细碎动离魂。', '2018-03-09 15:02:16');
INSERT INTO `t_poem` VALUES ('1041', 'poem', '唐', '于鹄', '巴女谣', '巴女骑牛唱竹枝，藕丝菱叶傍江时。\\n不愁日暮还家错，记得芭蕉出槿篱。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1042', 'poem', '唐', '杜甫', '偶题', '文章千古事，得失寸心知。\\n作者皆殊列，名声岂浪垂。\\n骚人嗟不见，汉道盛于斯。\\n前辈飞腾入，馀波绮丽为。\\n后贤兼旧列，历代各清规。\\n法自儒家有，心从弱岁疲。\\n永怀江左逸，多病邺中奇。\\n騄骥皆良马，骐驎带好儿。\\n车轮徒已斫，堂构惜仍亏。\\n漫作潜夫论，虚传幼妇碑。\\n缘情慰漂荡，抱疾屡迁移。\\n经济惭长策，飞栖假一枝。\\n尘沙傍蜂虿，江峡绕蛟螭。\\n萧瑟唐虞远，联翩楚汉危。\\n圣朝兼盗贼，异俗更喧卑。\\n郁郁星辰剑，苍苍云雨池。\\n两都开幕府，万宇插军麾。\\n南海残铜柱，东风避月支。\\n音书恨乌鹊，号怒怪熊罴。\\n稼穑分诗兴，柴荆学土宜。\\n故山迷白阁，秋水隐黄陂。\\n不敢要佳句，愁来赋别离。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1043', 'poem', '唐', '李白', '短歌行', '白日何短短，百年苦易满。\\n苍穹浩茫茫，万劫太极长。\\n麻姑垂两鬓，一半已成霜。\\n天公见玉女，大笑亿千场。\\n吾欲揽六龙，回车挂扶桑。\\n北斗酌美酒，劝龙各一觞。\\n富贵非所愿，与人驻颜光。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1044', 'poem', '唐', '韦应物', '西涧即事示卢陟', '寝扉临碧涧，晨起澹忘情。\\n空林细雨至，圆文遍水生。\\n永日无馀事，山中伐木声。\\n知子尘喧久，暂可散烦缨。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1045', 'poem', '唐', '李隆基', '好时光·宝髻偏宜宫样', '宝髻偏宜宫样，莲脸嫩，体红香。眉黛不须张敞画，天教入鬓长。\\n莫倚倾国貌，嫁取个，有情郎。彼此当年少，莫负好时光。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1046', 'poem', '唐', '李颀', '听安万善吹觱篥歌', '南山截竹为觱篥，此乐本自龟兹出。\\n流传汉地曲转奇，凉州胡人为我吹。\\n傍邻闻者多叹息，远客思乡皆泪垂。\\n世人解听不解赏，长飙风中自来往。\\n枯桑老柏寒飕飗，九雏鸣凤乱啾啾。\\n龙吟虎啸一时发，万籁百泉相与秋。\\n忽然更作渔阳掺，黄云萧条白日暗。\\n变调如闻杨柳春，上林繁花照眼新。\\n岁夜高堂列明烛，美酒一杯声一曲。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1047', 'poem', '唐', '郑谷', '迁客', '离夜闻横笛，可堪吹鹧鸪。\\n雪冤知早晚，雨泣渡江湖。\\n秋树吹黄叶，腊烟垂绿芜。\\n虞翻归有日，莫便哭穷途。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1048', 'poem', '唐', '薛涛', '送友人', '水国蒹葭夜有霜，月寒山色共苍苍。\\n谁言千里自今夕，离梦杳如关塞长。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1049', 'poem', '唐', '贾岛', '宿山寺', '众岫耸寒色，精庐向此分。\\n流星透疏木，走月逆行云。\\n绝顶人来少，高松鹤不群。\\n一僧年八十，世事未曾闻。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1050', 'poem', '唐', '杨乘', '吴中书事', '十万人家天堑东，管弦台榭满春风。\\n名归范蠡五湖上，国破西施一笑中。\\n香径自生兰叶小，响廊深映月华空。\\n尊前多暇但怀古，尽日愁吟谁与同。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1051', 'poem', '唐', '白居易', '春游', '上马临出门，出门复逡巡。\\n回头问妻子，应怪春游频。\\n诚知春游频，其奈老大身。\\n朱颜去复去，白发新更新。\\n请君屈十指，为我数交亲。\\n大限年百岁，几人及七旬。\\n我今六十五，走若下坂轮。\\n假使得七十，只有五度春。\\n逢春不游乐，但恐是痴人。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1052', 'poem', '唐', '吴融', '杏花', '春物竞相妒，杏花应最娇。\\n红轻欲愁杀，粉薄似啼销。\\n愿作南华蝶，翩翩绕此条。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1053', 'poem', '唐', '唐彦谦', '楚世家', '偏信由来惑是非，一言邪佞脱危机。\\n张仪重入怀王手，驷马安车却放归。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1054', 'poem', '唐', '王昌龄', '送张四', '枫林已愁暮，楚水复堪悲。\\n别后冷山月，清猿无断时。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1055', 'poem', '唐', '杜甫', '白帝城最高楼', '城尖径昃旌旆愁，独立缥缈之飞楼。\\n峡坼云霾龙虎卧，江清日抱鼋鼍游。\\n扶桑西枝对断石，弱水东影随长流。\\n杖藜叹世者谁子，泣血迸空回白头。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1056', 'poem', '唐', '于武陵', '劝酒', '劝君金屈卮，满酌不须辞。\\n花发多风雨，人生足别离。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1057', 'poem', '唐', '李益', '汴河曲', '汴水东流无限春，隋家宫阙已成尘。\\n行人莫上长堤望，风起杨花愁杀人。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1058', 'poem', '唐', '柳宗元', '酬曹侍御过象县见寄', '破额山前碧玉流，骚人遥驻木兰舟。\\n春风无限潇湘意，欲采蘋花不自由。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1059', 'poem', '唐', '贾岛', '送邹明府游灵武', '曾宰西畿县，三年马不肥。\\n债多平剑与，官满载书归。\\n边雪藏行径，林风透卧衣。\\n灵州听晓角，客馆未开扉。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1060', 'poem', '唐', '贾岛', '送天台僧', '远梦归华顶，扁舟背岳阳。\\n寒蔬修净食，夜浪动禅床。\\n雁过孤峰晓，猿啼一树霜。\\n身心无别念，馀习在诗章。', '2018-03-09 15:02:21');
INSERT INTO `t_poem` VALUES ('1061', 'poem', '唐', '李颀', '望秦川·秦川朝望迥', '秦川朝望迥，日出正东峰。\\n远近山河净，逶迤城阙重。\\n秋声万户竹，寒色五陵松。\\n客有归欤叹，凄其霜露浓。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1062', 'poem', '唐', '杜荀鹤', '山中寡妇 / 时世行', '夫因兵死守蓬茅，麻苎衣衫鬓发焦。\\n桑柘废来犹纳税，田园荒后尚征苗。\\n时挑野菜和根煮，旋斫生柴带叶烧。\\n任是深山更深处，也应无计避征徭。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1063', 'poem', '唐', '孟郊', '古怨别', '飒飒秋风生，愁人怨离别。\\n含情两相向，欲语气先咽。\\n心曲千万端，悲来却难说。\\n别后唯所思，天涯共明月。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1064', 'poem', '唐', '骆宾王', '浮槎', '昔负千寻质，高临九仞峰。\\n真心凌晚桂，劲节掩寒松。\\n忽值风飙折，坐为波浪冲。\\n摧残空有恨，拥肿遂无庸。\\n渤海三千里，泥沙几万重。\\n似舟飘不定，如梗泛何从。\\n仙客终难托，良工岂易逢。\\n徒怀万乘器，谁为一先容。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1065', 'poem', '唐', '杜甫', '秦州杂诗二十首', '满目悲生事，因人作远游。迟回度陇怯，浩荡及关愁。\\n水落鱼龙夜，山空鸟鼠秋。西征问烽火，心折此淹留。\\n秦州山北寺，胜迹隗嚣宫。苔藓山门古，丹青野殿空。\\n月明垂叶露，云逐渡溪风。清渭无情极，愁时独向东。\\n州图领同谷，驿道出流沙。降虏兼千帐，居人有万家。\\n马骄珠汗落，胡舞白蹄斜。年少临洮子，西来亦自夸。\\n鼓角缘边郡，川原欲夜时。秋听殷地发，风散入云悲。\\n抱叶寒蝉静，归来独鸟迟。万方声一概，吾道竟何之。\\n南使宜天马，由来万匹强。浮云连阵没，秋草遍山长。\\n闻说真龙种，仍残老骕骦，哀鸣思战斗，迥立向苍苍。\\n城上胡笳奏，山边汉节归。防河赴沧海，奉诏发金微。\\n士苦形骸黑，旌疏鸟兽稀。那闻往来戍，恨解邺城围。\\n莽莽万重山，孤城山谷间。无风云出塞，不夜月临关。\\n属国归何晚，楼兰斩未还。烟尘独长望，衰飒正摧颜。\\n闻道寻源使，从天此路回。牵牛去几许，宛马至今来。\\n一望幽燕隔，何时郡国开。东征健儿尽，羌笛暮吹哀。\\n今日明人眼，临池好驿亭。丛篁低地碧，高柳半天青。\\n稠叠多幽事，喧呼阅使星。老夫如有此，不异在郊坰，\\n云气接昆仑，涔涔塞雨繁。羌童看渭水，使客向河源。\\n烟火军中幕，牛羊岭上村。所居秋草净，正闭小蓬门。\\n萧萧古塞冷，漠漠秋云低。黄鹄翅垂雨，苍鹰饥啄泥。\\n蓟门谁自北，汉将独征西。不意书生耳，临衰厌鼓鼙。\\n山头南郭寺，水号北流泉。老树空庭得，清渠一邑传。\\n秋花危石底，晚景卧钟边。俯仰悲身世，溪风为飒然。\\n传道东柯谷，深藏数十家。对门藤盖瓦，映竹水穿沙。\\n瘦地翻宜粟，阳坡可种瓜。船人近相报，但恐失桃花。\\n万古仇池穴，潜通小有天。神鱼人不见，福地语真传。\\n近接西南境，长怀十九泉。何时一茅屋，送老白云边。\\n未暇泛沧海，悠悠兵马间。塞门风落木，客舍雨连山。\\n阮籍行多兴，庞公隐不还。东柯遂疏懒，休镊鬓毛斑。\\n东柯好崖谷，不与众峰群。落日邀双鸟，晴天养片云。\\n野人矜险绝，水竹会平分。采药吾将老，儿童未遣闻。\\n边秋阴易久，不复辨晨光。檐雨乱淋幔，山云低度墙。\\n鸬鹚窥浅井，蚯蚓上深堂。车马何萧索，门前百草长。\\n地僻秋将尽，山高客未归。塞云多断续，边日少光辉。\\n警急烽常报，传闻檄屡飞。西戎外甥国，何得迕天威。\\n凤林戈未息，鱼海路常难。候火云烽峻，悬军幕井干。\\n风连西极动，月过北庭寒。故老思飞将，何时议筑坛。\\n唐尧真自圣，野老复何知。晒药能无妇，应门幸有儿。\\n藏书闻禹穴，读记忆仇池。为报鸳行旧，鹪鹩在一枝。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1066', 'poem', '唐', '卢仝', '风中琴', '五音六律十三徽，龙吟鹤响思庖羲。\\n一弹流水一弹月，水月风生松树枝。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1067', 'poem', '唐', '李商隐', '马嵬二首', '冀马燕犀动地来，自埋红粉自成灰。\\n君王若道能倾国，玉辇何由过马嵬。\\n海外徒闻更九州，他生未卜此生休。\\n空闻虎旅传宵柝，无复鸡人报晓筹。\\n此日六军同驻马，当时七夕笑牵牛。\\n如何四纪为天子，不及卢家有莫愁。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1068', 'poem', '唐', '栖蟾', '牧童', '牛得自由骑，春风细雨飞。\\n青山青草里，一笛一蓑衣。\\n日出唱歌去，月明抚掌归。\\n何人得似尔，无是亦无非。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1069', 'poem', '唐', '李益', '竹窗闻风寄苗发司空曙', '微风惊暮坐，临牖思悠哉。\\n开门复动竹，疑是故人来。\\n时滴枝上露，稍沾阶下苔。\\n何当一入幌，为拂绿琴埃。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1070', 'poem', '唐', '李白', '结客少年场行', '紫燕黄金瞳，啾啾摇绿騣。\\n平明相驰逐，结客洛门东。\\n少年学剑术，凌轹白猿公。\\n珠袍曳锦带，匕首插吴鸿。\\n由来万夫勇，挟此生雄风。\\n托交从剧孟，买醉入新丰。\\n笑尽一杯酒，杀人都市中。\\n羞道易水寒，从令日贯虹。\\n燕丹事不立，虚没秦帝宫。\\n舞阳死灰人，安可与成功。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1071', 'poem', '唐', '杨巨源', '折杨柳/和练秀才杨柳', '水边杨柳麹尘丝，立马烦君折一枝。\\n惟有春风最相惜，殷勤更向手中吹。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1072', 'poem', '唐', '李白', '清平调词三首其一', '云想衣裳花相容，春风拂槛露华浓。\\n若非群玉山头见，会向瑶台月下逢。\\n一枝秾艳露凝香，云雨巫山枉断肠。\\n借问汉宫谁得似，可怜飞燕倚新妆。\\n名花倾国两相欢，长得君王带笑看。\\n解释春风无限恨，沉香亭北倚阑干。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1073', 'poem', '唐', '孟浩然', '洛中访袁拾遗不遇', '洛阳访才子，江岭作流人。\\n闻说梅花早，何如北地春。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1074', 'poem', '唐', '于濆', '野蚕', '野蚕食青桑，吐丝亦成茧。\\n无功及生人，何异偷饱暖。\\n我愿均尔丝，化为寒者衣。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1075', 'poem', '唐', '胡令能', '观郑州崔郎中诸妓绣样 / 咏绣障', '日暮堂前花蕊娇，争拈小笔上床描。\\n绣成安向春园里，引得黄莺下柳条。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1076', 'poem', '唐', '贾岛', '望山', '南山三十里，不见逾一旬。\\n冒雨时立望，望之如朋亲。\\n虬龙一掬波，洗荡千万春。\\n日日雨不断，愁杀望山人。\\n天事不可长，劲风来如奔。\\n阴霪一以扫，浩翠写国门。\\n长安百万家，家家张屏新。\\n谁家最好山，我愿为其邻。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1077', 'poem', '唐', '白居易', '放言五首·其一', '朝真暮伪何人辨，古往今来底事无。\\n但爱臧生能诈圣，可知宁子解佯愚。\\n草萤有耀终非火，荷露虽团岂是珠。\\n不取燔柴兼照乘，可怜光彩亦何殊。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1078', 'poem', '唐', '李如璧', '明月', '三五月华流炯光，可怜怀归郢路长。逾江越汉津无梁，\\n遥遥永夜思茫茫。昭君失宠辞上宫，蛾眉婵娟卧毡穹。\\n胡人琵琶弹北风，汉家音信绝南鸿。昭君此时怨画工，\\n可怜明月光朣胧。节既秋兮天向寒，沅有漪兮湘有澜，\\n沅湘纠合淼漫漫。洛阳才子忆长安，可怜明月复团团。\\n逐臣恋主心愈恪，弃妻思君情不薄。已悲芳岁徒沦落，\\n复恐红颜坐销铄。可怜明月方照灼，向影倾身比葵藿。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1079', 'poem', '唐', '司空曙', '早夏寄元校书', '独游野径送芳菲，高竹林居接翠微。\\n绿岸草深虫入遍，青丛花尽蝶来稀。\\n珠荷荐果香寒簟，玉柄摇风满夏衣。\\n蓬荜永无车马到，更当斋夜忆玄晖。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1080', 'poem', '唐', '王勃', '别薛华', '送送多穷路，遑遑独问津。\\n悲凉千里道，凄断百年身。\\n心事同漂泊，生涯共苦辛。\\n无论去与住，俱是梦中人。', '2018-03-09 15:02:28');
INSERT INTO `t_poem` VALUES ('1081', 'poem', '唐', '岑参', '冬夕', '浩汗霜风刮天地，温泉火井无生意。\\n泽国龙蛇冻不伸，南山瘦柏消残翠。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1082', 'poem', '唐', '佚名', '郊庙歌辞·享太庙乐章·升和', '顾惟菲薄，纂历应期。\\n中外同轨，夷狄来思。\\n乐用崇德，礼以陈词。\\n夕惕若厉，钦奉弘基。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1083', 'poem', '唐', '李白', '战城南', '去年战桑干源，今年战葱河道。洗兵条支海上波，\\n放马天山雪中草。万里长征战，三军尽衰老。\\n匈奴以杀戮为耕作，古来唯见白骨黄沙田。\\n秦家筑城避胡处，汉家还有烽火然。烽火然不息，\\n征战无已时。野战格斗死，败马号鸣向天悲。\\n乌鸢啄人肠，衔飞上挂枯树枝。士卒涂草莽，\\n将军空尔为。乃知兵者是凶器，圣人不得已而用之。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1084', 'poem', '唐', '温庭筠', '送人东游', '荒戍落黄叶，浩然离故关。\\n高风汉阳渡，初日郢门山。\\n江上几人在，天涯孤棹还。\\n何当重相见，尊酒慰离颜。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1085', 'poem', '唐', '崔峒', '书情寄上苏州韦使君兼呈吴县李明府', '数年湖上谢浮名，竹杖纱巾遂性情。\\n云外有时逢寺宿，日西无事傍江行。\\n陶潜县里看花发，庾亮楼中对月明。\\n谁念献书来万里，君王深在九重城。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1086', 'poem', '唐', '白居易', '和元八侍御升平新居四绝句·高亭', '亭脊太高君莫拆，东家留取当西山。\\n好看落日斜衔处，一片春岚映半环。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1087', 'poem', '唐', '窦巩', '襄阳寒食寄宇文籍', '烟水初销见万家，东风吹柳万条斜。\\n大堤欲上谁相伴，马踏春泥半是花。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1088', 'poem', '唐', '李白', '句', '眼前有景道不得，崔颢题诗在上头。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1089', 'poem', '唐', '佚名', '杂诗', '近寒食雨草萋萋，著麦苗风柳映堤。\\n等是有家归未得，杜鹃休向耳边啼。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1090', 'poem', '唐', '周朴', '春日秦国怀古', '荒郊一望欲消魂，泾水萦纡傍远村。\\n牛马放多春草尽，原田耕破古碑存。\\n云和积雪苍山晚，烟伴残阳绿树昏。\\n数里黄沙行客路，不堪回首思秦原。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1091', 'poem', '唐', '柳宗元', '夏昼偶作', '南州溽暑醉如酒，隐几熟眠开北牖。\\n日午独觉无馀声，山童隔竹敲茶臼。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1092', 'poem', '唐', '赵嘏', '寒塘', '晓发梳临水，寒塘坐见秋。\\n乡心正无限，一雁度南楼。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1093', 'poem', '唐', '李白', '乌夜啼·黄云城边乌欲栖', '黄云城边乌欲栖，归飞哑哑枝上啼。\\n机中织锦秦川女，碧纱如烟隔窗语。\\n停梭怅然忆远人，独宿孤房泪如雨。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1094', 'poem', '唐', '丁居晦', '琢玉', '卞玉何时献，初疑尚在荆。\\n琢来闻制器，价衒胜连城。\\n虹气冲天白，云浮入信贞。\\n珮为廉节德，杯作侈奢名。\\n露璞方期辨，雕文幸既成。\\n他山岂无石，宁及此时呈。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1095', 'poem', '唐', '杜甫', '后出塞五首', '男儿生世间，及壮当封侯。\\n战伐有功业，焉能守旧丘。\\n召募赴蓟门，军动不可留。\\n千金买马鞭，百金装刀头。\\n闾里送我行，亲戚拥道周。\\n斑白居上列，酒酣进庶羞。\\n少年别有赠，含笑看吴钩。\\n朝进东门营，暮上河阳桥。\\n落日照大旗，马鸣风萧萧。\\n平沙列万幕，部伍各见招。\\n中天悬明月，令严夜寂寥。\\n悲笳数声动，壮士惨不骄。\\n借问大将谁，恐是霍嫖姚。\\n古人重守边，今人重高勋。\\n岂知英雄主，出师亘长云。\\n六合已一家，四夷且孤军。\\n遂使貔虎士，奋身勇所闻。\\n拔剑击大荒，日收胡马群。\\n誓开玄冥北，持以奉吾君。\\n献凯日继踵，两蕃静无虞。\\n渔阳豪侠地，击鼓吹笙竽。\\n云帆转辽海，粳稻来东吴。\\n越罗与楚练，照耀舆台躯。\\n主将位益崇，气骄凌上都。\\n边人不敢议，议者死路衢。\\n我本良家子，出师亦多门。\\n将骄益愁思，身贵不足论。\\n跃马二十年，恐辜明主恩。\\n坐见幽州骑，长驱河洛昏。\\n中夜间道归，故里但空村。\\n恶名幸脱免，穷老无儿孙。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1096', 'poem', '唐', '刘长卿', '自夏口至鹦鹉洲夕望岳阳寄源中丞', '汀洲无浪复无烟，楚客相思益渺然。\\n汉口夕阳斜渡鸟，洞庭秋水远连天。\\n孤城背岭寒吹角，独戍临江夜泊船。\\n贾谊上书忧汉室，长沙谪去古今怜。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1097', 'poem', '唐', '杜甫', '端午日赐衣', '宫衣亦有名，端午被恩荣。\\n细葛含风软，香罗叠雪轻。\\n自天题处湿，当暑著来清。\\n意内称长短，终身荷圣情。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1098', 'poem', '唐', '刘禹锡', '台城', '台城六代竞豪华，结绮临春事最奢。\\n万户千门成野草，只缘一曲后庭花。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1099', 'poem', '唐', '戴叔伦', '塞上曲二首·其二', '汉家旌帜满阴山，不遣胡儿匹马还。\\n愿得此身长报国，何须生入玉门关。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1100', 'poem', '唐', '白居易', '池上二绝', '山僧对棋坐，局上竹阴清。\\n映竹无人见，时闻下子声。\\n小娃撑小艇，偷采白莲回。\\n不解藏踪迹，浮萍一道开。', '2018-03-09 15:02:34');
INSERT INTO `t_poem` VALUES ('1101', 'poem', '唐', '权德舆', '夏至日作', '璿枢无停运，四序相错行。\\n寄言赫曦景，今日一阴生。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1102', 'poem', '唐', '李忱', '吊白居易', '缀玉联珠六十年，谁教冥路作诗仙。\\n浮云不系名居易，造化无为字乐天。\\n童子解吟长恨曲，胡儿能唱琵琶篇。\\n文章已满行人耳，一度思卿一怆然。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1103', 'poem', '唐', '李白', '渌水曲', '渌水明秋月，南湖采白蘋。\\n荷花娇欲语，愁杀荡舟人。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1104', 'poem', '唐', '李贺', '马诗二十三首·其八', '赤兔无人用，当须吕布骑。\\n吾闻果下马，羁策任蛮儿。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1105', 'poem', '唐', '韩愈', '八月十五夜赠张功曹', '纤云四卷天无河，清风吹空月舒波。\\n沙平水息声影绝，一杯相属君当歌。\\n君歌声酸辞且苦，不能听终泪如雨。\\n洞庭连天九疑高，蛟龙出没猩鼯号。\\n十生九死到官所，幽居默默如藏逃。\\n下床畏蛇食畏药，海气湿蛰熏腥臊。\\n昨者州前捶大鼓，嗣皇继圣登夔皋。\\n赦书一日行万里，罪从大辟皆除死。\\n迁者追回流者还，涤瑕荡垢清朝班。\\n州家申名使家抑，坎轲只得移荆蛮。\\n判司卑官不堪说，未免捶楚尘埃间。\\n同时辈流多上道，天路幽险难追攀。\\n君歌且休听我歌，我歌今与君殊科。\\n一年明月今宵多，人生由命非由他。\\n有酒不饮奈明何。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1106', 'poem', '唐', '李商隐', '春风', '春风虽自好，春物太昌昌。\\n若教春有意，惟遣一枝芳。\\n我意殊春意，先春已断肠。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1107', 'poem', '唐', '白居易', '秦中吟十首。歌舞（一作伤阌乡县囚）', '秦中岁云暮，大雪满皇州。\\n雪中退朝者，朱紫尽公侯。\\n贵有风雪兴，富无饥寒忧。\\n所营唯第宅，所务在追游。\\n朱门车马客，红烛歌舞楼。\\n欢酣促密坐，醉暖脱重裘。\\n秋官为主人，廷尉居上头。\\n日中为一乐，夜半不能休。\\n岂知阌乡狱，中有冻死囚。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1108', 'poem', '唐', '贾岛', '寄韩潮州愈', '此心曾与木兰舟，直到天南潮水头。\\n隔岭篇章来华岳，出关书信过泷流。\\n峰悬驿路残云断，海浸城根老树秋。\\n一夕瘴烟风卷尽，月明初上浪西楼。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1109', 'poem', '唐', '贾至', '春思二首·其一', '草色青青柳色黄，桃花历乱李花香。\\n东风不为吹愁去，春日偏能惹恨长。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1110', 'poem', '唐', '孟浩然', '初秋', '不觉初秋夜渐长，清风习习重凄凉。\\n炎炎暑退茅斋静，阶下丛莎有露光。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1111', 'poem', '唐', '欧阳炯', '浣溪沙·落絮残莺半日天', '落絮残莺半日天，玉柔花醉只思眠，惹窗映竹满炉烟。\\n独掩画屏愁不语，斜欹瑶枕髻鬟偏，此时心在阿谁边？\\n天碧罗衣拂地垂，美人初著更相宜，宛风如舞透香肌。\\n独坐含嚬吹凤竹，园中缓步折花枝，有情无力泥人时。\\n相见休言有泪珠，酒阑重得叙欢娱，凤屏鸳枕宿金铺。\\n兰麝细香闻喘息，绮罗纤缕见肌肤，此时还恨薄情无。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1112', 'poem', '唐', '雍裕之', '农家望晴', '尝闻秦地西风雨，为问西风早晚回。\\n白发老农如鹤立，麦场高处望云开。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1113', 'poem', '唐', '卢延让', '苦吟', '莫话诗中事，诗中难更无。\\n吟安一个字，捻断数茎须。\\n险觅天应闷，狂搜海亦枯。\\n不同文赋易，为著者之乎。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1114', 'poem', '唐', '王昌龄', '听流人水调子', '孤舟微月对枫林，分付鸣筝与客心。\\n岭色千重万重雨，断弦收与泪痕深。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1115', 'poem', '唐', '张孜', '雪诗', '长安大雪天，鸟雀难相觅。\\n其中豪贵家，捣椒泥四壁。\\n到处爇红炉，周回下罗幂。\\n暖手调金丝，蘸甲斟琼液。\\n醉唱玉尘飞，困融香汗滴。\\n岂知饥寒人，手脚生皴劈。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1116', 'poem', '唐', '李商隐', '潭州', '潭州官舍暮楼空，今古无端入望中。\\n湘泪浅深滋竹色，楚歌重叠怨兰丛。\\n陶公战舰空滩雨，贾傅承尘破庙风。\\n目断故园人不至，松醪一醉与谁同。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1117', 'poem', '唐', '杜甫', '逃难', '五十头白翁，南北逃世难。\\n疏布缠枯骨，奔走苦不暖。\\n已衰病方入，四海一涂炭。\\n乾坤万里内，莫见容身畔。\\n妻孥复随我，回首共悲叹。\\n故国莽丘墟，邻里各分散。\\n归路从此迷，涕尽湘江岸。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1118', 'poem', '唐', '罗隐', '咏月', '湖上风高动白蘋，暂延清景此逡巡。\\n隔年违别成何事，半夜相看似故人。\\n蟾向静中矜爪距，兔隈明处弄精神。\\n嫦娥老大应惆怅，倚泣苍苍桂一轮。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1119', 'poem', '唐', '寒山', '三字诗六首', '寒山道，无人到。\\n若能行，称十号。\\n有蝉鸣，无鸦噪。\\n黄叶落，白云扫。\\n石磊磊，山隩隩。\\n我独居，名善导。\\n子细看，何相好。\\n寒山寒，冰锁石。\\n藏山青，现雪白。\\n日出照，一时释。\\n从兹暖，养老客。\\n我居山，勿人识。\\n白云中，常寂寂。\\n寒山深，称我心。\\n纯白石，勿黄金。\\n泉声响，抚伯琴。\\n有子期，辨此音。\\n重岩中，足清风。\\n扇不摇，凉冷通。\\n明月照，白云笼。\\n独自坐，一老翁。\\n寒山子，长如是。\\n独自居，不生死。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1120', 'poem', '唐', '秦韬玉', '亭台', '雕楹累栋架崔嵬，院宇生烟次第开。\\n为向西窗添月色，岂辞南海取花栽。\\n意将画地成幽沼，势拟驱山近小台。\\n清境渐深官转重，春时长是别人来。', '2018-03-09 15:02:39');
INSERT INTO `t_poem` VALUES ('1121', 'poem', '唐', '李中', '春晓', '残烛犹存月尚明，几家帏幌梦魂惊。\\n星河渐没行人动，历历林梢百舌声。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1122', 'poem', '唐', '韩愈', '湘中', '猿愁鱼踊水翻波，自古流传是汨罗。\\n蘋藻满盘无处奠，空闻渔父扣舷歌。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1123', 'poem', '唐', '白居易', '途中寒食', '路旁寒食行人尽，独占春愁在路旁。\\n马上垂鞭愁不语，风吹百草野田香。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1124', 'poem', '唐', '杜甫', '悲陈陶', '孟冬十郡良家子，血作陈陶泽中水。\\n野旷天清无战声，四万义军同日死。\\n群胡归来血洗箭，仍唱胡歌饮都市。\\n都人回面向北啼，日夜更望官军至。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1125', 'poem', '唐', '王绩', '秋夜喜遇王处士', '北场芸藿罢，东皋刈黍归。\\n相逢秋月满，更值夜萤飞。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1126', 'poem', '唐', '杜甫', '雨不绝', '鸣雨既过渐细微，映空摇飏如丝飞。\\n阶前短草泥不乱，院里长条风乍稀。\\n舞石旋应将乳子，行云莫自湿仙衣。\\n眼边江舸何匆促，未待安流逆浪归。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1127', 'poem', '唐', '萧遘', '春诗', '南国韶光早，春风送腊来。\\n水堤烟报柳，山寺雪惊梅。\\n练色铺江晚，潮声逐渚回。\\n青旗问沽酒，何处拨寒醅。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1128', 'poem', '唐', '吴融', '红白牡丹', '不必繁弦不必歌，静中相对更情多。\\n殷鲜一半霞分绮，洁澈旁边月飐波。\\n看久愿成庄叟梦，惜留须倩鲁阳戈。\\n重来应共今来别，风堕香残衬绿莎。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1129', 'poem', '唐', '张籍', '忆远', '行人犹未有归期，万里初程日暮时。\\n唯爱门前双柳树，枝枝叶叶不相离。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1130', 'poem', '唐', '韦庄', '长安清明', '蚤是伤春梦雨天，可堪芳草更芊芊。\\n内官初赐清明火，上相闲分白打钱。\\n紫陌乱嘶红叱拨，绿杨高映画秋千。\\n游人记得承平事，暗喜风光似昔年。', '2018-03-09 15:02:44');
INSERT INTO `t_poem` VALUES ('1131', 'poem', '唐', '储光羲', '题陆山人楼', '暮声杂初雁，夜色涵早秋。\\n独见海中月，照君池上楼。\\n山云拂高栋，天汉入云流。\\n不惜朝光满，其如千里游。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1132', 'poem', '唐', '司空曙', '送人游岭南', '万里南游客，交州见柳条。\\n逢迎人易合，时日酒能消。\\n浪晓浮青雀，风温解黑貂。\\n囊金如未足，莫恨故乡遥。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1133', 'poem', '唐', '李隆基', '平胡', '杂虏忽猖狂，无何敢乱常。\\n羽书朝继入，烽火夜相望。\\n将出凶门勇，兵因死地强。\\n蒙轮皆突骑，按剑尽鹰扬。\\n鼓角雄山野，龙蛇入战场。\\n流膏润沙漠，溅血染锋铓。\\n雾扫清玄塞，云开静朔方。\\n武功今已立，文德愧前王。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1134', 'poem', '唐', '王维', '春日与裴迪过新昌里访吕逸人不遇', '桃源一向绝风尘，柳市南头访隐沦。\\n到门不敢题凡鸟，看竹何须问主人。\\n城上青山如屋里，东家流水入西邻。\\n闭户著书多岁月，种松皆老作龙鳞。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1135', 'poem', '唐', '韩愈', '晚春二首·其二', '谁收春色将归去，慢绿妖红半不存。\\n榆荚只能随柳絮，等闲撩乱走空园。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1136', 'poem', '唐', '方干', '浅井', '夜入明河星似少，曙摇澄碧扇风翻。\\n细泉细脉难来到，应觉添瓶耗旧痕。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1137', 'poem', '唐', '杜甫', '滟滪', '滟滪既没孤根深，西来水多愁太阴。\\n江天漠漠鸟双去，风雨时时龙一吟。\\n舟人渔子歌回首，估客胡商泪满襟。\\n寄语舟航恶年少，休翻盐井横黄金。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1138', 'poem', '唐', '李白', '赠友人三首', '兰生不当户，别是闲庭草。\\n夙被霜露欺，红荣已先老。\\n谬接瑶华枝，结根君王池。\\n顾无馨香美，叨沐清风吹。\\n馀芳若可佩，卒岁长相随。\\n袖中赵匕首，买自徐夫人。\\n玉匣闭霜雪，经燕复历秦。\\n其事竟不捷，沦落归沙尘。\\n持此愿投赠，与君同急难。\\n荆卿一去后，壮士多摧残。\\n长号易水上，为我扬波澜。\\n凿井当及泉，张帆当济川。\\n廉夫唯重义，骏马不劳鞭。\\n人生贵相知，何必金与钱。\\n慢世薄功业，非无胸中画。\\n谑浪万古贤，以为儿童剧。\\n立产如广费，匡君怀长策。\\n但苦山北寒，谁知道南宅。\\n岁酒上逐风，霜鬓两边白。\\n蜀主思孔明，晋家望安石。\\n时人列五鼎，谈笑期一掷。\\n虎伏被胡尘，渔歌游海滨。\\n弊裘耻妻嫂，长剑托交亲。\\n夫子秉家义，群公难与邻。\\n莫持西江水，空许东溟臣。\\n他日青云去，黄金报主人。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1139', 'poem', '唐', '温庭筠', '赠少年', '江海相逢客恨多，秋风叶下洞庭波。\\n酒酣夜别淮阴市，月照高楼一曲歌。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1140', 'poem', '唐', '汪遵', '息国', '家国兴亡身独存，玉容还受楚王恩。\\n衔冤只合甘先死，何待花间不肯言。', '2018-03-09 15:02:45');
INSERT INTO `t_poem` VALUES ('1141', 'poem', '唐', '李益', '夜宴观石将军舞', '微月东南上戍楼，琵琶起舞锦缠头。\\n更闻横笛关山远，白草胡沙西塞秋。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1142', 'poem', '唐', '吴融', '阌乡寓居十首·小径', '碍竹妨花一径幽，攀援可到玉峰头。\\n若教须作康庄好，更有高车驷马忧。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1143', 'poem', '唐', '杜牧', '题宣州开元寺水阁阁下宛溪夹溪居人', '六朝文物草连空，天淡云闲今古同。\\n鸟去鸟来山色里，人歌人哭水声中。\\n深秋帘幕千家雨，落日楼台一笛风。\\n惆怅无日见范蠡，参差烟树五湖东。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1144', 'poem', '唐', '杜荀鹤', '闽中秋思', '雨匀紫菊丛丛色，风弄红蕉叶叶声。\\n北畔是山南畔海，只堪图画不堪行。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1145', 'poem', '唐', '白居易', '舟中读元九诗', '把君诗卷灯前读，诗尽灯残天未明。\\n眼痛灭灯犹暗坐，逆风吹浪打船声。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1146', 'poem', '唐', '蜀宫群仙', '太真', '春梦悠扬生下界，一堪成笑一堪悲。\\n马嵬不是无情地，自遇蓬莱睡觉时。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1147', 'poem', '唐', '白居易', '秋夕', '叶声落如雨，月色白似霜。\\n夜深方独卧，谁为拂尘床。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1148', 'poem', '唐', '王昌龄', '长信怨', '金井梧桐秋叶黄，珠帘不卷夜来霜。\\n熏笼玉枕无颜色，卧听南宫清漏长。\\n奉帚平明金殿开，暂将团扇共裴回。\\n玉颜不及寒鸦色，犹带昭阳日影来。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1149', 'poem', '唐', '高适', '听张立本女吟', '危冠广袖楚宫妆，独步闲庭逐夜凉。\\n自把玉钗敲砌竹，清歌一曲月如霜。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1150', 'poem', '唐', '王维', '奉和圣制从蓬莱向兴庆阁道中留春雨中春望之作应制', '渭水自萦秦塞曲，黄山旧绕汉宫斜。\\n銮舆迥出千门柳，阁道回看上苑花。\\n云里帝城双凤阙，雨中春树万人家。\\n为乘阳气行时令，不是宸游玩物华。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1151', 'poem', '唐', '胡曾', '咏史诗·长城', '祖舜宗尧自太平，秦皇何事苦苍生。\\n不知祸起萧墙内，虚筑防胡万里城。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1152', 'poem', '唐', '贺知章', '采莲曲', '稽山罢雾郁嵯峨，镜水无风也自波。\\n莫言春度芳菲尽，别有中流采芰荷。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1153', 'poem', '唐', '郑谷', '杏花', '不学梅欺雪，轻红照碧池。\\n小桃新谢后，双燕却来时。\\n香属登龙客，烟笼宿蝶枝。\\n临轩须貌取，风雨易离披。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1154', 'poem', '唐', '韩琮', '咏马', '曾经伯乐识长鸣，不似龙行不敢行。\\n金埒未登嘶若是，盐车犹驾瘦何惊。\\n难逢王济知音癖，欲就燕昭买骏名。\\n早晚飞黄引同皂，碧云天上作鸾鸣。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1155', 'poem', '唐', '汪遵', '西河', '花貌年年溺水滨，俗传河伯娶生人。\\n自从明宰投巫后，直至如今鬼不神。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1156', 'poem', '唐', '许浑', '夏日寄江上亲友', '雨过前山日未斜，清蝉嘒嘒落槐花。\\n车轮南北已无限，江上故人才到家。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1157', 'poem', '唐', '骆宾王', '送别', '寒更承夜永，凉夕向秋澄。\\n离心何以赠，自有玉壶冰。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1158', 'poem', '唐', '杜甫', '历历', '历历开元事，分明在眼前。\\n无端盗贼起，忽已岁时迁。\\n巫峡西江外，秦城北斗边。\\n为郎从白首，卧病数秋天。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1159', 'poem', '唐', '韩愈', '杏花', '居邻北郭古寺空，杏花两株能白红。\\n曲江满园不可到，看此宁避雨与风。\\n二年流窜出岭外，所见草木多异同。\\n冬寒不严地恒泄，阳气发乱无全功。\\n浮花浪蕊镇长有，才开还落瘴雾中。\\n山榴踯躅少意思，照耀黄紫徒为丛。\\n鹧鸪钩輈猿叫歇，杳杳深谷攒青枫。\\n岂如此树一来玩，若在京国情何穷。\\n今旦胡为忽惆怅，万片飘泊随西东。\\n明年更发应更好，道人莫忘邻家翁。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1160', 'poem', '唐', '王昌龄', '塞上曲·其一', '蝉鸣空桑林，八月萧关道。\\n出塞入塞寒，处处黄芦草。\\n从来幽并客，皆共沙尘老。\\n莫学游侠儿，矜夸紫骝好。', '2018-03-09 15:02:52');
INSERT INTO `t_poem` VALUES ('1161', 'poem', '唐', '姚合', '游终南山', '策杖度溪桥，云深步数劳。\\n青猿吟岭际，白鹤坐松梢。\\n天外浮烟远，山根野水交。\\n自缘名利系，好此结蓬茆。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1162', 'poem', '唐', '卢纶', '和张仆射塞下曲·其一', '鹫翎金仆姑，燕尾绣蝥弧。\\n独立扬新令，千营共一呼。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1163', 'poem', '唐', '李白', '折荷有赠', '涉江玩秋水，爱此红蕖鲜。\\n攀荷弄其珠，荡漾不成圆。\\n佳人彩云里，欲赠隔远天。\\n相思无因见，怅望凉风前。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1164', 'poem', '唐', '雍陶', '题君山 / 洞庭诗', '风波不动影沈沈，翠色全微碧色深。\\n疑是水仙梳洗处，一螺青黛镜中心。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1165', 'poem', '唐', '岑参', '凉州馆中与诸判官夜集', '弯弯月出挂城头，城头月出照凉州。\\n凉州七里十万家，胡人半解弹琵琶。\\n琵琶一曲肠堪断，风萧萧兮夜漫漫。\\n河西幕中多故人，故人别来三五春。\\n花门楼前见秋草，岂能贫贱相看老。\\n一生大笑能几回，斗酒相逢须醉倒。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1166', 'poem', '唐', '李商隐', '代赠二首·其一', '楼上黄昏欲望休，玉梯横绝月如钩。\\n芭蕉不展丁香结，同向春风各自愁。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1167', 'poem', '唐', '岑参', '武威送刘判官赴碛西行军', '火山五月行人少，看君马去疾如鸟。\\n都护行营太白西，角声一动胡天晓。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1168', 'poem', '唐', '刘言史', '寻花', '游春未足春将度，访紫寻红少在家。\\n借问流莺与飞蝶，更知何处有幽花。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1169', 'poem', '唐', '李白', '望庐山瀑布水二首', '西登香炉峰，南见瀑布水。挂流三百丈，喷壑数十里。\\n欻如飞电来，隐若白虹起。初惊河汉落，半洒云天里。\\n仰观势转雄，壮哉造化功。海风吹不断，江月照还空。\\n空中乱潈射，左右洗青壁。飞珠散轻霞，流沫沸穹石。\\n而我乐名山，对之心益闲。无论漱琼液，还得洗尘颜。\\n且谐宿所好，永愿辞人间。\\n日照香炉生紫烟，遥看瀑布挂前川。\\n飞流直下三千尺，疑是银河落九天。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1170', 'poem', '唐', '杜甫', '十月一日', '有瘴非全歇，为冬亦不难。\\n夜郎溪日暖，白帝峡风寒。\\n蒸裹如千室，焦糟幸一柈。\\n兹辰南国重，旧俗自相欢。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1171', 'poem', '唐', '温庭筠', '过陈琳墓', '曾于青史见遗文，今日飘蓬过此坟。\\n词客有灵应识我，霸才无主独怜君。\\n石麟埋没藏春草，铜雀荒凉对暮云。\\n莫怪临风倍惆怅，欲将书剑学从军。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1172', 'poem', '唐', '白居易', '母别子', '母别子，子别母，白日无光哭声苦。\\n关西骠骑大将军，去年破虏新策勋。\\n敕赐金钱二百万，洛阳迎得如花人。\\n新人迎来旧人弃，掌上莲花眼中刺。\\n迎新弃旧未足悲，悲在君家留两儿。\\n一始扶行一初坐，坐啼行哭牵人衣。\\n以汝夫妇新燕婉，使我母子生别离。\\n不如林中乌与鹊，母不失雏雄伴雌。\\n应似园中桃李树，花落随风子在枝。\\n新人新人听我语，洛阳无限红楼女。\\n但愿将军重立功，更有新人胜于汝。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1173', 'poem', '唐', '栖白', '八月十五夜玩月', '寻常三五夜，不是不婵娟。\\n及至中秋满，还胜别夜圆。\\n清光凝有露，皓魄爽无烟。\\n自古人皆望，年来又一年。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1174', 'poem', '唐', '李贤', '黄台瓜辞', '种瓜黄台下，瓜熟子离离。\\n一摘使瓜好，再摘令瓜稀。\\n三摘尚自可，摘绝抱蔓归。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1175', 'poem', '唐', '顾况', '游子吟', '故枥思疲马，故窠思迷禽。\\n浮云蔽我乡，踯躅游子吟。\\n游子悲久滞，浮云郁东岑。\\n客堂无丝桐，落叶如秋霖。\\n艰哉远游子，所以悲滞淫。\\n一为浮云词，愤塞谁能禁。\\n驰归百年内，唯愿展所钦。\\n胡为不归欤，坐使年病侵。\\n未老霜绕鬓，非狂火烧心。\\n太行何难哉，北斗不可斟。\\n夜静星河出，耿耿辰与参。\\n佳人夐青天，尺素重于金。\\n泬寥群动异，眇默诸境森。\\n苔衣上闲阶，蟋蟀催寒砧。\\n立身计几误，道险无容针。\\n三年不还家，万里遗锦衾。\\n梦魂无重阻，离忧罔古今。\\n胡为不归欤，辜负匣中琴。\\n腰下是何物，牵缠旷登寻。\\n朝与名山期，夕宿楚水阴。\\n楚水殊演漾，名山窅岖嶔。\\n客从洞庭来，婉娈潇湘深。\\n橘柚在南国，鸿雁遗秋音。\\n下有碧草洲，上有青橘林。\\n引烛窥洞穴，凌波睥天琛。\\n蒲荷影参差，凫鹤雏淋涔。\\n浩歌惜芳杜，散发轻华簪。\\n胡为不归欤，泪下沾衣襟。\\n鸢飞戾霄汉，蝼蚁制鳣鱏。\\n赫赫大圣朝，日月光照临。\\n圣主虽启迪，奇人分湮沈。\\n层城登云韶，王府锵球琳。\\n鹿鸣志丰草，况复虞人箴。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1176', 'poem', '唐', '孙昌胤', '清明', '清明暮春里，怅望北山陲。\\n燧火开新焰，桐花发故枝。\\n沈冥惭岁物，欢宴阻朋知。\\n不及林间鸟，迁乔并羽仪。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1177', 'poem', '唐', '辛弘智', '赋诗', '君为河边草，逢春心剩生。\\n妾如台上镜，得照始分明。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1178', 'poem', '唐', '李商隐', '日射', '日射纱窗风撼扉，香罗拭手春事违。\\n回廊四合掩寂寞，碧鹦鹉对红蔷薇。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1179', 'poem', '唐', '刘禹锡', '秋日送客至潜水驿', '候吏立沙际，田家连竹溪。\\n枫林社日鼓，茅屋午时鸡。\\n鹊噪晚禾地，蝶飞秋草畦。\\n驿楼宫树近，疲马再三嘶。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1180', 'poem', '唐', '贺知章', '回乡偶书', '离别家乡岁月多，近来人事半消磨。\\n惟有门前镜湖水，春风不改旧时波。', '2018-03-09 15:03:04');
INSERT INTO `t_poem` VALUES ('1181', 'poem', '唐', '陆龟蒙', '新沙', '渤澥声中涨小堤，官家知后海鸥知。\\n蓬莱有路教人到，应亦年年税紫芝。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1182', 'poem', '唐', '钱起', '送僧归日本', '上国随缘住，来途若梦行。\\n浮天沧海远，去世法舟轻。\\n水月通禅寂，鱼龙听梵声。\\n惟怜一灯影，万里眼中明。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1183', 'poem', '唐', '李益', '边思', '腰悬锦带佩吴钩，走马曾防玉塞秋。\\n莫笑关西将家子，只将诗思入凉州。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1184', 'poem', '唐', '谭用之', '秋宿湘江遇雨', '江上阴云锁梦魂，江边深夜舞刘琨。\\n秋风万里芙蓉国，暮雨千家薜荔村。\\n乡思不堪悲橘柚，旅游谁肯重王孙。\\n渔人相见不相问，长笛一声归岛门。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1185', 'poem', '唐', '李贺', '马诗二十三首·其一', '龙脊贴连钱，银蹄白踏烟。\\n无人织锦韂，谁为铸金鞭。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1186', 'poem', '唐', '李白', '哭宣城善酿纪叟', '纪叟黄泉里，还应酿老春。\\n夜台无晓日，沽酒与何人。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1187', 'poem', '唐', '薛涛', '寄张元夫', '前溪独立后溪行，鹭识朱衣自不惊。\\n借问人间愁寂意，伯牙弦绝已无声。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1188', 'poem', '唐', '杜甫', '又雪', '南雪不到地，青崖沾未消。\\n微微向日薄，脉脉去人遥。\\n冬热鸳鸯病，峡深豺虎骄。\\n愁边有江水，焉得北之朝。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1189', 'poem', '唐', '许浑', '鹭鸶', '西风澹澹水悠悠，雪点丝飘带雨愁。\\n何限归心倚前閤，绿蒲红蓼练塘秋。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1190', 'poem', '唐', '刘禹锡', '杂曲歌辞·浪淘沙', '九曲黄河万里沙，浪淘风簸自天涯。\\n如今直上银河去，同到牵牛织女家。\\n洛水桥边春日斜，碧流轻浅见琼沙。\\n无端陌上狂风急，惊起鸳鸯出浪花。\\n汴水东流虎眼文，清淮晓色鸭头春。\\n君看渡口淘沙处，渡却人间多少人。\\n鹦鹉洲头浪飐沙，青楼春望日将斜。\\n衔泥燕子争归舍，独自狂夫不忆家。\\n濯锦江边两岸花，春风吹浪正淘沙。\\n女郎剪下鸳鸯锦，将向中流匹晚霞。\\n日照澄洲江雾开，淘金女伴满江隈。\\n美人首饰侯王印，尽是沙中浪底来。\\n八月涛声吼地来，头高数丈触山回。\\n须臾却入海门去，卷起沙堆似雪堆。\\n莫道谗言如浪深，莫言迁客似沙沉。\\n千淘万漉虽辛苦，吹尽狂沙始到金。\\n流水淘沙不暂停，前波未灭后波生。\\n令人忽忆潇湘渚，回唱迎神三两声。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1191', 'poem', '唐', '白居易', '早春', '雪散因和气，冰开得暖光。\\n春销不得处，唯有鬓边霜。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1192', 'poem', '唐', '白居易', '秋游', '下马闲行伊水头，凉风清景胜春游。\\n何事古今诗句里，不多说著洛阳秋。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1193', 'poem', '唐', '韩愈', '枯树', '老树无枝叶，风霜不复侵。\\n腹穿人可过，皮剥蚁还寻。\\n寄托惟朝菌，依投绝暮禽。\\n犹堪持改火，未肯但空心。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1194', 'poem', '唐', '吕岩', '三字诀', '这个道，非常道。\\n性命根，生死窍。\\n说著丑，行着妙。\\n人人憎，个个笑。\\n大关键，在颠倒。\\n莫厌秽，莫计较。\\n得他来，立见效。\\n地天泰，为朕兆。\\n口对口，窍对窍。\\n吞入腹，自知道。\\n药苗新，先天兆。\\n审眉间，行逆道。\\n滓质物，自继绍。\\n二者馀，方绝妙。\\n要行持，令人叫。\\n气要坚，神莫耗。\\n若不行，空老耄。\\n认得真，老还少。\\n不知音，莫语要。\\n些儿法，合大道。\\n精气神，不老药。\\n静里全，明中报。\\n乘凤鸾，听天诏。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1195', 'poem', '唐', '温宪', '杏花', '团雪上晴梢，红明映碧寥。\\n店香风起夜，村白雨休朝。\\n静落犹和蒂，繁开正蔽条。\\n澹然闲赏久，无以破妖娆。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1196', 'poem', '唐', '李白', '谢公亭·盖谢脁范云之所游', '谢公离别处，风景每生愁。\\n客散青天月，山空碧水流。\\n池花春映日，窗竹夜鸣秋。\\n今古一相接，长歌怀旧游。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1197', 'poem', '唐', '李商隐', '蜂', '小苑华池烂熳通，后门前槛思无穷。\\n宓妃腰细才胜露，赵后身轻欲倚风。\\n红壁寂寥崖蜜尽，碧帘迢递雾巢空。\\n青陵粉蝶休离恨，长定相逢二月中。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1198', 'poem', '唐', '戎昱', '霁雪 / 韩舍人书窗残雪', '风卷寒云暮雪晴，江烟洗尽柳条轻。\\n檐前数片无人扫，又得书窗一夜明。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1199', 'poem', '唐', '杜甫', '恨别', '洛城一别四千里，胡骑长驱五六年。\\n草木变衰行剑外，兵戈阻绝老江边。\\n思家步月清宵立，忆弟看云白日眠。\\n闻道河阳近乘胜，司徒急为破幽燕。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1200', 'poem', '唐', '王昌龄', '龙标野宴', '沅溪夏晚足凉风，春酒相携就竹丛。\\n莫道弦歌愁远谪，青山明月不曾空。', '2018-03-09 15:03:12');
INSERT INTO `t_poem` VALUES ('1201', 'poem', '唐', '徐夤', '菊花', '桓景登高事可寻，黄花开处绿畦深。\\n消灾辟恶君须采，冷露寒霜我自禁。\\n篱物早荣还早谢，涧松同德复同心。\\n陶公岂是居贫者，剩有东篱万朵金。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1202', 'poem', '唐', '柳中庸', '江行', '繁阴乍隐洲，落叶初飞浦。\\n萧萧楚客帆，暮入寒江雨。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1203', 'poem', '唐', '张志和', '渔父', '八月九月芦花飞，南谿老人重钓归。\\n秋山入帘翠滴滴，野艇倚槛云依依。\\n却把渔竿寻小径，闲梳鹤发对斜晖。\\n翻嫌四皓曾多事，出为储皇定是非。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1204', 'poem', '唐', '贾岛', '积雪', '昔属时霖滞，今逢腊雪多。\\n南猜飘桂渚，北讶雨交河。\\n尽灭平芜色，弥重古木柯。\\n空中离白气，岛外下沧波。\\n隐者迷樵道，朝人冷玉珂。\\n夕繁仍昼密，漏间复钟和。\\n想积高嵩顶，新秋皎月过。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1205', 'poem', '唐', '韩偓', '咏柳', '褭雨拖风不自持，全身无力向人垂。\\n玉纤折得遥相赠，便似观音手里时。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1206', 'poem', '唐', '李白', '古风·其五十九', '碧荷生幽泉，朝日艳且鲜。\\n秋花冒绿水，密叶罗青烟。\\n秀色空绝世，馨香为谁传。\\n坐看飞霜满，凋此红芳年。\\n结根未得所，愿托华池边。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1207', 'poem', '唐', '雍陶', '城西访友人别墅', '澧水桥西小路斜，日高犹未到君家。\\n村园门巷多相似，处处春风枳壳花。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1208', 'poem', '唐', '鱼玄机', '愁思', '落叶纷纷暮雨和，朱丝独抚自清歌。\\n放情休恨无心友，养性空抛苦海波。\\n长者车音门外有，道家书卷枕前多。\\n布衣终作云霄客，绿水青山时一过。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1209', 'poem', '唐', '孟郊', '苦寒吟', '百泉冻皆咽，我吟寒更切。\\n半夜倚乔松，不觉满衣雪。\\n竹竿有甘苦，我爱抱苦节。\\n鸟声有悲欢，我爱口流血。\\n潘生若解吟，更早生白发。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1210', 'poem', '唐', '李商隐', '月夕', '草下阴虫叶上霜，朱栏迢递压湖光。\\n兔寒蟾冷桂花白，此夜姮娥应断肠。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1211', 'poem', '唐', '王维', '木兰柴', '秋山敛余照，飞鸟逐前侣。\\n彩翠时分明，夕岚无处所。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1212', 'poem', '唐', '杨敬之', '赠项斯', '几度见诗诗总好，及观标格过于诗。\\n平生不解藏人善，到处逢人说项斯。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1213', 'poem', '唐', '元稹', '寄赠薛涛', '锦江滑腻蛾眉秀，幻出文君与薛涛。\\n言语巧偷鹦鹉舌，文章分得凤凰毛。\\n纷纷辞客多停笔，个个公卿欲梦刀。\\n别后相思隔烟水，菖蒲花发五云高。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1214', 'poem', '唐', '鱼玄机', '暮春即事', '深巷穷门少侣俦，阮郎唯有梦中留。\\n香飘罗绮谁家席，风送歌声何处楼。\\n街近鼓鼙喧晓睡，庭闲鹊语乱春愁。\\n安能追逐人间事，万里身同不系舟。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1215', 'poem', '唐', '刘蕃', '状江南·季秋', '江南季秋天，栗熟大如拳。\\n枫叶红霞举，苍芦白浪川。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1216', 'poem', '唐', '李商隐', '离亭赋得折杨柳二首', '暂凭樽酒送无憀，莫损愁眉与细腰。\\n人世死前惟有别，春风争拟惜长条。\\n含烟惹雾每依依，万绪千条拂落晖。\\n为报行人休尽折，半留相送半迎归。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1217', 'poem', '唐', '马戴', '出塞词', '金带连环束战袍，马头冲雪度临洮。\\n卷旗夜劫单于帐，乱斫胡儿缺宝刀。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1218', 'poem', '唐', '韦应物', '夏日', '已谓心苦伤，如何日方永。\\n无人不昼寝，独坐山中静。\\n悟澹将遣虑，学空庶遗境。\\n积俗易为侵，愁来复难整。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1219', 'poem', '唐', '杜甫', '清明', '著处繁花务是日，长沙千人万人出。\\n渡头翠柳艳明眉，争道朱蹄骄啮膝。\\n此都好游湘西寺，诸将亦自军中至。\\n马援征行在眼前，葛强亲近同心事。\\n金镫下山红粉晚，牙樯捩柁青楼远。\\n古时丧乱皆可知，人世悲欢暂相遣。\\n弟侄虽存不得书，干戈未息苦离居。\\n逢迎少壮非吾道，况乃今朝更祓除。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1220', 'poem', '唐', '韩翃', '同题仙游观', '仙台下见五城楼，风物凄凄宿雨收。\\n山色遥连秦树晚，砧声近报汉宫秋。\\n疏松影落空坛静，细草香闲小洞幽。\\n何用别寻方外去，人间亦自有丹丘。', '2018-03-09 15:03:21');
INSERT INTO `t_poem` VALUES ('1221', 'poem', '唐', '祖咏', '江南旅情', '楚山不可极，归路但萧条。\\n海色晴看雨，江声夜听潮。\\n剑留南斗近，书寄北风遥。\\n为报空潭橘，无媒寄洛桥。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1222', 'poem', '唐', '李白', '怨歌行', '十五入汉宫，花颜笑春红。\\n君王选玉色，侍寝金屏中。\\n荐枕娇夕月，卷衣恋春风。\\n宁知赵飞燕，夺宠恨无穷。\\n沉忧能伤人，绿鬓成霜蓬。\\n一朝不得意，世事徒为空。\\n鹔鹴换美酒，舞衣罢雕龙。\\n寒苦不忍言，为君奏丝桐。\\n肠断弦亦绝，悲心夜忡忡。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1223', 'poem', '唐', '李煜', '悼诗', '永念难消释，孤怀痛自嗟。\\n雨深秋寂莫，愁引病增加。\\n咽绝风前思，昏濛眼上花。\\n空王应念我，穷子正迷家。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1224', 'poem', '唐', '李商隐', '深宫', '金殿销香闭绮栊，玉壶传点咽铜龙。\\n狂飙不惜萝阴薄，清露偏知桂叶浓。\\n斑竹岭边无限泪，景阳宫里及时钟。\\n岂知为雨为云处，只有高唐十二峰。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1225', 'poem', '唐', '李峤', '中秋月二首·其二', '圆魄上寒空，皆言四海同。\\n安知千里外，不有雨兼风？', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1226', 'poem', '唐', '白居易', '为薛台悼亡', '半死梧桐老病身，重泉一念一伤神。\\n手携稚子夜归院，月冷空房不见人。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1227', 'poem', '唐', '司空图', '长命缕', '他乡处处堪悲事，残照依依惜别天。\\n此去知名长命缕，殷勤为我唱花前。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1228', 'poem', '唐', '吴融', '途中见杏花', '一枝红杏出墙头，墙外行人正独愁。\\n长得看来犹有恨，可堪逢处更难留！\\n林空色暝莺先到，春浅香寒蝶未游。\\n更忆帝乡千万树，澹烟笼日暗神州。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1229', 'poem', '唐', '韩愈', '秋字', '淮南悲木落，而我亦伤秋。\\n况与故人别，那堪羁宦愁。\\n荣华今异路，风雨昔同忧。\\n莫以宜春远，江山多胜游。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1230', 'poem', '唐', '佚名', '郊庙歌辞·享太庙乐章·宁和', '炎驭失天纲，土德承天命。\\n英猷被寰宇，懿躅隆邦政。\\n七德已绥边，九夷咸底定。\\n景化覃遐迩，深仁洽翔泳。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1231', 'poem', '唐', '李翱', '赠药山高僧惟俨二首', '练得身形似鹤形，千株松下两函经。\\n我来问道无馀说，云在青霄水在瓶。\\n选得幽居惬野情，终年无送亦无迎。\\n有时直上孤峰顶，月下披云啸一声。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1232', 'poem', '唐', '释彪', '宝琴', '吾有一宝琴，价重双南金。\\n刻作龙凤象，弹为山水音。\\n星从徽里发，风来弦上吟。\\n钟期不可遇，谁辨曲中心。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1233', 'poem', '唐', '杜甫', '腊日', '腊日常年暖尚遥，今年腊日冻全消。\\n侵陵雪色还萱草，漏泄春光有柳条。\\n纵酒欲谋良夜醉，还家初散紫宸朝。\\n口脂面药随恩泽，翠管银罂下九霄。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1234', 'poem', '唐', '周昙', '六朝门·简文帝', '救兵方至强抽军，与贼开城是简文。\\n曲项琵琶催酒处，不图为乐向谁云。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1235', 'poem', '唐', '张祜', '纵游淮南', '十里长街市井连，月明桥上看神仙。\\n人生只合扬州死，禅智山光好墓田。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1236', 'poem', '唐', '岑参', '赵将军歌', '九月天山风似刀，城南猎马缩寒毛。\\n将军纵博场场胜，赌得单于貂鼠袍。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1237', 'poem', '唐', '王涯', '琴曲歌辞·蔡氏五弄·游春曲二首', '万树江边杏，新开一夜风。\\n满园深浅色，照在绿波中。\\n上苑何穷树，花开次第新。\\n香车与丝骑，风静亦生尘。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1238', 'poem', '唐', '李咸用', '苔', '几年风雨迹，叠在石孱颜。\\n生处景长静，看来情尽闲。\\n吟亭侵坏壁，药院掩空关。\\n每忆东行径，移筇独自还。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1239', 'poem', '唐', '薛涛', '春望词四首', '花开不同赏，花落不同悲。\\n欲问相思处，花开花落时。\\n揽草结同心，将以遗知音。\\n春愁正断绝，春鸟复哀吟。\\n风花日将老，佳期犹渺渺。\\n不结同心人，空结同心草。\\n那堪花满枝，翻作两相思。\\n玉箸垂朝镜，春风知不知。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1240', 'poem', '唐', '陈羽', '湘妃怨·舜欲省蛮陬', '舜欲省蛮陬，南巡非逸游。\\n九山沉白日，二女泣沧洲。\\n目极楚云断，恨连湘水流。\\n至今闻鼓瑟，咽绝不胜愁。', '2018-03-09 15:03:30');
INSERT INTO `t_poem` VALUES ('1241', 'poem', '唐', '和凝', '江城子·初夜含娇入洞房', '初夜含娇入洞房，理残妆，柳眉长。翡翠屏中，\\n亲爇玉炉香。整顿金钿呼小玉，排红烛，待潘郎。\\n竹里风生月上门，理秦筝，对云屏。轻拨朱弦，\\n恐乱马嘶声。含恨含娇独自语，今夜约，太迟生。\\n斗转星移玉漏频，已三更，对栖莺。历历花间，\\n似有马蹄声。含笑整衣开绣户，斜敛手，下阶迎。\\n迎得郎来入绣闱，语相思，连理枝。鬓乱钗垂，\\n梳堕印山眉。娅姹含情娇不语，纤玉手，抚郎衣。\\n帐里鸳鸯交颈情，恨鸡声，天已明。愁见街前，\\n还是说归程。临上马时期后会，待梅绽，月初生。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1242', 'poem', '唐', '白居易', '早冬', '十月江南天气好，可怜冬景似春华。\\n霜轻未杀萋萋草，日暖初干漠漠沙。\\n老柘叶黄如嫩树，寒樱枝白是狂花。\\n此时却羡闲人醉，五马无由入酒家。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1243', 'poem', '唐', '郎士元', '听邻家吹笙', '凤吹声如隔彩霞，不知墙外是谁家。\\n重门深锁无寻处，疑有碧桃千树花。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1244', 'poem', '唐', '孟浩然', '万山潭作', '垂钓坐盘石，水清心亦闲。\\n鱼行潭树下，猿挂岛藤间。\\n游女昔解佩，传闻于此山。\\n求之不可得，沿月棹歌还。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1245', 'poem', '唐', '白居易', '微雨夜行', '漠漠秋云起，稍稍夜寒生。\\n但觉衣裳湿，无点亦无声。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1246', 'poem', '唐', '白居易', '江楼月', '嘉陵江曲曲江池，明月虽同人别离。\\n一宵光景潜相忆，两地阴晴远不知。\\n谁料江边怀我夜，正当池畔望君时。\\n今朝共语方同悔，不解多情先寄诗。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1247', 'poem', '唐', '贯休', '春晚书山家屋壁二首', '柴门寂寂黍饭馨，山家烟火春雨晴。\\n庭花蒙蒙水泠泠，小儿啼索树上莺。\\n水香塘黑蒲森森，鸳鸯鸂鶒如家禽。\\n前村后垄桑柘深，东邻西舍无相侵。\\n蚕娘洗茧前溪渌，牧童吹笛和衣浴。\\n山翁留我宿又宿，笑指西坡瓜豆熟。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1248', 'poem', '唐', '顾况', '江上', '江清白鸟斜，荡桨罥蘋花。\\n听唱菱歌晚，回塘月照沙。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1249', 'poem', '唐', '徐夤', '风', '城上寒来思莫穷，土囊萍末两难同。\\n飘成远浪江湖际，吹起暮尘京洛中。\\n飞雪萧条残腊节，落花狼藉古行宫。\\n春能和煦秋摇落，生杀还同造化功。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1250', 'poem', '唐', '元稹', '雪天', '故乡千里梦，往事万重悲。\\n小雪沉阴夜，闲窗老病时。\\n独闻归去雁，偏咏别来诗。\\n惭愧红妆女，频惊两鬓丝。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1251', 'poem', '唐', '顾况', '宫词五首·其二', '玉楼天半起笙歌，风送宫嫔笑语和。\\n月殿影开闻夜漏，水晶帘卷近秋河。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1252', 'poem', '唐', '王维', '出塞作', '居延城外猎天骄，白草连山野火烧。\\n暮云空碛时驱马，秋日平原好射雕。\\n护羌校尉朝乘障，破虏将军夜渡辽。\\n玉靶角弓珠勒马，汉家将赐霍嫖姚。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1253', 'poem', '唐', '钱起', '赠阙下裴舍人', '二月黄莺飞上林，春城紫禁晓阴阴。\\n长乐钟声花外尽，龙池柳色雨中深。\\n阳和不散穷途恨，霄汉长怀捧日新。\\n献赋十年犹未遇，羞将白发对华簪。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1254', 'poem', '唐', '温庭筠', '元日', '神耀破氛昏，新阳入晏温。\\n绪风调玉吹，端日应铜浑。\\n威凤跄瑶簴，升龙护璧门。\\n雨旸春令煦，裘冕晬容尊。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1255', 'poem', '唐', '刘长卿', '江州重别薛六柳八二员外', '生涯岂料承优诏，世事空知学醉歌。\\n江上月明胡雁过，淮南木落楚山多。\\n寄身且喜沧洲近，顾影无如白发何。\\n今日龙钟人共弃，愧君犹遣慎风波。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1256', 'poem', '唐', '李白', '登太白峰', '西上太白峰，夕阳穷登攀。\\n太白与我语，为我开天关。\\n愿乘泠风去，直出浮云间。\\n举手可近月，前行若无山。\\n一别武功去，何时复见还。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1257', 'poem', '唐', '元结', '贼退示官吏', '昔岁逢太平，山林二十年。\\n泉源在庭户，洞壑当门前。\\n井税有常期，日晏犹得眠。\\n忽然遭世变，数岁亲戎旃。\\n今来典斯郡，山夷又纷然。\\n城小贼不屠，人贫伤可怜。\\n是以陷邻境，此州独见全。\\n使臣将王命，岂不如贼焉。\\n今彼征敛者，迫之如火煎。\\n谁能绝人命，以作时世贤。\\n思欲委符节，引竿自刺船。\\n将家就鱼麦，归老江湖边。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1258', 'poem', '唐', '独孤及', '送虢州王录事之任', '谓子文章达，当年羽翼高。\\n一经俄白首，三命尚青袍。\\n未遇须藏器，安卑莫告劳。\\n盘根倘相值，试用发硎刀。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1259', 'poem', '唐', '高适', '咏史', '尚有绨袍赠，应怜范叔寒。\\n不知天下士，犹作布衣看。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1260', 'poem', '唐', '王涯', '陇上行', '负羽到边州，鸣笳度陇头。\\n云黄知塞近，草白见边秋。', '2018-03-09 15:03:34');
INSERT INTO `t_poem` VALUES ('1261', 'poem', '唐', '李白', '胡无人', '严风吹霜海草凋，筋干精坚胡马骄。汉家战士三十万，\\n将军兼领霍嫖姚。流星白羽腰间插，剑花秋莲光出匣。\\n天兵照雪下玉关，虏箭如沙射金甲。云龙风虎尽交回，\\n太白入月敌可摧。敌可摧，旄头灭，履胡之肠涉胡血。\\n悬胡青天上，埋胡紫塞傍。胡无人，汉道昌。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1262', 'poem', '唐', '沈如筠', '闺怨二首·其一', '雁尽书难寄，愁多梦不成。\\n愿随孤月影，流照伏波营。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1263', 'poem', '唐', '韩愈', '独钓四首', '侯家林馆胜，偶入得垂竿。\\n曲树行藤角，平池散芡盘。\\n羽沈知食驶，缗细觉牵难。\\n聊取夸儿女，榆条系从鞍。\\n一径向池斜，池塘野草花。\\n雨多添柳耳，水长减蒲芽。\\n坐厌亲刑柄，偷来傍钓车。\\n太平公事少，吏隐讵相赊。\\n独往南塘上，秋晨景气醒。\\n露排四岸草，风约半池萍。\\n鸟下见人寂，鱼来闻饵馨。\\n所嗟无可召，不得倒吾瓶。\\n秋半百物变，溪鱼去不来。\\n风能坼芡觜，露亦染梨腮。\\n远岫重叠出，寒花散乱开。\\n所期终莫至，日暮与谁回。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1264', 'poem', '唐', '陈陶', '陇西行四首', '汉主东封报太平，无人金阙议边兵。\\n纵饶夺得林胡塞，碛地桑麻种不生。\\n誓扫匈奴不顾身，五千貂锦丧胡尘。\\n可怜无定河边骨，犹是春闺梦里人。\\n陇戍三看塞草青，楼烦新替护羌兵。\\n同来死者伤离别，一夜孤魂哭旧营。\\n黠虏生擒未有涯，黑山营阵识龙蛇。\\n自从贵主和亲后，一半胡风似汉家。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1265', 'poem', '唐', '白居易', '东城桂三首', '子堕本从天竺寺，根盘今在阖闾城。\\n当时应逐南风落，落向人间取次生。\\n霜雪压多虽不死，荆榛长疾欲相埋。\\n长忧落在樵人手，卖作苏州一束柴。\\n遥知天上桂花孤，试问嫦娥更要无。\\n月宫幸有闲田地，何不中央种两株。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1266', 'poem', '唐', '李白', '望黄鹤楼', '东望黄鹤山，雄雄半空出。四面生白云，中峰倚红日。\\n岩峦行穹跨，峰嶂亦冥密。颇闻列仙人，于此学飞术。\\n一朝向蓬海，千载空石室。金灶生烟埃，玉潭秘清谧。\\n地古遗草木，庭寒老芝术，蹇予羡攀跻，因欲保闲逸。\\n观奇遍诸岳，兹岭不可匹。结心寄青松，永悟客情毕。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1267', 'poem', '唐', '李郢', '南池', '小男供饵妇搓丝，溢榼香醪倒接罹。\\n日出两竿鱼正食，一家欢笑在南池。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1268', 'poem', '唐', '王贞白', '商山', '商山名利路，夜亦有人行。\\n四皓卧云处，千秋叠藓生。\\n昼烧笼涧黑，残雪隔林明。\\n我待酬恩了，来听水石声。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1269', 'poem', '唐', '李绅', '答章孝标', '假金方用真金镀，若是真金不镀金。\\n十载长安得一第，何须空腹用高心。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1270', 'poem', '唐', '李白', '陌上桑', '美女渭桥东，春还事蚕作。\\n五马如飞龙，青丝结金络。\\n不知谁家子，调笑来相谑。\\n妾本秦罗敷，玉颜艳名都。\\n绿条映素手，采桑向城隅。\\n使君且不顾，况复论秋胡。\\n寒螀爱碧草，鸣凤栖青梧。\\n托心自有处，但怪傍人愚。\\n徒令白日暮，高驾空踟蹰。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1271', 'poem', '唐', '薛莹', '秋日湖上', '落日五湖游，烟波处处愁。\\n沈浮千古事，谁与问东流。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1272', 'poem', '唐', '李贺', '牡丹种曲', '莲枝未长秦蘅老，走马驮金斸春草。\\n水灌香泥却月盘，一夜绿房迎白晓。\\n美人醉语园中烟，晚华已散蝶又阑。\\n梁王老去罗衣在，拂袖风吹蜀国弦。\\n归霞帔拖蜀帐昏，嫣红落粉罢承恩。\\n檀郎谢女眠何处？楼台月明燕夜语。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1273', 'poem', '唐', '刘长卿', '龙门八咏·下山', '谁识往来意，孤云长自闲。\\n风寒未渡水，日暮更看山。\\n木落众峰出，龙宫苍翠间。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1274', 'poem', '唐', '李白', '南轩松', '南轩有孤松，柯叶自绵幂。\\n清风无闲时，潇洒终日夕。\\n阴生古苔绿，色染秋烟碧。\\n何当凌云霄，直上数千尺。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1275', 'poem', '唐', '于武陵', '西归', '不系与舟闲，悠悠吴楚间。\\n羞将新白发，却到旧青山。\\n一叶忽离树，几人同入关。\\n长安家尚在，秋至又西还。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1276', 'poem', '唐', '王维', '辋川集·木兰柴', '秋山敛馀照，飞鸟逐前侣。\\n彩翠时分明，夕岚无处所。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1277', 'poem', '唐', '罗隐', '姥山', '临塘古庙一神仙，绣幌花容色俨然。\\n为逐朝云来此地，因随暮雨不归天。\\n眉分初月湖中鉴，香散馀风竹上烟。\\n借问邑人沈水事，已经秦汉几千年。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1278', 'poem', '唐', '张仲素', '陇上行', '行到黄云陇，唯闻羌戍鼙。\\n不如山下水，犹得任东西。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1279', 'poem', '唐', '李商隐', '杏花', '上国昔相值，亭亭如欲言。\\n异乡今暂赏，眽眽岂无恩。\\n援少风多力，墙高月有痕。\\n为含无限意，遂对不胜繁。\\n仙子玉京路，主人金谷园。\\n几时辞碧落，谁伴过黄昏。\\n镜拂铅华腻，炉藏桂烬温。\\n终应催竹叶，先拟咏桃根。\\n莫学啼成血，从教梦寄魂。\\n吴王采香径，失路入烟村。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1280', 'poem', '唐', '周昙', '隋门·贺若弼', '破敌将军意气豪，请除倾国斩妖娆。\\n红绡忍染娇春雪，瞪目看行切玉刀。', '2018-03-09 15:03:43');
INSERT INTO `t_poem` VALUES ('1281', 'poem', '唐', '白居易', '耳顺吟，寄敦诗、梦得', '三十四十五欲牵，七十八十百病缠。\\n五十六十却不恶，恬淡清净心安然。\\n已过爱贪声利后，犹在病羸昏耄前。\\n未无筋力寻山水，尚有心情听管弦。\\n闲开新酒尝数醆，醉忆旧诗吟一篇。\\n敦诗梦得且相劝，不用嫌他耳顺年。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1282', 'poem', '唐', '张九龄', '感遇·幽人归独卧', '幽人归独卧，滞虑洗孤清。\\n持此谢高鸟，因之传远情。\\n日夕怀空意，人谁感至精？\\n飞沈理自隔，何所慰吾诚？', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1283', 'poem', '唐', '韦应物', '寒食寄京师诸弟', '雨中禁火空斋冷，江上流莺独坐听。\\n把酒看花想诸弟，杜陵寒食草青青。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1284', 'poem', '唐', '高适', '人日寄杜二拾遗', '人日题诗寄草堂，遥怜故人思故乡。\\n柳条弄色不忍见，梅花满枝空断肠。\\n身在远藩无所预，心怀百忧复千虑。\\n今年人日空相忆，明年人日知何处。\\n一卧东山三十春，岂知书剑老风尘。\\n龙钟还忝二千石，愧尔东西南北人。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1285', 'poem', '唐', '成彦雄', '元日', '戴星先捧祝尧觞，镜里堪惊两鬓霜。\\n好是灯前偷失笑，屠苏应不得先尝。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1286', 'poem', '唐', '元稹', '独醉', '一树芳菲也当春，漫随车马拥行尘。\\n桃花解笑莺能语，自醉自眠那藉人。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1287', 'poem', '唐', '汪遵', '三闾庙', '为嫌朝野尽陶陶，不觉官高怨亦高。\\n憔悴莫酬渔父笑，浪交千载咏离骚。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1288', 'poem', '唐', '李咸用', '长歌行', '要衣须破束，欲炙须解牛。当年不快意，徒为他人留。\\n百岁之约何悠悠，华发星星稀满头。峨眉螓首聊我仇，\\n圆红阙白令人愁。何不夕引清奏，朝登翠楼，逢花便折，\\n闻胜即游？鼓腕腾棍晴雷收，舞腰困褭垂杨柔。\\n象箸击折歌勿休，玉山未到非风流。眼前有物俱是梦，\\n莫将身作黄金仇。死生同域不用惧，富贵在天何足忧！', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1289', 'poem', '唐', '李煜', '梅花', '殷勤移植地，曲槛小栏边。\\n共约重芳日，还忧不盛妍。\\n阻风开步障，乘月溉寒泉。\\n谁料花前后，蛾眉却不全。\\n失却烟花主，东君自不知。\\n清香更何用，犹发去年枝。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1290', 'poem', '唐', '白居易', '苦热题恒寂师禅室', '人人避暑走如狂，独有禅师不出房。\\n可是禅房无热到，但能心静即身凉。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1291', 'poem', '唐', '李德裕', '登崖州城作', '独上高楼望帝京，鸟飞犹是半年程。\\n青山似欲留人住，百匝千遭绕郡城。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1292', 'poem', '唐', '杜甫', '和裴迪登蜀州东亭送客逢早梅相忆见寄', '东阁官梅动诗兴，还如何逊在扬州。\\n此时对雪遥相忆，送客逢春可自由。\\n幸不折来伤岁暮，若为看去乱乡愁。\\n江边一树垂垂发，朝夕催人自白头。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1293', 'poem', '唐', '慕幽', '剑客', '去住知何处，空将一剑行。\\n杀人虽取次，为事爱公平。\\n戟立嗔髭鬓，星流忿眼睛。\\n晓来湘市说，拂曙别辽城。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1294', 'poem', '唐', '李商隐', '筹笔驿', '猿鸟犹疑畏简书，风云常为护储胥。\\n徒令上将挥神笔，终见降王走传车。\\n管乐有才真不忝，关张无命欲何如？\\n他年锦里经祠庙，梁父吟成恨有余。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1295', 'poem', '唐', '杜牧', '洛阳秋夕', '泠泠寒水带霜风，更在天桥夜景中。\\n清禁漏闲烟树寂，月轮移在上阳宫。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1296', 'poem', '唐', '韦应物', '观田家', '微雨众卉新，一雷惊蛰始。\\n田家几日闲，耕种从此起。\\n丁壮俱在野，场圃亦就理。\\n归来景常晏，饮犊西涧水。\\n饥劬不自苦，膏泽且为喜。\\n仓禀无宿储，徭役犹未已。\\n方惭不耕者，禄食出闾里。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1297', 'poem', '唐', '韦庄', '古离别', '晴烟漠漠柳毵毵，不那离情酒半酣。\\n更把玉鞭云外指，断肠春色在江南。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1298', 'poem', '唐', '张祜', '赠内人', '禁门宫树月痕过，媚眼唯看宿燕窠。\\n斜拔玉钗灯影畔，剔开红焰救飞蛾。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1299', 'poem', '唐', '郑谷', '中秋', '清香闻晓莲，水国雨馀天。\\n天气正得所，客心刚悄然。\\n乱兵何日息，故老几人全。\\n此际难消遣，从来未学禅。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1300', 'poem', '唐', '杜甫', '南邻', '锦里先生乌角巾，园收芋栗未全贫。\\n惯看宾客儿童喜，得食阶除鸟雀驯。\\n秋水才深四五尺，野航恰受两三人。\\n白沙翠竹江村暮，相对柴门月色新。', '2018-03-09 15:03:47');
INSERT INTO `t_poem` VALUES ('1301', 'poem', '唐', '李白', '赠从兄襄阳少府皓', '结发未识事，所交尽豪雄。\\n却秦不受赏，击晋宁为功。\\n托身白刃里，杀人红尘中。\\n当朝揖高义，举世称英雄。\\n小节岂足言，退耕舂陵东。\\n归来无产业，生事如转蓬。\\n一朝乌裘敝，百镒黄金空。\\n弹剑徒激昂，出门悲路穷。\\n吾兄青云士，然诺闻诸公。\\n所以陈片言，片言贵情通。\\n棣华倘不接，甘与秋草同。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1302', 'poem', '唐', '项斯', '长安退将', '塞晚冲沙损眼明，归来养病住秦京。\\n上高楼阁看星坐，著白衣裳把剑行。\\n常说老身思斗将，最悲无力制蕃营。\\n翠眉红脸和回鹘，惆怅中原不用兵。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1303', 'poem', '唐', '张继', '金谷园', '彩楼歌馆正融融，一骑星飞锦帐空。\\n老尽名花春不管，年年啼鸟怨东风。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1304', 'poem', '唐', '吕岩', '百字碑', '养气忘言守，降心为不为。\\n动静知宗祖，无事更寻谁。\\n真常须应物，应物要不迷。\\n不迷性自住，性住气自回。\\n气回丹自结，壶中配坎离。\\n阴阳生返复，普化一声雷。\\n白云朝顶上，甘露洒须弥。\\n自饮长生酒，逍遥谁得知。\\n坐听无弦曲，明通造化机。\\n都来二十句，端的上天梯。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1305', 'poem', '唐', '司空图', '下方', '昏旦松轩下，怡然对一瓢。\\n雨微吟思足，花落梦无聊。\\n细事当棋遣，衰容喜镜饶。\\n溪僧有深趣，书至又相邀。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1306', 'poem', '唐', '元稹', '灯影', '洛阳昼夜无车马，漫挂红纱满树头。\\n见说平时灯影里，玄宗潜伴太真游。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1307', 'poem', '唐', '司空图', '下方', '三十年来往，中间京洛尘。\\n倦行今白首，归卧已清神。\\n坡暖冬抽笋，松凉夏健人。\\n更惭征诏起，避世迹非真。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1308', 'poem', '唐', '姚合', '题金州西园九首·莓苔', '茅堂阶岂高，数寸是苔藓。\\n只恐秋雨中，窗户亦不溅。\\n眼前无此物，我情何由遣。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1309', 'poem', '唐', '贾至', '春思二首', '草色青青柳色黄，桃花历乱李花香。\\n东风不为吹愁去，春日偏能惹恨长。\\n红粉当垆弱柳垂，金花腊酒解酴醿。\\n笙歌日暮能留客，醉杀长安轻薄儿。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1310', 'poem', '唐', '白居易', '青门柳', '青青一树伤心色，曾入几人离恨中。\\n为近都门多送别，长条折尽减春风。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1311', 'poem', '唐', '韩愈', '谒衡岳庙遂宿岳寺题门楼', '五岳祭秩皆三公，四方环镇嵩当中。\\n火维地荒足妖怪，天假神柄专其雄。\\n喷云泄雾藏半腹，虽有绝顶谁能穷？\\n我来正逢秋雨节，阴气晦昧无清风。\\n潜心默祷若有应，岂非正直能感通！\\n须臾静扫众峰出，仰见突兀撑青空。\\n紫盖连延接天柱，石廪腾掷堆祝融。\\n森然魄动下马拜，松柏一径趋灵宫。\\n粉墙丹柱动光彩，鬼物图画填青红。\\n升阶伛偻荐脯酒，欲以菲薄明其衷。\\n庙令老人识神意，睢盱侦伺能鞠躬。\\n手持杯珓导我掷，云此最吉余难同。\\n窜逐蛮荒幸不死，衣食才足甘长终。\\n侯王将相望久绝，神纵欲福难为功。\\n夜投佛寺上高阁，星月掩映云曈昽。\\n猿鸣钟动不知曙，杲杲寒日生于东。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1312', 'poem', '唐', '白居易', '端居咏怀', '贾生俟罪心相似，张翰思归事不如。\\n斜日早知惊鵩鸟，秋风悔不忆鲈鱼。\\n胸襟曾贮匡时策，怀袖犹残谏猎书。\\n从此万缘都摆落，欲携妻子买山居。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1313', 'poem', '唐', '张九龄', '归燕诗', '海燕岁微渺，乘春亦暂来。\\n岂知泥滓贱，只见玉堂开。\\n绣户时双入，华堂日几回。\\n无心与物竞，鹰隼莫相猜。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1314', 'poem', '唐', '司空图', '重阳四首', '檐前减燕菊添芳，燕尽庭前菊又荒。\\n老大比他年少少，每逢佳节更悲凉。\\n雨寒莫待菊花催，须怕晴空暖并开。\\n开却一枝开却尽，且随幽蝶更徘徊。\\n青娥懒唱无衣换，黄菊新开乞酒难。\\n长有长亭惆怅事，隔河更得对凭栏。\\n白发怕寒梳更懒，黄花晴日照初开。\\n篱头应是蝶相报，已被邻家携酒来。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1315', 'poem', '唐', '白居易', '池上早夏', '水积春塘晚，阴交夏木繁。\\n舟船如野渡，篱落似江村。\\n静拂琴床席，香开酒库门。\\n慵闲无一事，时弄小娇孙。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1316', 'poem', '唐', '张谓', '题长安壁主人', '世人结交须黄金，黄金不多交不深。\\n纵令然诺暂相许，终是悠悠行路心。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1317', 'poem', '唐', '牛希济', '生查子·春山烟欲收', '春山烟欲收，天澹星稀小。残月脸边明，别泪临清晓。\\n语已多，情未了，回首犹重道。记得绿罗裙，处处怜芳草。\\n新月曲如眉，未有团圞意。红豆不堪看，满眼相思泪。\\n终日劈桃穰，人在心儿里。两朵隔墙花，早晚成连理。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1318', 'poem', '唐', '张文琮', '蜀道难', '梁山镇地险，积石阻云端。\\n深谷下寥廓，层岩上郁盘。\\n飞梁架绝岭，栈道接危峦。\\n揽辔独长息，方知斯路难。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1319', 'poem', '唐', '韦应物', '咏露珠', '秋荷一滴露，清夜坠玄天。\\n将来玉盘上，不定始知圆。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1320', 'poem', '唐', '张子容', '春江花月夜二首', '林花发岸口，气色动江新。\\n此夜江中月，流光花上春。\\n分明石潭里，宜照浣纱人。\\n交甫怜瑶珮，仙妃难重期。\\n沉沉绿江晚，惆怅碧云姿。\\n初逢花上月，言是弄珠时。', '2018-03-09 15:03:58');
INSERT INTO `t_poem` VALUES ('1321', 'poem', '唐', '高适', '夜别韦司士', '高馆张灯酒复清，夜钟残月雁归声。\\n只言啼鸟堪求侣，无那春风欲送行。\\n黄河曲里沙为岸，白马津边柳向城。\\n莫怨他乡暂离别，知君到处有逢迎。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1322', 'poem', '唐', '张说', '幽州夜饮', '凉风吹夜雨，萧瑟动寒林。\\n正有高堂宴，能忘迟暮心。\\n军中宜剑舞，塞上重笳音。\\n不作边城将，谁知恩遇深。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1323', 'poem', '唐', '周昙', '春秋战国门·王孙满', '九牧金熔物像成，辞昏去乱祚休明。\\n兴亡在德不在鼎，楚子何劳问重轻。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1324', 'poem', '唐', '白居易', '慈乌夜啼', '慈乌失其母，哑哑吐哀音。\\n昼夜不飞去，经年守故林。\\n夜夜夜半啼，闻者为沾襟。\\n声中如告诉，未尽反哺心。\\n百鸟岂无母，尔独哀怨深。\\n应是母慈重，使尔悲不任。\\n昔有吴起者，母殁丧不临。\\n嗟哉斯徒辈，其心不如禽。\\n慈乌复慈乌，鸟中之曾参。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1325', 'poem', '唐', '无可', '新年', '燃灯朝复夕，渐作长年身。\\n紫阁未归日，青门又见春。\\n掩关寒过尽，开定草生新。\\n自有林中趣，谁惊岁去频。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1326', 'poem', '唐', '羊士谔', '春望', '莫问华簪发已斑，归心满目是青山。\\n独上层城倚危槛，柳营春尽马嘶闲。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1327', 'poem', '唐', '欧阳衮', '田家', '黯黯日将夕，牛羊村外来。\\n岩阿青气发，篱落杏花开。\\n草木应初感，鶬鹒亦已催。\\n晚间春作好，行乐不须猜。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1328', 'poem', '唐', '雍裕之', '自君之出矣', '自君之出矣，宝镜为谁明？思君如陇水，长闻呜咽声。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1329', 'poem', '唐', '杜牧', '题木兰庙', '弯弓征战作男儿，梦里曾经与画眉。\\n几度思归还把酒，拂云堆上祝明妃。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1330', 'poem', '唐', '李德裕', '谪岭南道中作', '岭水争分路转迷，桄榔椰叶暗蛮溪。\\n愁冲毒雾逢蛇草，畏落沙虫避燕泥。\\n五月畲田收火米，三更津吏报潮鸡。\\n不堪肠断思乡处，红槿花中越鸟啼。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1331', 'poem', '唐', '胡曾', '咏史诗·赤壁', '烈火西焚魏帝旗，周郎开国虎争时。\\n交兵不假挥长剑，已挫英雄百万师。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1332', 'poem', '唐', '李白', '自遣', '对酒不觉暝，落花盈我衣。\\n醉起步溪月，鸟还人亦稀。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1333', 'poem', '唐', '刘禹锡', '望夫山', '终日望夫夫不归，化为孤石苦相思。\\n望来已是几千载，只似当时初望时。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1334', 'poem', '唐', '罗邺', '春风', '每岁东来助发生，舞空悠飏遍寰瀛。\\n暗添芳草池塘色，远递高楼箫管声。\\n帘透骊宫偏带恨，花催上苑剩多情。\\n如何一瑞车书日，吹取青云道路平。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1335', 'poem', '唐', '李咸用', '春风', '青帝使和气，吹嘘万国中。\\n发生宁有异，先后自难同。\\n辇草不消力，岩花应费功。\\n年年三十骑，飘入玉蟾宫。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1336', 'poem', '唐', '白居易', '三月三日', '画堂三月初三日，絮扑窗纱燕拂檐。\\n莲子数杯尝冷酒，柘枝一曲试春衫。\\n阶临池面胜看镜，户映花丛当下帘。\\n指点楼南玩新月，玉钩素手两纤纤。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1337', 'poem', '唐', '张籍', '凉州词三首·其三', '凤林关里水东流，白草黄榆六十秋。\\n边将皆承主恩泽，无人解道取凉州。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1338', 'poem', '唐', '祖咏', '清明宴司勋刘郎中别业', '田家复近臣，行乐不违亲。\\n霁日园林好，清明烟火新。\\n以文长会友，唯德自成邻。\\n池照窗阴晚，杯香药味春。\\n檐前花覆地，竹外鸟窥人。\\n何必桃源里，深居作隐沦。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1339', 'poem', '唐', '陈陶', '将进酒·金尊莫倚青春健', '金尊莫倚青春健，龌龊浮生如走电。\\n琴瑟盘倾从世珠，黄泥局泻流年箭。\\n麻姑爪秃瞳子昏，东皇肉角生鱼鳞。\\n灵鳌柱骨半枯朽，骊龙德悔愁耕人。\\n周孔蓍龟久沦没，黄蒿谁认贤愚骨。\\n兔苑词才去不还，兰亭水石空明月。\\n姮娥弄箫香雨收，江滨迸瑟鱼龙愁。\\n灵芝九折楚莲醉，翾风一叹梁庭秋。\\n醁亚蛮觥奉君寿，玉山三献春红透。\\n银鸭金鹅言待谁，隋家岳渎皇家有。\\n珊瑚座上凌香云，凤qV龙炙猩猩唇。\\n芝兰此日不倾倒，南山白石皆贤人。\\n文康调笑麒麟起，一曲飞龙寿天地。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1340', 'poem', '唐', '卢照邻', '九月九日玄武山旅眺', '九月九日眺山川，归心归望积风烟。\\n他乡共酌金花酒，万里同悲鸿雁天。', '2018-03-09 15:04:11');
INSERT INTO `t_poem` VALUES ('1341', 'poem', '唐', '白居易', '罗子', '有女名罗子，生来才两春。\\n我今年已长，日夜二毛新。\\n顾念娇啼面，思量老病身。\\n直应头似雪，始得见成人。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1342', 'poem', '唐', '王维', '临湖亭', '轻舸迎上客，悠悠湖上来。\\n当轩对尊酒，四面芙蓉开。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1343', 'poem', '唐', '白居易', '阿崔', '谢病卧东都，羸然一老夫。\\n孤单同伯道，迟暮过商瞿。\\n岂料鬓成雪，方看掌弄珠。\\n已衰宁望有，虽晚亦胜无。\\n兰入前春梦，桑悬昨日弧。\\n里闾多庆贺，亲戚共欢娱。\\n腻剃新胎发，香绷小绣襦。\\n玉芽开手爪，酥颗点肌肤。\\n弓冶将传汝，琴书勿坠吾。\\n未能知寿夭，何暇虑贤愚。\\n乳气初离壳，啼声渐变雏。\\n何时能反哺，供养白头乌。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1344', 'poem', '唐', '赵鸾鸾', '檀口', '衔杯微动樱桃颗，咳唾轻飘茉莉香。\\n曾见白家樊素口，瓠犀颗颗缀榴芳。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1345', 'poem', '唐', '司空曙', '贼平后送人北归', '世乱同南去，时清独北还。\\n他乡生白发，旧国见青山。\\n晓月过残垒，繁星宿故关。\\n寒禽与衰草，处处伴愁颜。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1346', 'poem', '唐', '杜甫', '望岳三首·其二', '西岳崚嶒竦处尊，诸峰罗立如儿孙。\\n安得仙人九节杖，拄到玉女洗头盆。\\n车箱入谷无归路，箭栝通天有一门。\\n稍待西风凉冷后，高寻白帝问真源。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1347', 'poem', '唐', '张籍', '野老歌 / 山农词', '老农家贫在山住，耕种山田三四亩。\\n苗疏税多不得食，输入官仓化为土。\\n岁暮锄犁傍空室，呼儿登山收橡实。\\n西江贾客珠百斛，船中养犬长食肉。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1348', 'poem', '唐', '司空图', '诗品二十四则·纤秾', '采采流水，蓬蓬远春。\\n窈窕深谷，时见美人。\\n碧桃满树，风日水滨。\\n柳阴路曲，流莺比邻。\\n乘之愈往，识之愈真。\\n如将不尽，与古为新。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1349', 'poem', '唐', '宋之问', '春日芙蓉园侍宴应制', '芙蓉秦地沼，卢橘汉家园。\\n谷转斜盘径，川回曲抱原。\\n风来花自舞，春入鸟能言。\\n侍宴瑶池夕，归途笳吹繁。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1350', 'poem', '唐', '李白', '古意', '君为女萝草，妾作兔丝花。\\n轻条不自引，为逐春风斜。\\n百丈托远松，缠绵成一家。\\n谁言会面易，各在青山崖。\\n女萝发馨香，兔丝断人肠。\\n枝枝相纠结，叶叶竞飘扬。\\n生子不知根，因谁共芬芳。\\n中巢双翡翠，上宿紫鸳鸯。\\n若识二草心，海潮亦可量。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1351', 'poem', '唐', '杜甫', '哀王孙', '长安城头头白乌，夜飞延秋门上呼。\\n又向人家啄大屋，屋底达官走避胡。\\n金鞭断折九马死，骨肉不得同驰驱。\\n腰下宝玦青珊瑚，可怜王孙泣路隅。\\n问之不肯道姓名，但道困苦乞为奴。\\n已经百日窜荆棘，身上无有完肌肤。\\n高帝子孙尽隆准，龙种自与常人殊。\\n豺狼在邑龙在野，王孙善保千金躯。\\n不敢长语临交衢，且为王孙立斯须。\\n昨夜东风吹血腥，东来橐驼满旧都。\\n朔方健儿好身手，昔何勇锐今何愚。\\n窃闻天子已传位，圣德北服南单于。\\n花门剺面请雪耻，慎勿出口他人狙。\\n哀哉王孙慎勿疏，五陵佳气无时无。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1352', 'poem', '唐', '裴迪', '辋川集二十首·栾家濑', '濑声喧极浦，沿涉向南津。\\n泛泛鸥凫渡，时时欲近人。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1353', 'poem', '唐', '上官昭容', '彩书怨', '叶下洞庭初，思君万里馀。\\n露浓香被冷，月落锦屏虚。\\n欲奏江南曲，贪封蓟北书。\\n书中无别意，惟怅久离居。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1354', 'poem', '唐', '李商隐', '燕台四首·春', '风光冉冉东西陌，几日娇魂寻不得。\\n蜜房羽客类芳心，冶叶倡条遍相识。\\n暖蔼辉迟桃树西，高鬟立共桃鬟齐。\\n雄龙雌凤杳何许，絮乱丝繁天亦迷。\\n醉起微阳若初曙，映帘梦断闻残语。\\n愁将铁网罥珊瑚，海阔天翻迷处所。\\n衣带无情有宽窄，春烟自碧秋霜白。\\n研丹擘石天不知，愿得天牢锁冤魄。\\n夹罗委箧单绡起，香肌冷衬琤琤珮。\\n今日东风自不胜，化作幽光入西海。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1355', 'poem', '唐', '释宝月', '行路难·君不见', '君不见，孤雁关外发，酸嘶度杨越。空城客子心肠断，\\n幽闺思妇气欲绝。凝霜夜下拂罗衣，浮云中断开明月。\\n夜夜遥遥徒相思，年年望望情不歇。取我匣中青铜镜，\\n情人为我除白发。行路难，行路难。夜闻南城汉使度，\\n使我流泪忆长安。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1356', 'poem', '唐', '杜甫', '赠李白', '二年客东都，所历厌机巧。\\n野人对膻腥，蔬食常不饱。\\n岂无青精饭，使我颜色好。\\n苦乏大药资，山林迹如扫。\\n李侯金闺彦，脱身事幽讨。\\n亦有梁宋游，方期拾瑶草。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1357', 'poem', '唐', '韩愈', '琴操十首·猗兰操', '兰之猗猗，扬扬其香。\\n不采而佩，于兰何伤。\\n今天之旋，其曷为然。\\n我行四方，以日以年。\\n雪霜贸贸，荠麦之茂。\\n子如不伤，我不尔觏。\\n荠麦之茂，荠麦之有。\\n君子之伤，君子之守。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1358', 'poem', '唐', '白居易', '自悲', '火宅煎熬地，霜松摧折身。\\n因知群动内，易死不过人。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1359', 'poem', '唐', '杜常', '华清宫', '行尽江南数十程，晓星残月入华清。\\n朝元阁上西风急，都入长杨作雨声。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1360', 'poem', '唐', '岑参', '奉和中书舍人贾至早朝大明宫', '鸡鸣紫陌曙光寒，莺啭皇州春色阑。\\n金阙晓钟开万户，玉阶仙仗拥千官。\\n花迎剑佩星初落，柳拂旌旗露未干。\\n独有凤凰池上客，阳春一曲和皆难。', '2018-03-09 15:04:24');
INSERT INTO `t_poem` VALUES ('1361', 'poem', '唐', '杜甫', '客从', '客从南溟来，遗我泉客珠。\\n珠中有隐字，欲辨不成书。\\n缄之箧笥久，以俟公家须。\\n开视化为血，哀今征敛无。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1362', 'poem', '唐', '杜甫', '孟冬', '殊俗还多事，方冬变所为。\\n破甘霜落爪，尝稻雪翻匙。\\n巫峡寒都薄，乌蛮瘴远随。\\n终然减滩濑，暂喜息蛟螭。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1363', 'poem', '唐', '杜甫', '甘林', '舍舟越西冈，入林解我衣。\\n青刍适马性，好鸟知人归。\\n晨光映远岫，夕露见日晞。\\n迟暮少寝食，清旷喜荆扉。\\n经过倦俗态，在野无所违。\\n试问甘藜藿，未肯羡轻肥。\\n喧静不同科，出处各天机。\\n勿矜朱门是，陋此白屋非。\\n明朝步邻里，长老可以依。\\n时危赋敛数，脱粟为尔挥。\\n相携行豆田，秋花霭菲菲。\\n子实不得吃，货市送王畿。\\n尽添军旅用，迫此公家威。\\n主人长跪问，戎马何时稀。\\n我衰易悲伤，屈指数贼围。\\n劝其死王命，慎莫远奋飞。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1364', 'poem', '唐', '白居易', '和梦得夏至忆苏州呈卢宾客', '忆在苏州日，常谙夏至筵。\\n粽香筒竹嫩，炙脆子鹅鲜。\\n水国多台榭，吴风尚管弦。\\n每家皆有酒，无处不过船。\\n交印君相次，褰帷我在前。\\n此乡俱老矣，东望共依然。\\n洛下麦秋月，江南梅雨天。\\n齐云楼上事，已上十三年。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1365', 'poem', '唐', '李商隐', '幽居冬暮', '羽翼摧残日，郊园寂寞时。\\n晓鸡惊树雪，寒鹜守冰池。\\n急景忽云暮，颓年浸已衰。\\n如何匡国分，不与夙心期。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1366', 'poem', '唐', '王维', '田园乐七首·其二', '再见封侯万户，立谈赐璧一双。\\n讵胜耦耕南亩，何如高卧东窗。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1367', 'poem', '唐', '李中', '江南春·千家事胜游', '千家事胜游，景物可忘忧。\\n水国楼台晚，春郊烟雨收。\\n鹧鸪啼竹树，杜若媚汀洲。\\n永巷歌声远，王孙会莫愁。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1368', 'poem', '唐', '杜牧', '河湟', '元载相公曾借箸，宪宗皇帝亦留神。\\n旋见衣冠就东市，忽遗弓剑不西巡。\\n牧羊驱马虽戎服，白发丹心尽汉臣。\\n唯有凉州歌舞曲，流传天下乐闲人。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1369', 'poem', '唐', '张籍', '凉州词三首', '边城暮雨雁飞低，芦笋初生渐欲齐。\\n无数铃声遥过碛，应驮白练到安西。\\n古镇城门白碛开，胡兵往往傍沙堆。\\n巡边使客行应早，欲问平安无使来。\\n凤林关里水东流，白草黄榆六十秋。\\n边将皆承主恩泽，无人解道取凉州。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1370', 'poem', '唐', '王维', '陇头吟', '长安少年游侠客，夜上戍楼看太白。\\n陇头明月迥临关，陇上行人夜吹笛。\\n关西老将不胜愁，驻马听之双泪流。\\n身经大小百馀战，麾下偏裨万户侯。\\n苏武才为典属国，节旄落尽海西头。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1371', 'poem', '唐', '高适', '塞下曲', '结束浮云骏，翩翩出从戎。\\n且凭天子怒，复倚将军雄。\\n万鼓雷殷地，千旗火生风。\\n日轮驻霜戈，月魄悬雕弓。\\n青海阵云匝，黑山兵气冲。\\n战酣太白高，战罢旄头空。\\n万里不惜死，一朝得成功。\\n画图麒麟阁，入朝明光宫。\\n大笑向文士，一经何足穷。\\n古人昧此道，往往成老翁。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1372', 'poem', '唐', '朱放', '题竹林寺', '岁月人间促，烟霞此地多。\\n殷勤竹林寺，能得几回过。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1373', 'poem', '唐', '李山甫', '代崇徽公主意', '金钗坠地鬓堆云，自别朝阳帝岂闻。\\n遣妾一身安社稷，不知何处用将军。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1374', 'poem', '唐', '李贺', '官街鼓', '晓声隆隆催转日，暮声隆隆呼月出。\\n汉城黄柳映新帘，柏陵飞燕埋香骨。\\n磓发千年日长白，孝武秦王听不得。\\n从君翠发芦花色，独共南山守中国。\\n几回天上葬神仙，漏声相将无断缘。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1375', 'poem', '唐', '李白', '陪族叔刑部侍郎晔及中书贾舍人至游洞庭五首', '洞庭西望楚江分，水尽南天不见云。\\n日落长沙秋色远，不知何处吊湘君。\\n南湖秋水夜无烟，耐可乘流直上天。\\n且就洞庭赊月色，将船买酒白云边。\\n洛阳才子谪湘川，元礼同舟月下仙。\\n记得长安还欲笑，不知何处是西天。\\n洞庭湖西秋月辉，潇湘江北早鸿飞。\\n醉客满船歌白苎，不知霜露入秋衣。\\n帝子潇湘去不还，空馀秋草洞庭间。\\n淡扫明湖开玉镜，丹青画出是君山。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1376', 'poem', '唐', '杜甫', '绝句漫兴九首', '眼见客愁愁不醒，无赖春色到江亭。\\n即遣花开深造次，便觉莺语太丁宁。\\n手种桃李非无主，野老墙低还似家。\\n恰似春风相欺得，夜来吹折数枝花。\\n熟知茅斋绝低小，江上燕子故来频。\\n衔泥点污琴书内，更接飞虫打着人。\\n二月已破三月来，渐老逢春能几回。\\n莫思身外无穷事，且尽生前有限杯。\\n肠断春江欲尽头，杖藜徐步立芳洲。\\n颠狂柳絮随风去，轻薄桃花逐水流。\\n懒慢无堪不出村，呼儿日在掩柴门。\\n苍苔浊酒林中静，碧水春风野外昏。\\n糁径杨花铺白毡，点溪荷叶叠青钱。\\n笋根稚子无人见，沙上凫雏傍母眠。\\n舍西柔桑叶可拈，江畔细麦复纤纤。\\n人生几何春已夏，不放香醪如蜜甜。\\n隔户杨柳弱袅袅，恰似十五女儿腰。\\n谁谓朝来不作意，狂风挽断最长条。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1377', 'poem', '唐', '李白', '月下独酌其二', '天若不爱酒，酒星不在天。\\n地若不爱酒，地应无酒泉。\\n天地既爱酒，爱酒不愧天。\\n已闻清比圣，复道浊如贤。\\n贤圣既已饮，何必求神仙。\\n三杯通大道，一斗合自然。\\n但得酒中趣，勿为醒者传。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1378', 'poem', '唐', '温庭筠', '杏花', '红花初绽雪花繁，重叠高低满小园。\\n正见盛时犹怅望，岂堪开处已缤翻。\\n情为世累诗千首，醉是吾乡酒一樽。\\n杳杳艳歌春日午，出墙何处隔朱门。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1379', 'poem', '唐', '王昌龄', '重别李评事', '莫道秋江离别难，舟船明日是长安。\\n吴姬缓舞留君醉，随意青枫白露寒。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1380', 'poem', '唐', '李商隐', '重过圣女祠', '白石岩扉碧藓滋，上清沦谪得归迟。\\n一春梦雨常飘瓦，尽日灵风不满旗。\\n萼绿华来无定所，杜兰香去未移时。\\n玉郎会此通仙籍，忆向天阶问紫芝。', '2018-03-09 15:04:31');
INSERT INTO `t_poem` VALUES ('1381', 'poem', '唐', '韩偓', '日高', '朦胧犹记管弦声，噤痄余寒酒半醒。\\n春暮日高帘半卷，落花和雨满中庭。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1382', 'poem', '唐', '刘禹锡', '昼居池上亭独吟', '日午树阴正，独吟池上亭。\\n静看蜂教诲，闲想鹤仪形。\\n法酒调神气，清琴入性灵。\\n浩然机已息，几杖复何铭。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1383', 'poem', '唐', '崔曙', '九日登望仙台呈刘明府容', '汉文皇帝有高台，此日登临曙色开。\\n三晋云山皆北向，二陵风雨自东来。\\n关门令尹谁能识，河上仙翁去不回。\\n且欲近寻彭泽宰，陶然共醉菊花杯。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1384', 'poem', '唐', '汪遵', '咏长城', '秦筑长城比铁牢，蕃戎不敢过临洮。\\n虽然万里连云际，争及尧阶三尺高。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1385', 'poem', '唐', '杜甫', '可叹', '天上浮云如白衣，斯须改变如苍狗。古往今来共一时，\\n人生万事无不有。近者抉眼去其夫，河东女儿身姓柳。\\n丈夫正色动引经，酆城客子王季友。群书万卷常暗诵，\\n孝经一通看在手。贫穷老瘦家卖屐，好事就之为携酒。\\n豫章太守高帝孙，引为宾客敬颇久。闻道三年未曾语，\\n小心恐惧闭其口。太守得之更不疑，人生反覆看亦丑。\\n明月无瑕岂容易，紫气郁郁犹冲斗。时危可仗真豪俊，\\n二人得置君侧否。太守顷者领山南，邦人思之比父母。\\n王生早曾拜颜色，高山之外皆培塿，用为羲和天为成，\\n用平水土地为厚。王也论道阻江湖，李也丞疑旷前后。\\n死为星辰终不灭，致君尧舜焉肯朽。吾辈碌碌饱饭行，\\n风后力牧长回首。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1386', 'poem', '唐', '谢良辅', '状江南·孟冬', '江南孟冬天，荻穗软如绵。\\n绿绢芭蕉裂，黄金橘柚悬。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1387', 'poem', '唐', '白居易', '紫薇花', '紫薇花对紫微翁，名目虽同貌不同。\\n独占芳菲当夏景，不将颜色托春风。\\n浔阳官舍双高树，兴善僧庭一大丛。\\n何似苏州安置处，花堂栏下月明中。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1388', 'poem', '唐', '元稹', '塞马', '塞马倦江渚，今朝神彩生。\\n晓风寒猎猎，乍得草头行。\\n夷狄寝烽候，关河无战声。\\n何由当阵面，从尔四蹄轻。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1389', 'poem', '唐', '柳宗元', '秋晓行南谷经荒村', '杪秋霜露重，晨起行幽谷。\\n黄叶覆溪桥，荒村唯古木。\\n寒花疏寂历，幽泉微断续。\\n机心久已忘，何事惊麋鹿。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1390', 'poem', '唐', '杜牧', '秋梦', '寒空动高吹，月色满清砧。\\n残梦夜魂断，美人边思深。\\n孤鸿秋出塞，一叶暗辞林。\\n又寄征衣去，迢迢天外心。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1391', 'poem', '唐', '张志和', '渔父歌', '西塞山前白鹭飞，桃花流水鳜鱼肥。\\n青箬笠，绿蓑衣，斜风细雨不须归。\\n钓台渔父褐为裘，两两三三舴艋舟。\\n能纵棹，惯乘流，长江白浪不曾忧。\\n霅溪湾里钓渔翁，舴艋为家西复东。\\n江上雪，浦边风，笑著荷衣不叹穷。\\n松江蟹舍主人欢，菰饭莼羹亦共餐。\\n枫叶落，荻花乾，醉宿渔舟不觉寒。\\n青草湖中月正圆，巴陵渔父棹歌连。\\n钓车子，橛头船，乐在风波不用仙。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1392', 'poem', '唐', '郑谷', '越鸟', '背霜南雁不到处，倚棹北人初听时。\\n梅雨满江春草歇，一声声在荔枝枝。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1393', 'poem', '唐', '王维', '晚春严少尹与诸公见过', '松菊荒三径，图书共五车。\\n烹葵邀上客，看竹到贫家。\\n鹊乳先春草，莺啼过落花。\\n自怜黄发暮，一倍惜年华。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1394', 'poem', '唐', '虞世南', '奉和咏风应魏王教', '逐舞飘轻袖，传歌共绕梁。\\n动枝生乱影，吹花送远香。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1395', 'poem', '唐', '韦庄', '与东吴生相遇', '十年身事各如萍，白首相逢泪满缨。\\n老去不知花有态，乱来唯觉酒多情。\\n贫疑陋巷春偏少，贵想豪家月最明。\\n且对一尊开口笑，未衰应见泰阶平。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1396', 'poem', '唐', '钱起', '逢侠者', '燕赵悲歌士，相逢剧孟家。\\n寸心言不尽，前路日将斜。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1397', 'poem', '唐', '戴叔伦', '过三闾庙', '沅湘流不尽，屈宋怨何深。\\n日暮秋烟起，萧萧枫树林。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1398', 'poem', '唐', '窦庠', '醉中赠符载', '白社会中尝共醉，青云路上未相逢。\\n时人莫小池中水，浅处无妨有卧龙。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1399', 'poem', '唐', '贾岛', '三月晦日赠刘评事 / 三月晦日送春', '三月正当三十日，风光别我苦吟身。\\n共君今夜不须睡，未到晓钟犹是春。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1400', 'poem', '唐', '常建', '三日寻李九庄', '雨歇杨林东渡头，永和三日荡轻舟。\\n故人家在桃花岸，直到门前溪水流。', '2018-03-09 15:04:39');
INSERT INTO `t_poem` VALUES ('1401', 'poem', '唐', '白居易', '种白莲', '吴中白藕洛中栽，莫恋江南花懒开。\\n万里携归尔知否，红蕉朱槿不将来。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1402', 'poem', '唐', '李白', '登庐山五老峰', '庐山东南五老峰，青天削出金芙蓉。\\n九江秀色可揽结，吾将此地巢云松。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1403', 'poem', '唐', '韦应物', '池上', '郡中卧病久，池上一来赊。\\n榆柳飘枯叶，风雨倒横查。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1404', 'poem', '唐', '李冶', '八至', '至近至远东西，至深至浅清溪。\\n至高至明日月，至亲至疏夫妻。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1405', 'poem', '唐', '李白', '游水西简郑明府', '天宫水西寺，云锦照东郭。\\n清湍鸣回溪，绿水绕飞阁。\\n凉风日潇洒，幽客时憩泊。\\n五月思貂裘，谓言秋霜落。\\n石萝引古蔓，岸笋开新箨。\\n吟玩空复情，相思尔佳作。\\n郑公诗人秀，逸韵宏寥廓。\\n何当一来游，惬我雪山诺。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1406', 'poem', '唐', '戎昱', '桂州腊夜', '坐到三更尽，归仍万里赊。\\n雪声偏傍竹，寒梦不离家。\\n晓角分残漏，孤灯落碎花。\\n二年随骠骑，辛苦向天涯。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1407', 'poem', '唐', '杜甫', '即事', '百宝装腰带，真珠络臂鞲。\\n笑时花近眼，舞罢锦缠头。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1408', 'poem', '唐', '马戴', '过野叟居', '野人闲种树，树老野人前。\\n居止白云内，渔樵沧海边。\\n呼儿采山药，放犊饮溪泉。\\n自著养生论，无烦忧暮年。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1409', 'poem', '唐', '李隆基', '经邹鲁祭孔子而叹之', '夫子何为者，栖栖一代中。\\n地犹鄹氏邑，宅即鲁王宫。\\n叹凤嗟身否，伤麟怨道穷。\\n今看两楹奠，当与梦时同。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1410', 'poem', '唐', '元稹', '春蝉', '我自东归日，厌苦春鸠声。\\n作诗怜化工，不遣春蝉生。\\n及来商山道，山深气不平。\\n春秋两相似，虫豸百种鸣。\\n风松不成韵，蜩螗沸如羹。\\n岂无朝阳凤，羞与微物争。\\n安得天上雨，奔浑河海倾。\\n荡涤反时气，然后好晴明。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1411', 'poem', '唐', '杜甫', '柏学士茅屋', '碧山学士焚银鱼，白马却走深岩居。\\n古人己用三冬足，年少今开万卷余。\\n晴云满户团倾盖，秋水浮阶溜决渠。\\n富贵必从勤苦得，男儿须读五车书。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1412', 'poem', '唐', '元稹', '三叹', '孤剑锋刃涩，犹能神彩生。\\n有时雷雨过，暗吼阗阗声。\\n主人閟灵宝，畏作升天行。\\n淬砺当阳铁，刻为干镆名。\\n远求鸊鹈莹，同用玉匣盛。\\n颜色纵相类，利钝颇相倾。\\n雄为光电烻，雌但深泓澄。\\n龙怒有奇变，青蛇终不惊。\\n仙凤翠皇死，葳蕤光彩低。\\n非无鸳鸾侣，誓不同树栖。\\n飞驰岁云暮，感念雏在泥。\\n顾影不自暖，寄尔蟠桃鸡。\\n驯养岂无愧，类族安得齐。\\n愿言成羽翼，奋翅凌丹梯。\\n天骥失龙偶，三年常夜嘶。\\n哀缘喷风断，渴且含霜啼。\\n长恐绝遗类，不复蹑云霓。\\n非无駉駉者，鹤意不在鸡。\\n春来筋骨瘦，吊影心亦迷。\\n自此渥洼种，应生浊水泥。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1413', 'poem', '唐', '王维', '欹湖', '吹箫凌极浦，日暮送夫君。\\n湖上一回首，山青卷白云。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1414', 'poem', '唐', '杜甫', '槐叶冷淘', '青青高槐叶，采掇付中厨。\\n新面来近市，汁滓宛相俱。\\n入鼎资过熟，加餐愁欲无。\\n碧鲜俱照箸，香饭兼苞芦。\\n经齿冷于雪，劝人投此珠。\\n愿随金騕褭，走置锦屠苏。\\n路远思恐泥，兴深终不渝。\\n献芹则小小，荐藻明区区。\\n万里露寒殿，开冰清玉壶。\\n君王纳凉晚，此味亦时须。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1415', 'poem', '唐', '刘禹锡', '海阳十咏·双溪', '流水绕双岛，碧溪相并深。\\n浮花拥曲处，远影落中心。\\n闲鹭久独立，曝龟惊复沉。\\n蘋风有时起，满谷箫韶音。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1416', 'poem', '唐', '张籍', '蓟北旅思 / 送远人', '日日望乡国，空歌白苎词。\\n长因送人处，忆得别家时。\\n失意还独语，多愁只自知。\\n客亭门外柳，折尽向南枝。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1417', 'poem', '唐', '张说', '送梁六自洞庭山作', '巴陵一望洞庭秋，日见孤峰水上浮。\\n闻道神仙不可接，心随湖水共悠悠。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1418', 'poem', '唐', '林滋', '春望', '春海镜长天，青郊丽上年。\\n林光虚霁晓，山翠薄晴烟。\\n气暖禽声变，风恬草色鲜。\\n散襟披石磴，韶景自深怜。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1419', 'poem', '唐', '罗隐', '北邙山', '一种山前路入秦，嵩山堪爱此伤神。\\n魏明未死虚留意，庄叟虽生酌满巾。\\n何必更寻无主骨，也知曾有弄权人。\\n羡他缑岭吹箫客，闲访云头看俗尘。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1420', 'poem', '唐', '吴商浩', '北邙山', '北邙山草又青青，今日销魂事可明。\\n绿酒醉来春未歇，白杨风起柳初晴。\\n冈原旋葬松新长，年代无人阙半平。\\n堪取金炉九还药，不能随梦向浮生。', '2018-03-09 15:04:50');
INSERT INTO `t_poem` VALUES ('1421', 'poem', '唐', '王维', '寒食城东即事', '清溪一道穿桃李，演漾绿蒲涵白芷。\\n溪上人家凡几家，落花半落东流水。\\n蹴鞠屡过飞鸟上，秋千竞出垂杨里。\\n少年分日作遨游，不用清明兼上巳。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1422', 'poem', '唐', '司空图', '王官二首', '风荷似醉和花舞，沙鸟无情伴客闲。\\n总是此中皆有恨，更堪微雨半遮山。\\n荷塘烟罩小斋虚，景物皆宜入画图。\\n尽日无人只高卧，一双白鸟隔纱厨。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1423', 'poem', '唐', '殷尧藩', '帝京二首', '列郡征才起俊髦，万机独使圣躬劳。\\n开藩上相颁龙节，破虏将军展豹韬。\\n地入黄图三辅壮，天垂华盖七星高。\\n迎春别赐瑶池宴，捧进金盘五色桃。\\n龙虎山河御气通，遥瞻帝阙五云红。\\n英雄尽入江东籍，将相多收蓟北功。\\n礼乐日稽三代盛，梯航岁贡万方同。\\n都将俭德熙文治，淳俗应还太古风。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1424', 'poem', '唐', '李贺', '钓鱼诗', '秋水钓红渠，仙人待素书。\\n菱丝萦独茧，蒲米蛰双鱼。\\n斜竹垂清沼，长纶贯碧虚。\\n饵悬春蜥蜴，钩坠小蟾蜍。\\n詹子情无限，龙阳恨有馀。\\n为看烟浦上，楚女泪沾裾。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1425', 'poem', '唐', '白居易', '放言五首·其五', '泰山不要欺毫末，颜子无心羡老彭。\\n松树千年终是朽，槿花一日自为荣。\\n何须恋世常忧死，亦莫嫌身漫厌生。\\n生去死来都是幻，幻人哀乐系何情。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1426', 'poem', '唐', '李白', '西施 / 咏苎萝山', '西施越溪女，出自苎萝山。\\n秀色掩今古，荷花羞玉颜。\\n浣纱弄碧水，自与清波闲。\\n皓齿信难开，沉吟碧云间。\\n勾践徵绝艳，扬蛾入吴关。\\n提携馆娃宫，杳渺讵可攀。\\n一破夫差国，千秋竟不还。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1427', 'poem', '唐', '窦常', '北固晚眺', '水国芒种后，梅天风雨凉。\\n露蚕开晚簇，江燕绕危樯。\\n山趾北来固，潮头西去长。\\n年年此登眺，人事几销亡。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1428', 'poem', '唐', '崔珏', '有赠', '莫道妆成断客肠，粉胸绵手白莲香。\\n烟分顶上三层绿，剑截眸中一寸光。\\n舞胜柳枝腰更软，歌嫌珠贯曲犹长。\\n虽然不似王孙女，解爱临邛卖赋郎。\\n锦里芬芳少佩兰，风流全占似君难。\\n心迷晓梦窗犹暗，粉落香肌汗未干。\\n两脸夭桃从镜发，一眸春水照人寒。\\n自嗟此地非吾土，不得如花岁岁看。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1429', 'poem', '唐', '司空图', '河湟有感', '一自萧关起战尘，河湟隔断异乡春。\\n汉儿尽作胡儿语，却向城头骂汉人。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1430', 'poem', '唐', '刘禹锡', '踏歌词四首·其一', '春江月出大堤平，堤上女郎连袂行。\\n唱尽新词欢不见，红霞映树鹧鸪鸣。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1431', 'poem', '唐', '李世民', '咏雪', '洁野凝晨曜，装墀带夕晖。\\n集条分树玉，拂浪影泉玑。\\n色洒妆台粉，花飘绮席衣。\\n入扇萦离匣，点素皎残机。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1432', 'poem', '唐', '韦庄', '江外思乡', '年年春日异乡悲，杜曲黄莺可得知。\\n更被夕阳江岸上，断肠烟柳一丝丝。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1433', 'poem', '唐', '白居易', '四十五', '行年四十五，雨鬓半苍苍。\\n清瘦诗成癖，粗豪酒放狂。\\n老来尤委命，安处即为乡。\\n或拟庐山下，来春结草堂。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1434', 'poem', '唐', '皮日休', '奉酬鲁望夏日四声四首·平声', '塘平芙蓉低，庭闲梧桐高。\\n清烟埋阳乌，蓝空含秋毫。\\n冠倾慵移簪，杯干将餔糟。\\n翛然非随时，夫君真吾曹。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1435', 'poem', '唐', '陆龟蒙', '奉酬袭美苦雨四声重寄三十二句·平声', '幽栖眠疏窗，豪居凭高楼。\\n浮沤惊跳丸，寒声思重裘。\\n床前垂文竿，巢边登轻舟。\\n虽无东皋田，还生鱼乎忧。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1436', 'poem', '唐', '陆龟蒙', '夏日闲居作四声诗寄袭美·平声', '荒池菰蒲深，闲阶莓苔平。\\n江边松篁多，人家帘栊清。\\n为书凌遗编，调弦夸新声。\\n求欢虽殊途，探幽聊怡情。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1437', 'poem', '唐', '张籍', '西州', '羌胡据西州，近甸无边城。\\n山东收税租，养我防塞兵。\\n胡骑来无时，居人常震惊。\\n嗟我五陵间，农者罢耘耕。\\n边头多杀伤，士卒难全形。\\n郡县发丁役，丈夫各征行。\\n生男不能养，惧身有姓名。\\n良马不念秣，烈士不苟营。\\n所愿除国难，再逢天下平。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1438', 'poem', '唐', '皮日休', '苦雨中又作四声诗寄鲁望·平声', '涔涔将经旬，昏昏空迷天。\\n鸬鹚成群嬉，芙蓉相偎眠。\\n鱼通蓑衣城，帆过菱花田。\\n秋收吾无望，悲之真徒然。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1439', 'poem', '唐', '耿湋', '之江淮留别京中亲故', '长云迷一雁，渐远向南声。\\n已带千霜鬓，初为万里行。\\n繁虫满夜草，连雨暗秋城。\\n前路诸侯贵，何人重客卿。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1440', 'poem', '唐', '白居易', '二月二日', '二月二日新雨晴，草芽菜甲一时生。\\n轻衫细马春年少，十字津头一字行。', '2018-03-09 15:04:57');
INSERT INTO `t_poem` VALUES ('1441', 'poem', '唐', '高适', '和王七玉门关听吹笛 / 塞上闻笛', '胡人吹笛戍楼间，楼上萧条海月闲。\\n借问落梅凡几曲，从风一夜满关山。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1442', 'poem', '唐', '皮日休', '读书', '家资是何物，积帙列梁梠。\\n高斋晓开卷，独共圣人语。\\n英贤虽异世，自古心相许。\\n案头见蠹鱼，犹胜凡俦侣。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1443', 'poem', '唐', '刘商', '白沙宿窦常宅观妓', '扬子澄江映晚霞，柳条垂岸一千家。\\n主人留客江边宿，十月繁霜见杏花。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1444', 'poem', '唐', '无可', '菊', '东篱摇落后，密艳被寒催。\\n夹雨惊新拆，经霜忽尽开。\\n野香盈客袖，禁蕊泛天杯。\\n不共春兰并，悠扬远蝶来。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1445', 'poem', '唐', '徐夤', '恨', '事与时违不自由，如烧如刺寸心头。\\n乌江项籍忍归去，雁塞李陵长系留。\\n燕国飞霜将破夏，汉宫纨扇岂禁秋。\\n须知入骨难销处，莫比人间取次愁。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1446', 'poem', '唐', '张说', '和尹从事懋泛洞庭', '平湖一望上连天，林景千寻下洞泉。\\n忽惊水上光华满，疑是乘舟到日边。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1447', 'poem', '唐', '祖咏', '苏氏别业', '别业居幽处，到来生隐心。\\n南山当户牖，沣水映园林。\\n屋覆经冬雪，庭昏未夕阴。\\n寥寥人境外，闲坐听春禽。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1448', 'poem', '唐', '杜甫', '禹庙', '禹庙空山里，秋风落日斜。\\n荒庭垂橘柚，古屋画龙蛇。\\n云气生虚壁，江声走白沙。\\n早知乘四载，疏凿控三巴。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1449', 'poem', '唐', '王维', '杂诗', '双燕初命子，五桃新作花。\\n王昌是东舍，宋玉次西家。\\n小小能织绮，时时出浣纱。\\n亲劳使君问，南陌驻香车。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1450', 'poem', '唐', '王维', '酬郭给事 / 赠郭给事', '洞门高阁霭馀辉，桃李阴阴柳絮飞。\\n禁里疏钟官舍晚，省中啼鸟吏人稀。\\n晨摇玉佩趋金殿，夕奉天书拜琐闱。\\n强欲从君无那老，将因卧病解朝衣。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1451', 'poem', '唐', '刘商', '酬问师', '虚空无处所，仿佛似琉璃。\\n诗境何人到，禅心又过诗。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1452', 'poem', '唐', '白居易', '别元九后咏所怀', '零落桐叶雨，萧条槿花风。\\n悠悠早秋意，生此幽闲中。\\n况与故人别，中怀正无悰。\\n勿云不相送，心到青门东。\\n相知岂在多，但问同不同。\\n同心一人去，坐觉长安空。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1453', 'poem', '唐', '杜甫', '后游', '寺忆新游处，桥怜再渡时。\\n江山如有待，花柳更无私。\\n野润烟光薄，沙暄日色迟。\\n客愁全为减，舍此复何之。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1454', 'poem', '唐', '胡曾', '咏史诗·青门', '汉皇提剑灭咸秦，亡国诸侯尽是臣。\\n唯有东陵守高节，青门甘作种瓜人。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1455', 'poem', '唐', '张旭', '柳', '濯濯烟条拂地垂，城边楼畔结春思。\\n请君细看风流意，未减灵和殿里时。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1456', 'poem', '唐', '郭元振', '杂曲歌辞·春江曲', '江水春沉沉，上有双竹林。\\n竹叶坏水色，郎亦坏人心。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1457', 'poem', '唐', '杜甫', '奉酬李都督表丈早春作', '力疾坐清晓，来时悲早春。\\n转添愁伴客，更觉老随人。\\n红入桃花嫩，青归柳叶新。\\n望乡应未已，四海尚风尘。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1458', 'poem', '唐', '杜荀鹤', '赠质上人', '枿坐云游出世尘，兼无瓶钵可随身。\\n逢人不说人间事，便是人间无事人。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1459', 'poem', '唐', '白居易', '齐物二首', '青松高百尺，绿蕙低数寸。\\n同生大块间，长短各有分。\\n长者不可退，短者不可进。\\n若用此理推，穷通两无闷。\\n椿寿八千春，槿花不经宿。\\n中间复何有，冉冉孤生竹。\\n竹身三年老，竹色四时绿。\\n虽谢椿有馀，犹胜槿不足。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1460', 'poem', '唐', '王勃', '春庄', '山中兰叶径，城外李桃园。\\n岂知人事静，不觉鸟声喧。', '2018-03-09 15:05:08');
INSERT INTO `t_poem` VALUES ('1461', 'poem', '唐', '陆龟蒙', '秘色越器', '九秋风露越窑开，夺得千峰翠色来。\\n好向中宵盛沆瀣，共嵇中散斗遗杯。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1462', 'poem', '唐', '白居易', '寄湘灵', '泪眼凌寒冻不流，每经高处即回头。\\n遥知别后西楼上，应凭栏干独自愁。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1463', 'poem', '唐', '史青', '应诏赋得除夜', '今岁今宵尽，明年明日催。\\n寒随一夜去，春逐五更来。\\n气色空中改，容颜暗里回。\\n风光人不觉，已著后园梅。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1464', 'poem', '唐', '李煜', '挽辞', '珠碎眼前珍，花凋世外春。\\n未销心里恨，又失掌中身。\\n玉笥犹残药，香奁已染尘。\\n前哀将后感，无泪可沾巾。\\n艳质同芳树，浮危道略同。\\n正悲春落实，又苦雨伤丛。\\n秾丽今何在，飘零事已空。\\n沉沉无问处，千载谢东风。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1465', 'poem', '唐', '王维', '送邢桂州', '铙吹喧京口，风波下洞庭。赭圻将赤岸，击汰复扬舲，\\n日落江湖白，潮来天地青。明珠归合浦，应逐使臣星。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1466', 'poem', '唐', '杜甫', '绝句', '江边踏青罢，回首见旌旗。\\n风起春城暮，高楼鼓角悲。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1467', 'poem', '唐', '王昌龄', '从军行二首', '向夕临大荒，朔风轸归虑。\\n平沙万里馀，飞鸟宿何处。\\n虏骑猎长原，翩翩傍河去。\\n边声摇白草，海气生黄雾。\\n百战苦风尘，十年履霜露。\\n虽投定远笔，未坐将军树。\\n早知行路难，悔不理章句。\\n秋草马蹄轻，角弓持弦急。\\n去为龙城战，正值胡兵袭。\\n军气横大荒，战酣日将入。\\n长风金鼓动，白露铁衣湿。\\n四起愁边声，南庭时伫立。\\n断蓬孤自转，寒雁飞相及。\\n万里云沙涨，平原冰霰涩。\\n惟闻汉使还，独向刀环泣。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1468', 'poem', '唐', '李白', '宿巫山下', '昨夜巫山下，猿声梦里长。\\n桃花飞绿水，三月下瞿塘。\\n雨色风吹去，南行拂楚王。\\n高丘怀宋玉，访古一沾裳。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1469', 'poem', '唐', '孟浩然', '清明日宴梅道士房 / 宴梅道士山房', '林卧愁春尽，开轩览物华。\\n忽逢青鸟使，邀入赤松家。\\n丹灶初开火，仙桃正落花。\\n童颜若可驻，何惜醉流霞。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1470', 'poem', '唐', '白居易', '勤政楼西老柳', '半朽临风树，多情立马人。\\n开元一株柳，长庆二年春。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1471', 'poem', '唐', '方干', '元日·晨鸡两遍报', '晨鸡两遍报更阑，刁斗无声晓漏干。\\n暖日映山调正气，东风入树舞残寒。\\n轩车欲识人间感，献岁须来帝里看。\\n才酌屠苏定年齿，坐中惟笑鬓毛斑。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1472', 'poem', '唐', '孙元晏', '陈·狎客', '八宫妃尽赋篇章，风揭歌声锦绣香。\\n选得十人为狎客，有谁能解谏君王。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1473', 'poem', '唐', '李商隐', '如有', '如有瑶台客，相难复索归。\\n芭蕉开绿扇，菡萏荐红衣。\\n浦外传光远，烟中结响微。\\n良宵一寸焰，回首是重帏。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1474', 'poem', '唐', '张说', '晦日', '晦日嫌春浅，江浦看湔衣。\\n道傍花欲合，枝上鸟犹稀。\\n共忆浮桥晚，无人不醉归。\\n寄书题此日，雁过洛阳飞。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1475', 'poem', '唐', '孟迟', '新安故关', '汉帝英雄重武材，崇山险处凿门开。\\n如今更有将军否，移取潼关向北来。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1476', 'poem', '唐', '韩愈', '送桂州严大夫同用南字', '苍苍森八桂，兹地在湘南。\\n江作青罗带，山如碧玉篸。\\n户多输翠羽，家自种黄甘。\\n远胜登仙去，飞鸾不假骖。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1477', 'poem', '唐', '赵嘏', '江楼有感', '独上江楼思悄然，月光如水水如天。\\n同来玩月人何在，风景依稀似去年。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1478', 'poem', '唐', '王勃', '深湾夜宿', '津涂临巨壑，村宇架危岑。\\n堰绝滩声隐，风交树影深。\\n江童暮理楫，山女夜调砧。\\n此时故乡远，宁知游子心。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1479', 'poem', '唐', '张祜', '折杨柳枝二首', '莫折宫前杨柳枝，玄宗曾向笛中吹。\\n伤心日暮烟霞起，无限春愁生翠眉。\\n凝碧池边敛翠眉，景阳楼下绾青丝。\\n那胜妃子朝元阁，玉手和烟弄一枝。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1480', 'poem', '唐', '杜荀鹤', '马上行', '五里复五里，去时无住时。\\n日将家渐远，犹恨马行迟。', '2018-03-09 15:05:18');
INSERT INTO `t_poem` VALUES ('1481', 'poem', '唐', '孟郊', '结交', '铸镜须青铜，青铜易磨拭。\\n结交远小人，小人难姑息。\\n铸镜图鉴微，结交图相依。\\n凡铜不可照，小人多是非。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1482', 'poem', '唐', '刘驾', '春台', '台上树阴合，台前流水多。\\n青春不出门，坐见野田花。\\n谁能学公子，走马逐香车。\\n六街尘满衣，鼓绝方还家。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1483', 'poem', '唐', '温庭筠', '清明日', '清娥画扇中，春树郁金红。\\n出犯繁花露，归穿弱柳风。\\n马骄偏避幰，鸡骇乍开笼。\\n柘弹何人发，黄鹂隔故宫。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1484', 'poem', '唐', '皇甫冉', '送陆鸿渐栖霞寺采茶', '采茶非采菉，远远上层崖。\\n布叶春风暖，盈筐白日斜。\\n旧知山寺路，时宿野人家。\\n借问王孙草，何时泛碗花。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1485', 'poem', '唐', '李白', '紫骝马', '紫骝行且嘶，双翻碧玉蹄。\\n临流不肯渡，似惜锦障泥。\\n白雪关山远，黄云海戍迷。\\n挥鞭万里去，安得念春闺。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1486', 'poem', '唐', '张祜', '杂曲歌辞·杨柳枝', '莫折宫前杨柳枝，玄宗曾向笛中吹。\\n伤心日暮烟霞起，无限春愁生翠眉。\\n疑碧池边敛翠眉，景阳台下绾青丝。\\n那胜妃子朝元阁，玉手和烟弄一枝。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1487', 'poem', '唐', '杜甫', '徒步归行', '明公壮年值时危，经济实藉英雄姿。\\n国之社稷今若是，武定祸乱非公谁。\\n凤翔千官且饱饭，衣马不复能轻肥。\\n青袍朝士最困者，白头拾遗徒步归。\\n人生交契无老少，论交何必先同调。\\n妻子山中哭向天，须公枥上追风骠。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1488', 'poem', '唐', '韩翃', '酬程延秋夜即事见赠', '长簟迎风早，空城澹月华。\\n星河秋一雁，砧杵夜千家。\\n节候看应晚，心期卧亦赊。\\n向来吟秀句，不觉已鸣鸦。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1489', 'poem', '唐', '贾岛', '新年', '嗟以龙钟身，如何岁复新。\\n石门思隐久，铜镜强窥频。\\n花发新移树，心知故国春。\\n谁能平此恨，岂是北宗人。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1490', 'poem', '唐', '曹邺', '庭草', '庭草根自浅，造化无遗功。\\n低回一寸心，不敢怨春风。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1491', 'poem', '唐', '薛逢', '宫词', '十二楼中尽晓妆，望仙楼上望君王。\\n锁衔金兽连环冷，水滴铜龙昼漏长。\\n云髻罢梳还对镜，罗衣欲换更添香。\\n遥窥正殿帘开处，袍袴宫人扫御床。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1492', 'poem', '唐', '张仲素', '春闺思', '袅袅城边的垂柳，青青路旁的柔桑。\\n提篮忘了把桑采，凝思昨夜梦渔阳。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1493', 'poem', '唐', '贾弇', '状江南·孟夏', '江南孟夏天，慈竹笋如编。\\n蜃气为楼阁，蛙声作管弦。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1494', 'poem', '唐', '刘禹锡', '赠李司空妓', '高髻云鬟宫样妆，春风一曲杜韦娘。\\n司空见惯浑闲事，断尽苏州刺史肠。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1495', 'poem', '唐', '李端', '芜城', '昔人登此地，丘陇已前悲。\\n今日又非昔，春风能几时。\\n风吹城上树，草没城边路。\\n城里月明时，精灵自来去。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1496', 'poem', '唐', '同谷子', '五子之歌', '邦惟固本自安宁，临下常须驭朽惊。\\n何事十旬游不返，祸胎从此召殷兵。\\n酒色声禽号四荒，那堪峻宇又雕墙。\\n静思今古为君者，未或因兹不灭亡。\\n唯彼陶唐有冀方，少年都不解思量。\\n如今算得当时事，首为盘游乱纪纲。\\n明明我祖万邦君，典则贻将示子孙。\\n惆怅太康荒坠后，覆宗绝祀灭其门。\\n仇雠万姓遂无依，颜厚何曾解忸怩。\\n五子既歌邦已失，一场前事悔难追。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1497', 'poem', '唐', '薛能', '杏花', '活色生香第一流，手中移得近青楼。\\n谁知艳性终相负，乱向春风笑不休。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1498', 'poem', '唐', '岑参', '送人赴安西', '上马带吴钩，翩翩度陇头。\\n小来思报国，不是爱封侯。\\n万里乡为梦，三边月作愁。\\n早须清黠虏，无事莫经秋。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1499', 'poem', '唐', '韩琮', '凉州词', '树发花如锦，莺啼柳若丝。\\n更游欢宴地，悲见别离时。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1500', 'poem', '唐', '王昌龄', '西宫秋怨', '芙蓉不及美人妆，水殿风来珠翠香。\\n谁分含啼掩秋扇，空悬明月待君王。', '2018-03-09 15:05:30');
INSERT INTO `t_poem` VALUES ('1501', 'poem', '唐', '胡曾', '咏史诗·鸿门', '项籍鹰扬六合晨，鸿门开宴贺亡秦。\\n樽前若取谋臣计，岂作阴陵失路人。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1502', 'poem', '唐', '李白', '嘲王历阳不肯饮酒', '地白风色寒，雪花大如手。\\n笑杀陶渊明，不饮杯中酒。\\n浪抚一张琴，虚栽五株柳。\\n空负头上巾，吾于尔何有。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1503', 'poem', '唐', '张蠙', '夏日题老将林亭', '百战功成翻爱静，侯门渐欲似仙家。\\n墙头雨细垂纤草，水面风回聚落花。\\n井放辘轳闲浸酒，笼开鹦鹉报煎茶。\\n几人图在凌烟阁，曾不交锋向塞沙？', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1504', 'poem', '唐', '白居易', '酬和元九东川路诗十二首·南秦雪', '往岁曾为西邑吏，惯从骆口到南秦。\\n三时云冷多飞雪，二月山寒少有春。\\n我思旧事犹惆怅，君作初行定苦辛。\\n仍赖愁猿寒不叫，若闻猿叫更愁人。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1505', 'poem', '唐', '白居易', '秋日', '池残寥落水，窗下悠扬日。\\n袅袅秋风多，槐花半成实。\\n下有独立人，年来四十一。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1506', 'poem', '唐', '罗邺', '梅花', '繁如瑞雪压枝开，越岭吴溪免用栽。\\n却是五侯家未识，春风不放过江来。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1507', 'poem', '唐', '温庭筠', '题卢处士山居 / 处士卢岵山居', '西溪问樵客，遥识楚人家。\\n古树老连石，急泉清露沙。\\n千峰随雨暗，一径入云斜。\\n日暮飞鸦集，满山荞麦花。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1508', 'poem', '唐', '李白', '独不见', '白马谁家子，黄龙边塞儿。\\n天山三丈雪，岂是远行时。\\n春蕙忽秋草，莎鸡鸣西池。\\n风摧寒棕响，月入霜闺悲。\\n忆与君别年，种桃齐蛾眉。\\n桃今百馀尺，花落成枯枝。\\n终然独不见，流泪空自知。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1509', 'poem', '唐', '李白', '春怨', '白马金羁辽海东，罗帷绣被卧春风。\\n落月低轩窥烛尽，飞花入户笑床空。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1510', 'poem', '唐', '贯休', '陋巷', '坠叶如花欲满沟，破篱荒井一蝉幽。\\n亦知希骥无希者，作么令人强转头。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1511', 'poem', '唐', '陈玉兰', '寄外征衣', '夫戍边关妾在吴，西风吹妾妾忧夫。\\n一行书信千行泪，寒到君边衣到无？', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1512', 'poem', '唐', '无名氏', '胡笳曲', '月明星稀霜满野，毡车夜宿阴山下。\\n汉家自失李将军，单于公然来牧马。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1513', 'poem', '唐', '严恽', '落花', '春光冉冉归何处，更向花前把一杯。\\n尽日问花花不语，为谁零落为谁开。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1514', 'poem', '唐', '郑谷', '山鸟', '惊飞失势粉墙高，好个声音好羽毛。\\n小婢不须催柘弹，且从枝上吃樱桃。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1515', 'poem', '唐', '杜甫', '成都府', '翳翳桑榆日，照我征衣裳。\\n我行山川异，忽在天一方。\\n但逢新人民，未卜见故乡。\\n大江东流去，游子去日长。\\n曾城填华屋，季冬树木苍。\\n喧然名都会，吹箫间笙簧。\\n信美无与适，侧身望川梁。\\n鸟雀夜各归，中原杳茫茫。\\n初月出不高，众星尚争光。\\n自古有羁旅，我何苦哀伤。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1516', 'poem', '唐', '章孝标', '咏弓', '较量武艺论勋庸，曾发将军箭落鸿。\\n握内从夸弯似月，眼前还怕撇来风。\\n只知击起穿雕镞，不解容和射鹄功。\\n得病自从杯里后，至今形状怕相逢。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1517', 'poem', '唐', '柴宿', '瑜不掩瑕', '朗玉微瑕在，分明异璞瑜。\\n坚贞宁可杂，美恶自能殊。\\n待价知弥久，称忠定不诬。\\n光辉今见黜，毫发外呈符。\\n岂假良工指，堪为达士模。\\n他山傥磨琢，慕爱是洪炉。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1518', 'poem', '唐', '许浑', '汴河亭', '广陵花盛帝东游，先劈昆仑一派流。\\n百二禁兵辞象阙，三千宫女下龙舟。\\n凝云鼓震星辰动，拂浪旗开日月浮。\\n四海义师归有道，迷楼还似景阳楼。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1519', 'poem', '唐', '张籍', '春别曲', '长江春水绿堪染，莲叶出水大如钱。\\n江头橘树君自种，那不长系木兰船。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1520', 'poem', '唐', '韦庄', '鹧鸪', '南禽无侣似相依，锦翅双双傍马飞。\\n孤竹庙前啼暮雨，汨罗祠畔吊残晖。\\n秦人只解歌为曲，越女空能画作衣。\\n懊恼泽家非有恨，年年长忆凤城归。', '2018-03-09 15:05:37');
INSERT INTO `t_poem` VALUES ('1521', 'poem', '唐', '韩偓', '船头', '两岸绿芜齐似翦，掩映云山相向晚。\\n船头独立望长空，日艳波光逼人眼。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1522', 'poem', '唐', '李白', '上三峡', '巫山夹青天，巴水流若兹。\\n巴水忽可尽，青天无到时。\\n三朝上黄牛，三暮行太迟。\\n三朝又三暮，不觉鬓成丝。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1523', 'poem', '唐', '杜荀鹤', '再经胡城县', '去岁曾经此县城，县民无口不冤声。\\n今来县宰加朱绂，便是生灵血染成。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1524', 'poem', '唐', '杜甫', '即事', '暮春三月巫峡长，皛皛行云浮日光。\\n雷声忽送千峰雨，花气浑如百和香。\\n黄莺过水翻回去，燕子衔泥湿不妨。\\n飞阁卷帘图画里，虚无只少对潇湘。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1525', 'poem', '唐', '柳宗元', '读书', '幽沉谢世事，俯默窥唐虞。\\n上下观古今，起伏千万途。\\n遇欣或自笑，感戚亦以吁。\\n缥帙各舒散，前后互相逾。\\n瘴痾扰灵府，日与往昔殊。\\n临文乍了了，彻卷兀若无。\\n竟夕谁与言，但与竹素俱。\\n倦极便倒卧，熟寐乃一苏。\\n欠伸展肢体，吟咏心自愉。\\n得意适其适，非愿为世儒。\\n道尽即闭口，萧散捐囚拘。\\n巧者为我拙，智者为我愚。\\n书史足自悦，安用勤与劬。\\n贵尔六尺躯，勿为名所驱。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1526', 'poem', '唐', '贾岛', '送无可上人', '圭峰霁色新，送此草堂人。\\n麈尾同离寺，蛩鸣暂别亲。\\n独行潭底影，数息树边身。\\n终有烟霞约，天台作近邻。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1527', 'poem', '唐', '韩偓', '再思', '暴殄犹来是片时，无人向此略迟疑。\\n流金铄石玉长润，败柳凋花松不知。\\n但保行藏天是证，莫矜纤巧鬼难欺。\\n近来更得穷经力，好事临行亦再思。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1528', 'poem', '唐', '纥干著', '灞上', '鸣鞭晚日禁城东，渭水晴烟灞岸风。\\n都傍柳阴回首望，春天楼阁五云中。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1529', 'poem', '唐', '刘方平', '春雪', '飞雪带春风，裴回乱绕空。\\n君看似花处，偏在洛阳东。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1530', 'poem', '唐', '元稹', '得乐天书', '远信入门先有泪，妻惊女哭问何如。\\n寻常不省曾如此，应是江州司马书。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1531', 'poem', '唐', '贾岛', '雪晴晚望', '倚杖望晴雪，溪云几万重。\\n樵人归白屋，寒日下危峰。\\n野火烧冈草，断烟生石松。\\n却回山寺路，闻打暮天钟。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1532', 'poem', '唐', '崔涂', '灞上', '长安名利路，役役古由今。\\n征骑少闲日，绿杨无旧阴。\\n水侵秦甸阔，草接汉陵深。\\n紫阁曾过处，依稀白鸟沈。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1533', 'poem', '唐', '白居易', '村居苦寒', '八年十二月，五日雪纷纷。\\n竹柏皆冻死，况彼无衣民。\\n回观村闾间，十室八九贫。\\n北风利如剑，布絮不蔽身。\\n唯烧蒿棘火，愁坐夜待晨。\\n乃知大寒岁，农者尤苦辛。\\n顾我当此日，草堂深掩门。\\n褐裘覆紖被，坐卧有馀温。\\n幸免饥冻苦，又无垄亩勤。\\n念彼深可愧，自问是何人。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1534', 'poem', '唐', '王维', '杂诗', '朝因折杨柳，相见洛阳隅。\\n楚国无如妾，秦家自有夫。\\n对人传玉腕，映烛解罗襦。\\n人见东方骑，皆言夫婿殊。\\n持谢金吾子，烦君提玉壶。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1535', 'poem', '唐', '杜甫', '缚鸡行', '小奴缚鸡向市卖，鸡被缚急相喧争。\\n家中厌鸡食虫蚁，不知鸡卖还遭烹。\\n虫鸡于人何厚薄，我斥奴人解其缚。\\n鸡虫得失无了时，注目寒江倚山阁。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1536', 'poem', '唐', '孟郊', '琴曲歌辞·湘妃怨', '南巡竟不返，帝子怨逾积。\\n万里丧蛾眉，潇湘水空碧。\\n冥冥荒山下，古庙收贞魄。\\n乔木深青春，清光满瑶席。\\n搴芳徒有荐，灵意殊脉脉。\\n玉佩不可亲，裴回烟波夕。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1537', 'poem', '唐', '胡曾', '咏史诗·乌江', '争帝图王势已倾，八千兵散楚歌声。\\n乌江不是无船渡，耻向东吴再起兵。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1538', 'poem', '唐', '刘长卿', '碧涧别墅喜皇甫侍御相访', '荒村带返照，落叶乱纷纷。\\n古路无行客，寒山独见君。\\n野桥经雨断，涧水向田分。\\n不为怜同病，何人到白云。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1539', 'poem', '唐', '柳宗元', '与浩初上人同看山寄京华亲故', '海畔尖山似剑芒，秋来处处割愁肠。\\n若为化作身千亿，散向峰头望故乡。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1540', 'poem', '唐', '刘长卿', '穆陵关北逢人归渔阳', '逢君穆陵路，匹马向桑乾。\\n楚国苍山古，幽州白日寒。\\n城池百战后，耆旧几家残。\\n处处蓬蒿遍，归人掩泪看。', '2018-03-09 15:05:48');
INSERT INTO `t_poem` VALUES ('1541', 'poem', '唐', '杜甫', '双燕', '旅食惊双燕，衔泥入此堂。\\n应同避燥湿，且复过炎凉。\\n养子风尘际，来时道路长。\\n今秋天地在，吾亦离殊方。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1542', 'poem', '唐', '齐己', '小松', '发地才过膝，蟠根已有灵。\\n严霜百草白，深院一林青。\\n后夜萧骚动，空阶蟋蟀听。\\n谁于千岁外，吟绕老龙形。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1543', 'poem', '唐', '杨炯', '夜送赵纵', '赵氏连城璧，由来天下传。\\n送君还旧府，明月满前川。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1544', 'poem', '唐', '李白', '早春寄王汉阳', '闻道春还未相识，走傍寒梅访消息。\\n昨夜东风入武阳，陌头杨柳黄金色。\\n碧水浩浩云茫茫，美人不来空断肠。\\n预拂青山一片石，与君连日醉壶觞。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1545', 'poem', '唐', '李白', '白头吟', '锦水东北流，波荡双鸳鸯。雄巢汉宫树，雌弄秦草芳。\\n宁同万死碎绮翼，不忍云间两分张。此时阿娇正娇妒，\\n独坐长门愁日暮。但愿君恩顾妾深，岂惜黄金买词赋。\\n相如作赋得黄金，丈夫好新多异心。一朝将聘茂陵女，\\n文君因赠白头吟。东流不作西归水，落花辞条羞故林。\\n兔丝固无情，随风任倾倒。谁使女萝枝，而来强萦抱。\\n两草犹一心，人心不如草。莫卷龙须席，从他生网丝。\\n且留琥珀枕，或有梦来时。覆水再收岂满杯，\\n弃妾已去难重回。古来得意不相负，只今惟见青陵台。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1546', 'poem', '唐', '杜甫', '绝句六首', '日出篱东水，云生舍北泥。\\n竹高鸣翡翠，沙僻舞鹍鸡。\\n蔼蔼花蕊乱，飞飞蜂蝶多。\\n幽栖身懒动，客至欲如何。\\n凿井交棕叶，开渠断竹根。\\n扁舟轻褭缆，小径曲通村。\\n急雨捎溪足，斜晖转树腰。\\n隔巢黄鸟并，翻藻白鱼跳。\\n舍下笋穿壁，庭中藤刺檐。\\n地晴丝冉冉，江白草纤纤。\\n江动月移石，溪虚云傍花。\\n鸟栖知故道，帆过宿谁家。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1547', 'poem', '唐', '李涉', '井栏砂宿遇夜客', '暮雨潇潇江上村，绿林豪客夜知闻。\\n他时不用逃名姓，世上如今半是君。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1548', 'poem', '唐', '李世民', '赋得樱桃', '华林满芳景，洛阳遍阳春。\\n朱颜含远日，翠色影长津。\\n乔柯啭娇鸟，低枝映美人。\\n昔作园中实，今来席上珍。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1549', 'poem', '唐', '孟浩然', '江上寄山阴崔少府国辅', '春堤杨柳发，忆与故人期。\\n草木本无意，荣枯自有时。\\n山阴定远近，江上日相思。\\n不及兰亭会，空吟祓禊诗。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1550', 'poem', '唐', '薛逢', '凉州词', '昨夜蕃兵报国仇，沙州都护破凉州。\\n黄河九曲今归汉，塞外纵横战血流。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1551', 'poem', '唐', '韦应物', '春思', '野花如雪绕江城，坐见年芳忆帝京。\\n阊阖晓开凝碧树，曾陪鸳鹭听流莺。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1552', 'poem', '唐', '杜甫', '长吟', '江渚翻鸥戏，官桥带柳阴。\\n江飞竞渡日，草见蹋春心。\\n已拨形骸累，真为烂漫深。\\n赋诗歌句稳，不免自长吟。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1553', 'poem', '唐', '李咸用', '石版', '高人好自然，移得它山碧。\\n不磨如版平，大巧非因力。\\n古藓小青钱，尘中看野色。\\n冷倚砌花春，静伴疏篁直。\\n山僧若转头，如逢旧相识。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1554', 'poem', '唐', '白居易', '早兴', '晨光出照屋梁明，初打开门鼓一声。\\n犬上阶眠知地湿，鸟临窗语报天晴。\\n半销宿酒头仍重，新脱冬衣体乍轻。\\n睡觉心空思想尽，近来乡梦不多成。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1555', 'poem', '唐', '贾至', '巴陵夜别王八员外 / 三湘有怀', '柳絮飞时别洛阳，梅花发后到三湘。\\n世情已逐浮云散，离恨空随江水长。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1556', 'poem', '唐', '崔道融', '西施滩', '宰嚭亡吴国，西施陷恶名。\\n浣纱春水急，似有不平声。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1557', 'poem', '唐', '李白', '秋夜板桥浦泛月独酌怀谢脁', '天上何所有，迢迢白玉绳。\\n斜低建章阙，耿耿对金陵。\\n汉水旧如练，霜江夜清澄。\\n长川泻落月，洲渚晓寒凝。\\n独酌板桥浦，古人谁可征。\\n玄晖难再得，洒酒气填膺。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1558', 'poem', '唐', '窦叔向', '夏夜宿表兄话旧', '夜合花开香满庭，夜深微雨醉初醒。\\n远书珍重何曾达，旧事凄凉不可听。\\n去日儿童皆长大，昔年亲友半凋零。\\n明朝又是孤舟别，愁见河桥酒幔青。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1559', 'poem', '唐', '陆龟蒙', '江南二首', '便风船尾香粳熟，细雨层头赤鲤跳。\\n待得江餐闲望足，日斜方动木兰桡。\\n村边紫豆花垂次，岸上红梨叶战初。\\n莫怪烟中重回首，酒家青纻一行书。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1560', 'poem', '唐', '孟浩然', '题大禹寺义公禅房', '义公习禅寂，结宇依空林。\\n户外一峰秀，阶前众壑深。\\n夕阳连雨足，空翠落庭阴。\\n看取莲花净，应知不染心。', '2018-03-09 15:05:55');
INSERT INTO `t_poem` VALUES ('1561', 'poem', '唐', '钱起', '春郊', '水绕冰渠渐有声，气融烟坞晚来明。\\n东风好作阳和使，逢草逢花报发生。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1562', 'poem', '唐', '杜甫', '登兖州城楼', '东郡趋庭日，南楼纵目初。\\n浮云连海岱，平野入青徐。\\n孤嶂秦碑在，荒城鲁殿馀。\\n从来多古意，临眺独踌躇。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1563', 'poem', '唐', '李咸用', '送谭孝廉赴举', '鼓鼙声里寻诗礼，戈戟林间入镐京。\\n好事尽从难处得，少年无向易中轻。\\n也知贵贱皆前定，未见疏慵遂有成。\\n吾道近来稀后进，善开金口答公卿。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1564', 'poem', '唐', '武则天', '腊日宣诏幸上苑 / 催花诗', '明朝游上苑，火速报春知。\\n花须连夜发，莫待晓风吹。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1565', 'poem', '唐', '权德舆', '朝元阁', '缭垣复道上层霄，十月离宫万国朝。\\n胡马忽来清跸去，空馀台殿照山椒。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1566', 'poem', '唐', '司马扎', '宫怨 / 闺怨', '柳色参差掩画楼，晓莺啼送满宫愁。\\n年年花落无人见，空逐春泉出御沟。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1567', 'poem', '唐', '韦应物', '新秋夜寄诸弟', '两地俱秋夕，相望共星河。高梧一叶下，空斋归思多。\\n方用忧人瘼，况自抱微痾，无将别来近，颜鬓已蹉跎。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1568', 'poem', '唐', '孙逖', '宿云门寺阁', '香阁东山下，烟花象外幽。\\n悬灯千嶂夕，卷幔五湖秋。\\n画壁馀鸿雁，纱窗宿斗牛。\\n更疑天路近，梦与白云游。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1569', 'poem', '唐', '杜荀鹤', '自叙', '酒瓮琴书伴病身，熟谙时事乐于贫。\\n宁为宇宙闲吟客，怕作乾坤窃禄人。\\n诗旨未能忘救物，世情奈值不容真。\\n平生肺腑无言处，白发吾唐一逸人。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1570', 'poem', '唐', '白居易', '百花亭', '朱槛在空虚，凉风八月初。\\n山形如岘首，江色似桐庐。\\n佛寺乘船入，人家枕水居。\\n高亭仍有月，今夜宿何如。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1571', 'poem', '唐', '吴融', '杂曲歌辞·水调', '凿河千里走黄沙，浮殿西来动日华。\\n可道新声是亡国，且贪惆怅后庭花。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1572', 'poem', '唐', '杜甫', '归燕', '不独避霜雪，其如俦侣稀。\\n四时无失序，八月自知归。\\n春色岂相访，众雏还识机。\\n故巢傥未毁，会傍主人飞。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1573', 'poem', '唐', '吴融', '水调', '凿河千里走黄沙，沙殿西来动日华。\\n可道新声是亡国，且贪惆怅后庭花。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1574', 'poem', '唐', '崔颢', '雁门胡人歌', '高山代郡东接燕，雁门胡人家近边。\\n解放胡鹰逐塞鸟，能将代马猎秋田。\\n山头野火寒多烧，雨里孤峰湿作烟。\\n闻道辽西无斗战，时时醉向酒家眠。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1575', 'poem', '唐', '王勃', '九日', '九日重阳节，开门有菊花。\\n不知来送酒，若个是陶家。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1576', 'poem', '唐', '白居易', '和友人洛中春感', '莫悲金谷园中月，莫叹天津桥上春。\\n若学多情寻往事，人间何处不伤神。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1577', 'poem', '唐', '周昙', '三国门·后主', '万峰如剑载前来，危阁横空信险哉。\\n对此玄休长叹息，方知刘禅是庸才。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1578', 'poem', '唐', '陆龟蒙', '山僧二首', '山藓几重生草履，涧泉长自满铜瓶。\\n时将如意敲眠虎，遣向林间坐听经。\\n一夏不离苍岛上，秋来频话石城南。\\n思归瀑布声前坐，却把松枝拂旧庵。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1579', 'poem', '唐', '骆宾王', '蓬莱镇', '旅客春心断，边城夜望高。野楼疑海气，白鹭似江涛。\\n结绶疲三入，承冠泣二毛。将飞怜弱羽，欲济乏轻舠，\\n赖有阳春曲，穷愁且代劳。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1580', 'poem', '唐', '温庭筠', '春江花月夜词', '玉树歌阑海云黑，花庭忽作青芜国。\\n秦淮有水水无情，还向金陵漾春色。\\n杨家二世安九重，不御华芝嫌六龙。\\n百幅锦帆风力满，连天展尽金芙蓉。\\n珠翠丁星复明灭，龙头劈浪哀笳发。\\n千里涵空澄水魂，万枝破鼻飘香雪。\\n漏转霞高沧海西，颇黎枕上闻天鸡。\\n鸾弦代雁曲如语，一醉昏昏天下迷。\\n四方倾动烟尘起，犹在浓香梦魂裹。\\n后主荒宫有晓莺，飞来只隔西江水。', '2018-03-09 15:06:06');
INSERT INTO `t_poem` VALUES ('1581', 'poem', '唐', '雍裕之', '柳絮', '无风才到地，有风还满空。\\n缘渠偏似雪，莫近鬓毛生。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1582', 'poem', '唐', '唐彦谦', '咏竹', '醉卧凉阴沁骨清，石床冰簟梦难成。\\n月明午夜生虚籁，误听风声是雨声。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1583', 'poem', '唐', '吴融', '阌乡寓居十首·山僧', '石臼山头有一僧，朝无香积夜无灯。\\n近嫌俗客知踪迹，拟向中方断石层。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1584', 'poem', '唐', '李商隐', '月', '过水穿楼触处明，藏人带树远含清。\\n初生欲缺虚惆怅，未必圆时即有情。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1585', 'poem', '唐', '刘禹锡', '汉寿城春望', '汉寿城边野草春，荒祠古墓对荆榛。\\n田中牧竖烧刍狗，陌上行人看石麟。\\n华表半空经霹雳，碑文才见满埃尘。\\n不知何日东瀛变，此地还成要路津。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1586', 'poem', '唐', '李贺', '马诗二十三首·其二', '腊月草根甜，天街雪似盐。\\n未知口硬软，先拟蒺藜衔。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1587', 'poem', '唐', '景云', '画松', '画松一似真松树，且待寻思记得无。\\n曾在天台山上见，石桥南畔第三株。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1588', 'poem', '唐', '李白', '宫中行乐词八首', '小小生金屋，盈盈在紫微。\\n山花插宝髻，石竹绣罗衣。\\n每出深宫里，常随步辇归。\\n只愁歌舞散，化作彩云飞。\\n柳色黄金嫩，梨花白雪香。\\n玉楼巢翡翠，金殿锁鸳鸯。\\n选妓随雕辇，征歌出洞房。\\n宫中谁第一，飞燕在昭阳。\\n卢橘为秦树，蒲萄出汉宫。\\n烟花宜落日，丝管醉春风。\\n笛奏龙吟水，箫鸣凤下空。\\n君王多乐事，还与万方同。\\n玉树春归日，金宫乐事多。\\n后庭朝未入，轻辇夜相过。\\n笑出花间语，娇来竹下歌。\\n莫教明月去，留著醉嫦娥。\\n绣户香风暖，纱窗曙色新。\\n宫花争笑日，池草暗生春。\\n绿树闻歌鸟，青楼见舞人。\\n昭阳桃李月，罗绮自相亲。\\n今日明光里，还须结伴游。\\n春风开紫殿，天乐下朱楼。\\n艳舞全知巧，娇歌半欲羞。\\n更怜花月夜，宫女笑藏钩。\\n寒雪梅中尽，春风柳上归。\\n宫莺娇欲醉，檐燕语还飞。\\n迟日明歌席，新花艳舞衣。\\n晚来移彩仗，行乐泥光辉。\\n水绿南薰殿，花红北阙楼。\\n莺歌闻太液，凤吹绕瀛洲。\\n素女鸣珠佩，天人弄彩球。\\n今朝风日好，宜入未央游。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1589', 'poem', '唐', '杜牧', '送人', '鸳鸯帐里暖芙蓉，低泣关山几万重。\\n明镜半边钗一股，此生何处不相逢。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1590', 'poem', '唐', '李白', '对酒忆贺监二首', '四明有狂客，风流贺季真。\\n长安一相见，呼我谪仙人。\\n昔好杯中物，翻为松下尘。\\n金龟换酒处，却忆泪沾巾。\\n狂客归四明，山阴道士迎。\\n敕赐镜湖水，为君台沼荣。\\n人亡馀故宅，空有荷花生。\\n念此杳如梦，凄然伤我情。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1591', 'poem', '唐', '栖一', '垓下怀古', '缅想咸阳事可嗟，楚歌哀怨思无涯。\\n八千子弟归何处？万里鸿沟属汉家。\\n弓断阵前争日月，血流垓下定龙蛇。\\n拔山力尽乌江水，今古悠悠空浪花。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1592', 'poem', '唐', '陈子昂', '送客', '故人洞庭去，杨柳春风生。\\n相送河洲晚，苍茫别思盈。\\n白蘋已堪把，绿芷复含荣。\\n江南多桂树，归客赠生平。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1593', 'poem', '唐', '柳宗元', '南涧中题', '秋气集南涧，独游亭午时。\\n回风一萧瑟，林影久参差。\\n始至若有得，稍深遂忘疲。\\n羁禽响幽谷，寒藻舞沦漪。\\n去国魂已远，怀人泪空垂。\\n孤生易为感，失路少所宜。\\n索寞竟何事，徘徊只自知。\\n谁为后来者，当与此心期。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1594', 'poem', '唐', '来鹄', '蚕妇', '晓夕采桑多苦辛，好花时节不闲身。\\n若教解爱繁华事，冻杀黄金屋里人。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1595', 'poem', '唐', '崔怀宝', '忆江南·平生愿', '平生愿，愿作乐中筝。得近玉人纤手子，\\n砑罗裙上放娇声。便死也为荣。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1596', 'poem', '唐', '温庭筠', '过五丈原 / 经五丈原', '铁马云雕久绝尘，柳营高压汉营春。\\n天清杀气屯关右，夜半妖星照渭滨。\\n下国卧龙空寤主，中原逐鹿不由人。\\n象床锦帐无言语，从此谯周是老臣。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1597', 'poem', '唐', '白居易', '六月三日夜闻蝉', '荷香清露坠，柳动好风生。\\n微月初三夜，新蝉第一声。\\n乍闻愁北客，静听忆东京。\\n我有竹林宅，别来蝉再鸣。\\n不知池上月，谁拨小船行。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1598', 'poem', '唐', '白居易', '秋虫', '切切暗窗下，喓喓深草里。\\n秋天思妇心，雨夜愁人耳。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1599', 'poem', '唐', '元结', '橘井', '灵橘无根井有泉，世间如梦又千年。\\n乡园不见重归鹤，姓字今为第几仙。\\n风泠露坛人悄悄，地闲荒径草绵绵。\\n如何蹑得苏君迹，白日霓旌拥上天。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1600', 'poem', '唐', '白居易', '紫薇花', '丝纶阁下文书静，钟鼓楼中刻漏长。\\n独坐黄昏谁是伴，紫薇花对紫微郎。', '2018-03-09 15:06:19');
INSERT INTO `t_poem` VALUES ('1601', 'poem', '唐', '薛逢', '元日田家', '南村晴雪北村梅，树里茅檐晓尽开。\\n蛮榼出门儿妇去，乌龙迎路女郎来。\\n相逢但祝新正寿，对举那愁暮景催。\\n长笑士林因宦别，一官轻是十年回。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1602', 'poem', '唐', '韦庄', '燕来', '去岁辞巢别近邻，今来空讶草堂新。\\n花开对语应相问，不是村中旧主人。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1603', 'poem', '唐', '孟郊', '择友', '兽中有人性，形异遭人隔。\\n人中有兽心，几人能真识。\\n古人形似兽，皆有大圣德。\\n今人表似人，兽心安可测。\\n虽笑未必和，虽哭未必戚。\\n面结口头交，肚里生荆棘。\\n好人常直道，不顺世间逆。\\n恶人巧谄多，非义苟且得。\\n若是效真人，坚心如铁石。\\n不谄亦不欺，不奢复不溺。\\n面无吝色容，心无诈忧惕。\\n君子大道人，朝夕恒的的。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1604', 'poem', '唐', '李白', '地藏菩萨赞', '本心若虚空，清净无一物。\\n焚荡淫怒痴，圆寂了见佛。\\n五彩图圣像，悟真非妄传。\\n扫雪万病尽，爽然清凉天。\\n赞此功德海，永为旷代宣。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1605', 'poem', '唐', '万楚', '骢马', '金络青骢白玉鞍，长鞭紫陌野游盘。\\n朝驱东道尘恒灭，暮到河源日未阑。\\n汗血每随边地苦，蹄伤不惮陇阴寒。\\n君能一饮长城窟，为报天山行路难。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1606', 'poem', '唐', '李益', '宫怨', '露湿晴花春殿香，月明歌吹在昭阳。\\n似将海水添宫漏，共滴长门一夜长。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1607', 'poem', '唐', '王之涣', '九日送别', '蓟庭萧瑟故人稀，何处登高且送归。\\n今日暂同芳菊酒，明朝应作断蓬飞。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1608', 'poem', '唐', '元稹', '落月', '落月沉馀影，阴渠流暗光。\\n蚊声霭窗户，萤火绕屋梁。\\n飞幌翠云薄，新荷清露香。\\n不吟复不寐，竟夕池水傍。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1609', 'poem', '唐', '李商隐', '天涯', '春日在天涯，天涯日又斜。\\n莺啼如有泪，为湿最高花。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1610', 'poem', '唐', '杜甫', '宿江边阁 / 后西阁', '暝色延山径，高斋次水门。\\n薄云岩际宿，孤月浪中翻。\\n鹳鹤追飞静，豺狼得食喧。\\n不眠忧战伐，无力正乾坤。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1611', 'poem', '唐', '杜牧', '登乐游原', '长空澹澹孤鸟没，万古销沉向此中。\\n看取汉家何事业，五陵无树起秋风。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1612', 'poem', '唐', '李白', '相逢行二首·其一', '朝骑五花马，谒帝出银台。\\n秀色谁家子，云车珠箔开。\\n金鞭遥指点，玉勒近迟回。\\n夹毂相借问，疑从天上来。\\n蹙入青绮门，当歌共衔杯。\\n衔杯映歌扇，似月云中见。\\n相见不得亲，不如不相见。\\n相见情已深，未语可知心。\\n胡为守空闺，孤眠愁锦衾。\\n锦衾与罗帏，缠绵会有时。\\n春风正澹荡，暮雨来何迟。\\n愿因三青鸟，更报长相思。\\n光景不待人，须臾发成丝。\\n当年失行乐，老去徒伤悲。\\n持此道密意，毋令旷佳期。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1613', 'poem', '唐', '李白', '寄东鲁二稚子', '吴地桑叶绿，吴蚕已三眠。\\n我家寄东鲁，谁种龟阴田？\\n春事已不及，江行复茫然。\\n南风吹归心，飞堕酒楼前。\\n楼东一株桃，枝叶拂青烟。\\n此树我所种，别来向三年。\\n桃今与楼齐，我行尚未旋。\\n娇女字平阳，折花倚桃边。\\n折花不见我，泪下如流泉。\\n小儿名伯禽，与姊亦齐肩。\\n双行桃树下，抚背复谁怜？\\n念此失次第，肝肠日忧煎。\\n裂素写远意，因之汶阳川。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1614', 'poem', '唐', '白居易', '代鹤', '我本海上鹤，偶逢江南客。\\n感君一顾恩，同来洛阳陌。\\n洛阳寡族类，皎皎唯两翼。\\n貌是天与高，色非日浴白。\\n主人诚可恋，其奈轩庭窄。\\n饮啄杂鸡群，年深损标格。\\n故乡渺何处，云水重重隔。\\n谁念深笼中，七换摩天翮。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1615', 'poem', '唐', '徐凝', '七夕', '一道鹊桥横渺渺，千声玉佩过玲玲。\\n别离还有经年客，怅望不如河鼓星。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1616', 'poem', '唐', '杨汉公', '明月楼', '吴兴城阙水云中，画舫青帘处处通。\\n溪上玉楼楼上月，清光合作水晶宫。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1617', 'poem', '唐', '丘为', '左掖梨花', '冷艳全欺雪，馀香乍入衣。\\n春风且莫定，吹向玉阶飞。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1618', 'poem', '唐', '张继', '闾门即事', '耕夫召募爱楼船，春草青青万项田；试上吴门窥郡郭，清明几处有新烟。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1619', 'poem', '唐', '元稹', '古艳诗二首', '春来频到宋家东，垂袖开怀待好风。\\n莺藏柳暗无人语，惟有墙花满树红。\\n深院无人草树光，娇莺不语趁阴藏。\\n等闲弄水浮花片，流出门前赚阮郎。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1620', 'poem', '唐', '吴融', '情', '依依脉脉两如何，细似轻丝渺似波。\\n月不长圆花易落，一生惆怅为伊多。', '2018-03-09 15:06:24');
INSERT INTO `t_poem` VALUES ('1621', 'poem', '唐', '李世民', '芳兰', '春晖开紫苑，淑景媚兰场。\\n映庭含浅色，凝露泫浮光。\\n日丽参差影，风传轻重香。\\n会须君子折，佩里作芬芳。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1622', 'poem', '唐', '高蟾', '秋', '阳羡溪声冷骇人，洞庭山翠晚凝神。\\n天将金玉为风露，曾为高秋几度贫。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1623', 'poem', '唐', '晁采', '子夜歌十八首', '侬既剪云鬟，郎亦分丝发。\\n觅向无人处，绾作同心结。\\n夜夜不成寐，拥被啼终夕。\\n郎不信侬时，但看枕上迹。\\n何时得成匹，离恨不复牵。\\n金针刺菡萏，夜夜得见莲。\\n相逢逐凉候，黄花忽复香。\\n颦眉腊月露，愁杀未成霜。\\n明窗弄玉指，指甲如水晶。\\n剪之特寄郎，聊当携手行。\\n寄语闺中娘，颜色不常好。\\n含笑对棘实，欢娱须是枣。\\n良会终有时，劝郎莫得怒。\\n姜蘖畏春蚕，要绵须辛苦。\\n醉梦幸逢郎，无奈乌哑哑。\\n中山如有酒，敢借千金价。\\n信使无虚日，玉酝寄盈觥。\\n一年一日雨，底事太多晴。\\n绣房拟会郎，四窗日离离。\\n手自施屏障，恐有女伴窥。\\n相思百馀日，相见苦无期。\\n褰裳摘藕花，要莲敢恨池。\\n金盆盥素手，焚香诵普门。\\n来生何所愿，与郎为一身。\\n花池多芳水，玉杯挹赠郎。\\n避人藏袖里，湿却素罗裳。\\n感郎金针赠，欲报物俱轻。\\n一双连素缕，与郎聊定情。\\n寒风响枯木，通夕不得卧。\\n早起遣问郎，昨宵何以过。\\n得郎日嗣音，令人不可睹。\\n熊胆磨作墨，书来字字苦。\\n轻巾手自制，颜色烂含桃。\\n先怀侬袖里，然后约郎腰。\\n侬赠绿丝衣，郎遗玉钩子。\\n即欲系侬心，侬思著郎体。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1624', 'poem', '唐', '杜甫', '为农', '锦里烟尘外，江村八九家。\\n圆荷浮小叶，细麦落轻花。\\n卜宅从兹老，为农去国赊。\\n远惭句漏令，不得问丹砂。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1625', 'poem', '唐', '贾岛', '戏赠友人', '一日不作诗，心源如废井。\\n笔砚为辘轳，吟咏作縻绠。\\n朝来重汲引，依旧得清冷。\\n书赠同怀人，词中多苦辛。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1626', 'poem', '唐', '白居易', '读老子', '言者不如知者默，此语吾闻于老君。\\n若道老君是知者，缘何自著五千文。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1627', 'poem', '唐', '皎然', '春日杼山寄赠李员外纵', '南山唯与北山邻，古树连拳伴我身。\\n黄鹤有心多不住，白云无事独相亲。\\n闲持竹锡深看水，懒系麻衣出见人。\\n欲掇幽芳聊赠远，郎官那赏石门春。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1628', 'poem', '唐', '柳中庸', '听筝', '抽弦促柱听秦筝，无限秦人悲怨声。\\n似逐春风知柳态，如随啼鸟识花情。\\n谁家独夜愁灯影，何处空楼思月明。\\n更入几重离别恨，江南岐路洛阳城。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1629', 'poem', '唐', '白居易', '西岩山', '千古仙居物象饶，道成丹熟昼升霄。\\n岩前宝磬转松韵，洞口灵池应海潮。\\n崖折百花迟日晚，鹤归清夜唳声遥。\\n登临渐到希夷境，手拂行云度石桥。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1630', 'poem', '唐', '白居易', '江楼晚眺景物鲜奇吟玩成篇寄水部张员外', '澹烟疏雨间斜阳，江色鲜明海气凉。\\n蜃散云收破楼阁，虹残水照断桥梁。\\n风翻白浪花千片，雁点青天字一行。\\n好著丹青图画取，题诗寄与水曹郎。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1631', 'poem', '唐', '李益', '度破讷沙二首·其二 / 塞北行次度破讷沙', '眼见风来沙旋移，经年不省草生时。\\n莫言塞北无春到，总有春来何处知。\\n破讷沙头雁正飞，鸊鹈泉上战初归。\\n平明日出东南地，满碛寒光生铁衣。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1632', 'poem', '唐', '王昌龄', '初日', '初日净金闺，先照床前暖。\\n斜光入罗幕，稍稍亲丝管。\\n云发不能梳，杨花更吹满。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1633', 'poem', '唐', '李白', '送陆判官往琵琶峡', '水国秋风夜，殊非远别时。\\n长安如梦里，何日是归期。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1634', 'poem', '唐', '施肩吾', '同诸隐者夜登四明山', '半夜寻幽上四明，手攀松桂触云行。\\n相呼已到无人境，何处玉箫吹一声。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1635', 'poem', '唐', '温庭筠', '莲花', '绿塘摇滟接星津，轧轧兰桡入白蘋。\\n应为洛神波上袜，至今莲蕊有香尘。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1636', 'poem', '唐', '窦群', '春雨', '昨日偷闲看花了，今朝多雨奈人何。\\n人间尽似逢花雨，莫爱芳菲湿绮罗。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1637', 'poem', '唐', '柳宗元', '柳州城西北隅种柑树', '手种黄柑二百株，春来新叶遍城隅。\\n方同楚客怜皇树，不学荆州利木奴。\\n几岁开花闻喷雪，何人摘实见垂珠。\\n若教坐待成林日，滋味还堪养老夫。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1638', 'poem', '唐', '白居易', '邯郸冬至夜思家', '邯郸驿里逢冬至，抱膝灯前影伴身。\\n想得家中夜深坐，还应说着远行人。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1639', 'poem', '唐', '孟浩然', '送张参明经举兼向泾州觐省', '十五彩衣年，承欢慈母前。\\n孝廉因岁贡，怀橘向秦川。\\n四座推文举，中郎许仲宣。\\n泛舟江上别，谁不仰神仙。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1640', 'poem', '唐', '岑参', '初授官题高冠草堂', '三十始一命，宦情多欲阑。\\n自怜无旧业，不敢耻微官。\\n涧水吞樵路，山花醉药栏。\\n只缘五斗米，辜负一渔竿。', '2018-03-09 15:06:29');
INSERT INTO `t_poem` VALUES ('1641', 'poem', '唐', '王维', '辋川集·柳浪', '分行接绮树，倒影入清漪。\\n不学御沟上，春风伤别离。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1642', 'poem', '唐', '杜甫', '陪裴使君登岳阳楼', '湖阔兼云雾，楼孤属晚晴。\\n礼加徐孺子，诗接谢宣城。\\n雪岸丛梅发，春泥百草生。\\n敢违渔父问，从此更南征。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1643', 'poem', '唐', '白居易', '秋江送客', '秋鸿次第过，哀猿朝夕闻。\\n是日孤舟客，此地亦离群。\\n濛濛润衣雨，漠漠冒帆云。\\n不醉浔阳酒，烟波愁杀人。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1644', 'poem', '唐', '李白', '春日醉起言志', '处世若大梦，胡为劳其生？\\n所以终日醉，颓然卧前楹。\\n觉来眄庭前，一鸟花间鸣。\\n借问此何时？春风语流莺。\\n感之欲叹息，对酒还自倾。\\n浩歌待明月，曲尽已忘情。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1645', 'poem', '唐', '湘中女子', '题玉泉溪', '红树醉秋色，碧溪弹夜弦。\\n佳期不可再，风雨杳如年。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1646', 'poem', '唐', '戴叔伦', '山行', '山行分曙色，一路见人稀。\\n野鸟啼还歇，林花堕不飞。\\n云迷栖鹤寺，水涩钓鱼矶。\\n回首天将暝，逢僧话未归。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1647', 'poem', '唐', '鲍溶', '越女词', '越女芙蓉妆，浣纱清浅水。\\n忽惊春心晓，不敢思君子。\\n君子纵我思，宁来浣溪里。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1648', 'poem', '唐', '顾云', '咏柳二首', '带露含烟处处垂，绽黄摇绿嫩参差。\\n长堤未见风飘絮，广陌初怜日映丝。\\n斜傍画筵偷舞态，低临妆阁学愁眉。\\n离亭不放到春暮，折尽拂檐千万枝。\\n闲花野草总争新，眉皱丝干独不匀。\\n乞取东风残气力，莫教虚度一年春。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1649', 'poem', '唐', '李白', '月夜听卢子顺弹琴', '闲坐夜明月，幽人弹素琴。\\n忽闻悲风调，宛若寒松吟。\\n白雪乱纤手，绿水清虚心。\\n钟期久已没，世上无知音。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1650', 'poem', '唐', '白居易', '放鹰', '十月鹰出笼，草枯雉兔肥。\\n下鞲随指顾，百掷无一遗。\\n鹰翅疾如风，鹰爪利如锥。\\n本为鸟所设，今为人所资。\\n孰能使之然，有术甚易知。\\n取其向背性，制在饥饱时。\\n不可使长饱，不可使长饥。\\n饥则力不足，饱则背人飞。\\n乘饥纵搏击，未饱须絷维。\\n所以爪翅功，而人坐收之。\\n圣明驭英雄，其术亦如斯。\\n鄙语不可弃，吾闻诸猎师。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1651', 'poem', '唐', '白居易', '落花', '留春春不住，春归人寂寞。\\n厌风风不定，风起花萧索。\\n既兴风前叹，重命花下酌。\\n劝君尝绿醅，教人拾红萼。\\n桃飘火焰焰，梨堕雪漠漠。\\n独有病眼花，春风吹不落。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1652', 'poem', '唐', '李百药', '渡汉江', '东流既瀰瀰，南纪信滔滔。\\n水激沉碑岸，波骇弄珠皋。\\n含星映浅石，浮盖下奔涛。\\n溜阔霞光近，川长晓气高。\\n樯乌转轻翼，戏鸟落风毛。\\n客心既多绪，长歌且代劳。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1653', 'poem', '唐', '吴融', '阌乡寓居十首·蛙声', '稚圭伦鉴未精通，只把蛙声鼓吹同。\\n君听月明人静夜，肯饶天籁与松风。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1654', 'poem', '唐', '姚合', '闲居', '不自识疏鄙，终年住在城。\\n过门无马迹，满宅是蝉声。\\n带病吟虽苦，休官梦已清。\\n何当学禅观，依止古先生？', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1655', 'poem', '唐', '杜牧', '齐安郡中偶题二首', '两竿落日溪桥上，半缕轻烟柳影中。\\n多少绿荷相倚恨，一时回首背西风。\\n秋声无不搅离心，梦泽蒹葭楚雨深。\\n自滴阶前大梧叶，干君何事动哀吟。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1656', 'poem', '唐', '张乔', '蝉', '先秋蝉一悲，长是客行时。\\n曾感去年者，又鸣何处枝。\\n细听残韵在，回望旧声迟。\\n断续谁家树，凉风送别离。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1657', 'poem', '唐', '杜甫', '绝句漫兴九首·其二', '手种桃李非无主，野老墙低还似家。\\n恰似春风相欺得，夜来吹折数枝花。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1658', 'poem', '唐', '虞世南', '咏萤', '的历流光小，飘飖弱翅轻。\\n恐畏无人识，独自暗中明。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1659', 'poem', '唐', '张籍', '没蕃故人', '前年伐月支，城上没全师。\\n蕃汉断消息，死生长别离。\\n无人收废帐，归马识残旗。\\n欲祭疑君在，天涯哭此时。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1660', 'poem', '唐', '王勃', '春园', '山泉两处晚，花柳一园春。\\n还持千日醉，共作百年人。', '2018-03-09 15:06:40');
INSERT INTO `t_poem` VALUES ('1661', 'poem', '唐', '钱起', '晚春归山居题窗前竹 / 暮春归故山草堂诗', '溪上残春黄鸟稀，辛夷花尽杏花飞。\\n始怜幽竹山窗下，不改清阴待我归。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1662', 'poem', '唐', '白居易', '重阳席上赋白菊', '满园花菊郁金黄，中有孤丛色似霜。\\n还似今朝歌酒席，白头翁入少年场。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1663', 'poem', '唐', '杜荀鹤', '田翁', '白发星星筋力衰，种田犹自伴孙儿。\\n官苗若不平平纳，任是丰年也受饥。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1664', 'poem', '唐', '杜牧', '早行', '垂鞭信马行，数里未鸡鸣。\\n林下带残梦，叶飞时忽惊。\\n霜凝孤鹤迥，月晓远山横。\\n僮仆休辞险，时平路复平。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1665', 'poem', '唐', '白居易', '闻虫', '暗虫唧唧夜绵绵，况是秋阴欲雨天。\\n犹恐愁人暂得睡，声声移近卧床前。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1666', 'poem', '唐', '罗邺', '河湟', '河湟何计绝烽烟，免使征人更戍边。\\n尽放农桑无一事，遣教知有太平年。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1667', 'poem', '唐', '崔道融', '羯鼓', '华清宫里打撩声，供奉丝簧束手听。\\n寂寞銮舆斜谷里，是谁翻得雨淋铃。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1668', 'poem', '唐', '王维', '齐州送祖三 / 河上送赵仙舟 / 淇上别赵仙舟', '相逢方一笑，相送还成泣。\\n祖帐已伤离，荒城复愁入。\\n天寒远山净，日暮长河急。\\n解缆君已遥，望君犹伫立。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1669', 'poem', '唐', '胡曾', '咏史诗·濮水', '青春行役思悠悠，一曲汀蒲濮水流。\\n正见涂中龟曳尾，令人特地感庄周。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1670', 'poem', '唐', '白居易', '彭蠡湖晚归', '彭蠡湖天晚，桃花水气春。\\n鸟飞千白点，日没半红轮。\\n何必为迁客，无劳是病身。\\n何来临此望，少有不愁人。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1671', 'poem', '唐', '周昙', '后汉门·羊续', '鱼悬洁白振清风，禄散亲宾岁自穷。\\n单席寒厅惭使者，葛衣何以至三公。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1672', 'poem', '唐', '白居易', '梦旧', '别来老大苦修道，炼得离心成死灰。\\n平生忆念消磨尽，昨夜因何入梦来。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1673', 'poem', '唐', '张巡', '闻笛', '岧峣试一临，虏骑附城阴。\\n不辨风尘色，安知天地心。\\n营开边月近，战苦阵云深。\\n旦夕更楼上，遥闻横笛音。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1674', 'poem', '唐', '曹修古', '池上', '荷叶罩芙蓉，圆青映嫩红。\\n佳人南陌上，翠盖立春风。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1675', 'poem', '唐', '白居易', '白羽扇', '素是自然色，圆因裁制功。\\n飒如松起籁，飘似鹤翻空。\\n盛夏不销雪，终年无尽风。\\n引秋生手里，藏月入怀中。\\n麈尾斑非疋，蒲葵陋不同。\\n何人称相对，清瘦白须翁。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1676', 'poem', '唐', '杜牧', '赠别二首其一', '娉娉袅袅十三余，豆蔻梢头二月初。\\n春风十里扬州路，卷上珠帘总不如。\\n多情却似总无情，唯觉樽前笑不成。\\n蜡烛有心还惜别，替人垂泪到天明。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1677', 'poem', '唐', '杜甫', '同诸公登慈恩寺塔', '高标跨苍天，烈风无时休。\\n自非旷士怀，登兹翻百忧。\\n方知象教力，足可追冥搜。\\n仰穿龙蛇窟，始出枝撑幽。\\n七星在北户，河汉声西流。\\n羲和鞭白日，少昊行清秋。\\n秦山忽破碎，泾渭不可求。\\n俯视但一气，焉能辨皇州。\\n回首叫虞舜，苍梧云正愁。\\n惜哉瑶池饮，日晏昆仑丘。\\n黄鹄去不息，哀鸣何所投。\\n君看随阳雁，各有稻粱谋。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1678', 'poem', '唐', '雍陶', '秋露', '白露暧秋色，月明清漏中。\\n痕沾珠箔重，点落玉盘空。\\n竹动时惊鸟，莎寒暗滴虫。\\n满园生永夜，渐欲与霜同。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1679', 'poem', '唐', '于鹄', '江南曲', '偶向江边采白蘋，还随女伴赛江神。\\n众中不敢分明语，暗掷金钱卜远人。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1680', 'poem', '唐', '杜牧', '杜秋娘诗', '京江水清滑，生女白如脂。\\n其间杜秋者，不劳朱粉施。\\n老濞即山铸，后庭千双眉。\\n秋持玉斝醉，与唱金缕衣。\\n濞既白首叛，秋亦红泪滋。\\n吴江落日渡，灞岸绿杨垂。\\n联裾见天子，盼眄独依依。\\n椒壁悬锦幕，镜奁蟠蛟螭。\\n低鬟认新宠，窈袅复融怡。\\n月上白璧门，桂影凉参差。\\n金阶露新重，闲捻紫箫吹。\\n莓苔夹城路，南苑雁初飞。\\n红粉羽林杖，独赐辟邪旗。\\n归来煮豹胎，餍饫不能饴。\\n咸池升日庆，铜雀分香悲。\\n雷音后车远，事往落花时。\\n燕禖得皇子，壮发绿緌緌。\\n画堂授傅姆，天人亲捧持。\\n虎睛珠络褓，金盘犀镇帷。\\n长杨射熊罴，武帐弄哑咿。\\n渐抛竹马剧，稍出舞鸡奇。\\n崭崭整冠珮，侍宴坐瑶池。\\n眉宇俨图画，神秀射朝辉。\\n一尺桐偶人，江充知自欺。\\n王幽茅土削，秋放故乡归。\\n觚棱拂斗极，回首尚迟迟。\\n四朝三十载，似梦复疑非。\\n潼关识旧吏，吏发已如丝。\\n却唤吴江渡，舟人那得知。\\n归来四邻改，茂苑草菲菲。\\n清血洒不尽，仰天知问谁。\\n寒衣一匹素，夜借邻人机。\\n我昨金陵过，闻之为歔欷。\\n自古皆一贯，变化安能推。\\n夏姬灭两国，逃作巫臣姬。\\n西子下姑苏，一舸逐鸱夷。\\n织室魏豹俘，作汉太平基。\\n误置代籍中，两朝尊母仪。\\n光武绍高祖，本系生唐儿。\\n珊瑚破高齐，作婢舂黄糜。\\n萧后去扬州，突厥为阏氏。\\n女子固不定，士林亦难期。\\n射钩后呼父，钓翁王者师。\\n无国要孟子，有人毁仲尼。\\n秦因逐客令，柄归丞相斯。\\n安知魏齐首，见断箦中尸。\\n给丧蹶张辈，廊庙冠峨危。\\n珥貂七叶贵，何妨戎虏支。\\n苏武却生返，邓通终死饥。\\n主张既难测，翻覆亦其宜。\\n地尽有何物，天外复何之。\\n指何为而捉，足何为而驰。\\n耳何为而听，目何为而窥。\\n已身不自晓，此外何思惟。\\n因倾一樽酒，题作杜秋诗。\\n愁来独长咏，聊可以自怡。', '2018-03-09 15:06:47');
INSERT INTO `t_poem` VALUES ('1681', 'poem', '唐', '白居易', '秋蝶', '秋花紫蒙蒙，秋蝶黄茸茸。\\n花低蝶新小，飞戏丛西东。\\n日暮凉风来，纷纷花落丛。\\n夜深白露冷，蝶已死丛中。\\n朝生夕俱死，气类各相从。\\n不见千年鹤，多栖百丈松。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1682', 'poem', '唐', '杜审言', '春日京中有怀', '今年游寓独游秦，愁思看春不当春。\\n上林苑里花徒发，细柳营前叶漫新。\\n公子南桥应尽兴，将军西第几留宾。\\n寄语洛城风日道，明年春色倍还人。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1683', 'poem', '唐', '骆宾王', '秋晨同淄川毛司马秋九咏·秋露', '玉关寒气早，金塘秋色归。\\n泛掌光逾净，添荷滴尚微。\\n变霜凝晓液，承月委圆辉。\\n别有吴台上，应湿楚臣衣。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1684', 'poem', '唐', '皎然', '九月十日', '爱杀柴桑隐，名溪近讼庭。\\n扫沙开野步，摇舸出闲汀。\\n宿简邀诗伴，馀花在酒瓶。\\n悠然南望意，自有岘山情。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1685', 'poem', '唐', '周昙', '春秋战国门·文公', '灭虢吞虞未息兵，柔秦败楚霸威成。\\n文公徒欲三强服，分晋元来是六卿。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1686', 'poem', '唐', '白居易', '琴茶', '兀兀寄形群动内，陶陶任性一生间。\\n自抛官后春多醉，不读书来老更闲。\\n琴里知闻唯渌水，茶中故旧是蒙山。\\n穷通行止长相伴，谁道吾今无往还。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1687', 'poem', '唐', '张萧远', '观灯', '十万人家火烛光，门门开处见红妆。\\n歌钟喧夜更漏暗，罗绮满街尘土香。\\n星宿别从天畔出，莲花不向水中芳。\\n宝钗骤马多遗落，依旧明朝在路傍。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1688', 'poem', '唐', '白居易', '病气', '自知气发每因情，情在何由气得平。\\n若问病根深与浅，此身应与病齐生。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1689', 'poem', '唐', '岑参', '万里桥', '成都与维扬，相去万里地。\\n沧江东流疾，帆去如鸟翅。\\n楚客过此桥，东看尽垂泪。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1690', 'poem', '唐', '韩偓', '醉著', '万里清江万里天，一村桑柘一村烟。\\n渔翁醉著无人唤，过午醒来雪满船。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1691', 'poem', '唐', '李商隐', '可叹', '幸会东城宴未回，年华忧共水相催。\\n梁家宅里秦宫入，赵后楼中赤凤来。\\n冰簟且眠金镂枕，琼筵不醉玉交杯。\\n宓妃愁坐芝田馆，用尽陈王八斗才。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1692', 'poem', '唐', '项斯', '江村夜泊', '日落江路黑，前村人语稀。\\n几家深树里，一火夜渔归。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1693', 'poem', '唐', '李端', '溪行逢雨与柳中庸', '日落众山昏，萧萧暮雨繁。\\n那堪两处宿，共听一声猿。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1694', 'poem', '唐', '孙元晏', '晋·蒲葵扇', '抛舍东山岁月遥，几施经略挫雄豪。\\n若非名德喧寰宇，争得蒲葵价数高。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1695', 'poem', '唐', '刘长卿', '酬李穆见寄', '孤舟相访至天涯，万转云山路更赊。\\n欲扫柴门迎远客，青苔黄叶满贫家。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1696', 'poem', '唐', '周昙', '后汉门·光武', '成败非儒孰可量，儒生何指指萧王。\\n萧王得众能宽裕，吴汉归来帝业昌。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1697', 'poem', '唐', '顾况', '南归', '老病力难任，犹多镜雪侵。\\n鲈鱼消宦况，鸥鸟识归心。\\n急雨江帆重，残更驿树深。\\n乡关殊可望，渐渐入吴音。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1698', 'poem', '唐', '唐彦谦', '竹风', '竹映风窗数阵斜，旅人愁坐思无涯。\\n夜来留得江湖梦，全为乾声似荻花。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1699', 'poem', '唐', '齐己', '看云', '何峰触石湿苔钱，便逐高风离瀑泉。\\n深处卧来真隐逸，上头行去是神仙。\\n千寻有影沧江底，万里无踪碧落边。\\n长忆旧山青壁里，绕庵闲伴老僧禅。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1700', 'poem', '唐', '黄公绍', '青玉案·年年社日停针线', '年年社日停针线。怎忍见、双飞燕。今日江城春已半。一身犹在，乱山深处，寂寞溪桥畔。\\n春衫著破谁针线。点点行行泪痕满。落日解鞍芳草岸。花无人戴，酒无人劝，醉也无人管。', '2018-03-09 15:06:54');
INSERT INTO `t_poem` VALUES ('1701', 'poem', '唐', '李白', '古风其十', '齐有倜傥生。\\n鲁连特高妙。\\n明月出海底。\\n一朝开光曜。\\n却秦振英声。\\n後世仰末照。\\n意轻千金赠。\\n顾向平原笑。\\n吾亦澹荡人。\\n拂衣可同调。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1702', 'poem', '唐', '钱起', '裴迪南门秋夜对月 / 裴迪书斋望月', '夜来诗酒兴，月满谢公楼。\\n影闭重门静，寒生独树秋。\\n鹊惊随叶散，萤远入烟流。\\n今夕遥天末，清光几处愁。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1703', 'poem', '唐', '韦应物', '夏花明', '夏条绿已密，朱萼缀明鲜。\\n炎炎日正午，灼灼火俱燃。\\n翻风适自乱，照水复成妍。\\n归视窗间字，荧煌满眼前。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1704', 'poem', '唐', '岑参', '宿铁关西馆', '马汗踏成泥，朝驰几万蹄。\\n雪中行地角，火处宿天倪。\\n塞迥心常怯，乡遥梦亦迷。\\n那知故园月，也到铁关西。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1705', 'poem', '唐', '刘禹锡', '金陵怀古', '潮满冶城渚，日斜征虏亭。\\n蔡洲新草绿，幕府旧烟青。\\n兴废由人事，山川空地形。\\n后庭花一曲，幽怨不堪听。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1706', 'poem', '唐', '李煜', '菩萨蛮', '花明月暗笼轻雾，今宵好向郎边去。刬袜步香阶，手提金缕鞋。\\n画堂南畔见，一晌偎人颤。好为出来难，教君恣意怜。\\n蓬莱院闭天台女，画堂昼寝人无语。抛枕翠云光，绣衣闻异香。\\n潜来珠锁动，惊觉鸳鸯梦。慢脸笑盈盈，相看无限情。\\n铜簧韵脆锵寒竹，新声慢奏移纤玉。眼色暗相钩，秋波横欲流。\\n雨云深绣户，来便谐衷素。宴罢又成空，梦迷春睡中。\\n人生愁恨何能免，销魂独我情何限。故国梦重归，觉来双泪垂。\\n高楼谁与上，长记秋晴望。往事已成空，还如一梦中。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1707', 'poem', '唐', '司空图', '秋燕', '从扑香尘拂面飞，怜渠只为解相依。\\n经冬好近深炉暖，何必千岩万水归。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1708', 'poem', '唐', '韩琮', '暮春浐水送别 / 一作暮春送客', '绿暗红稀出凤城，暮云楼阁古今情。\\n行人莫听宫前水，流尽年光是此声。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1709', 'poem', '唐', '白居易', '城东闲游', '宠辱忧欢不到情，任他朝市自营营。\\n独寻秋景城东去，白鹿原头信马行。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1710', 'poem', '唐', '杜甫', '冬日有怀李白', '寂寞书斋里，终朝独尔思。\\n更寻嘉树传，不忘角弓诗。\\n短褐风霜入，还丹日月迟。\\n未因乘兴去，空有鹿门期。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1711', 'poem', '唐', '惠能', '偈一', '菩提本非树，明镜亦非台；本来无一物，何处染尘埃？', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1712', 'poem', '唐', '白居易', '赠梦得', '前日君家饮，昨日王家宴。\\n今日过我庐，三日三会面。\\n当歌聊自放，对酒交相劝。\\n为我尽一杯，与君发三愿。\\n一愿世清平，二愿身强健。\\n三愿临老头，数与君相见。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1713', 'poem', '唐', '李白', '落日忆山中', '雨后烟景绿，晴天散馀霞。\\n东风随春归，发我枝上花。\\n花落时欲暮，见此令人嗟。\\n愿游名山去，学道飞丹砂。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1714', 'poem', '唐', '白居易', '寄情', '灼灼早春梅，东南枝最早。\\n持来玩未足，花向手中老。\\n芳香销掌握，怅望生怀抱。\\n岂无后开花，念此先开好。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1715', 'poem', '唐', '汪遵', '桐江', '光武重兴四海宁，汉臣无不受浮荣。\\n严陵何事轻轩冕，独向桐江钓月明。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1716', 'poem', '唐', '柳宗元', '相和歌辞·东门行', '汉家三十六将军，东方雷动横阵云。\\n鸡鸣函谷客如雾，貌同心异不可数。\\n赤丸夜语飞电光，徼巡司隶眠如羊。\\n当街一叱百吏走，冯敬胸中函匕首。\\n凶徒侧耳潜惬心，悍臣破胆皆杜口。\\n魏王卧内藏兵符，子西掩袂真无辜。\\n羌胡毂下一朝起，敌国舟中非所拟。\\n安陵谁辨削砺功，韩国讵明深井里。\\n绝咽断骨那下补，万金宠赠不如土。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1717', 'poem', '唐', '崔道融', '江鸥', '白鸟波上栖，见人懒飞起。\\n为有求鱼心，不是恋江水。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1718', 'poem', '唐', '杜甫', '遣闷戏呈路十九曹长', '江浦雷声喧昨夜，春城雨色动微寒。\\n黄鹂并坐交愁湿，白鹭群飞大剧干。\\n晚节渐于诗律细，谁家数去酒杯宽。\\n惟吾最爱清狂客，百遍相看意未阑。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1719', 'poem', '唐', '李颀', '鲛人歌', '鲛人潜织水底居，侧身上下随游鱼。\\n轻绡文彩不可识，夜夜澄波连月色。\\n有时寄宿来城市，海岛青冥无极已。\\n泣珠报恩君莫辞，今年相见明年期。\\n始知万族无不有，百尺深泉架户牖。\\n鸟没空山谁复望，一望云涛堪白首。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1720', 'poem', '唐', '李商隐', '滞雨', '滞雨长安夜，残灯独客愁。\\n故乡云水地，归梦不宜秋。', '2018-03-09 15:07:05');
INSERT INTO `t_poem` VALUES ('1721', 'poem', '唐', '卢仝', '守岁二首', '去年留不住，年来也任他。\\n当垆一榼酒，争奈两年何。\\n老来经节腊，乐事甚悠悠。\\n不及儿童日，都卢不解愁。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1722', 'poem', '唐', '来鹄', '云', '千形万象竟还空，映水藏山片复重。\\n无限旱苗枯欲尽，悠悠闲处作奇峰。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1723', 'poem', '唐', '上官仪', '故北平公挽歌', '木落园林旷，庭虚风露寒。\\n北里清音绝，南陔芳草残。\\n远气犹标剑，浮云尚写冠。\\n寂寂琴台晚，秋阴入井干。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1724', 'poem', '唐', '白居易', '早蝉', '六月初七日，江头蝉始鸣。\\n石楠深叶里，薄暮两三声。\\n一催衰鬓色，再动故园情。\\n西风殊未起，秋思先秋生。\\n忆昔在东掖，宫槐花下听。\\n今朝无限思，云树绕湓城。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1725', 'poem', '唐', '韩愈', '谁氏子', '非痴非狂谁氏子，去入王屋称道士。\\n白头老母遮门啼，挽断衫袖留不止。\\n翠眉新妇年二十，载送还家哭穿市。\\n或云欲学吹凤笙，所慕灵妃媲萧史。\\n又云时俗轻寻常，力行险怪取贵仕。\\n神仙虽然有传说，知者尽知其妄矣。\\n圣君贤相安可欺，乾死穷山竟何俟。\\n呜呼余心诚岂弟，愿往教诲究终始。\\n罚一劝百政之经，不从而诛未晚耳。\\n谁其友亲能哀怜，写吾此诗持送似。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1726', 'poem', '唐', '顾况', '宫词', '长乐宫连上苑春，玉楼金殿艳歌新。\\n君门一入无由出，唯有宫莺得见人。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1727', 'poem', '唐', '元稹', '西归绝句十二首', '双堠频频减去程，渐知身得近京城。\\n春来爱有归乡梦，一半犹疑梦里行。\\n五年江上损容颜，今日春风到武关。\\n两纸京书临水读，小桃花树满商山。\\n同归谏院韦丞相，共贬河南亚大夫。\\n今日还乡独憔悴，几人怜见白髭须。\\n只去长安六日期，多应及得杏花时。\\n春明门外谁相待，不梦闲人梦酒卮。\\n白头归舍意如何，贺处无穷吊亦多。\\n左降去时裴相宅，旧来车马几人过。\\n还乡何用泪沾襟，一半云霄一半沉。\\n世事渐多饶怅望，旧曾行处便伤心。\\n闲游寺观从容到，遍问亲知次第寻。\\n肠断裴家光德宅，无人扫地戟门深。\\n一世营营死是休，生前无事定无由。\\n不知山下东流水，何事长须日夜流。\\n今朝西渡丹河水，心寄丹河无限愁。\\n若到庄前竹园下，殷勤为绕故山流。\\n寒窗风雪拥深炉，彼此相伤指白须。\\n一夜思量十年事，几人强健几人无。\\n云覆蓝桥雪满溪，须臾便与碧峰齐。\\n风回面市连天合，冻压花枝著水低。\\n寒花带雪满山腰，著柳冰珠满碧条。\\n天色渐明回一望，玉尘随马度蓝桥。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1728', 'poem', '唐', '许浑', '途经秦始皇墓', '龙盘虎踞树层层，势入浮云亦是崩。\\n一种青山秋草里，路人唯拜汉文陵。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1729', 'poem', '唐', '白居易', '晚秋夜', '碧空溶溶月华静，月里愁人吊孤影。\\n花开残菊傍疏篱，叶下衰桐落寒井。\\n塞鸿飞急觉秋尽，邻鸡鸣迟知夜永。\\n凝情不语空所思，风吹白露衣裳冷。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1730', 'poem', '唐', '白居易', '宴散', '小宴追凉散，平桥步月回。\\n笙歌归院落，灯火下楼台。\\n残暑蝉催尽，新秋雁戴来。\\n将何还睡兴，临卧举残杯。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1731', 'poem', '唐', '宋之问', '灵隐寺', '鹫岭郁岧峣，龙宫锁寂寥。\\n楼观沧海日，门对浙江潮。\\n桂子月中落，天香云外飘。\\n扪萝登塔远，刳木取泉遥。\\n霜薄花更发，冰轻叶未凋。\\n夙龄尚遐异，搜对涤烦嚣。\\n待入天台路，看余度石桥。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1732', 'poem', '唐', '刘长卿', '弹琴', '泠泠七弦上，静听松风寒。\\n古调虽自爱，今人多不弹。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1733', 'poem', '唐', '罗隐', '乌程', '两府攀陪十五年，郡中甘雨幕中莲。\\n一瓶犹是乌程酒，须对霜风度泫然。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1734', 'poem', '唐', '高适', '醉后赠张九旭', '世上谩相识，此翁殊不然。\\n兴来书自圣，醉后语尤颠。\\n白发老闲事，青云在目前。\\n床头一壶酒，能更几回眠。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1735', 'poem', '唐', '白居易', '题岳阳楼', '岳阳城下水漫漫，独上危楼倚曲栏。\\n春岸绿时连梦泽，夕波红处近长安。\\n猿攀树立啼何苦，雁点湖飞渡亦难。\\n此地唯堪画图障，华堂张与贵人看。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1736', 'poem', '唐', '刘方平', '采莲曲', '落日晴江里，荆歌艳楚腰。\\n采莲从小惯，十五即乘潮。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1737', 'poem', '唐', '鱼玄机', '折杨柳·朝朝送别泣花钿', '朝朝送别泣花钿，折尽春风杨柳烟。\\n愿得西山无树木，免教人作泪悬悬。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1738', 'poem', '唐', '李商隐', '春日', '欲入卢家白玉堂，新春催破舞衣裳。\\n蝶衔红蕊蜂衔粉，共助青楼一日忙。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1739', 'poem', '唐', '李白', '咏桂', '世人种桃李，皆在金张门。\\n攀折争捷径，及此春风暄。\\n一朝天霜下，荣耀难久存。\\n安知南山桂，绿叶垂芳根。\\n清阴亦可托，何惜树君园。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1740', 'poem', '唐', '胡曾', '咏史诗·鸿沟', '虎倦龙疲白刃秋，两分天下指鸿沟。\\n项王不觉英雄挫，欲向彭门醉玉楼。', '2018-03-09 15:07:18');
INSERT INTO `t_poem` VALUES ('1741', 'poem', '唐', '杨巨源', '长城闻笛', '孤城笛满林，断续共霜砧。\\n夜月降羌泪，秋风老将心。\\n静过寒垒遍，暗入故关深。\\n惆怅梅花落，山川不可寻。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1742', 'poem', '唐', '罗隐', '黄河', '莫把阿胶向此倾，此中天意固难明。\\n解通银汉应须曲，才出昆仑便不清。\\n高祖誓功衣带小，仙人占斗客槎轻。\\n三千年后知谁在？何必劳君报太平！', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1743', 'poem', '唐', '薛逢', '猎骑', '兵印长封入卫稀，碧空云尽早霜微。\\n浐川桑落雕初下，渭曲禾收兔正肥。\\n陌上管弦清似语，草头弓马疾如飞。\\n岂知万里黄云戍，血迸金疮卧铁衣。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1744', 'poem', '唐', '薛昭蕴', '浣溪沙·红蓼渡头秋正雨', '红蓼渡头秋正雨，印沙鸥迹自成行，整鬟飘袖野风香。\\n不语含嚬深浦里，几回愁煞棹船郎，燕归帆尽水茫茫。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1745', 'poem', '唐', '贾岛', '绝句', '海底有明月，圆于天上轮。\\n得之一寸光，可买千里春。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1746', 'poem', '唐', '白居易', '远师', '东宫白庶子，南寺远禅师。\\n何处遥相见，心无一事时。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1747', 'poem', '唐', '杨凭', '千叶桃花', '千叶桃花胜百花，孤荣春晚驻年华。\\n若教避俗秦人见，知向河源旧侣夸。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1748', 'poem', '唐', '郑谷', '雪中偶题', '乱飘僧舍茶烟湿，密洒歌楼酒力微。\\n江上晚来堪画处，渔人披得一蓑归。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1749', 'poem', '唐', '白居易', '秋寒', '雪鬓年颜老，霜庭景气秋。\\n病看妻检药，寒遣婢梳头。\\n身外名何有，人间事且休。\\n澹然方寸内，唯拟学虚舟。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1750', 'poem', '唐', '李山甫', '寒食二首', '柳带东风一向斜，春阴澹澹蔽人家。\\n有时三点两点雨，到处十枝五枝花。\\n万井楼台疑绣画，九原珠翠似烟霞。\\n年年今日谁相问，独卧长安泣岁华。\\n风烟放荡花披猖，秋千女儿飞短墙。\\n绣袍驰马拾遗翠，锦袖斗鸡喧广场。\\n天地气和融霁色，池台日暖烧春光。\\n自怜尘土无他事，空脱荷衣泥醉乡。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1751', 'poem', '唐', '郑准', '云', '片片飞来静又闲，楼头江上复山前。\\n飘零尽日不归去，点破清光万里天。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1752', 'poem', '唐', '王维', '伊州歌', '清风明月苦相思，荡子从戎十载馀。\\n征人去日殷勤嘱，归雁来时数附书。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1753', 'poem', '唐', '裴迪', '辋川集二十首·欹湖', '空阔湖水广，青荧天色同。\\n舣舟一长啸，四面来清风。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1754', 'poem', '唐', '孟浩然', '耶溪泛舟', '落景余清辉，轻桡弄溪渚。\\n澄明爱水物，临泛何容与。\\n白首垂钓翁，新妆浣纱女。\\n相看似相识，脉脉不得语。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1755', 'poem', '唐', '李白', '丁督护歌', '云阳上征去，两岸饶商贾。\\n吴牛喘月时，拖船一何苦。\\n水浊不可饮，壶浆半成土。\\n一唱都护歌，心摧泪如雨。\\n万人凿盘石，无由达江浒。\\n君看石芒砀，掩泪悲千古。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1756', 'poem', '唐', '崔颢', '入若耶溪', '轻舟去何疾，已到云林境。\\n起坐鱼鸟间，动摇山水影。\\n岩中响自答，溪里言弥静。\\n事事令人幽，停桡向馀景。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1757', 'poem', '唐', '杜牧', '重送绝句', '绝艺如君天下少，闲人似我世间无。\\n别后竹窗风雪夜，一灯明暗覆吴图。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1758', 'poem', '唐', '白居易', '西湖晚归回望孤山寺赠诸客', '柳湖松岛莲花寺，晚动归桡出道场。\\n卢橘子低山雨重，棕榈叶战水风凉。\\n烟波澹荡摇空碧，楼殿参差倚夕阳。\\n到岸请君回首望，蓬莱宫在海中央。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1759', 'poem', '唐', '郑遨', '富贵曲', '美人梳洗时，满头间珠翠。\\n岂知两片云，戴却数乡税。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1760', 'poem', '唐', '李贞白', '咏月', '当涂当涂见，芜湖芜湖见。\\n八月十五夜，一似没柄扇。', '2018-03-09 15:07:28');
INSERT INTO `t_poem` VALUES ('1761', 'poem', '唐', '李嘉祐', '夜宴南陵留别', '雪满前庭月色闲，主人留客未能还。\\n预愁明日相思处，匹马千山与万山。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1762', 'poem', '唐', '李商隐', '赠柳', '章台从掩映，郢路更参差。\\n见说风流极，来当婀娜时。\\n桥回行欲断，堤远意相随。\\n忍放花如雪，青楼扑酒旗。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1763', 'poem', '唐', '孙元晏', '齐·潘妃', '曾步金莲宠绝伦，岂甘今日委埃尘。\\n玉儿还有怀恩处，不肯将身嫁小臣。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1764', 'poem', '唐', '杜牧', '柳绝句', '数树新开翠影齐，倚风情态被春迷。\\n依依故国樊川恨，半掩村桥半掩溪。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1765', 'poem', '唐', '李白', '赠钱征君少阳', '白玉一杯酒，绿杨三月时。\\n春风余几日，两鬓各成丝。\\n秉烛唯须饮，投竿也未迟。\\n如逢渭水猎，犹可帝王师。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1766', 'poem', '唐', '方干', '旅次洋州寓居郝氏林亭', '举目纵然非我有，思量似在故山时。\\n鹤盘远势投孤屿，蝉曳残声过别枝。\\n凉月照窗攲枕倦，澄泉绕石泛觞迟。\\n青云未得平行去，梦到江南身旅羁。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1767', 'poem', '唐', '刘禹锡', '竞渡曲', '沅江五月平堤流，邑人相将浮彩舟。\\n灵均何年歌已矣，哀谣振楫从此起。\\n杨桴击节雷阗阗，乱流齐进声轰然。\\n蛟龙得雨鬐鬣动，螮蝀饮河形影联。\\n刺史临流褰翠帏，揭竿命爵分雄雌。\\n先鸣馀勇争鼓舞，未至衔枚颜色沮。\\n百胜本自有前期，一飞由来无定所。\\n风俗如狂重此时，纵观云委江之湄。\\n彩旂夹岸照蛟室，罗袜凌波呈水嬉。\\n曲终人散空愁暮，招屈亭前水东注。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1768', 'poem', '唐', '刘长卿', '少年行', '射飞夸侍猎，行乐爱联镳。\\n荐枕青蛾艳，鸣鞭白马骄。\\n曲房珠翠合，深巷管弦调。\\n日晚春风里，衣香满路飘。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1769', 'poem', '唐', '司空图', '步虚', '阿母亲教学步虚，三元长遣下蓬壶。\\n云韶韵俗停瑶瑟，鸾鹤飞低拂宝炉。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1770', 'poem', '唐', '柳宗元', '零陵早春', '问春从此去，几日到秦原。\\n凭寄还乡梦，殷勤入故园。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1771', 'poem', '唐', '贾至', '早朝大明宫呈两省僚友', '银烛熏天紫陌长，禁城春色晓苍苍。\\n千条弱柳垂青琐，百啭流莺绕建章。\\n剑佩声随玉墀步，衣冠身惹御炉香。\\n共沐恩波凤池上，朝朝染翰侍君王。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1772', 'poem', '唐', '董思恭', '咏雪', '天山飞雪度，言是落花朝。\\n惜哉不我与，萧索从风飘。\\n鲜洁凌纨素，纷糅下枝条。\\n良时竟何在，坐见容华销。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1773', 'poem', '唐', '张又新', '牡丹', '牡丹一朵值千金，将谓从来色最深。\\n今日满栏开似雪，一生辜负看花心。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1774', 'poem', '唐', '岑参', '奉陪封大夫九日登高', '九日黄花酒，登高会昔闻。\\n霜威逐亚相，杀气傍中军。\\n横笛惊征雁，娇歌落塞云。\\n边头幸无事，醉舞荷吾君。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1775', 'poem', '唐', '韦庄', '衢州江上别李秀才', '千山红树万山云，把酒相看日又曛。\\n一曲离歌两行泪，不知何地再逢君。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1776', 'poem', '唐', '胡曾', '咏史诗·居延', '漠漠平沙际碧天，问人云此是居延。\\n停骖一顾犹魂断，苏武争禁十九年。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1777', 'poem', '唐', '权德舆', '月夜江行 / 旅次江亭', '扣船不得寐，浩露清衣襟。\\n弥伤孤舟夜，远结万里心。\\n幽兴惜瑶草，素怀寄鸣琴。\\n三奏月初上，寂寞寒江深。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1778', 'poem', '唐', '韦庄', '云散', '云散天边落照和，关关春树鸟声多。\\n刘伶避世唯沈醉，甯戚伤时亦浩歌。\\n已恨岁华添皎镜，更悲人事逐颓波。\\n青云自有鹓鸿待，莫说他山好薜萝。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1779', 'poem', '唐', '杜甫', '客夜', '客睡何曾著，秋天不肯明。\\n卷帘残月影，高枕远江声。\\n计拙无衣食，途穷仗友生。\\n老妻书数纸，应悉未归情。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1780', 'poem', '唐', '孙氏', '闻琴', '玉指朱弦轧复清，湘妃愁怨最难听。\\n初疑飒飒凉风劲，又似萧萧暮雨零。\\n近比流泉来碧嶂，远如玄鹤下青冥。\\n夜深弹罢堪惆怅，露湿丛兰月满庭。', '2018-03-09 15:07:34');
INSERT INTO `t_poem` VALUES ('1781', 'poem', '唐', '杜甫', '岁晏行', '岁云暮矣多北风，潇湘洞庭白雪中。\\n渔父天寒网罟冻，莫徭射雁鸣桑弓。\\n去年米贵阙军食，今年米贱大伤农。\\n高马达官厌酒肉，此辈杼轴茅茨空。\\n楚人重鱼不重鸟，汝休枉杀南飞鸿。\\n况闻处处鬻男女，割慈忍爱还租庸。\\n往日用钱捉私铸，今许铅锡和青铜。\\n刻泥为之最易得，好恶不合长相蒙。\\n万国城头吹画角，此曲哀怨何时终？', '2018-03-09 15:07:39');
INSERT INTO `t_poem` VALUES ('1782', 'poem', '唐', '李白', '别东林寺僧', '东林送客处，月出白猿啼。\\n笑别庐山远，何烦过虎溪。', '2018-03-09 15:07:39');
INSERT INTO `t_poem` VALUES ('1783', 'poem', '唐', '韩愈', '次潼关先寄张十二阁老使君', '荆山已去华山来，日出潼关四扇开。\\n刺史莫辞迎候远，相公亲破蔡州回。', '2018-03-09 15:07:39');
INSERT INTO `t_poem` VALUES ('1784', 'poem', '唐', '王维', '戏题盘石', '可怜盘石临泉水，复有垂杨拂酒杯。\\n若道春风不解意，何因吹送落花来。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1785', 'poem', '唐', '王初', '梅花二首', '应为阳春信未传，固将青艳属残年。\\n东君欲待寻佳约，剩寄衣香与粉绵。\\n迎春雪艳飘零极，度夕蟾华掩映多。\\n欲托清香传远信，一枝无计奈愁何。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1786', 'poem', '唐', '李白', '观猎', '太守耀清威，乘闲弄晚晖。\\n江沙横猎骑，山火绕行围。\\n箭逐云鸿落，鹰随月兔飞。\\n不知白日暮，欢赏夜方归。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1787', 'poem', '唐', '白居易', '长安早春旅怀', '轩车歌吹喧都邑，中有一人向隅立。\\n夜深明月卷帘愁，日暮青山望乡泣。\\n风吹新绿草芽坼，雨洒轻黄柳条湿。\\n此生知负少年春，不展愁眉欲三十。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1788', 'poem', '唐', '李商隐', '桂林', '城窄山将压，江宽地共浮。\\n东南通绝域，西北有高楼。\\n神护青枫岸，龙移白石湫。\\n殊乡竟何祷，箫鼓不曾休。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1789', 'poem', '唐', '岑参', '石犀', '江水初荡潏，蜀人几为鱼。\\n向无尔石犀，安得有邑居。\\n始知李太守，伯禹亦不如。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1790', 'poem', '唐', '司空图', '五月九日', '金石皆销铄，贤愚共网罗。\\n达从诗似偈，狂觉哭胜歌。\\n高燕凌鸿鹄，枯槎压芰荷。\\n此中无别境，此外是闲魔。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1791', 'poem', '唐', '皮日休', '咏蟹 / 咏螃蟹呈浙西从事', '未游沧海早知名，有骨还从肉上生。\\n莫道无心畏雷电，海龙王处也横行。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1792', 'poem', '唐', '无名氏', '六言诗', '把酒留君听琴，那堪岁暮离心。\\n霜叶无风自落，秋天不雨多阴。\\n人愁荒村路远，马怯寒溪水深。\\n望尽青山犹在，不知何处相寻。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1793', 'poem', '唐', '戎昱', '塞下曲', '惨惨寒日没，北风卷蓬根。\\n将军领疲兵，却入古塞门。\\n回头指阴山，杀气成黄云。\\n上山望胡兵，胡马驰骤速。\\n黄河冰已合，意又向南牧。\\n嫖姚夜出军，霜雪割人肉。\\n塞北无草木，乌鸢巢僵尸。\\n泱渀沙漠空，终日胡风吹。\\n战卒多苦辛，苦辛无四时。\\n晚渡西海西，向东看日没。\\n傍岸砂砾堆，半和战兵骨。\\n单于竟未灭，阴气常勃勃。\\n城上画角哀，即知兵心苦。\\n试问左右人，无言泪如雨。\\n何意休明时，终年事鼙鼓。\\n北风凋白草，胡马日駸駸。\\n夜后戍楼月，秋来边将心。\\n铁衣霜露重，战马岁年深。\\n自有卢龙塞，烟尘飞至今。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1794', 'poem', '唐', '罗隐', '炀帝陵', '入郭登桥出郭船，红楼日日柳年年。\\n君王忍把平陈业，只博雷塘数亩田。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1795', 'poem', '唐', '宋雍', '春日', '轻花细叶满林端，昨夜春风晓色寒。\\n黄鸟不堪愁里听，绿杨宜向雨中看。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1796', 'poem', '唐', '白居易', '除夜', '病眼少眠非守岁，老心多感又临春。\\n火销灯尽天明后，便是平头六十人。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1797', 'poem', '唐', '王维', '书事', '轻阴阁小雨，深院昼慵开。\\n坐看苍苔色，欲上人衣来。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1798', 'poem', '唐', '罗隐', '七夕', '络角星河菡萏天，一家欢笑设红筵。\\n应倾谢女珠玑箧，尽写檀郎锦绣篇。\\n香帐簇成排窈窕，金针穿罢拜婵娟。\\n铜壶漏报天将晓，惆怅佳期又一年。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1799', 'poem', '唐', '张说', '深渡驿', '旅泊青山夜，荒庭白露秋。\\n洞房悬月影，高枕听江流。\\n猿响寒岩树，萤飞古驿楼。\\n他乡对摇落，并觉起离忧。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1800', 'poem', '唐', '李世民', '咏雨·其二', '和气吹绿野，梅雨洒芳田。\\n新流添旧涧，宿雾足朝烟。\\n雁湿行无次，花沾色更鲜。\\n对此欣登岁，披襟弄五弦。', '2018-03-09 15:07:40');
INSERT INTO `t_poem` VALUES ('1801', 'poem', '唐', '温庭筠', '早秋山居', '山近觉寒早，草堂霜气晴。\\n树凋窗有日，池满水无声。\\n果落见猿过，叶干闻鹿行。\\n素琴机虑静，空伴夜泉清。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1802', 'poem', '唐', '杜甫', '夜宴左氏庄', '林风纤月落，衣露净琴张。\\n暗水流花径，春星带草堂。\\n检书烧烛短，看剑引杯长。\\n诗罢闻吴咏，扁舟意不忘。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1803', 'poem', '唐', '李商隐', '辛未七夕', '恐是仙家好别离，故教迢递作佳期。\\n由来碧落银河畔，可要金风玉露时。\\n清漏渐移相望久，微云未接过来迟。\\n岂能无意酬乌鹊，惟与蜘蛛乞巧丝。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1804', 'poem', '唐', '白居易', '乱后过流沟寺', '九月徐州新战后，悲风杀气满山河。\\n唯有流沟山下寺，门前依旧白云多。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1805', 'poem', '唐', '杜牧', '入茶山下题水口草市绝句', '倚溪侵岭多高树，夸酒书旗有小楼。\\n惊起鸳鸯岂无恨，一双飞去却回头。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1806', 'poem', '唐', '庄南杰', '雁门太守行', '旌旗闪闪摇天末，长笛横吹虏尘阔。\\n跨下嘶风白练狞，腰间切玉青蛇活。\\n击革摐金燧牛尾，犬羊兵败如山死。\\n九泉寂寞葬秋虫，湿云荒草啼秋思。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1807', 'poem', '唐', '岑参', '春梦', '洞房昨夜春风起，故人尚隔湘江水。\\n枕上片时春梦中，行尽江南数千里。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1808', 'poem', '唐', '白居易', '庾楼晓望', '独凭朱槛立凌晨，山色初明水色新。\\n竹雾晓笼衔岭月，蘋风暖送过江春。\\n子城阴处犹残雪，衙鼓声前未有尘。\\n三百年来庾楼上，曾经多少望乡人。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1809', 'poem', '唐', '李商隐', '高花', '花将人共笑，篱外露繁枝。\\n宋玉临江宅，墙低不碍窥。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1810', 'poem', '唐', '李煜', '玉楼春', '晚妆初了明肌雪，春殿嫔娥鱼贯列。\\n笙箫吹断水云开，重按霓裳歌遍彻。\\n临风谁更飘香屑，醉拍阑干情味切。\\n归时休放烛花红，待踏马蹄清夜月。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1811', 'poem', '唐', '许浑', '客至', '得路逢津更俊才，可怜鞍马照春来。\\n残花几日小斋闭，大笑一声幽抱开。\\n袖拂碧溪寒缭绕，冠欹红树晚徘徊。\\n相逢少别更堪恨，何必秋风江上台。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1812', 'poem', '唐', '白居易', '下邽庄南桃花', '村南无限桃花发，唯我多情独自来。\\n日暮风吹红满地，无人解惜为谁开。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1813', 'poem', '唐', '杜甫', '送韩十四江东觐省', '兵戈不见老莱衣，叹息人间万事非。\\n我已无家寻弟妹，君今何处访庭闱。\\n黄牛峡静滩声转，白马江寒树影稀。\\n此别应须各努力，故乡犹恐未同归。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1814', 'poem', '唐', '杜甫', '堂成', '背郭堂成荫白茅，缘江路熟俯青郊。\\n桤林碍日吟风叶，笼竹和烟滴露梢。\\n暂止飞乌将数子，频来语燕定新巢。\\n旁人错比扬雄宅，懒惰无心作解嘲。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1815', 'poem', '唐', '温庭筠', '送渤海王子归本国', '疆理虽重海，车书本一家。\\n盛勋归旧国，佳句在中华。\\n定界分秋涨，开帆到曙霞。\\n九门风月好，回首是天涯。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1816', 'poem', '唐', '王维', '阙题二首', '荆谿白石出，天寒红叶稀。\\n山路元无雨，空翠湿人衣。\\n相看不忍发，惨淡暮潮平。\\n语罢更携手，月明洲渚生。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1817', 'poem', '唐', '李世民', '咏风', '萧条起关塞，摇飏下蓬瀛。\\n拂林花乱彩，响谷鸟分声。\\n披云罗影散，泛水织文生。\\n劳歌大风曲，威加四海清。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1818', 'poem', '唐', '李商隐', '杜工部蜀中离席', '人生何处不离群，世路干戈惜暂分。\\n雪岭未归天外使，松州犹驻殿前军。\\n座中醉客延醒客，江上晴云杂雨云。\\n美酒成都堪送老，当垆仍是卓文君。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1819', 'poem', '唐', '刘叉', '独饮', '尽欲调太羹，自古无好手。\\n所以山中人，兀兀但饮酒。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1820', 'poem', '唐', '刘商', '咏双开莲花', '菡萏新花晓并开，浓妆美笑面相隈。\\n西方采画迦陵鸟，早晚双飞池上来。', '2018-03-09 15:07:46');
INSERT INTO `t_poem` VALUES ('1821', 'poem', '唐', '耿湋', '秋日', '反照入闾巷，忧来与谁语。\\n古道无人行，秋风动禾黍。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1822', 'poem', '唐', '李贺', '老夫采玉歌', '采玉采玉须水碧，琢作步摇徒好色。\\n老夫饥寒龙为愁，蓝溪水气无清白。\\n夜雨冈头食蓁子，杜鹃口血老夫泪。\\n蓝溪之水厌生人，身死千年恨溪水。\\n斜山柏风雨如啸，泉脚挂绳青袅袅。\\n村寒白屋念娇婴，古台石磴悬肠草。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1823', 'poem', '唐', '韦应物', '登楼', '兹楼日登眺，流岁暗蹉跎。\\n坐厌淮南守，秋山红树多。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1824', 'poem', '唐', '崔铉', '咏架上鹰', '天边心胆架头身，欲拟飞腾未有因。\\n万里碧霄终一去，不知谁是解绦人。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1825', 'poem', '唐', '韦应物', '喜园中茶生', '洁性不可污，为饮涤尘烦。\\n此物信灵味，本自出山原。\\n聊因理郡馀，率尔植荒园。\\n喜随众草长，得与幽人言。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1826', 'poem', '唐', '白居易', '湖亭望水', '久雨南湖涨，新晴北客过。\\n日沉红有影，风定绿无波。\\n岸没闾阎少，滩平船舫多。\\n可怜心赏处，其奈独游何。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1827', 'poem', '唐', '韦应物', '休假日访王侍御不遇', '九日驱驰一日闲，寻君不遇又空还。\\n怪来诗思清人骨，门对寒流雪满山。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1828', 'poem', '唐', '杜牧', '寄远', '两叶愁眉愁不开，独含惆怅上层台。\\n碧云空断雁行处，红叶已凋人未来。\\n塞外音书无信息，道傍车马起尘埃。\\n功名待寄凌烟阁，力尽辽城不肯回。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1829', 'poem', '唐', '李白', '上皇西巡南京歌十首', '胡尘轻拂建章台，圣主西巡蜀道来。\\n剑壁门高五千尺，石为楼阁九天开。\\n九天开出一成都，万户千门入画图。\\n草树云山如锦绣，秦川得及此间无。\\n华阳春树号新丰，行入新都若旧宫。\\n柳色未饶秦地绿，花光不减上阳红。\\n谁道君王行路难，六龙西幸万人欢。\\n地转锦江成渭水，天回玉垒作长安。\\n万国同风共一时，锦江何谢曲江池。\\n石镜更明天上月，后宫亲得照蛾眉。\\n濯锦清江万里流，云帆龙舸下扬州。\\n北地虽夸上林苑，南京还有散花楼。\\n锦水东流绕锦城，星桥北挂象天星。\\n四海此中朝圣主，峨眉山下列仙庭。\\n秦开蜀道置金牛，汉水元通星汉流。\\n天子一行遗圣迹，锦城长作帝王州。\\n水绿天青不起尘，风光和暖胜三秦。\\n万国烟花随玉辇，西来添作锦江春。\\n剑阁重关蜀北门，上皇归马若云屯。\\n少帝长安开紫极，双悬日月照乾坤。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1830', 'poem', '唐', '沈佺期', '遥同杜员外审言过岭', '天长地阔岭头分，去国离家见白云。\\n洛浦风光何所似，崇山瘴疠不堪闻。\\n南浮涨海人何处，北望衡阳雁几群。\\n两地江山万馀里，何时重谒圣明君。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1831', 'poem', '唐', '贾岛', '送路', '别我就蓬蒿，日斜飞伯劳。\\n龙门流水急，嵩岳片云高。\\n叹命无知己，梳头落白毛。\\n从军当此去，风起广陵涛。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1832', 'poem', '唐', '皎然', '九日与陆处士羽饮茶', '九日山僧院，东篱菊也黄。\\n俗人多泛酒，谁解助茶香。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1833', 'poem', '唐', '张潮', '江南行', '茨菰叶烂别西湾，莲子花开犹未还。\\n妾梦不离江水上，人传郎在凤凰山。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1834', 'poem', '唐', '薛涛', '筹边楼', '平临云鸟八窗秋，壮压西川四十州。\\n诸将莫贪羌族马，最高层处见边头。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1835', 'poem', '唐', '韩愈', '游太平公主山庄', '公主当年欲占春，故将台榭押城闉。\\n欲知前面花多少，直到南山不属人。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1836', 'poem', '唐', '白居易', '船夜援琴', '鸟栖鱼不动，月照夜江深。\\n身外都无事，舟中只有琴。\\n七弦为益友，两耳是知音。\\n心静即声淡，其间无古今。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1837', 'poem', '唐', '李白', '秋思', '燕支黄叶落，妾望自登台。\\n海上碧云断，单于秋色来。\\n胡兵沙塞合，汉使玉关回。\\n征客无归日，空悲蕙草摧。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1838', 'poem', '唐', '陆龟蒙', '闲书', '病学高僧置一床，披衣才暇即焚香。\\n闲阶雨过苔花润，小簟风来薤叶凉。\\n南国羽书催部曲，东山毛褐傲羲皇。\\n升平闻道无时节，试问中林亦不妨。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1839', 'poem', '唐', '吴融', '闲书', '接鹭陪鸾漫得群，未如高卧紫溪云。\\n晋阳起义寻常见，湖口屯营取次闻。\\n大底鹪鹏须自适，何尝玉石不同焚。\\n回看带砺山河者，济得危时没旧勋。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1840', 'poem', '唐', '吕岩', '六言', '春暖群花半开，逍遥石上徘徊。\\n独携玉律丹诀，闲踏青莎碧苔。\\n古洞眠来九载，流霞饮几千杯。\\n逢人莫话他事，笑指白云去来。', '2018-03-09 15:07:54');
INSERT INTO `t_poem` VALUES ('1841', 'poem', '唐', '李白', '西岳云台歌送丹丘子', '西岳峥嵘何壮哉！黄河如丝天际来。\\n黄河万里触山动，盘涡毂转秦地雷。\\n荣光休气纷五彩，千年一清圣人在。\\n巨灵咆哮擘两山，洪波喷箭射东海。\\n三峰却立如欲摧，翠崖丹谷高掌开。\\n白帝金精运元气，石作莲花云作台。\\n云台阁道连窈冥，中有不死丹丘生。\\n明星玉女备洒扫，麻姑搔背指爪轻。\\n我皇手把天地户，丹丘谈天与天语。\\n九重出入生光辉，东来蓬莱复西归。\\n玉浆倘惠故人饮，骑二茅龙上天飞。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1842', 'poem', '唐', '白居易', '得袁相书', '谷苗深处一农夫，面黑头斑手把锄。\\n何意使人犹识我，就田来送相公书。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1843', 'poem', '唐', '王建', '新嫁娘词三首', '邻家人未识，床上坐堆堆。\\n郎来傍门户，满口索钱财。\\n锦幛两边横，遮掩侍娘行。\\n遣郎铺簟席，相并拜亲情。\\n三日入厨下，洗手作羹汤。\\n未谙姑食性，先遣小姑尝。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1844', 'poem', '唐', '刘禹锡', '杨柳枝·杨子江头烟景迷', '杨子江头烟景迷，隋家宫树拂金堤。\\n嵯峨犹有当时色，半蘸波中水鸟栖。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1845', 'poem', '唐', '杜牧', '七夕', '银烛秋光冷画屏，轻罗小扇扑流萤。\\n天街夜色凉如水，卧看牵牛织女星。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1846', 'poem', '唐', '韦承庆', '南中咏雁诗', '万里人南去，三春雁北飞。\\n未知何岁月，得与尔同归？', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1847', 'poem', '唐', '白居易', '相和歌辞·怨诗', '夺宠心那惯，寻思倚殿门。\\n不知移旧爱，何处作新恩。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1848', 'poem', '唐', '白居易', '玩迎春花赠杨郎中', '金英翠萼带春寒，黄色花中有几般。\\n恁君与向游人道，莫作蔓菁花眼看。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1849', 'poem', '唐', '杜甫', '东屯北崦', '盗贼浮生困，诛求异俗贫。\\n空村惟见鸟，落日未逢人。\\n步壑风吹面，看松露滴身。\\n远山回白首，战地有黄尘。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1850', 'poem', '唐', '张籍', '南归', '促促念道路，四支不常宁。\\n行车未及家，天外非尽程。\\n骨肉待我欢，乡里望我荣。\\n岂知东与西，憔悴竟无成。\\n人言苦夜长，穷者不念明。\\n惧离其寝寐，百忧伤性灵。\\n世道多险薄，相劝毕中诚。\\n远游无知音，不如商贾行。\\n达人有常志，愚夫劳所营。\\n旧山行去远，言归乐此生。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1851', 'poem', '唐', '喻凫', '怀乡', '秋风江上家，钓艇泊芦花。\\n断岸绿杨荫，疏篱红槿遮。\\n鼍鸣积雨窟，鹤步夕阳沙。\\n抱疾僧窗夜，归心过月斜。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1852', 'poem', '唐', '白居易', '荔枝楼对酒', '荔枝新熟鸡冠色，烧酒初开琥珀香。\\n欲摘一枝倾一盏，西楼无客共谁尝。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1853', 'poem', '唐', '王维', '漆园', '古人非傲吏，自阙经世务。\\n偶寄一微官，婆娑数枝树。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1854', 'poem', '唐', '刘禹锡', '浪淘沙九首（第四首一作张籍诗）', '九曲黄河万里沙，浪淘风簸自天涯。\\n如今直上银河去，同到牵牛织女家。\\n洛水桥边春日斜，碧流清浅见琼砂。\\n无端陌上狂风急，惊起鸳鸯出浪花。\\n汴水东流虎眼纹，清淮晓色鸭头春。\\n君看渡口淘沙处，渡却人间多少人。\\n鹦鹉洲头浪飐沙，青楼春望日将斜。\\n衔泥燕子争归舍，独自狂夫不忆家。\\n濯锦江边两岸花，春风吹浪正淘沙。\\n女郎剪下鸳鸯锦，将向中流匹晚霞。\\n日照澄洲江雾开，淘金女伴满江隈。\\n美人首饰侯王印，尽是沙中浪底来。\\n八月涛声吼地来，头高数丈触山回。\\n须臾却入海门去，卷起沙堆似雪堆。\\n莫道谗言如浪深，莫言迁客似沙沉。\\n千淘万漉虽辛苦，吹尽狂沙始到金。\\n流水淘沙不暂停，前波未灭后波生。\\n令人忽忆潇湘渚，回唱迎神三两声。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1855', 'poem', '唐', '戎昱', '塞下曲', '汉将归来虏塞空，旌旗初下玉关东。\\n高蹄战马三千匹，落日平原秋草中。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1856', 'poem', '唐', '李约', '江南春·池塘春暖水纹开', '池塘春暖水纹开，堤柳垂丝间野梅。\\n江上年年芳意早，蓬瀛春色逐潮来。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1857', 'poem', '唐', '杜牧', '秋日', '有计自安业，秋风罢远吟。\\n买山惟种竹，对客更弹琴。\\n烟起药厨晚，杵声松院深。\\n闲眠得真性，惆怅旧时心。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1858', 'poem', '唐', '王维', '寄荆州张丞相', '所思竟何在，怅望深荆门。\\n举世无相识，终身思旧恩。\\n方将与农圃，艺植老丘园。\\n目尽南飞雁，何由寄一言。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1859', 'poem', '唐', '郭震', '莲花', '脸腻香薰似有情，世间何物比轻盈。\\n湘妃雨后来池看，碧玉盘中弄水晶。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1860', 'poem', '唐', '唐无名氏', '无题', '春水春池满，春时春草生。\\n春人饮春酒，春鸟弄春声。\\n君生我未生，我生君已老。\\n君恨我生迟，我恨君生早。\\n人归万里外，意在一杯中。\\n只虑前程远，开帆待好风。\\n自入长信宫，每对孤灯泣。\\n闺门镇不开，梦从何处入。\\n一别行千里，来时未有期。\\n月中三十日，无夜不相思。', '2018-03-09 15:08:02');
INSERT INTO `t_poem` VALUES ('1861', 'poem', '唐', '岑参', '暮秋山行', '疲马卧长坡，夕阳下通津。\\n山风吹空林，飒飒如有人。\\n苍旻霁凉雨，石路无飞尘。\\n千念集暮节，万籁悲萧晨。\\n鶗鴂昨夜鸣，蕙草色已陈。\\n况在远行客，自然多苦辛。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1862', 'poem', '唐', '王维', '夷门歌', '七雄雄雌犹未分，攻城杀将何纷纷。\\n秦兵益围邯郸急，魏王不救平原君。\\n公子为嬴停驷马，执辔愈恭意愈下。\\n亥为屠肆鼓刀人，嬴乃夷门抱关者。\\n非但慷慨献良谋，意气兼将身命酬。\\n向风刎颈送公子，七十老翁何所求。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1863', 'poem', '唐', '李贺', '昌谷北园新笋四首', '箨落长竿削玉开，君看母笋是龙材。\\n更容一夜抽千尺，别却池园数寸泥。\\n斫取青光写楚辞，腻香春粉黑离离。\\n无情有恨何人见，露压烟啼千万枝。\\n家泉石眼两三茎，晓看阴根紫脉生。\\n今年水曲春沙上，笛管新篁拔玉青。\\n古竹老梢惹碧云，茂陵归卧叹清贫。\\n风吹千亩迎雨啸，鸟重一枝入酒尊。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1864', 'poem', '唐', '韩偓', '故都', '故都遥想草萋萋，上帝深疑亦自迷。\\n塞雁已侵池籞宿，宫鸦犹恋女墙啼。\\n天涯烈士空垂涕，地下强魂必噬脐。\\n掩鼻计成终不觉，冯驩无路斅鸣鸡。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1865', 'poem', '唐', '孟浩然', '南归阻雪', '我行滞宛许，日夕望京豫。\\n旷野莽茫茫，乡山在何处。\\n孤烟村际起，归雁天边去。\\n积雪覆平皋，饥鹰捉寒兔。\\n少年弄文墨，属意在章句。\\n十上耻还家，裴回守归路。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1866', 'poem', '唐', '李贺', '杂曲歌辞·神仙曲', '碧峰海面藏灵书，上帝拣作神仙居。\\n晴时笑语闻空虚，斗乘巨浪骑鲸鱼。\\n春罗翦字邀王母，共宴红楼最深处。\\n鹤羽冲风过海迟，不如却使青龙去。\\n犹疑王母不相许，垂露娃鬟更传语。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1867', 'poem', '唐', '李贺', '神仙曲', '碧峰海面藏灵书，上帝拣作神仙居。\\n清明笑语闻空虚，斗乘巨浪骑鲸鱼。\\n春罗书字邀王母，共宴红楼最深处。\\n鹤羽冲风过海迟，不如却使青龙去。\\n犹疑王母不相许，垂露娃鬟更传语。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1868', 'poem', '唐', '白居易', '晚桃花', '一树红桃亚拂池，竹遮松荫晚开时。非因斜日无由见，\\n不是闲人岂得知。寒地生材遗校易，贫家养女嫁常迟。\\n春深欲落谁怜惜，白侍郎来折一枝。主', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1869', 'poem', '唐', '韦庄', '陪金陵府相中堂夜宴', '满耳笙歌满眼花，满楼珠翠胜吴娃。\\n因知海上神仙窟，只似人间富贵家。\\n绣户夜攒红烛市，舞衣晴曳碧天霞。\\n却愁宴罢青蛾散，杨子江头月半斜。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1870', 'poem', '唐', '周昙', '春秋战国门·鲍叔', '忠臣祝寿吐嘉词，鲍叔临轩酒一卮。\\n安不忘危臣所愿，愿思危困必无危。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1871', 'poem', '唐', '李白', '古风·其十五', '燕昭延郭隗，遂筑黄金台。\\n剧辛方赵至，邹衍复齐来。\\n奈何青云士，弃我如尘埃。\\n珠玉买歌笑，糟糠养贤才。\\n方知黄鹤举，千里独徘徊。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1872', 'poem', '唐', '白居易', '池上竹下作', '穿篱绕舍碧逶迤，十亩闲居半是池。\\n食饱窗间新睡后，脚轻林下独行时。\\n水能性淡为吾友，竹解心虚即我师。\\n何必悠悠人世上，劳心费目觅亲知。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1873', 'poem', '唐', '白居易', '放言五首·其二', '世途倚伏都无定，尘网牵缠卒未休。\\n祸福回还车轮毂，荣枯反覆手藏钩。\\n龟灵未免刳肠患，马失应无折足忧。\\n不信请看弈棋者，输赢须待局终头。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1874', 'poem', '唐', '岑参', '武威春暮闻宇文判官西使还已到晋昌', '岸雨过城头，黄鹂上戍楼。\\n塞花飘客泪，边柳挂乡愁。\\n白发悲明镜，青春换敝裘。\\n君从万里使，闻已到瓜州。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1875', 'poem', '唐', '刘禹锡', '堤上行三首', '酒旗相望大堤头，堤下连樯堤上楼。\\n日暮行人争渡急，桨声幽轧满中流。\\n江南江北望烟波，入夜行人相应歌。\\n桃叶传情竹枝怨，水流无限月明多。\\n春堤缭绕水徘徊，酒舍旗亭次第开。\\n日晚上楼招估客，轲峨大艑落帆来。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1876', 'poem', '唐', '王勃', '山亭夜宴', '桂宇幽襟积，山亭凉夜永。\\n森沉野径寒，肃穆岩扉静。\\n竹晦南汀色，荷翻北潭影。\\n清兴殊未阑，林端照初景。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1877', 'poem', '唐', '李商隐', '青陵台', '青陵台畔日光斜，万古贞魂倚暮霞。\\n莫讶韩凭为蛱蝶，等闲飞上别枝花。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1878', 'poem', '唐', '胡宿', '城南', '昨夜轻阴结夕霏，城南十里有香泥。\\n初闻山鸟惊新哢，遥见林花识旧蹊。\\n荡桨远从芳草渡，垫巾还傍绿杨堤。\\n罗敷正苦桑蚕事，惆怅南来五马蹄。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1879', 'poem', '唐', '卢纶', '逢病军人', '行多有病住无粮，万里还乡未到乡。\\n蓬鬓哀吟长城下，不堪秋气入金疮。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1880', 'poem', '唐', '崔国辅', '小长干曲', '月暗送潮风，相寻路不通。\\n菱歌唱不彻，知在此塘中。', '2018-03-09 15:08:11');
INSERT INTO `t_poem` VALUES ('1881', 'poem', '唐', '李白', '送韩侍御之广德', '昔日绣衣何足荣，今宵贳酒与君倾。\\n暂就东山赊月色，酣歌一夜送泉明。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1882', 'poem', '唐', '张碧', '农父', '运锄耕劚侵星起，陇亩丰盈满家喜。\\n到头禾黍属他人，不知何处抛妻子。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1883', 'poem', '唐', '高适', '金城北楼', '北楼西望满晴空，积水连山胜画中。\\n湍上急流声若箭，城头残月势如弓。\\n垂竿已羡磻溪老，体道犹思塞上翁。\\n为问边庭更何事，至今羌笛怨无穷。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1884', 'poem', '唐', '白居易', '短歌行', '曈曈太阳如火色，上行千里下一刻。\\n出为白昼入为夜，圆转如珠住不得。\\n住不得，可奈何，为君举酒歌短歌。\\n歌声苦，词亦苦，四座少年君听取。\\n今夕未竟明夕催，秋风才往春风回。\\n人无根蒂时不驻，朱颜白日相隳颓。\\n劝君且强笑一面，劝君且强饮一杯。\\n人生不得长欢乐，年少须臾老到来。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1885', 'poem', '唐', '贾岛', '即事', '索莫对孤灯，阴云积几层。\\n自嗟怜十上，谁肯待三征。\\n心被通人见，文叨大匠称。\\n悲秋秦塞草，怀古汉家陵。\\n城静高崖树，漏多幽沼冰。\\n过声沙岛鹭，绝行石庵僧。\\n岂谓旧庐在，谁言归未曾。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1886', 'poem', '唐', '白居易', '曲江忆元九', '春来无伴闲游少，行乐三分减二分。\\n何况今朝杏园里，闲人逢尽不逢君。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1887', 'poem', '唐', '刘得仁', '中秋', '尘里兼尘外，咸期此夕明。\\n一年惟一度，长恐有云生。\\n露洗微埃尽，光濡是物清。\\n朗吟看正好，惆怅又西倾。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1888', 'poem', '唐', '李商隐', '十一月中旬至扶风界见梅花', '匝路亭亭艳，非时裛裛香。\\n素娥惟与月，青女不饶霜。\\n赠远虚盈手，伤离适断肠。\\n为谁成早秀，不待作年芳。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1889', 'poem', '唐', '李白', '送别得书字', '水色南天远，舟行若在虚。\\n迁人发佳兴，吾子访闲居。\\n日落看归鸟，潭澄羡跃鱼。\\n圣朝思贾谊，应降紫泥书。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1890', 'poem', '唐', '杜甫', '燕子来舟中作', '湖南为客动经春，燕子衔泥两度新。\\n旧入故园尝识主，如今社日远看人。\\n可怜处处巢居室，何异飘飘托此身。\\n暂语船樯还起去，穿花贴水益沾巾。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1891', 'poem', '唐', '张翚', '绝句', '茫茫烟水上，日暮阴云飞。\\n孤坐正愁绪，湖南谁捣衣。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1892', 'poem', '唐', '周昙', '春秋战国门·田子方', '太子无嫌礼乐亏，愿听贫富与安危。\\n贱贫骄物贫终在，富贵骄人贵必隳。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1893', 'poem', '唐', '刘禹锡', '和令狐相公咏栀子花', '蜀国花已尽，越桃今已开。\\n色疑琼树倚，香似玉京来。\\n且赏同心处，那忧别叶催。\\n佳人如拟咏，何必待寒梅。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1894', 'poem', '唐', '李白', '登峨眉山', '蜀国多仙山，峨眉邈难匹。\\n周流试登览，绝怪安可悉？\\n青冥倚天开，彩错疑画出。\\n泠然紫霞赏，果得锦囊术。\\n云间吟琼箫，石上弄宝瑟。\\n平生有微尚，欢笑自此毕。\\n烟容如在颜，尘累忽相失。\\n倘逢骑羊子，携手凌白日。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1895', 'poem', '唐', '白居易', '酬和元九东川路诗十二首·江岸梨花', '梨花有思缘和叶，一树江头恼杀君。\\n最似孀闺少年妇，白妆素袖碧纱裙。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1896', 'poem', '唐', '许浑', '秋思', '琪树西风枕簟秋，楚云湘水忆同游。\\n高歌一曲掩明镜，昨日少年今白头。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1897', 'poem', '唐', '王维', '送赵都督赴代州得青字', '天官动将星，汉上柳条青。\\n万里鸣刁斗，三军出井陉。\\n忘身辞凤阙，报国取龙庭。\\n岂学书生辈，窗间老一经。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1898', 'poem', '唐', '李峤', '书', '削简龙文见，临池鸟迹舒。\\n河图八卦出，洛范九畴初。\\n垂露春光满，崩云骨气馀。\\n请君看入木，一寸乃非虚。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1899', 'poem', '唐', '杜甫', '除架', '束薪已零落，瓠叶转萧疏。\\n幸结白花了，宁辞青蔓除。\\n秋虫声不去，暮雀意何如。\\n寒事今牢落，人生亦有初。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1900', 'poem', '唐', '王维', '送丘为落第归江东', '怜君不得意，况复柳条春。\\n为客黄金尽，还家白发新。\\n五湖三亩宅，万里一归人。\\n知尔不能荐，羞称献纳臣。', '2018-03-09 15:08:17');
INSERT INTO `t_poem` VALUES ('1901', 'poem', '唐', '白居易', '箬岘东池', '箬岘亭东有小池，早荷新荇绿参差。\\n中宵把火行人发，惊起双栖白鹭鸶。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1902', 'poem', '唐', '王维', '叹白发', '宿昔朱颜成暮齿，须臾白发变垂髫。\\n一生几许伤心事，不向空门何处销。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1903', 'poem', '唐', '张祜', '樱桃', '石榴未拆梅犹小，爱此山花四五株。\\n斜日庭前风袅袅，碧油千片漏红珠。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1904', 'poem', '唐', '曹邺', '恃宠', '二月树色好，昭仪正骄奢。\\n恐君爱阳艳，斫却园中花。\\n三十六宫女，髻鬟各如鸦。\\n君王心所怜，独自不见瑕。\\n台上红灯尽，未肯下金车。\\n一笑不得所，尘中悉无家。\\n飞燕身更轻，何必恃容华。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1905', 'poem', '唐', '赵嘏', '喜张沨及第', '春雪满空来，触处似花开。\\n不知园里树，若个是真梅。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1906', 'poem', '唐', '杜牧', '残春独来南亭因寄张祜', '暖云如粉草如茵，独步长堤不见人。\\n一岭桃花红锦黦，半溪山水碧罗新。\\n高枝百舌犹欺鸟，带叶梨花独送春。\\n仲蔚欲知何处在，苦吟林下拂诗尘。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1907', 'poem', '唐', '李益', '登夏州城观送行人赋得六州胡儿歌', '六州胡儿六蕃语，十岁骑羊逐沙鼠。\\n沙头牧马孤雁飞，汉军游骑貂锦衣。\\n云中征戍三千里，今日征行何岁归。\\n无定河边数株柳，共送行人一杯酒。\\n胡儿起作和蕃歌，齐唱呜呜尽垂手。\\n心知旧国西州远，西向胡天望乡久。\\n回头忽作异方声，一声回尽征人首。\\n蕃音虏曲一难分，似说边情向塞云。\\n故国关山无限路，风沙满眼堪断魂。\\n不见天边青作冢，古来愁杀汉昭君。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1908', 'poem', '唐', '李群玉', '寄人', '寄语双莲子，须知用意深。\\n莫嫌一点苦，便拟弃莲心。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1909', 'poem', '唐', '罗隐', '故都', '江南江北两风流，一作迷津一拜侯。\\n至竟不如隋炀帝，破家犹得到扬州。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1910', 'poem', '唐', '李绅', '宿扬州', '江横渡阔烟波晚，潮过金陵落叶秋。\\n嘹唳塞鸿经楚泽，浅深红树见扬州。\\n夜桥灯火连星汉，水郭帆樯近斗牛。\\n今日市朝风俗变，不须开口问迷楼。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1911', 'poem', '唐', '李益', '长干行', '忆妾深闺里，烟尘不曾识。\\n嫁与长干人，沙头候风色。\\n五月南风兴，思君下巴陵。\\n八月西风起，想君发扬子。\\n去来悲如何，见少离别多。\\n湘潭几日到，妾梦越风波。\\n昨夜狂风度，吹折江头树。\\n渺渺暗无边，行人在何处。\\n好乘浮云骢，佳期兰渚东。\\n鸳鸯绿浦上，翡翠锦屏中。\\n自怜十五馀，颜色桃花红。\\n那作商人妇，愁水复愁风。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1912', 'poem', '唐', '陆龟蒙', '庭前', '合欢能解恚，萱草信忘忧。\\n尽向庭前种，萋萋特地愁。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1913', 'poem', '唐', '曹松', '中秋对月', '无云世界秋三五，共看蟾盘上海涯。\\n直到天头天尽处，不曾私照一人家。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1914', 'poem', '唐', '张祜', '雨霖铃·雨霖铃夜却归秦', '雨霖铃夜却归秦，犹见张徽一曲新。\\n长说上皇和泪教，月明南内更无人。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1915', 'poem', '唐', '陆龟蒙', '华阳巾', '莲花峰下得佳名，云褐相兼上鹤翎。\\n须是古坛秋霁后，静焚香炷礼寒星。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1916', 'poem', '唐', '李商隐', '丹丘', '青女丁宁结夜霜，羲和辛苦送朝阳。\\n丹丘万里无消息，几对梧桐忆凤凰。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1917', 'poem', '唐', '元稹', '兔丝', '人生莫依倚，依倚事不成。\\n君看兔丝蔓，依倚榛与荆。\\n荆榛易蒙密，百鸟撩乱鸣。\\n下有狐兔穴，奔走亦纵横。\\n樵童斫将去，柔蔓与之并。\\n翳荟生可耻，束缚死无名。\\n桂树月中出，珊瑚石上生。\\n俊鹘度海食，应龙升天行。\\n灵物本特达，不复相缠萦。\\n缠萦竟何者，荆棘与飞茎。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1918', 'poem', '唐', '蒋贻恭', '咏蚕', '辛勤得茧不盈筐，灯下缫丝恨更长。\\n著处不知来处苦，但贪衣上绣鸳鸯。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1919', 'poem', '唐', '胡令能', '喜韩少府见访', '忽闻梅福来相访，笑著荷衣出草堂。\\n儿童不惯见车马，走入芦花深处藏。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1920', 'poem', '唐', '李咸用', '自愧', '多负悬弧礼，危时隐薜萝。\\n有心明俎豆，无力执干戈。\\n壮士难移节，贞松不改柯。\\n缨尘徒自满，欲濯待清波。', '2018-03-09 15:08:24');
INSERT INTO `t_poem` VALUES ('1921', 'poem', '唐', '徐凝', '庐山瀑布', '虚空落泉千仞直，雷奔入江不暂息。\\n今古长如白练飞，一条界破青山色。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1922', 'poem', '唐', '白居易', '红线毯', '红线毯，择茧缲丝清水煮，拣丝练线红蓝染。\\n染为红线红于蓝，织作披香殿上毯。披香殿广十丈余，\\n红线织成可殿铺。彩丝茸茸香拂拂，线软花虚不胜物。\\n美人踏上歌舞来，罗袜绣鞋随步没。太原毯涩毳缕硬，\\n蜀都褥薄锦花冷，不如此毯温且柔，年年十月来宣州。\\n宣城太守加样织，自谓为臣能竭力。百夫同担进宫中，\\n线厚丝多卷不得。宣城太守知不知，一丈毯，千两丝。\\n地不知寒人要暖，少夺人衣作地衣。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1923', 'poem', '唐', '赵彦昭', '杂曲歌辞·桃花行', '红萼竞妍春苑曙，粉茸新向御筵开。\\n长年愿奉西王宴，近侍惭无东朔才。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1924', 'poem', '唐', '白居易', '春词', '低花树映小妆楼，春入眉心两点愁。\\n斜倚栏干背鹦鹉，思量何事不回头。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1925', 'poem', '唐', '卢殷', '晚蝉', '深藏高柳背斜晖，能轸孤愁减昔围。\\n犹畏旅人头不白，再三移树带声飞。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1926', 'poem', '唐', '白居易', '雨夜忆元九', '天阴一日便堪愁，何况连宵雨不休。\\n一种雨中君最苦，偏梁阁道向通州。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1927', 'poem', '唐', '王季友', '玉壶冰', '玉壶知素结，止水复中澄。\\n坚白能虚受，清寒得自凝。\\n分形同晓镜，照物掩宵灯。\\n壁映圆光入，人惊爽气凌。\\n金罍何足贵，瑶席几回升。\\n正值求珪瓒，提携共饮冰。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1928', 'poem', '唐', '钱众仲', '玉壶冰', '冬律初阴结，寒冰贮玉壶。\\n霜姿虽异禀，虹气亦相符。\\n对月光宜并，临池影不孤。\\n贞坚方共济，同处岂殊途。\\n色莹连城璧，形分照乘珠。\\n提携今在此，抱素节宁渝。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1929', 'poem', '唐', '无名氏', '玉壶冰', '玄律阴风劲，坚冰在玉壶。\\n暗中花更出，晓后色全无。\\n涸沍谁能伴，凄清讵可渝。\\n任圆空似璧，照物不成珠。\\n素质情方契，孤明道岂殊。\\n幽人若相比，还得咏生刍。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1930', 'poem', '唐', '王季友', '玉壶冰', '玉壶知素结，止水复中澄。\\n坚白能虚受，清寒得自凝。\\n分形同晓镜，照物掩宵灯。\\n壁映圆光入，人惊爽气凌。\\n金罍何足贵，瑶席几回升。\\n正值求珪瓒，提携共饮冰。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1931', 'poem', '唐', '皇甫冉', '送魏十六还苏州', '秋夜深深北送君，阴虫切切不堪闻。\\n归舟明日毗陵道，回首姑苏是白云。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1932', 'poem', '唐', '杜甫', '晓望白帝城盐山', '徐步移班杖，看山仰白头。\\n翠深开断壁，红远结飞楼。\\n日出清江望，暄和散旅愁。\\n春城见松雪，始拟进归舟。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1933', 'poem', '唐', '白居易', '三年别', '悠悠一别已三年，相望相思明月天。\\n肠断青天望明月，别来三十六回圆。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1934', 'poem', '唐', '王初', '雪霁', '星榆叶叶昼离披，云粉千重凝不飞。\\n昆玉楼台珠树密，夜来谁向月中归。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1935', 'poem', '唐', '张南史', '草', '草，草。折宜，看好。满地生，催人老。金殿玉砌，\\n荒城古道。青青千里遥，怅怅三春早。每逢南北离别，\\n乍逐东西倾倒。一身本是山中人，聊与王孙慰怀抱。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1936', 'poem', '唐', '李白', '游泰山六首', '四月上泰山，石屏御道开。\\n六龙过万壑，涧谷随萦回。\\n马迹绕碧峰，于今满青苔。\\n飞流洒绝巘，水急松声哀。\\n北眺崿嶂奇，倾崖向东摧。\\n洞门闭石扇，地底兴云雷。\\n登高望蓬瀛，想象金银台。\\n天门一长啸，万里清风来。\\n玉女四五人，飘飖下九垓。\\n含笑引素手，遗我流霞杯。\\n稽首再拜之，自愧非仙才。\\n旷然小宇宙，弃世何悠哉。\\n清晓骑白鹿，直上天门山。\\n山际逢羽人，方瞳好容颜。\\n扪萝欲就语，却掩青云关。\\n遗我鸟迹书，飘然落岩间。\\n其字乃上古，读之了不闲。\\n感此三叹息，从师方未还。\\n平明登日观，举手开云关。\\n精神四飞扬，如出天地间。\\n黄河从西来，窈窕入远山。\\n凭崖揽八极，目尽长空闲。\\n偶然值青童，绿发双云鬟。\\n笑我晚学仙，蹉跎凋朱颜。\\n踌躇忽不见，浩荡难追攀。\\n清斋三千日，裂素写道经。\\n吟诵有所得，众神卫我形。\\n云行信长风，飒若羽翼生。\\n攀崖上日观，伏槛窥东暝。\\n海色动远山，天鸡已先鸣。\\n银台出倒景，白浪翻长鲸。\\n安得不死药，高飞向蓬瀛。\\n日观东北倾，两崖夹双石。\\n海水落眼前，天光遥空碧。\\n千峰争攒聚，万壑绝凌历。\\n缅彼鹤上仙，去无云中迹。\\n长松入云汉，远望不盈尺。\\n山花异人间，五月雪中白。\\n终当遇安期，于此炼玉液。\\n朝饮王母池，暝投天门关。\\n独抱绿绮琴，夜行青山间。\\n山明月露白，夜静松风歇。\\n仙人游碧峰，处处笙歌发。\\n寂静娱清晖，玉真连翠微。\\n想像鸾凤舞，飘飖龙虎衣。\\n扪天摘匏瓜，恍惚不忆归。\\n举手弄清浅，误攀织女机。\\n明晨坐相失，但见五云飞。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1937', 'poem', '唐', '孟郊', '君子勿郁郁士有谤毁者作诗以赠之', '君子勿郁郁，听我青蝇歌。\\n人间少平地，森耸山岳多。\\n折輈不在道，覆舟不在河。\\n须知一尺水，日夜增高波。\\n叔孙毁仲尼，臧仓掩孟轲。\\n兰艾不同香，自然难为和。\\n良玉烧不热，直竹文不颇。\\n自古皆如此，其如道在何。\\n日往复不见，秋堂暮仍学。\\n玄发不知白，晓入寒铜觉。\\n为林未离树，有玉犹在璞。\\n谁把碧梧枝，刻作云门乐。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1938', 'poem', '唐', '杜审言', '旅寓安南', '交趾殊风候，寒迟暖复催。\\n仲冬山果熟，正月野花开。\\n积雨生昏雾，轻霜下震雷。\\n故乡逾万里，客思倍从来。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1939', 'poem', '唐', '罗隐', '韦公子', '击柱狂歌惨别颜，百年人事梦魂间。\\n李将军自嘉声在，不得封侯亦自闲。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1940', 'poem', '唐', '王建', '李处士故居', '露浓烟重草萋萋，树映阑干柳拂堤。\\n一院落花无客醉，半窗残月有莺啼。\\n芳筵想像情难尽，故榭荒凉路欲迷。\\n风景宛然人自改，却经门外马频嘶。', '2018-03-09 15:08:31');
INSERT INTO `t_poem` VALUES ('1941', 'poem', '唐', '林逋', '咏秋江', '苍茫沙嘴鹭鸶眠，片水无痕浸碧天。\\n最爱芦花经雨后，一蓬烟火饭鱼船。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1942', 'poem', '唐', '唐彦谦', '咏月', '阴盛此宵中，多为雨与风。\\n坐无风雨至，看与雪霜同。\\n抱湿离遥海，倾寒向远空。\\n年年不相值，还似道难通。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1943', 'poem', '唐', '白居易', '除夜寄弟妹', '感时思弟妹，不寐百忧生。\\n万里经年别，孤灯此夜情。\\n病容非旧日，归思逼新正。\\n早晚重欢会，羁离各长成。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1944', 'poem', '唐', '崔涂', '折杨柳·朝朝车马如蓬转', '朝朝车马如蓬转，处处江山待客归。\\n若使人间少离别，杨花应合过春飞。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1945', 'poem', '唐', '罗邺', '汴河', '炀帝开河鬼亦悲，生民不独力空疲。\\n至今呜咽东流水，似向清平怨昔时。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1946', 'poem', '唐', '杜甫', '花底', '紫萼扶千蕊，黄须照万花。\\n忽疑行暮雨，何事入朝霞。\\n恐是潘安县，堪留卫玠车。\\n深知好颜色，莫作委泥沙。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1947', 'poem', '唐', '薛逢', '咏柳', '弱植惊风急自伤，暮来翻遣思悠扬。\\n曾飘紫陌随高下，敢拂朱阑竞短长。\\n萦砌乍飞还乍舞，扑池如雪又如霜。\\n莫令岐路频攀折，渐拟垂阴到画堂。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1948', 'poem', '唐', '钱起', '与赵莒茶宴', '竹下忘言对紫茶，全胜羽客醉流霞。\\n尘心洗尽兴难尽，一树蝉声片影斜。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1949', 'poem', '唐', '李白', '巴女词', '巴水急如箭，巴船去若飞。\\n十月三千里，郎行几岁归。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1950', 'poem', '唐', '杜甫', '晚晴', '返照斜初彻，浮云薄未归。\\n江虹明远饮，峡雨落馀飞。\\n凫雁终高去，熊罴觉自肥。\\n秋分客尚在，竹露夕微微。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1951', 'poem', '唐', '杜甫', '黄河二首', '黄河北岸海西军，椎鼓鸣钟天下闻。\\n铁马长鸣不知数，胡人高鼻动成群。\\n黄河西岸是吾蜀，欲须供给家无粟。\\n愿驱众庶戴君王，混一车书弃金玉。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1952', 'poem', '唐', '王绩', '赠程处士', '百年长扰扰，万事悉悠悠。\\n日光随意落，河水任情流。\\n礼乐囚姬旦，诗书缚孔丘。\\n不如高枕枕，时取醉消愁。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1953', 'poem', '唐', '李世民', '帝京篇十首', '秦川雄帝宅，函谷壮皇居。\\n绮殿千寻起，离宫百雉馀。\\n连薨遥接汉，飞观迥凌虚。\\n云日隐层阙，风烟出绮疏。\\n岩廊罢机务，崇文聊驻辇。\\n玉匣启龙图，金绳披凤篆。\\n韦编断仍续，缥帙舒还卷。\\n对此乃淹留，欹案观坟典。\\n移步出词林，停舆欣武宴。\\n雕弓写明月，骏马疑流电。\\n惊雁落虚弦，啼猿悲急箭。\\n阅赏诚多美，于兹乃忘倦。\\n鸣笳临乐馆，眺听欢芳节。\\n急管韵朱弦，清歌凝白雪。\\n彩凤肃来仪，玄鹤纷成列。\\n去兹郑卫声，雅音方可悦。\\n芳辰追逸趣，禁苑信多奇。\\n桥形通汉上，峰势接云危。\\n烟霞交隐映，花鸟自参差。\\n何如肆辙迹，万里赏瑶池。\\n飞盖去芳园，兰桡游翠渚。\\n萍间日彩乱，荷处香风举。\\n桂楫满中川，弦歌振长屿。\\n岂必汾河曲，方为欢宴所。\\n落日双阙昏，回舆九重暮。\\n长烟散初碧，皎月澄轻素。\\n搴幌玩琴书，开轩引云雾。\\n斜汉耿层阁，清风摇玉树。\\n欢乐难再逢，芳辰良可惜。\\n玉酒泛云罍，兰殽陈绮席。\\n千钟合尧禹，百兽谐金石。\\n得志重寸阴，忘怀轻尺璧。\\n建章欢赏夕，二八尽妖妍。\\n罗绮昭阳殿，芬芳玳瑁筵。\\n佩移星正动，扇掩月初圆。\\n无劳上悬圃，即此对神仙。\\n以兹游观极，悠然独长想。\\n披卷览前踪，抚躬寻既往。\\n望古茅茨约，瞻今兰殿广。\\n人道恶高危，虚心戒盈荡。\\n奉天竭诚敬，临民思惠养。\\n纳善察忠谏，明科慎刑赏。\\n六五诚难继，四三非易仰。\\n广待淳化敷，方嗣云亭响。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1954', 'poem', '唐', '吕洞宾', '绝句', '捉得金晶固命基，日魂东畔月华西。\\n于中炼就长生药，服了还同天地齐。\\n莫怪瑶池消息稀，只缘尘事隔天机。\\n若人寻得水中火，有一黄童上太微。\\n混元海底隐生伦，内有黄童玉帝名。\\n白虎神符潜姹女，灵元镇在七元君。\\n三亩丹田无种种，种时须藉赤龙耕。\\n曾将此种教人种，不解铅池道不生。\\n闪灼虎龙神剑飞，好凭身事莫相违。\\n传时须在乾坤力，便透三清入紫微。\\n不用梯媒向外求，还丹只在体中收。\\n莫言大道人难得，自是功夫不到头。\\n饮酒须教一百杯，东浮西泛自梯媒。\\n日精自与月华合，有个明珠走上来。\\n不负三光不负人，不欺神道不欺贫。\\n有人问我修行法，只种心田养此身。\\n时人若拟去瀛洲，先过巍巍十八楼。\\n自有电雷声震动，一池金水向东流。\\n瓶子如金玉子黄，上升下降续神光。\\n三元一会经年净，这个天中日月长。\\n学道须教彻骨贫，囊中只有五三文。\\n有人问我修行法，遥指天边日月轮。\\n我自忘心神自悦，跨水穿云来相谒。\\n不问黄芽肘后方，妙道通微怎生说。\\n肘传丹篆千年术，口诵黄庭两卷经。\\n鹤观古坛松影里，悄无人迹户长扃。\\n独上高峰望八都，黑云散后月还孤。\\n茫茫宇宙人无数，几个男儿是丈夫。\\n天下都游半日功，不须跨凤与乘龙。\\n偶因博戏飞神剑，摧却终南第一峰。\\n朝游北越暮苍梧，袖里青蛇胆气粗。\\n三入岳阳人不识，朗吟飞过洞庭湖。\\n趯倒葫芦掉却琴，倒行直上卧牛岑。\\n水飞石上迸如雪，立地看天坐地吟。\\n吾家本住在天齐，零落白云锁石梯。\\n来往八千消半日，依前归路不曾迷。\\n莲峰道士高且洁，不下莲宫经岁月。\\n星辰夜礼玉簪寒，龙虎晓开金鼎热。\\n东山东畔忽相逢，握手丁宁语似钟。\\n剑术已成君把去，有蛟龙处斩蛟龙。\\n朝泛苍梧暮却还，洞中日月我为天。\\n匣中宝剑时时吼，不遇同人誓不传。\\n偎岩拍手葫芦舞，过岭穿云拄杖飞。\\n来往八千须半日，金州南畔有松扉。\\n养得儿形似我形，我身枯悴子光精。\\n生生世世常如此，争似留神养自身。\\n精养灵根气养神，此真之外更无真。\\n神仙不肯分明说，迷了千千万万人。\\n不事王侯不种田，日高犹自抱琴眠。\\n起来旋点黄金买，不使人间作业钱。\\n天涯海角人求我，行到天涯不见人。\\n忠孝义慈行方便，不须求我自然真。\\n莫道幽人一事无，闲中尽有静工夫。\\n闭门清昼读书罢，扫地焚香到日晡。\\n先生先生貌狞恶，拔剑当空气云错。\\n连喝三回急急去，欻然空里人头落。\\n剑起星奔万里诛，风雷时逐雨声粗。\\n人头携处非人在，何事高吟过五湖。\\n粗眉卓竖语如雷，闻说不平便放杯。\\n仗剑当空千里去，一更别我二更回。\\n先生先生莫外求，道要人传剑要收。\\n今日相逢江海畔，一杯村酒劝君休。\\n庞眉斗竖恶精神，万里腾空一踊身。\\n背上匣中三尺剑，为天且示不平人。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1955', 'poem', '唐', '司空图', '独望', '绿树连村暗，黄花出陌稀。\\n远陂春草绿，犹有水禽飞。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1956', 'poem', '唐', '李峤', '杂曲歌辞·桃花行', '岁去无言忽憔悴，时来含笑吐氛氲。\\n不能拥路迷仙客，故欲开蹊侍圣君。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1957', 'poem', '唐', '白居易', '送元八归凤翔', '莫道岐州三日程，其如风雪一身行。\\n与君况是经年别，暂到城来又出城。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1958', 'poem', '唐', '孟郊', '横吹曲辞·长安道', '胡风激秦树，贱子风中泣。\\n家家朱门开，得见不可入。\\n长安十二衢，投树鸟亦急。\\n高阁何人家，笙簧正喧吸。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1959', 'poem', '唐', '白居易', '七夕', '烟霄微月澹长空，银汉秋期万古同。\\n几许欢情与离恨，年年并在此宵中。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1960', 'poem', '唐', '宋之问', '扈从登封途中作', '帐殿郁崔嵬，仙游实壮哉。\\n晓云连幕卷，夜火杂星回。\\n谷暗千旗出，山鸣万乘来。\\n扈从良可赋，终乏掞天才。', '2018-03-09 15:08:36');
INSERT INTO `t_poem` VALUES ('1961', 'poem', '唐', '罗隐', '雪霁', '南山雪乍晴，寒气转峥嵘。\\n锁却闲门出，随他骏马行。\\n一竿如有计，五鼎岂须烹。\\n愁见天街草，青青又欲生。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1962', 'poem', '唐', '李煜', '病中感怀', '憔悴年来甚，萧条益自伤。\\n风威侵病骨，雨气咽愁肠。\\n夜鼎唯煎药，朝髭半染霜。\\n前缘竟何似，谁与问空王。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1963', 'poem', '唐', '卢纶', '寒食', '孤客飘飘岁载华，况逢寒食倍思家。\\n莺啼远墅多从柳，人哭荒坟亦有花。\\n浊水秦渠通渭急，黄埃京洛上原斜。\\n驱车西近长安好，宫观参差半隐霞。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1964', 'poem', '唐', '王勃', '送卢主簿', '穷途非所恨，虚室自相依。\\n城阙居年满，琴尊俗事稀。\\n开襟方未已，分袂忽多违。\\n东岩富松竹，岁暮幸同归。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1965', 'poem', '唐', '司空图', '诗品二十四则·典雅', '玉壶买春，赏雨茆屋。\\n坐中佳士，左右修竹。\\n白云初晴，幽鸟相逐。\\n眠琴绿阴，上有飞瀑。\\n落花无言，人淡如菊。\\n书之岁华，其曰可读。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1966', 'poem', '唐', '宋之问', '登逍遥楼', '逍遥楼上望乡关，绿水泓澄云雾间。\\n北去衡阳二千里，无因雁足系书还。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1967', 'poem', '唐', '杜甫', '送路六侍御入朝', '童稚情亲四十年，中间消息两茫然。\\n更为后会知何地，忽漫相逢是别筵。\\n不分桃花红胜锦，生憎柳絮白于绵。\\n剑南春色还无赖，触忤愁人到酒边。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1968', 'poem', '唐', '白居易', '赠内', '漠漠暗苔新雨地，微微凉露欲秋天。\\n莫对月明思往事，损君颜色减君年。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1969', 'poem', '唐', '王建', '夜看扬州市', '夜市千灯照碧云，高楼红袖客纷纷。\\n如今不似时平日，犹自笙歌彻晓闻。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1970', 'poem', '唐', '罗隐', '柳', '灞岸晴来送别频，相偎相倚不胜春。\\n自家飞絮犹无定，争解垂丝绊路人。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1971', 'poem', '唐', '罗隐', '汴河', '当时天子是闲游，今日行人特地愁。\\n柳色纵饶妆故国，水声何忍到扬州。\\n乾坤有意终难会，黎庶无情岂自由。\\n应笑秦皇用心错，谩驱神鬼海东头。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1972', 'poem', '唐', '白居易', '春至', '若为南国春还至，争向东楼日又长。\\n白片落梅浮涧水，黄梢新柳出城墙。\\n闲拈蕉叶题诗咏，闷取藤枝引酒尝。\\n乐事渐无身渐老，从今始拟负风光。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1973', 'poem', '唐', '贺知章', '晓发', '江皋闻曙钟，轻枻理还舼。\\n海潮夜约约，川露晨溶溶。\\n始见沙上鸟，犹埋云外峰。\\n故乡杳无际，明发怀朋从。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1974', 'poem', '唐', '王绩', '石竹咏', '萋萋结绿枝，晔晔垂朱英。\\n常恐零露降，不得全其生。\\n叹息聊自思，此生岂我情。\\n昔我未生时，谁者令我萌。\\n弃置勿重陈，委化何足惊。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1975', 'poem', '唐', '汪遵', '汴河', '隋皇意欲泛龙舟，千里昆仑水别流。\\n还待春风锦帆暖，柳阴相送到迷楼。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1976', 'poem', '唐', '鲍溶', '塞上行', '西风应时筋角坚，承露牧马水草冷。\\n可怜黄河九曲尽，毡馆牢落胡无影。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1977', 'poem', '唐', '白居易', '九江北岸遇风雨', '黄梅县边黄梅雨，白头浪里白头翁。\\n九江阔处不见岸，五月尽时多恶风。\\n人间稳路应无限，何事抛身在此中。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1978', 'poem', '唐', '韩偓', '柳', '一笼金线拂弯桥，几被儿童损细腰。\\n无奈灵和标格在，春来依旧褭长条。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1979', 'poem', '唐', '白居易', '食饱', '食饱拂枕卧，睡足起闲吟。\\n浅酌一杯酒，缓弹数弄琴。\\n既可畅情性，亦足傲光阴。\\n谁知利名尽，无复长安心。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1980', 'poem', '唐', '窦巩', '秋夕', '护霜云映月朦胧，乌鹊争飞井上桐。\\n夜半酒醒人不觉，满池荷叶动秋风。', '2018-03-09 15:08:43');
INSERT INTO `t_poem` VALUES ('1981', 'poem', '唐', '刘禹锡', '再授连州至衡阳酬柳柳州赠别', '去国十年同赴召，渡湘千里又分岐。\\n重临事异黄丞相，三黜名惭柳士师。\\n归目并随回雁尽，愁肠正遇断猿时。\\n桂江东过连山下，相望长吟有所思。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1982', 'poem', '唐', '张潮', '采莲词', '朝出沙头日正红，晚来云起半江中。\\n赖逢邻女曾相识，并著莲舟不畏风。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1983', 'poem', '唐', '白居易', '临水坐', '昔为东掖垣中客，今作西方社内人。\\n手把杨枝临水坐，闲思往事似前身。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1984', 'poem', '唐', '李商隐', '回中牡丹为雨所败二首', '下苑他年未可追，西州今日忽相期。\\n水亭暮雨寒犹在，罗荐春香暖不知。\\n舞蝶殷勤收落蕊，佳人惆怅卧遥帏。\\n章台街里芳菲伴，且问宫腰损几枝。\\n浪笑榴花不及春，先期零落更愁人。\\n玉盘迸泪伤心数，锦瑟惊弦破梦频。\\n万里重阴非旧圃，一年生意属流尘。\\n前溪舞罢君回顾，并觉今朝粉态新。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1985', 'poem', '唐', '贾至', '送李侍郎赴常州', '雪晴云散北风寒，楚水吴山道路难。\\n今日送君须尽醉，明朝相忆路漫漫。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1986', 'poem', '唐', '白居易', '酬赵秀才赠新登科诸先辈', '莫羡蓬莱鸾鹤侣，道成羽翼自生身。\\n君看名在丹台者，尽是人间修道人。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1987', 'poem', '唐', '李白', '相和歌辞·长歌行', '桃李得日开，荣华照当年。\\n东风动百物，草木尽欲言。\\n枯枝无丑叶，涸水吐清泉。\\n大力运天地，羲和无停鞭。\\n功名不早著，竹帛将何宣。\\n桃李务青春，谁能贯白日。\\n富贵与神仙，蹉跎成两失。\\n金石犹销铄，风霜无久质。\\n畏落日月后，强欢歌与酒。\\n秋霜不惜人，倏忽侵蒲柳。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1988', 'poem', '唐', '包何', '送王汶宰江阴', '郡北乘流去，花间竟日行。\\n海鱼朝满市，江鸟夜喧城。\\n止酒非关病，援琴不在声。\\n应缘五斗米，数日滞渊明。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1989', 'poem', '唐', '白居易', '听弹湘妃怨', '玉轸朱弦瑟瑟徽，吴娃徵调奏湘妃。\\n分明曲里愁云雨，似道萧萧郎不归。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1990', 'poem', '唐', '杜甫', '野望', '纳纳乾坤大，行行郡国遥。\\n云山兼五岭，风壤带三苗。\\n野树侵江阔，春蒲长雪消。\\n扁舟空老去，无补圣明朝。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1991', 'poem', '唐', '白居易', '禁中晓卧，因怀王起居', '迟迟禁漏尽，悄悄暝鸦喧。\\n夜雨槐花落，微凉卧北轩。\\n曙灯残未灭，风帘闲自翻。\\n每一得静境，思与故人言。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1992', 'poem', '唐', '聂夷中', '杂曲歌辞·古别离', '欲别牵郎衣，问郎游何处。\\n不恨归来迟，莫向临邛去。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1993', 'poem', '唐', '白居易', '醉后', '酒后高歌且放狂，门前闲事莫思量。\\n犹嫌小户长先醒，不得多时住醉乡。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1994', 'poem', '唐', '郑遨', '伤农', '一粒红稻饭，几滴牛颔血。\\n珊瑚枝下人，衔杯吐不歇。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1995', 'poem', '唐', '李白', '别中都明府兄', '吾兄诗酒继陶君，试宰中都天下闻。\\n东楼喜奉连枝会，南陌愁为落叶分。\\n城隅渌水明秋日，海上青山隔暮云。\\n取醉不辞留夜月，雁行中断惜离群。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1996', 'poem', '唐', '韩偓', '小隐', '借得茅斋岳麓西，拟将身世老锄犁。\\n清晨向市烟含郭，寒夜归村月照溪。\\n炉为窗明僧偶坐，松因雪折鸟惊啼。\\n灵椿朝菌由来事，却笑庄生始欲齐。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1997', 'poem', '唐', '杜荀鹤', '旅泊遇郡中叛乱示同志', '握手相看谁敢言，军家刀剑在腰边。\\n遍搜宝货无藏处，乱杀平人不怕天。\\n古寺拆为修寨木，荒坟开作甃城砖。\\n郡侯逐出浑闲事，正是銮舆幸蜀年。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1998', 'poem', '唐', '张谓', '春园家宴', '南园春色正相宜，大妇同行少妇随。\\n竹里登楼人不见，花间觅路鸟先知。\\n樱桃解结垂檐子，杨柳能低入户枝。\\n山简醉来歌一曲，参差笑杀郢中儿。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('1999', 'poem', '唐', '李商隐', '荷花', '都无色可并，不奈此香何。\\n瑶席乘凉设，金羁落晚过。\\n回衾灯照绮，渡袜水沾罗。\\n预想前秋别，离居梦棹歌。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('2000', 'poem', '唐', '刘方平', '代春怨', '朝日残莺伴妾啼，开帘只见草萋萋。\\n庭前时有东风入，杨柳千条尽向西。', '2018-03-09 15:08:53');
INSERT INTO `t_poem` VALUES ('2001', 'poem', '唐', '李群玉', '火炉前坐', '孤灯照不寐，风雨满西林。\\n多少关心事，书灰到夜深。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2002', 'poem', '唐', '陈叔达', '初年', '和风起天路，严气消冰井。\\n索索枝未柔，厌厌漏犹永。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2003', 'poem', '唐', '杜牧', '送友人', '十载名兼利，人皆与命争。\\n青春留不住，白发自然生。\\n夜雨滴乡思，秋风从别情。\\n都门五十里，驰马逐鸡声。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2004', 'poem', '唐', '薛涛', '句', '枝迎南北鸟，叶送往来风。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2005', 'poem', '唐', '王绩', '醉后', '阮籍醒时少，陶潜醉日多。\\n百年何足度，乘兴且长歌。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2006', 'poem', '唐', '徐夤', '惜牡丹', '今日狂风揭锦筵，预愁吹落夕阳天。\\n闲看红艳只须醉，谩惜黄金岂是贤。\\n南国好偷夸粉黛，汉宫宜摘赠神仙。\\n良时虽作莺花主，白马王孙恰少年。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2007', 'poem', '唐', '元稹', '画松', '张璪画古松，往往得神骨。\\n翠帚扫春风，枯龙戛寒月。\\n流传画师辈，奇态尽埋没。\\n纤枝无萧洒，顽干空突兀。\\n乃悟埃尘心，难状烟霄质。\\n我去淅阳山，深山看真物。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2008', 'poem', '唐', '李白', '酬裴侍御对雨感时见赠', '雨色秋来寒，风严清江爽。\\n孤高绣衣人，潇洒青霞赏。\\n平生多感激，忠义非外奖。\\n祸连积怨生，事及徂川往。\\n楚邦有壮士，鄢郢翻扫荡。\\n申包哭秦庭，泣血将安仰。\\n鞭尸辱已及，堂上罗宿莽。\\n颇似今之人，蟊贼陷忠谠。\\n渺然一水隔，何由税归鞅。\\n日夕听猿怨，怀贤盈梦想。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2009', 'poem', '唐', '顾非熊', '秋月夜', '旅雁迎风度，阶翻月露华。\\n砧声鸣夜永，江上几多家。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2010', 'poem', '唐', '刘禹锡', '同乐天登栖灵寺塔', '步步相携不觉难，九层云外倚阑干。\\n忽然笑语半天上，无限游人举眼看。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2011', 'poem', '唐', '白居易', '感发落', '昔日愁头白，谁知未白衰。\\n眼看应落尽，无可变成丝。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2012', 'poem', '唐', '骆宾王', '咏雪', '龙云玉叶上，鹤雪瑞花新。\\n影乱铜乌吹，光销玉马津。\\n含辉明素篆，隐迹表祥轮。\\n幽兰不可俪，徒自绕阳春。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2013', 'poem', '唐', '张循之', '送王汶宰江阴', '郡北乘流去，花间竟日行。\\n海鱼朝满市，江鸟夜喧城。\\n让酒非关病，援琴不在声。\\n应缘五斗米，数日滞渊明。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2014', 'poem', '唐', '李商隐', '残花', '残花啼露莫留春，尖发谁非怨别人。\\n若但掩关劳独梦，宝钗何日不生尘。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2015', 'poem', '唐', '司空图', '上方', '花落更同悲木落，莺声相续即蝉声。\\n荣枯了得无多事，只是闲人漫系情。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2016', 'poem', '唐', '李白', '金陵歌送别范宣', '石头巉岩如虎踞，凌波欲过沧江去。\\n钟山龙盘走势来，秀色横分历阳树。\\n四十馀帝三百秋，功名事迹随东流。\\n白马小儿谁家子，泰清之岁来关囚。\\n金陵昔时何壮哉，席卷英豪天下来。\\n冠盖散为烟雾尽，金舆玉座成寒灰。\\n扣剑悲吟空咄嗟，梁陈白骨乱如麻。\\n天子龙沉景阳井，谁歌玉树后庭花。\\n此地伤心不能道，目下离离长春草。\\n送尔长江万里心，他年来访南山老。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2017', 'poem', '唐', '李群玉', '同郑相并歌姬小饮戏赠 / 杜丞相悰筵中赠美人', '裙拖六幅湘江水，鬓耸巫山一段云。\\n风格只应天上有，歌声岂合世间闻。\\n胸前瑞雪灯斜照，眼底桃花酒半醺。\\n不是相如怜赋客，争教容易见文君。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2018', 'poem', '唐', '李白', '流夜郎赠辛判官', '昔在长安醉花柳，五侯七贵同杯酒。\\n气岸遥凌豪士前，风流肯落他人后。\\n夫子红颜我少年，章台走马著金鞭。\\n文章献纳麒麟殿，歌舞淹留玳瑁筵。\\n与君自谓长如此，宁知草动风尘起。\\n函谷忽惊胡马来，秦宫桃李向明开。\\n我愁远谪夜郎去，何日金鸡放赦回。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2019', 'poem', '唐', '杜牧', '边上闻笳三首', '何处吹笳薄暮天，塞垣高鸟没狼烟。\\n游人一听头堪白，苏武争禁十九年。\\n海路无尘边草新，荣枯不见绿杨春。\\n白沙日暮愁云起，独感离乡万里人。\\n胡雏吹笛上高台，寒雁惊飞去不回。\\n尽日春风吹不散，只应分付客愁来。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2020', 'poem', '唐', '贺知章', '送人之军', '常经绝脉塞，复见断肠流。\\n送子成今别，令人起昔愁。\\n陇云晴半雨，边草夏先秋。\\n万里长城寄，无贻汉国忧。', '2018-03-09 15:08:57');
INSERT INTO `t_poem` VALUES ('2021', 'poem', '唐', '徐夤', '画松', '涧底阴森验笔精，笔闲开展觉神清。\\n曾当月照还无影，若许风吹合有声。\\n枝偃只应玄鹤识，根深且与茯苓生。\\n天台道士频来见，说似株株倚赤城。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2022', 'poem', '唐', '宋之问', '下山歌', '下嵩山兮多所思，携佳人兮步迟迟。\\n松间明月长如此，君再游兮复何时。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2023', 'poem', '唐', '戚逍遥', '歌', '笑看沧海欲成尘，王母花前别众真。\\n千岁却归天上去，一心珍重世间人。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2024', 'poem', '唐', '李白', '赠崔侍郎', '黄河二尺鲤，本在孟津居。\\n点额不成龙，归来伴凡鱼。\\n故人东海客，一见借吹嘘。\\n风涛倘相见，更欲凌昆墟。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2025', 'poem', '唐', '李白', '登敬亭北二小山余时送客逢崔侍御并登此地', '送客谢亭北，逢君纵酒还。\\n屈盘戏白马，大笑上青山。\\n回鞭指长安，西日落秦关。\\n帝乡三千里，杳在碧云间。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2026', 'poem', '唐', '陈叔达', '咏菊', '霜间开紫蒂，露下发金英。\\n但令逢采摘，宁辞独晚荣。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2027', 'poem', '唐', '苏廷硕', '汾山惊秋', '北风吹白云，万里渡河汾。\\n心绪逢摇落，秋声不可闻。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2028', 'poem', '唐', '贯休', '春送僧', '蜀魄关关花雨深，送师冲雨到江浔。\\n不能更折江头柳，自有青青松柏心。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2029', 'poem', '唐', '杜甫', '去蜀', '五载客蜀郡，一年居梓州。\\n如何关塞阻，转作潇湘游。\\n世事已黄发，残生随白鸥。\\n安危大臣在，不必泪长流。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2030', 'poem', '唐', '薛涛', '蝉', '露涤清音远，风吹数叶齐。\\n声声似相接，各在一枝栖。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2031', 'poem', '唐', '杜甫', '醉歌行', '陆机二十作文赋，汝更小年能缀文。\\n总角草书又神速，世上儿子徒纷纷。\\n骅骝作驹已汗血，鸷鸟举翮连青云。\\n词源倒流三峡水，笔阵独扫千人军。\\n只今年才十六七，射策君门期第一。\\n旧穿杨叶真自知，暂蹶霜蹄未为失。\\n偶然擢秀非难取，会是排风有毛质。\\n汝身已见唾成珠，汝伯何由发如漆。\\n春光澹沱秦东亭，渚蒲牙白水荇青。\\n风吹客衣日杲杲，树搅离思花冥冥。\\n酒尽沙头双玉瓶，众宾皆醉我独醒。\\n乃知贫贱别更苦，吞声踯躅涕泪零。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2032', 'poem', '唐', '孙逖', '淮阴夜宿二首', '水国南无畔，扁舟北未期。\\n乡情淮上失，归梦郢中疑。\\n木落知寒近，山长见日迟。\\n客行心绪乱，不及洛阳时。\\n永夕卧烟塘，萧条天一方。\\n秋风淮水落，寒夜楚歌长。\\n宿莽非中土，鲈鱼岂我乡。\\n孤舟行已倦，南越尚茫茫。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2033', 'poem', '唐', '白居易', '江亭夕望', '凭高望远思悠哉，晚上江亭夜未回。\\n日欲没时红浪沸，月初生处白烟开。\\n辞枝雪蕊将春去，满镊霜毛送老来。\\n争敢三年作归计，心知不及贾生才。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2034', 'poem', '唐', '韩偓', '梅花', '梅花不肯傍春光，自向深冬著艳阳。\\n龙笛远吹胡地月，燕钗初试汉宫妆。\\n风虽强暴翻添思，雪欲侵凌更助香。\\n应笑暂时桃李树，盗天和气作年芳。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2035', 'poem', '唐', '卢仝', '扬子津', '风卷鱼龙暗楚关，白波沉却海门山。\\n鹏腾鳌倒且快性，地坼天开总是闲。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2036', 'poem', '唐', '白居易', '竹枝词四首', '瞿唐峡口水烟低，白帝城头月向西。\\n唱到竹枝声咽处，寒猿暗鸟一时啼。\\n竹枝苦怨怨何人，夜静山空歇又闻。\\n蛮儿巴女齐声唱，愁杀江楼病使君。\\n巴东船舫上巴西，波面风生雨脚齐。\\n水蓼冷花红簇簇，江蓠湿叶碧凄凄。\\n江畔谁人唱竹枝，前声断咽后声迟。\\n怪来调苦缘词苦，多是通州司马诗。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2037', 'poem', '唐', '韩偓', '别绪', '别绪静愔愔，牵愁暗入心。\\n已回花渚棹，悔听酒垆琴。\\n菊露凄罗幕，梨霜恻锦衾。\\n此生终独宿，到死誓相寻。\\n月好知何计，歌阑叹不禁。\\n山巅更高处，忆上上头吟。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2038', 'poem', '唐', '温庭筠', '马嵬驿', '穆满曾为物外游，六龙经此暂淹留。\\n返魂无验青烟灭，埋血空生碧草愁。\\n香辇却归长乐殿，晓钟还下景阳楼。\\n甘泉不复重相见，谁道文成是故侯。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2039', 'poem', '唐', '李白', '嘲鲁儒', '鲁叟谈五经，白发死章句。\\n问以经济策，茫如坠烟雾。\\n足著远游履，首戴方山巾。\\n缓步从直道，未行先起尘。\\n秦家丞相府，不重褒衣人。\\n君非叔孙通，与我本殊伦。\\n时事且未达，归耕汶水滨。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2040', 'poem', '唐', '王建', '寒食', '田舍清明日，家家出火迟。\\n白衫眠古巷，红索搭高枝。\\n纱带生难结，铜钗重欲垂。\\n斩新衣踏尽，还似去年时。', '2018-03-09 15:09:04');
INSERT INTO `t_poem` VALUES ('2041', 'poem', '唐', '麻温其', '登岳阳楼', '湖边景物属秋天，楼上风光似去年。\\n仙侣缑生留福地，湘娥帝子寄哀弦。\\n云门自统轩台外，木叶偏飞楚客前。\\n极目江山何处是，一帆万里信归船。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2042', 'poem', '唐', '赵嘏', '池上', '正怜佳月夜深坐，池上暖回燕雁声。\\n犹有渔舟系江岸，故人归尽独何情。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2043', 'poem', '唐', '罗隐', '蝉', '天地工夫一不遗，与君声调借君緌，风栖露饱今如此，应忘当年滓浊时。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2044', 'poem', '唐', '罗隐', '梅花', '吴王醉处十馀里，照野拂衣今正繁。\\n经雨不随山鸟散，倚风疑共路人言。\\n愁怜粉艳飘歌席，静爱寒香扑酒樽。\\n欲寄所思无好信，为人惆怅又黄昏。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2045', 'poem', '唐', '李群玉', '赠花', '酒为看花酝，花须趁酒红。\\n莫令芳树晚，使我绿尊空。\\n金谷园无主，桃源路不通。\\n纵非乘露折，长短尽随风。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2046', 'poem', '唐', '白居易', '忆微之', '与君何日出屯蒙，鱼恋江湖鸟厌笼。\\n分手各抛沧海畔，折腰俱老绿衫中。\\n三年隔阔音尘断，两地飘零气味同。\\n又被新年劝相忆，柳条黄软欲春风。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2047', 'poem', '唐', '李贺', '画角东城', '河转曙萧萧，鸦飞睥睨高。\\n帆长摽越甸，壁冷挂吴刀。\\n淡菜生寒日，鲕鱼潠白涛。\\n水花沾抹额，旗鼓夜迎潮。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2048', 'poem', '唐', '白居易', '谢李六郎中寄新蜀茶', '故情周匝向交亲，新茗分张及病身。\\n红纸一封书后信，绿芽十片火前春。\\n汤添勺水煎鱼眼，末下刀圭搅麹尘。\\n不寄他人先寄我，应缘我是别茶人。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2049', 'poem', '唐', '罗隐', '小松', '已有清阴逼座隅，爱声仙客肯过无。\\n陵迁谷变须高节，莫向人间作大夫。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2050', 'poem', '唐', '白居易', '销暑', '何以销烦暑，端居一院中。\\n眼前无长物，窗下有清风。\\n热散由心静，凉生为室空。\\n此时身自得，难更与人同。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2051', 'poem', '唐', '杜甫', '崔氏东山草堂', '爱汝玉山草堂静，高秋爽气相鲜新。\\n有时自发钟磬响，落日更见渔樵人。\\n盘剥白鸦谷口栗，饭煮青泥坊底芹。\\n何为西庄王给事，柴门空闭锁松筠。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2052', 'poem', '唐', '朱庆馀', '登玄都阁', '野色晴宜上阁看，树阴遥映御沟寒。\\n豪家旧宅无人住，空见朱门锁牡丹。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2053', 'poem', '唐', '萧淑妃', '夜梦', '昨日梦君归，贱妾下鸣机。\\n极知意气薄，不著去时衣。\\n故言如梦里，赖得雁书飞。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2054', 'poem', '唐', '白居易', '雨中题衰柳', '湿屈青条折，寒飘黄叶多。\\n不知秋雨意，更遣欲如何。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2055', 'poem', '唐', '白居易', '忆江柳', '曾栽杨柳江南岸，一别江南两度春。\\n遥忆青青江岸上，不知攀折是何人。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2056', 'poem', '唐', '崔涂', '七夕', '年年七夕渡瑶轩，谁道秋期有泪痕。\\n自是人间一周岁，何妨天上只黄昏。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2057', 'poem', '唐', '韦应物', '寒食', '晴明寒食好，春园百卉开。\\n彩绳拂花去，轻球度阁来。\\n长歌送落日，缓吹逐残杯。\\n非关无烛罢，良为羁思催。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2058', 'poem', '唐', '高适', '送李侍御赴安西', '行子对飞蓬，金鞭指铁骢。\\n功名万里外，心事一杯中。\\n虏障燕支北，秦城太白东。\\n离魂莫惆怅，看取宝刀雄。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2059', 'poem', '唐', '徐夤', '梦断', '梦断纱窗半夜雷，别君花落又花开。\\n渔阳路远书难寄，衡岳山高月不来。\\n玄燕有情穿绣户，灵龟无应祝金杯。\\n人生若得长相对，萤火生烟草化灰。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2060', 'poem', '唐', '白居易', '宿东林寺', '经窗灯焰短，僧炉火气深。\\n索落庐山夜，风雪宿东林。', '2018-03-09 15:09:11');
INSERT INTO `t_poem` VALUES ('2061', 'poem', '唐', '雍陶', '山行', '野花幽鸟几千般，头白山僧遍识难。\\n世上游人无复见，一生唯向画图看。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2062', 'poem', '唐', '白居易', '早春', '雪消冰又释，景和风复暄。\\n满庭田地湿，荠叶生墙根。\\n官舍悄无事，日西斜掩门。\\n不开庄老卷，欲与何人言。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2063', 'poem', '唐', '白居易', '禁中九日对菊花酒忆元九', '赐酒盈杯谁共持，宫花满把独相思。\\n相思只傍花边立，尽日吟君咏菊诗。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2064', 'poem', '唐', '杜甫', '归雁', '闻道今春雁，南归自广州。\\n见花辞涨海，避雪到罗浮。\\n是物关兵气，何时免客愁。\\n年年霜露隔，不过五湖秋。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2065', 'poem', '唐', '许浑', '金陵怀古', '玉树歌残王气终，景阳兵合戍楼空。\\n松楸远近千官冢，禾黍高低六代宫。\\n石燕拂云晴亦雨，江豚吹浪夜还风。\\n英雄一去豪华尽，惟有青山似洛中。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2066', 'poem', '唐', '柳宗元', '衡阳与梦得分路赠别', '十年憔悴到秦京，谁料翻为岭外行。\\n伏波故道风烟在，翁仲遗墟草树平。\\n直以慵疏招物议，休将文字占时名。\\n今朝不用临河别，垂泪千行便濯缨。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2067', 'poem', '唐', '陈子昂', '度荆门望楚', '遥遥去巫峡，望望下章台。\\n巴国山川尽，荆门烟雾开。\\n城分苍野外，树断白云隈。\\n今日狂歌客，谁知入楚来。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2068', 'poem', '唐', '王维', '冬晚对雪忆胡居士家', '寒更传晓箭，清镜览衰颜。\\n隔牖风惊竹，开门雪满山。\\n洒空深巷静，积素广庭闲。\\n借问袁安舍，翛然尚闭关。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2069', 'poem', '唐', '杜甫', '破船', '平生江海心，宿昔具扁舟。\\n岂惟青溪上，日傍柴门游。\\n苍皇避乱兵，缅邈怀旧丘。\\n邻人亦已非，野竹独修修。\\n船舷不重扣，埋没已经秋。\\n仰看西飞翼，下愧东逝流。\\n故者或可掘，新者亦易求。\\n所悲数奔窜，白屋难久留。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2070', 'poem', '唐', '李商隐', '观灯乐行', '月色灯山满帝都，香车宝盖隘通衢。\\n身闲不睹中兴盛，羞逐乡人赛紫姑。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2071', 'poem', '唐', '白居易', '题李次云窗竹', '不用裁为鸣凤管，不须截作钓鱼竿。\\n千花百草凋零后，留向纷纷雪里看。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2072', 'poem', '唐', '杜牧', '七夕', '云阶月地一相过，未抵经年别恨多。\\n最恨明朝洗车雨，不教回脚渡天河。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2073', 'poem', '唐', '白居易', '感秋寄远', '惆怅时节晚，两情千里同。\\n离忧不散处，庭树正秋风。\\n燕影动归翼，蕙香销故丛。\\n佳期与芳岁，牢落两成空。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2074', 'poem', '唐', '罗邺', '宫中二首', '芳草长含玉辇尘，君王游幸此中频。\\n今朝别有承恩处，鹦鹉飞来说似人。\\n虽然自小属梨园，不识先皇玉殿门。\\n还是当时歌舞曲，今来何处最承恩。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2075', 'poem', '唐', '李白', '冬日归旧山', '未洗染尘缨，归来芳草平。\\n一条藤径绿，万点雪峰晴。\\n地冷叶先尽，谷寒云不行。\\n嫩篁侵舍密，古树倒江横。\\n白犬离村吠，苍苔壁上生。\\n穿厨孤雉过，临屋旧猿鸣。\\n木落禽巢在，篱疏兽路成。\\n拂床苍鼠走，倒箧素鱼惊。\\n洗砚修良策，敲松拟素贞。\\n此时重一去，去合到三清。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2076', 'poem', '唐', '裴迪', '辋川集二十首·漆园', '好闲早成性，果此谐宿诺。\\n今日漆园游，还同庄叟乐。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2077', 'poem', '唐', '王维', '孟城坳', '新家孟城口，古木余衰柳。\\n来者复为谁？空悲昔人有。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2078', 'poem', '唐', '杜甫', '送远', '带甲满天地，胡为君远行。\\n亲朋尽一哭，鞍马去孤城。\\n草木岁月晚，关河霜雪清。\\n别离已昨日，因见古人情。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2079', 'poem', '唐', '张纮', '闺怨 / 怨诗', '去年离别雁初归，今夜裁缝萤已飞。\\n征客近来音信断：不知何处寄寒衣？', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2080', 'poem', '唐', '独孤及', '将赴京答李纾赠别', '胶漆常投分，荆蛮各倦游。\\n帝乡今独往，沟水便分流。\\n甘作远行客，深惭不系舟。\\n思君带将缓，岂直日三秋。', '2018-03-09 15:09:22');
INSERT INTO `t_poem` VALUES ('2081', 'poem', '唐', '李频', '送友人入蜀', '天际蜀门开，西看举别杯。\\n何人不异礼，上客自怀才。\\n夜涧青林发，秋江渌水来。\\n临邛行乐处，莫到白头回。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2082', 'poem', '唐', '徐铉', '泰州道中却寄东京故人', '风紧雨凄凄，川回岸渐低。\\n吴州林外近，隋苑雾中迷。\\n聚散纷如此，悲欢岂易齐。\\n料君残酒醒，还听子规啼。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2083', 'poem', '唐', '杜甫', '咏春笋', '无数春笋满林生，柴门密掩断行人。\\n会须上番看成竹，客至从嗔不出迎。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2084', 'poem', '唐', '雍陶', '初醒', '心中得胜暂抛愁，醉卧京风拂簟秋。\\n半夜觉来新酒醒，一条斜月到床头。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2085', 'poem', '唐', '冯道', '赠窦十', '燕山窦十郎，教子有义方。\\n灵椿一株老，丹桂五枝芳。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2086', 'poem', '唐', '白居易', '臼口阻风十日', '洪涛白浪塞江津，处处邅回事事迍。\\n世上方为失途客，江头又作阻风人。\\n鱼虾遇雨腥盈鼻，蚊蚋和烟痒满身。\\n老大光阴能几日，等闲臼口坐经旬。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2087', 'poem', '唐', '杜甫', '院中晚晴怀西郭茅舍', '幕府秋风日夜清，澹云疏雨过高城。\\n叶心朱实看时落，阶面青苔先自生。\\n复有楼台衔暮景，不劳钟鼓报新晴。\\n浣花溪里花饶笑，肯信吾兼吏隐名。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2088', 'poem', '唐', '韩偓', '避地寒食', '避地淹留已自悲，况逢寒食欲沾衣。\\n浓春孤馆人愁坐，斜日空园花乱飞。\\n路远渐忧知己少，时危又与赏心违。\\n一名所系无穷事，争敢当年便息机。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2089', 'poem', '唐', '白居易', '不二门', '两眼日将暗，四肢渐衰瘦。\\n束带剩昔围，穿衣妨宽袖。\\n流年似江水，奔注无昏昼。\\n志气与形骸，安得长依旧。\\n亦曾登玉陛，举措多纰缪。\\n至今金阙籍，名姓独遗漏。\\n亦曾烧大药，消息乖火候。\\n至今残丹砂，烧乾不成就。\\n行藏事两失，忧恼心交斗。\\n化作憔悴翁，抛身在荒陋。\\n坐看老病逼，须得医王救。\\n唯有不二门，其间无夭寿。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2090', 'poem', '唐', '李白', '寻山僧不遇作', '石径入丹壑，松门闭青苔。\\n闲阶有鸟迹，禅室无人开。\\n窥窗见白拂，挂壁生尘埃。\\n使我空叹息，欲去仍裴回。\\n香云遍山起，花雨从天来。\\n已有空乐好，况闻青猿哀。\\n了然绝世事，此地方悠哉。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2091', 'poem', '唐', '白居易', '杏园花落时招钱员外同醉', '花园欲去去应迟，正是风吹狼藉时。\\n近西数树犹堪醉，半落春风半在枝。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2092', 'poem', '唐', '汪遵', '夷门', '晋鄙兵回为重难，秦师收旆亦西还。\\n今来不是无朱亥，谁降轩车问抱关。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2093', 'poem', '唐', '李白', '答湖州迦叶司马问白是何人', '青莲居士谪仙人，酒肆藏名三十春。\\n湖州司马何须问，金粟如来是后身。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2094', 'poem', '唐', '王维', '菩提寺禁裴迪来相看说逆贼等凝碧池上作音乐…示裴迪', '万户伤心生野烟，百僚何日更朝天。\\n秋槐叶落空宫里，凝碧池头奏管弦。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2095', 'poem', '唐', '白居易', '醉后却寄元九', '蒲池村里匆匆别，沣水桥边兀兀回。\\n行到城门残酒醒，万重离恨一时来。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2096', 'poem', '唐', '李白', '口号吴王美人半醉', '风动荷花水殿香，姑苏台上宴吴王。\\n西施醉舞娇无力，笑倚东窗白玉床。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2097', 'poem', '唐', '柳郴', '赠别二首', '江浦程千里，离尊泪数行。\\n无论吴与楚，俱是客他乡。\\n何处最悲辛，长亭临古津。\\n往来舟楫路，前后别离人。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2098', 'poem', '唐', '韩愈', '出门', '长安百万家，出门无所之。\\n岂敢尚幽独，与世实参差。\\n古人虽已死，书上有其辞。\\n开卷读且想，千载若相期。\\n出门各有道，我道方未夷。\\n且于此中息，天命不吾欺。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2099', 'poem', '唐', '马戴', '山行偶作', '缘危路忽穷，投宿值樵翁。\\n鸟下山含暝，蝉鸣露滴空。\\n石门斜月入，云窦暗泉通。\\n寂寞生幽思，心疑旧隐同。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2100', 'poem', '唐', '郑谷', '蓼花', '蔟蔟复悠悠，年年拂漫流。\\n差池伴黄菊，冷淡过清秋。\\n晚带鸣虫急，寒藏宿鹭愁。\\n故溪归不得，凭仗系渔舟。', '2018-03-09 15:09:33');
INSERT INTO `t_poem` VALUES ('2101', 'poem', '唐', '灵一', '安公', '弥天称圣哲，象法初繄赖。\\n弘道识行藏，匡时知进退。\\n秦王轻与举，习生重酬对。\\n学文古篆中，义显心经内。\\n法服应华夏，金言流海岱。\\n西方浮云间，更陪龙华会。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2102', 'poem', '唐', '吴筠', '高士咏·玄晏先生', '士安逾弱冠，落魄未修饰。\\n一朝因感激，志学忘寝食。\\n著书穷天人，辞聘守玄默。\\n薄葬信昭俭，可为将来则。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2103', 'poem', '唐', '白居易', '秋雨中赠元九', '不堪红叶青苔地，又是凉风暮雨天。\\n莫怪独吟秋思苦，比君校近二毛年。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2104', 'poem', '唐', '许浑', '旅夜怀远客', '异乡多远情，梦断落江城。\\n病起惭书癖，贫家负酒名。\\n过春花自落，竟晓月空明。\\n独此一长啸，故人天际行。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2105', 'poem', '唐', '白居易', '重到城七绝句·恒寂师', '旧游分散人零落，如此伤心事几条。\\n会逐禅师坐禅去，一时灭尽定中消。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2106', 'poem', '唐', '杜甫', '偶题', '文章千古事，得失寸心知。\\n作者皆殊列，名声岂浪垂。\\n骚人嗟不见，汉道盛于斯。\\n前辈飞腾入，馀波绮丽为。\\n后贤兼旧列，历代各清规。\\n法自儒家有，心从弱岁疲。\\n永怀江左逸，多病邺中奇。\\n騄骥皆良马，骐驎带好儿。\\n车轮徒已斫，堂构惜仍亏。\\n漫作潜夫论，虚传幼妇碑。\\n缘情慰漂荡，抱疾屡迁移。\\n经济惭长策，飞栖假一枝。\\n尘沙傍蜂虿，江峡绕蛟螭。\\n萧瑟唐虞远，联翩楚汉危。\\n圣朝兼盗贼，异俗更喧卑。\\n郁郁星辰剑，苍苍云雨池。\\n两都开幕府，万宇插军麾。\\n南海残铜柱，东风避月支。\\n音书恨乌鹊，号怒怪熊罴。\\n稼穑分诗兴，柴荆学土宜。\\n故山迷白阁，秋水隐黄陂。\\n不敢要佳句，愁来赋别离。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2107', 'poem', '唐', '刘驾', '出门', '出门羡他人，奔走如得涂。\\n翻思他人意，与我或不殊。\\n以兹聊自安，默默行九衢。\\n生计逐羸马，每出似移居。\\n客从我乡来，但得邻里书。\\n田园几换主，梦归犹荷锄。\\n进犹希万一，退复何所如。\\n况今辟公道，安得不踌躇。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2108', 'poem', '唐', '白居易', '病中诗十五首·卖骆马', '五年花下醉骑行，临卖回头嘶一声。\\n项籍顾骓犹解叹，乐天别骆岂无情。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2109', 'poem', '唐', '李贺', '秦王饮酒', '秦王骑虎游八极，剑光照空天自碧。羲和敲日玻璃声，\\n劫灰飞尽古今平。龙头泻酒邀酒星，金槽琵琶夜枨枨。\\n洞庭雨脚来吹笙，酒酣喝月使倒行。银云栉栉瑶殿明，\\n宫门掌事报一更。花楼玉凤声娇狞，海绡红文香浅清，\\n黄鹅跌舞千年觥。仙人烛树蜡烟轻，清琴醉眼泪泓泓。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2110', 'poem', '唐', '裴度', '溪居', '门径俯清溪，茅檐古木齐。\\n红尘飘不到，时有水禽啼。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2111', 'poem', '唐', '戎昱', '咏史 / 和蕃', '汉家青史上，计拙是和亲。\\n社稷依明主，安危托妇人。\\n岂能将玉貌，便拟静胡尘。\\n地下千年骨，谁为辅佐臣。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2112', 'poem', '唐', '白居易', '题施山人野居', '得道应无著，谋生亦不妨。\\n春泥秧稻暖，夜火焙茶香。\\n水巷风尘少，松斋日月长。\\n高闲真是贵，何处觅侯王。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2113', 'poem', '唐', '皇甫松', '采莲子·菡萏香莲十顷陂', '菡萏香连十顷陂，小姑贪戏采莲迟。\\n晚来弄水船头湿，更脱红裙裹鸭儿。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2114', 'poem', '唐', '白居易', '送客之湖南', '年年渐见南方物，事事堪伤北客情。\\n山鬼趫跳唯一足，峡猿哀怨过三声。\\n帆开青草湖中去，衣湿黄梅雨里行。\\n别后双鱼难定寄，近来潮不到湓城。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2115', 'poem', '唐', '虞世南', '相和歌辞·饮马长城窟行', '驰马渡河干，流深马渡难。\\n前逢锦车使，都护在楼兰。\\n轻骑犹衔勒，疑兵尚解鞍。\\n温池下绝涧，栈道接危峦。\\n拓地勋未赏，亡城律讵宽。\\n有月关犹暗，经春陇尚寒。\\n云昏无复影，冰合不闻湍。\\n怀君不可遇，聊持报一餐。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2116', 'poem', '唐', '佚名', '郊庙歌辞·褒德庙乐章·昭德', '道赫梧宫，悲盈蒿里。\\n爰赐徽烈，载敷嘉祀。\\n享洽四时，规陈二簋。\\n灵应昭格，神其戾止。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2117', 'poem', '唐', '白居易', '开襟', '开襟何处好，竹下池边地。\\n馀热体犹烦，早凉风有味。\\n黄萎槐蕊结，红破莲芳坠。\\n无奈每年秋，先来入衰思。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2118', 'poem', '唐', '白居易', '朱陈村', '徐州古丰县，有村曰朱陈。\\n去县百馀里，桑麻青氛氲。\\n机梭声札札，牛驴走纭纭。\\n女汲涧中水，男采山上薪。\\n县远官事少，山深人俗淳。\\n有财不行商，有丁不入军。\\n家家守村业，头白不出门。\\n生为村之民，死为村之尘。\\n田中老与幼，相见何欣欣。\\n一村唯两姓，世世为婚姻。\\n亲疏居有族，少长游有群。\\n黄鸡与白酒，欢会不隔旬。\\n生者不远别，嫁娶先近邻。\\n死者不远葬，坟墓多绕村。\\n既安生与死，不苦形与神。\\n所以多寿考，往往见玄孙。\\n我生礼义乡，少小孤且贫。\\n徒学辨是非，只自取辛勤。\\n世法贵名教，士人重冠婚。\\n以此自桎梏，信为大谬人。\\n十岁解读书，十五能属文。\\n二十举秀才，三十为谏臣。\\n下有妻子累，上有君亲恩。\\n承家与事国，望此不肖身。\\n忆昨旅游初，迨今十五春。\\n孤舟三适楚，羸马四经秦。\\n昼行有饥色，夜寝无安魂。\\n东西不暂住，来往若浮云。\\n离乱失故乡，骨肉多散分。\\n江南与江北，各有平生亲。\\n平生终日别，逝者隔年闻。\\n朝忧卧至暮，夕哭坐达晨。\\n悲火烧心曲，愁霜侵鬓根。\\n一生苦如此，长羡村中民。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2119', 'poem', '唐', '张祜', '题润州金山寺', '一宿金山寺，超然离世群。\\n僧归夜船月，龙出晓堂云。\\n树色中流见，钟声两岸闻。\\n翻思在朝市，终日醉醺醺。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2120', 'poem', '唐', '白居易', '答劝酒', '莫怪近来都不饮，几回因醉却沾巾。\\n谁料平生狂酒客，如今变作酒悲人。', '2018-03-09 15:09:40');
INSERT INTO `t_poem` VALUES ('2121', 'poem', '唐', '白居易', '忆元九', '渺渺江陵道，相思远不知。\\n近来文卷里，半是忆君诗。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2122', 'poem', '唐', '杜甫', '大麦行', '大麦干枯小麦黄，妇女行泣夫走藏。\\n东至集壁西梁洋，问谁腰镰胡与羌。\\n岂无蜀兵三千人，部领辛苦江山长。\\n安得如鸟有羽翅，托身白云还故乡。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2123', 'poem', '唐', '李商隐', '石榴', '榴枝婀娜榴实繁，榴膜轻明榴子鲜。\\n可羡瑶池碧桃树，碧桃红颊一千年。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2124', 'poem', '唐', '刘禹锡', '尝茶', '生拍芳丛鹰觜芽，老郎封寄谪仙家。\\n今宵更有湘江月，照出菲菲满碗花。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2125', 'poem', '唐', '姚合', '除夜二首', '衰残归未遂，寂寞此宵情。\\n旧国当千里，新年隔数更。\\n寒犹近北峭，风渐向东生。\\n谁见长安陌，晨钟度火城。\\n殷勤惜此夜，此夜在逡巡。\\n烛尽年还别，鸡鸣老更新。\\n傩声方去疫，酒色已迎春。\\n明日持杯处，谁为最后人。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2126', 'poem', '唐', '韦庄', '湘中作', '千重烟树万重波，因便何妨吊汨罗。\\n楚地不知秦地乱，南人空怪北人多。\\n臣心未肯教迁鼎，天道还应欲止戈。\\n否去泰来终可待，夜寒休唱饭牛歌。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2127', 'poem', '唐', '戴叔伦', '夏日登鹤岩偶成', '天风吹我上层冈，露洒长松六月凉。\\n愿借老僧双白鹤，碧云深处共翱翔。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2128', 'poem', '唐', '杜甫', '杂歌谣辞·大麦行', '大麦干枯小麦黄，妇人行泣夫走藏。\\n东至集壁西梁洋，问谁腰镰胡与羌。\\n岂无蜀兵三千人，部领辛苦江山长。\\n安得如鸟有羽翅，托身白云还故乡。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2129', 'poem', '唐', '黄滔', '放榜日', '吾唐取士最堪夸，仙榜标名出曙霞。\\n白马嘶风三十辔，朱门秉烛一千家。\\n郄诜联臂升天路，宣圣飞章奏日华。\\n岁岁人人来不得，曲江烟水杏园花。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2130', 'poem', '唐', '吴融', '咏柳', '自与莺为地，不教花作媒。\\n细应和雨断，轻只爱风裁。\\n好拂锦步障，莫遮铜雀台。\\n灞陵千万树，日暮别离回。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2131', 'poem', '唐', '李煜', '病中书事', '病身坚固道情深，宴坐清香思自任。\\n月照静居唯捣药，门扃幽院只来禽。\\n庸医懒听词何取，小婢将行力未禁。\\n赖问空门知气味，不然烦恼万涂侵。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2132', 'poem', '唐', '雍裕之', '大言', '四溟杯渌醑，五岳髻青螺。\\n挥汗曾成雨，画地亦成河。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2133', 'poem', '唐', '罗隐', '感弄猴人赐朱绂', '十二三年就试期，五湖烟月奈相违。\\n何如买取胡孙弄，一笑君王便著绯。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2134', 'poem', '唐', '元稹', '和李校书新题乐府十二首·法曲', '吾闻黄帝鼓清角，弭伏熊罴舞玄鹤。\\n舜持干羽苗革心，尧用咸池凤巢阁。\\n大夏濩武皆象功，功多已讶玄功薄。\\n汉祖过沛亦有歌，秦王破阵非无作。\\n作之宗庙见艰难，作之军旅传糟粕。\\n明皇度曲多新态，宛转侵淫易沉著。\\n赤白桃李取花名，霓裳羽衣号天落。\\n雅弄虽云已变乱，夷音未得相参错。\\n自从胡骑起烟尘，毛毳腥膻满咸洛。\\n女为胡妇学胡妆，伎进胡音务胡乐。\\n火凤声沉多咽绝，春莺啭罢长萧索。\\n胡音胡骑与胡妆，五十年来竞纷泊。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2135', 'poem', '唐', '高骈', '锦城写望', '蜀江波影碧悠悠，四望烟花匝郡楼。\\n不会人家多少锦，春来尽挂树梢头。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2136', 'poem', '唐', '方干', '柳', '摇曳惹风吹，临堤软胜丝。\\n态浓谁为识，力弱自难持。\\n学舞枝翻袖，呈妆叶展眉。\\n如何一攀折，怀友又题诗。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2137', 'poem', '唐', '李涉', '井栏砂宿遇夜客', '暮雨潇潇江上村，绿林豪客夜知闻。\\n他时不用逃姓名，世上如今半是君。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2138', 'poem', '唐', '赵鸾鸾', '纤指', '纤纤软玉削春葱，长在香罗翠袖中。\\n昨日琵琶弦索上，分明满甲染猩红。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2139', 'poem', '唐', '韩翃', '送故人归鲁', '鲁客多归兴，居人怅别情。\\n雨馀衫袖冷，风急马蹄轻。\\n秋草灵光殿，寒云曲阜城。\\n知君拜亲后，少妇下机迎。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2140', 'poem', '唐', '蒋防', '至人无梦', '已赜希微理，知将静默邻。\\n坐忘宁有梦，迹灭未凝神。\\n化蝶诚知幻，征兰匪契真。\\n抱玄虽解带，守一自离尘。\\n寥朗壶中晓，虚明洞里春。\\n翛然碧霞客，那比漆园人。', '2018-03-09 15:09:50');
INSERT INTO `t_poem` VALUES ('2141', 'poem', '唐', '杜牧', '题武关', '碧溪留我武关东，一笑怀王迹自穷。\\n郑袖娇娆酣似醉，屈原憔悴去如蓬。\\n山樯谷堑依然在，弱吐强吞尽已空。\\n今日圣神家四海，戍旗长卷夕阳中。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2142', 'poem', '唐', '辛弘智', '杂曲歌辞·自君之出矣', '自君之出矣，梁尘静不飞。\\n思君如满月，夜夜减容晖。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2143', 'poem', '唐', '罗隐', '牡丹花', '似共东风别有因，绛罗高卷不胜春。\\n若教解语应倾国，任是无情亦动人。\\n芍药与君为近侍，芙蓉何处避芳尘。\\n可怜韩令功成后，辜负秾华过此身。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2144', 'poem', '唐', '罗隐', '京中正月七日立春', '一二三四五六七，万木生芽是今日。\\n远天归雁拂云飞，近水游鱼迸冰出。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2145', 'poem', '唐', '韩偓', '地炉', '两星残火地炉畔，梦断背灯重拥衾。\\n侧听空堂闻静响，似敲疏磬褭清音。\\n风灯有影随笼转，腊雪无声逐夜深。\\n禅客钓翁徒自好，那知此际湛然心。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2146', 'poem', '唐', '徐铉', '送王四十五归东都', '海内兵方起，离筵泪易垂。\\n怜君负米去，惜此落花时。\\n想忆看来信，相宽指后期。\\n殷勤手中柳，此是向南枝。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2147', 'poem', '唐', '韦承庆', '江楼', '独酌芳春酒，登楼已半曛。\\n谁惊一行雁，冲断过江云。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2148', 'poem', '唐', '李白', '望木瓜山', '早起见日出，暮见栖鸟还。\\n客心自酸楚，况对木瓜山。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2149', 'poem', '唐', '周昙', '春秋战国门·庄辛', '庄辛正谏谓妖词，兵及鄢陵始悔思。\\n见兔必能知顾犬，亡羊补栈未为迟。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2150', 'poem', '唐', '朱庆馀', '长城', '秦帝防胡虏，关心倍可嗟。\\n一人如有德，四海尽为家。\\n往事乾坤在，荒基草木遮。\\n至今徒者骨，犹自哭风沙。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2151', 'poem', '唐', '杜牧', '送隐者一绝', '无媒径路草萧萧，自古云林远市朝。\\n公道世间唯白发，贵人头上不曾饶。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2152', 'poem', '唐', '白居易', '见尹公亮新诗，偶赠绝句', '袖里新诗十首馀，吟看句句是琼琚。\\n如何持此将干谒，不及公卿一字书。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2153', 'poem', '唐', '李商隐', '药转', '郁金堂北画楼东，换骨神方上药通。\\n露气暗连青桂苑，风声偏猎紫兰丛。\\n长筹未必输孙皓，香枣何劳问石崇。\\n忆事怀人兼得句，翠衾归卧绣帘中。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2154', 'poem', '唐', '方干', '春日', '春去春来似有期，日高添睡是归时。\\n虽将细雨催芦笋，却用东风染柳丝。\\n重雾已应吞海色，轻霜犹自剉花枝。\\n此时野客因花醉，醉卧花间应不知。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2155', 'poem', '唐', '李適之', '罢相作', '避贤初罢相，乐圣且衔杯。\\n为问门前客，今朝几个来。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2156', 'poem', '唐', '胡曾', '咏史诗·杜邮', '自古功成祸亦侵，武安冤向杜邮深。\\n五湖烟月无穷水，何事迁延到陆沉。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2157', 'poem', '唐', '何扶', '寄旧同年', '金榜题名墨尚新，今年依旧去年春。\\n花间每被红妆问，何事重来只一人。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2158', 'poem', '唐', '韩偓', '旧馆', '前欢往恨分明在，酒兴诗情大半亡。\\n还似墙西紫荆树，残花摘索映高塘。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2159', 'poem', '唐', '李白', '送杨山人归嵩山', '我有万古宅，嵩阳玉女峰。\\n长留一片月，挂在东溪松。\\n尔去掇仙草，菖蒲花紫茸。\\n岁晚或相访，青天骑白龙。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2160', 'poem', '唐', '王昌龄', '塞下曲·秋风夜渡河', '秋风夜渡河，吹却雁门桑。\\n遥见胡地猎，鞴马宿严霜。\\n五道分兵去，孤军百战场。\\n功多翻下狱，士卒但心伤。', '2018-03-09 15:10:00');
INSERT INTO `t_poem` VALUES ('2161', 'poem', '唐', '白居易', '曲江醉后赠诸亲故', '郭东丘墓何年客，江畔风光几日春。\\n只合殷勤逐杯酒，不须疏索向交亲。\\n中天或有长生药，下界应无不死人。\\n除却醉来开口笑，世间何事更关身。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2162', 'poem', '唐', '陆龟蒙', '冬柳', '柳汀斜对野人窗，零落衰条傍晓江。\\n正是霜风飘断处，寒鸥惊起一双双。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2163', 'poem', '唐', '汪遵', '破陈', '猎猎朱旗映彩霞，纷纷白刃入陈家。\\n看看打破东平苑，犹舞庭前玉树花。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2164', 'poem', '唐', '刘商', '赠头陀师', '少壮从戎马上飞，雪山童子未缁衣。\\n秋山年长头陀处，说我军前射虎归。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2165', 'poem', '唐', '权德舆', '大言', '华嵩为佩河为带，南交北朔跬步内。\\n搏鹏作腊巨鳌鲙，伸舒轶出元气外。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2166', 'poem', '唐', '李商隐', '访秋', '酒薄吹还醒，楼危望已穷。\\n江皋当落日，帆席见归风。\\n烟带龙潭白，霞分鸟道红。\\n殷勤报秋意，只是有丹枫。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2167', 'poem', '唐', '郑谷', '贺进士骆用锡登第', '苦辛垂二纪，擢第却沾裳。\\n春榜到春晚，一家荣一乡。\\n题名登塔喜，醵宴为花忙。\\n好是东归日，高槐蕊半黄。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2168', 'poem', '唐', '韦庄', '浣溪沙·夜夜相思更漏残', '夜夜相思更漏残，伤心明月凭阑干，想君思我锦衾寒。\\n咫尺画堂深似海，忆来惟把旧书看，几时携手入长安？', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2169', 'poem', '唐', '白居易', '病中早春', '今朝枕上觉头轻，强起阶前试脚行。\\n膻腻断来无气力，风痰恼得少心情。\\n暖消霜瓦津初合，寒减冰渠冻不成。\\n唯有愁人鬓间雪，不随春尽逐春生。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2170', 'poem', '唐', '杜甫', '不归', '河间尚征伐，汝骨在空城。\\n从弟人皆有，终身恨不平。\\n数金怜俊迈，总角爱聪明。\\n面上三年土，春风草又生。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2171', 'poem', '唐', '韩翃', '送客贬五溪', '南过猿声一逐臣，回看秋草泪沾巾。\\n寒天暮雪空山里，几处蛮家是主人。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2172', 'poem', '唐', '贾岛', '友人婚杨氏催妆', '不知今夕是何夕，催促阳台近镜台。\\n谁道芙蓉水中种，青铜镜里一枝开。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2173', 'poem', '唐', '韩愈', '南山诗', '吾闻京城南，兹惟群山囿。东西两际海，巨细难悉究。\\n山经及地志，茫昧非受授。团辞试提挈，挂一念万漏。\\n欲休谅不能，粗叙所经觏。尝升崇丘望，戢戢见相凑。\\n晴明出棱角，缕脉碎分绣。蒸岚相澒洞，表里忽通透。\\n无风自飘簸，融液煦柔茂。横云时平凝，点点露数岫。\\n天空浮修眉，浓绿画新就。孤撑有巉绝，海浴褰鹏噣。\\n春阳潜沮洳，濯濯吐深秀。岩峦虽嵂崒，软弱类含酎。\\n夏炎百木盛，荫郁增埋覆。神灵日歊歔，云气争结构。\\n秋霜喜刻轹，磔卓立癯瘦。参差相叠重，刚耿陵宇宙。\\n冬行虽幽墨，冰雪工琢镂。新曦照危峨，亿丈恒高袤。\\n明昏无停态，顷刻异状候。西南雄太白，突起莫间簉。\\n藩都配德运，分宅占丁戊。逍遥越坤位，诋讦陷乾窦。\\n空虚寒兢兢，风气较搜漱。朱维方烧日，阴霰纵腾糅。\\n昆明大池北，去觌偶晴昼。绵联穷俯视，倒侧困清沤。\\n微澜动水面，踊跃躁猱狖。惊呼惜破碎，仰喜呀不仆。\\n前寻径杜墅，岔蔽毕原陋。崎岖上轩昂，始得观览富。\\n行行将遂穷，岭陆烦互走。勃然思坼裂，拥掩难恕宥。\\n巨灵与夸蛾，远贾期必售。还疑造物意，固护蓄精祐。\\n力虽能排斡，雷电怯呵诟。攀缘脱手足，蹭蹬抵积甃。\\n茫如试矫首，堛塞生怐愗。威容丧萧爽，近新迷远旧。\\n拘官计日月，欲进不可又。因缘窥其湫，凝湛閟阴兽。\\n鱼虾可俯掇，神物安敢寇。林柯有脱叶，欲堕鸟惊救。\\n争衔弯环飞，投弃急哺鷇。旋归道回睨，达枿壮复奏。\\n吁嗟信奇怪，峙质能化贸。前年遭谴谪，探历得邂逅。\\n初从蓝田入，顾盻劳颈脰。时天晦大雪，泪目苦矇瞀。\\n峻涂拖长冰，直上若悬溜。褰衣步推马，颠蹶退且复。\\n苍黄忘遐睎，所瞩才左右。杉篁咤蒲苏，杲耀攒介胄。\\n专心忆平道，脱险逾避臭。昨来逢清霁，宿愿忻始副。\\n峥嵘跻冢顶，倏闪杂鼯鼬。前低划开阔，烂漫堆众皱。\\n或连若相从，或蹙若相斗。或妥若弭伏，或竦若惊雊。\\n或散若瓦解，或赴若辐凑。或翩若船游，或决若马骤。\\n或背若相恶，或向若相佑。或乱若抽笋，或嵲若注灸。\\n或错若绘画，或缭若篆籀。或罗若星离，或蓊若云逗。\\n或浮若波涛，或碎若锄耨。或如贲育伦，赌胜勇前购。\\n先强势已出，后钝嗔bz譳。或如帝王尊，丛集朝贱幼。\\n虽亲不亵狎，虽远不悖谬。或如临食案，肴核纷饤饾。\\n又如游九原，坟墓包椁柩。或累若盆罂，或揭若bB豆。\\n或覆若曝鳖，或颓若寝兽。或蜿若藏龙，或翼若搏鹫。\\n或齐若友朋，或随若先后。或迸若流落，或顾若宿留。\\n或戾若仇雠，或密若婚媾。或俨若峨冠，或翻若舞袖。\\n或屹若战阵，或围若蒐狩。或靡然东注，或偃然北首。\\n或如火熹焰，或若气饙馏。或行而不辍，或遗而不收。\\n或斜而不倚，或弛而不彀。或赤若秃鬝，或熏若柴槱。\\n或如龟拆兆，或若卦分繇。或前横若剥，或后断若姤。\\n延延离又属，夬夬叛还遘。喁喁鱼闯萍，落落月经宿。\\n誾誾树墙垣，巘巘驾库厩。参参削剑戟，焕焕衔莹琇。\\n敷敷花披萼，k7々屋摧霤。悠悠舒而安，兀兀狂以狃。\\n超超出犹奔，蠢蠢骇不懋。大哉立天地，经纪肖营腠。\\n厥初孰开张，黾勉谁劝侑。创兹朴而巧，戮力忍劳疚。\\n得非施斧斤，无乃假诅咒。鸿荒竟无传，功大莫酬僦。\\n尝闻于祠官，芬苾降歆嗅。斐然作歌诗，惟用赞报酭。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2174', 'poem', '唐', '胡宿', '残花', '雨压残红一夜凋，晓来帘外正飘摇。\\n数枝翠叶空相对，万片香魂不可招。\\n长乐梦回春寂寂，武陵人去水迢迢。\\n愁将玉笛传遗恨，苦被芳风透绮寮。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2175', 'poem', '唐', '杜甫', '寒食', '寒食江村路，风花高下飞。\\n汀烟轻冉冉，竹日静晖晖。\\n田父要皆去，邻家闹不违。\\n地偏相识尽，鸡犬亦忘归。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2176', 'poem', '唐', '杜甫', '树间', '岑寂双甘树，婆娑一院香。\\n交柯低几杖，垂实碍衣裳。\\n满岁如松碧，同时待菊黄。\\n几回沾叶露，乘月坐胡床。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2177', 'poem', '唐', '李璟', '游后湖赏莲花', '蓼花蘸水火不灭，水鸟惊鱼银梭投。\\n满目荷花千万顷，红碧相杂敷清流。\\n孙武已斩吴宫女，琉璃池上佳人头。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2178', 'poem', '唐', '杜荀鹤', '春日山中对雪有作', '竹树无声或有声，霏霏漠漠散还凝。\\n岭梅谢后重妆蕊，岩水铺来却结冰。\\n牢系鹿儿防猎客，满添茶鼎候吟僧。\\n好将膏雨同功力，松径莓苔又一层。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2179', 'poem', '唐', '徐夤', '绿鬓', '绿鬓先生自出林，孟光同乐野云深。\\n躬耕为食古人操，非织不衣贤者心。\\n眼众岂能分瑞璧，舌多须信烁良金。\\n君看黄阁南迁客，一过泷州绝好音。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2180', 'poem', '唐', '梁锽', '天长节', '日月生天久，年年庆一回。\\n时平祥不去，寿远节长来。\\n连吹千家笛，同朝百郡杯。\\n愿持金殿镜，处处照遗才。', '2018-03-09 15:10:13');
INSERT INTO `t_poem` VALUES ('2181', 'poem', '唐', '卢纶', '和张仆射塞下曲·其四', '野幕敞琼筵，羌戎贺劳旋。\\n醉和金甲舞，雷鼓动山川。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2182', 'poem', '唐', '陈子昂', '感遇诗三十八首·其十九', '圣人不利己，忧济在元元。\\n黄屋非尧意，瑶台安可论？\\n吾闻西方化，清净道弥敦。\\n奈何穷金玉，雕刻以为尊？\\n云构山林尽，瑶图珠翠烦。\\n鬼工尚未可，人力安能存？\\n夸愚适增累，矜智道逾昏。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2183', 'poem', '唐', '杜审言', '大酺', '毗陵震泽九州通，士女欢娱万国同。\\n伐鼓撞钟惊海上，新妆袨服照江东。\\n梅花落处疑残雪，柳叶开时任好风。\\n火德云官逢道泰，天长地久属年丰。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2184', 'poem', '唐', '张籍', '征妇怨', '九月匈奴杀边将，汉军全没辽水上。\\n万里无人收白骨，家家城下招魂葬。\\n妇人依倚子与夫，同居贫贱心亦舒。\\n夫死战场子在腹，妾身虽存如昼烛。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2185', 'poem', '唐', '薛涛', '牡丹', '去春零落暮春时，泪湿红笺怨别离。\\n常恐便同巫峡散，因何重有武陵期。\\n传情每向馨香得，不语还应彼此知。\\n只欲栏边安枕席，夜深闲共说相思。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2186', 'poem', '唐', '李咸用', '咏柳', '日近烟饶还有意，东垣西掖几千株。\\n牵仍别恨知难尽，夸炫春光恐更无。\\n解引人情长婉约，巧随风势强盘纡。\\n天应绣出繁华景，处处茸丝惹路衢。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2187', 'poem', '唐', '司空图', '梦中', '几多亲爱在人间，上彻霞梯会却还。\\n须是蓬瀛长买得，一家同占作家山。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2188', 'poem', '唐', '陆龟蒙', '秋荷', '蒲茸承露有佳色，茭叶束烟如效颦。\\n盈盈一水不得渡，冷翠遗香愁向人。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2189', 'poem', '唐', '李绅', '新楼诗二十首·琪树', '石桥峰上栖玄鹤，碧阙岩边荫羽人。\\n冰叶万条垂碧实，玉珠千日保青春。\\n月中泣露应同浥，涧底侵云尚有尘。\\n徒使茯苓成琥珀，不为松老化龙鳞。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2190', 'poem', '唐', '王绩', '采药', '野情贪药饵，郊居倦蓬荜。\\n青龙护道符，白犬游仙术。\\n腰镰戊己月，负锸庚辛日。\\n时时断嶂遮，往往孤峰出。\\n行披葛仙经，坐检神农帙。\\n龟蛇采二苓，赤白寻双术。\\n地冻根难尽，丛枯苗易失。\\n从容肉作名，薯蓣膏成质。\\n家丰松叶酒，器贮参花蜜。\\n且复归去来，刀圭辅衰疾。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2191', 'poem', '唐', '李白', '别山僧', '何处名僧到水西，乘舟弄月宿泾溪。\\n平明别我上山去，手携金策踏云梯。\\n腾身转觉三天近，举足回看万岭低。\\n谑浪肯居支遁下，风流还与远公齐。\\n此度别离何日见，相思一夜暝猿啼。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2192', 'poem', '唐', '王维', '登河北城楼作', '井邑傅岩上，客亭云雾间。\\n高城眺落日，极浦映苍山。\\n岸火孤舟宿，渔家夕鸟还。\\n寂寥天地暮，心与广川闲。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2193', 'poem', '唐', '白居易', '初见白发', '白发生一茎，朝来明镜里。\\n勿言一茎少，满头从此始。\\n青山方远别，黄绶初从仕。\\n未料容鬓间，蹉跎忽如此。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2194', 'poem', '唐', '韩偓', '生查子·侍女动妆奁', '侍女动妆奁，故故惊人睡。\\n那知本未眠，背面偷垂泪。\\n懒卸凤凰钗，羞入鸳鸯被。\\n时复见残灯，和烟坠金穗。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2195', 'poem', '唐', '白居易', '题玉泉寺', '湛湛玉泉色，悠悠浮云身。\\n闲心对定水，清净两无尘。\\n手把青筇杖，头戴白纶巾。\\n兴尽下山去，知我是谁人。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2196', 'poem', '唐', '长孙佐辅', '陇西行', '阴云凝朔气，陇上正飞雪。\\n四月草不生，北风劲如切。\\n朝来羽书急，夜救长城窟。\\n道隘行不前，相呼抱鞍歇。\\n人寒指欲堕，马冻蹄亦裂。\\n射雁旋充饥，斧冰还止渴。\\n宁辞解围斗，但恐乘疲没。\\n早晚边候空，归来养羸卒。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2197', 'poem', '唐', '杜甫', '宗武生日', '小子何时见，高秋此日生。\\n自从都邑语，已伴老夫名。\\n诗是吾家事，人传世上情。\\n熟精文选理，休觅彩衣轻。\\n凋瘵筵初秩，欹斜坐不成。\\n流霞分片片，涓滴就徐倾。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2198', 'poem', '唐', '王建', '香印', '闲坐烧印香，满户松柏气。\\n火尽转分明，青苔碑上字。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2199', 'poem', '唐', '崔涂', '残花', '迟迟傍晓阴，昨夜色犹深。\\n毕竟终须落，堪悲古与今。\\n明年何处见，尽日此时心。\\n蜂蝶无情极，残香更不寻。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2200', 'poem', '唐', '李建勋', '采菊', '簇簇竟相鲜，一枝开几番。\\n味甘资麹糵，香好胜兰荪。\\n古道风摇远，荒篱露压繁。\\n盈筐时采得，服饵近知门。', '2018-03-09 15:10:23');
INSERT INTO `t_poem` VALUES ('2201', 'poem', '唐', '温庭筠', '春日', '问君何所思，迢递艳阳时。\\n门静人归晚，墙高蝶过迟。\\n一双青琐燕，千万绿杨丝。\\n屏上吴山远，楼中朔管悲。\\n宝书无寄处，香毂有来期。\\n草色将林彩，相添入黛眉。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2202', 'poem', '唐', '韩偓', '残花', '余霞残雪几多在，蔫香冶态犹无穷。\\n黄昏月下惆怅白，清明雨后寥梢红。\\n树底草齐千片净，墙头风急数枝空。\\n西园此日伤心处，一曲高歌水向东。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2203', 'poem', '唐', '岑参', '热海行送崔侍御还京', '侧闻阴山胡儿语，西头热海水如煮。\\n海上众鸟不敢飞，中有鲤鱼长且肥。\\n岸旁青草长不歇，空中白雪遥旋灭。\\n蒸沙烁石燃虏云，沸浪炎波煎汉月。\\n阴火潜烧天地炉，何事偏烘西一隅？\\n势吞月窟侵太白，气连赤坂通单于。\\n送君一醉天山郭，正见夕阳海边落。\\n柏台霜威寒逼人，热海炎气为之薄。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2204', 'poem', '唐', '李白', '铜官山醉后绝句', '我爱铜官乐，千年未拟还。\\n要须回舞袖，拂尽五松山。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2205', 'poem', '唐', '司空曙', '别卢秦卿', '知有前期在，难分此夜中。\\n无将故人酒，不及石尤风。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2206', 'poem', '唐', '刘禹锡', '送廖参谋东游二首', '九陌逢君又别离，行云别鹤本无期。\\n望嵩楼上忽相见，看过花开花落时。\\n繁花落尽君辞去，绿草垂杨引征路。\\n东道诸侯皆故人，留连必是多情处。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2207', 'poem', '唐', '王建', '海人谣', '海人无家海里住，采珠役象为岁赋。\\n恶波横天山塞路，未央宫中常满库。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2208', 'poem', '唐', '刘禹锡', '秋晚新晴夜月如练有怀乐天', '雨歇晚霞明，风调夜景清。\\n月高微晕散，云薄细鳞生。\\n露草百虫思，秋林千叶声。\\n相望一步地，脉脉万重情。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2209', 'poem', '唐', '贯休', '春', '自来自去动洪炉，无象无私无处无。\\n回雁不多消气力，染花应最费工夫。\\n溟濛便恨豪家惜，浓暖深为政笔驱。\\n莫讶相逢只添睡，伊余心不在荣枯。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2210', 'poem', '唐', '许浑', '送宋处士归山', '卖药修琴归去迟，山风吹尽桂花枝。\\n世间甲子须臾事，逢著仙人莫看棋。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2211', 'poem', '唐', '杜牧', '沈下贤', '斯人清唱何人和，草径苔芜不可寻。\\n一夕小敷山下梦，水如环珮月如襟。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2212', 'poem', '唐', '杨凝', '残花', '五马踟蹰在路岐，南来只为看花枝。\\n莺衔蝶弄红芳尽，此日深闺那得知。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2213', 'poem', '唐', '杜甫', '重经昭陵', '草昧英雄起，讴歌历数归。\\n风尘三尺剑，社稷一戎衣。\\n翼亮贞文德，丕承戢武威。\\n圣图天广大，宗祀日光辉。\\n陵寝盘空曲，熊罴守翠微。\\n再窥松柏路，还见五云飞。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2214', 'poem', '唐', '李世民', '望雪', '冻云宵遍岭，素雪晓凝华。\\n入牖千重碎，迎风一半斜。\\n不妆空散粉，无树独飘花。\\n萦空惭夕照，破彩谢晨霞。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2215', 'poem', '唐', '李商隐', '离思', '气尽前溪舞，心酸子夜歌。\\n峡云寻不得，沟水欲如何。\\n朔雁传书绝，湘篁染泪多。\\n无由见颜色，还自托微波。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2216', 'poem', '唐', '温庭筠', '塞寒行', '燕弓弦劲霜封瓦，朴簌寒雕睇平野，一点黄尘起雁喧，\\n白龙堆下千蹄马。河源怒浊风如刀，剪断朔云天更高。\\n晚出榆关逐征北，惊沙飞迸冲貂袍。心许凌烟名不灭，\\n年年锦字伤离别。彩毫一画竟何荣，空使青楼泪成血。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2217', 'poem', '唐', '刘禹锡', '昏镜词', '昏镜非美金，漠然丧其晶。\\n陋容多自欺，谓若他镜明。\\n瑕疵既不见，妍态随意生。\\n一日四五照，自言美倾城。\\n饰带以纹绣，装匣以琼瑛。\\n秦宫岂不重，非适乃为轻。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2218', 'poem', '唐', '顾况', '梦后吟', '醉中还有梦，身外已无心。\\n明镜唯知老，青山何处深。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2219', 'poem', '唐', '贾岛', '口号', '中夜忽自起，汲此百尺泉。\\n林木含白露，星斗在青天。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2220', 'poem', '唐', '陆龟蒙', '短歌行', '爪牙在身上，陷阱犹可制。\\n爪牙在胸中，剑戟无所畏。\\n人言畏猛虎，谁是撩头弊。\\n只见古来心，奸雄暗相噬。', '2018-03-09 15:10:29');
INSERT INTO `t_poem` VALUES ('2221', 'poem', '唐', '李白', '戏赠杜甫（以下见《唐诗纪事》）', '饭颗山头逢杜甫，顶戴笠子日卓午。借问别来太瘦生，总为从前作诗苦。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2222', 'poem', '宋', '陆游', '恩封渭南伯唐诗人赵嘏为渭南尉当时谓之赵渭', '老向人间久倦游，君恩乞与渭川秋。虚名定作陈惊坐，好句真惭赵倚楼。栈豆十年沾病马，烟波万里著浮鸥。就封他日轻裘去，应过三峰处处留。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2223', 'poem', '宋', '刘克庄', '唐诗', '瀛洲学士风流远，中叶唐惭贞观唐。灵武拾遗晚羁旅，开元供奉老佯狂。戏苕翡翠非伦拟，撼树蚍蜉不揣量。赖有元和韩十八，骑麟被发共翱翔。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2224', 'poem', '明', '袁宗道', '同惟长舅读唐诗有感', '数卷陈言逐字新，眼前君是赏音人。家家椟玉谁知赝，处处抽龙总忌真。再舍肉黥居易句，重捐金铸浪仙身。一从马粪卮言出，难洗诗家入骨尘。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2225', 'poem', '宋', '周南', '读唐诗', '一日勾消两卷诗，趁心抄得是灯时。隔窗一閧狂风雨，无数啁啾冻雀儿。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2226', 'poem', '宋', '朱继芳', '桃源官罢芸居以唐诗拙作赠别', '自作还相送，唐诗结伴来。归装携此重，笑口为君开。莲上巢难稳，桃头郁垒神。何须千岁上，即此是新陈。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2227', 'poem', '宋', '戴复古', '伏龙山民宋正甫湖山清隐乃唐诗人陈陶故圃曾', '故人昔住金华峰，面带双溪秋水容。故人今住伏龙山，陈陶故圃茅三间。千载清风徐孺子，门前共此一湖水。百花洲上万垂杨，白鸥群里歌沧浪。故人心事孺子高，故人诗句今陈陶。短衣饭牛不逢尧，何如绣鞍上着锦宫袍。瓦盆对客酌松醪，何如紫霞觞泛碧葡萄。豆萁然火度寒宵，何如玉堂夜照金莲膏。吟成秃笔写芭蕉，何如沉香亭北醉挥毫。再三问君君不对，目送飞鸿楚天外。细读山中招隐篇，超然意与烟霞会。照影湖边双鬓皓，此计知之悔不早。三椽可办愿卜隣，荷锸相随种瑶草。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2228', 'poem', '唐', '曹唐', '又游仙诗一绝（见《唐诗纪事》）', '靖节先生几代孙，青娥曾接玉郎魂。春风流水还无赖，偷放桃花出洞门。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2229', 'poem', '唐', '许瀍', '纪梦（一作梦入琼台，本事诗及唐诗纪事并作许浑）', '晚入瑶台露气清，天风飞下步虚声。尘心未尽俗缘在，十里下山空月明。', '2018-03-09 15:10:35');
INSERT INTO `t_poem` VALUES ('2230', 'poem', '唐', '李白', '戏赠杜甫（以下见《唐诗纪事》）', '饭颗山头逢杜甫，顶戴笠子日卓午。借问别来太瘦生，总为从前作诗苦。', '2018-03-09 15:10:40');
INSERT INTO `t_poem` VALUES ('2231', 'poem', '宋', '陆游', '恩封渭南伯唐诗人赵嘏为渭南尉当时谓之赵渭', '老向人间久倦游，君恩乞与渭川秋。虚名定作陈惊坐，好句真惭赵倚楼。栈豆十年沾病马，烟波万里著浮鸥。就封他日轻裘去，应过三峰处处留。', '2018-03-09 15:10:40');
INSERT INTO `t_poem` VALUES ('2446', 'poem', '唐', '温庭筠', '春日', '问君何所思，迢递艳阳时。\\n门静人归晚，墙高蝶过迟。\\n一双青琐燕，千万绿杨丝。\\n屏上吴山远，楼中朔管悲。\\n宝书无寄处，香毂有来期。\\n草色将林彩，相添入黛眉。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2447', 'poem', '唐', '韩偓', '残花', '余霞残雪几多在，蔫香冶态犹无穷。\\n黄昏月下惆怅白，清明雨后寥梢红。\\n树底草齐千片净，墙头风急数枝空。\\n西园此日伤心处，一曲高歌水向东。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2448', 'poem', '唐', '岑参', '热海行送崔侍御还京', '侧闻阴山胡儿语，西头热海水如煮。\\n海上众鸟不敢飞，中有鲤鱼长且肥。\\n岸旁青草长不歇，空中白雪遥旋灭。\\n蒸沙烁石燃虏云，沸浪炎波煎汉月。\\n阴火潜烧天地炉，何事偏烘西一隅？\\n势吞月窟侵太白，气连赤坂通单于。\\n送君一醉天山郭，正见夕阳海边落。\\n柏台霜威寒逼人，热海炎气为之薄。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2449', 'poem', '唐', '李白', '铜官山醉后绝句', '我爱铜官乐，千年未拟还。\\n要须回舞袖，拂尽五松山。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2450', 'poem', '唐', '司空曙', '别卢秦卿', '知有前期在，难分此夜中。\\n无将故人酒，不及石尤风。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2451', 'poem', '唐', '刘禹锡', '送廖参谋东游二首', '九陌逢君又别离，行云别鹤本无期。\\n望嵩楼上忽相见，看过花开花落时。\\n繁花落尽君辞去，绿草垂杨引征路。\\n东道诸侯皆故人，留连必是多情处。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2452', 'poem', '唐', '王建', '海人谣', '海人无家海里住，采珠役象为岁赋。\\n恶波横天山塞路，未央宫中常满库。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2453', 'poem', '唐', '刘禹锡', '秋晚新晴夜月如练有怀乐天', '雨歇晚霞明，风调夜景清。\\n月高微晕散，云薄细鳞生。\\n露草百虫思，秋林千叶声。\\n相望一步地，脉脉万重情。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2454', 'poem', '唐', '贯休', '春', '自来自去动洪炉，无象无私无处无。\\n回雁不多消气力，染花应最费工夫。\\n溟濛便恨豪家惜，浓暖深为政笔驱。\\n莫讶相逢只添睡，伊余心不在荣枯。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2455', 'poem', '唐', '许浑', '送宋处士归山', '卖药修琴归去迟，山风吹尽桂花枝。\\n世间甲子须臾事，逢著仙人莫看棋。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2456', 'poem', '唐', '杜牧', '沈下贤', '斯人清唱何人和，草径苔芜不可寻。\\n一夕小敷山下梦，水如环珮月如襟。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2457', 'poem', '唐', '杨凝', '残花', '五马踟蹰在路岐，南来只为看花枝。\\n莺衔蝶弄红芳尽，此日深闺那得知。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2458', 'poem', '唐', '杜甫', '重经昭陵', '草昧英雄起，讴歌历数归。\\n风尘三尺剑，社稷一戎衣。\\n翼亮贞文德，丕承戢武威。\\n圣图天广大，宗祀日光辉。\\n陵寝盘空曲，熊罴守翠微。\\n再窥松柏路，还见五云飞。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2459', 'poem', '唐', '李世民', '望雪', '冻云宵遍岭，素雪晓凝华。\\n入牖千重碎，迎风一半斜。\\n不妆空散粉，无树独飘花。\\n萦空惭夕照，破彩谢晨霞。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2460', 'poem', '唐', '李商隐', '离思', '气尽前溪舞，心酸子夜歌。\\n峡云寻不得，沟水欲如何。\\n朔雁传书绝，湘篁染泪多。\\n无由见颜色，还自托微波。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2461', 'poem', '唐', '温庭筠', '塞寒行', '燕弓弦劲霜封瓦，朴簌寒雕睇平野，一点黄尘起雁喧，\\n白龙堆下千蹄马。河源怒浊风如刀，剪断朔云天更高。\\n晚出榆关逐征北，惊沙飞迸冲貂袍。心许凌烟名不灭，\\n年年锦字伤离别。彩毫一画竟何荣，空使青楼泪成血。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2462', 'poem', '唐', '刘禹锡', '昏镜词', '昏镜非美金，漠然丧其晶。\\n陋容多自欺，谓若他镜明。\\n瑕疵既不见，妍态随意生。\\n一日四五照，自言美倾城。\\n饰带以纹绣，装匣以琼瑛。\\n秦宫岂不重，非适乃为轻。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2463', 'poem', '唐', '顾况', '梦后吟', '醉中还有梦，身外已无心。\\n明镜唯知老，青山何处深。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2464', 'poem', '唐', '贾岛', '口号', '中夜忽自起，汲此百尺泉。\\n林木含白露，星斗在青天。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2465', 'poem', '唐', '陆龟蒙', '短歌行', '爪牙在身上，陷阱犹可制。\\n爪牙在胸中，剑戟无所畏。\\n人言畏猛虎，谁是撩头弊。\\n只见古来心，奸雄暗相噬。', '2018-03-09 15:18:33');
INSERT INTO `t_poem` VALUES ('2466', 'poem', '唐', '韦庄', '残花', '江头沈醉泥斜晖，却向花前恸哭归。\\n惆怅一年春又去，碧云芳草两依依。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2467', 'poem', '唐', '白居易', '巴水', '城下巴江水，春来似麹尘。\\n软沙如渭曲，斜岸忆天津。\\n影蘸新黄柳，香浮小白蘋。\\n临流搔首坐，惆怅为何人。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2468', 'poem', '唐', '李白', '洛阳陌', '白玉谁家郎，回车渡天津。\\n看花东上陌，惊动洛阳人。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2469', 'poem', '唐', '张乔', '河湟旧卒', '少年随将讨河湟，头白时清返故乡。\\n十万汉军零落尽，独吹边曲向残阳。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2470', 'poem', '唐', '罗隐', '金钱花', '占得佳名绕树芳，依依相伴向秋光。\\n若教此物堪收贮，应被豪门尽劚将。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2471', 'poem', '唐', '处默', '圣果寺', '路自中峰上，盘回出薜萝。\\n到江吴地尽，隔岸越山多。\\n古木丛青霭，遥天浸白波。\\n下方城郭近，钟磬杂笙歌。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2472', 'poem', '唐', '罗隐', '遁迹', '遁迹知安住，沾襟欲奈何。\\n朝廷犹礼乐，郡邑忍干戈。\\n华马凭谁问，胡尘自此多。\\n因思汉明帝，中夜忆廉颇。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2473', 'poem', '唐', '元稹', '会真诗三十韵', '微月透帘栊，萤光度碧空。\\n遥天初缥缈，低树渐葱茏。\\n龙吹过庭竹，鸾歌拂井桐。\\n罗绡垂薄雾，环佩响轻风。\\n绛节随金母，云心捧玉童。\\n更深人悄悄，晨会雨濛濛。\\n珠莹光文履，花明隐绣栊。\\n宝钗行彩凤，罗帔掩丹虹。\\n言自瑶华浦，将朝碧帝宫。\\n因游李城北，偶向宋家东。\\n戏调初微拒，柔情已暗通。\\n低鬟蝉影动，回步玉尘蒙。\\n转面流花雪，登床抱绮丛。\\n鸳鸯交颈舞，翡翠合欢笼。\\n眉黛羞频聚，朱唇暖更融。\\n气清兰蕊馥，肤润玉肌丰。\\n无力慵移腕，多娇爱敛躬。\\n汗光珠点点，发乱绿松松。\\n方喜千年会，俄闻五夜穷。\\n留连时有恨，缱绻意难终。\\n慢脸含愁态，芳词誓素衷。\\n赠环明运合，留结表心同。\\n啼粉流清镜，残灯绕暗虫。\\n华光犹冉冉，旭日渐曈曈。\\n警乘还归洛，吹箫亦上嵩。\\n衣香犹染麝，枕腻尚残红。\\n幂幂临塘草，飘飘思渚蓬。\\n素琴鸣怨鹤，清汉望归鸿。\\n海阔诚难度，天高不易冲。\\n行云无处所，萧史在楼中。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2474', 'poem', '唐', '韩愈', '宿龙宫滩', '浩浩复汤汤，滩声抑更扬。\\n奔流疑激电，惊浪似浮霜。\\n梦觉灯生晕，宵残雨送凉。\\n如何连晓语，一半是思乡。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2475', 'poem', '唐', '罗邺', '长城', '当时无德御乾坤，广筑徒劳万古存。\\n谩役生民防极塞，不知血刃起中原。\\n珠玑旋见陪陵寝，社稷何曾保子孙。\\n降虏至今犹自说，冤声夜夜傍城根。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2476', 'poem', '唐', '陈子昂', '夏日游晖上人房', '山水开精舍，琴歌列梵筵。\\n人疑白楼赏，地似竹林禅。\\n对户池光乱，交轩岩翠连。\\n色空今已寂，乘月弄澄泉。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2477', 'poem', '唐', '武元衡', '途次', '去国策羸马，劳歌行路难。\\n地崇秦制险，人乐汉恩宽。\\n御沼澄泉碧，宫梨佛露丹。\\n鼎成仙驭远，龙化宿云残。\\n不问三苗宠，谁陪万国欢。\\n至今松桂色，长助玉楼寒。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2478', 'poem', '唐', '陆龟蒙', '怀宛陵旧游', '陵阳佳地昔年游，谢朓青山李白楼。\\n唯有日斜溪上思，酒旗风影落春流。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2479', 'poem', '唐', '樊珣', '状江南·仲夏', '江南仲夏天，时雨下如川。\\n卢橘垂金弹，甘蕉吐白莲。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2480', 'poem', '唐', '刘禹锡', '西塞山怀古', '王浚楼船下益州，金陵王气黯然收。\\n千寻铁锁沈江底，一片降幡出石头。\\n人世几回伤往事？山形依旧枕寒流。\\n从今四海为家日，故垒萧萧芦荻秋。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2481', 'poem', '唐', '白居易', '寄陈式五兄', '年来白发两三茎，忆别君时髭未生。\\n惆怅料君应满鬓，当初是我十年兄。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2482', 'poem', '唐', '刘长卿', '谪仙怨·晴川落日初低', '晴川落日初低，惆怅孤舟解携。\\n鸟向平芜远近，人随流水东西。\\n白云千里万里，明月前溪后溪。\\n独恨长沙谪去，江潭春草萋萋。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2483', 'poem', '唐', '无名氏', '古砚', '癖性爱古物，终岁求不得。\\n昨朝得古砚，兰河滩之侧。\\n波涛所击触，背面生隟隙。\\n质状朴且丑，令人作不得。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2484', 'poem', '唐', '白居易', '感春', '巫峡中心郡，巴城四面春。\\n草青临水地，头白见花人。\\n忧喜皆心火，荣枯是眼尘。\\n除非一杯酒，何物更关身。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2485', 'poem', '唐', '宋之问', '送杜审言', '卧病人事绝，嗟君万里行。\\n河桥不相送，江树远含情。\\n别路追孙楚，维舟吊屈平。\\n可惜龙泉剑，流落在丰城。', '2018-03-09 15:18:42');
INSERT INTO `t_poem` VALUES ('2486', 'poem', '唐', '杜甫', '月', '天上秋期近，人间月影清。\\n入河蟾不没，捣药兔长生。\\n只益丹心苦，能添白发明。\\n干戈知满地，休照国西营。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2487', 'poem', '唐', '白居易', '读禅经', '须知诸相皆非相，若住无余却有余。\\n言下忘言一时了，梦中说梦两重虚。\\n空花岂得兼求果，阳焰如何更觅鱼。\\n摄动是禅禅是动，不禅不动即如如。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2488', 'poem', '唐', '白居易', '赠杨秘书巨源', '早闻一箭取辽城，相识虽新有故情。\\n清句三朝谁是敌，白须四海半为兄。\\n贫家薙草时时入，瘦马寻花处处行。\\n不用更教诗过好，折君官职是声名。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2489', 'poem', '唐', '薛涛', '池上双鸟', '双栖绿池上，朝暮共飞还。\\n更忆将雏日，同心莲叶间。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2490', 'poem', '唐', '齐己', '秋江', '两岸山青映，中流一棹声。\\n远无风浪动，正向夕阳横。\\n岛屿蝉分宿，沙洲客独行。\\n浩然心自合，何必濯吾缨。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2491', 'poem', '唐', '罗隐', '秋江', '秋江待晚潮，客思旆旌摇。\\n细雨翻芦叶，高风却柳条。\\n兵戈村落破，饥俭虎狼骄。\\n吾土兼连此，离魂望里消。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2492', 'poem', '唐', '白居易', '龙昌寺荷池', '冷碧新秋水，残红半破莲。\\n从来寥落意，不似此池边。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2493', 'poem', '唐', '罗隐', '桃花', '暖触衣襟漠漠香，间梅遮柳不胜芳。\\n数枝艳拂文君酒，半里红欹宋玉墙。\\n尽日无人疑怅望，有时经雨乍凄凉。\\n旧山山下还如此，回首东风一断肠。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2494', 'poem', '唐', '王建', '励学', '买地不肥实，其繁系耕凿。\\n良田少锄理，兰焦香亦薄。\\n勿以听者迷，故使宫徵错。\\n谁言三岁童，还能分善恶。\\n孜孜日求益，犹恐业未博。\\n况我性顽蒙，复不勤修学。\\n有如朝暮食，暂亏忧陨获。\\n若使无六经，贤愚何所托。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2495', 'poem', '唐', '李白', '庭前晚花开', '西王母桃种我家，三千阳春始一花。\\n结实苦迟为人笑，攀折唧唧长咨嗟。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2496', 'poem', '唐', '白居易', '秋热', '西江风候接南威，暑气常多秋气微。\\n犹道江州最凉冷，至今九月著生衣。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2497', 'poem', '唐', '雍陶', '送蜀客', '剑南风景腊前春，山鸟江风得雨新。\\n莫怪送君行较远，自缘身是忆归人。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2498', 'poem', '唐', '吴筠', '高士咏·南郭子綦', '子綦方隐几，冥寂久灰心。\\n悟来应颜游，清义杳何深。\\n含响尽天籁，有言同鷇音。\\n是非不足辩，安用劳神襟。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2499', 'poem', '唐', '陆龟蒙', '中秋待月', '转缺霜输上转迟，好风偏似送佳期。\\n帘斜树隔情无限，烛暗香残坐不辞。\\n最爱笙调闻北里，渐看星澹失南箕。\\n何人为校清凉力，未似初圆欲午时。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2500', 'poem', '唐', '李颀', '赠苏明府', '苏君年几许，状貌如玉童。\\n采药傍梁宋，共言随日翁。\\n常辞小县宰，一往东山东。\\n不复有家室，悠悠人世中。\\n子孙皆老死，相识悲转蓬。\\n发白还更黑，身轻行若风。\\n泛然无所系，心与孤云同。\\n出入虽一杖，安然知始终。\\n愿闻素女事，去采山花丛。\\n诱我为弟子，逍遥寻葛洪。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2501', 'poem', '唐', '褚朝阳', '五丝', '越人传楚俗，截竹竞萦丝。\\n水底深休也，日中还贺之。\\n章施文胜质，列匹美于姬。\\n锦绣侔新段，羔羊寝旧诗。\\n但夸端午节，谁荐屈原祠。\\n把酒时伸奠，汨罗空远而。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2502', 'poem', '唐', '方干', '咏花', '狂心醉眼共裴回，一半先开笑未开。\\n此日不能偷折去，胡蜂直恐趁人来。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2503', 'poem', '唐', '周昙', '春秋战国门·公叔', '吴起南奔魏国荒，必听公叔失贤良。\\n无谋纵欲离安邑，可免河沟徙大梁。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2504', 'poem', '唐', '杜牧', '过勤政楼', '千秋佳节名空在，承露丝囊世已无。\\n唯有紫苔偏称意，年年因雨上金铺。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2505', 'poem', '唐', '常建', '江上琴兴', '江上调玉琴，一弦清一心。\\n泠泠七弦遍，万木澄幽阴。\\n能使江月白，又令江水深。\\n始知梧桐枝，可以徽黄金。', '2018-03-09 15:18:52');
INSERT INTO `t_poem` VALUES ('2506', 'poem', '唐', '白居易', '重寻杏园', '忽忆芳时频酩酊，却寻醉处重裴回。\\n杏花结子春深后，谁解多情又独来。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2507', 'poem', '唐', '牟融', '送徐浩', '渡口潮平促去舟，莫辞尊酒暂相留。\\n弟兄聚散云边雁，踪迹浮沉水上鸥。\\n千里好山青入楚，几家深树碧藏楼。\\n知君此去情偏切，堂上椿萱雪满头。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2508', 'poem', '唐', '白居易', '杭州回舫', '自别钱塘山水后，不多饮酒懒吟诗。\\n欲将此意凭回棹，报与西湖风月知。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2509', 'poem', '唐', '贾岛', '寻人不遇', '闻说到扬州，吹箫有旧游。\\n人来多不见，莫是上迷楼。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2510', 'poem', '唐', '白居易', '岁晚旅望', '朝来暮去星霜换，阴惨阳舒气序牵。\\n万物秋霜能坏色，四时冬日最凋年。\\n烟波半露新沙地，鸟雀群飞欲雪天。\\n向晚苍苍南北望，穷阴旅思两无边。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2511', 'poem', '唐', '杜甫', '又示宗武', '觅句新知律，摊书解满床。\\n试吟青玉案，莫羡紫罗囊。\\n假日从时饮，明年共我长。\\n应须饱经术，已似爱文章。\\n十五男儿志，三千弟子行。\\n曾参与游夏，达者得升堂。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2512', 'poem', '唐', '周昙', '春秋战国门·赵宣子', '门人曾不有提弥，连嗾呀呀孰敢支。\\n临难若教无苟免，乱朝争那以獒为。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2513', 'poem', '唐', '戴叔伦', '送人游岭南', '少别华阳万里游，近南风景不曾秋。\\n红芳绿笋是行路，纵有啼猿听却幽。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2514', 'poem', '唐', '宋之问', '苑中遇雪应制', '紫禁仙舆诘旦来，青旂遥倚望春台。\\n不知庭霰今朝落，疑是林花昨夜开。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2515', 'poem', '唐', '王昌龄', '答武陵田太守', '仗剑行千里，微躯敢一言。\\n曾为大梁客，不负信陵恩。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2516', 'poem', '唐', '贺知章', '望人家桃李花', '山源夜雨度仙家，朝发东园桃李花。\\n桃花红兮李花白，照灼城隅复南陌。\\n南陌青楼十二重，春风桃李为谁容。\\n弃置千金轻不顾，踟蹰五马谢相逢。\\n徒言南国容华晚，遂叹西家飘落远。\\n的皪长奉明光殿，氛氲半入披香苑。\\n苑中珍木元自奇，黄金作叶白银枝。\\n千年万岁不凋落，还将桃李更相宜。\\n桃李从来露井傍，成蹊结影矜艳阳。\\n莫道春花不可树，会持仙实荐君王。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2517', 'poem', '唐', '李颀', '李兵曹壁画山水各赋得桂水帆', '片帆浮桂水，落日天涯时。\\n飞鸟看共度，闲云相与迟。\\n长波无晓夜，泛泛欲何之。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2518', 'poem', '唐', '刘长卿', '酬屈突陕', '落叶纷纷满四邻，萧条环堵绝风尘。\\n乡看秋草归无路，家对寒江病且贫。\\n藜杖懒迎征骑客，菊花能醉去官人。\\n怜君计画谁知者，但见蓬蒿空没身。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2519', 'poem', '唐', '崔护', '五月水边柳', '结根挺涯涘，垂影覆清浅。\\n睡脸寒未开，懒腰晴更软。\\n摇空条已重，拂水带方展。\\n似醉烟景凝，如愁月露泫。\\n丝长鱼误恐，枝弱禽惊践。\\n长别几多情，含春任攀搴。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2520', 'poem', '唐', '李白', '江夏送倩公归汉东', '彼美汉东国，川藏明月辉。\\n宁知丧乱后，更有一珠归。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2521', 'poem', '唐', '白居易', '代州民问', '龙昌寺底开山路，巴子台前种柳林。\\n官职家乡都忘却，谁人会得使君心。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2522', 'poem', '唐', '元稹', '夜池', '荷叶团圆茎削削，绿萍面上红衣落。\\n满池明月思啼螀，高屋无人风张幕。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2523', 'poem', '唐', '刘禹锡', '韩信庙', '将略兵机命世雄，苍黄钟室叹良弓。\\n遂令后代登坛者，每一寻思怕立功。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2524', 'poem', '唐', '郑谷', '欹枕', '欹枕高眠日午春，酒酣睡足最闲身。\\n明朝会得穷通理，未必输他马上人。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2525', 'poem', '唐', '李群玉', '放鱼', '早觅为龙去，江湖莫漫游。\\n须知香饵下，触口是铦钩。', '2018-03-09 15:18:56');
INSERT INTO `t_poem` VALUES ('2526', 'poem', '唐', '无名氏', '秋', '月色驱秋下穹昊，梁间燕语辞巢早。\\n古苔凝紫贴瑶阶，露槿啼红堕江草。\\n越客羁魂挂长道，西风欲揭南山倒。\\n粉娥恨骨不胜衣，映门楚碧蝉声老。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2527', 'poem', '唐', '白居易', '三月三日', '暮春风景初三日，流世光阴半百年。\\n欲作闲游无好伴，半江惆怅却回船。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2528', 'poem', '唐', '李白', '夜泊黄山闻殷十四吴吟', '昨夜谁为吴会吟，风生万壑振空林。\\n龙惊不敢水中卧，猿啸时闻岩下音。\\n我宿黄山碧溪月，听之却罢松间琴。\\n朝来果是沧洲逸，酤酒醍盘饭霜栗。\\n半酣更发江海声，客愁顿向杯中失。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2529', 'poem', '唐', '杜牧', '江上雨寄崔碣', '春半平江雨，圆文破蜀罗。\\n声眠篷底客，寒湿钓来蓑。\\n暗澹遮山远，空濛著柳多。\\n此时怀旧恨，相望意如何。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2530', 'poem', '唐', '孟浩然', '夏日浮舟过陈大水亭 / 浮舟过滕逸人别业', '水亭凉气多，闲棹晚来过。\\n涧影见松竹，潭香闻芰荷。\\n野童扶醉舞，山鸟助酣歌。\\n幽赏未云遍，烟光奈夕何。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2531', 'poem', '唐', '贾岛', '代旧将', '旧事说如梦，谁当信老夫。\\n战场几处在，部曲一人无。\\n落日收病马，晴天晒阵图。\\n犹希圣朝用，自镊白髭须。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2532', 'poem', '唐', '刘禹锡', '视刀环歌', '常恨言语浅，不如人意深。\\n今朝两相视，脉脉万重心。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2533', 'poem', '唐', '王维', '早秋山中作', '无才不敢累明时，思向东溪守故篱。\\n岂厌尚平婚嫁早，却嫌陶令去官迟。\\n草间蛩响临秋急，山里蝉声薄暮悲。\\n寂寞柴门人不到，空林独与白云期。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2534', 'poem', '唐', '章孝标', '鲤鱼', '眼似真珠鳞似金，时时动浪出还沈。\\n河中得上龙门去，不叹江湖岁月深。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2535', 'poem', '唐', '李煜', '赐宫人庆奴', '风情渐老见春羞，到处销魂感旧游。\\n多谢长条似相识，强垂烟态拂人头。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2536', 'poem', '唐', '吴筠', '高士咏·司马季主', '季主超常伦，沉迹寄卜筮。\\n宋贾二大夫，停车试观艺。\\n高谈哂朝列，洪辩不可际。\\n终秉鸾凤心，翛然已遐逝。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2537', 'poem', '唐', '张蠙', '经范蠡旧居', '一变姓名离百越，越城犹在范家无。\\n他人不见扁舟意，却笑轻生泛五湖。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2538', 'poem', '唐', '李涉', '岳阳别张祜', '十年蹭蹬为逐臣，鬓毛白尽巴江春。鹿鸣猿啸虽寂寞，\\n水蛟山魅多精神。山疟困中闻有赦，死灰不望光阴借。\\n半夜州符唤牧童，虚教衰病生惊怕。巫峡洞庭千里馀，\\n蛮陬水国何亲疏。由来真宰不宰我，徒劳叹者怀吹噓，\\n霸桥昔与张生别，万变桑田何处说。龙蛇纵在没泥涂，\\n长衢却为驽骀设。爱君气坚风骨峭，文章真把江淹笑。\\n洛下诸生惧刺先，乌鸢不得齐鹰鹞。岳阳西南湖上寺，\\n水阁松房遍文字。新钉张生一首诗，自馀吟著皆无味。\\n策马前途需努力，莫学龙钟虚叹息。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2539', 'poem', '唐', '张籍', '江南春·江南杨柳春', '江南杨柳春，日暖地无尘。渡口过新雨，夜来生白蘋，\\n晴沙鸣乳燕，芳树醉游人。向晚青山下，谁家祭水神。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2540', 'poem', '唐', '秦韬玉', '钓翁', '一竿青竹老江隈，荷叶衣裳可自裁。\\n潭定静悬丝影直，风高斜飐浪纹开。\\n朝携轻棹穿云去，暮背寒塘戴月回。\\n世上无穷嶮巇事，算应难入钓船来。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2541', 'poem', '唐', '柳宗元', '闻黄鹂', '倦闻子规朝暮声，不意忽有黄鹂鸣。\\n一声梦断楚江曲，满眼故园春意生。\\n目极千里无山河，麦芒际天摇清波。\\n王畿优本少赋役，务闲酒熟饶经过。\\n此时晴烟最深处，舍南巷北遥相语。\\n翻日迥度昆明飞，凌风邪看细柳翥。\\n我今误落千万山，身同伧人不思还。\\n乡禽何事亦来此，令我生心忆桑梓。\\n闭声回翅归务速，西林紫椹行当熟。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2542', 'poem', '唐', '李白', '登锦城散花楼', '日照锦城头，朝光散花楼。\\n金窗夹绣户，珠箔悬银钩。\\n飞梯绿云中，极目散我忧。\\n暮雨向三峡，春江绕双流。\\n今来一登望，如上九天游。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2543', 'poem', '唐', '司空图', '春山', '可是武陵溪，春芳著路迷。\\n花明催曙早，云腻惹空低。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2544', 'poem', '唐', '李白', '白胡桃', '红罗袖里分明见，白玉盘中看却无。\\n疑是老僧休念诵，腕前推下水晶珠。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2545', 'poem', '唐', '刘沧', '赠道者', '真趣淡然居物外，忘机多是隐天台。\\n停灯深夜看仙箓，拂石高秋坐钓台。\\n卖药故人湘水别，入檐栖鸟旧山来。\\n无因朝市知名姓，地僻衡门对岳开。', '2018-03-09 15:19:06');
INSERT INTO `t_poem` VALUES ('2546', 'poem', '唐', '高骈', '边方春兴', '草色青青柳色浓，玉壶倾酒满金钟。\\n笙歌嘹亮随风去，知尽关山第几重。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2547', 'poem', '唐', '白居易', '红藤杖', '交亲过浐别，车马到江回。\\n唯有红藤杖，相随万里来。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2548', 'poem', '唐', '皮日休', '金钱花', '阴阳为炭地为炉，铸出金钱不用模。\\n莫向人间逞颜色，不知还解济贫无。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2549', 'poem', '唐', '李群玉', '新荷', '田田八九叶，散点绿池初。\\n嫩碧才平水，圆阴已蔽鱼。\\n浮萍遮不合，弱荇绕犹疏。\\n半在春波底，芳心卷未舒。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2550', 'poem', '唐', '白居易', '杂曲歌辞·小曲新词', '霁色鲜宫殿，秋声脆管弦。\\n圣明千岁乐，岁岁似今年。\\n红裙明月夜，碧殿早秋时。\\n好向昭阳宿，天凉玉漏迟。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2551', 'poem', '唐', '孟郊', '乱离', '天下无义剑，中原多疮痍。\\n哀哀陆大夫，正直神反欺。\\n子路已成血，嵇康今尚嗤。\\n为君每一恸，如剑在四肢。\\n折羽不复飞，逝水不复归。\\n直松摧高柯，弱蔓将何依。\\n朝为春日欢，夕为秋日悲。\\n泪下无尺寸，纷纷天雨丝。\\n积怨成疾疹，积恨成狂痴。\\n怨草岂有边，恨水岂有涯。\\n怨恨驰我心，茫茫日何之。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2552', 'poem', '唐', '李贺', '春坊正字剑子歌', '先辈匣中三尺水，曾入吴潭斩龙子。\\n隙月斜明刮露寒，练带平铺吹不起。\\n蛟胎皮老蒺藜刺，鸊鹈淬花白鹇尾。\\n直是荆轲一片心，莫教照见春坊字。\\n挼丝团金悬簏簌，神光欲截蓝田玉。\\n提出西方白帝惊，嗷嗷鬼母秋郊哭。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2553', 'poem', '唐', '韦庄', '关河道中', '槐陌蝉声柳市风，驿楼高倚夕阳东。\\n往来千里路长在，聚散十年人不同。\\n但见时光流似箭，岂知天道曲如弓。\\n平生志业匡尧舜，又拟沧浪学钓翁。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2554', 'poem', '唐', '耿湋', '送李端', '世上许刘桢，洋洋风雅声。\\n客来空改岁，归去未成名。\\n远近天初暮，关河雪半晴。\\n空怀谏书在，回首恋承明。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2555', 'poem', '唐', '姚合', '咏雪', '愁云残腊下阳台，混却乾坤六出开。\\n与月交光呈瑞色，共花争艳傍寒梅。\\n飞随郢客歌声远，散逐宫娥舞袖回。\\n其那知音不相见，剡溪乘兴为君来。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2556', 'poem', '唐', '卢照邻', '送二兄入蜀', '关山客子路，花柳帝王城。\\n此中一分手，相顾怜无声。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2557', 'poem', '唐', '白居易', '紫阳花', '何年植向仙坛上，早晚移栽到梵家。\\n虽在人间人不识，与君名作紫阳花。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2558', 'poem', '唐', '杜牧', '雨', '连云接塞添迢递，洒幕侵灯送寂寥。\\n一夜不眠孤客耳，主人窗外有芭蕉。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2559', 'poem', '唐', '杜甫', '滟滪堆', '巨积水中央，江寒出水长。\\n沈牛答云雨，如马戒舟航。\\n天意存倾覆，神功接混茫。\\n干戈连解缆，行止忆垂堂。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2560', 'poem', '唐', '韩偓', '寄恨', '秦钗枉断长条玉，蜀纸虚留小字红。\\n死恨物情难会处，莲花不肯嫁春风。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2561', 'poem', '唐', '李商隐', '七夕', '鸾扇斜分凤幄开，星桥横过鹊飞回。\\n争将世上无期别，换得年年一度来。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2562', 'poem', '唐', '赵鸾鸾', '云鬟', '扰扰香云湿未干，鸦领蝉翼腻光寒。\\n侧边斜插黄金凤，妆罢夫君带笑看。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2563', 'poem', '唐', '杜牧', '重登科', '星汉离宫月出轮，满街含笑绮罗春。\\n花前每被青蛾问，何事重来只一人。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2564', 'poem', '唐', '张蠙', '吊万人冢', '兵罢淮边客路通，乱鸦来去噪寒空。\\n可怜白骨攒孤冢，尽为将军觅战功。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2565', 'poem', '唐', '杜甫', '野望', '金华山北涪水西，仲冬风日始凄凄。\\n山连越巂蟠三蜀，水散巴渝下五溪。\\n独鹤不知何事舞，饥乌似欲向人啼。\\n射洪春酒寒仍绿，目极伤神谁为携。', '2018-03-09 15:19:16');
INSERT INTO `t_poem` VALUES ('2566', 'poem', '唐', '来鹄', '清明日与友人游玉粒塘庄', '几宿春山逐陆郎，清明时节好烟光。\\n归穿细荇船头滑，醉踏残花屐齿香。\\n风急岭云飘迥野，雨馀田水落方塘。\\n不堪吟罢东回首，满耳蛙声正夕阳。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2567', 'poem', '唐', '杜牧', '醉倒', '日晴空乐下仙云，俱在凉亭送使君。\\n莫辞一盏即相请，还是三年更不闻。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2568', 'poem', '唐', '常建', '落第长安', '家园好在尚留秦，耻作明时失路人。\\n恐逢故里莺花笑，且向长安度一春。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2569', 'poem', '唐', '李白', '东鲁见狄博通', '去年别我向何处，有人传道游江东。\\n谓言挂席度沧海，却来应是无长风。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2570', 'poem', '唐', '韩偓', '夕阳', '花前洒泪临寒食，醉里回头问夕阳。\\n不管相思人老尽，朝朝容易下西墙。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2571', 'poem', '唐', '白居易', '山下宿', '独到山下宿，静向月中行。\\n何处水边碓，夜舂云母声。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2572', 'poem', '唐', '郑谷', '读李白集', '何事文星与酒星，一时钟在李先生。\\n高吟大醉三千首，留著人间伴月明。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2573', 'poem', '唐', '白居易', '种桃杏', '无论海角与天涯，大抵心安即是家。\\n路远谁能念乡曲，年深兼欲忘京华。\\n忠州且作三年计，种杏栽桃拟待花。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2574', 'poem', '唐', '卢照邻', '横吹曲辞·梅花落', '梅岭花初发，天山雪未开。\\n雪处疑花满，花边似雪回。\\n因风入舞袖，杂粉向妆台。\\n匈奴几万里，春至不知来。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2575', 'poem', '唐', '白居易', '除夜', '薄晚支颐坐，中宵枕臂眠。\\n一从身去国，再见日周天。\\n老度江南岁，春抛渭北田。\\n浔阳来早晚，明日是三年。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2576', 'poem', '唐', '白居易', '渭村酬李二十见寄', '百里音书何太迟，暮秋把得暮春诗。\\n柳条绿日君相忆，梨叶红时我始知。\\n莫叹学官贫冷落，犹胜村客病支离。\\n形容意绪遥看取，不似华阳观里时。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2577', 'poem', '唐', '张说', '入海二首', '乘桴入南海，海旷不可临。\\n茫茫失方面，混混如凝阴。\\n云山相出没，天地互浮沉。\\n万里无涯际，云何测广深。\\n潮波自盈缩，安得会虚心。\\n海上三神山，逍遥集众仙。\\n灵心岂不同，变化无常全。\\n龙伯如人类，一钓两鳌连。\\n金台此沦没，玉真时播迁。\\n问子劳何事，江上泣经年。\\n隰中生红草，所美非美然。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2578', 'poem', '唐', '李白', '月下独酌其三', '三月咸阳城。\\n千花昼如锦。\\n谁能春独愁。\\n对此径须饮。\\n穷通与修短。\\n造化夙所禀。\\n一樽齐死生。\\n万事固难审。\\n醉後失天地。\\n兀然就孤枕。\\n不知有吾身。\\n此乐最为甚。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2579', 'poem', '唐', '白居易', '代春赠', '山吐晴岚水放光，辛夷花白柳梢黄。\\n但知莫作江西意，风景何曾异帝乡。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2580', 'poem', '唐', '孟郊', '桐庐山中赠李明府', '静境无浊氛，清雨零碧云。\\n千山不隐响，一叶动亦闻。\\n即此佳志士，精微谁相群。\\n欲识楚章句，袖中兰茝薰。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2581', 'poem', '唐', '周贺', '春日山居寄友人', '春居无俗喧，时立涧前村。\\n路远少来客，山深多过猿。\\n带岩松色老，临水杏花繁。\\n除忆文流外，何人更可言。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2582', 'poem', '唐', '白居易', '江楼偶宴赠同座', '南浦闲行罢，西楼小宴时。\\n望湖凭槛久，待月放杯迟。\\n江果尝卢橘，山歌听竹枝。\\n相逢且同乐，何必旧相知。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2583', 'poem', '唐', '李白', '庐江主人妇', '孔雀东飞何处栖，庐江小吏仲卿妻。\\n为客裁缝君自见，城乌独宿夜空啼。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2584', 'poem', '唐', '元稹', '香球', '顺俗唯团转，居中莫动摇。\\n爱君心不恻，犹讶火长烧。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2585', 'poem', '唐', '冯道', '天道', '穷达皆由命，何劳发叹声。\\n但知行好事，莫要问前程。\\n冬去冰须泮，春来草自生。\\n请君观此理，天道甚分明。', '2018-03-09 15:19:27');
INSERT INTO `t_poem` VALUES ('2586', 'poem', '唐', '白居易', '涂山寺独游', '野径行无伴，僧房宿有期。\\n涂山来去熟，唯是马蹄知。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2587', 'poem', '唐', '王昌龄', '斋心', '女萝覆石壁，溪水幽濛胧。\\n紫葛蔓黄花，娟娟寒露中。\\n朝饮花上露，夜卧松下风。\\n云英化为水，光采与我同。\\n日月荡精魄，寥寥天宇空。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2588', 'poem', '唐', '张仲素', '塞下曲五首', '三戍渔阳再渡辽，騂弓在臂剑横腰。\\n匈奴似若知名姓，休傍阴山更射雕。\\n猎马千行雁几双，燕然山下碧油幢。\\n传声漠北单于破，火照旌旗夜受降。\\n朔雪飘飘开雁门，平沙历乱卷蓬根。\\n功名耻计擒生数，直斩楼兰报国恩。\\n陇水潺湲陇树秋，征人到此泪双流。\\n乡关万里无因见，西戍河源早晚休。\\n阴碛茫茫塞草肥，桔槔烽上暮云飞。\\n交河北望天连海，苏武曾将汉节归。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2589', 'poem', '唐', '徐姓', '玄都观', '千寻绿嶂夹流溪，登眺因知海岳低。\\n瀑布迸舂青石碎，轮茵横翦翠峰齐。\\n步黏苔藓龙桥滑，日闭烟罗鸟径迷。\\n莫道穹天无路到，此山便是碧云梯。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2590', 'poem', '唐', '陆龟蒙', '吴宫怀古', '香径长洲尽棘丛，奢云艳雨只悲风。\\n吴王事事须亡国，未必西施胜六宫。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2591', 'poem', '唐', '白居易', '酬严中丞晚眺黔江见寄', '江水三回曲，愁人两地情。\\n磨围山下色，明月峡中声。\\n晚后连天碧，秋来彻底清。\\n临流有新恨，照见白须生。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2592', 'poem', '唐', '李愿', '思妇', '良人久不至，惟恨锦屏孤。\\n憔悴衣宽日，空房问女巫。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2593', 'poem', '唐', '徐惠', '进太宗', '朝来临镜台，妆罢暂裴回。\\n千金始一笑，一召讵能来。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2594', 'poem', '唐', '贺知章', '答朝士', '鈒镂银盘盛蛤蜊，镜湖莼菜乱如丝。\\n乡曲近来佳此味，遮渠不道是吴儿。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2595', 'poem', '唐', '方干', '题报恩寺上方', '来来先上上方看，眼界无穷世界宽。\\n岩溜喷空晴似雨，林萝碍日夏多寒。\\n众山迢递皆相叠，一路高低不记盘。\\n清峭关心惜归去，他时梦到亦难判。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2596', 'poem', '唐', '刘禹锡', '八月十五夜桃源玩月', '尘中见月心亦闲，况是清秋仙府间。\\n凝光悠悠寒露坠，此时立在最高山。\\n碧虚无云风不起，山上长松山下水。\\n群动悠然一顾中，天高地平千万里。\\n少君引我升玉坛，礼空遥请真仙官。\\n云拼欲下星斗动，天乐一声肌骨寒。\\n金霞昕昕渐东上，轮欹影促犹频望。\\n绝景良时难再并，他年此日应惆怅。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2597', 'poem', '唐', '白居易', '莲石', '青石一两片，白莲三四枝。\\n寄将东洛去，心与物相随。\\n石倚风前树，莲栽月下池。\\n遥知安置处，预想发荣时。\\n领郡来何远，还乡去已迟。\\n莫言千里别，岁晚有心期。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2598', 'poem', '唐', '王昌龄', '胡笳曲', '城南虏已合，一夜几重围。\\n自有金笳引，能沾出塞衣。\\n听临关月苦，清入海风微。\\n三奏高楼晓，胡人掩涕归。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2599', 'poem', '唐', '李世民', '月晦', '晦魄移中律，凝暄起丽城。\\n罩云朝盖上，穿露晓珠呈。\\n笑树花分色，啼枝鸟合声。\\n披襟欢眺望，极目畅春情。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2600', 'poem', '唐', '李世民', '咏桃', '禁苑春晖丽，花蹊绮树妆。\\n缀条深浅色，点露参差光。\\n向日分千笑，迎风共一香。\\n如何仙岭侧，独秀隐遥芳。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2601', 'poem', '唐', '白居易', '冬至夜', '老去襟怀常濩落，病来须鬓转苍浪。\\n心灰不及炉中火，鬓雪多于砌下霜。\\n三峡南宾城最远，一年冬至夜偏长。\\n今宵始觉房栊冷，坐索寒衣托孟光。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2602', 'poem', '唐', '杜甫', '冬至', '年年至日长为客，忽忽穷愁泥杀人。\\n江上形容吾独老，天边风俗自相亲。\\n杖藜雪后临丹壑，鸣玉朝来散紫宸。\\n心折此时无一寸，路迷何处见三秦。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2603', 'poem', '唐', '李白', '经下邳圯桥怀张子房', '子房未虎啸，破产不为家。\\n沧海得壮士，椎秦博浪沙。\\n报韩虽不成，天地皆振动。\\n潜匿游下邳，岂曰非智勇？\\n我来圯桥上，怀古钦英风。\\n唯见碧流水，曾无黄石公。\\n叹息此人去，萧条徐泗空。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2604', 'poem', '唐', '王绩', '古意六首', '幽人在何所，紫岩有仙躅。月下横宝琴，此外将安欲。\\n材抽峄山干，徽点昆丘玉。漆抱蛟龙唇，丝缠凤凰足。\\n前弹广陵罢，后以明光续。百金买一声，千金传一曲。\\n世无钟子期，谁知心所属。\\n竹生大夏溪，苍苍富奇质。绿叶吟风劲，翠茎犯霄密。\\n霜霰封其柯，鹓鸾食其实。宁知轩辕后，更有伶伦出。\\n刀斧俄见寻，根株坐相失。裁为十二管，吹作雄雌律。\\n有用虽自伤，无心复招疾。不如山上草，离离保终吉。\\n宝龟尺二寸，由来宅深水。浮游五湖内，宛转三江里。\\n何不深复深，轻然至溱洧。溱洧源流狭，春秋不濡轨。\\n渔人递往还，网罟相萦藟。一朝失运会，刳肠血流死。\\n丰骨输庙堂，鲜腴藉笾簋。弃置谁怨尤，自我招此否。\\n馀灵寄明卜，复来钦所履。\\n松生北岩下，由来人径绝。布叶捎云烟，插根拥岩穴。\\n自言生得地，独负凌云洁。何时畏斤斧，几度经霜雪。\\n风惊西北枝，雹陨东南节。不知岁月久，稍觉枝干折。\\n藤萝上下碎，枝干纵横裂。行当糜烂尽，坐共灰尘灭。\\n宁关匠石顾，岂为王孙折。盛衰自有时，圣贤未尝屑。\\n寄言悠悠者，无为嗟大耋。\\n桂树何苍苍，秋来花更芳。自言岁寒性，不知露与霜。\\n幽人重其德，徙植临前堂。连拳八九树，偃蹇二三行。\\n枝枝自相纠，叶叶还相当。去来双鸿鹄，栖息两鸳鸯。\\n荣荫诚不厚，斤斧亦勿伤。赤心许君时，此意那可忘。\\n彩凤欲将归，提罗出郊访。罗张大泽已，凤入重云飏，\\n朝栖昆阆木，夕饮蓬壶涨。问凤那远飞，贤君坐相望。\\n凤言荷深德，微禽安足尚。但使雏卵全，无令矰缴放。\\n皇臣力牧举，帝乐箫韶畅。自有来巢时，明年阿阁上。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2605', 'poem', '唐', '杜甫', '进艇', '南京久客耕南亩，北望伤神坐北窗。\\n昼引老妻乘小艇，晴看稚子浴清江。\\n俱飞蛱蝶元相逐，并蒂芙蓉本自双。\\n茗饮蔗浆携所有，瓷罂无谢玉为缸。', '2018-03-09 15:19:38');
INSERT INTO `t_poem` VALUES ('2606', 'poem', '唐', '王维', '文杏馆', '文杏裁为梁，香茅结为宇。\\n不知栋里云，去作人间雨。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2607', 'poem', '唐', '李中', '渔父二首', '偶向芦花深处行，溪光山色晚来晴。\\n渔家开户相迎接，稚子争窥犬吠声。\\n雪鬓衰髯白布袍，笑携赪鲤换村醪。\\n殷勤留我宿溪上，钓艇归来明月高。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2608', 'poem', '唐', '白居易', '送萧处士游黔南', '能文好饮老萧郎，身似浮云鬓似霜。\\n生计抛来诗是业，家园忘却酒为乡。\\n江从巴峡初成字，猿过巫阳始断肠。\\n不醉黔中争去得，磨围山月正苍苍。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2609', 'poem', '唐', '李峤', '风', '落日生蘋末，摇扬遍远林。\\n带花疑凤舞，向竹似龙吟。\\n月动临秋扇，松清入夜琴。\\n若至兰台下，还拂楚王襟。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2610', 'poem', '唐', '韩偓', '忍笑', '宫样衣裳浅画眉，晚来梳洗更相宜。\\n水精鹦鹉钗头颤，举袂佯羞忍笑时。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2611', 'poem', '唐', '徐姓', '玄都观', '登寻丹壑到玄都，接日红霞照座隅。\\n即向周回岩下看，似看曾进画图无。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2612', 'poem', '唐', '沈彬', '阳朔碧莲峰', '陶潜彭泽五株柳，潘岳河阳一县花。\\n两处争如阳朔好，碧莲峰里住人家。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2613', 'poem', '唐', '李白', '送温处士归黄山白鹅峰旧居', '黄山四千仞，三十二莲峰。\\n丹崖夹石柱，菡萏金芙蓉。\\n伊昔升绝顶，下窥天目松。\\n仙人炼玉处，羽化留馀踪。\\n亦闻温伯雪，独往今相逢。\\n采秀辞五岳，攀岩历万重。\\n归休白鹅岭，渴饮丹砂井。\\n凤吹我时来，云车尔当整。\\n去去陵阳东，行行芳桂丛。\\n回溪十六度，碧嶂尽晴空。\\n他日还相访，乘桥蹑彩虹。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2614', 'poem', '唐', '李商隐', '哭刘司户蕡', '路有论冤谪，言皆在中兴。\\n空闻迁贾谊，不待相孙弘。\\n江阔惟回首，天高但抚膺。\\n去年相送地，春雪满黄陵。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2615', 'poem', '唐', '宋之问', '陆浑山庄', '归来物外情，负杖阅岩耕。\\n源水看花入，幽林采药行。\\n野人相问姓，山鸟自呼名。\\n去去独吾乐，无然愧此生。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2616', 'poem', '唐', '韩偓', '青春', '眼意心期卒未休，暗中终拟约秦楼。\\n光阴负我难相遇，情绪牵人不自由。\\n遥夜定嫌香蔽膝，闷时应弄玉搔头。\\n樱桃花谢梨花发，肠断青春两处愁。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2617', 'poem', '唐', '李白', '送王屋山人魏万还王屋·并序', '王屋山人魏万，云自嵩宋沿吴相访，数千里不遇。乘兴游台越，经永嘉，观谢公石门。后于广陵相见，美其爱文好古，浪迹方外，因述其行而赠是诗。仙人东方生，浩荡弄云海。\\n仙人东方生，浩荡弄云海。沛然乘天游，独往失所在。\\n魏侯继大名，本家聊摄城。卷舒入元化，迹与古贤并。\\n十三弄文史，挥笔如振绮。辩折田巴生，心齐鲁连子。\\n西涉清洛源，颇惊人世喧。采秀卧王屋，因窥洞天门。\\n朅来游嵩峰，羽客何双双。朝携月光子，暮宿玉女窗。\\n鬼谷上窈窕，龙潭下奔潈。东浮汴河水，访我三千里。\\n逸兴满吴云，飘飖浙江汜。挥手杭越间，樟亭望潮还。\\n涛卷海门石，云横天际山。白马走素车，雷奔骇心颜。\\n遥闻会稽美，且度耶溪水。万壑与千岩，峥嵘镜湖里。\\n秀色不可名，清辉满江城。人游月边去，舟在空中行。\\n此中久延伫，入剡寻王许。笑读曹娥碑，沉吟黄绢语。\\n天台连四明，日入向国清。五峰转月色，百里行松声。\\n灵溪咨沿越，华顶殊超忽。石梁横青天，侧足履半月。\\n忽然思永嘉，不惮海路赊。挂席历海峤，回瞻赤城霞。\\n赤城渐微没，孤屿前峣兀。水续万古流，亭空千霜月。\\n缙云川谷难，石门最可观。瀑布挂北斗，莫穷此水端。\\n喷壁洒素雪，空濛生昼寒。却思恶溪去，宁惧恶溪恶。\\n咆哮七十滩，水石相喷薄。路创李北海，岩开谢康乐。\\n松风和猿声，搜索连洞壑。径出梅花桥，双溪纳归潮。\\n落帆金华岸，赤松若可招。沈约八咏楼，城西孤岧峣，\\n岧峣四荒外，旷望群川会。云卷天地开，波连浙西大。\\n乱流新安口，北指严光濑。钓台碧云中，邈与苍岭对。\\n稍稍来吴都，裴回上姑苏。烟绵横九疑，漭荡见五湖。\\n目极心更远，悲歌但长吁。回桡楚江滨，挥策扬子津。\\n身著日本裘，昂藏出风尘。五月造我语，知非儓儗人。\\n相逢乐无限，水石日在眼。徒干五诸侯，不致百金产。\\n吾友扬子云，弦歌播清芬。虽为江宁宰，好与山公群。\\n乘兴但一行，且知我爱君。君来几何时，仙台应有期。\\n东窗绿玉树，定长三五枝。至今天坛人，当笑尔归迟。\\n我苦惜远别，茫然使心悲。黄河若不断，白首长相思。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2618', 'poem', '唐', '张子容', '相和歌辞·春江花月夜二首', '林花发岸口，气色动江新。\\n此夜江中月，流光花上春。\\n分明石潭里，宜照浣纱人。\\n交甫怜瑶佩，仙妃难重期。\\n沉沉绿江晚，惆怅碧云姿。\\n初逢花上月，言是弄珠时。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2619', 'poem', '唐', '杜甫', '三绝句', '前年渝州杀刺史，今年开州杀刺史。\\n群盗相随剧虎狼，食人更肯留妻子。\\n二十一家同入蜀，惟残一人出骆谷。\\n自说二女啮臂时，回头却向秦云哭。\\n殿前兵马虽骁雄，纵暴略与羌浑同。\\n闻道杀人汉水上，妇女多在官军中。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2620', 'poem', '唐', '白居易', '杂曲歌辞·何满子', '世传满子是人名，临就刑时曲始成。\\n一曲四词歌八叠，从头便是断肠声。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2621', 'poem', '唐', '白居易', '赋得听边鸿', '惊风吹起塞鸿群，半拂平沙半入云。\\n为问昭君月下听，何如苏武雪中闻。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2622', 'poem', '唐', '汪遵', '乌江', '兵散弓残挫虎威，单枪匹马突重围。\\n英雄去尽羞容在，看却江东不得归。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2623', 'poem', '唐', '白居易', '见元九悼亡诗，因以此寄', '夜泪暗销明月幌，春肠遥断牡丹庭。\\n人间此病治无药，唯有楞伽四卷经。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2624', 'poem', '唐', '李商隐', '城外', '露寒风定不无情，临水当山又隔城。\\n未必明时胜蚌蛤，一生长共月亏盈。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2625', 'poem', '唐', '李世民', '赋得临池柳', '岸曲丝阴聚，波移带影疏。\\n还将眉里翠，来就镜中舒。', '2018-03-09 15:19:45');
INSERT INTO `t_poem` VALUES ('2626', 'poem', '唐', '李白', '南都行', '南都信佳丽，武阙横西关。白水真人居，万商罗鄽闤，\\n高楼对紫陌，甲第连青山。此地多英豪，邈然不可攀。\\n陶朱与五羖，名播天壤间。丽华秀玉色，汉女娇朱颜。\\n清歌遏流云，艳舞有馀闲。遨游盛宛洛，冠盖随风还。\\n走马红阳城，呼鹰白河湾。谁识卧龙客，长吟愁鬓斑。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2627', 'poem', '唐', '李商隐', '哭刘蕡', '上帝深宫闭九阍，巫咸不下问衔冤。\\n广陵别后春涛隔，湓浦书来秋雨翻。\\n只有安仁能作诔，何曾宋玉解招魂。\\n平生风义兼师友，不敢同君哭寝门。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2628', 'poem', '唐', '马戴', '雪中送青州薛评事', '腊景不可犯，从戎难自由。\\n怜君急王事，走马赴边州。\\n岳雪明日观，海云冒营丘。\\n惭无斗酒泻，敢望御重裘。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2629', 'poem', '唐', '殷文圭', '赵侍郎看红白牡丹因寄杨状头赞图', '迟开都为让群芳，贵地栽成对玉堂。\\n红艳袅烟疑欲语，素华映月只闻香。\\n剪裁偏得东风意，淡薄似矜西子妆。\\n雅称花中为首冠，年年长占断春光。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2630', 'poem', '唐', '白居易', '浔阳春三首·春来', '春来触地故乡情，忽见风光忆两京。\\n金谷踏花香骑入，曲江碾草钿车行。\\n谁家绿酒欢连夜，何处红楼睡失明。\\n独有不眠不醉客，经春冷坐古湓城。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2631', 'poem', '唐', '白居易', '村雪夜坐', '南窗背灯坐，风霰暗纷纷。\\n寂寞深村夜，残雁雪中闻。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2632', 'poem', '唐', '白居易', '立秋日曲江忆元九', '下马柳阴下，独上堤上行。\\n故人千万里，新蝉三两声。\\n城中曲江水，江上江陵城。\\n两地新秋思，应同此日情。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2633', 'poem', '唐', '韦庄', '江上别李秀才', '前年相送灞陵春，今日天涯各避秦。\\n莫向尊前惜沈醉，与君俱是异乡人。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2634', 'poem', '唐', '张固', '独秀山', '孤峰不与众山俦，直入青云势未休。\\n会得乾坤融结意，擎天一柱在南州。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2635', 'poem', '唐', '白居易', '答骑马入空台', '君入空台去，朝往暮还来。\\n我入泉台去，泉门无复开。\\n鳏夫仍系职，稚女未胜哀。\\n寂寞咸阳道，家人覆墓回。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2636', 'poem', '唐', '司空图', '诗品二十四则·含蓄', '不著一字，尽得风流。\\n语不涉难，已不堪忧。\\n是有真宰，与之沈浮。\\n如渌满酒，花时返秋。\\n悠悠空尘，忽忽海沤。\\n浅深聚散，万取一收。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2637', 'poem', '唐', '顾况', '春游曲二首', '游童苏合带，倡女蒲葵扇。\\n初日映城时，相思忽相见。\\n褰裳踏露草，理鬓回花面。\\n薄暮不同归，留情此芳甸。\\n柘弹连钱马，银钩妥堕鬟。\\n采桑春陌上，踏草夕阳间。\\n意合词先露，心诚貌却闲。\\n明朝若相忆，云雨出巫山。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2638', 'poem', '唐', '卢照邻', '浴浪鸟', '独舞依磐石，群飞动轻浪。\\n奋迅碧沙前，长怀白云上。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2639', 'poem', '唐', '韦庄', '绥州作', '雕阴无树水难流，雉堞连云古帝州。\\n带雨晚驼鸣远戍，望乡孤客倚高楼。\\n明妃去日花应笑，蔡琰归时鬓已秋。\\n一曲单于暮烽起，扶苏城上月如钩。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2640', 'poem', '唐', '韩偓', '懒起', '百舌唤朝眠，春心动几般。\\n枕痕霞黯澹，泪粉玉阑珊。\\n笼绣香烟歇，屏山烛焰残。\\n暖嫌罗袜窄，瘦觉锦衣宽。\\n昨夜三更雨，今朝一阵寒。\\n海棠花在否，侧卧卷帘看。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2641', 'poem', '唐', '杜甫', '野人送朱樱', '西蜀樱桃也自红，野人相赠满筠笼。\\n数回细写愁仍破，万颗匀圆讶许同。\\n忆昨赐沾门下省，退朝擎出大明宫。\\n金盘玉箸无消息，此日尝新任转蓬。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2642', 'poem', '唐', '杜审言', '登襄阳城', '旅客三秋至，层城四望开。\\n楚山横地出，汉水接天回。\\n冠盖非新里，章华即旧台。\\n习池风景异，归路满尘埃。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2643', 'poem', '唐', '刘长卿', '寻张逸人山居', '危石才通鸟道，空山更有人家。\\n桃源定在深处，涧水浮来落花。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2644', 'poem', '唐', '白居易', '暮立', '黄昏独立佛堂前，满地槐花满树蝉。\\n大抵四时心总苦，就中肠断是秋天。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2645', 'poem', '唐', '白居易', '夜坐', '斜月入前楹，迢迢夜坐情。\\n梧桐上阶影，蟋蟀近床声。\\n曙傍窗间至，秋从簟上生。\\n感时因忆事，不寝到鸡鸣。', '2018-03-09 15:19:57');
INSERT INTO `t_poem` VALUES ('2646', 'poem', '唐', '杜甫', '陪李北海宴历下亭', '东藩驻皂盖，北渚凌青荷。\\n海内此亭古，济南名士多。\\n云山已发兴，玉佩仍当歌。\\n修竹不受暑，交流空涌波。\\n蕴真惬所遇，落日将如何。\\n贵贱俱物役，从公难重过。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2647', 'poem', '唐', '秦韬玉', '送友人罢举授南陵令', '共言愁是酌离杯，况值弦歌枉大才。\\n献赋未为龙化去，除书犹喜凤衔来。\\n花明驿路燕脂暖，山入江亭罨画开。\\n莫把新诗题别处，谢家临水有池台。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2648', 'poem', '唐', '杨炯', '出塞', '塞外欲纷纭，雌雄犹未分。\\n明堂占气色，华盖辨星文。\\n二月河魁将，三千太乙军。\\n丈夫皆有志，会见立功勋。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2649', 'poem', '唐', '李益', '隋宫燕', '燕语如伤旧国春，宫花一落已成尘。\\n自从一闭风光后，几度飞来不见人。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2650', 'poem', '唐', '王建', '新晴', '夏夜新晴星校少，雨收残水入天河。\\n檐前熟著衣裳坐，风冷浑无扑火蛾。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2651', 'poem', '唐', '李商隐', '拟沈下贤', '千二百轻鸾，春衫瘦著宽。\\n倚风行稍急，含雪语应寒。\\n带火遗金斗，兼珠碎玉盘。\\n河阳看花过，曾不问潘安。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2652', 'poem', '唐', '白居易', '过郑处士', '闻道移居村坞间，竹林多处独开关。\\n故来不是求他事，暂借南亭一望山。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2653', 'poem', '唐', '薛涛', '菱荇沼', '水荇斜牵绿藻浮，柳丝和叶卧清流。\\n何时得向溪头赏，旋摘菱花旋泛舟。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2654', 'poem', '唐', '张祜', '信州水亭', '南檐架短廊，沙路白茫茫。\\n尽日不归处，一庭栀子香。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2655', 'poem', '唐', '李建勋', '尊前', '官为将相复何求，世路多端早合休。\\n渐老更知春可惜，正欢唯怕客难留。\\n雨催草色还依旧，晴放花枝始自由。\\n莫厌百壶相劝倒，免教无事结闲愁。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2656', 'poem', '唐', '令狐楚', '思君恩', '小苑莺歌歇，长门蝶舞多。\\n眼看春又去，翠辇不经过。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2657', 'poem', '唐', '佚名', '举子语', '槐花黄，举子忙。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2658', 'poem', '唐', '李白', '枯鱼过河泣', '白龙改常服，偶被豫且制。\\n谁使尔为鱼，徒劳诉天帝。\\n作书报鲸鲵，勿恃风涛势。\\n涛落归泥沙，翻遭蝼蚁噬。\\n万乘慎出入，柏人以为识。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2659', 'poem', '唐', '薛涛', '酬人雨后玩竹', '南天春雨时，那鉴雪霜姿。\\n众类亦云茂，虚心宁自持。\\n多留晋贤醉，早伴舜妃悲。\\n晚岁君能赏，苍苍劲节奇。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2660', 'poem', '唐', '王维', '辋川闲居', '一从归白社，不复到青门。\\n时倚檐前树，远看原上村。\\n青菰临水拔，白鸟向山翻。\\n寂寞於陵子，桔槔方灌园。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2661', 'poem', '唐', '顾况', '洛阳早春', '何地避春愁，终年忆旧游。\\n一家千里外，百舌五更头。\\n客路偏逢雨，乡山不入楼。\\n故园桃李月，伊水向东流。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2662', 'poem', '唐', '薛涛', '月', '魄依钩样小，扇逐汉机团。\\n细影将圆质，人间几处看。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2663', 'poem', '唐', '周昙', '春秋战国门·隐公', '今古难堤是小人，苟希荣宠任相亲。\\n陈谋不信怀忧惧，反间须防却害身。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2664', 'poem', '唐', '杜甫', '九日', '去年登高郪县北，今日重在涪江滨。\\n苦遭白发不相放，羞见黄花无数新。\\n世乱郁郁久为客，路难悠悠常傍人。\\n酒阑却忆十年事，肠断骊山清路尘。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2665', 'poem', '唐', '元结', '二风诗·治风诗五篇·至理', '理何为兮，系修文德。加之清一，莫不顺则。意彼刑法，\\n设以化人；致使无之，而化益纯。所谓代刑，以道去杀。\\n呜呼呜呼！人不斯察。', '2018-03-09 15:20:03');
INSERT INTO `t_poem` VALUES ('2666', 'poem', '唐', '白居易', '昼卧', '抱枕无言语，空房独悄然。\\n谁知尽日卧，非病亦非眠。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2667', 'poem', '唐', '雍陶', '蝉', '高树蝉声入晚云，不唯愁我亦愁君。\\n何时各得身无事，每到闻时似不闻。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2668', 'poem', '唐', '戴叔伦', '塞上曲二首', '军门频纳受降书，一剑横行万里馀。\\n汉祖谩夸娄敬策，却将公主嫁单于。\\n汉家旌帜满阴山，不遣胡儿匹马还。\\n愿得此身长报国，何须生入玉门关。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2669', 'poem', '唐', '王昌龄', '青楼曲二首', '白马金鞍从武皇，旌旗十万宿长杨。\\n楼头小妇鸣筝坐，遥见飞尘入建章。\\n驰道杨花满御沟，红妆缦绾上青楼。\\n金章紫绶千馀骑，夫婿朝回初拜侯。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2670', 'poem', '唐', '高适', '淇上别业', '依依西山下，别业桑林边。\\n庭鸭喜多雨，邻鸡知暮天。\\n野人种秋菜，古老开原田。\\n且向世情远，吾今聊自然。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2671', 'poem', '唐', '白居易', '春来', '春来触动故乡情，忽见风光忆两京。金谷蹋花香骑入，曲江碾草钿车行。谁家绿酒欢连夜？何处红楼睡失明？独有不眠不醉客，经春冷坐古湓城。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2672', 'poem', '唐', '张祜', '赠窦家小儿', '深绿衣裳小小人，每来听里解相亲。\\n天生合去云霄上，一尺松栽已出尘。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2673', 'poem', '唐', '袁皓', '及第后作', '金榜高悬姓字真，分明折得一枝春。\\n蓬瀛乍接神仙侣，江海回思耕钓人。\\n九万抟扶排羽翼，十年辛苦涉风尘。\\n升平时节逢公道，不觉龙门是嶮津。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2674', 'poem', '唐', '白居易', '岁暮道情二首', '壮日苦曾惊岁月，长年都不惜光阴。\\n为学空门平等法，先齐老少死生心。\\n半故青衫半白头，雪风吹面上江楼。\\n禅功自见无人觉，合是愁时亦不愁。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2675', 'poem', '唐', '韦应物', '月夜', '皓月流春城，华露积芳草。\\n坐念绮窗空，翻伤清景好。\\n清景终若斯，伤多人自老。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2676', 'poem', '唐', '钱起', '送征雁', '秋空万里净，嘹唳独南征。\\n风急翻霜冷，云开见月惊。\\n塞长怯去翼，影灭有馀声。\\n怅望遥天外，乡愁满目生。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2677', 'poem', '唐', '李世民', '赐房玄龄', '太液仙舟迥，西园引上才。\\n未晓征车度，鸡鸣关早开。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2678', 'poem', '唐', '王建', '园果', '雨中梨果病，每树无数个。\\n小儿出入看，一半鸟啄破。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2679', 'poem', '唐', '刘采春', '啰唝曲·那年离别日', '那年离别日，只道住桐庐。\\n桐庐人不见，今得广州书。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2680', 'poem', '唐', '陈翥', '金钱花', '袅露牵风夹瘦莎，一星星火遍窠窠。\\n闲门永巷新秋里，幸不伤廉莫怕多。\\n古藓寒芜让品流，小斋多谢伴清幽。\\n若教夷甫门前种，也是无多过一秋。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2681', 'poem', '唐', '曹邺', '寄刘驾', '一川草色青袅袅，绕屋水声如在家。\\n怅望美人不携手，墙东又发数枝花。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2682', 'poem', '唐', '李煜', '长相思·一名双红豆、山渐青、忆多娇', '一重山，两重山。山远天高烟水寒，相思枫叶丹。\\n菊花开，菊花残。塞雁高飞人未还，一帘风月闲。\\n云一涡，玉一梭。澹澹衫儿薄薄罗，轻颦双黛螺。\\n秋风多，雨如和。帘外芭蕉三两窠，夜长人奈何。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2683', 'poem', '唐', '白居易', '再因公事到骆口驿', '今年到时夏云白，去年来时秋树红。\\n两度见山心有愧，皆因王事到山中。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2684', 'poem', '唐', '李白', '月下独酌其四', '穷愁千万端。\\n美酒三百杯。\\n愁多酒虽少。\\n酒倾愁不来。\\n所以知酒圣。\\n酒酣心自开。\\n辞粟卧首阳。\\n屡空饥颜回。\\n当代不乐饮。\\n虚名安用哉。\\n蟹螯即金液。\\n糟丘是蓬莱。\\n且须饮美酒。\\n乘月醉高台。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2685', 'poem', '唐', '杜牧', '登池州九峰楼寄张祜', '百感中来不自由，角声孤起夕阳楼。\\n碧山终日思无尽，芳草何年恨即休。\\n睫在眼前长不见，道非身外更何求。\\n谁人得似张公子，千首诗轻万户侯。', '2018-03-09 15:20:09');
INSERT INTO `t_poem` VALUES ('2686', 'poem', '唐', '李白', '赠新平少年', '韩信在淮阴，少年相欺凌。\\n屈体若无骨，壮心有所凭。\\n一遭龙颜君，啸咤从此兴。\\n千金答漂母，万古共嗟称。\\n而我竟何为，寒苦坐相仍。\\n长风入短袂，两手如怀冰。\\n故友不相恤，新交宁见矜。\\n摧残槛中虎，羁绁鞲上鹰。\\n何时腾风云，搏击申所能。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2687', 'poem', '唐', '王勃', '秋江送别二首', '早是他乡值早秋，江亭明月带江流。\\n已觉逝川伤别念，复看津树隐离舟。\\n归舟归骑俨成行，江南江北互相望。\\n谁谓波澜才一水，已觉山川是两乡。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2688', 'poem', '唐', '何希尧', '海棠', '著雨胭脂点点消，半开时节最妖娆。\\n谁家更有黄金屋，深锁东风贮阿娇。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2689', 'poem', '唐', '李商隐', '赋得鸡', '稻粱犹足活诸雏，妒敌专场好自娱。\\n可要五更惊晓梦，不辞风雪为阳乌。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2690', 'poem', '唐', '白居易', '酬哥舒大见赠', '去岁欢游何处去，曲江西岸杏园东。\\n花下忘归因美景，尊前劝酒是春风。\\n各从微宦风尘里，共度流年离别中。\\n今日相逢愁又喜，八人分散两人同。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2691', 'poem', '唐', '章孝标', '日者', '十指中央了五行，说人休咎见前生。\\n我来本乞真消息，却怕呵钱卦欲成。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2692', 'poem', '唐', '柳宗元', '岭南江行', '瘴江南去入云烟，望尽黄茆是海边。\\n山腹雨晴添象迹，潭心日暖长蛟涎。\\n射工巧伺游人影，飓母偏惊旅客船。\\n从此忧来非一事，岂容华发待流年。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2693', 'poem', '唐', '柳宗元', '雨后晓行独至愚溪北池', '宿云散洲渚，晓日明村坞。\\n高树临清池，风惊夜来雨。\\n予心适无事，偶此成宾主。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2694', 'poem', '唐', '王翰', '子夜春歌', '春气满林香，春游不可忘。\\n落花吹欲尽，垂柳折还长。\\n桑女淮南曲，金鞍塞北装。\\n行行小垂手，日暮渭川阳。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2695', 'poem', '唐', '韩偓', '守愚', '深院寥寥竹荫廊，披衣欹枕过年芳。\\n守愚不觉世途险，无事始知春日长。\\n一亩落花围隙地，半竿浓日界空墙。\\n今来自责趋时懒，翻恨松轩书满床。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2696', 'poem', '唐', '李白', '荆州歌', '白帝城边足风波，瞿塘五月谁敢过。\\n荆州麦熟茧成蛾，缲丝忆君头绪多。\\n拨谷飞鸣奈妾何。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2697', 'poem', '唐', '白居易', '赠苏少府', '籍甚二十年，今日方款颜。\\n相送嵩洛下，论心杯酒间。\\n河亚懒出入，府寮多闭关。\\n苍发彼此老，白日寻常闲。\\n朝从携手出，暮思联骑还。\\n何当挈一榼，同宿龙门山。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2698', 'poem', '唐', '常建', '塞下曲四首', '玉帛朝回望帝乡，乌孙归去不称王。\\n天涯静处无征战，兵气销为日月光。\\n北海阴风动地来，明君祠上望龙堆。\\n髑髅皆是长城卒，日暮沙场飞作灰。\\n龙斗雌雄势已分，山崩鬼哭恨将军。\\n黄河直北千馀里，冤气苍茫成黑云。\\n因嫁单于怨在边，蛾眉万古葬胡天。\\n汉家此去三千里，青冢常无草木烟。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2699', 'poem', '唐', '杜甫', '狂歌行，赠四兄', '与兄行年校一岁，贤者是兄愚者弟。兄将富贵等浮云，\\n弟切功名好权势。长安秋雨十日泥，我曹鞴马听晨鸡。\\n公卿朱门未开锁，我曹已到肩相齐。吾兄睡稳方舒膝，\\n不袜不巾蹋晓日。男啼女哭莫我知，身上须缯腹中实。\\n今年思我来嘉州。嘉州酒重花绕楼。楼头吃酒楼下卧，\\n长歌短咏还相酬。四时八节还拘礼，女拜弟妻男拜弟。\\n幅巾鞶带不挂身，头脂足垢何曾洗。吾兄吾兄巢许伦，\\n一生喜怒长任真。日斜枕肘寝已熟，啾啾唧唧为何人。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2700', 'poem', '唐', '李商隐', '东还', '自有仙才自不知，十年长梦采华芝。\\n秋风动地黄云暮，归去嵩阳寻旧师。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2701', 'poem', '唐', '白居易', '鹅赠鹤', '君因风送入青云，我被人驱向鸭群。\\n雪颈霜毛红网掌，请看何处不如君。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2702', 'poem', '唐', '李白', '对雪献从兄虞城宰', '昨夜梁园里，弟寒兄不知。\\n庭前看玉树，肠断忆连枝。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2703', 'poem', '唐', '韦庄', '遣兴', '如幻如泡世，多愁多病身。\\n乱来知酒圣，贫去觉钱神。\\n异国清明节，空江寂寞春。\\n声声林上鸟，唤我北归秦。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2704', 'poem', '唐', '耿湋', '宋中', '日暮黄云合，年深白骨稀。\\n旧村乔木在，秋草远人归。\\n废井莓苔厚，荒田路径微。\\n唯馀近山色，相对似依依。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2705', 'poem', '唐', '杜荀鹤', '春日山居寄友人', '野吟何处最相宜，春景暄和好入诗。\\n高下麦苗新雨后，浅深山色晚晴时。\\n半岩云脚风牵断，平野花枝鸟踏垂。\\n倒载干戈是何日，近来麋鹿欲相随。', '2018-03-09 15:20:14');
INSERT INTO `t_poem` VALUES ('2706', 'poem', '唐', '白居易', '西湖留别', '征途行色惨风烟，祖帐离声咽管弦。\\n翠黛不须留五马，皇恩只许住三年。\\n绿藤阴下铺歌席，红藕花中泊妓船。\\n处处回头尽堪恋，就中难别是湖边。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2707', 'poem', '唐', '秦韬玉', '寄怀', '总藏心剑事儒风，大道如今已浑同。\\n会致名津搜俊彦，是张愁网绊英雄。\\n苏公有国皆悬印，楚将无官可赏功。\\n若使重生太平日，也应回首哭途穷。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2708', 'poem', '唐', '李白', '金陵新亭', '金陵风景好，豪士集新亭。\\n举目山河异，偏伤周顗情。\\n四坐楚囚悲，不忧社稷倾。\\n王公何慷慨，千载仰雄名。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2709', 'poem', '唐', '白居易', '卧小斋', '朝起视事毕，晏坐饱食终。\\n散步长廊下，卧退小斋中。\\n拙政自多暇，幽情谁与同。\\n孰云二千石，心如田野翁。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2710', 'poem', '唐', '白居易', '感鹤', '鹤有不群者，飞飞在野田。\\n饥不啄腐鼠，渴不饮盗泉。\\n贞姿自耿介，杂鸟何翩翾。\\n同游不同志，如此十馀年。\\n一兴嗜欲念，遂为矰缴牵。\\n委质小池内，争食群鸡前。\\n不惟怀稻粱，兼亦竞腥膻。\\n不惟恋主人，兼亦狎乌鸢。\\n物心不可知，天性有时迁。\\n一饱尚如此，况乘大夫轩。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2711', 'poem', '唐', '白居易', '秋江晚泊', '扁舟泊云岛，倚棹念乡国。\\n四望不见人，烟江澹秋色。\\n客心贫易动，日入愁未息。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2712', 'poem', '唐', '郑谷', '池上', '池榭惬幽独，狂吟学解嘲。\\n露荷香自在，风竹冷相敲。\\n丧志嫌孤宦，忘机爱澹交。\\n仙山如有分，必拟访三茅。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2713', 'poem', '唐', '李郢', '鹅儿', '腊后闲行村舍边，黄鹅清水真可怜。\\n何穷散乱随新草，永日淹留在野田。\\n无事群鸣遮水际，争来引颈逼人前。\\n风吹楚泽蒹葭暮，看下寒溪逐去船。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2714', 'poem', '唐', '高适', '蓟中作', '策马自沙漠，长驱登塞垣。\\n边城何萧条，白日黄云昏。\\n一到征战处，每愁胡虏翻。\\n岂无安边书，诸将已承恩。\\n惆怅孙吴事，归来独闭门。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2715', 'poem', '唐', '张友正', '春草凝露', '苍苍芳草色，含露对青春。\\n已赖阳和长，仍惭润泽频。\\n日临残未滴，风度欲成津。\\n蕙叶垂偏重，兰丛洗转新。\\n将行愁裛径，欲采畏濡身。\\n独爱池塘畔，清华远袭人。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2716', 'poem', '唐', '李白', '别内赴征三首', '王命三征去未还，明朝离别出吴关。\\n白玉高楼看不见，相思须上望夫山。\\n出门妻子强牵衣，问我西行几日归。\\n归时倘佩黄金印，莫学苏秦不下机。\\n翡翠为楼金作梯，谁人独宿倚门啼。\\n夜坐寒灯连晓月，行行泪尽楚关西。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2717', 'poem', '唐', '李商隐', '念远', '日月淹秦甸，江湖动越吟。\\n苍桐应露下，白阁自云深。\\n皎皎非鸾扇，翘翘失凤簪。\\n床空鄂君被，杵冷女媭砧。\\n北思惊沙雁，南情属海禽。\\n关山已摇落，天地共登临。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2718', 'poem', '唐', '白居易', '琴', '置琴曲几上，慵坐但含情。\\n何烦故挥弄，风弦自有声。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2719', 'poem', '唐', '李白', '颍阳别元丹丘之淮阳', '吾将元夫子，异姓为天伦。\\n本无轩裳契，素以烟霞亲。\\n尝恨迫世网，铭意俱未伸。\\n松柏虽寒苦，羞逐桃李春。\\n悠悠市朝间，玉颜日缁磷。\\n所失重山岳，所得轻埃尘。\\n精魄渐芜秽，衰老相凭因。\\n我有锦囊诀，可以持君身。\\n当餐黄金药，去为紫阳宾。\\n万事难并立，百年犹崇晨。\\n别尔东南去，悠悠多悲辛。\\n前志庶不易，远途期所遵。\\n已矣归去来，白云飞天津。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2720', 'poem', '唐', '高适', '宋中十首', '梁王昔全盛，宾客复多才。悠悠一千年，陈迹唯高台。\\n寂寞向秋草，悲风千里来。\\n朝临孟诸上，忽见芒砀间。赤帝终已矣，白云长不还。\\n时清更何有，禾黍遍空山。\\n景公德何广，临变莫能欺。三请皆不忍，妖星终自移。\\n君心本如此，天道岂无知。\\n梁苑白日暮，梁山秋草时。君王不可见，修竹令人悲。\\n九月桑叶尽，寒风鸣树枝。\\n登高临旧国，怀古对穷秋。落日鸿雁度，寒城砧杵愁。\\n昔贤不复有，行矣莫淹留。\\n出门望终古，独立悲且歌。忆昔鲁仲尼，凄凄此经过。\\n众人不可向，伐树将如何。\\n逍遥漆园吏，冥没不知年。世事浮云外，闲居大道边。\\n古来同一马，今我亦忘筌。\\n五霸递征伐，宋人无战功。解围幸奇说，易子伤吾衷。\\n唯见卢门外，萧条多转蓬。\\n常爱宓子贱，鸣琴能自亲。邑中静无事，岂不由其身。\\n何意千年后，寂寞无此人。\\n阏伯去已久，高丘临道傍。人皆有兄弟，尔独为参商。\\n终古犹如此。而今安可量。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2721', 'poem', '唐', '韦庄', '春日', '忽觉东风景渐迟，野梅山杏暗芳菲。\\n落星楼上吹残角，偃月营中挂夕晖。\\n旅梦乱随蝴蝶散，离魂渐逐杜鹃飞。\\n红尘遮断长安陌，芳草王孙暮不归。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2722', 'poem', '唐', '李煜', '秋莺', '残莺何事不知秋，横过幽林尚独游。\\n老舌百般倾耳听，深黄一点入烟流。\\n栖迟背世同悲鲁，浏亮如笙碎在缑。\\n莫更留连好归去，露华凄冷蓼花愁。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2723', 'poem', '唐', '鱼玄机', '闺怨', '靡芜盈手泣斜晖，闻道邻家夫婿归。\\n别日南鸿才北去，今朝北雁又南飞。\\n春来秋去相思在，秋去春来信息稀。\\n扃闭朱门人不到，砧声何事透罗帏。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2724', 'poem', '唐', '白居易', '题山石榴花', '一丛千朵压阑干，翦碎红绡却作团。\\n风袅舞腰香不尽，露销妆脸泪新干。\\n蔷薇带刺攀应懒，菡萏生泥玩亦难。\\n争及此花檐户下，任人采弄尽人看。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2725', 'poem', '唐', '金地藏', '送童子下山', '空门寂寞汝思家，礼别云房下九华。\\n爱向竹栏骑竹马，懒于金地聚金沙。\\n添瓶涧底休招月，烹茗瓯中罢弄花。\\n好去不须频下泪，老僧相伴有烟霞。', '2018-03-09 15:20:18');
INSERT INTO `t_poem` VALUES ('2726', 'poem', '唐', '方干', '海石榴', '亭际夭妍日日看，每朝颜色一般般。\\n满枝犹待春风力，数朵先欺腊雪寒。\\n舞蝶似随歌拍转，游人只怕酒杯干。\\n久长年少应难得，忍不丛边到夜观。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2727', 'poem', '唐', '李商隐', '清夜怨', '含泪坐春宵，闻君欲度辽。\\n绿池荷叶嫩，红砌杏花娇。\\n曙月当窗满，征云出塞遥。\\n画楼终日闭，清管为谁调。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2728', 'poem', '唐', '雍陶', '经杜甫旧宅', '浣花溪里花多处，为忆先生在蜀时。\\n万古只应留旧宅，千金无复换新诗。\\n沙崩水槛鸥飞尽，树压村桥马过迟。\\n山月不知人事变，夜来江上与谁期。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2729', 'poem', '唐', '贾岛', '不欺', '上不欺星辰，下不欺鬼神。\\n知心两如此，然后何所陈。\\n食鱼味在鲜，食蓼味在辛。\\n掘井须到流，结交须到头。\\n此语诚不谬，敌君三万秋。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2730', 'poem', '唐', '王维', '少年行', '一身能擘两雕弧，虏骑千重只似无。\\n偏坐金鞍调白羽，纷纷射杀五单于。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2731', 'poem', '唐', '李中', '柳二首', '春来无树不青青，似共东风别有情。\\n闲忆旧居湓水畔，数枝烟雨属啼莺。\\n最爱青青水国中，莫愁门外间花红。\\n纤纤无力胜春色，撼起啼莺恨晚风。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2732', 'poem', '唐', '白居易', '重到城七绝句·张十八', '谏垣几见迁遗补，宪府频闻转殿监。\\n独有咏诗张太祝，十年不改旧官衔。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2733', 'poem', '唐', '来鹄', '金钱花', '也无棱郭也无神，露洗还同铸出新。\\n青帝若教花里用，牡丹应是得钱人。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2734', 'poem', '唐', '林杰', '王仙坛', '羽客已登仙路去，丹炉草木尽凋残。\\n不知千载归何日，空使时人扫旧坛。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2735', 'poem', '唐', '张祜', '琴曲歌辞·司马相如琴歌', '凤兮凤兮非无凰，山重水阔不可量。\\n梧桐结阴在朝阳，濯羽弱水鸣高翔。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2736', 'poem', '唐', '白居易', '冬至夜怀湘灵', '艳质无由见，寒衾不可亲。\\n何堪最长夜，俱作独眠人。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2737', 'poem', '唐', '张籍', '短歌行', '青天荡荡高且虚，上有白日无根株。\\n流光暂出还入地，使我年少不须臾。\\n与君相逢勿寂寞，衰老不复如今乐。\\n玉卮盛酒置君前，再拜愿君千万年。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2738', 'poem', '唐', '白居易', '闺怨词三首', '朝憎莺百啭，夜妒燕双栖。\\n不惯经春别，谁知到晓啼。\\n珠箔笼寒月，纱窗背晓灯。\\n夜来巾上泪，一半是春冰。\\n关山征戍远，闺阁别离难。\\n苦战应憔悴，寒衣不要宽。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2739', 'poem', '唐', '杜牧', '月', '三十六宫秋夜深，昭阳歌断信沈沈。\\n唯应独伴陈皇后，照见长门望幸心。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2740', 'poem', '唐', '张蠙', '登单于台', '边兵春尽回，独上单于台。\\n白日地中出，黄河天外来。\\n沙翻痕似浪，风急响疑雷。\\n欲向阴关度，阴关晓不开。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2741', 'poem', '唐', '杜甫', '秋尽', '秋尽东行且未回，茅斋寄在少城隈。\\n篱边老却陶潜菊，江上徒逢袁绍杯。\\n雪岭独看西日落，剑门犹阻北人来。\\n不辞万里长为客，怀抱何时得好开。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2742', 'poem', '唐', '李景伯', '回波辞', '回波尔时酒卮，微臣职在箴规。\\n侍宴既过三爵，喧哗窃恐非仪。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2743', 'poem', '唐', '白居易', '上巳日恩赐曲江宴会即事', '赐欢仍许醉，此会兴如何。\\n翰苑主恩重，曲江春意多。\\n花低羞艳妓，莺散让清歌。\\n共道升平乐，元和胜永和。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2744', 'poem', '唐', '白居易', '柳絮', '三月尽是头白日，与春老别更依依。\\n凭莺为向杨花道，绊惹春风莫放归。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2745', 'poem', '唐', '李白', '自代内赠', '宝刀截流水，无有断绝时。\\n妾意逐君行，缠绵亦如之。\\n别来门前草，秋巷春转碧。\\n扫尽更还生，萋萋满行迹。\\n鸣凤始相得，雄惊雌各飞。\\n游云落何山？一往不见归。\\n估客发大楼，知君在秋浦。\\n梁苑空锦衾，阳台梦行雨。\\n妾家三作相，失势去西秦。\\n犹有旧歌管，凄清闻四邻。\\n曲度入紫云，啼无眼中人。\\n妾似井底桃，开花向谁笑？\\n君如天上月，不肯一回照。\\n窥镜不自识，别多憔悴深。\\n安得秦吉了，为人道寸心。', '2018-03-09 15:20:24');
INSERT INTO `t_poem` VALUES ('2746', 'poem', '唐', '于武陵', '赠卖松人', '入市虽求利，怜君意独真。\\n劚将寒涧树，卖与翠楼人。\\n瘦叶几经雪，淡花应少春。\\n长安重桃李，徒染六街尘。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2747', 'poem', '唐', '刘长卿', '送薛承矩秩满北游', '匹马向何处，北游殊未还。\\n寒云带飞雪，日暮雁门关。\\n一路傍汾水，数州看晋山。\\n知君喜初服，只爱此身闲。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2748', 'poem', '唐', '白居易', '相和歌辞·王昭君二首', '满面胡沙满鬓风，眉销残黛脸销红。\\n愁苦辛勤憔悴尽，如今却似画图中。\\n汉使却回凭寄语，黄金何日赎蛾眉。\\n君王若问妾颜色，莫道不如宫里时。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2749', 'poem', '唐', '崔涂', '赤壁怀古', '汉室河山鼎势分，勤王谁肯顾元勋。\\n不知征伐由天子，唯许英雄共使君。\\n江上战馀陵是谷，渡头春在草连云。\\n分明胜败无寻处，空听渔歌到夕曛。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2750', 'poem', '唐', '李咸用', '物情', '谁分万类二仪间，禀性高卑各自然。\\n野鹤不栖葱蒨树，流莺长喜艳阳天。\\n李斯溷鼠心应动，庄叟泥龟意已坚。\\n成是败非如赋命，更教何处认愚贤。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2751', 'poem', '唐', '韩愈', '感春五首', '辛夷高花最先开，青天露坐始此回。\\n已呼孺人戛鸣瑟，更遣稚子传清杯。\\n选壮军兴不为用，坐狂朝论无由陪。\\n如今到死得闲处，还有诗赋歌康哉。\\n洛阳东风几时来，川波岸柳春全回。\\n宫门一锁不复启，虽有九陌无尘埃。\\n策马上桥朝日出，楼阙赤白正崔嵬。\\n孤吟屡阕莫与和，寸恨至短谁能裁。\\n春田可耕时已催，王师北讨何当回。\\n放车载草农事济，战马苦饥谁念哉。\\n蔡州纳节旧将死，起居谏议联翩来。\\n朝廷未省有遗策，肯不垂意瓶与罍。\\n前随杜尹拜表回，笑言溢口何欢咍。\\n孔丞别我适临汝，风骨峭峻遗尘埃。\\n音容不接只隔夜，凶讣讵可相寻来。\\n天公高居鬼神恶，欲保性命诚难哉。\\n辛夷花房忽全开，将衰正盛须频来。\\n清晨辉辉烛霞日，薄暮耿耿和烟埃。\\n朝明夕暗已足叹，况乃满地成摧颓。\\n迎繁送谢别有意，谁肯留恋少环回。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2752', 'poem', '唐', '杨巨源', '山中主人', '十里青山有一家，翠屏深处更添霞。\\n若为说得溪中事，锦石和烟四面花。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2753', 'poem', '唐', '李白', '杜陵绝句', '南登杜陵上，北望五陵间。\\n秋水明落日，流光灭远山。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2754', 'poem', '唐', '吴仁璧', '金钱花', '浅绛浓香几朵匀，日熔金铸万家新。\\n堪疑刘宠遗芳在，不许山阴父老贫。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2755', 'poem', '唐', '卢子发', '金钱花', '轮廓休夸四字书，红窠写出对庭除。\\n时时买得佳人笑，本色金钱却不如。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2756', 'poem', '唐', '李煜', '亡后见形诗', '异国非所志，烦劳殊清闲。\\n惊涛千万里，无乃见钟山。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2757', 'poem', '唐', '陈子昂', '赠乔侍郎', '汉廷荣巧宦，云阁薄边功。可怜骢马使，白首为谁雄？', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2758', 'poem', '唐', '白居易', '感月悲逝者', '存亡感月一潸然，月色今宵似往年。\\n何处曾经同望月，樱桃树下后堂前。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2759', 'poem', '唐', '杨玉环', '赠张云容舞', '罗袖动香香不已，红蕖袅袅秋烟里。\\n轻云岭上乍摇风，嫩柳池边初拂水。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2760', 'poem', '唐', '卢肇', '金钱花', '轮郭休夸四字书，红窠写出对庭除。\\n时时买得佳人笑，本色金钱却不如。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2761', 'poem', '唐', '徐夤', '蝴蝶二首', '缥缈青虫脱壳微，不堪烟重雨霏霏。\\n一枝秾艳留教住，几处春风借与飞。\\n防患每忧鸡雀口，怜香偏绕绮罗衣。\\n无情岂解关魂梦，莫信庄周说是非。\\n拂绿穿红丽日长，一生心事住春光。\\n最嫌神女来行雨，爱伴西施去采香。\\n风定只应攒蕊粉，夜寒长是宿花房。\\n鸣蝉性分殊迂阔，空解三秋噪夕阳。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2762', 'poem', '唐', '杜甫', '题桃树', '小径升堂旧不斜，五株桃树亦从遮。\\n高秋总喂贫人实，来岁还舒满眼花。\\n帘户每宜通乳燕，儿童莫信打慈鸦。\\n寡妻群盗非今日，天下车书正一家。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2763', 'poem', '唐', '李咸用', '送人', '一轴烟花满口香，诸侯相见肯相忘。\\n未闻珪璧为人弃，莫倦江山去路长。\\n盈耳暮蝉催别骑，数杯浮蚁咽离肠。\\n眼前多少难甘事，自古男儿当自强。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2764', 'poem', '唐', '陈子昂', '感遇诗三十八首·其六', '吾观龙变化，乃知至阳精。\\n石林何冥密，幽洞无留行。\\n古之得仙道，信与元化并。\\n玄感非象识，谁能测沈冥？\\n世人拘目见，酣酒笑丹经。\\n昆仑有瑶树，安得采其英？', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2765', 'poem', '唐', '李白', '结袜子', '燕南壮士吴门豪，筑中置铅鱼隐刀。\\n感君恩重许君命，太山一掷轻鸿毛。', '2018-03-09 15:20:28');
INSERT INTO `t_poem` VALUES ('2766', 'poem', '唐', '高适', '田家春望', '出门何所见，春色满平芜。\\n可叹无知己，高阳一酒徒。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2767', 'poem', '唐', '司空图', '故乡杏花', '寄花寄酒喜新开，左把花枝右把杯。\\n欲问花枝与杯酒，故人何得不同来。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2768', 'poem', '唐', '李商隐', '忆住一师', '无事经年别远公，帝城钟晓忆西峰。\\n炉烟消尽寒灯晦，童子开门雪满松。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2769', 'poem', '唐', '刘昚虚', '暮秋扬子江寄孟浩然', '木叶纷纷下，东南日烟霜。\\n林山相晚暮，天海空青苍。\\n暝色况复久，秋声亦何长。\\n孤舟兼微月，独夜仍越乡。\\n寒笛对京口，故人在襄阳。\\n咏思劳今夕，江汉遥相望。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2770', 'poem', '唐', '李白', '秋夕旅怀', '凉风度秋海，吹我乡思飞。\\n连山去无际，流水何时归。\\n目极浮云色，心断明月晖。\\n芳草歇柔艳，白露催寒衣。\\n梦长银汉落，觉罢天星稀。\\n含悲想旧国，泣下谁能挥。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2771', 'poem', '唐', '万楚', '五日观妓', '西施谩道浣春纱，碧玉今时斗丽华。\\n眉黛夺将萱草色，红裙妒杀石榴花。\\n新歌一曲令人艳，醉舞双眸敛鬓斜。\\n谁道五丝能续命，却令今日死君家。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2772', 'poem', '唐', '杜甫', '客亭', '秋窗犹曙色，落木更天风。\\n日出寒山外，江流宿雾中。\\n圣朝无弃物，老病已成翁。\\n多少残生事，飘零任转蓬。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2773', 'poem', '唐', '岑参', '过碛', '黄沙碛里客行迷，四望云天直下低。\\n为言地尽天还尽，行到安西更向西。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2774', 'poem', '唐', '李商隐', '南朝', '地险悠悠天险长，金陵王气应瑶光。\\n休夸此地分天下，只得徐妃半面妆。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2775', 'poem', '唐', '孟郊', '离思', '不寐亦不语，片月秋稍举。\\n孤鸿忆霜群，独鹤叫云侣。\\n怨彼浮花心，飘飘无定所。\\n高张系繂帆，远过梅根渚。\\n回织别离字，机声有酸楚。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2776', 'poem', '唐', '王绩', '赠李征君大寿', '孔淳辞散骑，陆昶谢中郎。\\n幅巾朝帝罢，杖策去官忙。\\n附车还赵郡，乘船向武昌。\\n九徵书未已，十辟誉弥彰。\\n副君迎绮季，天子送严光。\\n灞陵幽径近，磻溪隐路长。\\n编蓬还作室，绩草更为裳。\\n会稽置樵处，兰陵卖药行。\\n看书惟道德，开教止农桑。\\n别有幽怀侣，由来高让王。\\n前年辞厚币，今岁返寒乡。\\n有书横石架，无毡坐土床。\\n兰英犹足酿，竹实本无粮。\\n涧松寒转直，山菊秋自香。\\n管宁存祭礼，王霸重朝章。\\n去去相随去，披裘骄盛唐。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2777', 'poem', '唐', '李白', '当涂赵炎少府粉图山水歌', '峨眉高出西极天，罗浮直与南溟连。\\n名公绎思挥彩笔，驱山走海置眼前。\\n满堂空翠如可扫，赤城霞气苍梧烟。\\n洞庭潇湘意渺绵，三江七泽情洄沿。\\n惊涛汹涌向何处，孤舟一去迷归年。\\n征帆不动亦不旋，飘如随风落天边。\\n心摇目断兴难尽，几时可到三山巅。\\n西峰峥嵘喷流泉，横石蹙水波潺湲。\\n东崖合沓蔽轻雾，深林杂树空芊绵。\\n此中冥昧失昼夜，隐几寂听无鸣蝉。\\n长松之下列羽客，对坐不语南昌仙。\\n南昌仙人赵夫子，妙年历落青云士。\\n讼庭无事罗众宾，杳然如在丹青里。\\n五色粉图安足珍，真仙可以全吾身。\\n若待功成拂衣去，武陵桃花笑杀人。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2778', 'poem', '唐', '孟贯', '秋江送客', '秋风楚江上，送子话游遨。\\n远水宿何处，孤舟春夜涛。\\n浦云沈雁影，山月照猿嗥。\\n莫为饥寒苦，便成名利劳。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2779', 'poem', '唐', '韦应物', '游开元精舍', '夏衣始轻体，游步爱僧居。\\n果园新雨后，香台照日初。\\n绿阴生昼静，孤花表春馀。\\n符竹方为累，形迹一来疏。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2780', 'poem', '唐', '杜甫', '春水生二绝', '二月六夜春水生，门前小滩浑欲平。\\n鸬鹚鸂鶒莫漫喜，吾与汝曹俱眼明。\\n一夜水高二尺强，数日不可更禁当。\\n南市津头有船卖，无钱即买系篱旁。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2781', 'poem', '唐', '白居易', '杂曲歌辞·浪淘沙', '一泊沙来一泊去，一重浪灭一重生。\\n相搅相淘无歇日，会交山海一时平。\\n白浪茫茫与海连，平沙浩浩四无边。\\n暮去朝来淘不住，遂令东海变桑田。\\n青草湖中万里程，黄梅雨里一人行。\\n愁见滩头夜泊处，风翻暗浪打船声。\\n借问江湖与海水，何似君情与妾心。\\n相恨不如潮有信，相思始觉海非深。\\n海底飞尘终有日，山头化石岂无时。\\n谁道小郎抛小妇，船头一去没回期。\\n随波逐浪到天涯，迁客生还有几家。\\n却到帝乡重富贵，请君莫忘浪淘沙。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2782', 'poem', '唐', '李端', '闺情', '月落星稀天欲明，孤灯未灭梦难成。\\n披衣更向门前望，不忿朝来鹊喜声。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2783', 'poem', '唐', '刘商', '醉后', '春草秋风老此身，一瓢长醉任家贫。\\n醒来还爱浮萍草，漂寄官河不属人。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2784', 'poem', '唐', '韦应物', '咏玉', '乾坤有精物，至宝无文章。\\n雕琢为世器，真性一朝伤。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2785', 'poem', '唐', '刘商', '不羡花', '惆怅朝阳午又斜，剩栽桃李学仙家。\\n花开花落人如旧，谁道容颜不及花。', '2018-03-09 15:20:41');
INSERT INTO `t_poem` VALUES ('2786', 'poem', '唐', '李频', '春日思归', '春情不断若连环，一夕思归鬓欲斑。\\n壮志未酬三尺剑，故乡空隔万重山。\\n音书断绝干戈后，亲友相逢梦寐间。\\n却羡浮云与飞鸟，因风吹去又吹还。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2787', 'poem', '唐', '白居易', '禁中秋宿', '风翻朱里幕，雨冷通中枕。\\n耿耿背斜灯，秋床一人寝。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2788', 'poem', '唐', '陈子昂', '岘山怀古', '秣马临荒甸，登高览旧都。\\n犹悲堕泪碣，尚想卧龙图。\\n城邑遥分楚，山川半入吴。\\n丘陵徒自出，贤圣几凋枯！\\n野树苍烟断，津楼晚气孤。\\n谁知万里客，怀古正踌蹰。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2789', 'poem', '唐', '韩愈', '答张十一功曹', '山净江空水见沙，哀猿啼处两三家。\\n筼筜竞长纤纤笋，踯躅闲开艳艳花。\\n未报恩波知死所，莫令炎瘴送生涯。\\n吟君诗罢看双鬓，斗觉霜毛一半加。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2790', 'poem', '唐', '刘禹锡', '华山歌', '洪垆作高山，元气鼓其橐。\\n俄然神功就，峻拔在寥廓。\\n灵迹露指爪，杀气见棱角。\\n凡木不敢生，神仙聿来托。\\n天资帝王宅，以我为关钥。\\n能令下国人，一见换神骨。\\n高山固无限，如此方为岳。\\n丈夫无特达，虽贵犹碌碌。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2791', 'poem', '唐', '刘长卿', '晚泊湘江怀故人', '天涯片云去，遥指帝乡忆。\\n惆怅增暮情，潇湘复秋色。\\n扁舟宿何处，落日羡归翼。\\n万里无故人，江鸥不相识。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2792', 'poem', '唐', '杜牧', '赠猎骑', '已落双雕血尚新，鸣鞭走马又翻身。\\n凭君莫射南来雁，恐有家书寄远人。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2793', 'poem', '唐', '杜甫', '九日登梓州城', '伊昔黄花酒，如今白发翁。\\n追欢筋力异，望远岁时同。\\n弟妹悲歌里，朝廷醉眼中。\\n兵戈与关塞，此日意无穷。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2794', 'poem', '唐', '王梵志', '诗二首其一', '我有一方便，价值百匹练。\\n相打长伏弱，至死不入县。\\n他人骑大马，我独跨驴子。\\n回顾担柴汉，心下较些子。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2795', 'poem', '唐', '白居易', '逍遥咏', '亦莫恋此身，亦莫厌此身。\\n此身何足恋，万劫烦恼根。\\n此身何足厌，一聚虚空尘。\\n无恋亦无厌，始是逍遥人。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2796', 'poem', '唐', '李端', '题崔端公园林', '上士爱清辉，开门向翠微。\\n抱琴看鹤去，枕石待云归。\\n野坐苔生席，高眠竹挂衣。\\n旧山东望远，惆怅暮花飞。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2797', 'poem', '唐', '杜荀鹤', '秋夕', '世间多少能诗客，谁是无愁得睡人。\\n自我夜来霜月下，到头吟魄始终身。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2798', 'poem', '唐', '齐己', '病中勉送小师往清凉山礼大圣', '丰衣足食处莫住，圣迹灵踪好遍寻。\\n忽遇文殊开慧眼，他年应记老师心。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2799', 'poem', '唐', '王维', '皇甫岳云溪杂题五首·萍池', '春池深且广，会待轻舟回。\\n靡靡绿萍合，垂杨扫复开。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2800', 'poem', '唐', '杜甫', '宿昔', '宿昔青门里，蓬莱仗数移。\\n花娇迎杂树，龙喜出平池。\\n落日留王母，微风倚少儿。\\n宫中行乐秘，少有外人知。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2801', 'poem', '唐', '刘长卿', '早春', '微雨夜来歇，江南春色回。\\n本惊时不住，还恐老相催。\\n人好千场醉，花无百日开。\\n岂堪沧海畔，为客十年来。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2802', 'poem', '唐', '李商隐', '乱石', '虎踞龙蹲纵复横，星光渐减雨痕生。\\n不须并碍东西路，哭杀厨头阮步兵。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2803', 'poem', '唐', '李益', '上汝州郡楼', '黄昏鼓角似边州，三十年前上此楼。\\n今日山城对垂泪，伤心不独为悲秋。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2804', 'poem', '唐', '杜甫', '又于韦处乞大邑瓷碗', '大邑烧瓷轻且坚，扣如哀玉锦城传。\\n君家白碗胜霜雪，急送茅斋也可怜。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2805', 'poem', '唐', '佚名', '郊庙歌辞·晋朝飨乐章·举酒', '大明御宇，至德动天。\\n君臣庆会，礼乐昭宣。\\n剑佩成列，金石在悬。\\n椒觞再献，宝历万年。', '2018-03-09 15:20:53');
INSERT INTO `t_poem` VALUES ('2806', 'poem', '唐', '子兰', '短歌行', '日月何忙忙，出没住不得。\\n使我勇壮心，少年如顷刻。\\n人生石火光，通时少于塞。\\n四季倏往来，寒暑变为贼。\\n偷人面上花，夺人头上黑。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2807', 'poem', '唐', '李商隐', '银河吹笙', '怅望银河吹玉笙，楼寒院冷接平明。\\n重衾幽梦他年断，别树羁雌昨夜惊。\\n月榭故香因雨发，风帘残烛隔霜清。\\n不须浪作缑山意，湘瑟秦箫自有情。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2808', 'poem', '唐', '杜甫', '叹庭前甘菊花', '檐前甘菊移时晚，青蕊重阳不堪摘。\\n明日萧条醉尽醒，残花烂熳开何益。\\n篱边野外多众芳，采撷细琐升中堂。\\n念兹空长大枝叶，结根失所缠风霜。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2809', 'poem', '唐', '钱起', '蓝田溪杂咏二十二首·远山钟', '风送出山钟，云霞度水浅。\\n欲知声尽处，鸟灭寥天远。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2810', 'poem', '唐', '陈玉兰', '寄夫', '夫戍边关妾在吴，西风吹妾妾忧夫。\\n一行书信千行泪，寒到君边衣到无。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2811', 'poem', '唐', '白居易', '魏王堤', '花寒懒发鸟慵啼，信马闲行到日西。\\n何处未春先有思，柳条无力魏王堤。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2812', 'poem', '唐', '高骈', '送春·水浅鱼争跃', '水浅鱼争跃，花深鸟竞啼。\\n春光看欲尽，判却醉如泥。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2813', 'poem', '唐', '齐己', '观荷叶露珠', '霏微晓露成珠颗，宛转田田未有风。\\n任器方圆性终在，不妨翻覆落池中。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2814', 'poem', '唐', '崔道融', '江夕', '江心秋月白，起柁信潮行。\\n蛟龙化为人，半夜吹笛声。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2815', 'poem', '唐', '王昌龄', '长歌行', '旷野饶悲风，飕飕黄蒿草。\\n系马倚白杨，谁知我怀抱。\\n所是同袍者，相逢尽衰老。\\n北登汉家陵，南望长安道。\\n下有枯树根，上有鼯鼠窠。\\n高皇子孙尽，千载无人过。\\n宝玉频发掘，精灵其奈何。\\n人生须达命，有酒且长歌。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2816', 'poem', '唐', '杜牧', '宣州送裴坦判官往舒州，时牧欲赴官归京', '日暖泥融雪半消，行人芳草马声骄。\\n九华山路云遮寺，清弋江村柳拂桥。\\n君意如鸿高的的，我心悬旆正摇摇。\\n同来不得同归去，故国逢春一寂寥。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2817', 'poem', '唐', '赵嘏', '齐安早秋', '流年堪惜又堪惊，砧杵风来满郡城。\\n高鸟过时秋色动，征帆落处暮云平。\\n思家正叹江南景，听角仍含塞北情。\\n此日沾襟念岐路，不知何处是前程。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2818', 'poem', '唐', '韩愈', '题张十一旅舍三咏·蒲萄', '新茎未遍半犹枯，高架支离倒复扶。\\n若欲满盘堆马乳，莫辞添竹引龙须。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2819', 'poem', '唐', '杜甫', '绝句九首（前六首已见第十三卷中）', '闻道巴山里，春船正好行。\\n都将百年兴，一望九江城。\\n水槛温江口，茅堂石笋西。\\n移船先主庙，洗药浣沙溪。\\n设道春来好，狂风大放颠。\\n吹花随水去，翻却钓鱼船。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2820', 'poem', '唐', '李贺', '三月过行宫', '渠水红繁拥御墙，风娇小叶学娥妆。\\n垂帘几度青春老，堪锁千年白日长。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2821', 'poem', '唐', '唐彦谦', '夜蝉', '翠竹高梧夹后溪，劲风危露雨凄凄。\\n那知北牖残灯暗，又送西楼片月低。\\n清夜更长应未已，远烟寻断莫频嘶。\\n羁人此夕如三岁，不整寒衾待曙鸡。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2822', 'poem', '唐', '白居易', '凉夜有怀', '念别感时节，早蛩闻一声。\\n风帘夜凉入，露簟秋意生。\\n灯尽梦初罢，月斜天未明。\\n暗凝无限思，起傍药阑行。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2823', 'poem', '唐', '王昌龄', '春怨', '音书杜绝白狼西，桃李无颜黄鸟啼。\\n寒雁春深归去尽，出门肠断草萋萋。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2824', 'poem', '唐', '灵一', '与元居士青山潭饮茶', '野泉烟火白云间，坐饮香茶爱此山。\\n岩下维舟不忍去，青溪流水暮潺潺。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2825', 'poem', '唐', '李山甫', '早秋山中作', '谁到山中语，雨馀风气秋。\\n烟岚出涧底，瀑布落床头。\\n至道亦非远，僻诗须苦求。\\n千峰有嘉景，拄杖独巡游。', '2018-03-09 15:20:59');
INSERT INTO `t_poem` VALUES ('2826', 'poem', '唐', '郑谷', '入阁', '秘殿临轩日，和銮返正年。\\n两班文武盛，百辟羽仪全。\\n霜漏清中禁，风旗拂曙天。\\n门严新勘契，仗入乍承宣。\\n玉几当红旭，金炉纵碧烟。\\n对扬称法吏，赞引出宫钿。\\n言动挥毫疾，雍容执簿专。\\n寿山晴叆叇，颢气暖连延。\\n礼有鸳鸾集，恩无雨露偏。\\n小臣叨备位，歌咏泰阶前。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2827', 'poem', '唐', '刘威', '早春', '晓来庭户外，草树似依依。\\n一夜东风起，万山春色归。\\n冰消泉派动，日暖露珠晞。\\n已酝看花酒，娇莺莫预飞。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2828', 'poem', '唐', '李山甫', '早秋山中作', '荣枯无路入千峰，肥遁谁谐此志同。\\n司寇亦曾遭鲁黜，步兵何事哭途穷。\\n桧松瘦健滴秋露，户牖虚明生晚风。\\n山思更清人影绝，陇云飞入草堂中。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2829', 'poem', '唐', '李嘉祐', '寄王舍人竹楼', '傲吏身闲笑五侯，西江取竹起高楼。\\n南风不用蒲葵扇，纱帽闲眠对水鸥。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2830', 'poem', '唐', '骆宾王', '从军行', '平生一顾重，意气溢三军。\\n野日分戈影，天星合剑文。\\n弓弦抱汉月，马足践胡尘。\\n不求生入塞，唯当死报君。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2831', 'poem', '唐', '齐己', '桃花', '千株含露态，何处照人红。\\n风暖仙源里，春和水国中。\\n流莺应见落，舞蝶未知空。\\n拟欲求图画，枝枝带竹丛。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2832', 'poem', '唐', '陈子昂', '登蓟城西北楼送崔著作融入都', '蓟楼望燕国，负剑喜兹登。\\n清规子方奏，单戟我无能。\\n仲冬边风急，云汉复霜棱。\\n慷慨竟何道，西南恨失朋。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2833', 'poem', '唐', '常建', '春词二首', '菀菀黄柳丝，濛濛杂花垂。\\n日高红妆卧，倚对春光迟。\\n宁知傍淇水，騕褭黄金羁。\\n翳翳陌上桑，南枝交北堂。\\n美人金梯出，素手自提筐。\\n非但畏蚕饥，盈盈娇路傍。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2834', 'poem', '唐', '孟浩然', '自洛之越', '皇皇三十载，书剑两无成。\\n山水寻吴越，风尘厌洛京。\\n扁舟泛湖海，长揖谢公卿。\\n且乐杯中物，谁论世上名。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2835', 'poem', '唐', '陈后主', '戏赠陈应', '留人不留人，不留人也去。\\n此处不留人，自有留人处。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2836', 'poem', '唐', '张祜', '相和歌辞·雁门太守行', '城头月没霜如水，趚趚蹋沙人似鬼。灯前拭泪试香裘，\\n长引一声残漏子。驼囊泻酒酒一杯，前头啑血心不回。\\n寄语年少妻莫哀，鱼金虎竹天上来，雁门山边骨成灰。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2837', 'poem', '唐', '刘希夷', '将军行', '将军辟辕门，耿介当风立。\\n诸将欲言事，逡巡不敢入。\\n剑气射云天，鼓声振原隰。\\n黄尘塞路起，走马追兵急。\\n弯弓从此去，飞箭如雨集。\\n截围一百里，斩首五千级。\\n代马流血死，胡人抱鞍泣。\\n古来养甲兵，有事常讨袭。\\n乘我庙堂运，坐使干戈戢。\\n献凯归京师，军容何翕习。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2838', 'poem', '唐', '白居易', '棣华驿见杨八题梦兄弟诗', '遥闻旅宿梦兄弟，应为邮亭名棣华。\\n名作棣华来早晚，自题诗后属杨家。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2839', 'poem', '唐', '耿湋', '宋中', '百战无军食，孤城陷虏尘。\\n为伤多易子，翻吊浅为臣。\\n漫漫东流水，悠悠南陌人。\\n空思前事往，向晓泪沾巾。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2840', 'poem', '唐', '张九龄', '湘中作', '湘流绕南岳，绝目转青青。\\n怀禄未能已，瞻途屡所经。\\n烟屿宜春望，林猿莫夜听。\\n永路日多绪，孤舟天复冥。\\n浮没从此去，嗟嗟劳我形。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2841', 'poem', '唐', '怀素', '题张僧繇醉僧图', '人人送酒不曾沽，终日松间挂一壶。\\n草圣欲成狂便发，真堪画入醉僧图。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2842', 'poem', '唐', '元稹', '酬乐天劝醉', '神曲清浊酒，牡丹深浅花。\\n少年欲相饮，此乐何可涯。\\n沉机造神境，不必悟楞伽。\\n酡颜返童貌，安用成丹砂。\\n刘伶称酒德，所称良未多。\\n愿君听此曲，我为尽称嗟。\\n一杯颜色好，十盏胆气加。\\n半酣得自恣，酩酊归太和。\\n共醉真可乐，飞觥撩乱歌。\\n独醉亦有趣，兀然无与他。\\n美人醉灯下，左右流横波。\\n王孙醉床上，颠倒眠绮罗。\\n君今劝我醉，劝醉意如何。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2843', 'poem', '唐', '李白', '在水军宴赠幕府诸侍御', '月化五白龙，翻飞凌九天。\\n胡沙惊北海，电扫洛阳川。\\n虏箭雨宫阙，皇舆成播迁。\\n英王受庙略，秉钺清南边。\\n云旗卷海雪，金戟罗江烟。\\n聚散百万人，弛张在一贤。\\n霜台降群彦，水国奉戎旃。\\n绣服开宴语，天人借楼船。\\n如登黄金台，遥谒紫霞仙。\\n卷身编蓬下，冥机四十年。\\n宁知草间人，腰下有龙泉。\\n浮云在一决，誓欲清幽燕。\\n愿与四座公，静谈金匮篇。\\n齐心戴朝恩，不惜微躯捐。\\n所冀旄头灭，功成追鲁连。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2844', 'poem', '唐', '刘希夷', '公子行', '天津桥下阳春水，天津桥上繁华子。\\n马声回合青云外，人影动摇绿波里。\\n绿波荡漾玉为砂，青云离披锦作霞。\\n可怜杨柳伤心树，可怜桃李断肠花。\\n此日遨游邀美女，此时歌舞入娼家。\\n娼家美女郁金香，飞来飞去公子傍。\\n的的珠帘白日映，娥娥玉颜红粉妆。\\n花际裴回双蛱蝶，池边顾步两鸳鸯。\\n倾国倾城汉武帝，为云为雨楚襄王。\\n古来容光人所羡，况复今日遥相见。\\n愿作轻罗著细腰，愿为明镜分娇面。\\n与君相向转相亲，与君双栖共一身。\\n愿作贞松千岁古，谁论芳槿一朝新。\\n百年同谢西山日，千秋万古北邙尘。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2845', 'poem', '唐', '白居易', '赠江客', '江柳影寒新雨地，塞鸿声急欲霜天。\\n愁君独向沙头宿，水绕芦花月满船。', '2018-03-09 15:21:11');
INSERT INTO `t_poem` VALUES ('2846', 'poem', '唐', '于季子', '咏项羽', '北伐虽全赵，东归不王秦。\\n空歌拔山力，羞作渡江人。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2847', 'poem', '唐', '白居易', '清夜琴兴', '月出鸟栖尽，寂然坐空林。\\n是时心境闲，可以弹素琴。\\n清泠由木性，恬澹随人心。\\n心积和平气，木应正始音。\\n响馀群动息，曲罢秋夜深。\\n正声感元化，天地清沉沉。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2848', 'poem', '唐', '刘禹锡', '庭竹', '露涤铅粉节，风摇青玉枝。\\n依依似君子，无地不相宜。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2849', 'poem', '唐', '李治', '谒大慈恩寺', '日宫开万仞，月殿耸千寻。\\n花盖飞团影，幡虹曳曲阴。\\n绮霞遥笼帐，丛珠细网林。\\n寥廓烟云表，超然物外心。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2850', 'poem', '唐', '李益', '同崔邠登鹳雀楼', '鹳雀楼西百尺樯，汀洲云树共茫茫。汉家萧鼓空流水，\\n魏国山河半夕阳。事去千年犹恨速。愁来一日即为长。\\n风烟并起思归望，远目非春亦自伤。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2851', 'poem', '唐', '王维', '送秘书晁监还日本国', '积水不可极，安知沧海东。\\n九州何处远，万里若乘空。\\n向国唯看日，归帆但信风。\\n鳌身映天黑，鱼眼射波红。\\n乡树扶桑外，主人孤岛中。\\n别离方异域，音信若为通。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2852', 'poem', '唐', '柳宗元', '种柳戏题', '柳州柳刺史，种柳柳江边。\\n谈笑为故事，推移成昔年。\\n垂阴当覆地，耸干会参天。\\n好作思人树，惭无惠化传。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2853', 'poem', '唐', '岑参', '碛西头送李判官入京', '一身从远使，万里向安西。\\n汉月垂乡泪，胡沙费马蹄。\\n寻河愁地尽，过碛觉天低。\\n送子军中饮，家书醉里题。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2854', 'poem', '唐', '张籍', '将军行', '弹筝峡东有胡尘，天子择日拜将军。\\n蓬莱殿前赐六纛，还领禁兵为部曲。\\n当朝受诏不辞家，夜向咸阳原上宿。\\n战车彭彭旌旗动，三十六军齐上陇。\\n陇头战胜夜亦行，分兵处处收旧城。\\n胡儿杀尽阴碛暮，扰扰唯有牛羊声。\\n边人亲戚曾战没，今逐官军收旧骨。\\n碛西行见万里空，幕府独奏将军功。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2855', 'poem', '唐', '韩愈', '醉后', '煌煌东方星，奈此众客醉。\\n初喧或忿争，中静杂嘲戏。\\n淋漓身上衣，颠倒笔下字。\\n人生如此少，酒贱且勤置。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2856', 'poem', '唐', '李白', '赠何七判官昌浩', '有时忽惆怅，匡坐至夜分。\\n平明空啸咤，思欲解世纷。\\n心随长风去，吹散万里云。\\n羞作济南生，九十诵古文。\\n不然拂剑起，沙漠收奇勋。\\n老死阡陌间，何因扬清芬。\\n夫子今管乐，英才冠三军。\\n终与同出处，岂将沮溺群。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2857', 'poem', '唐', '李白', '上之回', '三十六离宫，楼台与天通。\\n阁道步行月，美人愁烟空。\\n恩疏宠不及，桃李伤春风。\\n淫乐意何极，金舆向回中。\\n万乘出黄道，千旗扬彩虹。\\n前军细柳北，后骑甘泉东。\\n岂问渭川老，宁邀襄野童。\\n但慕瑶池宴，归来乐未穷。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2858', 'poem', '唐', '吴融', '溪边', '溪边花满枝，百鸟带香飞。\\n下有一白鹭，日斜翘石矶。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2859', 'poem', '唐', '权德舆', '醉后', '美禄与贤人，相逢自可亲。\\n愿将花柳月，尽赏醉乡春。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2860', 'poem', '唐', '令狐楚', '少年行四首', '少小边州惯放狂，骣骑蕃马射黄羊。\\n如今年老无筋力，犹倚营门数雁行。\\n家本清河住五城，须凭弓箭得功名。\\n等闲飞鞚秋原上，独向寒云试射声。\\n弓背霞明剑照霜，秋风走马出咸阳。\\n未收天子河湟地，不拟回头望故乡。\\n霜满中庭月满楼，金樽玉柱对清秋。\\n当年称意须行乐，不到天明不肯休。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2861', 'poem', '唐', '司空图', '武陵路', '橘岸舟间罾网挂，茶坡日暖鹧鸪啼。\\n女郎指点行人笑，知向花间路已迷。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2862', 'poem', '唐', '杜荀鹤', '离家', '丈夫三十身如此，疲马离乡懒著鞭。\\n槐柳路长愁杀我，一枝蝉到一枝蝉。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2863', 'poem', '唐', '李世民', '出猎', '楚王云梦泽，汉帝长杨宫。\\n岂若因农暇，阅武出轘嵩。\\n三驱陈锐卒，七萃列材雄。\\n寒野霜氛白，平原烧火红。\\n雕戈夏服箭，羽骑绿沉弓。\\n怖兽潜幽壑，惊禽散翠空。\\n长烟晦落景，灌木振严风。\\n所为除民瘼，非是悦林丛。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2864', 'poem', '唐', '白居易', '秋思', '病眠夜少梦，闲立秋多思。\\n寂寞馀雨晴，萧条早寒至。\\n鸟栖红叶树，月照青苔地。\\n何况镜中年，又过三十二。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2865', 'poem', '唐', '郑畋', '麦穗两岐', '圣虑忧千亩，嘉苗荐两岐。\\n如云方表盛，成穗忽标奇。\\n瑞露纵横滴，祥风左右吹。\\n讴歌连上苑，化日遍平陂。\\n史册书堪重，丹青画更宜。\\n愿依连理树，俱作万年枝。', '2018-03-09 15:21:22');
INSERT INTO `t_poem` VALUES ('2866', 'poem', '唐', '白居易', '庾楼新岁', '岁时销旅貌，风景触乡愁。\\n牢落江湖意，新年上庾楼。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2867', 'poem', '唐', '李商隐', '日日', '日日春光斗日光，山城斜路杏花香。\\n几时心绪浑无事，得及游丝百尺长。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2868', 'poem', '唐', '高骈', '太公庙', '青山长在境长新，寂寞持竿一水滨。\\n及得王师身已老，不知辛苦为何人。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2869', 'poem', '唐', '张继', '山家', '板桥人渡泉声，茅檐日午鸡鸣。\\n莫嗔焙茶烟暗，却喜晒谷天晴。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2870', 'poem', '唐', '李中', '梅花', '群木方憎雪，开花长在先。\\n流莺与舞蝶，不见许因缘。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2871', 'poem', '唐', '许浑', '鸂鶒', '池寒柳复凋，独宿夜迢迢。\\n雨顶冠应冷，风毛剑欲飘。\\n故巢迷碧水，旧侣越丹霄。\\n不是无归处，心高多寂寥。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2872', 'poem', '唐', '杜牧', '村舍燕', '汉宫一百四十五，多下珠帘闭琐窗。\\n何处营巢夏将半，茅檐烟里语双双。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2873', 'poem', '唐', '无名氏', '杂诗（第八首一作白居易诗）', '劝君莫惜金缕衣，劝君惜取少年时。\\n有花堪折直须折，莫待无花空折枝。\\n青天无云月如烛，露泣梨花白如玉。\\n子规一夜啼到明，美人独在空房宿。\\n空赐罗衣不赐恩，一薰香后一销魂。\\n虽然舞袖何曾舞，常对春风裛泪痕。\\n不洗残妆凭绣床，也同女伴绣鸳鸯。\\n回针刺到双飞处，忆著征夫泪数行。\\n眼想心思梦里惊，无人知我此时情。\\n不如池上鸳鸯鸟，双宿双飞过一生。\\n一去辽阳系梦魂，忽传征骑到中门。\\n纱窗不肯施红粉，徒遣萧郎问泪痕。\\n莺啼露冷酒初醒，罨画楼西晓角鸣。\\n翠羽帐中人梦觉，宝钗斜坠枕函声。\\n行人南北分征路，流水东西接御沟。\\n终日坡前怨离别，谩名长乐是长愁。\\n偏倚绣床愁不起，双垂玉箸翠鬟低。\\n卷帘相待无消息，夜合花前日又西。\\n悔将泪眼向东开，特地愁从望里来。\\n三十六峰犹不见，况伊如燕这身材。\\n满目笙歌一段空，万般离恨总随风。\\n多情为谢残阳意，与展晴霞片片红。\\n两心不语暗知情，灯下裁缝月下行。\\n行到阶前知未睡，夜深闻放剪刀声。\\n近寒食雨草萋萋，著麦苗风柳映堤。\\n早是有家归未得，杜鹃休向耳边啼。\\n水纹珍簟思悠悠，千里佳期一夕休。\\n从此无心爱良夜，任他明月下西楼。\\n数日相随两不忘，郎心如妾妾如郎。\\n出门便是东西路，把取红笺各断肠。\\n无定河边暮角声，赫连台畔旅人情。\\n函关归路千馀里，一夕秋风白发生。\\n花落长川草色青，暮山重叠两冥冥。\\n逢春便觉飘蓬苦，今日分飞一涕零。\\n洛阳才子邻箫恨，湘水佳人锦瑟愁。\\n今昔两成惆怅事，临邛春尽暮江流。\\n浙江轻浪去悠悠，望海楼吹望海愁。\\n莫怪乡心随魄断，十年为客在他州。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2874', 'poem', '唐', '李商隐', '韩翃舍人即事', '萱草含丹粉，荷花抱绿房。\\n鸟应悲蜀帝，蝉是怨齐王。\\n通内藏珠府，应官解玉坊。\\n桥南荀令过，十里送衣香。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2875', 'poem', '唐', '羊士谔', '野望二首', '萋萋麦陇杏花风，好是行春野望中。\\n日暮不辞停五马，鸳鸯飞去绿江空。\\n忘怀不使海鸥疑，水映桃花酒满卮。\\n亭上一声歌白苎，野人归棹亦行迟。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2876', 'poem', '唐', '李忱', '百丈山', '大雄真迹枕危峦，梵宇层楼耸万般。\\n日月每从肩上过，山河长在掌中看。\\n仙峰不间三春秀，灵境何时六月寒。\\n更有上方人罕到，暮钟朝磬碧云端。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2877', 'poem', '唐', '董思恭', '咏风', '萧萧度阊阖，习习下庭闱。\\n花蝶自飘舞，兰蕙生光辉。\\n相乌正举翼，退鹢已惊飞。\\n方从列子御，更逐浮云归。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2878', 'poem', '唐', '刘禹锡', '晚泊牛渚', '芦苇晚风起，秋江鳞甲生。\\n残霞忽变色，游雁有馀声。\\n戍鼓音响绝，渔家灯火明。\\n无人能咏史，独自月中行。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2879', 'poem', '唐', '薛稷', '秋日还京陕西十里作', '驱车越陕郊，北顾临大河。\\n隔河望乡邑，秋风水增波。\\n西登咸阳途，日暮忧思多。\\n傅岩既纡郁，首山亦嵯峨。\\n操筑无昔老，采薇有遗歌。\\n客游节回换，人生知几何。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2880', 'poem', '唐', '张祜', '峰顶寺', '月明如水山头寺，仰面看天石上行。\\n夜半深廊人语定，一枝松动鹤来声。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2881', 'poem', '唐', '杜光庭', '题福唐观二首', '盘空蹑翠到山巅，竹殿云楼势逼天。\\n古洞草深微有路，旧碑文灭不知年。\\n八州物象通檐外，万里烟霞在目前。\\n自是人间轻举地，何须蓬岛访真仙。\\n曾随云水此山游，行尽层峰更上楼。\\n九月登临须有意，七年岐路亦堪愁。\\n树红树碧高低影，烟淡烟浓远近秋。\\n暂熟炉香不须去，伫陪天仗入神州。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2882', 'poem', '唐', '白居易', '宿竹阁', '晚坐松檐下，宵眠竹阁间。\\n清虚当服药，幽独抵归山。\\n巧未能胜拙，忙应不及闲。\\n无劳别修道，即此是玄关。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2883', 'poem', '唐', '李群玉', '鸂鶒', '锦羽相呼暮沙曲，波上双声戛哀玉。\\n霞明川静极望中，一时飞灭青山绿。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2884', 'poem', '唐', '杜甫', '漫成一绝', '江月去人只数尺，风灯照夜欲三更。\\n沙头宿鹭联拳静，船尾跳鱼拨剌鸣。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2885', 'poem', '唐', '子兰', '千叶石榴花', '一朵花开千叶红，开时又不藉春风。\\n若教移在香闺畔，定与佳人艳态同。', '2018-03-09 15:21:34');
INSERT INTO `t_poem` VALUES ('2886', 'poem', '唐', '雍陶', '题情尽桥', '从来只有情难尽，何事名为情尽桥。\\n自此改名为折柳，任他离恨一条条。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2887', 'poem', '唐', '罗隐', '水边偶题', '野水无情去不回，水边花好为谁开。\\n只知事逐眼前去，不觉老从头上来。\\n穷似丘轲休叹息，达如周召亦尘埃。\\n思量此理何人会，蒙邑先生最有才。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2888', 'poem', '唐', '李商隐', '谒山', '从来系日乏长绳，水去云回恨不胜。\\n欲就麻姑买沧海，一杯春露冷如冰。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2889', 'poem', '唐', '郎士元', '盖少府新除江南尉问风俗', '闻君作尉向江潭，吴越风烟到自谙。\\n客路寻常随竹影，人家大底傍山岚。\\n缘溪花木偏宜远，避地衣冠尽向南。\\n惟有夜猿啼海树，思乡望国意难堪。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2890', 'poem', '唐', '王缙', '九日作', '莫将边地比京都，八月严霜草已枯。\\n今日登高樽酒里，不知能有菊花无。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2891', 'poem', '唐', '崔道融', '夜泊九江', '夜泊江门外，欢声月里楼。\\n明朝归去路，犹隔洞庭秋。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2892', 'poem', '唐', '白居易', '强酒', '若不坐禅销妄想，即须行醉放狂歌。\\n不然秋月春风夜，争那闲思往事何。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2893', 'poem', '唐', '郑锡', '玉阶怨', '长门寒水流，高殿晓风秋。\\n昨夜鸳鸯梦，还陪豹尾游。\\n前鱼不解泣，共辇岂关羞。\\n那及轻身燕，双飞上玉楼。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2894', 'poem', '唐', '钱起', '陪考功王员外城东池亭宴', '无双锦帐郎，绝境有林塘。\\n鹤静疏群羽，蓬开失众芳。\\n晴山看不厌，流水趣何长。\\n日晚催归骑，钟声下夕阳。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2895', 'poem', '唐', '刘兼', '春宵', '春云春日共朦胧，满院梨花半夜风。\\n宿酒未醒珠箔卷，艳歌初阕玉楼空。\\n五湖范蠡才堪重，六印苏秦道不同。\\n再取素琴聊假寐，南柯灵梦莫相通。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2896', 'poem', '唐', '唐彦谦', '鸂鶒', '一宿南塘烟雨时，好风摇动绿波微。\\n惊离晓岸冲花去，暖下春汀照影飞。\\n华屋撚弦弹鼓舞，绮窗含笔澹毛衣。\\n画屏见后长回首，争得雕笼莫放归。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2897', 'poem', '唐', '白居易', '秋池', '洗浪清风透水霜，水边闲坐一绳床。\\n眼尘心垢见皆尽，不是秋池是道场。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2898', 'poem', '唐', '尚颜', '送朴山人归新罗', '浩渺行无极，扬帆但信风。\\n云山过海半，乡树入舟中。\\n波定遥天出，沙平远岸穷。\\n离心寄何处，目击曙霞东。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2899', 'poem', '唐', '李中', '鸂鶒', '流品是鸳鸯，翻飞云水乡。\\n风高离极浦，烟暝下方塘。\\n比鹭行藏别，穿荷羽翼香。\\n双双浴轻浪，谁见在潇湘。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2900', 'poem', '唐', '裴说', '春日山中竹', '数竿苍翠拟龙形，峭拔须教此地生。\\n无限野花开不得，半山寒色与春争。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2901', 'poem', '唐', '杜甫', '峡口二首', '峡口大江间，西南控百蛮。\\n城欹连粉堞，岸断更青山。\\n开辟多天险，防隅一水关。\\n乱离闻鼓角，秋气动衰颜。\\n时清关失险，世乱戟如林。\\n去矣英雄事，荒哉割据心。\\n芦花留客晚，枫树坐猿深。\\n疲苶烦亲故，诸侯数赐金。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2902', 'poem', '唐', '杜牧', '皇风', '仁圣天子神且武，内兴文教外披攘。\\n以德化人汉文帝，侧身修道周宣王。\\n迒蹊巢穴尽窒塞，礼乐刑政皆弛张。\\n何当提笔侍巡狩，前驱白旆吊河湟。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2903', 'poem', '唐', '陆龟蒙', '蝉', '只凭风作使，全仰柳为都。\\n一腹清何甚，双翎薄更无。\\n伴貂金换酒，并雀画成图。\\n恐是千年恨，偏令落日呼。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2904', 'poem', '唐', '王维', '皇甫岳云溪杂题五首·上平田', '朝耕上平田，暮耕上平田。\\n借问问津者，宁知沮溺贤。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2905', 'poem', '唐', '马戴', '送朴山人归新罗', '浩渺行无极，扬帆但信风。\\n云山过海半，乡树入舟中。\\n波定遥天出，沙平远岸穷。\\n离心寄何处，目断曙霞东。', '2018-03-09 15:21:47');
INSERT INTO `t_poem` VALUES ('2906', 'poem', '唐', '白居易', '病中得樊大书', '荒村破屋经年卧，寂绝无人问病身。\\n唯有东都樊著作，至今书信尚殷勤。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2907', 'poem', '唐', '杜甫', '江头四咏·鸂鶒', '故使笼宽织，须知动损毛。\\n看云莫怅望，失水任呼号。\\n六翮曾经剪，孤飞卒未高。\\n且无鹰隼虑，留滞莫辞劳。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2908', 'poem', '唐', '李德裕', '春暮思平泉杂咏二十首·鸂鶒', '清沚双鸂鶒，前年海上雏。\\n今来恋洲屿，思若在江湖。\\n欲起摇荷盖，闲飞溅水珠。\\n不能常泛泛，惟作逐波凫。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2909', 'poem', '唐', '王梵志', '吾富有钱时', '吾富有钱时，妇儿看我好。\\n吾若脱衣裳，与吾叠袍袄。\\n吾出经求去，送吾即上道。\\n将钱入舍来，见吾满面笑。\\n绕吾白鸽旋，恰似鹦鹉鸟。\\n邂逅暂时贫，看吾即貌哨。\\n人有七贫时，七富还相报。\\n图财不顾人，且看来时道。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2910', 'poem', '唐', '刘长卿', '晚次湖口有怀', '霭然空水合，目极平江暮。\\n南望天无涯，孤帆落何处。\\n顷为衡湘客，颇见湖山趣。\\n朝气和楚云，夕阳映江树。\\n帝乡劳想望，万里心来去。\\n白发生扁舟，沧波满归路。\\n秋风今已至，日夜雁南度。\\n木叶辞洞庭，纷纷落无数。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2911', 'poem', '唐', '韦应物', '出还', '昔出喜还家，今还独伤意。\\n入室掩无光，衔哀写虚位。\\n凄凄动幽幔，寂寂惊寒吹。\\n幼女复何知，时来庭下戏。\\n咨嗟日复老，错莫身如寄。\\n家人劝我餐，对案空垂泪。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2912', 'poem', '唐', '李白', '酬宇文少府见赠桃竹书筒', '桃竹书筒绮绣文，良工巧妙称绝群。\\n灵心圆映三江月，彩质叠成五色云。\\n中藏宝诀峨眉去，千里提携长忆君。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2913', 'poem', '唐', '姚合', '杏溪十首·杏溪', '桃花四散飞，桃子压枝垂。\\n寂寂青阴里，幽人举步迟。\\n殷勤念此径，我去复来谁。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2914', 'poem', '唐', '白居易', '日长', '日长昼加餐，夜短朝馀睡。\\n春来寝食间，虽老犹有味。\\n林塘得芳景，园曲生幽致。\\n爱水多棹舟，惜花不扫地。\\n幸无眼下病，且向樽前醉。\\n身外何足言，人间本无事。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2915', 'poem', '唐', '李白', '江南春怀', '青春几何时，黄鸟鸣不歇。\\n天涯失乡路，江外老华发。\\n心飞秦塞云，影滞楚关月。\\n身世殊烂漫，田园久芜没。\\n岁晏何所从？长歌谢金阙。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2916', 'poem', '唐', '李白', '姑孰十咏·丹阳湖', '湖与元气连，风波浩难止。\\n天外贾客归，云间片帆起。\\n龟游莲叶上，鸟宿芦花里。\\n少女棹归舟，歌声逐流水。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2917', 'poem', '唐', '李商隐', '嘲桃', '无赖夭桃面，平时露井东。\\n春风为开了，却拟笑春风。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2918', 'poem', '唐', '雍陶', '韦处士郊居', '满庭诗境飘红叶，绕砌琴声滴暗泉。\\n门外晚晴秋色老，万条寒玉一溪烟。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2919', 'poem', '唐', '李商隐', '微雨', '初随林霭动，稍共夜凉分。\\n窗迥侵灯冷，庭虚近水闻。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2920', 'poem', '唐', '李益', '立秋前一日览镜', '万事销身外，生涯在镜中。\\n唯将满鬓雪，明日对秋风。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2921', 'poem', '唐', '白居易', '看恽家牡丹花戏赠李二十', '香胜烧兰红胜霞，城中最数令公家。\\n人人散后君须看，归到江南无此花。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2922', 'poem', '唐', '方干', '思江南', '昨日草枯今日青，羁人又动望乡情。\\n夜来有梦登归路，不到桐庐已及明。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2923', 'poem', '唐', '佚名', '杂曲歌辞·凉州歌第二', '朔风吹叶雁门秋，万里烟尘昏戍楼。\\n征马长思青海北，胡笳夜听陇山头。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2924', 'poem', '唐', '骆宾王', '冬日宴', '二三物外友，一百杖头钱。\\n赏洽袁公地，情披乐令天。\\n促席鸾觞满，当炉兽炭然。\\n何须攀桂树，逢此自留连。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2925', 'poem', '唐', '齐己', '边上', '汉地从休马，胡家自牧羊。\\n都来销帝道，浑不用兵防。\\n草上孤城白，沙翻大漠黄。\\n秋风起边雁，一一向潇湘。', '2018-03-09 15:21:55');
INSERT INTO `t_poem` VALUES ('2926', 'poem', '唐', '崔道融', '秋霁', '雨霁长空荡涤清，远山初出未知名。\\n夜来江上如钩月，时有惊鱼掷浪声。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2927', 'poem', '唐', '白居易', '前庭凉夜', '露簟色似玉，风幌影如波。\\n坐愁树叶落，中庭明月多。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2928', 'poem', '唐', '司空图', '诗品二十四则·冲淡', '素处以默，妙机其微。\\n饮之太和，独鹤与飞。\\n犹之惠风，荏苒在衣。\\n阅音修篁，美曰载归。\\n遇之匪深，即之愈稀。\\n脱有形似，握手已违。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2929', 'poem', '唐', '崔颢', '七夕', '长安城中月如练，家家此夜持针线。\\n仙裙玉佩空自知，天上人间不相见。\\n长信深阴夜转幽，瑶阶金阁数萤流。\\n班姬此夕愁无限，河汉三更看斗牛。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2930', 'poem', '唐', '子兰', '夜直', '大内隔重墙，多闻乐未央。\\n灯明宫树色，茶煮禁泉香。\\n凤辇通门静，鸡歌入漏长。\\n宴荣陪御席，话密近龙章。\\n吟步彤庭月，眠分玉署凉。\\n欲黏朱绂重，频草白麻忙。\\n笔力将群吏，人情在致唐。\\n万方瞻仰处，晨夕面吾皇。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2931', 'poem', '唐', '李山甫', '柳十首', '灞岸江头腊雪消，东风偷软入纤条。\\n春来不忍登楼望，万架金丝著地娇。\\n受尽风霜得到春，一条条是逐年新。\\n寻常送别无馀事，争忍攀将过与人。\\n长恨阳和也世情，把香和艳与红英。\\n家家只是栽桃李，独自无根到处生。\\n只为遮楼又拂桥，被人摧折好枝条。\\n假饶张绪如今在，须把风流暗里销。\\n弱带低垂可自由，傍他门户倚他楼。\\n金风不解相抬举，露压烟欺直到秋。\\n终日堂前学画眉，几人曾道胜花枝。\\n试看三月春残后，门外青阴是阿谁。\\n也曾飞絮谢家庭，从此风流别有名。\\n不是向人无用处，一枝愁杀别离情。\\n从来只是爱花人，杨柳何曾占得春。\\n多向客亭门外立，与他迎送往来尘。\\n强扶柔态酒难醒，殢著春风别有情。\\n公子王孙且相伴，与君俱得几时荣。\\n无赖秋风斗觉寒，万条烟草一时干。\\n游人若要春消息，直向江头腊后看。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2932', 'poem', '唐', '王驾', '古意', '夫戍萧关妾在吴，西风吹妾妾忧夫。\\n一行书信千行泪，寒到君边衣到无。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2933', 'poem', '唐', '高适', '送郑侍御谪闽中', '谪去君无恨，闽中我旧过。\\n大都秋雁少，只是夜猿多。\\n东路云山合，南天瘴疠和。\\n自当逢雨露，行矣慎风波。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2934', 'poem', '唐', '李洞', '山居喜友人见访', '入云晴劚茯苓还，日暮逢迎木石间。\\n看待诗人无别物，半潭秋水一房山。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2935', 'poem', '唐', '杜甫', '杜位宅守岁', '守岁阿戎家，椒盘已颂花。\\n盍簪喧枥马，列炬散林鸦。\\n四十明朝过，飞腾暮景斜。\\n谁能更拘束，烂醉是生涯。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2936', 'poem', '唐', '李商隐', '楚吟', '山上离宫宫上楼，楼前宫畔暮江流。\\n楚天长短黄昏雨，宋玉无愁亦自愁。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2937', 'poem', '唐', '胡曾', '咏史诗·褒城', '恃宠娇多得自由，骊山举火戏诸侯。\\n只知一笑倾人国，不觉胡尘满玉楼。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2938', 'poem', '唐', '王昌龄', '旅望 / 出塞行', '白花原头望京师，黄河水流无尽时。\\n穷秋旷野行人绝，马首东来知是谁。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2939', 'poem', '唐', '李昂', '宫中题', '辇路生春草，上林花发时。\\n凭高何限意，无复侍臣知。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2940', 'poem', '唐', '杜甫', '江头四咏·丁香', '丁香体柔弱，乱结枝犹垫。\\n细叶带浮毛，疏花披素艳。\\n深栽小斋后，庶近幽人占。\\n晚堕兰麝中，休怀粉身念。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2941', 'poem', '唐', '杜牧', '长兴里夏日寄南邻避暑', '侯家大道傍，蝉噪树苍苍。\\n开锁洞门远，卷帘官舍凉。\\n栏围红药盛，架引绿萝长。\\n永日一欹枕，故山云水乡。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2942', 'poem', '唐', '白居易', '戒药', '促促急景中，蠢蠢微尘里。\\n生涯有分限，爱恋无终已。\\n早夭羡中年，中年羡暮齿。\\n暮齿又贪生，服食求不死。\\n朝吞太阳精，夕吸秋石髓。\\n徼福反成灾，药误者多矣。\\n以之资嗜欲，又望延甲子。\\n天人阴骘间，亦恐无此理。\\n域中有真道，所说不如此。\\n后身始身存，吾闻诸老氏。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2943', 'poem', '唐', '白居易', '秋池二首', '身闲无所为，心闲无所思。\\n况当故园夜，复此新秋池。\\n岸暗鸟栖后，桥明月出时。\\n菱风香散漫，桂露光参差。\\n静境多独得，幽怀竟谁知。\\n悠然心中语，自问来何迟。\\n朝衣薄且健，晚簟清仍滑。\\n社近燕影稀，雨馀蝉声歇。\\n闲中得诗境，此境幽难说。\\n露荷珠自倾，风竹玉相戛。\\n谁能一同宿，共玩新秋月。\\n暑退早凉归，池边好时节。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2944', 'poem', '唐', '李白', '翰林读书言怀呈集贤诸学士', '晨趋紫禁中，夕待金门诏。\\n观书散遗帙，探古穷至妙。\\n片言苟会心，掩卷忽而笑。\\n青蝇易相点，白雪难同调。\\n本是疏散人，屡贻褊促诮。\\n云天属清朗，林壑忆游眺。\\n或时清风来，闲倚栏下啸。\\n严光桐庐溪，谢客临海峤。\\n功成谢人间，从此一投钓。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2945', 'poem', '唐', '白居易', '听歌六绝句·离别难', '绿杨陌上送行人，马去车回一望尘。\\n不觉别时红泪尽，归来无泪可沾巾。', '2018-03-09 15:22:04');
INSERT INTO `t_poem` VALUES ('2946', 'poem', '唐', '许浑', '蝉', '噪柳鸣槐晚未休，不知何事爱悲秋。\\n朱门大有长吟处，刚傍愁人又送愁。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2947', 'poem', '唐', '白居易', '春雪', '元和岁在卯，六年春二月。\\n月晦寒食天，天阴夜飞雪。\\n连宵复竟日，浩浩殊未歇。\\n大似落鹅毛，密如飘玉屑。\\n寒销春茫苍，气变风凛冽。\\n上林草尽没，曲江水复结。\\n红乾杏花死，绿冻杨枝折。\\n所怜物性伤，非惜年芳绝。\\n上天有时令，四序平分别。\\n寒燠苟反常，物生皆夭阏。\\n我观圣人意，鲁史有其说。\\n或记水不冰，或书霜不杀。\\n上将儆政教，下以防灾孽。\\n兹雪今如何，信美非时节。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2948', 'poem', '唐', '张祜', '题松汀驿', '山色远含空，苍茫泽国东。\\n海明先见日，江白迥闻风。\\n鸟道高原去，人烟小径通。\\n那知旧遗逸，不在五湖中。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2949', 'poem', '唐', '雍陶', '和孙明府怀旧山', '五柳先生本在山，偶然为客落人间。\\n秋来见月多归思，自起开笼放白鹇。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2950', 'poem', '唐', '曹邺', '四怨三愁五情诗十二首·一情', '东西是长江，南北是官道。\\n牛羊不恋山，只恋山中草。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2951', 'poem', '唐', '李白', '送别', '斗酒渭城边，垆头醉不眠。\\n梨花千树雪，杨叶万条烟。\\n惜别倾壶醑，临分赠马鞭。\\n看君颍上去，新月到应圆。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2952', 'poem', '唐', '秦韬玉', '陈宫', '临春高阁拟瀛洲，贪宠张妃作胜游。\\n更把江山为己有，岂知台榭是身雠。\\n金城暗逐歌声碎，钱瓮潜随舞势休。\\n谁识古宫堪恨处，井桐吟雨不胜秋。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2953', 'poem', '唐', '白居易', '秋池二首', '前池秋始半，卉物多摧坏。\\n欲暮槿先萎，未霜荷已败。\\n默然有所感，可以从兹诫。\\n本不种松筠，早凋何足怪。\\n凿池贮秋水，中有蘋与芰。\\n天旱水暗消，塌然委空地。\\n有似泛泛者，附离权与贵。\\n一旦恩势移，相随共憔悴。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2954', 'poem', '唐', '曹松', '天台瀑布', '万仞得名云瀑布，远看如织挂天台。\\n休疑宝尺难量度，直恐金刀易剪裁。\\n喷向林梢成夏雪，倾来石上作春雷。\\n欲知便是银河水，堕落人间合却回。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2955', 'poem', '唐', '白居易', '燕子楼三首', '满窗明月满帘霜，被冷灯残拂卧床。\\n燕子楼中霜月夜，秋来只为一人长。\\n钿晕罗衫色似烟，几回欲著即潸然。\\n自从不舞霓裳曲，叠在空箱十一年。\\n今春有客洛阳回，曾到尚书墓上来。\\n见说白杨堪作柱，争教红粉不成灰。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2956', 'poem', '唐', '白居易', '北楼送客归上都', '凭高眺远一凄凄，却下朱阑即解携。\\n京路人归天直北，江楼客散日平西。\\n长津欲度回渡尾，残酒重倾簇马蹄。\\n不独别君须强饮，穷愁自要醉如泥。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2957', 'poem', '唐', '白居易', '东亭闲望', '东亭尽日坐，谁伴寂寥身。\\n绿桂为佳客，红蕉当美人。\\n笑言虽不接，情状似相亲。\\n不作悠悠想，如何度晚春。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2958', 'poem', '唐', '吴融', '秋池', '冷涵秋水碧溶溶，一片澄明见底空。\\n有日晴来云衬白，几时吹落叶浮红。\\n香啼蓼穗娟娟露，乾动莲茎淅淅风。\\n凌晓无端照衰发，便悲霜雪镜光中。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2959', 'poem', '唐', '李咸用', '春日', '浩荡东风里，裴回无所亲。\\n危城三面水，古树一边春。\\n衰世难修道，花时不称贫。\\n滔滔天下者，何处问通津。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2960', 'poem', '唐', '钱起', '避暑纳凉', '木槿花开畏日长，时摇轻扇倚绳床。\\n初晴草蔓缘新笋，频雨苔衣染旧墙。\\n十旬河朔应虚醉，八柱天台好纳凉。\\n无事始然知静胜，深垂纱帐咏沧浪。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2961', 'poem', '唐', '白居易', '别州民', '耆老遮归路，壶浆满别筵。\\n甘棠无一树，那得泪潸然。\\n税重多贫户，农饥足旱田。\\n唯留一湖水，与汝救凶年。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2962', 'poem', '唐', '刘长卿', '送河南元判官赴河南勾当苗税充百官俸钱', '春草长河曲，离心共渺然。\\n方收汉家俸，独向汶阳田。\\n鸟雀空城在，榛芜旧路迁。\\n山东征战苦，几处有人烟。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2963', 'poem', '唐', '翁承赞', '书斋谩兴二首', '池塘四五尺深水，篱落两三般样花。\\n过客不须频问姓，读书声里是吾家。\\n官事归来衣雪埋，儿童灯火小茅斋。\\n人家不必论贫富，惟有读书声最佳。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2964', 'poem', '唐', '杜甫', '村夜', '萧萧风色暮，江头人不行。\\n村舂雨外急，邻火夜深明。\\n胡羯何多难，渔樵寄此生。\\n中原有兄弟，万里正含情。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2965', 'poem', '唐', '韦庄', '汉州', '比侬初到汉州城，郭邑楼台触目惊。\\n松桂影中旌旆色，芰荷风里管弦声。\\n人心不似经离乱，时运还应却太平。\\n十日醉眠金雁驿，临岐无恨脸波横。', '2018-03-09 15:22:11');
INSERT INTO `t_poem` VALUES ('2966', 'poem', '唐', '李白', '戏赠杜甫（以下见《唐诗纪事》）', '饭颗山头逢杜甫，顶戴笠子日卓午。借问别来太瘦生，总为从前作诗苦。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2967', 'poem', '宋', '陆游', '恩封渭南伯唐诗人赵嘏为渭南尉当时谓之赵渭', '老向人间久倦游，君恩乞与渭川秋。虚名定作陈惊坐，好句真惭赵倚楼。栈豆十年沾病马，烟波万里著浮鸥。就封他日轻裘去，应过三峰处处留。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2968', 'poem', '宋', '刘克庄', '唐诗', '瀛洲学士风流远，中叶唐惭贞观唐。灵武拾遗晚羁旅，开元供奉老佯狂。戏苕翡翠非伦拟，撼树蚍蜉不揣量。赖有元和韩十八，骑麟被发共翱翔。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2969', 'poem', '明', '袁宗道', '同惟长舅读唐诗有感', '数卷陈言逐字新，眼前君是赏音人。家家椟玉谁知赝，处处抽龙总忌真。再舍肉黥居易句，重捐金铸浪仙身。一从马粪卮言出，难洗诗家入骨尘。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2970', 'poem', '宋', '周南', '读唐诗', '一日勾消两卷诗，趁心抄得是灯时。隔窗一閧狂风雨，无数啁啾冻雀儿。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2971', 'poem', '宋', '朱继芳', '桃源官罢芸居以唐诗拙作赠别', '自作还相送，唐诗结伴来。归装携此重，笑口为君开。莲上巢难稳，桃头郁垒神。何须千岁上，即此是新陈。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2972', 'poem', '宋', '戴复古', '伏龙山民宋正甫湖山清隐乃唐诗人陈陶故圃曾', '故人昔住金华峰，面带双溪秋水容。故人今住伏龙山，陈陶故圃茅三间。千载清风徐孺子，门前共此一湖水。百花洲上万垂杨，白鸥群里歌沧浪。故人心事孺子高，故人诗句今陈陶。短衣饭牛不逢尧，何如绣鞍上着锦宫袍。瓦盆对客酌松醪，何如紫霞觞泛碧葡萄。豆萁然火度寒宵，何如玉堂夜照金莲膏。吟成秃笔写芭蕉，何如沉香亭北醉挥毫。再三问君君不对，目送飞鸿楚天外。细读山中招隐篇，超然意与烟霞会。照影湖边双鬓皓，此计知之悔不早。三椽可办愿卜隣，荷锸相随种瑶草。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2973', 'poem', '唐', '曹唐', '又游仙诗一绝（见《唐诗纪事》）', '靖节先生几代孙，青娥曾接玉郎魂。春风流水还无赖，偷放桃花出洞门。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2974', 'poem', '唐', '许瀍', '纪梦（一作梦入琼台，本事诗及唐诗纪事并作许浑）', '晚入瑶台露气清，天风飞下步虚声。尘心未尽俗缘在，十里下山空月明。', '2018-03-09 15:22:20');
INSERT INTO `t_poem` VALUES ('2975', 'poem', '唐', '李白', '戏赠杜甫（以下见《唐诗纪事》）', '饭颗山头逢杜甫，顶戴笠子日卓午。借问别来太瘦生，总为从前作诗苦。', '2018-03-09 15:22:24');
INSERT INTO `t_poem` VALUES ('2976', 'poem', '宋', '陆游', '恩封渭南伯唐诗人赵嘏为渭南尉当时谓之赵渭', '老向人间久倦游，君恩乞与渭川秋。虚名定作陈惊坐，好句真惭赵倚楼。栈豆十年沾病马，烟波万里著浮鸥。就封他日轻裘去，应过三峰处处留。', '2018-03-09 15:22:24');
INSERT INTO `t_poem` VALUES ('2993', 'poem', '唐', '许浑', '蝉', '噪柳鸣槐晚未休，不知何事爱悲秋。\\n朱门大有长吟处，刚傍愁人又送愁。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('2994', 'poem', '唐', '白居易', '春雪', '元和岁在卯，六年春二月。\\n月晦寒食天，天阴夜飞雪。\\n连宵复竟日，浩浩殊未歇。\\n大似落鹅毛，密如飘玉屑。\\n寒销春茫苍，气变风凛冽。\\n上林草尽没，曲江水复结。\\n红乾杏花死，绿冻杨枝折。\\n所怜物性伤，非惜年芳绝。\\n上天有时令，四序平分别。\\n寒燠苟反常，物生皆夭阏。\\n我观圣人意，鲁史有其说。\\n或记水不冰，或书霜不杀。\\n上将儆政教，下以防灾孽。\\n兹雪今如何，信美非时节。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('2995', 'poem', '唐', '张祜', '题松汀驿', '山色远含空，苍茫泽国东。\\n海明先见日，江白迥闻风。\\n鸟道高原去，人烟小径通。\\n那知旧遗逸，不在五湖中。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('2996', 'poem', '唐', '雍陶', '和孙明府怀旧山', '五柳先生本在山，偶然为客落人间。\\n秋来见月多归思，自起开笼放白鹇。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('2997', 'poem', '唐', '曹邺', '四怨三愁五情诗十二首·一情', '东西是长江，南北是官道。\\n牛羊不恋山，只恋山中草。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('2998', 'poem', '唐', '李白', '送别', '斗酒渭城边，垆头醉不眠。\\n梨花千树雪，杨叶万条烟。\\n惜别倾壶醑，临分赠马鞭。\\n看君颍上去，新月到应圆。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('2999', 'poem', '唐', '秦韬玉', '陈宫', '临春高阁拟瀛洲，贪宠张妃作胜游。\\n更把江山为己有，岂知台榭是身雠。\\n金城暗逐歌声碎，钱瓮潜随舞势休。\\n谁识古宫堪恨处，井桐吟雨不胜秋。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3000', 'poem', '唐', '白居易', '秋池二首', '前池秋始半，卉物多摧坏。\\n欲暮槿先萎，未霜荷已败。\\n默然有所感，可以从兹诫。\\n本不种松筠，早凋何足怪。\\n凿池贮秋水，中有蘋与芰。\\n天旱水暗消，塌然委空地。\\n有似泛泛者，附离权与贵。\\n一旦恩势移，相随共憔悴。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3001', 'poem', '唐', '曹松', '天台瀑布', '万仞得名云瀑布，远看如织挂天台。\\n休疑宝尺难量度，直恐金刀易剪裁。\\n喷向林梢成夏雪，倾来石上作春雷。\\n欲知便是银河水，堕落人间合却回。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3002', 'poem', '唐', '白居易', '燕子楼三首', '满窗明月满帘霜，被冷灯残拂卧床。\\n燕子楼中霜月夜，秋来只为一人长。\\n钿晕罗衫色似烟，几回欲著即潸然。\\n自从不舞霓裳曲，叠在空箱十一年。\\n今春有客洛阳回，曾到尚书墓上来。\\n见说白杨堪作柱，争教红粉不成灰。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3003', 'poem', '唐', '白居易', '北楼送客归上都', '凭高眺远一凄凄，却下朱阑即解携。\\n京路人归天直北，江楼客散日平西。\\n长津欲度回渡尾，残酒重倾簇马蹄。\\n不独别君须强饮，穷愁自要醉如泥。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3004', 'poem', '唐', '白居易', '东亭闲望', '东亭尽日坐，谁伴寂寥身。\\n绿桂为佳客，红蕉当美人。\\n笑言虽不接，情状似相亲。\\n不作悠悠想，如何度晚春。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3005', 'poem', '唐', '吴融', '秋池', '冷涵秋水碧溶溶，一片澄明见底空。\\n有日晴来云衬白，几时吹落叶浮红。\\n香啼蓼穗娟娟露，乾动莲茎淅淅风。\\n凌晓无端照衰发，便悲霜雪镜光中。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3006', 'poem', '唐', '李咸用', '春日', '浩荡东风里，裴回无所亲。\\n危城三面水，古树一边春。\\n衰世难修道，花时不称贫。\\n滔滔天下者，何处问通津。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3007', 'poem', '唐', '钱起', '避暑纳凉', '木槿花开畏日长，时摇轻扇倚绳床。\\n初晴草蔓缘新笋，频雨苔衣染旧墙。\\n十旬河朔应虚醉，八柱天台好纳凉。\\n无事始然知静胜，深垂纱帐咏沧浪。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3008', 'poem', '唐', '白居易', '别州民', '耆老遮归路，壶浆满别筵。\\n甘棠无一树，那得泪潸然。\\n税重多贫户，农饥足旱田。\\n唯留一湖水，与汝救凶年。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3009', 'poem', '唐', '刘长卿', '送河南元判官赴河南勾当苗税充百官俸钱', '春草长河曲，离心共渺然。\\n方收汉家俸，独向汶阳田。\\n鸟雀空城在，榛芜旧路迁。\\n山东征战苦，几处有人烟。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3010', 'poem', '唐', '翁承赞', '书斋谩兴二首', '池塘四五尺深水，篱落两三般样花。\\n过客不须频问姓，读书声里是吾家。\\n官事归来衣雪埋，儿童灯火小茅斋。\\n人家不必论贫富，惟有读书声最佳。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3011', 'poem', '唐', '杜甫', '村夜', '萧萧风色暮，江头人不行。\\n村舂雨外急，邻火夜深明。\\n胡羯何多难，渔樵寄此生。\\n中原有兄弟，万里正含情。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3012', 'poem', '唐', '韦庄', '汉州', '比侬初到汉州城，郭邑楼台触目惊。\\n松桂影中旌旆色，芰荷风里管弦声。\\n人心不似经离乱，时运还应却太平。\\n十日醉眠金雁驿，临岐无恨脸波横。', '2018-03-09 15:32:29');
INSERT INTO `t_poem` VALUES ('3013', 'poem', '唐', '李白', '豫章行', '胡风吹代马，北拥鲁阳关。\\n吴兵照海雪，西讨何时还。\\n半渡上辽津，黄云惨无颜。\\n老母与子别，呼天野草间。\\n白马绕旌旗，悲鸣相追攀。\\n白杨秋月苦，早落豫章山。\\n本为休明人，斩虏素不闲。\\n岂惜战斗死，为君扫凶顽。\\n精感石没羽，岂云惮险艰。\\n楼船若鲸飞，波荡落星湾。\\n此曲不可奏，三军鬓成斑。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3014', 'poem', '唐', '白居易', '春村', '二月村园暖，桑间戴胜飞。\\n农夫舂旧谷，蚕妾捣新衣。\\n牛马因风远，鸡豚过社稀。\\n黄昏林下路，鼓笛赛神归。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3015', 'poem', '唐', '白居易', '西河雨夜送客', '云黑雨翛翛，江昏水暗流。\\n有风催解缆，无月伴登楼。\\n酒罢无多兴，帆开不少留。\\n唯看一点火，遥认是行舟。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3016', 'poem', '唐', '朱庆馀', '过耶溪', '春溪缭绕出无穷，两岸桃花正好风。\\n恰是扁舟堪入处，鸳鸯飞起碧流中。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3017', 'poem', '唐', '卢肇', '木笔花', '软如新竹管初齐，粉腻红轻样可携。\\n谁与诗人偎槛看，好于笺墨并分题。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3018', 'poem', '唐', '李百药', '咏蝉', '清心自饮露，哀响乍吟风。\\n未上华冠侧，先惊翳叶中。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3019', 'poem', '唐', '李白', '题峰顶寺', '夜宿峰顶寺。\\n举手扪星辰。\\n不敢高声语。\\n恐惊天上人。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3020', 'poem', '唐', '王维', '酬虞部苏员外过蓝田别业不见留之作', '贫居依谷口，乔木带荒村。\\n石路枉回驾，山家谁候门。\\n渔舟胶冻浦，猎火烧寒原。\\n唯有白云外，疏钟闻夜猿。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3021', 'poem', '唐', '罗邺', '水帘', '万点飞泉下白云，似帘悬处望疑真。\\n若将此水为霖雨，更胜长垂隔路尘。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3022', 'poem', '唐', '白居易', '答韦八', '丽句劳相赠，佳期恨有违。\\n早知留酒待，悔不趁花归。\\n春尽绿醅老，雨多红萼稀。\\n今朝如一醉，犹得及芳菲。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3023', 'poem', '唐', '王维', '淇上田园即事', '屏居淇水上，东野旷无山。\\n日隐桑柘外，河明闾井间。\\n牧童望村去，猎犬随人还。\\n静者亦何事，荆扉乘昼关。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3024', 'poem', '唐', '张白', '武陵春色', '武陵春色好，十二酒家楼。\\n大醉方回首，逢人不举头。\\n是非都不采，名利混然休。\\n戴个星冠子，浮沈逐世流。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3025', 'poem', '唐', '张谓', '代北州老翁答', '负薪老翁往北州，北望乡关生客愁。\\n自言老翁有三子，两人已向黄沙死。\\n如今小儿新长成，明年闻道又征兵。\\n定知此别必零落，不及相随同死生。\\n尽将田宅借邻伍，且复伶俜去乡土。\\n在生本求多子孙，及有谁知更辛苦。\\n近传天子尊武臣，强兵直欲静胡尘。\\n安边自合有长策，何必流离中国人。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3026', 'poem', '唐', '杜甫', '江头四咏·栀子', '栀子比众木，人间诚未多。\\n于身色有用，与道气伤和。\\n红取风霜实，青看雨露柯。\\n无情移得汝，贵在映江波。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3027', 'poem', '唐', '元稹', '献荥阳公诗五十韵', '郑驿骑翩翩，丘门子弟贤。\\n文翁开学日，正礼骋途年。\\n骏骨黄金买，英髦绛帐延。\\n趋风皆蹀足，侍坐各差肩。\\n解榻招徐稚，登楼引仲宣。\\n凤攒题字扇，鱼落讲经筵。\\n盛气河包济，贞姿岳柱天。\\n皋夔当五百，邹鲁重三千。\\n科斗翻腾取，关雎教授先。\\n篆垂朝露滴，诗缀夜珠联。\\n逸礼多心匠，焚书旧口传。\\n陈遵修尺牍，阮瑀让飞笺。\\n中的颜初启，抽毫踵未旋。\\n森罗万木合，属对百花全。\\n词海跳波涌，文星拂坐悬。\\n戴冯遥避席，祖逖后施鞭。\\n西蜀凌云赋，东阳咏月篇。\\n劲芟鳌足断，精贯虱心穿。\\n浩汗神弥王，鹞飏兴欲仙。\\n冰壶通皓雪，绮树眇晴烟。\\n驱驾雷霆走，铺陈锦绣鲜。\\n清机登穾奥，流韵溢山川。\\n墨客膺潜服，谈宾膝误前。\\n张鳞定摧败，折角反矜怜。\\n句句推琼玉，声声播管弦。\\n纤新撩造化，澒洞斡陶甄。\\n卫磬琤鍧极，齐竽僭滥偏。\\n空虚惭炙輠，点窜许怀铅。\\n惫色秋来草，哀吟雨后蝉。\\n自伤魂惨沮，何暇思幽玄。\\n喜到樽罍侧，愁亲几案边。\\n菁华知竭矣，肺腑尚求旃。\\n抵滞浑成醉，徘徊转慕膻。\\n老叹才渐少，闲苦病相煎。\\n瓦砾难追琢，刍荛分弃捐。\\n漫劳成恳恳，那得美娟娟。\\n拙劣仍非速，迂愚且异专。\\n移时停笔砚，挥景乏戈鋋。\\n仪舌忻犹在，舒帷誓不褰。\\n会将连献楚，深耻谬游燕。\\n蒲有临书叶，韦充读易编。\\n沙须披见宝，经拟带耕田。\\n入雾长期闰，持朱本望研。\\n轮辕呈曲直，凿枘取方圆。\\n呼吸宁徒尔，沾濡岂浪然。\\n过箫资响亮，随水涨沦涟。\\n惜日看圭短，偷光恨壁坚。\\n勤勤雕朽木，细细导蒙泉。\\n传癖今应甚，头风昨已痊。\\n丹青公旧物，一为变蚩妍。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3028', 'poem', '唐', '刘禹锡', '相和歌辞·采菱行', '白马湖平秋日光，紫菱如锦彩鸾翔。\\n荡舟游女满中央，采菱不顾马上郎。\\n争多逐胜纷相向，时转兰桡破轻浪。\\n长鬟弱袂动参差，钗影钏文浮荡漾。\\n笑语哇咬顾晚晖，蓼花绿岸扣舷归。\\n归来共到市桥步，野蔓系船萍满衣。\\n家家竹楼临广陌，下有连樯多估客。\\n携觞荐芰夜经过，醉踏大堤相应歌。\\n屈平祠下沅江水，月照寒波白烟起。\\n一曲南音此地闻，长安北望三千里。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3029', 'poem', '唐', '崔国辅', '杂曲歌辞·丽人曲', '红颜称绝代，欲并真无侣。\\n独有镜中人，由来自相许。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3030', 'poem', '唐', '冷朝阳', '送红线', '采菱歌怨木兰舟，送客魂销百尺楼。\\n还似洛妃乘雾去，碧天无际水空流。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3031', 'poem', '唐', '李白', '少年行其二', '五陵年少金市东，银鞍白马度春风。\\n落花踏尽游何处，笑入胡姬酒肆中。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3032', 'poem', '唐', '李咸用', '秋夕', '寥廓秋云薄，空庭月影微。\\n树寒栖鸟密，砌冷夜蛩稀。\\n晓鼓军容肃，疏钟客梦归。\\n吟馀何所忆，圣主尚宵衣。', '2018-03-09 15:32:34');
INSERT INTO `t_poem` VALUES ('3033', 'poem', '唐', '顾况', '登楼望水', '鸟啼花发柳含烟，掷却风光忆少年。\\n更上高楼望江水，故乡何处一归船。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3034', 'poem', '唐', '戴叔伦', '过柳溪道院', '溪上谁家掩竹扉，鸟啼浑似惜春晖。\\n日斜深巷无人迹，时见梨花片片飞。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3035', 'poem', '唐', '戴叔伦', '和汴州李相公勉人日喜春', '年来日日春光好，今日春光好更新。\\n独献菜羹怜应节，遍传金胜喜逢人。\\n烟添柳色看犹浅，鸟踏梅花落已频。\\n东閤此时闻一曲，翻令和者不胜春。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3036', 'poem', '唐', '李廷璧', '愁诗', '到来难遣去难留，著骨黏心万事休。\\n潘岳愁丝生鬓里，婕妤悲色上眉头。\\n长途诗尽空骑马，远雁声初独倚楼。\\n更有相思不相见，酒醒灯背月如钩。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3037', 'poem', '唐', '白居易', '寄内', '条桑初绿即为别，柿叶半红犹未归。\\n不如村妇知时节，解为田夫秋捣衣。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3038', 'poem', '唐', '白居易', '晚燕', '百鸟乳雏毕，秋燕独蹉跎。\\n去社日已近，衔泥意如何。\\n不悟时节晚，徒施工用多。\\n人间事亦尔，不独燕营窠。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3039', 'poem', '唐', '杜甫', '畏人', '早花随处发，春鸟异方啼。\\n万里清江上，三年落日低。\\n畏人成小筑，褊性合幽栖。\\n门径从榛草，无心走马蹄。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3040', 'poem', '唐', '綦毋潜', '宿龙兴寺', '香刹夜忘归，松青古殿扉。\\n灯明方丈室，珠系比丘衣。\\n白日传心静，青莲喻法微。\\n天花落不尽，处处鸟衔飞。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3041', 'poem', '唐', '韦承庆', '直中书省', '清切凤凰池，扶疏鸡树枝。\\n唯应集鸾鹭，何为宿羁雌。\\n大造乾坤辟，深恩雨露垂。\\n昆蚑皆含养，驽骀亦驱驰。\\n木偶翻为用，芝泥忽滥窥。\\n九思空自勉，五字本无施。\\n徒喜逢千载，何阶答二仪。\\n萤光向日尽，蚊力负山疲。\\n禁宇庭除阔，闲宵钟箭移。\\n暗花临户发，残月下帘欹。\\n白发随年改，丹心为主披。\\n命将时共泰，言与行俱危。\\n寄谢巢由客，尧年正在斯。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3042', 'poem', '唐', '高蟾', '春', '天柱几条支白日，天门几扇锁明时。\\n阳春发处无根蒂，凭仗东风分外吹。\\n明月断魂清霭霭，平芜归思绿迢迢。\\n人生莫遣头如雪，纵得春风亦不消。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3043', 'poem', '唐', '白居易', '早梳头', '夜沐早梳头，窗明秋镜晓。\\n飒然握中发，一沐知一少。\\n年事渐蹉跎，世缘方缴绕。\\n不学空门法，老病何由了。\\n未得无生心，白头亦为夭。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3044', 'poem', '唐', '栖蟾', '短歌行', '蟾光堪自笑，浮世懒思量。\\n身得几时活，眼开终日忙。\\n千门无寿药，一镜有愁霜。\\n早向尘埃外，光阴任短长。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3045', 'poem', '唐', '韩愈', '花岛', '蜂蝶去纷纷，香风隔岸闻。\\n欲知花岛处，水上觅红云。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3046', 'poem', '唐', '孟郊', '归信吟', '泪墨洒为书，将寄万里亲。\\n书去魂亦去，兀然空一身。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3047', 'poem', '唐', '吴融', '微雨', '天青织未遍，风急舞难成。\\n粉重低飞蝶，黄沈不语莺。\\n自随春霭乱，还放夕阳明。\\n惆怅池塘上，荷珠点点倾。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3048', 'poem', '唐', '李商隐', '题小松', '怜君孤秀植庭中，细叶轻阴满座风。\\n桃李盛时虽寂寞，雪霜多后始青葱。\\n一年几变枯荣事，百尺方资柱石功。\\n为谢西园车马客，定悲摇落尽成空。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3049', 'poem', '唐', '李商隐', '牡丹', '锦帏初卷卫夫人，绣被犹堆越鄂君。\\n垂手乱翻雕玉佩，招腰争舞郁金裙。\\n石家蜡烛何曾剪，荀令香炉可待熏。\\n我是梦中传彩笔，欲书花叶寄朝云。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3050', 'poem', '唐', '苏拯', '渔人', '垂竿朝与暮，披蓑卧横楫。\\n不问清平时，自乐沧波业。\\n长畏不得闲，几度避游畋。\\n当笑钓台上，逃名名却传。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3051', 'poem', '唐', '白居易', '池上夜境', '晴空星月落池塘，澄鲜净绿表里光。\\n露簟清莹迎夜滑，风襟潇洒先秋凉。\\n无人惊处野禽下，新睡觉时幽草香。\\n但问尘埃能去否，濯缨何必向沧浪。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3052', 'poem', '唐', '李白', '越女词其三', '耶溪采莲女。\\n见客棹歌回。\\n笑入荷花去。\\n佯羞不出来。', '2018-03-09 15:32:43');
INSERT INTO `t_poem` VALUES ('3053', 'poem', '唐', '刘禹锡', '岁夜咏怀', '弥年不得意，新岁又如何。\\n念昔同游者，而今有几多。\\n以闲为自在，将寿补蹉跎。\\n春色无情故，幽居亦见过。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3054', 'poem', '唐', '张谓', '赠赵使君美人', '红粉青蛾映楚云，桃花马上石榴裙。\\n罗敷独向东方去，漫学他家作使君。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3055', 'poem', '唐', '刘采春', '啰唝曲六首', '不喜秦淮水，生憎江上船。\\n载儿夫婿去，经岁又经年。\\n借问东园柳，枯来得几年。\\n自无枝叶分，莫恐太阳偏。\\n莫作商人妇，金钗当卜钱。\\n朝朝江口望，错认几人船。\\n那年离别日，只道住桐庐。\\n桐庐人不见，今得广州书。\\n昨日胜今日，今年老去年。\\n黄河清有日，白发黑无缘。\\n昨日北风寒，牵船浦里安。\\n潮来打缆断，摇橹始知难。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3056', 'poem', '唐', '王涯', '秋思赠远二首', '当年只自守空帷，梦里关山觉别离。\\n不见乡书传雁足，唯看新月吐蛾眉。\\n厌攀杨柳临清阁，闲采芙蕖傍碧潭。\\n走马台边人不见，拂云堆畔战初酣。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3057', 'poem', '唐', '杜甫', '一百五日夜对月', '无家对寒食，有泪如金波。\\n斫却月中桂，清光应更多。\\n仳离放红蕊，想像嚬青蛾。\\n牛女漫愁思，秋期犹渡河。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3058', 'poem', '唐', '白居易', '旅次景空寺宿幽上人院', '不与人境接，寺门开向山。\\n暮钟寒鸟聚，秋雨病僧闲。\\n月隐云树外，萤飞廊宇间。\\n幸投花界宿，暂得静心颜。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3059', 'poem', '唐', '陆龟蒙', '奉和袭美酒中十咏·酒旗', '摇摇倚青岸，远荡游人思。\\n风欹翠竹杠，雨澹香醪字。\\n才来隔烟见，已觉临江迟。\\n大旆非不荣，其如有王事。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3060', 'poem', '唐', '杜牧', '台城曲二首', '整整复斜斜，随旗簇晚沙。\\n门外韩擒虎，楼头张丽华。\\n谁怜容足地，却羡井中蛙。\\n王颁兵势急，鼓下坐蛮奴。\\n潋滟倪塘水，叉牙出骨须。\\n干芦一炬火，回首是平芜。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3061', 'poem', '唐', '韦庄', '登咸阳县楼望雨', '乱云如兽出山前，细雨和风满渭川。\\n尽日空濛无所见，雁行斜去字联联。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3062', 'poem', '唐', '李白', '紫藤树', '紫藤挂云木，花蔓宜阳春。\\n密叶隐歌鸟，香风留美人。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3063', 'poem', '唐', '韩琮', '骆谷晚望', '秦川如画渭如丝，去国还家一望时。\\n公子王孙莫来好，岭花多是断肠枝。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3064', 'poem', '唐', '白居易', '浪淘沙', '青草湖中万里程，黄梅雨里一人行。\\n愁见滩头夜泊处，风翻暗浪打船声。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3065', 'poem', '唐', '皮日休', '酒中十咏·酒旗', '青帜阔数尺，悬于往来道。\\n多为风所飏，时见酒名号。\\n拂拂野桥幽，翻翻江市好。\\n双眸复何事，终竟望君老。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3066', 'poem', '唐', '鱼玄机', '题隐雾亭', '春花秋月入诗篇，白日清宵是散仙。\\n空卷珠帘不曾下，长移一榻对山眠。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3067', 'poem', '唐', '杜牧', '题敬爱寺楼', '暮景千山雪，春寒百尺楼。\\n独登还独下，谁会我悠悠。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3068', 'poem', '唐', '卢肇', '牧童', '谁人得似牧童心，牛上横眠秋听深。\\n时复往来吹一曲，何愁南北不知音。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3069', 'poem', '唐', '白居易', '长相思·九月西风兴', '九月西风兴，月冷露华凝。\\n思君秋夜长，一夜魂九升。\\n二月东风来，草拆花心开。\\n思君春日迟，一日肠九回。\\n妾住洛桥北，君住洛桥南。\\n十五即相识，今年二十三。\\n有如女萝草，生在松之侧。\\n蔓短枝苦高，萦回上不得。\\n人言人有愿，愿至天必成。\\n愿作远方兽，步步比肩行。\\n愿作深山木，枝枝连理生。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3070', 'poem', '唐', '许浑', '闻雁', '带霜南去雁，夜好宿汀沙。\\n惊起向何处，高飞极海涯。\\n入云声渐远，离岳路犹赊。\\n归梦当时断，参差欲到家。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3071', 'poem', '唐', '白居易', '秋槿', '风露飒已冷，天色亦黄昏。\\n中庭有槿花，荣落同一晨。\\n秋开已寂寞，夕陨何纷纷。\\n正怜少颜色，复叹不逡巡。\\n感此因念彼，怀哉聊一陈。\\n男儿老富贵，女子晚婚姻。\\n头白始得志，色衰方事人。\\n后时不获已，安得如青春。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3072', 'poem', '唐', '李白', '寻高凤石门山中元丹丘', '寻幽无前期，乘兴不觉远。\\n苍崖渺难涉，白日忽欲晚。\\n未穷三四山，已历千万转。\\n寂寂闻猿愁，行行见云收。\\n高松来好月，空谷宜清秋。\\n溪深古雪在，石断寒泉流。\\n峰峦秀中天，登眺不可尽。\\n丹丘遥相呼，顾我忽而哂。\\n遂造穷谷间，始知静者闲。\\n留欢达永夜，清晓方言还。', '2018-03-09 15:32:54');
INSERT INTO `t_poem` VALUES ('3073', 'poem', '唐', '柳宗元', '得卢衡州书因以诗寄', '临蒸且莫叹炎方，为报秋来雁几行。\\n林邑东回山似戟，牂牁南下水如汤。\\n蒹葭淅沥含秋雾，橘柚玲珑透夕阳。\\n非是白蘋洲畔客，还将远意问潇湘。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3074', 'poem', '唐', '孟郊', '春雨后', '昨夜一霎雨，天意苏群物。\\n何物最先知，虚庭草争出。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3075', 'poem', '唐', '杜甫', '悲青坂', '我军青坂在东门，天寒饮马太白窟。\\n黄头奚儿日向西，数骑弯弓敢驰突。\\n山雪河冰野萧瑟，青是烽烟白人骨。\\n焉得附书与我军，忍待明年莫仓卒。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3076', 'poem', '唐', '王翰', '题败荷', '曾向西湖载酒归，香风十里弄晴晖。\\n芳菲今日凋零尽，却送秋声到客衣。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3077', 'poem', '唐', '罗隐', '庭花', '昨日芳艳浓，开尊几同醉。\\n今朝风雨恶，惆怅人生事。\\n南威病不起，西子老兼至。\\n向晚寂无人，相偎堕红泪。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3078', 'poem', '唐', '李白', '江上望皖公山', '奇峰出奇云，秀木含秀气。\\n清宴皖公山，巉绝称人意。\\n独游沧江上，终日淡无味。\\n但爱兹岭高，何由讨灵异。\\n默然遥相许，欲往心莫遂。\\n待吾还丹成，投迹归此地。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3079', 'poem', '唐', '白居易', '微之宅残牡丹', '残红零落无人赏，雨打风摧花不全。\\n诸处见时犹怅望，况当元九小亭前。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3080', 'poem', '唐', '罗隐', '鹦鹉', '莫恨雕笼翠羽残，江南地暖陇西寒。\\n劝君不用分明语，语得分明出转难。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3081', 'poem', '唐', '李群玉', '静夜相思', '山空天籁寂，水榭延轻凉。\\n浪定一浦月，藕花闲自香。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3082', 'poem', '唐', '杜甫', '乐游园歌', '乐游古园崒森爽，烟绵碧草萋萋长。\\n公子华筵势最高，秦川对酒平如掌。\\n长生木瓢示真率，更调鞍马狂欢赏。\\n青春波浪芙蓉园，白日雷霆夹城仗。\\n阊阖晴开昳荡荡，曲江翠幕排银榜。\\n拂水低徊舞袖翻，缘云清切歌声上。\\n却忆年年人醉时，只今未醉已先悲。\\n数茎白发那抛得，百罚深杯亦不辞。\\n圣朝亦知贱士丑，一物自荷皇天慈。\\n此身饮罢无归处，独立苍茫自咏诗。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3083', 'poem', '唐', '李崇嗣', '独愁', '闻道成都酒，无钱亦可求。\\n不知将几斗，销得此来愁。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3084', 'poem', '唐', '杜牧', '方响', '数条秋水挂琅玕，玉手丁当怕夜寒。\\n曲尽连敲三四下，恐惊珠泪落金盘。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3085', 'poem', '唐', '王昌龄', '观猎', '角鹰初下秋草稀，铁骢抛鞚去如飞。\\n少年猎得平原兔，马后横捎意气归。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3086', 'poem', '唐', '白居易', '梦仙', '人有梦仙者，梦身升上清。\\n坐乘一白鹤，前引双红旌。\\n羽衣忽飘飘，玉鸾俄铮铮。\\n半空直下视，人世尘冥冥。\\n渐失乡国处，才分山水形。\\n东海一片白，列岳五点青。\\n须臾群仙来，相引朝玉京。\\n安期羡门辈，列侍如公卿。\\n仰谒玉皇帝，稽首前致诚。\\n帝言汝仙才，努力勿自轻。\\n却后十五年，期汝不死庭。\\n再拜受斯言，既寤喜且惊。\\n秘之不敢泄，誓志居岩扃。\\n恩爱舍骨肉，饮食断膻腥。\\n朝餐云母散，夜吸沆瀣精。\\n空山三十载，日望辎輧迎。\\n前期过已久，鸾鹤无来声。\\n齿发日衰白，耳目减聪明。\\n一朝同物化，身与粪壤并。\\n神仙信有之，俗力非可营。\\n苟无金骨相，不列丹台名。\\n徒传辟谷法，虚受烧丹经。\\n只自取勤苦，百年终不成。\\n悲哉梦仙人，一梦误一生。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3087', 'poem', '唐', '杜牧', '宣州送裴坦判官往舒州时牧欲赴官归京', '日暖泥融雪半消，行人芳草马声骄。\\n九华山路云遮寺，清弋江村柳拂桥。\\n君意如鸿高的的，我心悬旆正摇摇。\\n同来不得同归去，故国逢春一寂寥！', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3088', 'poem', '唐', '李白', '树中草', '鸟衔野田草，误入枯桑里。\\n客土植危根，逢春犹不死。\\n草木虽无情，因依尚可生。\\n如何同枝叶，各自有枯荣。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3089', 'poem', '唐', '高适', '东平别前卫县李寀少府 / 送前卫县李宷少府', '黄鸟翩翩杨柳垂，春风送客使人悲。\\n怨别自惊千里外，论交却忆十年时。\\n云开汶水孤帆远，路绕梁山匹马迟。\\n此地从来可乘兴，留君不住益凄其。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3090', 'poem', '唐', '杜甫', '石龛', '熊罴哮我东，虎豹号我西。\\n我后鬼长啸，我前狨又啼。\\n天寒昏无日，山远道路迷。\\n驱车石龛下，仲冬见虹霓。\\n伐竹者谁子，悲歌上云梯。\\n为官采美箭，五岁供梁齐。\\n苦云直簳尽，无以充提携。\\n奈何渔阳骑，飒飒惊蒸黎。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3091', 'poem', '唐', '杜牧', '独柳', '含烟一株柳，拂地摇风久。\\n佳人不忍折，怅望回纤手。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3092', 'poem', '唐', '王维', '送刘司直赴安西', '绝域阳关道，胡沙与塞尘。\\n三春时有雁，万里少行人。\\n苜蓿随天马，葡萄逐汉臣。\\n当令外国惧，不敢觅和亲。', '2018-03-09 15:33:06');
INSERT INTO `t_poem` VALUES ('3093', 'poem', '唐', '顾况', '石上藤', '空山无鸟迹，何物如人意。\\n委曲结绳文，离披草书字。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3094', 'poem', '唐', '王维', '冬夜书怀', '冬宵寒且永，夜漏宫中发。\\n草白霭繁霜，木衰澄清月。\\n丽服映颓颜，朱灯照华发。\\n汉家方尚少，顾影惭朝谒。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3095', 'poem', '唐', '陆龟蒙', '北渡', '江客柴门枕浪花，鸣机寒橹任呕哑。\\n轻舟过去真堪画，惊起鸬鹚一阵斜。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3096', 'poem', '唐', '吴筠', '高士咏·汉阴丈人', '野哉汉阴叟，好古遂忘机。\\n抱瓮诚亦勤，守朴全道微。\\n子贡初不达，听言识其非。\\n已为风波人，怳惘失所依。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3097', 'poem', '唐', '来鹄', '闻蝉', '绿槐阴里一声新，雾薄风轻力未匀。\\n莫道闻时总惆怅，有愁人有不愁人。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3098', 'poem', '唐', '李咸用', '关山月', '离离天际云，皎皎关山月。\\n羌笛一声来，白尽征人发。\\n嘹唳孤鸿高，萧索悲风发。\\n雪压塞尘清，雕落沙场阔。\\n何当胡无人，荷戈朝凤阙。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3099', 'poem', '唐', '殷遥', '春晚山行', '寂历青山晚，山行趣不稀。\\n野花成子落，江燕引雏飞。\\n暗草薰苔径，晴杨扫石矶。\\n俗人犹语此，余亦转忘归。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3100', 'poem', '唐', '鲍溶', '杂曲歌辞·壮士行', '西方太白高，壮士羞病死。\\n心知报恩处，对酒歌易水。\\n砂鸿嗥天末，横剑别妻子。\\n苏武执节归，班超束书起。\\n山河不足重，重在遇知己。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3101', 'poem', '唐', '李贺', '白虎行', '火乌日暗崩腾云，秦皇虎视苍生群。\\n烧书灭国无暇日，铸剑佩玦惟将军。\\n玉坛设醮思冲天，一世二世当万年。\\n烧丹未得不死药，拏舟海上寻神仙。\\n鲸鱼张鬣海波沸，耕人半作征人鬼。\\n雄豪气猛如焰烟，无人为决天河水。\\n谁最苦兮谁最苦，报人义士深相许。\\n渐离击筑荆卿歌，荆卿把酒燕丹语。\\n剑如霜兮胆如铁，出燕城兮望秦月。\\n天授秦封祚未移，衮龙衣点荆卿血。\\n朱旗卓地白虎死，汉皇知是真天子。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3102', 'poem', '唐', '李白', '峨眉山月歌送蜀僧晏入中京', '我在巴东三峡时，西看明月忆峨眉。\\n月出峨眉照沧海，与人万里长相随。\\n黄鹤楼前月华白，此中忽见峨眉客。\\n峨眉山月还送君，风吹西到长安陌。\\n长安大道横九天，峨眉山月照秦川。\\n黄金狮子乘高座，白玉麈尾谈重玄。\\n我似浮云殢吴越，君逢圣主游丹阙。\\n一振高名满帝都，归时还弄峨眉月。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3103', 'poem', '唐', '李白', '凤凰曲', '嬴女吹玉箫，吟弄天上春。\\n青鸾不独去，更有携手人。\\n影灭彩云断，遗声落西秦。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3104', 'poem', '唐', '白居易', '感兴二首', '吉凶祸福有来由，但要深知不要忧。\\n只见火光烧润屋，不闻风浪覆虚舟。\\n名为公器无多取，利是身灾合少求。\\n虽异匏瓜难不食，大都食足早宜休。\\n鱼能深入宁忧钓，鸟解高飞岂触罗。\\n热处先争炙手去，悔时其奈噬脐何。\\n尊前诱得猩猩血，幕上偷安燕燕窠。\\n我有一言君记取，世间自取苦人多。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3105', 'poem', '唐', '李商隐', '俳谐', '短顾何由遂，迟光且莫惊。\\n莺能歌子夜，蝶解舞宫城。\\n柳讶眉双浅，桃猜粉太轻。\\n年华有情状，吾岂怯平生。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3106', 'poem', '唐', '张说', '咏瓢', '美酒酌悬瓢，真淳好相映。\\n蜗房卷堕首，鹤颈抽长柄。\\n雅色素而黄，虚心轻且劲。\\n岂无雕刻者，贵此成天性。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3107', 'poem', '唐', '鲍溶', '壮士行', '西方太白高，壮士羞病死。\\n心知报恩处，对酒歌易水。\\n沙鸿嗥天末，横剑别妻子。\\n苏武执节归，班超束书起。\\n山河不足重，重在遇知己。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3108', 'poem', '唐', '韦庄', '中酒', '南邻酒熟爱相招，蘸甲倾来绿满瓢。\\n一醉不知三日事，任他童稚作渔樵。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3109', 'poem', '唐', '刘长卿', '喜李翰自越至', '南浮沧海上，万里到吴台。\\n久别长相忆，孤舟何处来。\\n春风催客醉，江月向人开。\\n羡尔无羁束，沙鸥独不猜。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3110', 'poem', '唐', '白居易', '重伤小女子', '学人言语凭床行，嫩似花房脆似琼。\\n才知恩爱迎三岁，未辨东西过一生。\\n汝异下殇应杀礼，吾非上圣讵忘情。\\n伤心自叹鸠巢拙，长堕春雏养不成。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3111', 'poem', '唐', '杜甫', '诸将五首', '汉朝陵墓对南山，胡虏千秋尚入关。\\n昨日玉鱼蒙葬地，早时金碗出人间。\\n见愁汗马西戎逼，曾闪朱旗北斗殷。\\n多少材官守泾渭，将军且莫破愁颜。\\n韩公本意筑三城，拟绝天骄拔汉旌。\\n岂谓尽烦回纥马，翻然远救朔方兵。\\n胡来不觉潼关隘，龙起犹闻晋水清。\\n独使至尊忧社稷，诸君何以答升平。\\n洛阳宫殿化为烽，休道秦关百二重。\\n沧海未全归禹贡，蓟门何处尽尧封。\\n朝廷衮职虽多预，天下军储不自供。\\n稍喜临边王相国，肯销金甲事春农。\\n回首扶桑铜柱标，冥冥氛祲未全销。\\n越裳翡翠无消息，南海明珠久寂寥。\\n殊锡曾为大司马，总戎皆插侍中貂。\\n炎风朔雪天王地，只在忠臣翊圣朝。\\n锦江春色逐人来，巫峡清秋万壑哀。\\n正忆往时严仆射，共迎中使望乡台。\\n主恩前后三持节，军令分明数举杯。\\n西蜀地形天下险，安危须仗出群材。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3112', 'poem', '唐', '吴融', '闻歌', '贯珠一夜奏累累，尽是荀家旧教词。\\n落尽梁尘肠不断，九原谁报小怜知。', '2018-03-09 15:33:17');
INSERT INTO `t_poem` VALUES ('3113', 'poem', '唐', '钱起', '访李卿不遇', '画戟朱楼映晚霞，高梧寒柳度飞鸦。\\n门前不见归轩至，城上愁看落日斜。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3114', 'poem', '唐', '韩偓', '梦仙', '紫霄宫阙五云芝，九级坛前再拜时。\\n鹤舞鹿眠春草远，山高水阔夕阳迟。\\n每嗟阮肇归何速，深羡张骞去不疑。\\n澡练纯阳功力在，此心唯有玉皇知。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3115', 'poem', '唐', '杜甫', '九日登梓州城', '客心惊暮序，宾雁下襄州。\\n共赏重阳节，言寻戏马游。\\n湖风秋戍柳，江雨暗山楼。\\n且酌东篱菊，聊祛南国愁。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3116', 'poem', '唐', '刘禹锡', '罢郡归洛阳闲居', '十年江海守，旦夕有归心。\\n及此西还日，空成东武吟。\\n花间数杯酒，月下一张琴。\\n闻说功名事，依前惜寸阴。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3117', 'poem', '唐', '元稹', '高荷', '种藕百馀根，高荷才四叶。飐闪碧云扇，团圆青玉叠。\\n亭亭自抬举，鼎鼎难藏擫，不学著水荃，一生长怗怗，', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3118', 'poem', '唐', '陆龟蒙', '方响', '击霜寒玉乱丁丁，花底秋风拂坐生。\\n王母闲看汉天子，满猗兰殿佩环声。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3119', 'poem', '唐', '项斯', '梦仙', '昨宵魂梦到仙津，得见蓬山不死人。\\n云叶许裁成野服，玉浆教吃润愁身。\\n红楼近月宜寒水，绿杏摇风占古春。\\n次第引看行未遍，浮光牵入世间尘。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3120', 'poem', '唐', '曹邺', '过白起墓', '夷陵火焰灭，长平生气低。\\n将军临老病，赐剑咸阳西。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3121', 'poem', '唐', '司空图', '秋思', '身病时亦危，逢秋多恸哭。\\n风波一摇荡，天地几翻覆。\\n孤萤出荒池，落叶穿破屋。\\n势利长草草，何人访幽独。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3122', 'poem', '唐', '李峤', '海', '习坎疏丹壑，朝宗合紫微。\\n三山巨鳌涌，万里大鹏飞。\\n楼写春云色，珠含明月辉。\\n会因添雾露，方逐众川归。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3123', 'poem', '唐', '王维', '听百舌鸟', '上兰门外草萋萋，未央宫中花里栖。\\n亦有相随过御苑，不知若个向金堤。\\n入春解作千般语，拂曙能先百鸟啼。\\n万户千门应觉晓，建章何必听鸣鸡。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3124', 'poem', '唐', '柳宗元', '郊居岁暮', '屏居负山郭，岁暮惊离索。\\n野迥樵唱来，庭空烧烬落。\\n世纷因事远，心赏随年薄。\\n默默谅何为，徒成今与昨。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3125', 'poem', '唐', '李中', '离家', '送别人归春日斜，独鞭羸马指天涯。\\n月生江上乡心动，投宿匆忙近酒家。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3126', 'poem', '唐', '杜牧', '汴河阻冻', '千里长河初冻时，玉珂瑶佩响参差。\\n浮生却似冰底水，日夜东流人不知。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3127', 'poem', '唐', '白居易', '郡中', '乡路音信断，山城日月迟。\\n欲知州近远，阶前摘荔枝。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3128', 'poem', '唐', '李峤', '羊', '绝饮惩浇俗，行驱梦逸材。\\n仙人拥石去，童子驭车来。\\n夜玉含星动，晨毡映雪开。\\n莫言鸿渐力，长牧上林隈。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3129', 'poem', '唐', '许浑', '闻歌', '新秋弦管清，时转遏云声。\\n曲尽不知处，月高风满城。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3130', 'poem', '唐', '李白', '赠段七娘', '罗袜凌波生网尘，那能得计访情亲。\\n千杯绿酒何辞醉，一面红妆恼杀人。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3131', 'poem', '唐', '韦应物', '开元观怀旧寄李二、韩二、裴四兼呈崔郎中、严家令', '宿昔清都燕，分散各西东。\\n车马行迹在，霜雪竹林空。\\n方轸故物念，谁复一樽同。\\n聊披道书暇，还此听松风。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3132', 'poem', '唐', '韩偓', '意绪', '绝代佳人何寂寞，梨花未发梅花落。\\n东风吹雨入西园，银线千条度虚阁。\\n脸粉难匀蜀酒浓，口脂易印吴绫薄。\\n娇饶意态不胜羞，愿倚郎肩永相著。', '2018-03-09 15:33:27');
INSERT INTO `t_poem` VALUES ('3133', 'poem', '唐', '李商隐', '闻歌', '敛笑凝眸意欲歌，高云不动碧嵯峨。\\n铜台罢望归何处，玉辇忘还事几多。\\n青冢路边南雁尽，细腰宫里北人过。\\n此声肠断非今日，香灺灯光奈尔何。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3134', 'poem', '唐', '宋之问', '寒食江州满塘驿', '去年上巳洛桥边，今年寒食庐山曲。\\n遥怜巩树花应满，复见吴洲草新绿。\\n吴洲春草兰杜芳，感物思归怀故乡。\\n驿骑明朝发何处？猿声今夜断君肠。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3135', 'poem', '唐', '李白', '大堤曲', '汉水临襄阳，花开大堤暖。\\n佳期大堤下，泪向南云满。\\n春风无复情，吹我梦魂散。\\n不见眼中人，天长音信断。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3136', 'poem', '唐', '黄滔', '司马长卿', '一自梁园失意回，无人知有掞天才。\\n汉宫不锁陈皇后，谁肯量金买赋来。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3137', 'poem', '唐', '李白', '白鹭鸶', '白鹭下秋水，孤飞如坠霜。\\n心闲且未去，独立沙洲傍。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3138', 'poem', '唐', '徐彦伯', '采莲曲', '妾家越水边，摇艇入江烟。\\n既觅同心侣，复采同心莲。\\n折藕丝能脆，开花叶正圆。\\n春歌弄明月，归棹落花前。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3139', 'poem', '唐', '雍裕之', '剪彩花', '敢竞桃李色，自呈刀尺功。\\n蝶犹迷剪翠，人岂辨裁红。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3140', 'poem', '唐', '杜荀鹤', '闲居书事', '竹门茅屋带村居，数亩生涯自有馀。\\n鬓白只应秋炼句，眼昏多为夜抄书。\\n雁惊风浦渔灯动，猿叫霜林橡实疏。\\n待得功成即西去，时清不问命何如。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3141', 'poem', '唐', '张泌', '边上·戍楼吹角起', '戍楼吹角起征鸿，猎猎寒旌背晚风。\\n千里暮烟愁不尽，一川秋草恨无穷。\\n山河惨澹关城闭，人物萧条市井空。\\n只此旅魂招未得，更堪回首夕阳中。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3142', 'poem', '唐', '李白', '千里思', '李陵没胡沙，苏武还汉家。\\n迢迢五原关，朔雪乱边花。\\n一去隔绝国，思归但长嗟。\\n鸿雁向西北，因书报天涯。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3143', 'poem', '唐', '葛鸦儿', '怀良人', '蓬鬓荆钗世所稀，布裙犹是嫁时衣。\\n胡麻好种无人种，正是归时不见归。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3144', 'poem', '唐', '司空图', '山鹊', '多惊本为好毛衣，只赖人怜始却归。\\n众鸟自知颜色减，妒他偏向眼前飞。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3145', 'poem', '唐', '王勃', '麻平晚行', '百年怀土望，千里倦游情。\\n高低寻戍道，远近听泉声。\\n涧叶才分色，山花不辨名。\\n羁心何处尽，风急暮猿清。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3146', 'poem', '唐', '周昙', '春秋战国门·哀公', '贤为邻用国忧危，庙算无非委艳奇。\\n两叶翠娥春乍展，一毛须去不难吹。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3147', 'poem', '唐', '王建', '小松', '小松初数尺，未有直生枝。\\n闲即傍边立，看多长却迟。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3148', 'poem', '唐', '李白', '出自蓟北门行', '虏阵横北荒，胡星曜精芒。\\n羽书速惊电，烽火昼连光。\\n虎竹救边急，戎车森已行。\\n明主不安席，按剑心飞扬。\\n推毂出猛将，连旗登战场。\\n兵威冲绝漠，杀气凌穹苍。\\n列卒赤山下，开营紫塞傍。\\n途冬沙风紧，旌旗飒凋伤。\\n画角悲海月，征衣卷天霜。\\n挥刃斩楼兰，弯弓射贤王。\\n单于一平荡，种落自奔亡。\\n收功报天子，行歌归咸阳。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3149', 'poem', '唐', '袁不约', '离家', '步步远晨昏，凄心出里门。\\n见乌唯有泪，看雁更伤魂。\\n宿酒宁辞醉，回书讳苦言。\\n野人应怪笑，不解爱田园。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3150', 'poem', '唐', '刘禹锡', '乐天见示伤微之敦诗晦叔三君子皆有深分因成是诗以寄', '吟君叹逝双绝句，使我伤怀奏短歌。\\n世上空惊故人少，集中惟觉祭文多。\\n芳林新叶催陈叶，流水前波让后波。\\n万古到今同此恨，闻琴泪尽欲如何。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3151', 'poem', '唐', '白居易', '新制布裘', '桂布白似雪，吴绵软于云。\\n布重绵且厚，为裘有馀温。\\n朝拥坐至暮，夜覆眠达晨。\\n谁知严冬月，支体暖如春。\\n中夕忽有念，抚裘起逡巡。\\n丈夫贵兼济，岂独善一身。\\n安得万里裘，盖裹周四垠。\\n稳暖皆如我，天下无寒人。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3152', 'poem', '唐', '吴融', '桃花', '满树和娇烂漫红，万枝丹彩灼春融。\\n何当结作千年实，将示人间造化工。', '2018-03-09 15:33:49');
INSERT INTO `t_poem` VALUES ('3153', 'poem', '唐', '王勃', '长柳', '晨征犯烟磴，夕憩在云关。\\n晚风清近壑，新月照澄湾。\\n郊童樵唱返，津叟钓歌还。\\n客行无与晤，赖此释愁颜。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3154', 'poem', '唐', '王昌龄', '相和歌辞·长歌行', '旷野饶悲风，飕飕黄蒿草。\\n系马停白杨，谁知我怀抱。\\n所是同袍者，相逢尽衰老。\\n况登汉家陵，南望长安道。\\n下有枯树根，上有鼷鼠窠。\\n高皇子孙尽，千古无人过。\\n宝玉频发掘，精灵其奈何。\\n人生须达命，有酒且长歌。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3155', 'poem', '唐', '柳宗元', '过衡山见新花开却寄弟', '故国名园久别离，今朝楚树发南枝。\\n晴天归路好相逐，正是峰前回雁时。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3156', 'poem', '唐', '李群玉', '引水行', '一条寒玉走秋泉，引出深萝洞口烟。\\n十里暗流声不断，行人头上过潺湲。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3157', 'poem', '唐', '黄滔', '秋思', '碧嶂猿啼夜，新秋雨霁天。\\n谁人爱明月，露坐洞庭船。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3158', 'poem', '唐', '贺知章', '句', '落花真好些，一醉一回颠。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3159', 'poem', '唐', '岑参', '醉戏窦子美人', '朱唇一点桃花殷，宿妆娇羞偏髻鬟。\\n细看只似阳台女，醉著莫许归巫山。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3160', 'poem', '唐', '李白', '东武吟', '好古笑流俗，素闻贤达风。\\n方希佐明主，长揖辞成功。\\n白日在高天，回光烛微躬。\\n恭承凤凰诏，欻起云萝中。\\n清切紫霄迥，优游丹禁通。\\n君王赐颜色，声价凌烟虹。\\n乘舆拥翠盖，扈从金城东。\\n宝马丽绝景，锦衣入新丰。\\n依岩望松雪，对酒鸣丝桐。\\n因学扬子云，献赋甘泉宫。\\n天书美片善，清芬播无穷。\\n归来入咸阳，谈笑皆王公。\\n一朝去金马，飘落成飞蓬。\\n宾客日疏散，玉樽亦已空。\\n才力犹可倚，不惭世上雄。\\n闲作东武吟，曲尽情未终。\\n书此谢知己，吾寻黄绮翁。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3161', 'poem', '唐', '王绩', '在京思故园见乡人问', '旅泊多年岁，老去不知回。\\n忽逢门前客，道发故乡来。\\n敛眉俱握手，破涕共衔杯。\\n殷勤访朋旧，屈曲问童孩。\\n衰宗多弟侄，若个赏池台。\\n旧园今在否，新树也应栽。\\n柳行疏密布，茅斋宽窄裁。\\n经移何处竹，别种几株梅。\\n渠当无绝水，石计总生苔。\\n院果谁先熟，林花那后开。\\n羁心只欲问，为报不须猜。\\n行当驱下泽，去剪故园莱。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3162', 'poem', '唐', '周朴', '春日游北园寄韩侍郎', '灼灼春园晚色分，露珠千点映寒云。\\n多情舞蝶穿花去，解语流莺隔水闻。\\n冷酒杯中宜泛滟，暖风林下自氛氲。\\n仙桃不肯全开拆，应借馀芳待使君。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3163', 'poem', '唐', '杜牧', '梅', '轻盈照溪水，掩敛下瑶台。\\n妒雪聊相比，欺春不逐来。\\n偶同佳客见，似为冻醪开。\\n若在秦楼畔，堪为弄玉媒。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3164', 'poem', '唐', '杜甫', '月圆', '孤月当楼满，寒江动夜扉。\\n委波金不定，照席绮逾依。\\n未缺空山静，高悬列宿稀。\\n故园松桂发，万里共清辉。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3165', 'poem', '唐', '杜甫', '征夫', '十室几人在，千山空自多。\\n路衢唯见哭，城市不闻歌。\\n漂梗无安地，衔枚有荷戈。\\n官军未通蜀，吾道竟如何。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3166', 'poem', '唐', '杜牧', '故洛阳城有感', '一片宫墙当道危，行人为尔去迟迟。\\n筚圭苑里秋风后，平乐馆前斜日时。\\n锢党岂能留汉鼎，清谈空解识胡儿。\\n千烧万战坤灵死，惨惨终年鸟雀悲。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3167', 'poem', '唐', '李世民', '望终南山', '重峦俯渭水，碧嶂插遥天。\\n出红扶岭日，入翠贮岩烟。\\n叠松朝若夜，复岫阙疑全。\\n对此恬千虑，无劳访九仙。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3168', 'poem', '唐', '白居易', '江楼宴别', '楼中别曲催离酌，灯下红裙间绿袍。\\n缥缈楚风罗绮薄，铮鏦越调管弦高。\\n寒流带月澄如镜，夕吹和霜利似刀。\\n樽酒未空欢未尽，舞腰歌袖莫辞劳。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3169', 'poem', '唐', '白居易', '山枇杷', '深山老去惜年华，况对东溪野枇杷。\\n火树风来翻绛焰，琼枝日出晒红纱。\\n回看桃李都无色，映得芙蓉不是花。\\n争奈结根深石底，无因移得到人家。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3170', 'poem', '唐', '元稹', '茅舍', '楚俗不理居，居人尽茅舍。\\n茅苫竹梁栋，茅疏竹仍罅。\\n边缘堤岸斜，诘屈檐楹亚。\\n篱落不蔽肩，街衢不容驾。\\n南风五月盛，时雨不来下。\\n竹蠹茅亦干，迎风自焚灺。\\n防虞集邻里，巡警劳昼夜。\\n遗烬一星然，连延祸相嫁。\\n号呼怜谷帛，奔走伐桑柘。\\n旧架已新焚，新茅又初架。\\n前日洪州牧，念此常嗟讶。\\n牧民未及久，郡邑纷如化。\\n峻邸俨相望，飞甍远相跨。\\n旗亭红粉泥，佛庙青鸳瓦。\\n斯事才未终，斯人久云谢。\\n有客自洪来，洪民至今藉。\\n惜其心太亟，作役无容暇。\\n台观亦已多，工徒稍冤咤。\\n我欲他郡长，三时务耕稼。\\n农收次邑居，先室后台榭。\\n启闭既及期，公私亦相借。\\n度材无强略，庀役有定价。\\n不使及僭差，粗得御寒夏。\\n火至殊陈郑，人安极嵩华。\\n谁能继此名，名流袭兰麝。\\n五袴有前闻，斯言我非诈。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3171', 'poem', '唐', '司空图', '诗品二十四则·精神', '欲返不尽，相期与来。明漪绝底，奇花初胎。\\n青春鹦鹉，杨柳池台。碧山人来，清酒满杯。\\n生气远出，不著死灰。妙造自然，伊谁与裁？', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3172', 'poem', '唐', '白居易', '有木诗八首', '有木名弱柳，结根近清池。\\n风烟借颜色，雨露助华滋。\\n峨峨白雪花，袅袅青丝枝。\\n渐密阴自庇，转高梢四垂。\\n截枝扶为杖，软弱不自持。\\n折条用樊圃，柔脆非其宜。\\n为树信可玩，论材何所施。\\n可惜金堤地，栽之徒尔为。\\n有木名樱桃，得地早滋茂。\\n叶密独承日，花繁偏受露。\\n迎风闇摇动，引鸟潜来去。\\n鸟啄子难成，风来枝莫住。\\n低软易攀玩，佳人屡回顾。\\n色求桃李饶，心向松筠妒。\\n好是映墙花，本非当轩树。\\n所以姓萧人，曾为伐樱赋。\\n有木秋不凋，青青在江北。\\n谓为洞庭橘，美人自移植。\\n上受顾盼恩，下勤浇溉力。\\n实成乃是枳，臭苦不堪食。\\n物有似是者，真伪何由识。\\n美人默无言，对之长叹息。\\n中含害物意，外矫凌霜色。\\n仍向枝叶间，潜生刺如棘。\\n有木名杜梨，阴森覆丘壑。\\n心蠹已空朽，根深尚盘薄。\\n狐媚言语巧，鸟妖声音恶。\\n凭此为巢穴，往来互栖托。\\n四傍五六本，叶枝相交错。\\n借问因何生，秋风吹子落。\\n为长社坛下，无人敢芟斫。\\n几度野火来，风回烧不著。\\n有木香苒苒，山头生一蕟。\\n主人不知名，移种近轩闼。\\n爱其有芳味，因以调麹糵。\\n前后曾饮者，十人无一活。\\n岂徒悔封植，兼亦误采掇。\\n试问识药人，始知名野葛。\\n年深已滋蔓，刀斧不可伐。\\n何时猛风来，为我连根拔。\\n有木名水柽，远望青童童。\\n根株非劲挺，柯叶多蒙笼。\\n彩翠色如柏，鳞皴皮似松。\\n为同松柏类，得列嘉树中。\\n枝弱不胜雪，势高常惧风。\\n雪压低还举，风吹西复东。\\n柔芳甚杨柳，早落先梧桐。\\n惟有一堪赏，中心无蠹虫。\\n有木名凌霄，擢秀非孤标。\\n偶依一株树，遂抽百尺条。\\n托根附树身，开花寄树梢。\\n自谓得其势，无因有动摇。\\n一旦树摧倒，独立暂飘飖。\\n疾风从东起，吹折不终朝。\\n朝为拂云花，暮为委地樵。\\n寄言立身者，勿学柔弱苗。\\n有木名丹桂，四时香馥馥。\\n花团夜雪明，叶翦春云绿。\\n风影清似水，霜枝冷如玉。\\n独占小山幽，不容凡鸟宿。\\n匠人爱芳直，裁截为厦屋。\\n干细力未成，用之君自速。\\n重任虽大过，直心终不曲。\\n纵非梁栋材，犹胜寻常木。', '2018-03-09 15:33:53');
INSERT INTO `t_poem` VALUES ('3173', 'poem', '唐', '杜甫', '客居', '客居所居堂，前江后山根。\\n下堑万寻岸，苍涛郁飞翻。\\n葱青众木梢，邪竖杂石痕。\\n子规昼夜啼，壮士敛精魂。\\n峡开四千里，水合数百源。\\n人虎相半居，相伤终两存。\\n蜀麻久不来，吴盐拥荆门。\\n西南失大将，商旅自星奔。\\n今又降元戎，已闻动行轩。\\n舟子候利涉，亦凭节制尊。\\n我在路中央，生理不得论。\\n卧愁病脚废，徐步视小园。\\n短畦带碧草，怅望思王孙。\\n凤随其皇去，篱雀暮喧繁。\\n览物想故国，十年别荒村。\\n日暮归几翼，北林空自昏。\\n安得覆八溟，为君洗乾坤。\\n稷契易为力，犬戎何足吞。\\n儒生老无成，臣子忧四番。\\n箧中有旧笔，情至时复援。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3174', 'poem', '唐', '卢仝', '人日立春', '春度春归无限春，今朝方始觉成人。\\n从今克己应犹及，颜与梅花俱自新。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3175', 'poem', '唐', '子兰', '蝉二首', '独蝉初唱古槐枝，委曲悲凉断续迟。\\n雨后忽闻谁最苦，异乡孤馆忆家时。\\n衰柳蝉吟旁浊河，正当残日角声和。\\n寻常不足少愁思，此际闻时愁更多。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3176', 'poem', '唐', '温庭筠', '题柳', '杨柳千条拂面丝，绿烟金穗不胜吹。\\n香随静婉歌尘起，影伴娇娆舞袖垂。\\n羌管一声何处曲，流莺百啭最高枝。\\n千门九陌花如雪，飞过宫墙两自知。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3177', 'poem', '唐', '鱼玄机', '游崇真观南楼，睹新及第题名处', '云峰满目放春晴，历历银钩指下生。\\n自恨罗衣掩诗句，举头空羡榜中名。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3178', 'poem', '唐', '王维', '与卢员外象过崔处士兴宗林亭', '绿树重阴盖四邻，青苔日厚自无尘。\\n科头箕踞长松下，白眼看他世上人。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3179', 'poem', '唐', '元稹', '使东川·南秦雪', '帝城寒尽临寒食，骆谷春深未有春。\\n才见岭头云似盖，已惊岩下雪如尘。\\n千峰笋石千株玉，万树松萝万朵银。\\n飞鸟不飞猿不动，青骢御史上南秦。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3180', 'poem', '唐', '皎然', '南池杂咏五首·溪云', '舒卷意何穷，萦流复带空。\\n有形不累物，无迹去随风。\\n莫怪长相逐，飘然与我同。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3181', 'poem', '唐', '杜甫', '东津送韦讽摄阆州录事', '闻说江山好，怜君吏隐兼。\\n宠行舟远泛，怯别酒频添。\\n推荐非承乏，操持必去嫌。\\n他时如按县，不得慢陶潜。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3182', 'poem', '唐', '韩偓', '离家', '八月初长夜，千山第一程。\\n款颜唯有梦，怨泣却无声。\\n祖席诸宾散，空郊匹马行。\\n自怜非达识，局促为浮名。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3183', 'poem', '唐', '张籍', '洛阳行', '洛阳宫阙当中州，城上峨峨十二楼。\\n翠华西去几时返，枭巢乳鸟藏蛰燕。\\n御门空锁五十年，税彼农夫修玉殿。\\n六街朝暮鼓冬冬，禁兵持戟守空宫。\\n百官月月拜章表，驿使相续长安道。\\n上阳宫树黄复绿，野豺入苑食麋鹿。\\n陌上老翁双泪垂，共说武皇巡幸时。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3184', 'poem', '唐', '杜甫', '苏端、薛复筵简薛华醉歌', '文章有神交有道，端复得之名誉早。爱客满堂尽豪翰，\\n开筵上日思芳草。安得健步移远梅，乱插繁花向晴昊。\\n千里犹残旧冰雪，百壶且试开怀抱。垂老恶闻战鼓悲，\\n急觞为缓忧心捣。少年努力纵谈笑，看我形容已枯槁。\\n坐中薛华善醉歌，歌辞自作风格老。近来海内为长句，\\n汝与山东李白好。何刘沈谢力未工，才兼鲍昭愁绝倒。\\n诸生颇尽新知乐，万事终伤不自保。气酣日落西风来，\\n愿吹野水添金杯。如渑之酒常快意，亦知穷愁安在哉。\\n忽忆雨时秋井塌，古人白骨生青苔，如何不饮令心哀。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3185', 'poem', '唐', '郑谷', '赠圆昉公（昉，蜀僧。僖宗幸蜀，昉坚免紫衣）', '天阶让紫衣，冷格鹤犹卑。\\n道胜嫌名出，身闲觉老迟。\\n晚香延宿火，寒磬度高枝。\\n长说长松寺，他年与我期。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3186', 'poem', '唐', '张仲素', '春闺思', '袅袅城边柳，青青陌上桑。\\n提笼忘采叶，昨夜梦渔阳。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3187', 'poem', '唐', '李白', '赠任城卢主簿', '海鸟知天风，窜身鲁门东。\\n临觞不能饮，矫翼思凌空。\\n钟鼓不为乐，烟霜谁与同。\\n归飞未忍去，流泪谢鸳鸿。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3188', 'poem', '唐', '杜甫', '因崔五侍御寄高彭州一绝', '百年已过半，秋至转饥寒。\\n为问彭州牧，何时救急难。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3189', 'poem', '唐', '沈佺期', '邙山', '北邙山上列坟茔，万古千秋对洛城。\\n城中日夕歌钟起，山上唯闻松柏声。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3190', 'poem', '唐', '王昌龄', '梁苑', '梁园秋竹古时烟，城外风悲欲暮天。\\n万乘旌旗何处在，平台宾客有谁怜。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3191', 'poem', '唐', '吴融', '江树', '终日冲奔浪，何年坠乱风？谢公堪入咏，目极在云中。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3192', 'poem', '唐', '孟宾于', '公子行', '锦衣红夺彩霞明，侵晓春游向野庭。\\n不识农夫辛苦力，骄骢蹋烂麦青春。', '2018-03-09 15:34:05');
INSERT INTO `t_poem` VALUES ('3193', 'poem', '唐', '杜甫', '羌村', '峥嵘赤云西，日脚下平地。\\n柴门鸟雀噪，归客千里至。\\n妻孥怪我在，惊定还拭泪。\\n世乱遭飘荡，生还偶然遂。\\n邻人满墙头，感叹亦歔欷。\\n夜阑更秉烛，相对如梦寐。\\n晚岁迫偷生，还家少欢趣。\\n娇儿不离膝，畏我复却去。\\n忆昔好追凉，故绕池边树。\\n萧萧北风劲，抚事煎百虑。\\n赖知禾黍收，已觉糟床注。\\n如今足斟酌，且用慰迟暮。\\n群鸡正乱叫，客至鸡斗争。\\n驱鸡上树木，始闻叩柴荆。\\n父老四五人，问我久远行。\\n手中各有携，倾榼浊复清。\\n莫辞酒味薄，黍地无人耕。\\n兵革既未息，儿童尽东征。\\n请为父老歌，艰难愧深情。\\n歌罢仰天叹，四座泪纵横。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3194', 'poem', '唐', '韩愈', '戏题牡丹', '幸自同开俱隐约，何须相倚斗轻盈。\\n陵晨并作新妆面，对客偏含不语情。\\n双燕无机还拂掠，游蜂多思正经营。\\n长年是事皆抛尽，今日栏边暂眼明。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3195', 'poem', '唐', '孟郊', '巫山曲', '巴江上峡重复重，阳台碧峭十二峰。\\n荆王猎时逢暮雨，夜卧高丘梦神女。\\n轻红流烟湿艳姿，行云飞去明星稀。\\n目极魂断望不见，猿啼三声泪滴衣。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3196', 'poem', '唐', '张籍', '塞下曲', '边州八月修城堡，候骑先烧碛中草。\\n胡风吹沙度陇飞，陇头林木无北枝。\\n将军阅兵青塞下，鸣鼓逢逢促猎围。\\n天寒山路石断裂，白日不销帐上雪。\\n乌孙国乱多降胡，诏使名王持汉节。\\n年年征战不得闲，边人杀尽唯空山。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3197', 'poem', '唐', '李白', '走笔赠独孤驸马', '都尉朝天跃马归，香风吹人花乱飞。\\n银鞍紫鞚照云日，左顾右盼生光辉。\\n是时仆在金门里，待诏公车谒天子。\\n长揖蒙垂国士恩，壮心剖出酬知己。\\n一别蹉跎朝市间，青云之交不可攀。\\n倘其公子重回顾，何必侯嬴长抱关。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3198', 'poem', '唐', '薛逢', '观猎', '马缩寒毛鹰落膘，角弓初暖箭新调。\\n平原踏尽无禽出，竟日翻身望碧霄。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3199', 'poem', '唐', '李商隐', '月', '池上与桥边，难忘复可怜。\\n帘开最明夜，簟卷已凉天。\\n流处水花急，吐时云叶鲜。\\n姮娥无粉黛，只是逞婵娟。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3200', 'poem', '唐', '鱼玄机', '早秋', '嫩菊含新彩，远山闲夕烟。\\n凉风惊绿树，清韵入朱弦。\\n思妇机中锦，征人塞外天。\\n雁飞鱼在水，书信若为传。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3201', 'poem', '唐', '张祜', '杂曲歌辞·上巳乐', '猩猩血彩系头标，天上齐声举画桡。\\n却是内人争意切，六宫罗袖一时招。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3202', 'poem', '唐', '李舜弦', '钓鱼不得', '尽日池边钓锦鳞，芰荷香里暗消魂。\\n依稀纵有寻香饵，知是金钩不肯吞。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3203', 'poem', '唐', '司空图', '赠圆昉公（昉，蜀僧。僖宗幸蜀，昉坚免紫衣）', '天阶让紫衣，冷格鹤犹卑。\\n道胜嫌名出，身闲觉老迟。\\n晓香延宿火，寒磬度高枝。\\n每说长松寺，他年与我期。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3204', 'poem', '唐', '李商隐', '子初全溪作', '全溪不可到，况复尽馀醅。\\n汉苑生春水，昆池换劫灰。\\n战蒲知雁唼，皱月觉鱼来。\\n清兴恭闻命，言诗未敢回。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3205', 'poem', '唐', '贯休', '晚望', '落日碧江静，莲唱清且闲。\\n更寻花发处，借月过前湾。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3206', 'poem', '唐', '王適', '江滨梅', '忽见寒梅树，开花汉水滨。\\n不知春色早，疑是弄珠人。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3207', 'poem', '唐', '吕温', '孟冬蒲津关河亭作', '息驾非穷途，未济岂迷津。\\n独立大河上，北风来吹人。\\n雪霜自兹始，草木当更新。\\n严冬不肃杀，何以见阳春。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3208', 'poem', '唐', '李益', '夜上西城听梁州曲二首', '行人夜上西城宿，听唱梁州双管逐。\\n此时秋月满关山，何处关山无此曲。\\n鸿雁新从北地来，闻声一半却飞回。\\n金河戍客肠应断，更在秋风百尺台。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3209', 'poem', '唐', '韩偓', '安贫', '手风慵展一行书，眼暗休寻九局图。\\n窗里日光飞野马，案头筠管长蒲卢。\\n谋身拙为安蛇足，报国危曾捋虎须。\\n举世可能无默识，未知谁拟试齐竽。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3210', 'poem', '唐', '杜牧', '茶山下作', '春风最窈窕，日晓柳村西。\\n娇云光占岫，健水鸣分溪。\\n燎岩野花远，戛瑟幽鸟啼。\\n把酒坐芳草，亦有佳人携。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3211', 'poem', '唐', '杜甫', '暮寒', '雾隐平郊树，风含广岸波。\\n沉沉春色静，惨惨暮寒多。\\n戍鼓犹长击，林莺遂不歌。\\n忽思高宴会，朱袖拂云和。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3212', 'poem', '唐', '杜甫', '玉台观', '浩劫因王造，平台访古游。\\n彩云萧史驻，文字鲁恭留。\\n宫阙通群帝，乾坤到十洲。\\n人传有笙鹤，时过此山头。', '2018-03-09 15:34:12');
INSERT INTO `t_poem` VALUES ('3213', 'poem', '唐', '王勃', '寒夜思友三首', '久别侵怀抱，他乡变容色。\\n月下调鸣琴，相思此何极。\\n云间征思断，月下归愁切。\\n鸿雁西南飞，如何故人别。\\n朝朝翠山下，夜夜苍江曲。\\n复此遥相思，清尊湛芳绿。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3214', 'poem', '唐', '袁高', '茶山诗', '禹贡通远俗，所图在安人。\\n后王失其本，职吏不敢陈。\\n亦有奸佞者，因兹欲求伸。\\n动生千金费，日使万姓贫。\\n我来顾渚源，得与茶事亲。\\n氓辍耕农耒，采采实苦辛。\\n一夫旦当役，尽室皆同臻。\\n扪葛上欹壁，蓬头入荒榛。\\n终朝不盈掬，手足皆鳞皴。\\n悲嗟遍空山，草木为不春。\\n阴岭芽未吐，使者牒已频。\\n心争造化功，走挺糜鹿均。\\n选纳无昼夜，捣声昏继晨。\\n众工何枯栌，俯视弥伤神。\\n皇帝尚巡狩，东郊路多堙。\\n周回绕天涯，所献愈艰勤。\\n况减兵革困，重兹固疲民。\\n未知供御馀，谁合分此珍。\\n顾省忝邦守，又惭复因循。\\n茫茫沧海间，丹愤何由申。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3215', 'poem', '唐', '李商隐', '题鹅', '眠沙卧水自成群，曲岸残阳极浦云。\\n那解将心怜孔翠，羁雌长共故雄分。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3216', 'poem', '唐', '贾岛', '冬夜送人', '平明走马上村桥，花落梅溪雪未消。\\n日短天寒愁送客，楚山无限路迢迢。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3217', 'poem', '唐', '李建勋', '春词', '日高闲步下堂阶，细草春莎没绣鞋。\\n折得玫瑰花一朵，凭君簪向凤凰钗。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3218', 'poem', '唐', '李白', '田园言怀', '贾谊三年谪，班超万里侯。\\n何如牵白犊，饮水对清流。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3219', 'poem', '唐', '刘商', '画石', '苍藓千年粉绘传，坚贞一片色犹全。\\n哪知忽遇非常用，不把分铢补上天。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3220', 'poem', '唐', '权德舆', '六府诗', '金罍映玉俎，宾友纷宴喜。\\n木兰泛方塘，桂酒启皓齿。\\n水榭临空迥，酣歌当座起。\\n火云散奇峰，瑶瑟韵清徵。\\n土梗乃虚论，康庄有逸轨。\\n穀成一编书，谈笑佐天子。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3221', 'poem', '唐', '杜甫', '夔州歌十绝句', '中巴之东巴东山，江水开辟流其间。\\n白帝高为三峡镇，夔州险过百牢关。\\n白帝夔州各异城，蜀江楚峡混殊名。\\n英雄割据非天意，霸主并吞在物情。\\n群雄竞起问前朝，王者无外见今朝。\\n比讶渔阳结怨恨，元听舜日旧箫韶。\\n赤甲白盐俱刺天，闾阎缭绕接山巅。\\n枫林橘树丹青合，复道重楼锦绣悬。\\n瀼东瀼西一万家，江北江南春冬花。\\n背飞鹤子遗琼蕊，相趁凫雏入蒋牙。\\n东屯稻畦一百顷，北有涧水通青苗。\\n晴浴狎鸥分处处，雨随神女下朝朝。\\n蜀麻吴盐自古通，万斛之舟行若风。\\n长年三老长歌里，白昼摊钱高浪中。\\n忆昔咸阳都市合，山水之图张卖时。\\n巫峡曾经宝屏见，楚宫犹对碧峰疑。\\n武侯祠堂不可忘，中有松柏参天长。\\n干戈满地客愁破，云日如火炎天凉。\\n阆风玄圃与蓬壶，中有高堂天下无。\\n借问夔州压何处，峡门江腹拥城隅。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3222', 'poem', '唐', '刘沧', '晚归山居', '寥落霜空木叶稀，初行郊野思依依。\\n秋深频忆故乡事，日暮独寻荒径归。\\n山影暗随云水动，钟声潜入远烟微。\\n娟娟唯有西林月，不惜清光照竹扉。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3223', 'poem', '唐', '杜甫', '昼梦', '二月饶睡昏昏然，不独夜短昼分眠。\\n桃花气暖眼自醉，春渚日落梦相牵。\\n故乡门巷荆棘底，中原君臣豺虎边。\\n安得务农息战斗，普天无吏横索钱。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3224', 'poem', '唐', '李白', '望鹦鹉洲怀祢衡', '魏帝营八极，蚁观一祢衡。\\n黄祖斗筲人，杀之受恶名。\\n吴江赋鹦鹉，落笔超群英。\\n锵锵振金玉，句句欲飞鸣。\\n鸷鹗啄孤凤，千春伤我情。\\n五岳起方寸，隐然讵可平。\\n才高竟何施，寡识冒天刑。\\n至今芳洲上，兰蕙不忍生。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3225', 'poem', '唐', '白居易', '寓意诗五首', '豫樟生深山，七年而后知。\\n挺高二百尺，本末皆十围。\\n天子建明堂，此材独中规。\\n匠人执斤墨，采度将有期。\\n孟冬草木枯，烈火燎山陂。\\n疾风吹猛焰，从根烧到枝。\\n养材三十年，方成栋梁姿。\\n一朝为灰烬，柯叶无孑遗。\\n地虽生尔材，天不与尔时。\\n不如粪土英，犹有人掇之。\\n已矣勿重陈，重陈令人悲。\\n不悲焚烧苦，但悲采用迟。\\n赫赫京内史，炎炎中书郎。\\n昨传征拜日，恩赐颇殊常。\\n貂冠水苍玉，紫绶黄金章。\\n佩服身未暖，已闻窜遐荒。\\n亲戚不得别，吞声泣路旁。\\n宾客亦已散，门前雀罗张。\\n富贵来不久，倏如瓦沟霜。\\n权势去尤速，瞥若石火光。\\n不如守贫贱，贫贱可久长。\\n传语宦游子，且来归故乡。\\n促织不成章，提壶但闻声。\\n嗟哉虫与鸟，无实有虚名。\\n与君定交日，久要如弟兄。\\n何以示诚信，白水指为盟。\\n云雨一为别，飞沉两难并。\\n君为得风鹏，我为失水鲸。\\n音信日已疏，恩分日已轻。\\n穷通尚如此，何况死与生。\\n乃知择交难，须有知人明。\\n莫将山上松，结托水上萍。\\n翩翩两玄鸟，本是同巢燕。\\n分飞来几时，秋夏炎凉变。\\n一宿蓬荜庐，一栖明光殿。\\n偶因衔泥处，复得重相见。\\n彼矜杏梁贵，此嗟茅栋贱。\\n眼看秋社至，两处俱难恋。\\n所托各暂时，胡为相叹羡。\\n婆娑园中树，根株大合围。\\n蠢尔树间虫，形质一何微。\\n孰谓虫之微，虫蠹已无期。\\n孰谓树之大，花叶有衰时。\\n花衰夏未实，叶病秋先萎。\\n树心半为土，观者安得知。\\n借问虫何在，在身不在枝。\\n借问虫何食，食心不食皮。\\n岂无啄木鸟，觜长将何为。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3226', 'poem', '唐', '白居易', '夏夜宿直', '人少庭宇旷，夜凉风露清。\\n槐花满院气，松子落阶声。\\n寂寞挑灯坐，沉吟蹋月行。\\n年衰自无趣，不是厌承明。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3227', 'poem', '唐', '胡曾', '咏史诗·葛陂', '长房回到葛陂中，人已登真竹化龙。\\n莫道神仙难顿学，嵇生自是不遭逢。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3228', 'poem', '唐', '白居易', '友人夜访', '檐间清风簟，松下明月杯。\\n幽意正如此，况乃故人来。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3229', 'poem', '唐', '白居易', '舟夜赠内', '三声猿后垂乡泪，一叶舟中载病身。\\n莫凭水窗南北望，月明月暗总愁人。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3230', 'poem', '唐', '李白', '高句骊', '金花折风帽，白马小迟回。\\n翩翩舞广袖，似鸟海东来。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3231', 'poem', '唐', '刘禹锡', '伤桃源薛道士', '坛边松在鹤巢空，白鹿闲行旧径中。\\n手植红桃千树发，满山无主任春风。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3232', 'poem', '唐', '隐峦', '逢老人', '路逢一老翁，两鬓白如雪。\\n一里二里行，四回五回歇。', '2018-03-09 15:34:23');
INSERT INTO `t_poem` VALUES ('3233', 'poem', '唐', '司空图', '九月八日', '已是人间寂寞花，解怜寂寞傍贫家。\\n老来不得登高看，更甚残春惜岁华。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3234', 'poem', '唐', '张祜', '筝', '绰绰下云烟，微收皓腕鲜。\\n夜风生碧柱，春水咽红弦。\\n翠佩轻犹触，莺枝涩未迁。\\n芳音何更妙，清月共婵娟。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3235', 'poem', '唐', '杜牧', '赠别', '眼前迎送不曾休，相续轮蹄似水流。\\n门外若无南北路，人间应免别离愁。\\n苏秦六印归何日，潘岳双毛去值秋。\\n莫怪分襟衔泪语，十年耕钓忆沧洲。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3236', 'poem', '唐', '杜甫', '十二月一日三首', '今朝腊月春意动，云安县前江可怜。\\n一声何处送书雁，百丈谁家上水船。\\n未将梅蕊惊愁眼，要取楸花媚远天。\\n明光起草人所羡，肺病几时朝日边。\\n寒轻市上山烟碧，日满楼前江雾黄。\\n负盐出井此溪女，打鼓发船何郡郎。\\n新亭举目风景切，茂陵著书消渴长。\\n春花不愁不烂漫，楚客唯听棹相将。\\n即看燕子入山扉，岂有黄鹂历翠微。\\n短短桃花临水岸，轻轻柳絮点人衣。\\n春来准拟开怀久，老去亲知见面稀。\\n他日一杯难强进，重嗟筋力故山违。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3237', 'poem', '唐', '顾况', '芙蓉榭', '风摆莲衣干，月背鸟巢寒。\\n文鱼翻乱叶，翠羽上危栏。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3238', 'poem', '唐', '李白', '秋思', '春阳如昨日，碧树鸣黄鹂。\\n芜然蕙草暮，飒尔凉风吹。\\n天秋木叶下，月冷莎鸡悲。\\n坐愁群芳歇，白露凋华滋。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3239', 'poem', '唐', '孟郊', '赠建业契公', '师住青山寺，清华常绕身。\\n虽然到城郭，衣上不栖尘。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3240', 'poem', '唐', '贾岛', '辩士', '辩士多毁訾，不闻谈己非。猛虎恣杀暴，未尝啮妻儿。\\n此理天所感，所感当问谁。求食饲雏禽，吐出美言词。\\n善哉君子人，扬光掩瑕玼，', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3241', 'poem', '唐', '李涉', '重登滕王阁', '滕王阁上唱伊州，二十年前向此游。\\n半是半非君莫问，好山长在水长流。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3242', 'poem', '唐', '李煜', '开元乐', '心事数茎白发，生涯一片青山。\\n空山有雪相待，野路无人自还。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3243', 'poem', '唐', '李中', '早春', '一种和风至，千花未放妍。\\n草心并柳眼，长是被恩先。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3244', 'poem', '唐', '白居易', '答山驿梦', '入君旅梦来千里，闭我幽魂欲二年。\\n莫忘平生行坐处，后堂阶下竹丛前。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3245', 'poem', '唐', '施肩吾', '诮山中叟', '老人今年八十几，口中零落残牙齿。\\n天阴伛偻带嗽行，犹向岩前种松子。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3246', 'poem', '唐', '上官仪', '奉和山夜临秋', '殿帐清炎气，辇道含秋阴。\\n凄风移汉筑，流水入虞琴。\\n云飞送断雁，月上净疏林。\\n滴沥露枝响，空濛烟壑深。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3247', 'poem', '唐', '杜甫', '早花', '西京安稳未，不见一人来。\\n腊日巴江曲，山花已自开。\\n盈盈当雪杏，艳艳待春梅。\\n直苦风尘暗，谁忧容鬓催。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3248', 'poem', '唐', '李白', '拟古其九', '生者为过客，死者为归人。\\n天地一逆旅，同悲万古尘。\\n月兔空捣药，扶桑已成薪。\\n白骨寂无言，青松岂知春。\\n前後更叹息，浮荣安足珍。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3249', 'poem', '唐', '白居易', '长安送柳大东归', '白社羁游伴，青门远别离。\\n浮名相引住，归路不同归。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3250', 'poem', '唐', '杜甫', '梅雨', '南京犀浦道，四月熟黄梅。\\n湛湛长江去，冥冥细雨来。\\n茅茨疏易湿，云雾密难开。\\n竟日蛟龙喜，盘涡与岸回。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3251', 'poem', '唐', '李洞', '绣岭宫词', '春日迟迟春草绿，野棠开尽飘香玉。\\n绣岭宫前鹤发翁，犹唱开元太平曲。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3252', 'poem', '唐', '杜审言', '夏日过郑七山斋', '共有樽中好，言寻谷口来。\\n薜萝山径入，荷芰水亭开。\\n日气含残雨，云阴送晚雷。\\n洛阳钟鼓至，车马系迟回。', '2018-03-09 15:34:30');
INSERT INTO `t_poem` VALUES ('3253', 'poem', '唐', '李白', '赠内', '三百六十日，日日醉如泥。\\n虽为李白妇，何异太常妻。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3254', 'poem', '唐', '孟郊', '再下第', '一夕九起嗟，梦短不到家。\\n两度长安陌，空将泪见花。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3255', 'poem', '唐', '白居易', '重到城七绝句·裴五', '莫怪相逢无笑语，感今思旧戟门前。\\n张家伯仲偏相似，每见清扬一惘然。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3256', 'poem', '唐', '杨师道', '还山宅', '暮春还旧岭，徙倚玩年华。\\n芳草无行径，空山正落花。\\n垂藤扫幽石，卧柳碍浮槎。\\n鸟散茅檐静，云披涧户斜。\\n依然此泉路，犹是昔烟霞。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3257', 'poem', '唐', '欧阳炯', '三字令', '春欲尽，日迟迟，牡丹时。罗幌卷，翠帘垂。彩笺书，\\n红粉泪，两心知。\\n人不在，燕空归，负佳期。香烬落，枕函欹。月分明，\\n花澹薄，惹相思。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3258', 'poem', '唐', '章孝标', '柘枝', '柘枝初出鼓声招，花钿罗衫耸细腰。移步锦靴空绰约，\\n迎风绣帽动飘飖，亚身踏节鸾形转，背面羞人凤影娇。\\n只恐相公看未足，便随风雨上青霄。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3259', 'poem', '唐', '于鹄', '题邻居', '僻巷邻家少，茅檐喜并居。\\n蒸梨常共灶，浇薤亦同渠。\\n传屐朝寻药，分灯夜读书。\\n虽然在城市，还得似樵渔。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3260', 'poem', '唐', '李煜', '九月十日偶书', '晚雨秋阴酒乍醒，感时心绪杳难平。\\n黄花冷落不成艳，红叶飕飗竞鼓声。\\n背世返能厌俗态，偶缘犹未忘多情。\\n自从双鬓斑斑白，不学安仁却自惊。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3261', 'poem', '唐', '杜甫', '归雁二首', '万里衡阳雁，今年又北归。\\n双双瞻客上，一一背人飞。\\n云里相呼疾，沙边自宿稀。\\n系书元浪语，愁寂故山薇。\\n欲雪违胡地，先花别楚云。\\n却过清渭影，高起洞庭群。\\n塞北春阴暮，江南日色曛。\\n伤弓流落羽，行断不堪闻。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3262', 'poem', '唐', '李白', '玉真公主别馆苦雨赠卫尉张卿二首', '秋坐金张馆，繁阴昼不开。\\n空烟迷雨色，萧飒望中来。\\n翳翳昏垫苦，沉沉忧恨催。\\n清秋何以慰，白酒盈吾杯。\\n吟咏思管乐，此人已成灰。\\n独酌聊自勉，谁贵经纶才。\\n弹剑谢公子，无鱼良可哀。\\n苦雨思白日，浮云何由卷。\\n稷契和天人，阴阳乃骄蹇。\\n秋霖剧倒井，昏雾横绝巘。\\n欲往咫尺途，遂成山川限。\\n潈潈奔溜闻，浩浩惊波转。\\n泥沙塞中途，牛马不可辨。\\n饥从漂母食，闲缀羽陵简。\\n园家逢秋蔬，藜藿不满眼。\\n蟏蛸结思幽，蟋蟀伤褊浅。\\n厨灶无青烟，刀机生绿藓。\\n投箸解鹔鹴，换酒醉北堂。\\n丹徒布衣者，慷慨未可量。\\n何时黄金盘，一斛荐槟榔。\\n功成拂衣去，摇曳沧洲傍。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3263', 'poem', '唐', '李煜', '题金楼子后', '牙签万轴裹红绡，王粲书同付火烧。\\n不于祖龙留面目，遗篇那得到今朝。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3264', 'poem', '唐', '杜审言', '戏赠赵使君美人', '红粉青娥映楚云，桃花马上石榴裙。\\n罗敷独向东方去，漫学他家作使君。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3265', 'poem', '唐', '白居易', '杨柳枝词八首', '六么水调家家唱，白雪梅花处处吹。\\n古歌旧曲君休听，听取新翻杨柳枝。\\n陶令门前四五树，亚夫营里百千条。\\n何似东都正二月，黄金枝映洛阳桥。\\n依依褭褭复青青，句引春风无限情。\\n白雪花繁空扑地，绿丝条弱不胜莺。\\n红板江桥青酒旗，馆娃宫暖日斜时。\\n可怜雨歇东风定，万树千条各自垂。\\n苏州杨柳任君夸，更有钱唐胜馆娃。\\n若解多情寻小小，绿杨深处是苏家。\\n苏家小女旧知名，杨柳风前别有情。\\n剥条盘作银环样，卷叶吹为玉笛声。\\n叶含浓露如啼眼，枝袅轻风似舞腰。\\n小树不禁攀折苦，乞君留取两三条。\\n人言柳叶似愁眉，更有愁肠似柳丝。\\n柳丝挽断肠牵断，彼此应无续得期。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3266', 'poem', '唐', '熊孺登', '青溪村居二首', '家占溪南千个竹，地临湖上一群山。\\n渔船多在马长放，出处自由闲不闲。\\n深树黄鹂晓一声，林西江上月犹明。\\n野人早起无他事，贪绕沙泉看笋生。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3267', 'poem', '唐', '钱起', '送严维尉河南', '蕙叶青青花乱开，少年趋府下蓬莱。\\n甘泉未献扬雄赋，吏道何劳贾谊才。\\n征陌独愁飞盖远，离筵只惜暝钟催。\\n欲知别后相思处，愿植琼枝向柏台。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3268', 'poem', '唐', '翁绶', '咏酒', '逃暑迎春复送秋，无非绿蚁满杯浮。\\n百年莫惜千回醉，一盏能消万古愁。\\n几为芳菲眠细草，曾因雨雪上高楼。\\n平生名利关身者，不识狂歌到白头。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3269', 'poem', '唐', '李白', '从军行', '从军玉门道，逐虏金微山。\\n笛奏梅花曲，刀开明月环。\\n鼓声鸣海上，兵气拥云间。\\n愿斩单于首，长驱静铁关。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3270', 'poem', '唐', '胡曾', '咏史诗·武陵溪', '一溪春水彻云根，流出桃花片片新。\\n若道长生是虚语，洞中争得有秦人。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3271', 'poem', '唐', '白居易', '秘书省中忆旧山', '厌从薄宦校青简，悔别故山思白云。\\n犹喜兰台非傲吏，归时应免动移文。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3272', 'poem', '唐', '皮日休', '春夕酒醒', '四弦才罢醉蛮奴，醽醁馀香在翠炉。\\n夜半醒来红蜡短，一枝寒泪作珊瑚。', '2018-03-09 15:34:42');
INSERT INTO `t_poem` VALUES ('3273', 'poem', '唐', '李商隐', '初起', '想像咸池日欲光，五更钟后更回肠。\\n三年苦雾巴江水，不为离人照屋梁。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3274', 'poem', '唐', '白居易', '无梦', '老眼花前暗，春衣雨后寒。旧诗多忘却，新酒且尝看。\\n拙定于身稳，慵应趁伴难。渐销名利想，无梦到长安。主', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3275', 'poem', '唐', '白居易', '偶宴有怀', '遇兴寻文客，因欢命酒徒。\\n春游忆亲故，夜会似京都。\\n诗思闲仍在，乡愁醉暂无。\\n狂来欲起舞，惭见白髭须。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3276', 'poem', '唐', '徐夤', '初夏戏题', '长养薰风拂晓吹，渐开荷芰落蔷薇。\\n青虫也学庄周梦，化作南园蛱蝶飞。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3277', 'poem', '唐', '白居易', '戏赠户部李巡官', '好去民曹李判官，少贪公事且谋欢。\\n男儿未死争能料，莫作忠州刺史看。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3278', 'poem', '唐', '孟郊', '达士', '四时如逝水，百川皆东波。\\n青春去不还，白发镊更多。\\n达人识元化，变愁为高歌。\\n倾产取一醉，富者奈贫何。\\n君看土中宅，富贵无偏颇。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3279', 'poem', '唐', '卢照邻', '咏史四首', '季生昔未达，身辱功不成。\\n髡钳为台隶，灌园变姓名。\\n幸逢滕将军，兼遇曹丘生。\\n汉祖广招纳，一朝拜公卿。\\n百金孰云重，一诺良匪轻。\\n廷议斩樊哙，群公寂无声。\\n处身孤且直，遭时坦而平。\\n丈夫当如此，唯唯何足荣。\\n大汉昔云季，小人道遂振。\\n玉帛委奄尹，斧锧婴缙绅。\\n邈哉郭先生，卷舒得其真。\\n雍容谢朝廷，谈笑奖人伦。\\n在晦不绝俗，处乱不为亲。\\n诸侯不得友，天子不得臣。\\n冲情甄负甑，重价折角巾。\\n悠悠天下士，相送洛桥津。\\n谁知仙舟上，寂寂无四邻。\\n公业负奇志，交结尽才雄。\\n良田四百顷，所食常不充。\\n一为侍御史，慷慨说何公。\\n何公何为败，吾谋适不同。\\n仲颖恣残忍，废兴良在躬。\\n死人如乱麻，天子如转蓬。\\n干戈及黄屋，荆棘生紫宫。\\n郑生运其谋，将以清国戎。\\n时来命不遂，脱身归山东。\\n凛凛千载下，穆然怀清风。\\n昔有平陵男，姓朱名阿游。\\n直发上冲冠，壮气横三秋。\\n愿得斩马剑，先断佞臣头。\\n天子玉槛折，将军丹血流。\\n捐生不肯拜，视死其若休。\\n归来教乡里，童蒙远相求。\\n弟子数百人，散在十二州。\\n三公不敢吏，五鹿何能酬。\\n名与日月悬，义与天壤俦。\\n何必疲执戟，区区在封侯。\\n伟哉旷达士，知命固不忧。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3280', 'poem', '唐', '白居易', '杏园中枣树', '人言百果中，唯枣凡且鄙。\\n皮皴似龟手，叶小如鼠耳。\\n胡为不自知，生花此园里。\\n岂宜遇攀玩，幸免遭伤毁。\\n二月曲江头，杂英红旖旎。\\n枣亦在其间，如嫫对西子。\\n东风不择木，吹喣长未已。\\n眼看欲合抱，得尽生生理。\\n寄言游春客，乞君一回视。\\n君爱绕指柔，从君怜柳杞。\\n君求悦目艳，不敢争桃李。\\n君若作大车，轮轴材须此。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3281', 'poem', '唐', '王昌龄', '太湖秋夕', '水宿烟雨寒，洞庭霜落微。\\n月明移舟去，夜静魂梦归。\\n暗觉海风度，萧萧闻雁飞。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3282', 'poem', '唐', '白居易', '除夜寄微之', '鬓毛不觉白毵毵，一事无成百不堪。\\n共惜盛时辞阙下，同嗟除夜在江南。\\n家山泉石寻常忆，世路风波子细谙。\\n老校于君合先退，明年半百又加三。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3283', 'poem', '唐', '武则天', '曳鼎歌', '羲农首出，轩昊膺期。\\n唐虞继踵，汤禹乘时。\\n天下光宅，海内雍熙。\\n上玄降鉴，方建隆基。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3284', 'poem', '唐', '王邕', '湘灵鼓瑟', '宝瑟和琴韵，灵妃应乐章。\\n依稀闻促柱，仿佛梦新妆。\\n波外声初发，风前曲正长。\\n凄清和万籁，断续绕三湘。\\n转觉云山迥，空怀杜若芳。\\n诚能传此意，雅奏在宫商。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3285', 'poem', '唐', '陈凝', '马', '未明龙骨骏，幸得到神州。\\n自有千金价，宁忘伯乐酬。\\n虽知殊款段，莫敢比骅骝。\\n若遇追风便，当轩一举头。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3286', 'poem', '唐', '杨凝', '送客往洞庭', '九江归路远，万里客舟还。\\n若过巴江水，湘东满碧烟。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3287', 'poem', '唐', '李中', '春云', '阴去为膏泽，晴来媚晓空。\\n无心亦无滞，舒卷在东风。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3288', 'poem', '唐', '李世民', '赋得临池竹', '贞条障曲砌，翠叶贯寒霜。\\n拂牖分龙影，临池待凤翔。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3289', 'poem', '唐', '贾岛', '竹', '篱外清阴接药栏，晓风交戛碧琅玕。\\n子猷没后知音少，粉节霜筠漫岁寒。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3290', 'poem', '唐', '李益', '上洛桥', '金谷园中柳，春来似舞腰。\\n何堪好风景，独上洛阳桥。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3291', 'poem', '唐', '张籍', '永嘉行', '黄头鲜卑入洛阳，胡儿执戟升明堂。\\n晋家天子作降虏，公卿奔走如牛羊。\\n紫陌旌幡暗相触，家家鸡犬惊上屋。\\n妇人出门随乱兵，夫死眼前不敢哭。\\n九州诸侯自顾土，无人领兵来护主。\\n北人避胡多在南，南人至今能晋语。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3292', 'poem', '唐', '崔湜', '边愁', '九月蓬根断，三边草叶腓。\\n风尘马变色，霜雪剑生衣。\\n客思愁阴晚，边书驿骑归。\\n殷勤凤楼上，还袂及春晖。', '2018-03-09 15:34:51');
INSERT INTO `t_poem` VALUES ('3293', 'poem', '唐', '佚名', '杂曲歌辞·昆仑子', '扬子谭经去，淮王载酒过。\\n醉来啼鸟唤，坐久落花多。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3294', 'poem', '唐', '李商隐', '无题', '万里风波一叶舟，忆归初罢更夷犹。\\n碧江地没元相引，黄鹤沙边亦少留。\\n益德冤魂终报主，阿童高义镇横秋。\\n人生岂得长无谓，怀古思乡共白头。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3295', 'poem', '唐', '姚合', '穷边词二首', '将军作镇古汧洲，水腻山春节气柔。\\n清夜满城丝管散，行人不信是边头。\\n箭利弓调四镇兵，蕃人不敢近东行。\\n沿边千里浑无事，唯见平安火入城。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3296', 'poem', '唐', '孟郊', '秋怀十五首', '孤骨夜难卧，吟虫相唧唧。\\n老泣无涕洟，秋露为滴沥。\\n去壮暂如剪，来衰纷似织。\\n触绪无新心，丛悲有馀忆。\\n讵忍逐南帆，江山践往昔。\\n秋月颜色冰，老客志气单。\\n冷露滴梦破，峭风梳骨寒。\\n席上印病文，肠中转愁盘。\\n疑怀无所凭，虚听多无端。\\n棓桐枯峥嵘，声响如哀弹。\\n一尺月透户，仡栗如剑飞。\\n老骨坐亦惊，病力所尚微。\\n虫苦贪夜色，鸟危巢焚辉。\\n孀娥理故丝，弧哭抽馀噫。\\n浮年不可追，衰步多夕归。\\n秋至老更贫，破屋无门扉。\\n一片月落床，四壁风入衣。\\n疏梦不复远，弱心良易归。\\n商葩将去绿，缭绕争馀辉。\\n野步踏事少，病谋向物违。\\n幽幽草根虫，生意与我微。\\n竹风相戛语，幽闺暗中闻。\\n鬼神满衰听，恍愡难自分。\\n商叶堕干雨，秋衣卧单云。\\n病骨可剸物，酸呻亦成文。\\n瘦攒如此枯，壮落随西曛。\\n袅袅一线命，徒言系絪缊。\\n老骨惧秋月，秋月刀剑棱。\\n纤威不可干，冷魂坐自凝。\\n羁雌巢空镜，仙飙荡浮冰。\\n惊步恐自翻，病大不敢凌。\\n单床寤皎皎，瘦卧心兢兢。\\n洗河不见水，透浊为清澄。\\n诗壮昔空说，诗衰今何凭。\\n老病多异虑，朝夕非一心。\\n商虫哭衰运，繁响不可寻。\\n秋草瘦如发，贞芳缀疏金。\\n晚鲜讵几时，驰景还易阴。\\n弱习徒自耻，暮知欲何任。\\n露才一见馋，潜智早已深。\\n防深不防露，此意古所箴。\\n岁暮景气干，秋风兵甲声。\\n织织劳无衣，喓喓徒自鸣。\\n商声耸中夜，蹇支废前行。\\n青发如秋园，一剪不复生。\\n少年如饿花，瞥见不复明。\\n君子山岳定，小人丝毫争。\\n多争多无寿，天道戒其盈。\\n冷露多瘁索，枯风饶吹嘘。\\n秋深月清苦。虫老声粗疏。\\n赪珠枝累累，芳金蔓舒舒。\\n草木亦趣时，寒荣似春馀。\\n自悲零落生，与我心何如。\\n老人朝夕异，生死每日中。\\n坐随一啜安，卧与万景空。\\n视短不到门，听涩讵逐风。\\n还如刻削形，免有纤悉聪。\\n浪浪谢初始，皎皎幸归终。\\n孤隔文章友，亲密蒿莱翁。\\n岁绿闵似黄，秋节迸已穷。\\n四时既相迫，万虑自然丛。\\n南逸浩淼际，北贫硗确中。\\n曩怀沉遥江，衰思结秋嵩。\\n锄食难满腹，叶衣多丑躬。\\n尘缕不自整，古吟将谁通。\\n幽竹啸鬼神，楚铁生虬龙。\\n志生多异感，运郁由邪衷。\\n常思书破衣，至死教初重。\\n习乐莫习声，习声多顽聋。\\n明明胸中言，愿写为高崇。\\n幽苦日日甚，老力步步微。\\n常恐暂下床，至门不复归。\\n饥者重一食，寒者重一衣。\\n泛广岂无涘，姿行亦有随。\\n语中失次第，身外生疮痍。\\n桂蠧既潜污，桂花损贞姿。\\n詈言一失香，千古闻臭词。\\n将死始前悔，前悔不可追。\\n哀哉轻薄行，终日与驷驰。\\n流运闪欲尽，枯折皆相号。\\n棘枝风哭酸，桐叶霜颜高。\\n老虫干铁鸣，惊兽孤玉咆。\\n商气洗声瘦，晚阴驱景芳。\\n集耳不可遏，噎神不可逃。\\n蹇行散馀郁，幽坐谁与曹。\\n抽壮无一线，剪怀盈千刀。\\n清诗既名朓，金菊亦姓陶。\\n收拾昔所弃，咨嗟今比毛。\\n幽幽岁晏言，零落不可操。\\n霜气入病骨，老人身生冰。\\n衰毛暗相刺，冷痛不可胜。\\n鷕鷕伸至明，强强揽所凭。\\n瘦坐形欲折，晚饥心将崩。\\n劝药左右愚，言语如见憎。\\n耸耳噎神开，始知功用能。\\n日中视馀疮，暗锁闻绳蝇。\\n彼齅一何酷，此味半点凝。\\n潜毒尔无猒，馀生我堪矜。\\n冻飞幸不远，冬令反心惩。\\n出没各有时，寒热苦相凌。\\n仰谢调运翁，请命愿有征。\\n黄河倒上天，众水有却来。\\n人心不及水，一直去不回。\\n一直亦有巧，不肯至蓬莱。\\n一直不知疲，唯闻至省台。\\n忍古不失古，失古志易摧。\\n失古剑亦折，失古琴亦哀。\\n夫子失古泪，当时落漼漼。\\n诗老失古心，至今寒皑皑。\\n古骨无浊肉，古衣如藓苔。\\n劝君勉忍古，忍古销尘埃。\\n詈言不见血，杀人何纷纷。\\n声如穷家犬，吠窦何訚訚。\\n詈痛幽鬼哭，詈侵黄金贫。\\n言词岂用多，憔悴在一闻。\\n古詈舌不死，至今书云云。\\n今人咏古书，善恶宜自分。\\n秦火不爇舌，秦火空蓺文。\\n所以詈更生，至今横絪缊。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3297', 'poem', '唐', '刘禹锡', '团扇歌', '团扇复团扇，奉君清暑殿。\\n秋风入庭树，从此不相见。\\n上有乘鸾女，苍苍虫网遍。\\n明年入怀袖，别是机中练。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3298', 'poem', '唐', '白居易', '东楼晓', '脉脉复脉脉，东楼无宿客。\\n城暗云雾多，峡深田地窄。\\n宵灯尚留焰，晨禽初展翮。\\n欲知山高低，不见东方白。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3299', 'poem', '唐', '孟昶', '避暑摩诃池上作', '冰肌玉骨清无汗，水殿风来暗香暖。\\n帘开明月独窥人，欹枕钗横云鬓乱。\\n起来琼户寂无声，时见疏星渡河汉。\\n屈指西风几时来，只恐流年暗中换。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3300', 'poem', '唐', '白居易', '山雉', '五步一啄草，十步一饮水。\\n适性遂其生，时哉山梁雉。\\n梁上无罾缴，梁下无鹰鹯。\\n雌雄与群雏，皆得终天年。\\n嗟嗟笼下鸡，及彼池中雁。\\n既有稻粱恩，必有牺牲患。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3301', 'poem', '唐', '李贺', '胡蝶飞', '杨花扑帐春云热，龟甲屏风醉眼缬。\\n东家蝴蝶西家飞，白骑少年今日归。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3302', 'poem', '唐', '佚名', '杂曲歌辞·入破第四', '行乐三阳早，芳菲二月春。\\n闺中红粉态，陌上看花人。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3303', 'poem', '唐', '崔涂', '橹声', '烟外桡声远，天涯幽梦回。\\n争知江上客，不是故乡来。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3304', 'poem', '唐', '罗隐', '梅', '天赐胭脂一抹腮，盘中磊落笛中哀。\\n虽然未得和羹便，曾与将军止渴来。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3305', 'poem', '唐', '李群玉', '北亭', '斜雨飞丝织晓空，疏帘半卷野亭风。\\n荷花开尽秋光晚，零落残红绿沼中。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3306', 'poem', '唐', '韩偓', '疏雨', '疏雨从东送疾雷，小庭凉气净莓苔。\\n卷帘燕子穿人去，洗砚鱼儿触手来。\\n但欲进贤求上赏，唯将拯溺作良媒。\\n戎衣一挂清天下，傅野非无济世才。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3307', 'poem', '唐', '李嘉祐', '晚登江楼有怀', '独坐南楼佳兴新，青山绿水共为邻。\\n爽气遥分隔浦岫，斜光偏照渡江人。\\n心闲鸥鸟时相近，事简鱼竿私自亲。\\n只忆帝京不可到，秋琴一弄欲沾巾。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3308', 'poem', '唐', '唐彦谦', '莲', '新莲映多浦，迢递绿塘东。\\n静影摇波日，寒香映水风。\\n金尘飘落蕊，玉露洗残红。\\n看著馀芳少，无人问的中。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3309', 'poem', '唐', '柳宗元', '咏荆轲', '燕秦不两立，太子已为虞。\\n千金奉短计，匕首荆卿趋。\\n穷年徇所欲，兵势且见屠。\\n微言激幽愤，怒目辞燕都。\\n朔风动易水，挥爵前长驱。\\n函首致宿怨，献田开版图。\\n炯然耀电光，掌握罔正夫。\\n造端何其锐，临事竟趑趄。\\n长虹吐白日，仓卒反受诛。\\n按剑赫凭怒，风雷助号呼。\\n慈父断子首，狂走无容躯。\\n夷城芟七族，台观皆焚污。\\n始期忧患弭，卒动灾祸枢。\\n秦皇本诈力，事与桓公殊。\\n奈何效曹子，实谓勇且愚。\\n世传故多谬，太史征无且。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3310', 'poem', '唐', '李白', '赠从弟宣州长史昭', '淮南望江南，千里碧山对。\\n我行倦过之，半落青天外。\\n宗英佐雄郡，水陆相控带。\\n长川豁中流，千里泻吴会。\\n君心亦如此，包纳无小大。\\n摇笔起风霜，推诚结仁爱。\\n讼庭垂桃李，宾馆罗轩盖。\\n何意苍梧云，飘然忽相会。\\n才将圣不偶，命与时俱背。\\n独立山海间，空老圣明代。\\n知音不易得，抚剑增感慨。\\n当结九万期，中途莫先退。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3311', 'poem', '唐', '路德延', '小儿诗', '情态任天然，桃红两颊鲜。\\n乍行人共看，初语客多怜。\\n臂膊肥如瓠，肌肤软胜绵。\\n长头才覆额，分角渐垂肩。\\n散诞无尘虑，逍遥占地仙。\\n排衙朱阁上，喝道画堂前。\\n合调歌杨柳，齐声踏采莲。\\n走堤行细雨，奔巷趁轻烟。\\n嫩竹乘为马，新蒲折作鞭。\\n莺雏金镟系，猫子彩丝牵。\\n拥鹤归晴岛，驱鹅入暖泉。\\n杨花争弄雪，榆叶共收钱。\\n锡镜当胸挂，银珠对耳悬。\\n头依苍鹘裹，袖学柘枝揎。\\n酒殢丹砂暖，茶催小玉煎。\\n频邀筹箸挣，时乞绣针穿。\\n宝箧拏红豆，妆奁拾翠钿。\\n戏袍披按褥，劣帽戴靴毡。\\n展画趋三圣，开屏笑七贤。\\n贮怀青杏小，垂额绿荷圆。\\n惊滴沾罗泪，娇流污锦涎。\\n倦书饶娅姹，憎药巧迁延。\\n弄帐鸾绡映，藏衾凤绮缠。\\n指敲迎使鼓，筋拨赛神弦。\\n帘拂鱼钩动，筝推雁柱偏。\\n棋图添路画，笛管欠声镌。\\n恼客初酣睡，惊僧半入禅。\\n寻蛛穷屋瓦，探雀遍楼椽。\\n抛果忙开口，藏钩乱出拳。\\n夜分围榾柮，聚朝打秋千。\\n折竹装泥燕，添丝放纸鸢。\\n互夸轮水碓，相教放风旋。\\n旗小裁红绢，书幽截碧笺。\\n远铺张鸽网，低控射蝇弦。\\n詀语时时道，谣歌处处传。\\n匿窗眉乍曲，遮路臂相连。\\n斗草当春径，争球出晚田。\\n柳傍慵独坐，花底困横眠。\\n等鹊前篱畔，听蛩伏砌边。\\n傍枝粘舞蝶，隈树捉鸣蝉。\\n平岛夸趫上，层崖逞捷缘。\\n嫩苔车迹小，深雪履痕全。\\n竞指云生岫，齐呼月上天。\\n蚁窠寻径劚，蜂穴绕阶填。\\n樵唱回深岭，牛歌下远川。\\n垒柴为屋木，和土作盘筵。\\n险砌高台石，危跳峻塔砖。\\n忽升邻舍树，偷上后池船。\\n项橐称师日，甘罗作相年。\\n明时方任德，劝尔减狂颠。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3312', 'poem', '唐', '李远', '咏雁', '早晚辞沙漠，南来处处飞。\\n关山多雨雪，风水损毛衣。\\n碧海魂应断，红楼信自稀。\\n不知矰缴外，留得几行归。', '2018-03-09 15:35:02');
INSERT INTO `t_poem` VALUES ('3313', 'poem', '唐', '杜甫', '病马', '乘尔亦已久，天寒关塞深。\\n尘中老尽力，岁晚病伤心。\\n毛骨岂殊众，驯良犹至今。\\n物微意不浅，感动一沉吟。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3314', 'poem', '唐', '杜牧', '睦州四韵', '州在钓台边，溪山实可怜。\\n有家皆掩映，无处不潺湲。\\n好树鸣幽鸟，晴楼入野烟。\\n残春杜陵客，中酒落花前。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3315', 'poem', '唐', '杜甫', '将赴成都草堂途中有作，先寄严郑公五首', '得归茅屋赴成都，直为文翁再剖符。\\n但使闾阎还揖让，敢论松竹久荒芜。\\n鱼知丙穴由来美，酒忆郫筒不用酤。\\n五马旧曾谙小径，几回书札待潜夫。\\n处处青江带白蘋，故园犹得见残春。\\n雪山斥候无兵马，锦里逢迎有主人。\\n休怪儿童延俗客，不教鹅鸭恼比邻。\\n习池未觉风流尽，况复荆州赏更新。\\n竹寒沙碧浣花溪，菱刺藤梢咫尺迷。\\n过客径须愁出入，居人不自解东西。\\n书签药裹封蛛网，野店山桥送马蹄。\\n岂藉荒庭春草色，先判一饮醉如泥。\\n常苦沙崩损药栏，也从江槛落风湍。\\n新松恨不高千尺，恶竹应须斩万竿。\\n生理只凭黄阁老，衰颜欲付紫金丹。\\n三年奔走空皮骨，信有人间行路难。\\n锦官城西生事微，乌皮几在还思归。\\n昔去为忧乱兵入，今来已恐邻人非。\\n侧身天地更怀古，回首风尘甘息机。\\n共说总戎云鸟阵，不妨游子芰荷衣。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3316', 'poem', '唐', '元稹', '遣悲怀三首其一', '谢公最小偏怜女，自嫁黔娄百事乖。\\n顾我无衣搜荩箧，泥他沽酒拔金钗。\\n野蔬充膳甘长藿，落叶添薪仰古槐。\\n今日俸钱过十万，与君营奠复营斋。\\n昔日戏言身后事，今朝都到眼前来。\\n衣裳已施行看尽，针线犹存未忍开。\\n尚想旧情怜婢仆，也曾因梦送钱财。\\n诚知此恨人人有，贫贱夫妻百事哀。\\n闲坐悲君亦自悲，百年都是几多时。\\n邓攸无子寻知命，潘岳悼亡犹费辞。\\n同穴窅冥何所望，他生缘会更难期。\\n惟将终夜长开眼，报答平生未展眉。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3317', 'poem', '唐', '刘商', '送濬上人', '木落前山霜露多，手持寒锡远头陀。\\n眼看庭树梅花发，不见诗人独咏歌。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3318', 'poem', '唐', '白居易', '迂叟', '一辞魏阙就商宾，散地闲居八九春。\\n初时被目为迂叟，近日蒙呼作隐人。\\n冷暖俗情谙世路，是非闲论任交亲。\\n应须绳墨机关外，安置疏愚钝滞身。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3319', 'poem', '唐', '李煜', '病起题山舍壁', '山舍初成病乍轻，杖藜巾褐称闲情。\\n炉开小火深回暖，沟引新流几曲声。\\n暂约彭涓安朽质，终期宗远问无生。\\n谁能役役尘中累，贪合鱼龙构强名。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3320', 'poem', '唐', '韦应物', '春游南亭', '川明气已变，岩寒云尚拥。\\n南亭草心绿，春塘泉脉动。\\n景煦听禽响，雨馀看柳重。\\n逍遥池馆华，益愧专城宠。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3321', 'poem', '唐', '刘湾', '云南曲', '百蛮乱南方，群盗如猬起。\\n骚然疲中原，征战从此始。\\n白门太和城，来往一万里。\\n去者无全生，十人九人死。\\n岱马卧阳山，燕兵哭泸水。\\n妻行求死夫，父行求死子。\\n苍天满愁云，白骨积空垒。\\n哀哀云南行，十万同已矣。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3322', 'poem', '唐', '杜牧', '书情', '谁家洛浦神，十四五来人。\\n媚发轻垂额，香衫软著身。\\n摘莲红袖湿，窥渌翠蛾频。\\n飞鹊徒来往，平阳公主亲。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3323', 'poem', '唐', '岑参', '醉里送裴子赴镇西', '醉后未能别，待醒方送君。\\n看君走马去，直上天山云。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3324', 'poem', '唐', '李德裕', '题柳郎中故居', '下马荒阶日欲曛，潺潺石溜静中闻。\\n鸟啼花发人声绝，寂寞山窗掩白云。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3325', 'poem', '唐', '白居易', '登西楼忆行简', '每因楼上西南望，始觉人间道路长。\\n碍日暮山青蔟蔟，漫天秋水白茫茫。\\n风波不见三年面，书信难传万里肠。\\n早晚东归来下峡，稳乘船舫过瞿唐。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3326', 'poem', '唐', '高适', '送桂阳孝廉', '桂阳年少西入秦，数经甲科犹白身。\\n即今江海一归客，他日云霄万里人。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3327', 'poem', '唐', '白居易', '闲意', '不争荣耀任沉沦，日与时疏共道亲。\\n北省朋僚音信断，东林长老往还频。\\n病停夜食闲如社，慵拥朝裘暖似春。\\n渐老渐谙闲气味，终身不拟作忙人。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3328', 'poem', '唐', '齐己', '伤秋', '旦暮馀生在，肌肤十分无。\\n眠寒半榻朽，立月一株枯。\\n梦已随双树，诗犹却万夫。\\n名山未归得，可惜死江湖。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3329', 'poem', '唐', '李白', '少年子', '青云少年子，挟弹章台左。\\n鞍马四边开，突如流星过。\\n金丸落飞鸟，夜入琼楼卧。\\n夷齐是何人，独守西山饿。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3330', 'poem', '唐', '薛逢', '六街尘', '六街尘起鼓冬冬，马足车轮在处通。\\n百役并驱衣食内，四民长走路岐中。\\n年光与物随流水，世事如花落晓风。\\n名利到身无了日，不知今古旋成空。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3331', 'poem', '唐', '朱湾', '咏玉', '歌玉屡招疑，终朝省复思。\\n既哀黄鸟兴，还复白圭诗。\\n请益先求友，将行必择师。\\n谁知不鸣者，独下董生帷。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3332', 'poem', '唐', '李峤', '桥', '乌鹊填应满，黄公去不归。\\n势疑虹始见，形似雁初飞。\\n妙应七星制，高分半月辉。\\n秦王空构石，仙岛远难依。', '2018-03-09 15:35:10');
INSERT INTO `t_poem` VALUES ('3333', 'poem', '唐', '綦毋潜', '祇园寺', '宝坊求往迹，神理驻沿洄。\\n雁塔酬前愿，王身更后来。\\n加持将暝合，朗悟豁然开。\\n两世分明见，馀生复几哉。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3334', 'poem', '唐', '白居易', '食后', '食罢一觉睡，起来两瓯茶。\\n举头看日影，已复西南斜。\\n乐人惜日促，忧人厌年赊。\\n无忧无乐者，长短任生涯。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3335', 'poem', '唐', '白居易', '禁中月', '海水明月出，禁中清夜长。\\n东南楼殿白，稍稍上宫墙。\\n净落金塘水，明浮玉砌霜。\\n不比人间见，尘土污清光。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3336', 'poem', '唐', '吴融', '登鹳雀楼', '鸟在林梢脚底看，夕阳无际戍烟残。\\n冻开河水奔浑急，雪洗条山错落寒。\\n始为一名抛故国，近因多难怕长安。\\n祖鞭掉折徒为尔，赢得云溪负钓竿。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3337', 'poem', '唐', '陈季', '湘灵鼓瑟', '神女泛瑶瑟，古祠严野亭。\\n楚云来泱漭，湘水助清泠。\\n妙指微幽契，繁声入杳冥。\\n一弹新月白，数曲暮山青。\\n调苦荆人怨，时遥帝子灵。\\n遗音如可赏，试奏为君听。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3338', 'poem', '唐', '白居易', '废琴', '丝桐合为琴，中有太古声。\\n古声澹无味，不称今人情。\\n玉徽光彩灭，朱弦尘土生。\\n废弃来已久，遗音尚泠泠。\\n不辞为君弹，纵弹人不听。\\n何物使之然，羌笛与秦筝。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3339', 'poem', '唐', '李白', '游溧阳北湖亭望瓦屋山怀古赠同旅 / 赠孟浩然', '朝登北湖亭，遥望瓦屋山。\\n天清白露下，始觉秋风还。\\n游子托主人，仰观眉睫间。\\n目色送飞鸿，邈然不可攀。\\n长吁相劝勉，何事来吴关？\\n闻有贞义女，振穷溧水湾。\\n清光了在眼，白日如披颜。\\n高坟五六墩，崒兀栖猛虎。\\n遗迹翳九泉，芳名动千古。\\n子胥昔乞食，此女倾壶浆。\\n运开展宿愤，入楚鞭平王。\\n凛冽天地间，闻名若怀霜。\\n壮夫或未达，十步九太行。\\n与君拂衣去，万里同翱翔。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3340', 'poem', '唐', '顾况', '杂曲歌辞·游子吟', '故枥思疲马，故巢思迷禽。\\n浮云蔽我乡，踯躅游子吟。\\n游子悲久滞，浮云郁东岑。\\n客堂无丝桐，落叶如秋霖。\\n艰哉远游子，所以悲滞淫。\\n一为浮云词，愤塞谁能禁。\\n驰晖百年内，惟愿展所钦。\\n胡为不归欤，坐使年病侵。\\n未老霜绕鬓，非狂火烧心。\\n太行何艰哉，北斗不可斟。\\n夜晴星河出，耿耿辰与参。\\n佳人夐青天，尺素重于金。\\n泬寥群动异，眇默诸境森。\\n苔衣上闲阶，蜻蛚催寒砧。\\n立身计几误，道险无容针。\\n三年不还家，万里遗锦衾。\\n梦魂无重阻，离忧因古今。\\n胡为不归欤，孤负丘中琴。\\n腰下是何物，牵缠旷登寻。\\n朝与名山期，夕宿楚水阴。\\n楚水殊演漾，名山杳岖嶔。\\n客从洞庭来，婉娈潇湘深。\\n橘柚在南国，鸿雁遗秋音。\\n下有碧草洲，上有青橘林。\\n引烛窥洞穴，凌波睥天琛。\\n蒲荷影参差，凫鹤雏淋涔。\\n浩歌惜芳杜，散发轻华簪。\\n胡为不归欤，泪下沾衣襟。\\n鸢飞戾霄汉，蝼蚁制鳝鲟。\\n赫赫大圣朝，日月光照临。\\n圣主虽启迪，奇人分堙沉。\\n层城发云韶，玉府锵球琳。\\n鹿鸣志丰草，况复虞人箴。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3341', 'poem', '唐', '孙逖', '同洛阳李少府观永乐公主入蕃', '边地莺花少，年来未觉新。\\n美人天上落，龙塞始应春。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3342', 'poem', '唐', '李白', '送友人寻越中山水', '闻道稽山去，偏宜谢客才。\\n千岩泉洒落，万壑树萦回。\\n东海横秦望，西陵绕越台。\\n湖清霜镜晓，涛白雪山来。\\n八月枚乘笔，三吴张翰杯。\\n此中多逸兴，早晚向天台。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3343', 'poem', '唐', '丘为', '题农父庐舍', '东风何时至，已绿湖上山。\\n湖上春已早，田家日不闲。\\n沟塍流水处，耒耜平芜间。\\n薄暮饭牛罢，归来还闭关。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3344', 'poem', '唐', '刘长卿', '苕溪酬梁耿别后见寄', '清川永路何极，落日孤舟解携。\\n鸟向平芜远近，人随流水东西。\\n白云千里万里，明月前溪后溪。\\n惆怅长沙谪去，江潭芳草萋萋。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3345', 'poem', '唐', '崔国辅', '子夜冬歌', '寂寥抱冬心，裁罗又褧褧。\\n夜久频挑灯，霜寒剪刀冷。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3346', 'poem', '唐', '王维', '辋川集·宫槐陌', '仄径荫宫槐，幽阴多绿苔。\\n应门但迎扫，畏有山僧来。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3347', 'poem', '唐', '吕温', '贞元十四年旱甚见权门移芍药花', '绿原青垄渐成尘，汲井开园日日新。\\n四月带花移芍药，不知忧国是何人。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3348', 'poem', '唐', '顾况', '听角思归', '故园黄叶满青苔，梦后城头晓角哀。\\n此夜断肠人不见，起行残日影徘徊。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3349', 'poem', '唐', '杜甫', '伤秋', '林僻来人少，山长去鸟微。\\n高秋收画扇，久客掩荆扉。\\n懒慢头时栉，艰难带减围。\\n将军犹汗马，天子尚戎衣。\\n白蒋风飙脆，殷柽晓夜稀。\\n何年减豺虎，似有故园归。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3350', 'poem', '唐', '李商隐', '重有感', '玉帐牙旗得上游，安危须共主君忧。\\n窦融表已来关右，陶侃军宜次石头。\\n岂有蛟龙愁失水，更无鹰隼与高秋。\\n昼号夜哭兼幽显，早晚星关雪涕收。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3351', 'poem', '唐', '李白', '雨后望月', '四郊阴霭散，开户半蟾生。\\n万里舒霜合，一条江练横。\\n出时山眼白，高后海心明。\\n为惜如团扇，长吟到五更。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3352', 'poem', '唐', '郑畋', '马嵬坡', '玄宗回马杨妃死，云雨难忘日月新。\\n终是圣明天子事，景阳宫井又何人。', '2018-03-09 15:35:21');
INSERT INTO `t_poem` VALUES ('3353', 'poem', '唐', '杜甫', '送孔巢父谢病归游江东兼呈李白', '巢父掉头不肯住，东将人海随烟雾。\\n诗卷长留天地间，钓竿欲拂珊瑚树。\\n深山大泽龙蛇远，春寒野阴风景暮。\\n蓬莱织女回云车，指点虚无是归路。\\n自是君身有仙骨，世人那得知其故。\\n惜君只欲苦死留，富贵何如草头露？\\n蔡侯静者意有涂，清夜置酒临前除。\\n罢琴惆怅月照席：“几岁寄我空中书？\\n南寻禹穴见李白，道甫问讯今何如！”', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3354', 'poem', '唐', '殷尧藩', '登凤凰台二首', '凤凰台上望长安，五色宫袍照水寒。\\n彩笔十年留翰墨，银河一夜卧阑干。\\n三山飞鸟江天暮，六代离宫草树残。\\n始信人生如一梦，壮怀莫使酒杯干。\\n梧桐叶落秋风老，人去台空凤不来。\\n梁武台城芳草合，吴王宫殿野花开。\\n石头城下春生水，燕子堂前雨长苔。\\n莫问人间兴废事，百年相遇且衔杯。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3355', 'poem', '唐', '魏征', '横吹曲辞·出关', '中原还逐鹿，投笔事戎轩。\\n纵横计不就，慷慨志犹存。\\n策杖谒天子，驱马出关门。\\n请缨羁南越，凭轼下东藩。\\n郁纡陟高岫，出没望平原。\\n古木吟寒鸟，空山啼夜猿。\\n既伤千里目，还惊九折魂。\\n岂不惮艰险，深怀国士恩。\\n季布无二诺，侯嬴重一言。\\n人生感意气，功名谁复论。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3356', 'poem', '唐', '李煜', '送邓王二十弟从益牧宣城', '且维轻舸更迟迟，别酒重倾惜解携。\\n浩浪侵愁光荡漾，乱山凝恨色高低。\\n君驰桧楫情何极，我凭阑干日向西。\\n咫尺烟江几多地，不须怀抱重凄凄。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3357', 'poem', '唐', '崔涂', '过洛阳故城', '三十世皇都，萧条是霸图。\\n片墙看破尽，遗迹渐应无。\\n野径通荒苑，高槐映远衢。\\n独吟人不问，清冷自呜呜。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3358', 'poem', '唐', '李贺', '南园', '方领蕙带折角巾，杜若已老兰苕春。\\n南山削秀蓝玉合，小雨归去飞凉云。\\n熟杏暖香梨叶老，草梢竹栅锁池痕。\\n郑公乡老开酒尊，坐泛楚奏吟招魂。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3359', 'poem', '唐', '李商隐', '七月二十九日崇让宅宴作', '露如微霰下前池，月过回塘万竹悲。\\n浮世本来多聚散，红蕖何事亦离披。\\n悠扬归梦惟灯见，濩落生涯独酒知。\\n岂到白头长只尔，嵩阳松雪有心期。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3360', 'poem', '唐', '杜甫', '王命', '汉北豺狼满，巴西道路难。\\n血埋诸将甲，骨断使臣鞍。\\n牢落新烧栈，苍茫旧筑坛。\\n深怀喻蜀意，恸哭望王官。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3361', 'poem', '唐', '杜甫', '遣兴', '骥子好男儿，前年学语时。\\n问知人客姓，诵得老夫诗。\\n世乱怜渠小，家贫仰母慈。\\n鹿门携不遂，雁足系难期。\\n天地军麾满，山河战角悲。\\n傥归免相失，见日敢辞迟。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3362', 'poem', '唐', '韩愈', '学诸进士作精卫衔石填海', '鸟有偿冤者，终年抱寸诚。\\n口衔山石细，心望海波平。\\n渺渺功难见，区区命已轻。\\n人皆讥造次，我独赏专精。\\n岂计休无日，惟应尽此生。\\n何惭刺客传，不著报雠名。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3363', 'poem', '唐', '白居易', '浔阳三题·东林寺白莲', '东林北塘水，湛湛见底清。\\n中生白芙蓉，菡萏三百茎。\\n白日发光彩，清飚散芳馨。\\n泄香银囊破，泻露玉盘倾。\\n我惭尘垢眼，见此琼瑶英。\\n乃知红莲花，虚得清净名。\\n夏萼敷未歇，秋房结才成。\\n夜深众僧寝，独起绕池行。\\n欲收一颗子，寄向长安城。\\n但恐出山去，人间种不生。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3364', 'poem', '唐', '虞世南', '出塞', '上将三略远，元戎九命尊。\\n缅怀古人节，思酬明主恩。\\n山西多勇气，塞北有游魂。\\n扬桴上陇坂，勒骑下平原。\\n誓将绝沙漠，悠然去玉门。\\n轻赍不遑舍，惊策骛戎轩。\\n凛凛边风急，萧萧征马烦。\\n雪暗天山道，冰塞交河源。\\n雾锋黯无色，霜旗冻不翻。\\n耿介倚长剑，日落风尘昏。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3365', 'poem', '唐', '薛涛', '柳絮', '二月杨花轻复微，春风摇荡惹人衣。\\n他家本是无情物，一任南飞又北飞。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3366', 'poem', '唐', '姚合', '古碑', '荒田一片石，文字满青苔。\\n不是逢闲客，何人肯读来。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3367', 'poem', '唐', '吕温', '冬夜即事', '百忧攒心起复卧，夜长耿耿不可过。\\n风吹雪片似花落，月照冰文如镜破。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3368', 'poem', '唐', '湘驿女子', '题玉泉溪', '红叶醉秋色，碧溪弹夜弦。\\n佳期不可再，风雨杳如年。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3369', 'poem', '唐', '白居易', '后宫词', '雨露由来一点恩，争能遍布及千门。\\n三千宫女胭脂面，几个春来无泪痕。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3370', 'poem', '唐', '白居易', '罢药', '自学坐禅休服药，从他时复病沉沉。\\n此身不要全强健，强健多生人我心。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3371', 'poem', '唐', '秦韬玉', '塞下', '到处人皆著战袍，麾旗风紧马蹄劳。\\n黑山霜重弓添硬，青冢沙平月更高。\\n大野几重开雪岭，长河无限旧云涛。\\n凤林关外皆唐土，何日陈兵戍不毛。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3372', 'poem', '唐', '元稹', '竹簟', '竹簟衬重茵，未忍都令卷。\\n忆昨初来日，看君自施展。', '2018-03-09 15:35:29');
INSERT INTO `t_poem` VALUES ('3373', 'poem', '唐', '白居易', '感故张仆射诸妓', '黄金不惜买蛾眉，拣得如花三四枝。\\n歌舞教成心力尽，一朝身去不相随。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3374', 'poem', '唐', '佚名', '嘲四相（宣宗时曹确、杨收、徐商、路岩同秉政）', '确确无馀事，钱财总被收。\\n商人都不管，货赂几时休。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3375', 'poem', '唐', '白居易', '潮', '早潮才落晚潮来，一月周流六十回。\\n不独光阴朝复暮，杭州老去被潮催。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3376', 'poem', '唐', '刘兼', '芳春', '微雨微风隔画帘，金炉檀炷冷慵添。\\n桃花满地春牢落，柳絮成堆雪弃嫌。\\n宝瑟不能邀卓氏，彩毫何必梦江淹。\\n宦情归兴休相挠，隼旆渔舟总未厌。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3377', 'poem', '唐', '赵嘏', '宛陵望月寄沈学士', '一川如画敬亭东，待诏闲游处处同。\\n天竺山前镜湖畔，何如今日庾楼中。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3378', 'poem', '唐', '韦应物', '登楼寄王卿', '踏阁攀林恨不同，楚云沧海思无穷。\\n数家砧杵秋山下，一郡荆榛寒雨中。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3379', 'poem', '唐', '齐己', '片云', '水底分明天上云，可怜形影似吾身。\\n何妨舒作从龙势，一雨吹销万里尘。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3380', 'poem', '唐', '周瑀', '潘司马别业', '门对青山近，汀牵绿草长。\\n寒深包晚橘，风紧落垂杨。\\n湖畔闻渔唱，天边数雁行。\\n萧然有高士，清思满书堂。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3381', 'poem', '唐', '杜荀鹤', '溪兴', '山雨溪风卷钓丝，瓦瓯篷底独斟时。\\n醉来睡着无人唤，流下前溪也不知。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3382', 'poem', '唐', '白居易', '小曲新词二首', '霁色鲜宫殿，秋声脆管弦。\\n圣明千岁乐，岁岁似今年。\\n红裾明月夜，碧簟早秋时。\\n好向昭阳宿，天凉玉漏迟。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3383', 'poem', '唐', '白居易', '留北客', '峡外相逢远，樽前一会难。\\n即须分手别，且强展眉欢。\\n楚袖萧条舞，巴弦趣数弹。\\n笙歌随分有，莫作帝乡看。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3384', 'poem', '唐', '李颀', '古塞下曲', '行人朝走马，直指蓟城傍。\\n蓟城通漠北，万里别吾乡。\\n海上千烽火，沙中百战场。\\n军书发上郡，春色度河阳。\\n袅袅汉宫柳，青青胡地桑。\\n琵琶出塞曲，横笛断君肠。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3385', 'poem', '唐', '徐彦伯', '雪', '雪暗穷海云，洒空纷似露。\\n朔风吹故里，宛转玉阶树。\\n孤妾调玉瑟，早寒生锦衿。\\n况君张罗幕，愁坐北庭阴。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3386', 'poem', '唐', '王维', '春园即事', '宿雨乘轻屐，春寒著弊袍。\\n开畦分白水，间柳发红桃。\\n草际成棋局，林端举桔槔。\\n还持鹿皮几，日暮隐蓬蒿。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3387', 'poem', '唐', '鱼玄机', '冬夜寄温飞卿', '苦思搜诗灯下吟，不眠长夜怕寒衾。\\n满庭木叶愁风起，透幌纱窗惜月沈。\\n疏散未闲终遂愿，盛衰空见本来心。\\n幽栖莫定梧桐处，暮雀啾啾空绕林。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3388', 'poem', '唐', '张文琮', '咏水', '标名资上善，流派表灵长。\\n地图罗四渎，天文载五潢。\\n方流涵玉润，圆折动珠光。\\n独有蒙园吏，栖偃玩濠梁。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3389', 'poem', '唐', '白居易', '疑梦二首', '莫惊宠辱虚忧喜，莫计恩雠浪苦辛。\\n黄帝孔丘无处问，安知不是梦中身。\\n鹿疑郑相终难辨，蝶化庄生讵可知。\\n假使如今不是梦，能长于梦几多时。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3390', 'poem', '唐', '方干', '早春', '运行元化不参差，四极中华共一时。\\n正气才随灰律变，残寒便被柳条欺。\\n冰融大泽朝阳觉，草绿陈根夜雨知。\\n不信风光疾于箭，年来年去变霜髭。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3391', 'poem', '唐', '白居易', '思旧', '闲日一思旧，旧游如目前。\\n再思今何在，零落归下泉。\\n退之服硫黄，一病讫不痊。\\n微之炼秋石，未老身溘然。\\n杜子得丹诀，终日断腥膻。\\n崔君夸药力，经冬不衣绵。\\n或疾或暴夭，悉不过中年。\\n唯予不服食，老命反迟延。\\n况在少壮时，亦为嗜欲牵。\\n但耽荤与血，不识汞与铅。\\n饥来吞热物，渴来饮寒泉。\\n诗役五藏神，酒汩三丹田。\\n随日合破坏，至今粗完全。\\n齿牙未缺落，肢体尚轻便。\\n已开第七秩，饱食仍安眠。\\n且进杯中物，其馀皆付天。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3392', 'poem', '唐', '李白', '于五松山赠南陵常赞府', '为草当作兰，为木当作松。\\n兰秋香风远，松寒不改容。\\n松兰相因依，萧艾徒丰茸。\\n鸡与鸡并食，鸾与鸾同枝。\\n拣珠去沙砾，但有珠相随。\\n远客投名贤，真堪写怀抱。\\n若惜方寸心，待谁可倾倒。\\n虞卿弃赵相，便与魏齐行。\\n海上五百人，同日死田横。\\n当时不好贤，岂传千古名。\\n愿君同心人，于我少留情。\\n寂寂还寂寂，出门迷所适。\\n长铗归来乎，秋风思归客。', '2018-03-09 15:35:39');
INSERT INTO `t_poem` VALUES ('3393', 'poem', '唐', '杜甫', '诣徐卿觅果栽', '草堂少花今欲栽，不问绿李与黄梅。\\n石笋街中却归去，果园坊里为求来。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3394', 'poem', '唐', '岑参', '春兴戏题赠李侯', '黄雀始欲衔花来，君家种桃花未开。\\n长安二月眼看尽，寄报春风早为催。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3395', 'poem', '唐', '庄若讷', '湘灵鼓瑟', '帝子鸣金瑟，馀声自抑扬。\\n悲风丝上断，流水曲中长。\\n出没游鱼听，逶迤彩凤翔。\\n微音时扣徵，雅韵乍含商。\\n神理诚难测，幽情讵可量。\\n至今闻古调，应恨滞三湘。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3396', 'poem', '唐', '白居易', '和李澧州题韦开州经藏诗', '既悟莲花藏，须遗贝叶书。\\n菩提无处所，文字本空虚。\\n观指非知月，忘筌是得鱼。\\n闻君登彼岸，舍筏复何如。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3397', 'poem', '唐', '高适', '自蓟北归', '驱马蓟门北，北风边马哀。\\n苍茫远山口，豁达胡天开。\\n五将已深入，前军止半回。\\n谁怜不得意，长剑独归来。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3398', 'poem', '唐', '朱湾', '寻隐者韦九山人于东溪草堂', '寻得仙源访隐沦，渐来深处渐无尘。\\n初行竹里唯通马，直到花间始见人。\\n四面云山谁作主，数家烟火自为邻。\\n路傍樵客何须问，朝市如今不是秦。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3399', 'poem', '唐', '司空曙', '新柳', '全欺芳蕙晚，似妒寒梅疾。\\n撩乱发青条，春风来几日。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3400', 'poem', '唐', '周朴', '桃花', '桃花春色暖先开，明媚谁人不看来。\\n可惜狂风吹落后，殷红片片点莓苔。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3401', 'poem', '唐', '张继', '归山', '心事数茎白发，生涯一片青山。\\n空林有雪相待，古道无人独还。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3402', 'poem', '唐', '武元衡', '夕次潘山下', '南国独行日，三巴春草齐。\\n漾波归海疾，危栈入云迷。\\n锦谷岚烟里，刀州晚照西。\\n旅情方浩荡，蜀魄满林啼。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3403', 'poem', '唐', '白居易', '冬至宿杨梅馆', '十一月中长至夜，三千里外远行人。\\n若为独宿杨梅馆，冷枕单床一病身。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3404', 'poem', '唐', '许浑', '塞下', '夜战桑干北，秦兵半不归。\\n朝来有乡信，犹自寄征衣。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3405', 'poem', '唐', '卢储', '催妆', '昔年将去玉京游，第一仙人许状头。\\n今日幸为秦晋会，早教鸾凤下妆楼。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3406', 'poem', '唐', '刘禹锡', '学阮公体', '少年负志气，信道不从时。只言绳自知，安知室可欺？百胜难虑敌，三折乃良医。人生不失意，焉能慕知己？', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3407', 'poem', '唐', '包佶', '郊庙歌辞·祀风师乐章·亚献终献', '膋芗备，玉帛陈。\\n风动物，乐感神。\\n三献终，百神臻。\\n草木荣，天下春。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3408', 'poem', '唐', '陆龟蒙', '春思', '怨莺新语涩，双蝶斗飞高。\\n作个名春恨，浮生百倍劳。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3409', 'poem', '唐', '骆宾王', '咏水', '列名通地纪，疏派合天津。\\n波随月色净，态逐桃花春。\\n照霞如隐石，映柳似沉鳞。\\n终当挹上善，属意澹交人。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3410', 'poem', '唐', '张旭', '春草', '春草青青万里馀，边城落日见离居。\\n情知海上三年别，不寄云间一纸书。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3411', 'poem', '唐', '魏璀', '湘灵鼓瑟', '瑶瑟多哀怨，朱弦且莫听。\\n扁舟三楚客，丛竹二妃灵。\\n淅沥闻馀响，依稀欲辨形。\\n柱间寒水碧，曲里暮山青。\\n良马悲衔草，游鱼思绕萍。\\n知音若相遇，终不滞南溟。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3412', 'poem', '唐', '王贞白', '忆张处士', '天台张处士，诗句造玄微。\\n古乐知音少，名言与俗违。\\n山风入松径，海月上岩扉。\\n毕世唯高卧，无人说是非。', '2018-03-09 15:35:47');
INSERT INTO `t_poem` VALUES ('3413', 'poem', '唐', '张祜', '枫桥', '长洲苑外草萧萧，却算游城岁月遥。\\n唯有别时今不忘，暮烟疏雨过枫桥。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3414', 'poem', '唐', '牟融', '陈使君山庄', '新卜幽居地自偏，士林争羡使君贤。\\n数椽潇洒临溪屋，十亩膏腴附郭田。\\n流水断桥芳草路，淡烟疏雨落花天。\\n秋成准拟重来此，沉醉何妨一榻眠。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3415', 'poem', '唐', '白居易', '题韦家泉池', '泉落青山出白云，萦村绕郭几家分。\\n自从引作池中水，深浅方圆一任君。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3416', 'poem', '唐', '杜甫', '渡江', '春江不可渡，二月已风涛。\\n舟楫欹斜疾，鱼龙偃卧高。\\n渚花兼素锦，汀草乱青袍。\\n戏问垂纶客，悠悠见汝曹。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3417', 'poem', '唐', '汪遵', '杨柳', '亚夫营畔柳濛濛，隋主堤边四路通。\\n攀折赠君还有意，翠眉轻嫩怕春风。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3418', 'poem', '唐', '沈佺期', '寒食', '普天皆灭焰，匝地尽藏烟。\\n不知何处火，来就客心然。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3419', 'poem', '唐', '戴叔伦', '送前上饶严明府摄玉山', '家在故林吴楚间，冰为溪水玉为山。\\n更将旧政化邻邑，遥见逋人相逐还。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3420', 'poem', '唐', '张籍', '采莲曲', '秋江岸边莲子多，采莲女儿凭船歌。\\n青房圆实齐戢戢，争前竞折漾微波。\\n试牵绿茎下寻藕，断处丝多刺伤手。\\n白练束腰袖半卷，不插玉钗妆梳浅。\\n船中未满度前洲，借问阿谁家住远。\\n归时共待暮潮上，自弄芙蓉还荡桨。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3421', 'poem', '唐', '韩偓', '偶见', '秋千打困解罗裙，指点醍醐索一尊。\\n见客入来和笑走，手搓梅子映中门。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3422', 'poem', '唐', '卢照邻', '初夏日幽庄', '闻有高踪客，耿介坐幽庄。\\n林壑人事少，风烟鸟路长。\\n瀑水含秋气，垂藤引夏凉。\\n苗深全覆陇，荷上半侵塘。\\n钓渚青凫没，村田白鹭翔。\\n知君振奇藻，还嗣海隅芳。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3423', 'poem', '唐', '李白', '发白马', '将军发白马，旌节度黄河。\\n箫鼓聒川岳，沧溟涌涛波。\\n武安有振瓦，易水无寒歌。\\n铁骑若雪山，饮流涸滹沱。\\n扬兵猎月窟，转战略朝那。\\n倚剑登燕然，边烽列嵯峨。\\n萧条万里外，耕作五原多。\\n一扫清大漠，包虎戢金戈。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3424', 'poem', '唐', '张籍', '赠贾岛', '篱落荒凉僮仆饥，乐游原上住多时。\\n蹇驴放饱骑将出，秋卷装成寄与谁。\\n拄杖傍田寻野菜，封书乞米趁时炊。\\n姓名未上登科记，身屈惟应内史知。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3425', 'poem', '唐', '薛能', '吴姬十首', '夜锁重门昼亦监，眼波娇利瘦岩岩。\\n偏怜不怕傍人笑，自把春罗等舞衫。\\n龙麝薰多骨亦香，因经寒食好风光。\\n何人画得天生态，枕破施朱隔宿妆。\\n滴滴春霖透荔枝，笔题笺动手中垂。\\n天阴不得君王召，嚬著青蛾作小诗。\\n钿合重盛绣结深，昭阳初幸赐同心。\\n君知一夜恩多少，明日宣教放德音。\\n退红香汗湿轻纱，高卷蚊厨独卧斜。\\n娇泪半垂珠不破，恨君瞋折后庭花。\\n取次衣裳尽带珠，别添龙脑裛罗襦。\\n年来寄与乡中伴，杀尽春蚕税亦无。\\n画烛烧兰暖复迷，殿帏深密下银泥。\\n开门欲作侵晨散，已是明朝日向西。\\n楼台重叠满天云，殷殷鸣鼍世上闻。\\n此日杨花初似雪，女儿弦管弄参军。\\n冠剪黄绡帔紫罗，薄施铅粉画青娥。\\n因将素手夸纤巧，从此椒房宠更多。\\n身是三千第一名，内家丛里独分明。\\n芙蓉殿上中元日，水拍银台弄化生。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3426', 'poem', '唐', '陈子昂', '蓟丘览古赠卢居士藏用七首·燕太子', '秦王日无道，太子怨亦深。\\n一闻田光义，匕首赠千金。\\n其事虽不立，千载为伤心。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3427', 'poem', '唐', '冯著', '短歌行', '寂寞草中兰，亭亭山上松。\\n贞芳日有分，生长耐相容。\\n结根各得地，幸沾雨露功。\\n参辰无停泊，且顾一西东。\\n君但开怀抱，猜恨莫匆匆。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3428', 'poem', '唐', '李世民', '除夜', '岁阴穷暮纪，献节启新芳。\\n冬尽今宵促，年开明日长。\\n冰消出镜水，梅散入风香。\\n对此欢终宴，倾壶待曙光。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3429', 'poem', '唐', '李益', '赠毛仙翁', '玉树溶溶仙气深，含光混俗似无心。\\n长愁忽作鹤飞去，一片孤云何处寻。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3430', 'poem', '唐', '李九龄', '读三国志', '有国由来在得贤，莫言兴废是循环。\\n武侯星落周瑜死，平蜀降吴似等闲。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3431', 'poem', '唐', '韩愈', '题木居士二首', '火透波穿不计春，根如头面干如身。\\n偶然题作木居士，便有无穷求福人。\\n为神讵比沟中断，遇赏还同爨下馀。\\n朽蠹不胜刀锯力，匠人虽巧欲何如。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3432', 'poem', '唐', '王昌龄', '送高三之桂林', '留君夜饮对潇湘，从此归舟客梦长。\\n岭上梅花侵雪暗，归时还拂桂花香。', '2018-03-09 15:35:55');
INSERT INTO `t_poem` VALUES ('3433', 'poem', '唐', '白居易', '咏老赠梦得', '与君俱老也，自问老何如。\\n眼涩夜先卧，头慵朝未梳。\\n有时扶杖出，尽日闭门居。\\n懒照新磨镜，休看小字书。\\n情于故人重，迹共少年疏。\\n唯是闲谈兴，相逢尚有馀。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3434', 'poem', '唐', '戴叔伦', '潘处士宅会别', '相邀寒影晚，惜别故山空。\\n邻里疏林在，池塘野水通。\\n十年难遇后，一醉几人同。\\n复此悲行子，萧萧逐转蓬。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3435', 'poem', '唐', '司空图', '诗品二十四则·实境', '取语甚直，计思匪深。\\n忽逢幽人，如见道心。\\n晴涧之曲，碧松之阴。\\n一客荷樵，一客听琴。\\n情性所至，妙不自寻。\\n遇之自天，冷然希音。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3436', 'poem', '唐', '杜牧', '山石榴', '似火山榴映小山，繁中能薄艳中闲。\\n一朵佳人玉钗上，只疑烧却翠云鬟。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3437', 'poem', '唐', '白居易', '即事寄微之', '畬田涩米不耕锄，旱地荒园少菜蔬。\\n想念土风今若此，料看生计合何如。\\n衣缝纰颣黄丝绢，饭下腥咸白小鱼。\\n饱暖饥寒何足道，此身长短是空虚。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3438', 'poem', '唐', '韩愈', '池上絮', '池上无风有落晖，杨花晴后自飞飞。\\n为将纤质凌清镜，湿却无穷不得归。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3439', 'poem', '唐', '韩偓', '夏夜', '猛风飘电黑云生，霎霎高林簇雨声。\\n夜久雨休风又定，断云流月却斜明。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3440', 'poem', '唐', '姚合', '杏溪十首·杏水', '不与江水接，自出林中央。\\n穿花复远水，一山闻杏香。\\n我来持茗瓯，日屡此来尝。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3441', 'poem', '唐', '韦庄', '天井关', '太行山上云深处，谁向云中筑女墙。\\n短绠讵能垂玉甃，缭垣何用学金汤。\\n劚开岚翠为高垒，截断云霞作巨防。\\n守吏不教飞鸟过，赤眉何路到吾乡。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3442', 'poem', '唐', '白居易', '睡觉', '星河耿耿漏绵绵，月暗灯微欲曙天。\\n转枕频伸书帐下，披裘箕踞火炉前。\\n老眠早觉常残夜，病力先衰不待年。\\n五欲已销诸念息，世间无境可勾牵。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3443', 'poem', '唐', '王播', '题木兰院二首', '三十年前此院游，木兰花发院新修。\\n如今再到经行处，树老无花僧白头。\\n上堂已了各西东，惭愧阇黎饭后钟。\\n三十年来尘扑面，如今始得碧纱笼。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3444', 'poem', '唐', '刘禹锡', '鹤叹二首', '寂寞一双鹤，主人在西京。\\n故巢吴苑树，深院洛阳城。\\n徐引竹间步，远含云外情。\\n谁怜好风月，邻舍夜吹笙。\\n丹顶宜承日，霜翎不染泥。\\n爱池能久立，看月未成栖。\\n一院春草长，三山归路迷。\\n主人朝谒早，贪养汝南鸡。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3445', 'poem', '唐', '韦应物', '见紫荆花', '杂英纷已积，含芳独暮春。\\n还如故园树，忽忆故园人。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3446', 'poem', '唐', '雍裕之', '江边柳', '袅袅古堤边，青青一树烟。\\n若为丝不断，留取系郎船。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3447', 'poem', '唐', '栖蟾', '游边', '边云四顾浓，饥马嗅枯丛。\\n万里八九月，一身西北风。\\n偷营天正黑，战地雪多红。\\n昨夜东归梦，桃花暖色中。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3448', 'poem', '唐', '赵嘏', '春酿', '春酿正风流，梨花莫问愁。\\n马卿思一醉，不惜鹔鹴裘。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3449', 'poem', '唐', '齐己', '咏茶十二韵', '百草让为灵，功先百草成。\\n甘传天下口，贵占火前名。\\n出处春无雁，收时谷有莺。\\n封题从泽国，贡献入秦京。\\n嗅觉精新极，尝知骨自轻。\\n研通天柱响，摘绕蜀山明。\\n赋客秋吟起，禅师昼卧惊。\\n角开香满室，炉动绿凝铛。\\n晚忆凉泉对，闲思异果平。\\n松黄干旋泛，云母滑随倾。\\n颇贵高人寄，尤宜别匮盛。\\n曾寻修事法，妙尽陆先生。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3450', 'poem', '唐', '李白', '登广武古战场怀古', '秦鹿奔野草，逐之若飞蓬。\\n项王气盖世，紫电明双瞳。\\n呼吸八千人，横行起江东。\\n赤精斩白帝，叱咤入关中。\\n两龙不并跃，五纬与天同。\\n楚灭无英图，汉兴有成功。\\n按剑清八极，归酣歌大风。\\n伊昔临广武，连兵决雌雄。\\n分我一杯羹，太皇乃汝翁。\\n战争有古迹，壁垒颓层穹。\\n猛虎啸洞壑，饥鹰鸣秋空。\\n翔云列晓阵，杀气赫长虹。\\n拨乱属豪圣，俗儒安可通。\\n沉湎呼竖子，狂言非至公。\\n抚掌黄河曲，嗤嗤阮嗣宗。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3451', 'poem', '唐', '神赞', '空门不肯出', '空门不肯出，投窗也大痴。\\n百年钻故纸，何日出头时。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3452', 'poem', '唐', '武则天', '游九龙潭', '山窗游玉女，涧户对琼峰。\\n岩顶翔双凤，潭心倒九龙。\\n酒中浮竹叶，杯上写芙蓉。\\n故验家山赏，惟有风入松。', '2018-03-09 15:36:05');
INSERT INTO `t_poem` VALUES ('3453', 'poem', '唐', '汪遵', '咏酒二首', '九酝松醪一曲歌，本图闲放养天和。\\n后人不识前贤意，破国亡家事甚多。\\n万事销沈向一杯，竹门哑轧为风开。\\n秋宵睡足芭蕉雨，又是江湖入梦来。', '2018-03-09 15:36:12');
INSERT INTO `t_poem` VALUES ('3454', 'poem', '唐', '崔道融', '鸡', '买得晨鸡共鸡语，常时不用等闲鸣。\\n深山月黑风雨夜，欲近晓天啼一声。', '2018-03-09 15:36:12');
INSERT INTO `t_poem` VALUES ('3455', 'poem', '唐', '羊士谔', '初移琪树', '爱此丘中物，烟霜尽日看。\\n无穷碧云意，更助绿窗寒。', '2018-03-09 15:36:12');
INSERT INTO `t_poem` VALUES ('3456', 'poem', '唐', '黄滔', '广州试越台怀古', '南越千年事，兴怀一旦来。\\n歌钟非旧俗，烟月有层台。\\n北望人何在，东流水不回。\\n吹窗风杂瘴，沾槛雨经梅。\\n壮气曾难揖，空名信可哀。\\n不堪登览处，花落与花开。', '2018-03-09 15:36:12');
INSERT INTO `t_poem` VALUES ('3457', 'poem', '唐', '李商隐', '柳枝五首', '花房与蜜脾，蜂雄蛱蝶雌。\\n同时不同类，那复更相思。\\n本是丁香树，春条结始生。\\n玉作弹棋局，中心亦不平。\\n嘉瓜引蔓长，碧玉冰寒浆。\\n东陵虽五色，不忍值牙香。\\n柳枝井上蟠，莲叶浦中干。\\n锦鳞与绣羽，水陆有伤残。\\n画屏绣步障，物物自成双。\\n如何湖上望，只是见鸳鸯。', '2018-03-09 15:36:12');
INSERT INTO `t_poem` VALUES ('3458', 'poem', '唐', '陈子昂', '燕昭王', '南登碣石馆，遥望黄金台。\\n丘陵尽乔木，昭王安在哉？\\n霸图今已矣，驱马复归来。', '2018-03-09 15:36:12');
INSERT INTO `t_poem` VALUES ('3459', 'poem', '唐', '杜甫', '江梅', '梅蕊腊前破，梅花年后多。\\n绝知春意好，最奈客愁何。\\n雪树元同色，江风亦自波。\\n故园不可见，巫岫郁嵯峨。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3460', 'poem', '唐', '杨凭', '乐游园望月', '炎灵全盛地，明月半秋时。\\n今古人同望，盈亏节暗移。\\n彩凝双月迥，轮度八川迟。\\n共惜鸣珂去，金波送酒卮。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3461', 'poem', '唐', '李商隐', '房中曲', '蔷薇泣幽素，翠带花钱小。\\n娇郎痴若云，抱日西帘晓。\\n枕是龙宫石，割得秋波色。\\n玉簟失柔肤，但见蒙罗碧。\\n忆得前年春，未语含悲辛。\\n归来已不见，锦瑟长于人。\\n今日涧底松，明日山头檗。\\n愁到天池翻，相看不相识。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3462', 'poem', '唐', '韩翃', '送王少府归杭州', '归舟一路转青蘋，更欲随潮向富春。\\n吴郡陆机称地主，钱塘苏小是乡亲。\\n葛花满把能消酒，栀子同心好赠人。\\n早晚重过鱼浦宿，遥怜佳句箧中新。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3463', 'poem', '唐', '张籍', '水', '荡漾空沙际，虚明入远天。\\n秋光照不极，鸟色去无边。\\n势引长云阔，波轻片雪连。\\n汀洲杳难测，万古覆苍烟。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3464', 'poem', '唐', '李世民', '临洛水', '春搜驰骏骨，总辔俯长河。\\n霞处流萦锦，风前漾卷罗。\\n水花翻照树，堤兰倒插波。\\n岂必汾阴曲，秋云发棹歌。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3465', 'poem', '唐', '冯延巳', '玉楼春', '雪云乍变春云簇，渐觉年华堪纵目。\\n北枝梅蕊犯寒开，南浦波纹如酒绿。\\n芳菲次第长相续，自是情多无处足。\\n尊前百计见春归，莫为伤春眉黛蹙。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3466', 'poem', '唐', '孟浩然', '岁除夜会乐城张少府宅', '畴昔通家好，相知无间然。\\n续明催画烛，守岁接长筵。\\n旧曲梅花唱，新正柏酒传。\\n客行随处乐，不见度年年。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3467', 'poem', '唐', '郑谷', '席上贻歌者', '花月楼台近九衢，清歌一曲倒金壶。\\n座中亦有江南客，莫向春风唱鹧鸪。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3468', 'poem', '唐', '陈子昂', '题祀山烽树赠乔十二侍御', '汉庭荣巧宦，云阁薄边功。\\n可怜骢马使，白首为谁雄。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3469', 'poem', '唐', '张叔卿', '空灵岸', '寒尽鸿先去，江回客未归。\\n早知名是幻，不敢绣为衣。\\n雾积川原暗，山多郡县稀。\\n今朝下湘岸，更逐鹧鸪飞。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3470', 'poem', '唐', '白居易', '病起', '病不出门无限时，今朝强出与谁期。\\n经年不上江楼醉，劳动春风飏酒旗。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3471', 'poem', '唐', '岑参', '送杨子', '斗酒渭城边，垆头耐醉眠。\\n梨花千树雪，杨叶万条烟。\\n惜别添壶酒，临岐赠马鞭。\\n看君颍上去，新月到家圆。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3472', 'poem', '唐', '温庭筠', '和友人伤歌姬', '月缺花残莫怆然，花须终发月终圆。\\n更能何事销芳念，亦有浓华委逝川。\\n一曲艳歌留婉转，九原春草妒婵娟。\\n王孙莫学多情客，自古多情损少年。', '2018-03-09 15:36:13');
INSERT INTO `t_poem` VALUES ('3473', 'poem', '唐', '韩偓', '村居', '二月三月雨晴初，舍南舍北唯平芜。\\n前欢入望盈千恨，胜景牵心非一途。\\n日照神堂闻啄木，风含社树叫提壶。\\n行看旦夕梨霜发，犹有山寒伤酒垆。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3474', 'poem', '唐', '白居易', '三年为刺史二首', '三年为刺史，无政在人口。\\n唯向城郡中，题诗十馀首。\\n惭非甘棠咏，岂有思人不。\\n三年为刺史，饮冰复食檗。\\n唯向天竺山，取得两片石。\\n此抵有千金，无乃伤清白。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3475', 'poem', '唐', '张籍', '杂曲歌辞·秋夜长', '秋天如水夜未央，天汉东西月色光。\\n愁人不寐畏枕席，暗虫唧唧绕我傍。\\n荒城为村无更声，起看北斗天未明。\\n白露满田风褭褭，千声万声鹖鸟鸣。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3476', 'poem', '唐', '厉玄', '从军行', '边草旱不春，剑光增野尘。\\n战场收骥尾，清瀚怯龙鳞。\\n帆色起归越，松声厌避秦。\\n几时逢范蠡，处处是通津。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3477', 'poem', '唐', '杜甫', '游修觉寺', '野寺江天豁，山扉花竹幽。\\n诗应有神助，吾得及春游。\\n径石相萦带，川云自去留。\\n禅枝宿众鸟，漂转暮归愁。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3478', 'poem', '唐', '白居易', '寄生衣与微之，因题封上', '浅色縠衫轻似雾，纺花纱袴薄于云。\\n莫嫌轻薄但知著，犹恐通州热杀君。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3479', 'poem', '唐', '灵一', '林公', '支公信高逸，久向山林住。\\n时将孙许游，岂以形骸遇。\\n幸辞天子诏，复览名臣疏。\\n西晋尚虚无，南朝久沦误。\\n因谈老庄意，乃尽逍遥趣。\\n谁为竹林贤，风流相比附。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3480', 'poem', '唐', '李白', '君子有所思行', '紫阁连终南，青冥天倪色。\\n凭崖望咸阳，宫阙罗北极。\\n万井惊画出，九衢如弦直。\\n渭水银河清，横天流不息。\\n朝野盛文物，衣冠何翕赩。\\n厩马散连山，军容威绝域。\\n伊皋运元化，卫霍输筋力。\\n歌钟乐未休，荣去老还逼。\\n圆光过满缺，太阳移中昃。\\n不散东海金，何争西飞匿。\\n无作牛山悲，恻怆泪沾臆。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3481', 'poem', '唐', '刘禹锡', '别夔州官吏', '三年楚国巴城守，一去扬州扬子津。\\n青帐联延喧驿步，白头俯伛到江滨。\\n巫山暮色常含雨，峡水秋来不恐人。\\n惟有九歌词数首，里中留与赛蛮神。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3482', 'poem', '唐', '李频', '湘口送友人', '中流欲暮见湘烟，苇岸无穷接楚田。\\n去雁远冲云梦雪，离人独上洞庭船。\\n风波尽日依山转，星汉通霄向水悬。\\n零落梅花过残腊，故园归醉及新年。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3483', 'poem', '唐', '刘禹锡', '浪淘沙', '日照澄州江雾开，淘金女伴满江隈。\\n美人首饰侯王印，尽是沙中浪底来。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3484', 'poem', '唐', '王维', '答裴迪辋口遇雨忆终南山之作', '淼淼寒流广，苍苍秋雨晦。\\n君问终南山，心知白云外。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3485', 'poem', '唐', '胡曾', '咏史诗·圯桥', '庙算张良独有余，少年逃难下邳初。\\n逡巡不进泥中履，争得先生一卷书。', '2018-03-09 15:36:19');
INSERT INTO `t_poem` VALUES ('3486', 'poem', '唐', '汪遵', '咏酒二首', '九酝松醪一曲歌，本图闲放养天和。\\n后人不识前贤意，破国亡家事甚多。\\n万事销沈向一杯，竹门哑轧为风开。\\n秋宵睡足芭蕉雨，又是江湖入梦来。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3487', 'poem', '唐', '崔道融', '鸡', '买得晨鸡共鸡语，常时不用等闲鸣。\\n深山月黑风雨夜，欲近晓天啼一声。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3488', 'poem', '唐', '羊士谔', '初移琪树', '爱此丘中物，烟霜尽日看。\\n无穷碧云意，更助绿窗寒。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3489', 'poem', '唐', '黄滔', '广州试越台怀古', '南越千年事，兴怀一旦来。\\n歌钟非旧俗，烟月有层台。\\n北望人何在，东流水不回。\\n吹窗风杂瘴，沾槛雨经梅。\\n壮气曾难揖，空名信可哀。\\n不堪登览处，花落与花开。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3490', 'poem', '唐', '李商隐', '柳枝五首', '花房与蜜脾，蜂雄蛱蝶雌。\\n同时不同类，那复更相思。\\n本是丁香树，春条结始生。\\n玉作弹棋局，中心亦不平。\\n嘉瓜引蔓长，碧玉冰寒浆。\\n东陵虽五色，不忍值牙香。\\n柳枝井上蟠，莲叶浦中干。\\n锦鳞与绣羽，水陆有伤残。\\n画屏绣步障，物物自成双。\\n如何湖上望，只是见鸳鸯。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3491', 'poem', '唐', '陈子昂', '燕昭王', '南登碣石馆，遥望黄金台。\\n丘陵尽乔木，昭王安在哉？\\n霸图今已矣，驱马复归来。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3492', 'poem', '唐', '杜甫', '江梅', '梅蕊腊前破，梅花年后多。\\n绝知春意好，最奈客愁何。\\n雪树元同色，江风亦自波。\\n故园不可见，巫岫郁嵯峨。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3493', 'poem', '唐', '杨凭', '乐游园望月', '炎灵全盛地，明月半秋时。\\n今古人同望，盈亏节暗移。\\n彩凝双月迥，轮度八川迟。\\n共惜鸣珂去，金波送酒卮。', '2018-03-09 15:38:15');
