INSERT INTO `t_poem` VALUES ('3497', 'poem', '唐', '李世民', '临洛水', '春搜驰骏骨，总辔俯长河。\\n霞处流萦锦，风前漾卷罗。\\n水花翻照树，堤兰倒插波。\\n岂必汾阴曲，秋云发棹歌。', '2018-03-09 15:38:15');
