INSERT INTO `t_poem` VALUES ('3503', 'poem', '唐', '白居易', '病起', '病不出门无限时，今朝强出与谁期。\\n经年不上江楼醉，劳动春风飏酒旗。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3504', 'poem', '唐', '岑参', '送杨子', '斗酒渭城边，垆头耐醉眠。\\n梨花千树雪，杨叶万条烟。\\n惜别添壶酒，临岐赠马鞭。\\n看君颍上去，新月到家圆。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3505', 'poem', '唐', '温庭筠', '和友人伤歌姬', '月缺花残莫怆然，花须终发月终圆。\\n更能何事销芳念，亦有浓华委逝川。\\n一曲艳歌留婉转，九原春草妒婵娟。\\n王孙莫学多情客，自古多情损少年。', '2018-03-09 15:38:15');
INSERT INTO `t_poem` VALUES ('3506', 'poem', '唐', '李世民', '春日望海', '披襟眺沧海，凭轼玩春芳。\\n积流横地纪，疏派引天潢。\\n仙气凝三岭，和风扇八荒。\\n拂潮云布色，穿浪日舒光。\\n照岸花分彩，迷云雁断行。\\n怀卑运深广，持满守灵长。\\n有形非易测，无源讵可量。\\n洪涛经变野，翠岛屡成桑。\\n之罘思汉帝，碣石想秦皇。\\n霓裳非本意，端拱且图王。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3507', 'poem', '唐', '王贞白', '庐山', '岳立镇南楚，雄名天下闻。\\n五峰高阂日，九叠翠连云。\\n夏谷雪犹在，阴岩昼不分。\\n唯应嵩与华，清峻得为群。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3508', 'poem', '唐', '赵虚舟', '戏赠', '砌下梧桐叶正齐，花繁雨后压枝低。\\n报道不须鸦鸟乱，他家自有凤凰栖。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3509', 'poem', '唐', '陆龟蒙', '峡客行', '万仞峰排千剑束，孤舟夜系峰头宿。\\n蛮溪雪坏蜀江倾，滟滪朝来大如屋。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3510', 'poem', '唐', '杜牧', '游边', '黄沙连海路无尘，边草长枯不见春。\\n日暮拂云堆下过，马前逢著射雕人。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3511', 'poem', '唐', '刘禹锡', '八月十五日夜玩月', '天将今夜月，一遍洗寰瀛。\\n暑退九霄净，秋澄万景清。\\n星辰让光彩，风露发晶英。\\n能变人间世，翛然是玉京。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3512', 'poem', '唐', '王维', '赠韦穆十八', '与君青眼客，共有白云心。\\n不向东山去，日令春草深。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3513', 'poem', '唐', '韦铿', '嘲邵景、萧嵩', '一双胡子著绯袍，一个须多一鼻高。\\n相对厅前捺且立，自惭身品世间毛。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3514', 'poem', '唐', '白居易', '遣怀', '羲和走驭趁年光，不许人间日月长。\\n遂使四时都似电，争教两鬓不成霜。\\n荣销枯去无非命，壮尽衰来亦是常。\\n已共身心要约定，穷通生死不惊忙。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3515', 'poem', '唐', '李衍', '过白卫岭和韩昭', '先朝神武力开边，画断封疆四五千。\\n前望陇山屯剑戟，后凭巫峡锁烽烟。\\n轩皇尚自亲平寇，嬴政徒劳爱学仙。\\n想到隗宫寻胜处，正应莺语暮春天。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3516', 'poem', '唐', '唐彦谦', '垂柳', '绊惹春风别有情，世间谁敢斗轻盈。\\n楚王江畔无端种，饿损纤腰学不成。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3517', 'poem', '唐', '孟郊', '出东门', '饿马骨亦耸，独驱出东门。\\n少年一日程，衰叟十日奔。\\n寒景不我为，疾走落平原。\\n眇默荒草行，恐惧夜魄翻。\\n一生自组织，千首大雅言。\\n道路如抽蚕，宛转羁肠繁。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3518', 'poem', '唐', '赵嘏', '江楼感旧', '独上江楼思渺然，月光如水水如天。\\n同来望月人何处？风景依稀似去年。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3519', 'poem', '唐', '张又新', '孤屿', '碧水逶迤浮翠巘，绿萝蒙密媚晴江。\\n不知谁与名孤屿，其实中川是一双。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3520', 'poem', '唐', '李山甫', '曲江二首', '南山低对紫云楼，翠影红阴瑞气浮。\\n一种是春长富贵，大都为水也风流。\\n争攀柳带千千手，间插花枝万万头。\\n独向江边最惆怅，满衣尘土避王侯。\\n江色沈天万草齐，暖烟晴霭自相迷。\\n蜂怜杏蕊细香落，莺坠柳条浓翠低。\\n千队国娥轻似雪，一群公子醉如泥。\\n斜阳怪得长安动，陌上分飞万马蹄。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3521', 'poem', '唐', '司空图', '早春', '伤怀同客处，病眼却花朝。\\n草嫩侵沙短，冰轻著雨消。\\n风光知可爱，容发不相饶。\\n早晚丹丘去，飞书肯见招。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3522', 'poem', '唐', '刘长卿', '送李使君贬连州', '独过长沙去，谁堪此路愁。\\n秋风散千骑，寒雨泊孤舟。\\n贾谊辞明主，萧何识故侯。\\n汉廷当自召，湘水但空流。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3523', 'poem', '唐', '白居易', '欲与元八卜邻，先有是赠', '平生心迹最相亲，欲隐墙东不为身。\\n明月好同三径夜，绿杨宜作两家春。\\n每因暂出犹思伴，岂得安居不择邻。\\n何独终身数相见，子孙长作隔墙人。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3524', 'poem', '唐', '无名氏', '夏', '赤帝旗迎火云起，南山石裂吴牛死。\\n绣楹夜夜箔虾须，象榻重重簟湘水。\\n彤彤日脚烧冰井，古陌尘飞野烟静。\\n汉帝高堂汗若珠，班姬明月无停影。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3525', 'poem', '唐', '张籍', '咏怀', '老去多悲事，非唯见二毛。\\n眼昏书字大，耳重觉声高。\\n望月偏增思，寻山易发劳。\\n都无作官意，赖得在闲曹。', '2018-03-09 15:38:25');
INSERT INTO `t_poem` VALUES ('3526', 'poem', '唐', '杨志坚', '送妻', '平生志业在琴诗，头上如今有二丝。\\n渔父尚知溪谷暗，山妻不信出身迟。\\n荆钗任意撩新鬓，明镜从他别画眉。\\n今日便同行路客，相逢即是下山时。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3527', 'poem', '唐', '许浑', '晚登龙门驿楼', '鱼龙多处凿门开，万古人知夏禹材。\\n青嶂远分从地断，洪流高泻自天来。\\n风云有路皆烧尾，波浪无程尽曝腮。\\n心感膺门身过此，晚山秋树独徘徊。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3528', 'poem', '唐', '贾岛', '清明日园林寄友人', '今日清明节，园林胜事偏。\\n晴风吹柳絮，新火起厨烟。\\n杜草开三径，文章忆二贤。\\n几时能命驾，对酒落花前。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3529', 'poem', '唐', '元稹', '表夏十首', '夏风多暖暖，树木有繁阴。\\n新笋紫长短，早樱红浅深。\\n烟花云幕重，榴艳朝景侵。\\n华实各自好，讵云芳意沉。\\n初日满阶前，轻风动帘影。\\n旬时得休浣，高卧阅清景。\\n僮儿拂巾箱，鸦轧深林井。\\n心到物自闲，何劳远箕颍。\\n江瘴炎夏早，蒸腾信难度。\\n今宵好风月，独此荒庭趣。\\n露叶倾暗光，流星委馀素。\\n但恐清夜徂，讵悲朝景暮。\\n孟月夏犹浅，奇云未成峰。\\n度霞红漠漠，压浪白溶溶。\\n玉委有馀润，飙驰无去踪。\\n何如捧云雨，喷毒随蛟龙。\\n流芳递炎景，繁英尽寥落。\\n公署香满庭，晴霞覆阑药。\\n裁红起高焰，缀绿排新萼。\\n凭此遣幽怀，非言念将谑。\\n红丝散芳树，旋转光风急。\\n烟泛被笼香，露浓妆面湿。\\n佳人不在此，恨望阶前立。\\n忽厌夏景长，今春行已及。\\n百舌渐吞声，黄莺正娇小。\\n云鸿方警夜，笼鸡已鸣晓。\\n当时客自适，运去谁能矫。\\n莫厌夏虫多，蜩螗定相扰。\\n翩翩帘外燕，戢戢巢内雏。\\n啖食筋力尽，毛衣成紫襦。\\n朝来各飞去，雄雌梁上呼。\\n养子将备老，恶儿那胜无。\\n西山夏雪消，江势东南泻。\\n风波高若天，滟滪低于马。\\n正被黄牛旋，难期白帝下。\\n我在平地行，翻忧济川者。\\n灵均死波后，是节常浴兰。\\n彩缕碧筠粽，香粳白玉团。\\n逝者良自苦，今人反为欢。\\n哀哉徇名士，没命求所难。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3530', 'poem', '唐', '韩愈', '杂诗四首', '朝蝇不须驱，暮蚊不可拍。\\n蝇蚊满八区，可尽与相格。\\n得时能几时，与汝恣啖咋。\\n凉风九月到，扫不见踪迹。\\n鹊鸣声楂楂，乌噪声护护。\\n争斗庭宇间，持身博弹射。\\n黄鹄能忍饥，两翅久不擘。\\n苍苍云海路，岁晚将无获。\\n截橑为欂栌，斫楹以为椽。\\n束蒿以代之，小大不相权。\\n虽无风雨灾，得不覆且颠。\\n解辔弃骐骥，蹇驴鞭使前。\\n昆仑高万里，岁尽道苦邅。\\n停车卧轮下，绝意于神仙。\\n雀鸣朝营食，鸠鸣暮觅群。\\n独有知时鹤，虽鸣不缘身。\\n喑蝉终不鸣，有抱不列陈。\\n蛙黾鸣无谓，閤閤只乱人。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3531', 'poem', '唐', '司空图', '渡江', '秋江共僧渡，乡泪滴船回。\\n一夜吴船梦，家书立马开。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3532', 'poem', '唐', '李德裕', '盘陀岭驿楼', '嵩少心期杳莫攀，好山聊复一开颜。\\n明朝便是南荒路，更上层楼望故关。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3533', 'poem', '唐', '杜牧', '读韩杜集', '杜诗韩笔愁来读，似倩麻姑痒处搔。\\n天外凤凰谁得髓？无人解合续弦胶。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3534', 'poem', '唐', '李泌', '咏方圆动静', '方如行义，圆如用智。\\n动如逞才，静如遂意。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3535', 'poem', '唐', '韩愈', '游城南十六首·遣兴', '断送一生惟有酒，寻思百计不如闲。\\n莫忧世事兼身事，须著人间比梦间。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3536', 'poem', '唐', '白居易', '醉后赠人', '香球趁拍回环匼，花醆抛巡取次飞。\\n自入春来未同醉，那能夜去独先归。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3537', 'poem', '唐', '李白', '别匡山', '晓峰如画碧参差，藤影风摇拂槛垂。\\n野径来多将犬伴，人间归晚带樵随。\\n看云客倚啼猿树，洗钵僧临失鹤池。\\n莫怪无心恋清境，已将书剑许明时。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3538', 'poem', '唐', '李白', '襄阳曲四首', '襄阳行乐处，歌舞白铜鞮。\\n江城回绿水，花月使人迷。\\n山公醉酒时，酩酊高阳下。\\n头上白接篱，倒著还骑马。\\n岘山临汉江，水绿沙如雪。\\n上有堕泪碑，青苔久磨灭。\\n且醉习家池，莫看堕泪碑。\\n山公欲上马，笑杀襄阳儿。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3539', 'poem', '唐', '白居易', '咏怀', '岁去年来尘土中，眼看变作白头翁。\\n如何办得归山计，两顷村田一亩宫。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3540', 'poem', '唐', '于邺', '书情', '负郭有田在，年年长废耕。\\n欲磨秋镜净，恐见白头生。\\n未作一旬别，已过千里程。\\n不知书与剑，十载两无成。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3541', 'poem', '唐', '元稹', '使东川·梁州梦', '梦君同绕曲江头，也向慈恩院院游。\\n亭吏呼人排去马，所惊身在古梁州。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3542', 'poem', '唐', '崔道融', '雪窦禅师', '雪窦峰前一派悬，雪窦五月无炎天。\\n客尘半日洗欲尽，师到白头林下禅。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3543', 'poem', '唐', '徐惠', '赋得北方有佳人', '由来称独立，本自号倾城。\\n柳叶眉间发，桃花脸上生。\\n腕摇金钏响，步转玉环鸣。\\n纤腰宜宝袜，红衫艳织成。\\n悬知一顾重，别觉舞腰轻。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3544', 'poem', '唐', '杜甫', '诸葛庙', '久游巴子国，屡入武侯祠。\\n竹日斜虚寝，溪风满薄帷。\\n君臣当共济，贤圣亦同时。\\n翊戴归先主，并吞更出师。\\n虫蛇穿画壁，巫觋醉蛛丝。\\n欻忆吟梁父，躬耕也未迟。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3545', 'poem', '唐', '白居易', '枯桑', '道傍老枯树，枯来非一朝。\\n皮黄外尚活，心黑中先焦。\\n有似多忧者，非因外火烧。', '2018-03-09 15:38:37');
INSERT INTO `t_poem` VALUES ('3546', 'poem', '唐', '白居易', '八月十五日夜禁中独直，对月忆元九', '银台金阙夕沈沈，独宿相思在翰林。\\n三五夜中新月色，二千里外故人心。\\n渚宫东面烟波冷，浴殿西头钟漏深。\\n犹恐清光不同见，江陵卑湿足秋阴。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3547', 'poem', '唐', '王贞白', '江上吟晓', '一叶野人舟，长将载酒游。\\n夜来吟思苦，江上月华秋。\\n晓露满红蓼，轻波飏白鸥。\\n渔翁似有约，相伴钓中流。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3548', 'poem', '唐', '张籍', '秋夜长', '秋天如水夜未央，天汉东西月色光。\\n愁人不寐畏枕席，暗虫唧唧绕我傍。\\n荒城为村无更声，起看北斗天未明。\\n白露满田风袅袅，千声万声鹖鸟鸣。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3549', 'poem', '唐', '杜甫', '初冬', '垂老戎衣窄，归休寒色深。\\n渔舟上急水，猎火著高林。\\n日有习池醉，愁来梁甫吟。\\n干戈未偃息，出处遂何心。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3550', 'poem', '唐', '唐彦谦', '离鸾', '闻道离鸾思故乡，也知情愿嫁王昌。\\n尘埃一别杨朱路，风月三年宋玉墙。\\n下疾不成双点泪，断多难到九回肠。\\n庭前佳树名栀子，试结同心寄谢娘。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3551', 'poem', '唐', '白居易', '雨中招张司业宿', '过夏衣香润，迎秋簟色鲜。\\n斜支花石枕，卧咏蕊珠篇。\\n泥泞非游日，阴沉好睡天。\\n能来同宿否，听雨对床眠。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3552', 'poem', '唐', '李商隐', '柳', '动春何限叶，撼晓几多枝。\\n解有相思否，应无不舞时。\\n絮飞藏皓蝶，带弱露黄鹂。\\n倾国宜通体，谁来独赏眉。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3553', 'poem', '唐', '李白', '纪南陵题五松山', '圣达有去就，潜光愚其德。\\n鱼与龙同池，龙去鱼不测。\\n当时版筑辈，岂知傅说情。\\n一朝和殷羹，光气为列星。\\n伊尹生空桑，捐庖佐皇极。\\n桐宫放太甲，摄政无愧色。\\n三年帝道明，委质终辅翼。\\n旷哉至人心，万古可为则。\\n时命或大缪，仲尼将奈何。\\n鸾凤忽覆巢，麒麟不来过。\\n龟山蔽鲁国，有斧且无柯。\\n归来归去来，宵济越洪波。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3554', 'poem', '唐', '陆龟蒙', '素丝', '园客丽独茧，诗人吟五緵。\\n如何墨子泪，反以悲途穷。\\n我意岂如是，愿参天地功。\\n为线补君衮，为弦系君桐。\\n左右修阙职，宫商还古风。\\n端然洁白心，可与神明通。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3555', 'poem', '唐', '刘禹锡', '磨镜篇', '流尘翳明镜，岁久看如漆。\\n门前负局人，为我一磨拂。\\n萍开绿池满，晕尽金波溢。\\n白日照空心，圆光走幽室。\\n山神妖气沮，野魅真形出。\\n却思未磨时，瓦砾来唐突。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3556', 'poem', '唐', '杜甫', '水槛', '苍江多风飙，云雨昼夜飞。\\n茅轩驾巨浪，焉得不低垂。\\n游子久在外，门户无人持。\\n高岸尚如谷，何伤浮柱欹。\\n扶颠有劝诫，恐贻识者嗤。\\n既殊大厦倾，可以一木支。\\n临川视万里，何必阑槛为。\\n人生感故物，慷慨有馀悲。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3557', 'poem', '唐', '杜甫', '夏日叹', '夏日出东北，陵天经中街。\\n朱光彻厚地，郁蒸何由开。\\n上苍久无雷，无乃号令乖。\\n雨降不濡物，良田起黄埃。\\n飞鸟苦热死，池鱼涸其泥。\\n万人尚流冗，举目唯蒿莱。\\n至今大河北，化作虎与豺。\\n浩荡想幽蓟，王师安在哉。\\n对食不能餐，我心殊未谐。\\n眇然贞观初，难与数子偕。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3558', 'poem', '唐', '韩愈', '知音者诚希', '知音者诚希，念子不能别。\\n行行天未晓，携酒踏明月。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3559', 'poem', '唐', '李商隐', '夜冷', '树绕池宽月影多，村砧坞笛隔风萝。\\n西亭翠被馀香薄，一夜将愁向败荷。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3560', 'poem', '唐', '张九龄', '咏燕 / 归燕诗', '海燕虽微渺，乘春亦暂来。\\n岂知泥滓贱，只见玉堂开。\\n绣户时双入，华堂日几回。\\n无心与物竞，鹰隼莫相猜。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3561', 'poem', '唐', '王初', '望雪', '银花珠树晓来看，宿醉初醒一倍寒。\\n已似王恭披鹤氅，凭栏仍是玉栏干。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3562', 'poem', '唐', '李德裕', '长安秋夜', '内宫传诏问戎机，载笔金銮夜始归。\\n万户千门皆寂寂，月中清露点朝衣。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3563', 'poem', '唐', '王昌龄', '题僧房', '棕榈花满院，苔藓入闲房。\\n彼此名言绝，空中闻异香。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3564', 'poem', '唐', '陆龟蒙', '秋思三首', '桐露珪初落，兰风佩欲衰。\\n不知能赋客，何似枉刀儿。\\n谁在嫖姚幕，能教霹雳车。\\n至今思秃尾，无以代寒菹。\\n未得同齑杵，何时减药囊。\\n莫言天帝醉，秦暴不灵长。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3565', 'poem', '唐', '吴融', '阌乡寓居十首·茆堂', '结得茆檐瞰碧溪，闲云之外不同栖。\\n犹嫌未远函关道，正睡刚闻报晓鸡。', '2018-03-09 15:38:48');
INSERT INTO `t_poem` VALUES ('3566', 'poem', '唐', '司空图', '河上二首', '惨惨日将暮，驱羸独到庄。\\n沙痕傍墟落，风色入牛羊。\\n新霁田园处，夕阳禾黍明。\\n沙村平见水，深巷有鸥声。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3567', 'poem', '唐', '白居易', '送王十八归山，寄题仙游寺', '曾于太白峰前住，数到仙游寺里来。\\n黑水澄时潭底出，白云破处洞门开。\\n林间暖酒烧红叶，石上题诗扫绿苔。\\n惆怅旧游那复到，菊花时节羡君回。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3568', 'poem', '唐', '裴度', '雪中讶诸公不相访', '忆昨雨多泥又深，犹能携妓远过寻。\\n满空乱雪花相似，何事居然无赏心。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3569', 'poem', '唐', '王维', '送张判官赴河西', '单车曾出塞，报国敢邀勋。\\n见逐张征虏，今思霍冠军。\\n沙平连白云，蓬卷入黄云。\\n慷慨倚长剑，高歌一送君。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3570', 'poem', '唐', '罗隐', '竹', '篱外清阴接药阑，晓风交戛碧琅玕。\\n子猷死后知音少，粉节霜筠谩岁寒。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3571', 'poem', '唐', '牟融', '送友人', '有客棹扁舟，相逢不暂留。\\n衣冠重文物，诗酒足风流。\\n官路生归兴，家林想旧游。\\n临岐分手后，乘月过苏州。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3572', 'poem', '唐', '骆宾王', '出石门', '层岩远接天，绝岭上栖烟。\\n松低轻盖偃，藤细弱丝悬。\\n石明如挂镜，苔分似列钱。\\n暂策为龙杖，何处得神仙。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3573', 'poem', '唐', '李白', '岘山怀古', '访古登岘首，凭高眺襄中。\\n天清远峰出，水落寒沙空。\\n弄珠见游女，醉酒怀山公。\\n感叹发秋兴，长松鸣夜风。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3574', 'poem', '唐', '李贺', '溪晚凉', '白狐向月号山风，秋寒扫云留碧空。\\n玉烟青湿白如幢，银湾晓转流天东。\\n溪汀眠鹭梦征鸿，轻涟不语细游溶。\\n层岫回岑复叠龙，苦篁对客吟歌筒。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3575', 'poem', '唐', '元稹', '度门寺', '北祖三禅地，西山万树松。\\n门临溪一带，桥映竹千重。\\n翦凿基阶正，包藏景气浓。\\n诸岩分院宇，双岭抱垣墉。\\n舍利开层塔，香炉占小峰。\\n道场居士置，经藏大师封。\\n太子知栽植，神王守要冲。\\n由旬排讲座，丈六写真容。\\n佛语迦陵说，僧行猛虎从。\\n修罗抬日拒，楼至拔霜锋。\\n画井垂枯朽，穿池救噞喁。\\n蕉非难败坏，槿喻暂丰茸。\\n宝界留遗事，金棺灭去踪。\\n钵传烘玛瑙，石长翠芙蓉。\\n影帐纱全落，绳床土半壅。\\n荒林迷醉象，危壁亚盘龙。\\n行色怜初月，归程待晓钟。\\n心源虽了了，尘世苦憧憧。\\n宿荫高声忏，斋粮并力舂。\\n他生再来此，还愿总相逢。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3576', 'poem', '唐', '皮日休', '茶中杂咏·茶瓯', '邢客与越人，皆能造兹器。\\n圆似月魂堕，轻如云魄起。\\n枣花势旋眼，蘋沫香沾齿。\\n松下时一看，支公亦如此。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3577', 'poem', '唐', '白居易', '寒食江畔', '草香沙暖水云晴，风景令人忆帝京。\\n还似往年春气味，不宜今日病心情。\\n闻莺树下沈吟立，信马江头取次行。\\n忽见紫桐花怅望，下邽明日是清明。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3578', 'poem', '唐', '韦庄', '闺月', '明月照前除，烟华蕙兰湿。\\n清风行处来，白露寒蝉急。\\n美人情易伤，暗上红楼立。\\n欲言无处言，但向姮娥泣。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3579', 'poem', '唐', '杜甫', '熟食日示宗文、宗武', '消渴游江汉，羁栖尚甲兵。\\n几年逢熟食，万里逼清明。\\n松柏邛山路，风花白帝城。\\n汝曹催我老，回首泪纵横。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3580', 'poem', '唐', '沈亚之', '村居', '有树巢宿鸟，无酒共客醉。\\n月上蝉韵残，梧桐阴绕地。\\n独出村舍门，吟剧微风起。\\n萧萧芦荻丛，叫啸如山鬼。\\n应缘我憔悴，为我哭秋思。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3581', 'poem', '唐', '罗隐', '蟋蟀诗', '顽飔毙芳，吹愁夕长。\\n屑戍有动，歌离吊梦。\\n如诉如言，绪引虚宽。\\n周隙伺榻，繁咽夤缘。\\n范睡蝉老，冠峨緌好。\\n不冠不緌，尔奚以悲。\\n蚊蚋有毒，食人肌肉。\\n苍蝇多端，黑白偷安。\\n尔也出处，物兮莫累。\\n坏舍啼衰，虚堂泣曙。\\n勿徇喧哗，鼠岂无牙。\\n勿学萋菲，垣亦有耳。\\n危条槁飞，抽恨咿咿。\\n别帐缸冷，柔魂不定。\\n美人在何，夜影流波。\\n与子伫立，裴回思多。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3582', 'poem', '唐', '韩偓', '侍宴', '蜂黄蝶粉两依依，狎宴临春日正迟。\\n密旨不教江令醉，丽华微笑认皇慈。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3583', 'poem', '唐', '白居易', '别桥上竹', '穿桥迸竹不依行，恐碍行人被损伤。\\n我去自惭遗爱少，不教君得似甘棠。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3584', 'poem', '唐', '罗隐', '偶题（一作嘲钟陵妓云英）', '钟陵醉别十余春，重见云英掌上身。\\n我未成名君未嫁，可能俱是不如人。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3585', 'poem', '唐', '刘长卿', '移使鄂州，次岘阳馆怀旧居', '多惭恩未报，敢问路何长。\\n万里通秋雁，千峰共夕阳。\\n旧游成远道，此去更违乡。\\n草露深山里，朝朝落客裳。', '2018-03-09 15:38:52');
INSERT INTO `t_poem` VALUES ('3586', 'poem', '唐', '李商隐', '题僧壁', '舍生求道有前踪，乞脑剜身结愿重。\\n大去便应欺粟颗，小来兼可隐针锋。\\n蚌胎未满思新桂，琥珀初成忆旧松。\\n若信贝多真实语，三生同听一楼钟。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3587', 'poem', '唐', '白居易', '答刘戒之早秋别墅见寄', '凉风木槿篱，暮雨槐花枝。\\n并起新秋思，为得故人诗。\\n避地鸟择木，升朝鱼在池。\\n城中与山下，喧静暗相思。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3588', 'poem', '唐', '李白', '重忆一首', '欲向江东去，定将谁举杯。\\n稽山无贺老，却棹酒船回。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3589', 'poem', '唐', '李白', '泾溪东亭寄郑少府谔', '我游东亭不见君，沙上行将白鹭群。\\n白鹭行时散飞去，又如雪点青山云。\\n欲往泾溪不辞远，龙门蹙波虎眼转。\\n杜鹃花开春已阑，归向陵阳钓鱼晚。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3590', 'poem', '唐', '郑谷', '竹', '宜烟宜雨又宜风，拂水藏村复间松。\\n移得萧骚从远寺，洗来疏净见前峰。\\n侵阶藓拆春芽迸，绕径莎微夏荫浓。\\n无赖杏花多意绪，数枝穿翠好相容。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3591', 'poem', '唐', '白居易', '过元家履信宅', '鸡犬丧家分散后，林园失主寂寥时。\\n落花不语空辞树，流水无情自入池。\\n风荡宴船初破漏，雨淋歌阁欲倾欹。\\n前庭后院伤心事，唯是春风秋月知。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3592', 'poem', '唐', '王维', '从岐王过杨氏别业应教', '杨子谈经所，淮王载酒过。\\n兴阑啼鸟换，坐久落花多。\\n径转回银烛，林开散玉珂。\\n严城时未启，前路拥笙歌。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3593', 'poem', '唐', '白居易', '赠写真者', '子骋丹青日，予当丑老时。\\n无劳役神思，更画病容仪。\\n迢递麒麟阁，图功未有期。\\n区区尺素上，焉用写真为。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3594', 'poem', '唐', '徐惠', '拟小山篇', '仰幽岩而流盼，抚桂枝以凝想。\\n将千龄兮此遇，荃何为兮独往。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3595', 'poem', '唐', '孙鲂', '杂曲歌辞·杨柳枝', '灵和风暖太昌春，舞线摇丝向昔人。\\n何似晓来江雨后，一行如画隔遥津。\\n彭泽初栽五树时，只应闲看一枝枝。\\n不知天意风流处，要与佳人学画眉。\\n暖傍离亭静拂桥，入流穿槛绿摇摇。\\n不知落日谁相送，魂断千条与万条。\\n春来绿树遍天涯，未见垂杨未可夸。\\n晴日万株烟一阵，闲坊兼是莫愁家。\\n十首当年有旧词，唱青歌翠几无遗。\\n未曾得向行人道，不为离情莫折伊。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3596', 'poem', '唐', '刘沧', '咸阳怀古', '经过此地无穷事，一望凄然感废兴。\\n渭水故都秦二世，咸原秋草汉诸陵。\\n天空绝塞闻边雁，叶尽孤村见夜灯。\\n风景苍苍多少恨，寒山半出白云层。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3597', 'poem', '唐', '高蟾', '秋思', '天地太萧索，山川何渺茫。\\n不堪星斗柄，犹把岁寒量。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3598', 'poem', '唐', '刘沧', '晚秋野望', '秋尽郊原情自哀，菊花寂寞晚仍开。\\n高风疏叶带霜落，一雁寒声背水来。\\n荒垒几年经战后，故山终日望书回。\\n归途休问从前事，独唱劳歌醉数杯。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3599', 'poem', '唐', '李端', '关山月', '露湿月苍苍，关头榆叶黄。\\n回轮照海远，分彩上楼长。\\n水冻频移幕，兵疲数望乡。\\n只应城影外，万里共胡霜。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3600', 'poem', '唐', '皮日休', '添鱼具诗·箬笠', '圆似写月魂，轻如织烟翠。\\n涔涔向上雨，不乱窥鱼思。\\n携来沙日微，挂处江风起。\\n纵带二梁冠，终身不忘尔。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3601', 'poem', '唐', '陆龟蒙', '大子夜歌二首', '歌谣数百种，子夜最可怜。\\n慷慨吐清音，明转出天然。\\n丝竹发歌响，假器扬清音。\\n不知歌谣妙，声势出口心。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3602', 'poem', '唐', '薛能', '宋氏林亭', '地湿莎青雨后天，桃花红近竹林边。\\n行人本是农桑客，记得春深欲种田。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3603', 'poem', '唐', '曹松', '驸马宅宴罢', '粉墙残月照宫祠，宴阕银瓶一半欹。\\n学语莺儿飞未稳，放身斜坠绿杨枝。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3604', 'poem', '唐', '吴筠', '高士咏·东方曼倩', '东方禀易象，玩世隐廊庙。\\n栖心抱清微，混迹秘光耀。\\n玄览寄数术，纳规在谈笑。\\n卖药五湖中，还从九仙妙。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3605', 'poem', '唐', '吉师老', '放猿', '放尔千山万水身，野泉晴树好为邻。\\n啼时莫近潇湘岸，明月孤舟有旅人。', '2018-03-09 15:38:58');
INSERT INTO `t_poem` VALUES ('3606', 'poem', '唐', '贯休', '砚瓦', '浅薄虽顽朴，其如近笔端。\\n低心蒙润久，入匣更身安。\\n应念研磨苦，无为瓦砾看。\\n傥然仁不弃，还可比琅玕。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3607', 'poem', '唐', '崔日用', '又赐宴自歌', '东馆总是鹓鸾，南台自多杞梓。\\n日用读书万卷，何忍不蒙学士。\\n墨制帘下出来，微臣眼看喜死。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3608', 'poem', '唐', '岑参', '天山雪歌送萧治归京', '天山雪云常不开，千峰万岭雪崔嵬。\\n北风夜卷赤亭口，一夜天山雪更厚。\\n能兼汉月照银山，复逐胡风过铁关。\\n交河城边鸟飞绝，轮台路上马蹄滑。\\n晻霭寒氛万里凝，阑干阴崖千丈冰。\\n将军狐裘卧不暖，都护宝刀冻欲断。\\n正是天山雪下时，送君走马归京师。\\n雪中何以赠君别，惟有青青松树枝。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3609', 'poem', '唐', '刘禹锡', '秋江晚泊', '长泊起秋色，空江涵霁晖。\\n暮霞千万状，宾鸿次第飞。\\n古戍见旗迥，荒村闻犬稀。\\n轲峨艑上客，劝酒夜相依。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3610', 'poem', '唐', '姚合', '咏南池嘉莲', '芙蓉池里叶田田，一本双花出碧泉。\\n浓淡共妍香各散，东西分艳蒂相连。\\n自知政术无他异，纵是祯祥亦偶然。\\n四野人闻皆尽喜，争来入郭看嘉莲。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3611', 'poem', '唐', '白居易', '寒食', '人老何所乐，乐在归乡国。\\n我归故园来，九度逢寒食。\\n故园在何处，池馆东城侧。\\n四邻梨花时，二月伊水色。\\n岂独好风土，仍多旧亲戚。\\n出去恣欢游，归来聊燕息。\\n有官供禄俸，无事劳心力。\\n但恐优稳多，微躬销不得。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3612', 'poem', '唐', '王勃', '九日怀封元寂', '九日郊原望，平野遍霜威。\\n兰气添新酌，花香染别衣。\\n九秋良会少，千里故人稀。\\n今日龙山外，当忆雁书归。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3613', 'poem', '唐', '岑参', '至大梁却寄匡城主人', '一从弃鱼钓，十载干明王。\\n无由谒天阶，却欲归沧浪。\\n仲秋至东郡，遂见天雨霜。\\n昨日梦故山，蕙草色已黄。\\n平明辞铁丘，薄暮游大梁。\\n仲秋萧条景，拔剌飞鹅鶬。\\n四郊阴气闭，万里无晶光。\\n长风吹白茅，野火烧枯桑。\\n故人南燕吏，籍籍名更香。\\n聊以玉壶赠，置之君子堂。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3614', 'poem', '唐', '李商隐', '碧瓦', '碧瓦衔珠树，红轮结绮寮。\\n无双汉殿鬓，第一楚宫腰。\\n雾唾香难尽，珠啼冷易销。\\n歌从雍门学，酒是蜀城烧。\\n柳暗将翻巷，荷欹正抱桥。\\n钿辕开道入，金管隔邻调。\\n梦到飞魂急，书成即席遥。\\n河流冲柱转，海沫近槎飘。\\n吴市蠀蛦甲，巴賨翡翠翘。\\n他时未知意，重叠赠娇饶。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3615', 'poem', '唐', '李正封', '夏游招隐寺暴雨晚晴', '竹柏风雨过，萧疏台殿凉。\\n石渠写奔溜，金刹照颓阳。\\n鹤飞岩烟碧，鹿鸣涧草香。\\n山僧引清梵，幡盖绕回廊。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3616', 'poem', '唐', '卢纶', '山中一绝', '饥食松花渴饮泉，偶从山后到山前。\\n阳坡软草厚如织，因与鹿麛相伴眠。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3617', 'poem', '唐', '李世民', '赋得残菊', '阶兰凝曙霜，岸菊照晨光。\\n露浓晞晚笑，风劲浅残香。\\n细叶凋轻翠，圆花飞碎黄。\\n还持今岁色，复结后年芳。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3618', 'poem', '唐', '佚名', '杂曲歌辞·簇拍陆州', '西去轮台万里馀，故乡音耗日应疏。\\n陇山鹦鹉能言语，为报闺人数寄书。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3619', 'poem', '唐', '崔橹', '山路木芙蓉', '不向横塘泥里栽，两株晴笑碧岩隈。\\n枉教绝世深红色，只向深山僻处开。\\n万里王孙应有恨，三年贾傅惜无才。\\n缘花更叹人间事，半日江边怅望回。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3620', 'poem', '唐', '裴夷直', '忆家', '天海相连无尽处，梦魂来往尚应难。\\n谁言南海无霜雪，试向愁人两鬓看。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3621', 'poem', '唐', '白居易', '重到城七绝句·仇家酒', '年年老去欢情少，处处春来感事深。\\n时到仇家非爱酒，醉时心胜醒时心。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3622', 'poem', '唐', '朱庆馀', '早梅', '天然根性异，万物尽难陪。\\n自古承春早，严冬斗雪开。\\n艳寒宜雨露，香冷隔尘埃。\\n堪把依松竹，良涂一处栽。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3623', 'poem', '唐', '韦应物', '除日', '思怀耿如昨，季月已云暮。\\n忽惊年复新，独恨人成故。\\n冰池始泮绿，梅援还飘素。\\n淑景方转延，朝朝自难度。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3624', 'poem', '唐', '孟郊', '伤时', '常闻贫贱士之常，嗟尔富者莫相笑。\\n男儿得路即荣名，邂逅失途成不调。\\n古人结交而重义，今人结交而重利。\\n劝人一种种桃李，种亦直须遍天地。\\n一生不爱嘱人事，嘱即直须为生死。\\n我亦不羡季伦富，我亦不笑原宪贫。\\n有财有势即相识，无财无势同路人。\\n因知世事皆如此，却向东溪卧白云。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3625', 'poem', '唐', '白居易', '早秋曲江感怀', '离离暑云散，袅袅凉风起。\\n池上秋又来，荷花半成子。\\n朱颜易销歇，白日无穷已。\\n人寿不如山，年光忽于水。\\n青芜与红蓼，岁岁秋相似。\\n去岁此悲秋，今秋复来此。', '2018-03-09 15:39:10');
INSERT INTO `t_poem` VALUES ('3626', 'poem', '唐', '唐彦谦', '索虾', '姑孰多紫虾，独有湖阳优。\\n出产在四时，极美宜于秋。\\n双箝鼓繁须，当顶抽长矛。\\n鞠躬见汤王，封作朱衣侯。\\n所以供盘餐，罗列同珍羞。\\n蒜友日相亲，瓜朋时与俦。\\n既名钓诗钓，又作钩诗钩。\\n于时同相访，数日承款留。\\n厌饮多美味，独此心相投。\\n别来岁云久，驰想空悠悠。\\n衔杯动遐思，啰口涎空流。\\n封缄托双鲤，于焉来远求。\\n慷慨胡隐君，果肯分惠否。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3627', 'poem', '唐', '德宗宫人', '题花叶诗', '一入深宫里，无由得见春。\\n题诗花叶上，寄与接流人。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3628', 'poem', '唐', '张祜', '登乐游原', '几年诗酒滞江干，水积云重思万端。\\n今日南方惆怅尽，乐游原上见长安。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3629', 'poem', '唐', '董思恭', '咏日', '沧海十枝晖，悬圃重轮庆。\\n蕣华发晨楹，菱彩翻朝镜。\\n忽遇惊风飘，自有浮云映。\\n更也人皆仰，无待挥戈正。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3630', 'poem', '唐', '白居易', '山中五绝句·岭上云', '岭上白云朝未散，田中青麦旱将枯。\\n自生自灭成何事，能逐东风作雨无。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3631', 'poem', '唐', '殷尧藩', '忆家二首', '新霁飏林初，蘋花贴岸舒。\\n故乡今夜月，犹得照孤庐。\\n树拥溪边阁，山浮雨后岚。\\n白头归未得，梦里望江南。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3632', 'poem', '唐', '岑参', '赴北庭度陇思家', '西向轮台万里馀，也知乡信日应疏。\\n陇山鹦鹉能言语，为报家人数寄书。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3633', 'poem', '唐', '张继', '宿白马寺', '白马驮经事已空，断碑残刹见遗踪。\\n萧萧茅屋秋风起，一夜雨声羁思浓。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3634', 'poem', '唐', '王梵志', '城外土馒头', '城外土馒头，馅草在城里。\\n一人吃一个，莫嫌没滋味。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3635', 'poem', '唐', '张九龄', '题画山水障', '心累犹不尽，果为物外牵。\\n偶因耳目好，复假丹青妍。\\n尝抱野间意，而迫区中缘。\\n尘事固已矣，秉意终不迁。\\n良工适我愿，妙墨挥岩泉。\\n变化合群有，高深侔自然。\\n置陈北堂上，仿像南山前。\\n静无户庭出，行已兹地偏。\\n萱草忧可树，合欢忿益蠲。\\n所因本微物，况乃凭幽筌。\\n言象会自泯，意色聊自宣。\\n对玩有佳趣，使我心渺绵。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3636', 'poem', '唐', '韦应物', '咏珊瑚', '绛树无花叶，非石亦非琼。\\n世人何处得，蓬莱石上生。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3637', 'poem', '唐', '张祜', '上巳乐', '猩猩血彩系头标，天上齐声举画桡。\\n却是内人争意切，六宫红袖一时招。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3638', 'poem', '唐', '元稹', '岁日', '一日今年始，一年前事空。\\n凄凉百年事，应与一年同。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3639', 'poem', '唐', '李世民', '初晴落景', '晚霞聊自怡，初晴弥可喜。\\n日晃百花色，风动千林翠。\\n池鱼跃不同，园鸟声还异。\\n寄言博通者，知予物外志。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3640', 'poem', '唐', '杜牧', '酬张祜处士见寄长句四韵', '七子论诗谁似公，曹刘须在指挥中。\\n荐衡昔日知文举，乞火无人作蒯通。\\n北极楼台长挂梦，西江波浪远吞空。\\n可怜故国三千里，虚唱歌词满六宫。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3641', 'poem', '唐', '白居易', '赠元稹', '自我从宦游，七年在长安。\\n所得惟元君，乃知定交难。\\n岂无山上苗，径寸无岁寒。\\n岂无要津水，咫尺有波澜。\\n之子异于是，久处誓不谖。\\n无波古井水，有节秋竹竿。\\n一为同心友，三及芳岁阑。\\n花下鞍马游，雪中杯酒欢。\\n衡门相逢迎，不具带与冠。\\n春风日高睡，秋月夜深看。\\n不为同登科，不为同署官。\\n所合在方寸，心源无异端。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3642', 'poem', '唐', '岑参', '裴将军宅芦管歌', '辽东九月芦叶断，辽东小儿采芦管。\\n可怜新管清且悲，一曲风飘海头满。\\n海树萧索天雨霜，管声寥亮月苍苍。\\n白狼河北堪愁恨，玄兔城南皆断肠。\\n辽东将军长安宅，美人芦管会佳客。\\n弄调啾飕胜洞箫，发声窈窕欺横笛。\\n夜半高堂客未回，只将芦管送君杯。\\n巧能陌上惊杨柳，复向园中误落梅。\\n诸客爱之听未足，高卷珠帘列红烛。\\n将军醉舞不肯休，更使美人吹一曲。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3643', 'poem', '唐', '白居易', '宿紫阁山北村', '晨游紫阁峰，暮宿山下村。\\n村老见余喜，为余开一尊。\\n举杯未及饮，暴卒来入门。\\n紫衣挟刀斧，草草十馀人。\\n夺我席上酒，掣我盘中飧。\\n主人退后立，敛手反如宾。\\n中庭有奇树，种来三十春。\\n主人惜不得，持斧断其根。\\n口称采造家，身属神策军。\\n主人慎勿语，中尉正承恩。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3644', 'poem', '唐', '令狐楚', '杂曲歌辞·长相思二首', '君行登陇上，妾梦在闺中。\\n玉箸千行落，银床一半空。\\n绮席春眠觉，纱窗晓望迷。\\n朦胧残梦里，犹自在辽西。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3645', 'poem', '唐', '白居易', '答元八郎中、杨十二博士', '身觉浮云无所著，心同止水有何情。\\n但知潇洒疏朝市，不要崎岖隐姓名。\\n尽日观鱼临涧坐，有时随鹿上山行。\\n谁能抛得人间事，来共腾腾过此生。', '2018-03-09 15:39:23');
INSERT INTO `t_poem` VALUES ('3646', 'poem', '唐', '白居易', '寄韬光禅师', '一山门作两山门，两寺原从一寺分。\\n东涧水流西涧水，南山云起北山云。\\n前台花发后台见，上界钟声下界闻。\\n遥想吾师行道处，天香桂子落纷纷。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3647', 'poem', '唐', '刘长卿', '戏题赠二小男', '异乡流落频生子，几许悲欢并在身。\\n欲并老容羞白发，每看儿戏忆青春。\\n未知门户谁堪主，且免琴书别与人。\\n何幸暮年方有后，举家相对却沾巾。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3648', 'poem', '唐', '贯休', '陪冯使君游六首·迎仙阁', '涧香霞影绕楼台，卷箔凭阑耳目开。\\n况从旌旗近鸾凤，可怜谈笑出尘埃。\\n火云不入长松径，露茗何须白玉杯。\\n谁道迎仙仙不至，今朝还有谢公来。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3649', 'poem', '唐', '李世民', '还陕述怀', '慨然抚长剑，济世岂邀名。\\n星旂纷电举，日羽肃天行。\\n遍野屯万骑，临原驻五营。\\n登山麾武节，背水纵神兵。\\n在昔戎戈动，今来宇宙平。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3650', 'poem', '唐', '韦应物', '听嘉陵江水声寄深上人', '凿崖泄奔湍，称古神禹迹。\\n夜喧山门店，独宿不安席。\\n水性自云静，石中本无声。\\n如何两相激，雷转空山惊。\\n贻之道门旧，了此物我情。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3651', 'poem', '唐', '李白', '月夜金陵怀古', '苍苍金陵月，空悬帝王州。\\n天文列宿在，霸业大江流。\\n绿水绝驰道，青松摧古丘。\\n台倾鳷鹊观，宫没凤凰楼。\\n别殿悲清暑，芳园罢乐游。\\n一闻歌玉树，萧瑟后庭秋。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3652', 'poem', '唐', '白居易', '京兆府栽莲', '污沟贮浊水，水上叶田田。\\n我来一长叹，知是东溪莲。\\n下有青污泥，馨香无复全。\\n上有红尘扑，颜色不得鲜。\\n物性犹如此，人事亦宜然。\\n托根非其所，不如遭弃捐。\\n昔在溪中日，花叶媚清涟。\\n今年不得地，憔悴府门前。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3653', 'poem', '唐', '沈彬', '塞下三首', '塞叶声悲秋欲霜，寒山数点下牛羊。\\n映霞旅雁随疏雨，向碛行人带夕阳。\\n边骑不来沙路失，国恩深后海城荒。\\n胡儿向化新成长，犹自千回问汉王。\\n贵主和亲杀气沉，燕山闲猎鼓鼙音。\\n旗分雪草偷边马，箭入寒云落塞禽。\\n陇月尽牵乡思动，战衣谁寄泪痕深。\\n金钗谩作封侯别，劈破佳人万里心。\\n月冷榆关过雁行，将军寒笛老思乡。\\n贰师骨恨千夫壮，李广魂飞一剑长。\\n戍角就沙催落日，阴云分碛护飞霜。\\n谁知汉武轻中国，闲夺天山草木荒。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3654', 'poem', '唐', '白居易', '适意二首', '十年为旅客，常有饥寒愁。\\n三年作谏官，复多尸素羞。\\n有酒不暇饮，有山不得游。\\n岂无平生志，拘牵不自由。\\n一朝归渭上，泛如不系舟。\\n置心世事外，无喜亦无忧。\\n终日一蔬食，终年一布裘。\\n寒来弥懒放，数日一梳头。\\n朝睡足始起，夜酌醉即休。\\n人心不过适，适外复何求。\\n早岁从旅游，颇谙时俗意。\\n中年忝班列，备见朝廷事。\\n作客诚已难，为臣尤不易。\\n况余方且介，举动多忤累。\\n直道速我尤，诡遇非吾志。\\n胸中十年内，消尽浩然气。\\n自从返田亩，顿觉无忧愧。\\n蟠木用难施，浮云心易遂。\\n悠悠身与世，从此两相弃。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3655', 'poem', '唐', '薛涛', '秋泉', '冷色初澄一带烟，幽声遥泻十丝弦。\\n长来枕上牵情思，不使愁人半夜眠。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3656', 'poem', '唐', '常建', '塞下', '铁马胡裘出汉营，分麾百道救龙城。\\n左贤未遁旌竿折，过在将军不在兵。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3657', 'poem', '唐', '司空曙', '望水', '高楼晴见水，楚色霭相和。\\n野极空如练，天遥不辨波。\\n永无人迹到，时有鸟行过。\\n况是苍茫外，残阳照最多。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3658', 'poem', '唐', '徐夤', '楚国史', '六国商於恨最多，良弓休绾剑休磨。\\n君王不剪如簧舌，再得张仪欲奈何。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3659', 'poem', '唐', '白居易', '夜送孟司功', '浔阳白司马，夜送孟功曹。\\n江暗管弦急，楼明灯火高。\\n湖波翻似箭，霜草杀如刀。\\n且莫开征棹，阴风正怒号。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3660', 'poem', '唐', '熊皎', '月中桂', '断破重轮种者谁，银蟾何事便相随。\\n莫言望夜无攀处，却是吟人有得时。\\n孤影不凋清露滴，异香常在好风吹。\\n几回目断云霄外，未必姮娥惜一枝。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3661', 'poem', '唐', '苏味道', '咏石', '济北甄神贶，河西濯锦文。\\n声应天池雨，影触岱宗云。\\n燕归犹可候，羊起自成群。\\n何当握灵髓，高枕绝嚣氛。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3662', 'poem', '唐', '孙元晏', '宋·羊玄保', '运命将来各有期，好官才阙即思之。\\n就中堪爱羊玄保，偏受君王分外知。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3663', 'poem', '唐', '杜牧', '哭韩绰', '平明送葬上都门，绋翣交横逐去魂。\\n归来冷笑悲身事，唤妇呼儿索酒盆。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3664', 'poem', '唐', '司马扎', '蚕女', '养蚕先养桑，蚕老人亦衰。\\n苟无园中叶，安得机上丝。\\n妾家非豪门，官赋日相追。\\n鸣梭夜达晓，犹恐不及时。\\n但忧蚕与桑，敢问结发期。\\n东邻女新嫁，照镜弄蛾眉。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3665', 'poem', '唐', '胡曾', '咏史诗·檀溪', '三月襄阳绿草齐，王孙相引到檀溪。\\n的卢何处埋龙骨，流水依前绕大堤。', '2018-03-09 15:39:28');
INSERT INTO `t_poem` VALUES ('3666', 'poem', '唐', '王建', '江陵使至汝州', '回看巴路在云间，寒食离家麦熟还。\\n日暮数峰青似染，商人说是汝州山。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3667', 'poem', '唐', '温庭筠', '碧涧驿晓思', '香灯伴残梦，楚国在天涯。\\n月落子规歇，满庭山杏花。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3668', 'poem', '唐', '韩偓', '蜻蜓', '碧玉眼睛云母翅，轻于粉蝶瘦于蜂。\\n坐来迎拂波光久，岂是殷勤为蓼丛。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3669', 'poem', '唐', '李涉', '竹枝词·荆门滩急水潺潺', '荆门滩急水潺潺，两岸猿啼烟满山。\\n渡头少年应官去，月落西陵望不还。\\n巫峡云开神女祠，绿潭红树影参差。\\n不劳戍口初相问，无义滩头剩别离。\\n石壁千重树万重，白云斜掩碧芙蓉。\\n昭君溪上年年月，偏照婵娟色最浓。\\n十二峰头月欲低，空聆滩上子规啼。\\n孤舟一夜东归客，泣向东风忆建溪。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3670', 'poem', '唐', '陆龟蒙', '子夜变歌三首', '人传欢负情，我自未尝见。\\n三更开门去，始知子夜变。\\n岁月如流迈，春尽秋已至。\\n荧荧条上花，零落何乃驶。\\n岁月如流迈，行已及素秋。\\n蟋蟀吟堂前，惆怅使侬愁。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3671', 'poem', '唐', '白居易', '慵不能', '架上非无书，眼慵不能看。\\n匣中亦有琴，手慵不能弹。\\n腰慵不能带，头慵不能冠。\\n午后恣情寝，午时随事餐。\\n一餐终日饱，一寝至夜安。\\n饥寒亦闲事，况乃不饥寒。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3672', 'poem', '唐', '翁洮', '春', '漠漠烟花处处通，游人南北思无穷。\\n林间鸟奏笙簧月，野外花含锦绣风。\\n鸑抱云霞朝凤阙，鱼翻波浪化龙宫。\\n此时谁羡神仙客，车马悠扬九陌中。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3673', 'poem', '唐', '李商隐', '访隐者不遇成二绝', '秋水悠悠浸墅扉，梦中来数觉来稀。\\n玄蝉去尽叶黄落，一树冬青人未归。\\n城郭休过识者稀，哀猿啼处有柴扉。\\n沧江白日樵渔路，日暮归来雨满衣。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3674', 'poem', '唐', '黄滔', '过长江', '曾搜景象恐通神，地下还应有主人。\\n若把长江比湘浦，离骚不合自灵均。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3675', 'poem', '唐', '李世民', '琵琶', '半月无双影，全花有四时。\\n摧藏千里态，掩抑几重悲。\\n促节萦红袖，清音满翠帷。\\n驶弹风响急，缓曲钏声迟。\\n空馀关陇恨，因此代相思。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3676', 'poem', '唐', '宋之问', '早发始兴江口至虚氏村作', '候晓逾闽峤，乘春望越台。\\n宿云鹏际落，残月蚌中开。\\n薜荔摇青气，桄榔翳碧苔。\\n桂香多露裛，石响细泉回。\\n抱叶玄猿啸，衔花翡翠来。\\n南中虽可悦，北思日悠哉。\\n鬒发俄成素，丹心已作灰。\\n何当首归路，行剪故园莱。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3677', 'poem', '唐', '许棠', '塞下二首', '胡虏偏狂悍，边兵不敢闲。\\n防秋朝伏弩，纵火夜搜山。\\n雁逆风鼙振，沙飞猎骑还。\\n安西虽有路，难更出阳关。\\n征役已不定，又缘无定河。\\n塞深烽砦密，山乱犬羊多。\\n汉卒闻笳泣，胡儿击剑歌。\\n番情终未测，今昔谩言和。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3678', 'poem', '唐', '司空图', '诗品二十四则·沈著', '绿杉野屋，落日气清。\\n脱巾独步，时闻鸟声。\\n鸿雁不来，之子远行。\\n所思不远，若为平生。\\n海风碧云，夜渚月明。\\n如有佳语，大河前横。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3679', 'poem', '唐', '白居易', '病中诗十五首·枕上作', '风疾侵凌临老头，血凝筋滞不调柔。\\n甘从此后支离卧，赖是从前烂漫游。\\n回思往事纷如梦，转觉馀生杳若浮。\\n浩气自能充静室，惊飙何必荡虚舟。\\n腹空先进松花酒，膝冷重装桂布裘。\\n若问乐天忧病否，乐天知命了无忧。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3680', 'poem', '唐', '徐铉', '春分日', '仲春初四日，春色正中分。\\n绿野徘徊月，晴天断续云。\\n燕飞犹个个，花落已纷纷。\\n思妇高楼晚，歌声不可闻。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3681', 'poem', '唐', '冷朝阳', '送唐六赴举', '秋色生边思，送君西入关。\\n草衰空大野，叶落露青山。\\n故国烟霞外，新安道路间。\\n碧霄知己在，香桂月中攀。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3682', 'poem', '唐', '张九龄', '晨出郡舍林下', '晨兴步北林，萧散一开襟。\\n复见林上月，娟娟犹未沉。\\n片云自孤远，丛筱亦清深。\\n无事由来贵，方知物外心。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3683', 'poem', '唐', '常建', '吊王将军墓', '嫖姚北伐时，深入强千里。\\n战馀落日黄，军败鼓声死。\\n尝闻汉飞将，可夺单于垒。\\n今与山鬼邻，残兵哭辽水。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3684', 'poem', '唐', '元稹', '松树', '华山高幢幢，上有高高松。\\n株株遥各各，叶叶相重重。\\n槐树夹道植，枝叶俱冥蒙。\\n既无贞直干，复有罥挂虫。\\n何不种松树，使之摇清风。\\n秦时已曾种，憔悴种不供。\\n可怜孤松意，不与槐树同。\\n闲在高山顶，樛盘虬与龙。\\n屈为大厦栋，庇荫侯与公。\\n不肯作行伍，俱在尘土中。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3685', 'poem', '唐', '白居易', '栽松二首', '小松未盈尺，心爱手自移。\\n苍然涧底色，云湿烟霏霏。\\n栽植我年晚，长成君性迟。\\n如何过四十，种此数寸枝。\\n得见成阴否，人生七十稀。\\n爱君抱晚节，怜君含直文。\\n欲得朝朝见，阶前故种君。\\n知君死则已，不死会凌云。', '2018-03-09 15:39:39');
INSERT INTO `t_poem` VALUES ('3686', 'poem', '唐', '鲍溶', '塞下', '北风号蓟门，杀气日夜兴。\\n咸阳三千里，驿马如饥鹰。\\n行子久去乡，逢山不敢登。\\n寒日惨大野，虏云若飞鹏。\\n西北防秋军，麾幢宿层层。\\n匈奴天未丧，战鼓长登登。\\n汉卒马上老，繁缨空丝绳。\\n诚知天所骄，欲罢又不能。', '2018-03-09 15:39:53');
INSERT INTO `t_poem` VALUES ('3687', 'poem', '唐', '储光羲', '洛阳道五首献吕四郎中', '洛水春冰开，洛城春水绿。\\n朝看大道上，落花乱马足。\\n剧孟不知名，千金买宝剑。\\n出入平津邸，自言娇且艳。\\n大道直如发，春日佳气多。\\n五陵贵公子，双双鸣玉珂。\\n春风二月时，道傍柳堪把。\\n上枝覆官阁，下枝覆车马。\\n洛水照千门，千门碧空里。\\n少年不得志，走马游新市。', '2018-03-09 15:39:53');
INSERT INTO `t_poem` VALUES ('3688', 'poem', '唐', '殷尧藩', '馆娃宫', '宫女三千去不回，真珠翠羽是尘埃。\\n夫差旧国久破碎，红燕自归花自开。', '2018-03-09 15:39:53');
INSERT INTO `t_poem` VALUES ('3689', 'poem', '唐', '孟浩然', '彭蠡湖中望庐山', '太虚生月晕，舟子知天风。\\n挂席候明发，渺漫平湖中。\\n中流见匡阜，势压九江雄。\\n黯黮凝黛色，峥嵘当曙空。\\n香炉初上日，瀑水喷成虹。\\n久欲追尚子，况兹怀远公。\\n我来限于役，未暇息微躬。\\n淮海途将半，星霜岁欲穷。\\n寄言岩栖者，毕趣当来同。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3690', 'poem', '唐', '张继', '重经巴丘', '昔年高接李膺欢，日泛仙舟醉碧澜。\\n诗句乱随青草落，酒肠俱逐洞庭宽。\\n浮生聚散云相似，往事冥微梦一般。\\n今日片帆城下去，秋风回首泪阑干。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3691', 'poem', '唐', '杜牧', '醉眠', '秋醪雨中熟，寒斋落叶中。\\n幽人本多睡，更酌一樽空。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3692', 'poem', '唐', '柳宗元', '梅雨', '梅实迎时雨，苍茫值晚春。\\n愁深楚猿夜，梦断越鸡晨。\\n海雾连南极，江云暗北津。\\n素衣今尽化，非为帝京尘。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3693', 'poem', '唐', '黄滔', '塞下', '匹马萧萧去不前，平芜千里见穷边。\\n关山色死秋深日，鼓角声沈霜重天。\\n荒骨或衔残铁露，惊风时掠暮沙旋。\\n陇头冤气无归处，化作阴云飞杳然。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3694', 'poem', '唐', '韦应物', '答李瀚', '林中观易罢，溪上对鸥闲。\\n楚俗饶词客，何人最往还。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3695', 'poem', '唐', '张祜', '塞下', '万里配长征，连年惯野营。\\n入群来拣马，抛伴去擒生。\\n箭插雕翎阔，弓盘鹊角轻。\\n闲看行近远，西去受降城。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3696', 'poem', '唐', '邓倚', '春云', '摇曳自西东，依林又逐风。\\n势移青道里，影泛绿波中。\\n夕霁方明日，朝阳复蔽空。\\n度关随去马，出塞引归鸿。\\n色任寒暄变，光将远近同。\\n为霖如见用，还得助成功。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3697', 'poem', '唐', '陈去疾', '采莲曲', '粉光花色叶中开，荷气衣香水上来。\\n棹响清潭见斜领，双鸳何事亦相猜。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3698', 'poem', '唐', '白居易', '闲坐', '暖拥红炉火，闲搔白发头。\\n百年慵里过，万事醉中休。\\n有室同摩诘，无儿比邓攸。\\n莫论身在日，身后亦无忧。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3699', 'poem', '唐', '岑参', '送韩巽入都觐省便赴举', '槐叶苍苍柳叶黄，秋高八月天欲霜。\\n青门百壶送韩侯，白云千里连嵩丘。\\n北堂倚门望君忆，东归扇枕后秋色。\\n洛阳才子能几人，明年桂枝是君得。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3700', 'poem', '唐', '白居易', '山中五绝句·涧中鱼', '海水桑田欲变时，风涛翻覆沸天池。\\n鲸吞蛟斗波成血，深涧游鱼乐不知。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3701', 'poem', '唐', '徐安期', '催妆', '传闻烛下调红粉，明镜台前别作春。\\n不须面上浑妆却，留著双眉待画人。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3702', 'poem', '唐', '白居易', '听幽兰', '琴中古曲是幽兰，为我殷勤更弄看。\\n欲得身心俱静好，自弹不及听人弹。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3703', 'poem', '唐', '卢照邻', '早度分水岭', '丁年游蜀道，班鬓向长安。\\n徒费周王粟，空弹汉吏冠。\\n马蹄穿欲尽，貂裘敝转寒。\\n层冰横九折，积石凌七盘。\\n重溪既下漱，峻峰亦上干。\\n陇头闻戍鼓，岭外咽飞湍。\\n瑟瑟松风急，苍苍山月团。\\n传语后来者，斯路诚独难。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3704', 'poem', '唐', '张继', '明德宫', '碧瓦朱楹白昼闲，金衣宝扇晓风寒。\\n摩云观阁高如许，长对河流出断山。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3705', 'poem', '唐', '灵一', '远公', '远公逢道安，一朝弃儒服。\\n真机久消歇，世教空拘束。\\n誓入罗浮中，遂栖庐山曲。\\n禅经初纂定，佛语新名目。\\n钵帽绝朝宗，簪裾翻拜伏。\\n东林多隐士，为我辞荣禄。', '2018-03-09 15:39:54');
INSERT INTO `t_poem` VALUES ('3706', 'poem', '唐', '杜牧', '秋思', '热去解钳釱，飘萧秋半时。\\n微雨池塘见，好风襟袖知。\\n发短梳未足，枕凉闲且欹。\\n平生分过此，何事不参差。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3707', 'poem', '唐', '韩愈', '谢自然诗', '果州南充县，寒女谢自然。\\n童騃无所识，但闻有神仙。\\n轻生学其术，乃在金泉山。\\n繁华荣慕绝，父母慈爱捐。\\n凝心感魑魅，慌惚难具言。\\n一朝坐空室，云雾生其间。\\n如聆笙竽韵，来自冥冥天。\\n白日变幽晦，萧萧风景寒。\\n檐楹暂明灭，五色光属联。\\n观者徒倾骇，踯躅讵敢前。\\n须臾自轻举，飘若风中烟。\\n茫茫八纮大，影响无由缘。\\n里胥上其事，郡守惊且叹。\\n驱车领官吏，氓俗争相先。\\n入门无所见，冠履同蜕蝉。\\n皆云神仙事，灼灼信可传。\\n余闻古夏后，象物知神奸。\\n山林民可入，魍魉莫逢旃。\\n逶迤不复振，后世恣欺谩。\\n幽明纷杂乱，人鬼更相残。\\n秦皇虽笃好，汉武洪其源。\\n自从二主来，此祸竟连连。\\n木石生怪变，狐狸骋妖患。\\n莫能尽性命，安得更长延。\\n人生处万类，知识最为贤。\\n奈何不自信，反欲从物迁。\\n往者不可悔，孤魂抱深冤。\\n来者犹可诫，余言岂空文。\\n人生有常理，男女各有伦。\\n寒衣及饥食，在纺绩耕耘。\\n下以保子孙，上以奉君亲。\\n苟异于此道，皆为弃其身。\\n噫乎彼寒女，永托异物群。\\n感伤遂成诗，昧者宜书绅。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3708', 'poem', '唐', '刘禹锡', '伤愚溪三首', '溪水悠悠春自来，草堂无主燕飞回。\\n隔帘惟见中庭草，一树山榴依旧开。\\n草圣数行留坏壁，木奴千树属邻家。\\n唯见里门通德榜，残阳寂寞出樵车。\\n柳门竹巷依依在，野草青苔日日多。\\n纵有邻人解吹笛，山阳旧侣更谁过。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3709', 'poem', '唐', '李白', '门有车马客行', '门有车马宾，金鞍曜朱轮。\\n谓从丹霄落，乃是故乡亲。\\n呼儿扫中堂，坐客论悲辛。\\n对酒两不饮，停觞泪盈巾。\\n叹我万里游，飘飘三十春。\\n空谈帝王略，紫绶不挂身。\\n雄剑藏玉匣，阴符生素尘。\\n廓落无所合，流离湘水滨。\\n借问宗党间，多为泉下人。\\n生苦百战役，死托万鬼邻。\\n北风扬胡沙，埋翳周与秦。\\n大运且如此，苍穹宁匪仁。\\n恻怆竟何道，存亡任大钧。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3710', 'poem', '唐', '张文琮', '昭君怨·戒途飞万里', '戒途飞万里，回首望三秦。\\n忽见天山雪，还疑上苑春。\\n玉痕垂粉泪，罗袂拂胡尘。\\n为得胡中曲，还悲远嫁人。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3711', 'poem', '唐', '贾岛', '登江亭晚望', '浩渺浸云根，烟岚没远村。\\n鸟归沙有迹，帆过浪无痕。\\n望水知柔性，看山欲倦魂。\\n纵情犹未已，回马欲黄昏。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3712', 'poem', '唐', '施肩吾', '杂曲歌辞·壮士行', '一斗之胆撑脏腑，如磥之筋碍臂骨。\\n有时误入千人丛，自觉一身横突兀。\\n当今四海无烟尘，胸襟被压不得伸。\\n冻枭残虿我不取，污我匣里青蛇鳞。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3713', 'poem', '唐', '郑谷', '早入谏院二首', '玉阶春冷未催班，暂拂尘衣就笏眠。\\n孤立小心还自笑，梦魂潜绕御炉烟。\\n紫云重叠抱春城，廊下人稀唱漏声。\\n偷得微吟斜倚柱，满衣花露听宫莺。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3714', 'poem', '唐', '杜甫', '铜瓶', '乱后碧井废，时清瑶殿深。\\n铜瓶未失水，百丈有哀音。\\n侧想美人意，应非寒甃沉。\\n蛟龙半缺落，犹得折黄金。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3715', 'poem', '唐', '杜荀鹤', '江岸秋思', '驱马傍江行，乡愁步步生。\\n举鞭挥柳色，随手失蝉声。\\n秋稼缘长道，寒云约古城。\\n家贫遇丰岁，无地可归耕。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3716', 'poem', '唐', '郑谷', '张谷田舍', '县官清且俭，深谷有人家。\\n一径入寒竹，小桥穿野花。\\n碓喧春涧满，梯倚绿桑斜。\\n自说年来稔，前村酒可赊。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3717', 'poem', '唐', '杜甫', '秋野五首', '秋野日疏芜，寒江动碧虚。\\n系舟蛮井络，卜宅楚村墟。\\n枣熟从人打，葵荒欲自锄。\\n盘餐老夫食，分减及溪鱼。\\n易识浮生理，难教一物违。\\n水深鱼极乐，林茂鸟知归。\\n吾老甘贫病，荣华有是非。\\n秋风吹几杖，不厌此山薇。\\n礼乐攻吾短，山林引兴长。\\n掉头纱帽仄，曝背竹书光。\\n风落收松子，天寒割蜜房。\\n稀疏小红翠，驻屐近微香。\\n远岸秋沙白，连山晚照红。\\n潜鳞输骇浪，归翼会高风。\\n砧响家家发，樵声个个同。\\n飞霜任青女，赐被隔南宫。\\n身许麒麟画，年衰鸳鹭群。\\n大江秋易盛，空峡夜多闻。\\n径隐千重石，帆留一片云。\\n儿童解蛮语，不必作参军。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3718', 'poem', '唐', '杜甫', '曲江三章，章五句', '曲江萧条秋气高，菱荷枯折随风涛，游子空嗟垂二毛。\\n白石素沙亦相荡，哀鸿独叫求其曹。\\n即事非今亦非古，长歌激越梢林莽，比屋豪华固难数。\\n吾人甘作心似灰，弟侄何伤泪如雨。\\n自断此生休问天，杜曲幸有桑麻田，故将移住南山边。\\n短衣匹马随李广，看射猛虎终残年。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3719', 'poem', '唐', '白居易', '卯时酒', '佛法赞醍醐，仙方夸沆瀣。\\n未如卯时酒，神速功力倍。\\n一杯置掌上，三咽入腹内。\\n煦若春贯肠，暄如日炙背。\\n岂独肢体畅，仍加志气大。\\n当时遗形骸，竟日忘冠带。\\n似游华胥国，疑反混元代。\\n一性既完全，万机皆破碎。\\n半醒思往来，往来吁可怪。\\n宠辱忧喜间，惶惶二十载。\\n前年辞紫闼，今岁抛皂盖。\\n去矣鱼返泉，超然蝉离蜕。\\n是非莫分别，行止无疑碍。\\n浩气贮胸中，青云委身外。\\n扪心私自语，自语谁能会。\\n五十年来心，未如今日泰。\\n况兹杯中物，行坐长相对。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3720', 'poem', '唐', '刘沧', '经炀帝行宫', '此地曾经翠辇过，浮云流水竟如何。\\n香销南国美人尽，怨入东风芳草多。\\n残柳宫前空露叶，夕阳川上浩烟波。\\n行人遥起广陵思，古渡月明闻棹歌。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3721', 'poem', '唐', '韦应物', '同褒子秋斋独宿', '山月皎如烛，风霜时动竹。\\n夜半鸟惊栖，窗间人独宿。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3722', 'poem', '唐', '王勃', '三月曲水宴得烟字', '彭泽官初去，河阳赋始传。\\n田园归旧国，诗酒间长筵。\\n列室窥丹洞，分楼瞰紫烟。\\n萦回亘津渡，出没控郊鄽。\\n凤琴调上客，龙辔俨群仙。\\n松石偏宜古，藤萝不记年。\\n重檐交密树，复磴拥危泉。\\n抗石晞南岭，乘沙眇北川。\\n傅岩来筑处，磻溪入钓前。\\n日斜真趣远，幽思梦凉蝉。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3723', 'poem', '唐', '沈佺期', '七夕', '秋近雁行稀，天高鹊夜飞。\\n妆成应懒织，今夕渡河归。\\n月皎宜穿线，风轻得曝衣。\\n来时不可觉，神验有光辉。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3724', 'poem', '唐', '裴迪', '辋川集二十首·孟城坳', '结庐古城下，时登古城上。\\n古城非畴昔，今人自来往。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3725', 'poem', '唐', '张乔', '秋夕', '春恨复秋悲，秋悲难到时。\\n每逢明月夜，长起故山思。\\n巷僻行吟远，蛩多独卧迟。\\n溪僧与樵客，动别十年期。', '2018-03-09 15:40:02');
INSERT INTO `t_poem` VALUES ('3726', 'poem', '唐', '顾况', '听山鹧鸪', '谁家无春酒，何处无春鸟。\\n夜宿桃花村，踏歌接天晓。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3727', 'poem', '唐', '温庭筠', '春日野行', '骑马踏烟莎，青春奈怨何。\\n蝶翎朝粉尽，鸦背夕阳多。\\n柳艳欺芳带，山愁萦翠蛾。\\n别情无处说，方寸是星河。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3728', 'poem', '唐', '刘长卿', '春草宫怀古', '君王不可见，芳草旧宫春。\\n犹带罗裙色，青青向楚人。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3729', 'poem', '唐', '孟郊', '春愁', '春物与愁客，遇时各有违。\\n故花辞新枝，新泪落故衣。\\n日暮两寂寞，飘然亦同归。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3730', 'poem', '唐', '刘叉', '姚秀才爱予小剑因赠', '一条古时水，向我手心流。\\n临行泻赠君，勿薄细碎仇。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3731', 'poem', '唐', '杜牧', '瑶瑟', '玉仙瑶瑟夜珊珊，月过楼西桂烛残。\\n风景人间不如此，动摇湘水彻明寒。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3732', 'poem', '唐', '陆龟蒙', '奉和袭美茶具十咏·茶瓯', '昔人谢塸埞，徒为妍词饰。\\n岂如珪璧姿，又有烟岚色。\\n光参筠席上，韵雅金罍侧。\\n直使于阗君，从来未尝识。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3733', 'poem', '唐', '章孝标', '瀑布', '秋河溢长空，天洒万丈布。\\n深雷隐云壑，孤电挂岩树。\\n沧溟晓喷寒，碧落晴荡素。\\n非趋下流急，热使不得住。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3734', 'poem', '唐', '贾岛', '送唐环归敷水庄', '毛女峰当户，日高头未梳。\\n地侵山影扫，叶带露痕书。\\n松径僧寻药，沙泉鹤见鱼。\\n一川风景好，恨不有吾庐。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3735', 'poem', '唐', '白居易', '客中月', '客从江南来，来时月上弦。\\n悠悠行旅中，三见清光圆。\\n晓随残月行，夕与新月宿。\\n谁谓月无情，千里远相逐。\\n朝发渭水桥，暮入长安陌。\\n不知今夜月，又作谁家客。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3736', 'poem', '唐', '卢士衡', '僧房听雨', '古寺松轩雨声别，寒窗听久诗魔发。\\n记得年前在赤城，石楼梦觉三更雪。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3737', 'poem', '唐', '许棠', '过分水岭', '陇山高共鸟行齐，瞰险盘空甚蹑梯。\\n云势崩腾时向背，水声呜咽若东西。\\n风兼雨气吹人面，石带冰棱碍马蹄。\\n此去秦川无别路，隔崖穷谷却难迷。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3738', 'poem', '唐', '杜甫', '至后', '冬至至后日初长，远在剑南思洛阳。\\n青袍白马有何意，金谷铜驼非故乡。\\n梅花欲开不自觉，棣萼一别永相望。\\n愁极本凭诗遣兴，诗成吟咏转凄凉。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3739', 'poem', '唐', '王勃', '仲春郊外', '东园垂柳径，西堰落花津。\\n物色连三月，风光绝四邻。\\n鸟飞村觉曙，鱼戏水知春。\\n初晴山院里，何处染嚣尘。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3740', 'poem', '唐', '李中', '宿山店书怀寄东林令图上人', '一宿山前店，旅情安可穷。\\n猿声乡梦后，月影竹窗中。\\n南楚征途阔，东吴旧业空。\\n虎溪莲社客，应笑此飘蓬。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3741', 'poem', '唐', '杜牧', '寓言', '暖风迟日柳初含，顾影看身又自惭。\\n何事明朝独惆怅，杏花时节在江南。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3742', 'poem', '唐', '储光羲', '观竞渡', '大夫沉楚水，千祀国人哀。\\n习棹江流长，迎神雨雾开。\\n标随绿云动，船逆清波来。\\n下怖鱼龙起，上惊凫雁回。\\n能令秋大有，鼓吹远相催。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3743', 'poem', '唐', '李白', '秦女休行', '西门秦氏女，秀色如琼花。\\n手挥白杨刀，清昼杀雠家。\\n罗袖洒赤血，英气凌紫霞。\\n直上西山去，关吏相邀遮。\\n婿为燕国王，身被诏狱加。\\n犯刑若履虎，不畏落爪牙。\\n素颈未及断，摧眉伏泥沙。\\n金鸡忽放赦，大辟得宽赊。\\n何惭聂政姊，万古共惊嗟。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3744', 'poem', '唐', '罗隐', '长安秋夜', '远闻天子似羲皇，偶舍渔乡入帝乡。\\n五等列侯无故旧，一枝仙桂有风霜。\\n灯欹短焰烧离鬓，漏转寒更滴旅肠。\\n归计未知身已老，九衢双阙夜苍苍。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3745', 'poem', '唐', '陈羽', '长安卧病秋夜言怀', '九重门锁禁城秋，月过南宫渐映楼。\\n紫陌夜深槐露滴，碧空云尽火星流。\\n风清刻漏传三殿，甲第歌钟乐五侯。\\n楚客病来乡思苦，寂寥灯下不胜愁。', '2018-03-09 15:40:06');
INSERT INTO `t_poem` VALUES ('3746', 'poem', '唐', '王建', '凉州行', '凉州四边沙皓皓，汉家无人开旧道。\\n边头州县尽胡兵，将军别筑防秋城。\\n万里人家皆已没，年年旌节发西京。\\n多来中国收妇女，一半生男为汉语。\\n蕃人旧日不耕犁，相学如今种禾黍。\\n驱羊亦著锦为衣，为惜毡裘防斗时。\\n养蚕缲茧成匹帛，那堪绕帐作旌旗。\\n城头山鸡鸣角角，洛阳家家学胡乐。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3747', 'poem', '唐', '杜甫', '光禄坂行', '山行落日下绝壁，西望千山万山赤。\\n树枝有鸟乱鸣时，暝色无人独归客。\\n马惊不忧深谷坠，草动只怕长弓射。\\n安得更似开元中，道路即今多拥隔。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3748', 'poem', '唐', '李白', '咏邻女东窗海石榴', '鲁女东窗下，海榴世所稀。\\n珊瑚映绿水，未足比光辉。\\n清香随风发，落日好鸟归。\\n愿为东南枝，低举拂罗衣。\\n无由一攀折，引领望金扉。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3749', 'poem', '唐', '文偃', '金屑眼中翳', '金屑眼中翳，衣珠法上尘。己灵犹不重，佛视为何人？', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3750', 'poem', '唐', '羊士谔', '登楼', '槐柳萧疏绕郡城，夜添山雨作江声。\\n秋风南陌无车马，独上高楼故国情。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3751', 'poem', '唐', '王维', '皇甫岳云溪杂题五首·鸬鹚堰', '乍向红莲没，复出清蒲飏。\\n独立何褵褷，衔鱼古查上。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3752', 'poem', '唐', '刘叉', '野哭', '棘针生狞义路闲，野泉相吊声潺潺。\\n哀哉异教溺颓俗，淳源一去何时还。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3753', 'poem', '唐', '徐铉', '春分日', '仲春初四日，春色正中分。\\n绿野裴回月，晴在断续云。\\n燕飞犹个个，花落已纷纷。\\n思妇高楼晚，歌声不可闻。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3754', 'poem', '唐', '李商隐', '正月崇让宅', '密锁重关掩绿苔，廊深阁迥此徘徊。\\n先知风起月含晕，尚自露寒花未开。\\n蝙拂帘旌终展转，鼠翻窗网小惊猜。\\n背灯独共馀香语，不觉犹歌起夜来。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3755', 'poem', '唐', '胡曾', '咏史诗·垓下', '拔山力尽霸图隳，倚剑空歌不逝骓。\\n明月满营天似水，那堪回首别虞姬。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3756', 'poem', '唐', '贾至', '别裴九弟', '西江万里向东流，今夜江边驻客舟。\\n月色更添春色好，芦风似胜竹风幽。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3757', 'poem', '唐', '李益', '杂曲歌辞·游子吟', '女羞夫婿荡，客耻主人贱。\\n遭遇同众流，低回愧相见。\\n君非青铜镜，何事空照面。\\n莫以衣上尘，不谓心如练。\\n人生当荣盛，待士勿言倦。\\n君看白日驰，何异弦上箭。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3758', 'poem', '唐', '周昙', '春秋战国门·中山君', '敌临烹子一何庸，激怒来军速自攻。\\n结怨岂思围不解，愚谋多以杀为雄。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3759', 'poem', '唐', '郑谷', '旅寓洛南村舍', '村落清明近，秋千稚女夸。\\n春阴妨柳絮，月黑见梨花。\\n白鸟窥鱼网，青帘认酒家。\\n幽栖虽自适，交友在京华。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3760', 'poem', '唐', '张孜', '纪梦句', '上天知我忆其人，使向人间梦中见。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3761', 'poem', '唐', '徐氏', '丈人观谒先帝御容', '圣帝归梧野，躬来谒圣颜。\\n旋登三径路，似陟九嶷山。\\n日照堆岚迥，云横积翠间。\\n期修封禅礼，方俟再跻攀。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3762', 'poem', '唐', '刘禹锡', '和裴相公傍水闲行', '为爱逍遥第一篇，时时闲步赏风烟。\\n看花临水心无事，功业成来二十年。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3763', 'poem', '唐', '周濆', '废宅', '牢落画堂空锁尘，荒凉庭树暗消春。\\n豪家莫笑此中事，曾见此中人笑人。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3764', 'poem', '唐', '戴叔伦', '荔枝', '红颗珍珠诚可爱，白须太守亦何痴。\\n十年结子知谁在，自向中庭种荔枝。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3765', 'poem', '唐', '杜牧', '少年行', '连环羁玉声光碎，绿锦蔽泥虬卷高。\\n春风细雨走马去，珠落璀璀白罽袍。', '2018-03-09 15:40:16');
INSERT INTO `t_poem` VALUES ('3766', 'poem', '唐', '李商隐', '和孙朴韦蟾孔雀咏', '此去三梁远，今来万里携。\\n西施因网得，秦客被花迷。\\n可在青鹦鹉，非关碧野鸡。\\n约眉怜翠羽，刮目想金篦。\\n瘴气笼飞远，蛮花向坐低。\\n轻于赵皇后，贵极楚悬黎。\\n都护矜罗幕，佳人炫绣袿。\\n屏风临烛釦，捍拨倚香脐。\\n旧思牵云叶，新愁待雪泥。\\n爱堪通梦寐，画得不端倪。\\n地锦排苍雁，帘钉镂白犀。\\n曙霞星斗外，凉月露盘西。\\n妒好休夸舞，经寒且少啼。\\n红楼三十级，稳稳上丹梯。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3767', 'poem', '唐', '李白', '苏武', '苏武在匈奴，十年持汉节。\\n白雁上林飞，空传一书札。\\n牧羊边地苦，落日归心绝。\\n渴饮月窟冰，饥餐天上雪。\\n东还沙塞远，北怆河梁别。\\n泣把李陵衣，相看泪成血。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3768', 'poem', '唐', '吴融', '废宅', '风飘碧瓦雨摧垣，却有邻人与锁门。\\n几树好花闲白昼，满庭荒草易黄昏。\\n放鱼池涸蛙争聚，栖燕梁空雀自喧。\\n不独凄凉眼前事，咸阳一火便成原。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3769', 'poem', '唐', '陈陶', '赠别', '海国一尺绮，冰壶万缕丝。\\n以君西攀桂，赠此金莲枝。\\n高鸟思茂林，穷鱼乐洿池。\\n平生握中宝，无使岁寒移。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3770', 'poem', '唐', '邵谒', '秋夕', '万里凭梦归，骨肉皆在眼。\\n觉来益惆怅，不信长安远。\\n人人但为农，我独常逢旱。\\n恶命如漏卮，滴滴添不满。\\n天末雁来时，一叫一肠断。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3771', 'poem', '唐', '王维', '不遇咏', '北阙献书寝不报，南山种田时不登。\\n百人会中身不预，五侯门前心不能。\\n身投河朔饮君酒，家在茂陵平安否。\\n且此登山复临水，莫问春风动杨柳。\\n今人昨人多自私，我心不说君应知。\\n济人然后拂衣去，肯作徒尔一男儿。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3772', 'poem', '唐', '孟浩然', '赠王九', '日暮田家远，山中勿久淹。\\n归人须早去，稚子望陶潜。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3773', 'poem', '唐', '李益', '听晓角', '边霜昨夜堕关榆，吹角当城汉月孤。\\n无限塞鸿飞不度，秋风卷入小单于。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3774', 'poem', '唐', '高蟾', '楚思', '叠浪与云急，翠兰和意香。\\n风流化为雨，日暮下巫阳。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3775', 'poem', '唐', '白居易', '酬裴令公赠马相戏', '安石风流无奈何，欲将赤骥换青娥。\\n不辞便送东山去，临老何人与唱歌。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3776', 'poem', '唐', '孟浩然', '问舟子', '向夕问舟子，前程复几多。\\n湾头正堪泊，淮里足风波。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3777', 'poem', '唐', '张祜', '散花楼', '锦江城外锦城头，回望秦川上轸忧。\\n正值血魂来梦里，杜鹃声在散花楼。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3778', 'poem', '唐', '李商隐', '西亭', '此夜西亭月正圆，疏帘相伴宿风烟。\\n梧桐莫更翻清露，孤鹤从来不得眠。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3779', 'poem', '唐', '卢纶', '客舍喜崔补阙司空拾遗访宿', '步月访诸邻，蓬居宿近臣。\\n乌裘先醉客，清镜早朝人。\\n坏壁烟垂网，香街火照尘。\\n悲荣俱是分，吾亦乐吾贫。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3780', 'poem', '唐', '虞世南', '咏舞', '繁弦奏渌水，长袖转回鸾。\\n一双俱应节，还似镜中看。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3781', 'poem', '唐', '杨凭', '海榴', '海榴殷色透帘栊，看盛看衰意欲同。\\n若许三英随五马，便将浓艳斗繁红。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3782', 'poem', '唐', '李商隐', '酬崔八早梅有赠兼示之作', '知访寒梅过野塘，久留金勒为回肠。\\n谢郎衣袖初翻雪，荀令熏炉更换香。\\n何处拂胸资蝶粉，几时涂额藉蜂黄。\\n维摩一室虽多病，亦要天花作道场。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3783', 'poem', '唐', '杜甫', '怀锦水居止二首', '军旅西征僻，风尘战伐多。\\n犹闻蜀父老，不忘舜讴歌。\\n天险终难立，柴门岂重过。\\n朝朝巫峡水，远逗锦江波。\\n万里桥南宅，百花潭北庄。\\n层轩皆面水，老树饱经霜。\\n雪岭界天白，锦城曛日黄。\\n惜哉形胜地，回首一茫茫。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3784', 'poem', '唐', '杜甫', '月三首', '断续巫山雨，天河此夜新。\\n若无青嶂月，愁杀白头人。\\n魍魉移深树，虾蟆动半轮。\\n故园当北斗，直指照西秦。\\n并照巫山出，新窥楚水清。\\n羁栖愁里见，二十四回明。\\n必验升沉体，如知进退情。\\n不违银汉落，亦伴玉绳横。\\n万里瞿塘峡，春来六上弦。\\n时时开暗室，故故满青天。\\n爽合风襟静，高当泪脸悬。\\n南飞有乌鹊，夜久落江边。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3785', 'poem', '唐', '皮日休', '太湖诗。崦里（傍龟山下有良田二十顷）', '崦里何幽奇，膏腴二十顷。\\n风吹稻花香，直过龟山顶。\\n青苗细腻卧，白羽悠溶静。\\n塍畔起鸊鹈，田中通舴艋。\\n几家傍潭洞，孤戍当林岭。\\n罢钓时煮菱，停缲或焙茗。\\n峭然八十翁，生计于此永。\\n苦力供征赋，怡颜过朝暝。\\n洞庭取异事，包山极幽景。\\n念尔饱得知，亦是遗民幸。', '2018-03-09 15:40:22');
INSERT INTO `t_poem` VALUES ('3786', 'poem', '唐', '韦应物', '林园晚霁', '雨歇见青山，落日照林园。\\n山多烟鸟乱，林清风景翻。\\n提携唯子弟，萧散在琴言。\\n同游不同意，耿耿独伤魂。\\n寂寞钟已尽，如何还入门。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3787', 'poem', '唐', '李白', '答族侄僧中孚赠玉泉仙人掌茶', '常闻玉泉山，山洞多乳窟。\\n仙鼠如白鸦，倒悬清溪月。\\n茗生此中石，玉泉流不歇。\\n根柯洒芳津，采服润肌骨。\\n丛老卷绿叶，枝枝相接连。\\n曝成仙人掌，似拍洪崖肩。\\n举世未见之，其名定谁传。\\n宗英乃禅伯，投赠有佳篇。\\n清镜烛无盐，顾惭西子妍。\\n朝坐有馀兴，长吟播诸天。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3788', 'poem', '唐', '宋之问', '过函谷关', '二百四十载，海内何纷纷。\\n六国兵同合，七雄势未分。\\n从成拒秦帝，策决问苏君。\\n鸡鸣将狗盗，论德不论勋。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3789', 'poem', '唐', '杜甫', '中宵', '西阁百寻馀，中宵步绮疏。\\n飞星过水白，落月动沙虚。\\n择木知幽鸟，潜波想巨鱼。\\n亲朋满天地，兵甲少来书。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3790', 'poem', '唐', '韦庄', '送李秀才归荆溪', '八月中秋月正圆，送君吟上木兰船。\\n人言格调胜玄度，我爱篇章敌浪仙。\\n晚渡去时冲细雨，夜滩何处宿寒烟。\\n楚王宫去阳台近，莫倚风流滞少年。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3791', 'poem', '唐', '权德舆', '送崔端公郎君入京觐省', '已见风姿美，仍闻艺业勤。\\n清秋上国路，白皙少年人。\\n带月轻帆疾，迎霜彩服新。\\n过庭若有问，一为说漳滨。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3792', 'poem', '唐', '李白', '天台晓望', '天台邻四明，华顶高百越。\\n门标赤城霞，楼栖沧岛月。\\n凭高登远览，直下见溟渤。\\n云垂大鹏翻，波动巨鳌没。\\n风潮争汹涌，神怪何翕忽。\\n观奇迹无倪，好道心不歇。\\n攀条摘朱实，服药炼金骨。\\n安得生羽毛，千春卧蓬阙？', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3793', 'poem', '唐', '储光羲', '临江亭五咏', '晋家南作帝，京镇北为关。\\n江水中分地，城楼下带山。\\n金陵事已往，青盖理无还。\\n落日空亭上，愁看龙尾湾。\\n山横小苑前，路尽大江边。\\n此地兴王业，无如宋主贤。\\n潮生建业水，风散广陵烟。\\n直望清波里，只言别有天。\\n城头落暮晖，城外捣秋衣。\\n江水青云挹，芦花白雪飞。\\n南州王气疾，东国海风微。\\n借问商歌客，年年何处归。\\n古木啸寒禽，层城带夕阴。\\n梁园多绿柳，楚岸尽枫林。\\n山际空为险，江流长自深。\\n平生何以恨，天地本无心。\\n京山千里过，孤愤望中来。\\n江势将天合，城门向水开。\\n落霞明楚岸，夕露湿吴台。\\n去去无相识，陈皇安在哉。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3794', 'poem', '唐', '羊士谔', '忆江南旧游二首', '山阴道上桂花初，王谢风流满晋书。\\n曾作江南步从事，秋来还复忆鲈鱼。\\n曲水三春弄彩毫，樟亭八月又观涛。\\n金罍几醉乌程酒，鹤舫闲吟把蟹螯。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3795', 'poem', '唐', '秦韬玉', '燕子', '不知大厦许栖无，频已衔泥到座隅。\\n曾与佳人并头语，几回抛却绣工夫。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3796', 'poem', '唐', '周昙', '三代门·管蔡', '伊商胡越尚同图，管蔡如何有异谟。\\n不念祖宗危社稷，强于仁圣遣行诛。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3797', 'poem', '唐', '李贺', '浩歌', '南风吹山作平地，帝遣天吴移海水。\\n王母桃花千遍红，彭祖巫咸几回死。\\n青毛骢马参差钱，娇春杨柳含细烟。\\n筝人劝我金屈卮，神血未凝身问谁。\\n不须浪饮丁都护，世上英雄本无主。\\n买丝绣作平原君，有酒惟浇赵州土。\\n漏催水咽玉蟾蜍，卫娘发薄不胜梳。\\n看见秋眉换新绿，二十男儿那刺促。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3798', 'poem', '唐', '白居易', '清明日送韦侍御贬虔州', '寂寞清明日，萧条司马家。\\n留饧和冷粥，出火煮新茶。\\n欲别能无酒，相留亦有花。\\n南迁更何处，此地已天涯。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3799', 'poem', '唐', '杜审言', '赠苏绾书记', '知君书记本翩翩，为许从戎赴朔边。\\n红粉楼中应计日，燕支山下莫经年。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3800', 'poem', '唐', '杜甫', '独坐', '悲愁回白首，倚杖背孤城。\\n江敛洲渚出，天虚风物清。\\n沧溟服衰谢，朱绂负平生。\\n仰羡黄昏鸟，投林羽翮轻。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3801', 'poem', '唐', '骆宾王', '送郑少府入辽共赋侠客远从戎', '边烽警榆塞，侠客度桑干。\\n柳叶开银镝，桃花照玉鞍。\\n满月临弓影，连星入剑端。\\n不学燕丹客，空歌易水寒。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3802', 'poem', '唐', '白居易', '姚侍御见过，戏赠', '晚起春寒慵裹头，客来池上偶同游。\\n东台御史多提举，莫按金章系布裘。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3803', 'poem', '唐', '崔仲容', '戏赠', '暂到昆仑未得归，阮郎何事教人非。\\n如今身佩上清箓，莫遣落花沾羽衣。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3804', 'poem', '唐', '杜甫', '见萤火', '巫山秋夜萤火飞，帘疏巧入坐人衣。\\n忽惊屋里琴书冷，复乱檐边星宿稀。\\n却绕井阑添个个，偶经花蕊弄辉辉。\\n沧江白发愁看汝，来岁如今归未归。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3805', 'poem', '唐', '韩偓', '偶见', '千金莫惜旱莲生，一笑从教下蔡倾。\\n仙树有花难问种，御香闻气不知名。\\n愁来自觉歌喉咽，瘦去谁怜舞掌轻。\\n小叠红笺书恨字，与奴方便寄卿卿。', '2018-03-09 15:40:28');
INSERT INTO `t_poem` VALUES ('3806', 'poem', '唐', '方干', '废宅', '主人何处独裴回，流水自流花自开。\\n若见故交皆散去，即应新燕不归来。\\n入门缭绕穿荒竹，坐石逡巡染绿苔。\\n应是曾经恶风雨，修桐半折损琴材。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3807', 'poem', '唐', '翁绶', '横吹曲辞·陇头吟', '陇水潺湲陇树黄，征人陇上尽思乡。\\n马嘶斜日朔风急，雁过寒云边思长。\\n残月出林明剑戟，平沙隔水见牛羊。\\n横行俱足封侯者，谁斩楼兰献未央。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3808', 'poem', '唐', '元稹', '春病', '病来闲卧久，因见静时心。\\n残月晓窗迥，落花幽院深。\\n望山移坐榻，行药步墙阴。\\n车马门前度，遥闻哀苦吟。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3809', 'poem', '唐', '杜甫', '观李固请司马弟山水图三首', '简易高人意，匡床竹火炉。\\n寒天留远客，碧海挂新图。\\n虽对连山好，贪看绝岛孤。\\n群仙不愁思，冉冉下蓬壶。\\n方丈浑连水，天台总映云。\\n人间长见画，老去恨空闻。\\n范蠡舟偏小，王乔鹤不群。\\n此生随万物，何路出尘氛。\\n高浪垂翻屋，崩崖欲压床。\\n野桥分子细，沙岸绕微茫。\\n红浸珊瑚短，青悬薜荔长。\\n浮查并坐得，仙老暂相将。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3810', 'poem', '唐', '韩愈', '新竹', '笋添南阶竹，日日成清閟。\\n缥节已储霜，黄苞犹掩翠。\\n出栏抽五六，当户罗三四。\\n高标陵秋严，贞色夺春媚。\\n稀生巧补林，并出疑争地。\\n纵横乍依行，烂熳忽无次。\\n风枝未飘吹，露粉先涵泪。\\n何人可携玩，清景空瞪视。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3811', 'poem', '唐', '司空图', '诗品二十四则·超诣', '匪神之灵，匪机之微。\\n如将白云，清风与归。\\n远引若至，临之已非。\\n少有道契，终与俗违。\\n乱山高木，碧苔芳晖。\\n诵之思之，其声愈稀。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3812', 'poem', '唐', '贾岛', '送人适越', '高城满夕阳，何事欲沾裳。\\n迁客蓬蒿暮，游人道路长。\\n晴湖胜镜碧，寒柳似金黄。\\n若有相思梦，殷勤载八行。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3813', 'poem', '唐', '白居易', '郡亭', '平旦起视事，亭午卧掩关。\\n除亲簿领外，多在琴书前。\\n况有虚白亭，坐见海门山。\\n潮来一凭槛，宾至一开筵。\\n终朝对云水，有时听管弦。\\n持此聊过日，非忙亦非闲。\\n山林太寂寞，朝阙空喧烦。\\n唯兹郡阁内，嚣静得中间。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3814', 'poem', '唐', '李颀', '塞下曲', '黄云雁门郡，日暮风沙里。\\n千骑黑貂裘，皆称羽林子。\\n金笳吹朔雪，铁马嘶云水。\\n帐下饮蒲萄，平生寸心是。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3815', 'poem', '唐', '施肩吾', '杜鹃花词', '杜鹃花时夭艳然，所恨帝城人不识。\\n丁宁莫遣春风吹，留与佳人比颜色。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3816', 'poem', '唐', '宋之问', '途中寒食题黄梅临江驿寄崔融', '马上逢寒食，愁中属暮春。\\n可怜江浦望，不见洛阳人。\\n北极怀明主，南溟作逐臣。\\n故园肠断处，日夜柳条新。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3817', 'poem', '唐', '华山老人', '月夜', '涧水泠泠声不绝，溪流茫茫野花发。\\n自去自来人不知，归时常对空山月。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3818', 'poem', '唐', '白居易', '空闺怨', '寒月沉沉洞房静，真珠帘外梧桐影。\\n秋霜欲下手先知，灯底裁缝剪刀冷。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3819', 'poem', '唐', '杨齐哲', '过函谷关', '地险崤函北，途经分陕东。\\n逶迤众山尽，荒凉古塞空。\\n河光流晓日，树影散朝风。\\n圣德今无外，何处是关中。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3820', 'poem', '唐', '杜甫', '天边行', '天边老人归未得，日暮东临大江哭。\\n陇右河源不种田，胡骑羌兵入巴蜀。\\n洪涛滔天风拔木，前飞秃鹙后鸿鹄。\\n九度附书向洛阳，十年骨肉无消息。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3821', 'poem', '唐', '刘禹锡', '思黯南墅赏牡丹', '偶然相遇人间世，合在增城阿姥家。\\n有此倾城好颜色，天教晚发赛诸花。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3822', 'poem', '唐', '孟浩然', '途次望乡', '客行愁落日，乡思重相催。\\n况在他山外，天寒夕鸟来。\\n雪深迷郢路，云暗失阳台。\\n可叹凄惶子，高歌谁为媒。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3823', 'poem', '唐', '张乔', '寄弟', '故里行人战后疏，青崖萍寄白云居。\\n那堪又是伤春日，把得长安落第书。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3824', 'poem', '唐', '孙元晏', '宋·乌衣巷', '古迹荒基好叹嗟，满川吟景只烟霞。\\n乌衣巷在何人住，回首令人忆谢家。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3825', 'poem', '唐', '杜牧', '怀吴中冯秀才', '长洲苑外草萧萧，却算游程岁月遥。\\n唯有别时今不忘，暮烟秋雨过枫桥。', '2018-03-09 15:40:32');
INSERT INTO `t_poem` VALUES ('3826', 'poem', '唐', '刘长卿', '送裴郎中贬吉州', '乱军交白刃，一骑出黄尘。\\n汉节同归阙，江帆共逐臣。\\n猿愁歧路晚，梅作异方春。\\n知己酂侯在，应怜脱粟人。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3827', 'poem', '唐', '崔涂', '海棠图', '海棠花底三年客，不见海棠花盛开。\\n却向江南看图画，始惭虚到蜀城来。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3828', 'poem', '唐', '王琚', '美女篇', '东邻美女实名倡，绝代容华无比方。\\n浓纤得中非短长，红素天生谁饰妆。\\n桂楼椒阁木兰堂，绣户雕轩文杏梁。\\n屈曲屏风绕象床，萎蕤翠帐缀香囊。\\n玉台龙镜洞彻光，金炉沉烟酷烈芳。\\n遥闻行佩音锵锵，含娇欲笑出洞房。\\n二八三五闺心切，褰帘卷幔迎春节。\\n清歌始发词怨咽，鸣琴一弄心断绝。\\n借问哀怨何所为，盛年情多心自悲。\\n须臾破颜倏敛态，一悲一喜并相宜。\\n何能见此不注心，惜无媒氏为传音。\\n可怜盈盈直千金，谁家君子为藁砧。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3829', 'poem', '唐', '鱼玄机', '浣纱庙', '吴越相谋计策多，浣纱神女已相和。\\n一双笑靥才回面，十万精兵尽倒戈。\\n范蠡功成身隐遁，伍胥谏死国消磨。\\n只今诸暨长江畔，空有青山号苎萝。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3830', 'poem', '唐', '刘禹锡', '曲江春望', '凤城烟雨歇，万象含佳气。\\n酒后人倒狂，花时天似醉。\\n三春车马客，一代繁华地。\\n何事独伤怀，少年曾得意。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3831', 'poem', '唐', '李白', '送张舍人之江东', '张翰江东去，正值秋风时。\\n天清一雁远，海阔孤帆迟。\\n白日行欲暮，沧波杳难期。\\n吴洲如见月，千里幸相思。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3832', 'poem', '唐', '沈佺期', '奉和春初幸太平公主南庄应制', '主家山第早春归，御辇春游绕翠微。\\n买地铺金曾作埒，寻河取石旧支机。\\n云间树色千花满，竹里泉声百道飞。\\n自有神仙鸣凤曲，并将歌舞报恩晖。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3833', 'poem', '唐', '李白', '金门答苏秀才', '君还石门日，朱火始改木。\\n春草如有情，山中尚含绿。\\n折芳愧遥忆，永路当日勖。\\n远见故人心，平生以此足。\\n巨海纳百川，麟阁多才贤。\\n献书入金阙，酌醴奉琼筵。\\n屡忝白云唱，恭闻黄竹篇。\\n恩光照拙薄，云汉希腾迁。\\n铭鼎倘云遂，扁舟方渺然。\\n我留在金门，君去卧丹壑。\\n未果三山期，遥欣一丘乐。\\n玄珠寄象罔，赤水非寥廓。\\n愿狎东海鸥，共营西山药。\\n栖岩君寂灭，处世余龙蠖。\\n良辰不同赏，永日应闲居。\\n鸟吟檐间树，花落窗下书。\\n缘溪见绿筱，隔岫窥红蕖。\\n采薇行笑歌，眷我情何已。\\n月出石镜间，松鸣风琴里。\\n得心自虚妙，外物空颓靡。\\n身世如两忘，从君老烟水。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3834', 'poem', '唐', '白居易', '短歌曲', '世人求富贵，多为奉嗜欲。\\n盛衰不自由，得失常相逐。\\n问君少年日，苦学将干禄。\\n负笈尘中游，抱书雪前读。\\n布衾不周体，藜茹才充腹。\\n三十登宦途，五十被朝服。\\n奴温新挟纩，马肥初食粟。\\n未敢议欢游，尚为名检束。\\n耳目聋暗后，堂上调丝竹。\\n牙齿缺落时，盘中堆酒肉。\\n彼来此已去，外馀中不足。\\n少壮与荣华，相避如寒燠。\\n青云去地远，白日经天速。\\n从古无奈何，短歌听一曲。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3835', 'poem', '唐', '李白', '寻鲁城北范居士失道落苍耳中见范置酒摘苍耳作', '雁度秋色远，日静无云时。\\n客心不自得，浩漫将何之。\\n忽忆范野人，闲园养幽姿。\\n茫然起逸兴，但恐行来迟。\\n城壕失往路，马首迷荒陂。\\n不惜翠云裘，遂为苍耳欺。\\n入门且一笑，把臂君为谁。\\n酒客爱秋蔬，山盘荐霜梨。\\n他筵不下箸，此席忘朝饥。\\n酸枣垂北郭，寒瓜蔓东篱。\\n还倾四五酌，自咏猛虎词。\\n近作十日欢，远为千载期。\\n风流自簸荡，谑浪偏相宜。\\n酣来上马去，却笑高阳池。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3836', 'poem', '唐', '韦庄', '春云', '春云春水两溶溶，倚郭楼台晚翠浓。\\n山好只因人化石，地灵曾有剑为龙。\\n官辞凤阙频经岁，家住峨嵋第几峰。\\n王粲不知多少恨，夕阳吟断一声钟。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3837', 'poem', '唐', '杨炯', '广溪峡', '广溪三峡首，旷望兼川陆。\\n山路绕羊肠，江城镇鱼腹。\\n乔林百丈偃，飞水千寻瀑。\\n惊浪回高天，盘涡转深谷。\\n汉氏昔云季，中原争逐鹿。\\n天下有英雄，襄阳有龙伏。\\n常山集军旅，永安兴版筑。\\n池台忽已倾，邦家遽沦覆。\\n庸才若刘禅，忠佐为心腹。\\n设险犹可存，当无贾生哭。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3838', 'poem', '唐', '杜牧', '盆池', '凿破苍苔地，偷他一片天。\\n白云生镜里，明月落阶前。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3839', 'poem', '唐', '白居易', '禁中闻蛩', '悄悄禁门闭，夜深无月明。\\n西窗独暗坐，满耳新蛩声。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3840', 'poem', '唐', '关盼盼', '燕子楼', '适看鸿雁岳阳回，又睹玄禽逼社来。\\n瑶瑟玉箫无意绪，任从蛛网任从灰。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3841', 'poem', '唐', '孟郊', '姑蔑城', '劲越既成土，强吴亦为墟。\\n皇风一已被，兹邑信平居。\\n抚俗观旧迹，行春布新书。\\n兴亡意何在，绵叹空踌蹰。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3842', 'poem', '唐', '元稹', '酬知退', '终须修到无修处，闻尽声闻始不闻。\\n莫著妄心销彼我，我心无我亦无君。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3843', 'poem', '唐', '李涉', '黄葵花', '此花莫遣俗人看，新染鹅黄色未乾。\\n好逐秋风上天去，紫阳宫女要头冠。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3844', 'poem', '唐', '顾况', '溪上', '采莲溪上女，舟小怯摇风。\\n惊起鸳鸯宿，水云撩乱红。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3845', 'poem', '唐', '欧阳衮', '神光寺', '香刹悬青磴，飞楼界碧空。\\n石门栖怖鸽，慈塔绕归鸿。\\n有法将心镜，无名属性通。\\n从来乐幽寂，寻觅未能穷。', '2018-03-09 15:40:44');
INSERT INTO `t_poem` VALUES ('3846', 'poem', '唐', '李商隐', '谢书', '微意何曾有一毫，空携笔砚奉龙韬。\\n自蒙半夜传衣后，不羡王祥得佩刀。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3847', 'poem', '唐', '韦庄', '惊秋', '不向烟波狎钓舟，强亲文墨事儒丘。\\n长安十二槐花陌，曾负秋风多少秋。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3848', 'poem', '唐', '胡曾', '咏史诗·牛渚', '温峤南归辍棹晨，燃犀牛渚照通津。\\n谁知万丈洪流下，更有朱衣跃马人。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3849', 'poem', '唐', '李商隐', '景阳井', '景阳宫井剩堪悲，不尽龙鸾誓死期。\\n肠断吴王宫外水，浊泥犹得葬西施。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3850', 'poem', '唐', '黄滔', '催妆', '北府迎尘南郡来，莫将芳意更迟回。\\n虽言天上光阴别，且被人间更漏催。\\n烟树迥垂连蒂杏，彩童交捧合欢杯。\\n吹箫不是神仙曲，争引秦娥下凤台。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3851', 'poem', '唐', '杜甫', '暮归', '霜黄碧梧白鹤栖，城上击柝复乌啼。\\n客子入门月皎皎，谁家捣练风凄凄。\\n南渡桂水阙舟楫，北归秦川多鼓鼙。\\n年过半百不称意，明日看云还杖藜。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3852', 'poem', '唐', '白居易', '醉中对红叶', '临风杪秋树，对酒长年人。\\n醉貌如霜叶，虽红不是春。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3853', 'poem', '唐', '武元衡', '赠佳人', '步摇金翠玉搔头，倾国倾城胜莫愁。\\n若逞仙姿游洛浦，定知神女谢风流。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3854', 'poem', '唐', '元稹', '恨妆成', '晓日穿隙明，开帷理妆点。\\n傅粉贵重重，施朱怜冉冉。\\n柔鬟背额垂，丛鬓随钗敛。\\n凝翠晕蛾眉，轻红拂花脸。\\n满头行小梳，当面施圆靥。\\n最恨落花时，妆成独披掩。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3855', 'poem', '唐', '李颀', '送皇甫曾游襄阳山水兼谒韦太守', '岘山枕襄阳，滔滔江汉长。\\n山深卧龙宅，水净斩蛟乡。\\n元凯春秋传，昭明文选堂。\\n风流满今古，烟岛思微茫。\\n白雁暮冲雪，青林寒带霜。\\n芦花独戍晚，柑实万家香。\\n旧国欲兹别，轻舟眇未央。\\n百花亭漫漫，一柱观苍苍。\\n按俗荆南牧，持衡吏部郎。\\n逢君立五马，应醉习家塘。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3856', 'poem', '唐', '李商隐', '和韩录事送宫人入道', '星使追还不自由，双童捧上绿琼輈，九枝灯下朝金殿，\\n三素云中侍玉楼。凤女颠狂成久别，月娥孀独好同游。\\n当时若爱韩公子，埋骨成灰恨未休。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3857', 'poem', '唐', '孟浩然', '洗然弟竹亭', '吾与二三子，平生结交深。\\n俱怀鸿鹄志，昔有鶺鴒心。\\n逸气假毫翰，清风在竹林。\\n达是酒中趣，琴上偶然音。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3858', 'poem', '唐', '刘长卿', '杂咏八首上礼部李侍郎·幽琴', '月色满轩白，琴声宜夜阑。\\n飗飗青丝上，静听松风寒。\\n古调虽自爱，今人多不弹。\\n向君投此曲，所贵知音难。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3859', 'poem', '唐', '温庭筠', '春日野行', '雨涨西塘金堤斜，碧草芊芊晴吐芽。\\n野岸明媚山芍药，水田叫噪官虾蟆。\\n镜中有浪动菱蔓，陌上无风飘柳花。\\n何事轻桡句溪客，绿萍方好不归家。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3860', 'poem', '唐', '李商隐', '富平少侯', '七国三边未到忧，十三身袭富平侯。\\n不收金弹抛林外，却惜银床在井头。\\n彩树转灯珠错落，绣檀回枕玉雕锼。\\n当关不报侵晨客，新得佳人字莫愁。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3861', 'poem', '唐', '李白', '湖边采莲妇', '小姑织白纻，未解将人语。\\n大嫂采芙蓉，溪湖千万重。\\n长兄行不在，莫使外人逢。\\n愿学秋胡妇，贞心比古松。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3862', 'poem', '唐', '陈标', '焦桐树', '江上烹鱼采野樵，鸾枝摧折半曾烧。\\n未经良匠材虽散，待得知音尾已焦。\\n若使琢磨徽白玉，便来风律轸青瑶。\\n还能万里传山水，三峡泉声岂寂寥。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3863', 'poem', '唐', '卢照邻', '登封大酺歌四首', '明君封禅日重光，天子垂衣历数长。\\n九州四海常无事，万岁千秋乐未央。\\n日观仙云随凤辇，天门瑞雪照龙衣。\\n繁弦绮席方终夜，妙舞清歌欢未归。\\n翠凤逶迤登介丘，仙鹤裴回天上游。\\n借问乾封何所乐，人皆寿命得千秋。\\n千年圣主应昌期，万国淳风王化基。\\n请比上古无为代，何如今日太平时。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3864', 'poem', '唐', '元稹', '六年春遣怀八首', '伤禽我是笼中鹤，沉剑君为泉下龙。\\n重纩犹存孤枕在，春衫无复旧裁缝。\\n检得旧书三四纸，高低阔狭粗成行。\\n自言并食寻高事，唯念山深驿路长。\\n公无渡河音响绝，已隔前春复去秋。\\n今日闲窗拂尘土，残弦犹迸钿箜篌。\\n婢仆晒君馀服用，娇痴稚女绕床行。\\n玉梳钿朵香胶解，尽日风吹玳瑁筝。\\n伴客销愁长日饮，偶然乘兴便醺醺。\\n怪来醒后傍人泣，醉里时时错问君。\\n我随楚泽波中梗，君作咸阳泉下泥。\\n百事无心值寒食，身将稚女帐前啼。\\n童稚痴狂撩乱走，绣球花仗满堂前。\\n病身一到繐帷下，还向临阶背日眠。\\n小于潘岳头先白，学取庄周泪莫多。\\n止竟悲君须自省，川流前后各风波。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3865', 'poem', '唐', '李白', '杂诗', '白日与明月，昼夜尚不闲。\\n况尔悠悠人，安得久世间。\\n传闻海水上，乃有蓬莱山。\\n玉树生绿叶，灵仙每登攀。\\n一食驻玄发，再食留红颜。\\n吾欲从此去，去之无时还。', '2018-03-09 15:40:57');
INSERT INTO `t_poem` VALUES ('3866', 'poem', '唐', '权德舆', '建除诗', '建节出王都，雄雄大丈夫。\\n除书加右职，骑吏拥前驱。\\n满月张繁弱，含霜耀鹿卢。\\n平明跃騕褭，清夜击珊瑚。\\n定远功那比，平津策乃迂。\\n执心思报国，效节在忘躯。\\n破胆销丹浦，颦蛾舞绿珠。\\n危冠徒自爱，长毂事应殊。\\n成绩封千室，畴劳使五符。\\n收功轻骠卫，致埋迈黄虞。\\n开济今如此，英威古不侔。\\n闭关草玄者，无乃误为儒。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3867', 'poem', '唐', '温庭筠', '晚归曲', '格格水禽飞带波，孤光斜起夕阳多。\\n湖西山浅似相笑，菱刺惹衣攒黛蛾。\\n青丝系船向江木，兰芽出土吴江曲。\\n水极晴摇泛滟红，草平春染烟绵绿。\\n玉鞭骑马杨叛儿，刻金作凤光参差。\\n丁丁暖漏滴花影，催入景阳人不知。\\n弯堤弱柳遥相瞩，雀扇团圆掩香玉。\\n莲塘艇子归不归，柳暗桑秾闻布谷。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3868', 'poem', '唐', '赵嘏', '赠别', '水边秋草暮萋萋，欲驻残阳恨马蹄。\\n曾是管弦同醉伴，一声歌尽各东西。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3869', 'poem', '唐', '司空曙', '云阳寺石竹花', '一自幽山别，相逢此寺中。\\n高低俱出叶，深浅不分丛。\\n野蝶难争白，庭榴暗让红。\\n谁怜芳最久，春露到秋风。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3870', 'poem', '唐', '陆龟蒙', '回文', '静烟临碧树，残雪背晴楼。\\n冷天侵极戍，寒月对行舟。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3871', 'poem', '唐', '李隆基', '温汤对雪', '北风吹同云，同云飞白雪。\\n白雪乍回散，同云何惨烈。\\n未见温泉冰，宁知火井灭。\\n表瑞良在兹，庶几可怡悦。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3872', 'poem', '唐', '杜甫', '立春', '春日春盘细生菜，忽忆两京梅发时。\\n盘出高门行白玉，菜传纤手送青丝。\\n巫峡寒江那对眼，杜陵远客不胜悲。\\n此身未知归定处，呼儿觅纸一题诗。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3873', 'poem', '唐', '高衢', '和三乡诗', '南北千山与万山，轩车谁不思乡关。\\n独留芳翰悲前迹，陌上恐伤桃李颜。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3874', 'poem', '唐', '张为', '渔阳将军', '霜髭拥颔对穷秋，著白貂裘独上楼。\\n向北望星提剑立，一生长为国家忧。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3875', 'poem', '唐', '司空图', '诗品二十四则·清奇', '娟娟群松，下有漪流。\\n晴雪满汀，隔溪渔舟。\\n可人如玉，步屟寻幽。\\n载行载止，空碧悠悠。\\n神出古异，淡不可收。\\n如月之曙，如气之秋。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3876', 'poem', '唐', '周昙', '晋门·王夷甫', '六合谁为辅弼臣，八风昏处尽胡尘。\\n是知济弱扶倾术，不属高谈虚论人。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3877', 'poem', '唐', '虞世南', '春夜', '春苑月裴回，竹堂侵夜开。\\n惊鸟排林度，风花隔水来。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3878', 'poem', '唐', '齐己', '不睡', '永夜不欲睡，虚堂闭复开。\\n却离灯影去，待得月光来。\\n落叶逢巢住，飞萤值我回。\\n天明拂经案，一炷白檀灰。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3879', 'poem', '唐', '岑参', '胡歌', '黑姓蕃王貂鼠裘，葡萄宫锦醉缠头。\\n关西老将能苦战，七十行兵仍未休。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3880', 'poem', '唐', '王勃', '秋日别王长史', '别路馀千里，深恩重百年。\\n正悲西候日，更动北梁篇。\\n野色笼寒雾，山光敛暮烟。\\n终知难再奉，怀德自潸然。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3881', 'poem', '唐', '薛能', '桃花', '香色自天种，千年岂易逢。\\n开齐全未落，繁极欲相重。\\n冷湿朝如淡，晴干午更浓。\\n风光新社燕，时节旧春农。\\n篱落欹临竹，亭台盛间松。\\n乱缘堪羡蚁，深入不如蜂。\\n有影宜暄煦，无言自冶容。\\n洞连非俗世，溪静接仙踪。\\n子熟河应变，根盘土已封。\\n西王潜爱惜，东朔盗过从。\\n醉席眠英好，题诗恋景慵。\\n芳菲聊一望，何必在临邛。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3882', 'poem', '唐', '杨凝式', '题怀素酒狂帖后', '十年挥素学临池，始识王公学卫非。\\n草圣未须因酒发，笔端应解化龙飞。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3883', 'poem', '唐', '李商隐', '有感', '中路因循我所长，古来才命两相妨。\\n劝君莫强安蛇足，一盏芳醪不得尝。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3884', 'poem', '唐', '李嘉祐', '题道虔上人竹房', '诗思禅心共竹闲，任他流水向人间。\\n手持如意高窗里，斜日沿江千万山。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3885', 'poem', '唐', '罗隐', '王夷甫', '把得闲书坐水滨，读来前事亦酸辛。\\n莫言麈尾清谭柄，坏却淳风是此人。', '2018-03-09 15:41:02');
INSERT INTO `t_poem` VALUES ('3886', 'poem', '唐', '白居易', '送兄弟回雪夜', '日晦云气黄，东北风切切。\\n时从村南还，新与兄弟别。\\n离襟泪犹湿，回马嘶未歇。\\n欲归一室坐，天阴多无月。\\n夜长火消尽，岁暮雨凝结。\\n寂寞满炉灰，飘零上阶雪。\\n对雪画寒灰，残灯明复灭。\\n灰死如我心，雪白如我发。\\n所遇皆如此，顷刻堪愁绝。\\n回念入坐忘，转忧作禅悦。\\n平生洗心法，正为今宵设。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3887', 'poem', '唐', '苏拯', '医人', '古人医在心，心正药自真。\\n今人医在手，手滥药不神。\\n我愿天地炉，多衔扁鹊身。\\n遍行君臣药，先从冻馁均。\\n自然六合内，少闻贫病人。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3888', 'poem', '唐', '杜甫', '龙门阁', '清江下龙门，绝壁无尺土。\\n长风驾高浪，浩浩自太古。\\n危途中萦盘，仰望垂线缕。\\n滑石欹谁凿，浮梁袅相拄。\\n目眩陨杂花，头风吹过雨。\\n百年不敢料，一坠那得取。\\n饱闻经瞿塘，足见度大庾。\\n终身历艰险，恐惧从此数。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3889', 'poem', '唐', '贺知章', '唐禅社首乐章·顺和', '至哉含柔德，万物资以生。\\n常顺称厚载，流谦通变盈。\\n圣心事能察，增广陈厥诚。\\n黄祇僾如在，泰折俟咸亨。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3890', 'poem', '唐', '李白', '荆门浮舟望蜀江', '春水月峡来，浮舟望安极。\\n正是桃花流，依然锦江色。\\n江色绿且明，茫茫与天平。\\n逶迤巴山尽，摇曳楚云行。\\n雪照聚沙雁，花飞出谷莺。\\n芳洲却已转，碧树森森迎。\\n流目浦烟夕，扬帆海月生。\\n江陵识遥火，应到渚宫城。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3891', 'poem', '唐', '杨炯', '梅花落', '窗外一株梅，寒花五出开。\\n影随朝日远，香逐便风来。\\n泣对铜钩障，愁看玉镜台。\\n行人断消息，春恨几裴回。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3892', 'poem', '唐', '祖咏', '泊扬子津', '才入维扬郡，乡关此路遥。\\n林藏初过雨，风退欲归潮。\\n江火明沙岸，云帆碍浦桥。\\n客衣今日薄，寒气近来饶。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3893', 'poem', '唐', '白居易', '对琴待月', '竹院新晴夜，松窗未卧时。\\n共琴为老伴，与月有秋期。\\n玉轸临风久，金波出雾迟。\\n幽音待清景，唯是我心知。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3894', 'poem', '唐', '齐己', '惊秋', '晓窗惊觉向秋风，万里心凝淡荡中。\\n池影碎翻红菡萏，井声干落绿梧桐。\\n破除闲事浑归道，销耗劳生旋逐空。\\n妖杀九原狐兔意，岂知丘陇是英雄。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3895', 'poem', '唐', '刘兼', '江楼望乡寄内', '独上江楼望故乡，泪襟霜笛共凄凉。\\n云生陇首秋虽早，月在天心夜已长。\\n魂梦只能随蛱蝶，烟波无计学鸳鸯。\\n蜀笺都有三千幅，总写离情寄孟光。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3896', 'poem', '唐', '李赤', '姑熟杂咏·丹阳湖', '湖与元气通，风波浩难止。\\n天外贾客归，云间片帆起。\\n龟游莲叶上，鸟宿芦花里。\\n少女棹舟归，歌声逐流水。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3897', 'poem', '唐', '杜甫', '寄贺兰铦', '朝野欢娱后，乾坤震荡中。\\n相随万里日，总作白头翁。\\n岁晚仍分袂，江边更转蓬。\\n勿云俱异域，饮啄几回同。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3898', 'poem', '唐', '李峤', '雪', '瑞雪惊千里，同云暗九霄。\\n地疑明月夜，山似白云朝。\\n逐舞花光动，临歌扇影飘。\\n大周天阙路，今日海神朝。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3899', 'poem', '唐', '卢照邻', '送郑司仓入蜀', '离人丹水北，游客锦城东。\\n别意还无已，离忧自不穷。\\n陇云朝结阵，江月夜临空。\\n关塞疲征马，霜氛落早鸿。\\n潘年三十外，蜀道五千中。\\n送君秋水曲，酌酒对清风。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3900', 'poem', '唐', '刘禹锡', '和仆射牛相公寓言二首', '两度竿头立定夸，回眸举袖拂青霞。\\n尽抛今日贵人样，复振前朝名相家。\\n御史定来休直宿，尚书依旧趁参衙。\\n具瞻尊重诚无敌，犹忆洛阳千树花。\\n心如止水鉴常明，见尽人间万物情。\\n雕鹗腾空犹逞俊，骅骝啮足自无惊。\\n时来未觉权为祟，贵了方知退是荣。\\n只恐重重世缘在，事须三度副苍生。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3901', 'poem', '唐', '李白', '拟古其八', '月色不可扫，客愁不可道。\\n玉露生秋衣，流萤飞百草。\\n日月终销毁，天地同枯槁。\\n蟪蛄啼青松，安见此树老。\\n金丹宁误俗，昧者难精讨。\\n尔非千岁翁，多恨去世早。\\n饮酒入玉壶，藏身以为宝。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3902', 'poem', '唐', '李白', '送鲁郡刘长史迁弘农长史', '鲁国一杯水，难容横海鳞。\\n仲尼且不敬，况乃寻常人。\\n白玉换斗粟，黄金买尺薪。\\n闭门木叶下，始觉秋非春。\\n闻君向西迁，地即鼎湖邻。\\n宝镜匣苍藓，丹经埋素尘。\\n轩后上天时，攀龙遗小臣。\\n及此留惠爱，庶几风化淳。\\n鲁缟如白烟，五缣不成束。\\n临行赠贫交，一尺重山岳。\\n相国齐晏子，赠行不及言。\\n托阴当树李，忘忧当树萱。\\n他日见张禄，绨袍怀旧恩。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3903', 'poem', '唐', '耿湋', '凉州词', '国使翻翻随旆旌，陇西岐路足荒城。\\n毡裘牧马胡雏小，日暮蕃歌三两声。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3904', 'poem', '唐', '孟郊', '游子', '萱草生堂阶，游子行天涯。\\n慈亲倚堂门，不见萱草花。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3905', 'poem', '唐', '于邺', '斜谷道', '乱峰连叠嶂，千里绿峨峨。\\n蜀国路如此，游人车亦过。\\n远烟当驿敛，骤雨逐风多。\\n独忆紫芝叟，临风歌旧歌。', '2018-03-09 15:41:07');
INSERT INTO `t_poem` VALUES ('3906', 'poem', '唐', '唐彦谦', '松', '托根蟠泰华，倚干蚀莓苔。\\n谁云山泽间，而无梁栋材。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3907', 'poem', '唐', '杨巨源', '关山月', '苍茫临故关，迢递照秋山。\\n万里平芜静，孤城落叶闲。\\n露浓栖雁起，天远戍兵还。\\n复映征西府，光深组练间。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3908', 'poem', '唐', '钱起', '题崔逸人山亭', '药径深红藓，山窗满翠微。\\n羡君花下酒，蝴蝶梦中飞。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3909', 'poem', '唐', '李商隐', '听鼓', '城头叠鼓声，城下暮江清。\\n欲问渔阳掺，时无祢正平。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3910', 'poem', '唐', '宋之问', '和赵员外桂阳桥遇佳人', '江雨朝飞浥细尘，阳桥花柳不胜春。\\n金鞍白马来从赵，玉面红妆本姓秦。\\n妒女犹怜镜中发，侍儿堪感路傍人。\\n荡舟为乐非吾事，自叹空闺梦寐频。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3911', 'poem', '唐', '裴澄', '春云', '漠漠复溶溶，乘春任所从。\\n映林初展叶，触石未成峰。\\n旭日消寒翠，晴烟点净容。\\n霏微将似灭，深浅又如重。\\n薄彩临溪散，轻阴带雨浓。\\n空馀负樵者，岭上自相逢。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3912', 'poem', '唐', '李商隐', '初食笋呈座中', '嫩箨香苞初出林，於陵论价重如金。\\n皇都陆海应无数，忍剪凌云一寸心。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3913', 'poem', '唐', '李商隐', '嘲樱桃', '朱实鸟含尽，青楼人未归。\\n南园无限树，独自叶如帏。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3914', 'poem', '唐', '张琰', '官柳', '不见桥东黄布帘，树犹如此我何堪。\\n袅袅亭亭忒无赖，又将春色误江南。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3915', 'poem', '唐', '齐己', '春兴', '柳暖莺多语，花明草尽长。\\n风流在诗句，牵率绕池塘。\\n叫切禽名宇，飞忙蝶姓庄。\\n时来真可惜，自勉掇兰芳。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3916', 'poem', '唐', '徐夤', '灯花', '点蜡烧银却胜栽，九华红艳吐玫瑰。\\n独含冬夜寒光拆，不傍春风暖处开。\\n难见只因能送喜，莫挑唯恐堕成灰。\\n贪膏附热多相误，为报飞蛾罢拂来。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3917', 'poem', '唐', '白居易', '听夜筝有感', '江州去日听筝夜，白发新生不愿闻。\\n如今格是头成雪，弹到天明亦任君。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3918', 'poem', '唐', '杜牧', '留诲曹师等诗', '万物有丑好，各一姿状分。\\n唯人即不尔，学与不学论。\\n学非探其花，要自拨其根。\\n孝友与诚实，而不忘尔言。\\n根本既深实，柯叶自滋繁。\\n念尔无忽此，期以庆吾门。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3919', 'poem', '唐', '刘驾', '牧童', '牧童见客拜，山果怀中落。\\n昼日驱牛归，前溪风雨恶。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3920', 'poem', '唐', '张泌', '碧户', '碧户扃鱼锁，兰窗掩镜台。\\n落花疑怅望，归燕自裴回。\\n咏絮知难敌，伤春不易裁。\\n恨从芳草起，愁为晚风来。\\n衣惹湘云薄，眉分楚岫开。\\n香浓眠旧枕，梦好醉春杯。\\n小障明金凤，幽屏点翠苔。\\n宝筝横塞雁，怨笛落江梅。\\n卓氏仍多酒，相如正富才。\\n莫教琴上意，翻作鹤声哀。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3921', 'poem', '唐', '白居易', '东楼招客夜饮', '莫辞数数醉东楼，除醉无因破得愁。\\n唯有绿樽红烛下，暂时不似在忠州。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3922', 'poem', '唐', '于武陵', '斜谷道', '乱峰连叠嶂，千里绿峨峨。\\n蜀国路如此，游人车亦过。\\n远烟当叶敛，骤雨逐风多。\\n独忆紫芝叟，临风歌旧歌。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3923', 'poem', '唐', '杨汝士', '句', '昔日兰亭无艳质，此时金谷有高人。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3924', 'poem', '唐', '李白', '塞上曲', '大汉无中策，匈奴犯渭桥。\\n五原秋草绿，胡马一何骄。\\n命将征西极，横行阴山侧。\\n燕支落汉家，妇女无华色。\\n转战渡黄河，休兵乐事多。\\n萧条清万里，瀚海寂无波。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3925', 'poem', '唐', '高适', '别崔少府', '知君少得意，汶上掩柴扉。\\n寒食仍留火，春风未授衣。\\n皆言黄绶屈，早向青云飞。\\n借问他乡事，今年归不归。', '2018-03-09 15:41:17');
INSERT INTO `t_poem` VALUES ('3926', 'poem', '唐', '杜甫', '赤甲', '卜居赤甲迁居新，两见巫山楚水春。\\n炙背可以献天子，美芹由来知野人。\\n荆州郑薛寄书近，蜀客郗岑非我邻。\\n笑接郎中评事饮，病从深酌道吾真。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3927', 'poem', '唐', '白居易', '晚题东林寺双池', '向晚双池好，初晴百物新。\\n袅枝翻翠羽，溅水跃红鳞。\\n萍泛同游子，莲开当丽人。\\n临流一惆怅，还忆曲江春。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3928', 'poem', '唐', '王维', '叹白发', '我年一何长，鬓发日已白。\\n俯仰天地间，能为几时客。\\n惆怅故山云，徘徊空日夕。\\n何事与时人，东城复南陌。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3929', 'poem', '唐', '司空图', '诗品二十四则·豪放', '观花匪禁，吞吐大荒。\\n由道返气，处得以狂。\\n天风浪浪，海山苍苍。\\n真力弥满，万象在旁。\\n前招三辰，后引凤凰。\\n晓策六鳌，濯足扶桑。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3930', 'poem', '唐', '鱼玄机', '寄子安', '醉别千卮不浣愁，离肠百结解无由。\\n蕙兰销歇归春圃，杨柳东西绊客舟。\\n聚散已悲云不定，恩情须学水长流。\\n有花时节知难遇，未肯厌厌醉玉楼。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3931', 'poem', '唐', '可朋', '桐花鸟', '五色毛衣比凤雏，深花丛里只如无。\\n美人买得偏怜惜，移向金钗重几铢。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3932', 'poem', '唐', '李白', '暖酒', '热暖将来镔铁文。\\n暂时不动聚白云。\\n拨却白云见青天。\\n掇头里许便成仙。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3933', 'poem', '唐', '章碣', '东都望幸', '懒修珠翠上高台，眉月连娟恨不开。\\n纵使东巡也无益，君王自领美人来。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3934', 'poem', '唐', '皇甫曾', '山下泉', '漾漾带山光，澄澄倒林影。\\n那知石上喧，却忆山中静。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3935', 'poem', '唐', '李白', '鲁中都东楼醉起作', '昨日东楼醉，还应倒接蓠。\\n阿谁扶上马，不省下楼时。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3936', 'poem', '唐', '皎然', '答李季兰', '天女来相试，将花欲染衣。\\n禅心竟不起，还捧旧花归。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3937', 'poem', '唐', '成彦雄', '村行', '暧暧村烟暮，牧童出深坞。\\n骑牛不顾人，吹笛寻山去。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3938', 'poem', '唐', '白居易', '初罢中书舍人', '自惭拙宦叨清贵，还有痴心怕素餐。\\n或望君臣相献替，可图妻子免饥寒。\\n性疏岂合承恩久，命薄元知济事难。\\n分寸宠光酬未得，不休更拟觅何官。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3939', 'poem', '唐', '司空曙', '雪二首', '乐游春苑望鹅毛，宫殿如星树似毫。\\n漫漫一川横渭水，太阳初出五陵高。\\n王屋南崖见洛城，石龛松寺上方平。\\n半山槲叶当窗下，一夜曾闻雪打声。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3940', 'poem', '唐', '韦皋', '忆玉箫', '黄雀衔来已数春，别时留解赠佳人。\\n长江不见鱼书至，为遣相思梦入秦。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3941', 'poem', '唐', '无名氏', '春二首', '褭褭东风吹水国，金鸦影暖南山北。\\n蒲抽小剑割湘波，柳拂长眉舞春色。\\n白铜堤下烟苍苍，林端细蕊参差香。\\n绿桑枝下见桃叶，回看青云空断肠。\\n乌足迟迟日宫里，天门击鼓龙蛇起。\\n风师剪翠换枯条，青帝挼蓝染江水。\\n蜂蝶缤纷抱香蕊，锦鳞跳掷红云尾。\\n绣衣白马不归来，双成倚槛春心醉。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3942', 'poem', '唐', '左偃', '江上晚泊', '寒云淡淡天无际，片帆落处沙鸥起。\\n水阔风高日复斜，扁舟独宿芦花里。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3943', 'poem', '唐', '白居易', '三月三十日题慈恩寺', '慈恩春色今朝尽，尽日裴回倚寺门。\\n惆怅春归留不得，紫藤花下渐黄昏。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3944', 'poem', '唐', '杜甫', '蕃剑', '致此自僻远，又非珠玉装。\\n如何有奇怪，每夜吐光芒。\\n虎气必腾踔，龙身宁久藏。\\n风尘苦未息，持汝奉明王。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3945', 'poem', '唐', '崔道融', '梅', '溪上寒梅初满枝，夜来霜月透芳菲。\\n清光寂寞思无尽，应待琴尊与解围。', '2018-03-09 15:41:24');
INSERT INTO `t_poem` VALUES ('3946', 'poem', '唐', '周昙', '晋门·怀帝', '蕃汉戈矛遍九垓，两京簪绂走黄埃。\\n刘聪大会平阳日，遣帝行觞事可哀。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3947', 'poem', '唐', '白居易', '老去', '老去愧妻儿，冬来有劝词。\\n暖寒从饮酒，冲冷少吟诗。\\n战胜心还壮，斋勤体校羸。\\n由来世间法，损益合相随。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3948', 'poem', '唐', '温庭筠', '汉皇迎春词', '春草芊芊晴扫烟，宫城大锦红殷鲜。\\n海日初融照仙掌，淮王小队缨铃响。\\n猎猎东风焰赤旗，画神金甲葱龙网。\\n钜公步辇迎句芒，复道扫尘燕彗长。\\n豹尾竿前赵飞燕，柳风吹尽眉间黄。\\n碧草含情杏花喜，上林莺啭游丝起。\\n宝马摇环万骑归，恩光暗入帘栊里。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3949', 'poem', '唐', '李贺', '杂曲歌辞·浩歌', '南风吹山作平地，帝遣天吴移海水。\\n王母桃花千遍红，彭祖巫咸几回死。\\n青毛骢马参差钱，娇春杨柳含细烟。\\n筝人劝我金屈卮，神血未凝身问谁。\\n不须浪饮丁都护，世上英雄本无主。\\n买丝绣作平原君，有酒惟浇赵州土。\\n漏催水咽玉蟾蜍，卫娘发薄不胜梳。\\n看见秋眉换深绿，二十男儿那刺促。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3950', 'poem', '唐', '钱起', '酬王维春夜竹亭赠别', '山月随客来，主人兴不浅。今宵竹林下，谁觉花源远。\\n惆怅曙莺啼，孤云还绝巘，', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3951', 'poem', '唐', '武则天', '唐享昊天乐·第二', '瞻紫极，望玄穹。\\n翘至恳，罄深衷。\\n听虽远，诚必通。\\n垂厚泽，降云宫。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3952', 'poem', '唐', '白居易', '野行', '草润衫襟重，沙干屐齿轻。\\n仰头听鸟立，信脚望花行。\\n暇日无公事，衰年有道情。\\n浮生短于梦，梦里莫营营。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3953', 'poem', '唐', '李白', '感遇其四', '宋玉事楚王。\\n立身本高洁。\\n巫山赋彩云。\\n郢路歌白雪。\\n举国莫能和。\\n巴人皆卷舌。\\n一感登徒言。\\n恩情遂中绝。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3954', 'poem', '唐', '白居易', '残暑招客', '云截山腰断，风驱雨脚回。\\n早阴江上散，残热日中来。\\n却取生衣著，重拈竹簟开。\\n谁能淘晚热，闲饮两三杯。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3955', 'poem', '唐', '李白', '同友人舟行游台越作', '楚臣伤江枫，谢客拾海月。\\n怀沙去潇湘，挂席泛溟渤。\\n蹇予访前迹，独往造穷发。\\n古人不可攀，去若浮云没。\\n愿言弄倒景，从此炼真骨。\\n华顶窥绝溟，蓬壶望超忽。\\n不知青春度，但怪绿芳歇。\\n空持钓鳌心，从此谢魏阙。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3956', 'poem', '唐', '王昌龄', '宿裴氏山庄', '苍苍竹林暮，吾亦知所投。\\n静坐山斋月，清溪闻远流。\\n西峰下微雨，向晓白云收。\\n遂解尘中组，终南春可游。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3957', 'poem', '唐', '骆宾王', '晚泊江镇', '四运移阴律，三翼泛阳侯。\\n荷香销晚夏，菊气入新秋。\\n夜乌喧粉堞，宿雁下芦洲。\\n海雾笼边徼，江风绕戍楼。\\n转蓬惊别渚，徙橘怆离忧。\\n魂飞灞陵岸，泪尽洞庭流。\\n振影希鸿陆，逃名谢蚁丘。\\n还嗟帝乡远，空望白云浮。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3958', 'poem', '唐', '李白', '赠宣城赵太守悦', '赵得宝符盛，山河功业存。三千堂上客，出入拥平原。\\n六国扬清风，英声何喧喧。大贤茂远业，虎竹光南藩。\\n错落千丈松，虬龙盘古根。枝下无俗草，所植唯兰荪。\\n忆在南阳时，始承国士恩。公为柱下史，脱绣归田园。\\n伊昔簪白笔，幽都逐游魂。持斧冠三军，霜清天北门。\\n差池宰两邑，鹗立重飞翻。焚香入兰台，起草多芳言。\\n夔龙一顾重，矫翼凌翔鹓，赤县扬雷声，强项闻至尊。\\n惊飙颓秀木，迹屈道弥敦。出牧历三郡，所居猛兽奔。\\n迁人同卫鹤，谬上懿公轩。自笑东郭履，侧惭狐白温。\\n闲吟步竹石，精义忘朝昏。憔悴成丑士，风云何足论。\\n猕猴骑土牛，羸马夹双辕。愿借羲皇景，为人照覆盆。\\n溟海不振荡，何由纵鹏鲲。所期玄津白，倜傥假腾鶱，', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3959', 'poem', '唐', '李贺', '秦宫诗', '越罗衫袂迎春风，玉刻麒麟腰带红。\\n楼头曲宴仙人语，帐底吹笙香雾浓。\\n人间酒暖春茫茫，花枝入帘白日长。\\n飞窗复道传筹饮，十夜铜盘腻烛黄。\\n秃衿小袖调鹦鹉，紫绣麻鞋踏哮虎。\\n斫桂烧金待晓筵，白鹿青苏夜半煮。\\n桐英永巷骑新马，内屋深屏生色画。\\n开门烂用水衡钱，卷起黄河向身泻。\\n皇天厄运犹曾裂，秦宫一生花底活。\\n鸾篦夺得不还人，醉睡氍毹满堂月。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3960', 'poem', '唐', '杜甫', '移居公安敬赠卫大郎钧', '卫侯不易得，余病汝知之。\\n雅量涵高远，清襟照等夷。\\n平生感意气，少小爱文辞。\\n河海由来合，风云若有期。\\n形容劳宇宙，质朴谢轩墀。\\n自古幽人泣，流年壮士悲。\\n水烟通径草，秋露接园葵。\\n入邑豺狼斗，伤弓鸟雀饥。\\n白头供宴语，乌几伴栖迟。\\n交态遭轻薄，今朝豁所思。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3961', 'poem', '唐', '佚名', '沈询', '莫打南来雁，从他向北飞。\\n打时双打取，莫遣两分离。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3962', 'poem', '唐', '孟郊', '旅行', '楚水结冰薄，楚云为雨微。\\n野梅参差发，旅榜逍遥归。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3963', 'poem', '唐', '杜牧', '江上逢友人', '故国归人酒一杯，暂停兰棹共裴回。\\n村连三峡暮云起，潮送九江寒雨来。\\n已作相如投赋计，还凭殷浩寄书回。\\n到时若见东篱菊，为问经霜几度开。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3964', 'poem', '唐', '张祜', '江南逢故人', '河洛多尘事，江山半旧游。\\n春风故人夜，又醉白蘋洲。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3965', 'poem', '唐', '韦应物', '咏琉璃', '有色同寒冰，无物隔纤玉。\\n象筵看不见，堪将对玉人。', '2018-03-09 15:41:37');
INSERT INTO `t_poem` VALUES ('3966', 'poem', '唐', '白居易', '过天门街', '雪尽终南又欲春，遥怜翠色对红尘。\\n千车万马九衢上，回首看山无一人。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3967', 'poem', '唐', '孙鲂', '杨柳枝词五首', '灵和风暖太昌春，舞线摇丝向昔人。\\n何似晓来江雨后，一行如画隔遥津。\\n彭泽初栽五树时，只应闲看一枝垂。\\n不知天意风流处，要与佳人学画眉。\\n暖傍离宁静拂桥，入流穿槛绿摇摇。\\n不知落日谁相送，魂断千条与万条。\\n春来绿树遍天涯，未见垂杨未可夸。\\n晴日万株烟一阵，闲坊兼是莫愁家。\\n十首当年有旧词，唱青歌翠几无遗。\\n未曾得向行人道，不为离情莫折伊。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3968', 'poem', '唐', '司空图', '灯花三首', '蜀柳丝丝幂画楼，窗尘满镜不梳头。\\n几时金雁传归信，剪断香魂一缕愁。\\n姊姊教人且抱儿，逐他女伴卸头迟。\\n明朝斗草多应喜，翦得灯花自扫眉。\\n闰前小雪过经旬，犹自依依向主人。\\n开尽菊花怜强舞，与教弟子待新春。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3969', 'poem', '唐', '黄滔', '河梁', '五原人走马，昨夜到京师。\\n绣户新夫妇，河梁生别离。\\n陇花开不艳，羌笛静犹悲。\\n惆怅良哉辅，锵锵立凤池。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3970', 'poem', '唐', '翁宏', '秋残', '又是秋残也，无聊意若何。\\n客程江外远，归思夜深多。\\n岘首飞黄叶，湘湄走白波。\\n仍闻汉都护，今岁合休戈。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3971', 'poem', '唐', '温庭筠', '偶游', '曲巷斜临一水间，小门终日不开关。\\n红珠斗帐樱桃熟，金尾屏风孔雀闲。\\n云髻几迷芳草蝶，额黄无限夕阳山。\\n与君便是鸳鸯侣，休向人间觅往还。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3972', 'poem', '唐', '胡曾', '咏史诗·河梁', '汉家英杰出皇都，携手河梁话入胡。\\n不是子卿全大节，也应低首拜单于。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3973', 'poem', '唐', '杜牧', '屏风绝句', '屏风周昉画纤腰，岁久丹青色半销。\\n斜倚玉窗鸾发女，拂尘犹自妒娇娆。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3974', 'poem', '唐', '张旭', '清溪泛舟', '旅人倚征棹，薄暮起劳歌。\\n笑揽清溪月，清辉不厌多。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3975', 'poem', '唐', '崔涂', '送友人', '登高迎送远，春恨并依依。\\n不得沧洲信，空看白鹤归。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3976', 'poem', '唐', '郑谷', '黯然', '搢绅奔避复沦亡，消息春来到水乡。\\n屈指故人能几许，月明花好更悲凉。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3977', 'poem', '唐', '王维', '辋川集·茱萸沜', '结实红且绿，复如花更开。\\n山中傥留客，置此芙蓉杯。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3978', 'poem', '唐', '王维', '宿郑州', '朝与周人辞，暮投郑人宿。\\n他乡绝俦侣，孤客亲僮仆。\\n宛洛望不见，秋霖晦平陆。\\n田父草际归，村童雨中牧。\\n主人东皋上，时稼绕茅屋。\\n虫思机杼悲，雀喧禾黍熟。\\n明当渡京水，昨晚犹金谷。\\n此去欲何言，穷边徇微禄。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3979', 'poem', '唐', '李白', '邯郸南亭观妓', '歌鼓燕赵儿，魏姝弄鸣丝。\\n粉色艳日彩，舞袖拂花枝。\\n把酒顾美人，请歌邯郸词。\\n清筝何缭绕，度曲绿云垂。\\n平原君安在，科斗生古池。\\n座客三千人，于今知有谁。\\n我辈不作乐，但为后代悲。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3980', 'poem', '唐', '白居易', '狂歌词', '明月照君席，白露沾我衣。\\n劝君酒杯满，听我狂歌词。\\n五十已后衰，二十已前痴。\\n昼夜又分半，其间几何时。\\n生前不欢乐，死后有馀赀。\\n焉用黄墟下，珠衾玉匣为。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3981', 'poem', '唐', '司空图', '二十四诗品', '涓涓群松，下有漪流。晴雪满竹，隔溪鱼舟。可人如玉，步渫寻幽，载瞻载止，空碧悠悠。神出古异，淡不可收。如月之曙，如气之秋。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3982', 'poem', '唐', '刘禹锡', '百花行', '长安百花时，风景宜轻薄。\\n无人不沽酒，何处不闻乐。\\n春风连夜动，微雨凌晓濯。\\n红焰出墙头，雪光映楼角。\\n繁紫韵松竹，远黄绕篱落。\\n临路不胜愁，轻烟去何托。\\n满庭荡魂魄，照庑成丹渥。\\n烂熳簇颠狂，飘零劝行乐。\\n时节易晼晚，清阴覆池阁。\\n唯有安石榴，当轩慰寂寞。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3983', 'poem', '唐', '韦应物', '西亭', '亭宇丽朝景，帘牖散暄风。\\n小山初构石，珍树正然红。\\n弱藤已扶树，幽兰欲成丛。\\n芳心幸如此，佳人时不同。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3984', 'poem', '唐', '李白', '赠江油尉', '岚光深院里，傍砌水泠泠。野燕巢官舍。溪云入古厅。日斜孤吏过，帘卷乱峰青。五色神仙尉，焚香读道经。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3985', 'poem', '唐', '秦韬玉', '对花', '长与韶光暗有期，可怜蜂蝶却先知。\\n谁家促席临低树，何处横钗戴小枝。\\n丽日多情疑曲照，和风得路合偏吹。\\n向人虽道浑无语，笑劝王孙到醉时。', '2018-03-09 15:41:43');
INSERT INTO `t_poem` VALUES ('3986', 'poem', '唐', '贺知章', '奉和圣制送张说巡边', '荒憬尽怀忠，梯航已自通。\\n九攻虽不战，五月尚持戎。\\n遣戍征周牒，恢边重汉功。\\n选车命元宰，授律取文雄。\\n胄出天弧上，谋成帝幄中。\\n诏旂分夏物，专土锡唐弓。\\n帐宿伊川右，钲传晋苑东。\\n饔人藉蕡实，乐正理丝桐。\\n岐陌涵馀雨，离川照晚虹。\\n恭闻咏方叔，千载舞皇风。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3987', 'poem', '唐', '王勃', '登城春望', '物外山川近，晴初景霭新。\\n芳郊花柳遍，何处不宜春。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3988', 'poem', '唐', '张说', '恩制赐食于丽正殿书院宴赋得林字', '东壁图书府，西园翰墨林。\\n诵诗闻国政，讲易见天心。\\n位窃和羹重，恩叨醉酒深。\\n缓歌春兴曲，情竭为知音。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3989', 'poem', '唐', '张文规', '湖州贡焙新茶', '凤辇寻春半醉回，仙娥进水御帘开。\\n牡丹花笑金钿动，传奏吴兴紫笋来。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3990', 'poem', '唐', '薛能', '题逃户', '几界瞢农桑，凶年竟失乡。\\n朽关生湿菌，倾屋照斜阳。\\n雨水淹残臼，葵花压倒墙。\\n明时岂致此，应自负苍苍。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3991', 'poem', '唐', '白居易', '建昌江', '建昌江水县门前，立马教人唤渡船。\\n忽似往年归蔡渡，草风沙雨渭河边。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3992', 'poem', '唐', '岑参', '寄韩樽', '夫子素多疾，别来未得书。\\n北庭苦寒地，体内今何如。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3993', 'poem', '唐', '戴叔伦', '雨', '历历愁心乱，迢迢独夜长。\\n春帆江上雨，晓镜鬓边霜。\\n啼鸟云山静，落花溪水香。\\n家人亦念我，与汝黯相忘。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3994', 'poem', '唐', '陈子昂', '送殷大入蜀', '禺山金碧路，此地饶英灵。\\n送君一为别，凄断故乡情。\\n片云生极浦，斜日隐离亭。\\n坐看征骑没，惟见远山青。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3995', 'poem', '唐', '元稹', '桃花', '桃花浅深处，似匀深浅妆。\\n春风助肠断，吹落白衣裳。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3996', 'poem', '唐', '刘沧', '长洲怀古', '野烧原空尽荻灰，吴王此地有楼台。\\n千年事往人何在，半夜月明潮自来。\\n白鸟影从江树没，清猿声入楚云哀。\\n停车日晚荐蘋藻，风静寒塘花正开。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3997', 'poem', '唐', '张乔', '咏棋子赠弈僧', '黑白谁能用入玄，千回生死体方圆。\\n空门说得恒沙劫，应笑终年为一先。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3998', 'poem', '唐', '张籍', '山禽', '山禽毛如白练带，栖我庭前栗树枝。\\n猕猴半夜来取栗，一双中林向月飞。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('3999', 'poem', '唐', '李白', '古风其十二', '松柏本孤直。\\n难为桃李颜。\\n昭昭严子陵。\\n垂钓沧波间。\\n身将客星隐。\\n心与浮云闲。\\n长揖万乘君。\\n还归富春山。\\n清风洒六合。\\n邈然不可攀。\\n使我长叹息。\\n冥栖岩石间。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('4000', 'poem', '唐', '西施', '西施诗', '高花岩外晓相鲜，幽鸟雨中啼不歇。\\n红云飞过大江西，从此人间怨风月。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('4001', 'poem', '唐', '王昌龄', '巴陵送李十二', '摇曳巴陵洲渚分，清江传语便风闻。\\n山长不见秋城色，日暮蒹葭空水云。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('4002', 'poem', '唐', '郑谷', '赠别', '南游曾共游，相别倍相留。\\n行色回灯晓，离声满竹秋。\\n稳眠彭蠡浪，好醉岳阳楼。\\n明日逢佳景，为君成白头。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('4003', 'poem', '唐', '刘禹锡', '重答柳柳州', '弱冠同怀长者忧，临岐回想尽悠悠。\\n耦耕若便遗身老，黄发相看万事休。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('4004', 'poem', '唐', '刘长卿', '馀干旅舍', '摇落暮天迥，青枫霜叶稀。\\n孤城向水闭，独鸟背人飞。\\n渡口月初上，邻家渔未归。\\n乡心正欲绝，何处捣寒衣。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('4005', 'poem', '唐', '李群玉', '莲叶', '根是泥中玉，心承露下珠。\\n在君塘下种，埋没任春浦。', '2018-03-09 15:41:50');
INSERT INTO `t_poem` VALUES ('4006', 'poem', '唐', '裴度', '夏日对雨', '登楼逃盛夏，万象正埃尘。\\n对面雷嗔树，当街雨趁人。\\n檐疏蛛网重，地湿燕泥新。\\n吟罢清风起，荷香满四邻。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4007', 'poem', '唐', '韦应物', '闲居寄诸弟', '秋草生庭白露时，故园诸弟益相思。\\n尽日高斋无一事，芭蕉叶上独题诗。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4008', 'poem', '唐', '李世民', '仪鸾殿早秋', '寒惊蓟门叶，秋发小山枝。\\n松阴背日转，竹影避风移。\\n提壶菊花岸，高兴芙蓉池。\\n欲知凉气早，巢空燕不窥。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4009', 'poem', '唐', '陆龟蒙', '春思二首', '竹外麦烟愁漠漠，短翅啼禽飞魄魄。\\n此时忆著千里人，独坐支颐看花落。\\n江南酒熟清明天，高高绿旆当风悬。\\n谁家无事少年子，满面落花犹醉眠。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4010', 'poem', '唐', '白居易', '衰荷', '白露凋花花不残，凉风吹叶叶初乾。\\n无人解爱萧条境，更绕衰丛一匝看。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4011', 'poem', '唐', '李白', '观胡人吹笛', '胡人吹玉笛，一半是秦声。\\n十月吴山晓，梅花落敬亭。\\n愁闻出塞曲，泪满逐臣缨。\\n却望长安道，空怀恋主情。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4012', 'poem', '唐', '仲子陵', '秦镜', '万古秦时镜，从来抱至精。\\n依台月自吐，在匣水常清。\\n烂烂金光发，澄澄物象生。\\n云天皆洞鉴，表里尽虚明。\\n但见人窥胆，全胜响应声。\\n妍媸定可识，何处更逃情。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4013', 'poem', '唐', '白居易', '岁暮', '已任时命去，亦从岁月除。\\n中心一调伏，外累尽空虚。\\n名宦意已矣，林泉计何如。\\n拟近东林寺，溪边结一庐。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4014', 'poem', '唐', '孙鲂', '题金山寺', '万古波心寺，金山名目新。\\n天多剩得月，地少不生尘。\\n过橹妨僧定，惊涛溅佛身。\\n谁言张处士，题后更无人。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4015', 'poem', '唐', '章孝标', '长安秋夜', '田家无五行，水旱卜蛙声。\\n牛犊乘春放，儿童候暖耕。\\n池塘烟未起，桑柘雨初晴。\\n步晚香醪熟，村村自送迎。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4016', 'poem', '唐', '杜审言', '秋夜宴临津郑明府宅', '行止皆无地，招寻独有君。\\n酒中堪累月，身外即浮云。\\n露白宵钟彻，风清晓漏闻。\\n坐携馀兴往，还似未离群。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4017', 'poem', '唐', '戴叔伦', '过贾谊宅', '一谪长沙地，三年叹逐臣。上书忧汉室，作赋吊灵均。\\n旧宅秋荒草，西风客荐蘋，凄凉回首处，不见洛阳人。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4018', 'poem', '唐', '王维', '同崔员外秋宵寓直', '建礼高秋夜，承明候晓过。\\n九门寒漏彻，万井曙钟多。\\n月迥藏珠斗，云消出绛河。\\n更惭衰朽质，南陌共鸣珂。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4019', 'poem', '唐', '刘允济', '咏琴', '昔在龙门侧，谁想凤鸣时。\\n雕琢今为器，宫商不自持。\\n巴人缓疏节，楚客弄繁丝。\\n欲作高张引，翻成下调悲。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4020', 'poem', '唐', '郑损', '艺堂', '堂开冻石千年翠，艺讲秋胶百步威。\\n揖让未能忘典礼，英雄孰不惯戎衣。\\n风波险似金机骇，日月忙如雪羽飞。\\n莫怪尊前频浩叹，男儿志愿与时违。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4021', 'poem', '唐', '李白', '江夏送友人', '雪点翠云裘，送君黄鹤楼。\\n黄鹤振玉羽，西飞帝王州。\\n凤无琅玕实，何以赠远游。\\n裴回相顾影，泪下汉江流。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4022', 'poem', '唐', '李世民', '采芙蓉', '结伴戏方塘，携手上雕航。\\n船移分细浪，风散动浮香。\\n游莺无定曲，惊凫有乱行。\\n莲稀钏声断，水广棹歌长。\\n栖乌还密树，泛流归建章。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4023', 'poem', '唐', '李白', '读诸葛武侯传书怀赠长安崔少府叔封昆季', '汉道昔云季，群雄方战争。\\n霸图各未立，割据资豪英。\\n赤伏起颓运，卧龙得孔明。\\n当其南阳时，陇亩躬自耕。\\n鱼水三顾合，风云四海生。\\n武侯立岷蜀，壮志吞咸京。\\n何人先见许，但有崔州平。\\n余亦草间人，颇怀拯物情。\\n晚途值子玉，华发同衰荣。\\n托意在经济，结交为弟兄。\\n毋令管与鲍，千载独知名。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4024', 'poem', '唐', '鲍溶', '将归旧山留别孟郊', '择木无利刃，羡鱼无巧纶。\\n如何不量力，自取中路贫。\\n前者不厌耕，一日不离亲。\\n今来千里外，我心不在身。\\n悠悠慈母心，惟愿才如人。\\n蚕桑能几许，衣服常著新。\\n一饭吐尺丝，谁见此殷勤。\\n别君归耕去，持火烧车轮。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4025', 'poem', '唐', '杜甫', '病后遇王倚饮，赠歌', '麟角凤觜世莫识，煎胶续弦奇自见。尚看王生抱此怀，\\n在于甫也何由羡。且遇王生慰畴昔，素知贱子甘贫贱。\\n酷见冻馁不足耻，多病沈年苦无健。王生怪我颜色恶，\\n答云伏枕艰难遍，疟疠三秋孰可忍，寒热百日相交战。\\n头白眼暗坐有胝，肉黄皮皱命如线。惟生哀我未平复，\\n为我力致美肴膳。遣人向市赊香粳，唤妇出房亲自馔。\\n长安冬菹酸且绿，金城土酥静如练。兼求富豪且割鲜，\\n密沽斗酒谐终宴。故人情义晚谁似，令我手脚轻欲漩。\\n老马为驹信不虚，当时得意况深眷。但使残年饱吃饭，\\n只愿无事常相见。', '2018-03-09 15:42:02');
INSERT INTO `t_poem` VALUES ('4026', 'poem', '唐', '温庭筠', '海榴', '海榴开似火，先解报春风。\\n叶乱裁笺绿，花宜插鬓红。\\n蜡珠攒作蒂，缃彩剪成丛。\\n郑驿多归思，相期一笑同。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4027', 'poem', '唐', '李贺', '啁少年', '青骢马肥金鞍光，龙脑入缕罗衫香。\\n美人狭坐飞琼觞，贫人唤云天上郎。\\n别起高楼临碧筱，丝曳红鳞出深沼。\\n有时半醉百花前，背把金丸落飞鸟。\\n自说生来未为客，一身美妾过三百。\\n岂知劚地种苗家，官税频催勿人织。\\n长得积玉夸豪毅，每揖闲人多意气。\\n生来不读半行书，只把黄金买身贵。\\n少年安得长少年，海波尚变为桑田。\\n荣枯递传急如箭，天公不肯于公偏。\\n莫道韶华镇长在，发白面皱专相待。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4028', 'poem', '唐', '罗隐', '登夏州城楼', '寒城猎猎戍旗风，独倚危楼怅望中。\\n万里山河唐土地，千年魂魄晋英雄。\\n离心不忍听边马，往事应须问塞鸿。\\n好脱儒冠从校尉，一枝长戟六钧弓。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4029', 'poem', '唐', '雍裕之', '芦花', '夹岸复连沙，枝枝摇浪花。\\n月明浑似雪，无处认渔家。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4030', 'poem', '唐', '岑参', '叹白发', '白发生偏速，交人不奈何。\\n今朝两鬓上，更较数茎多。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4031', 'poem', '唐', '裴迪', '辋川集二十首·木兰柴', '苍苍落日时，鸟声乱溪水。\\n缘溪路转深，幽兴何时已。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4032', 'poem', '唐', '许浑', '长安早春怀江南', '云月有归处，故山清洛南。\\n如何一花发，春梦遍江潭。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4033', 'poem', '唐', '李商隐', '蝶', '孤蝶小徘徊，翩翾粉翅开。\\n并应伤皎洁，频近雪中来。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4034', 'poem', '唐', '刘禹锡', '荆门道怀古', '南国山川旧帝畿，宋台梁馆尚依稀。\\n马嘶古道行人歇，麦秀空城野雉飞。\\n风吹落叶填宫井，火入荒陵化宝衣。\\n徒使词臣庾开府，咸阳终日苦思归。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4035', 'poem', '唐', '王建', '江馆', '水面细风生，菱歌慢慢声。\\n客亭临小市，灯火夜妆明。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4036', 'poem', '唐', '杜甫', '又送', '双峰寂寂对春台，万竹青青照客杯。\\n细草留连侵坐软，残花怅望近人开。\\n同舟昨日何由得，并马今朝未拟回。\\n直到绵州始分首，江边树里共谁来。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4037', 'poem', '唐', '白居易', '听崔七妓人筝', '花脸云鬟坐玉楼，十三弦里一时愁。\\n凭君向道休弹去，白尽江州司马头。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4038', 'poem', '唐', '张佐', '秦镜', '楼上秦时镜，千秋独有名。\\n菱花寒不落，冰质夏长清。\\n龙在形难掩，人来胆易呈。\\n升台宜远照，开匣乍藏明。\\n皎色新磨出，圆规旧铸成。\\n愁容如可鉴，当欲拂尘缨。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4039', 'poem', '唐', '孙元晏', '梁·勍敌', '传闻天子重儒才，特为皇华绮宴开。\\n今日方惊遇勍敌，此人元自北朝来。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4040', 'poem', '唐', '李白', '挂席江上待月有怀', '待月月未出，望江江自流。\\n倏忽城西郭，青天悬玉钩。\\n素华虽可揽，清景不同游。\\n耿耿金波里，空瞻鳷鹊楼。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4041', 'poem', '唐', '李群玉', '题竹', '一顷含秋绿，森风十万竿。\\n气吹朱夏转，声扫碧霄寒。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4042', 'poem', '唐', '杜牧', '和令狐侍御赏蕙草', '寻常诗思巧如春，又喜幽亭蕙草新。\\n本是馨香比君子，绕栏今更为何人。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4043', 'poem', '唐', '许棠', '题金山寺', '四面波涛匝，中楼日月邻。\\n上穷如出世，下瞷忽惊神。\\n刹碍长空鸟，船通外国人。\\n房房皆叠石，风扫永无尘。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4044', 'poem', '唐', '李商隐', '促漏', '促漏遥钟动静闻，报章重叠杳难分。\\n舞鸾镜匣收残黛，睡鸭香炉换夕熏。\\n归去定知还向月，梦来何处更为云。\\n南塘渐暖蒲堪结，两两鸳鸯护水纹。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4045', 'poem', '唐', '李商隐', '岳阳楼', '汉水方城带百蛮，四邻谁道乱周班。\\n如何一梦高唐雨，自此无心入武关。', '2018-03-09 15:42:09');
INSERT INTO `t_poem` VALUES ('4046', 'poem', '唐', '贾岛', '宿悬泉驿', '晓行沥水楼，暮到悬泉驿。\\n林月值云遮，山灯照愁寂。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4047', 'poem', '唐', '韩愈', '鸣雁', '嗷嗷鸣雁鸣且飞，穷秋南去春北归。去寒就暖识所依，\\n天长地阔栖息稀。风霜酸苦稻粱微，毛羽摧落身不肥。\\n裴回反顾群侣违。哀鸣欲下洲渚非。江南水阔朝云多，\\n草长沙软无网罗。闲飞静集鸣相和，违忧怀惠性匪他。\\n凌风一举君谓何。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4048', 'poem', '唐', '杜甫', '楠树为风雨所拔叹', '倚江楠树草堂前，故老相传二百年。\\n诛茅卜居总为此，五月仿佛闻寒蝉。\\n东南飘风动地至，江翻石走流云气。\\n干排雷雨犹力争，根断泉源岂天意。\\n沧波老树性所爱，浦上童童一青盖。\\n野客频留惧雪霜，行人不过听竽籁。\\n虎倒龙颠委榛棘，泪痕血点垂胸臆。\\n我有新诗何处吟，草堂自此无颜色。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4049', 'poem', '唐', '韩愈', '盆池五首', '老翁真个似童儿，汲水埋盆作小池。\\n一夜青蛙鸣到晓，恰如方口钓鱼时。\\n莫道盆池作不成，藕稍初种已齐生。\\n从今有雨君须记，来听萧萧打叶声。\\n瓦沼晨朝水自清，小虫无数不知名。\\n忽然分散无踪影，惟有鱼儿作队行。\\n泥盆浅小讵成池，夜半青蛙圣得知。\\n一听暗来将伴侣，不烦鸣唤斗雄雌。\\n池光天影共青青，拍岸才添水数瓶。\\n且待夜深明月去，试看涵泳几多星。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4050', 'poem', '唐', '李白', '秋登巴陵望洞庭', '清晨登巴陵，周览无不极。\\n明湖映天光，彻底见秋色。\\n秋色何苍然，际海俱澄鲜。\\n山青灭远树，水绿无寒烟。\\n来帆出江中，去鸟向日边。\\n风清长沙浦，山空云梦田。\\n瞻光惜颓发，阅水悲徂年。\\n北渚既荡漾，东流自潺湲。\\n郢人唱白雪，越女歌采莲。\\n听此更肠断，凭崖泪如泉。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4051', 'poem', '唐', '韩愈', '郴口又赠二首', '山作剑攒江写镜，扁舟斗转疾于飞。\\n回头笑向张公子，终日思归此日归。\\n雪飐霜翻看不分，雷惊电激语难闻。\\n沿涯宛转到深处，何限青天无片云。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4052', 'poem', '唐', '韩偓', '荷花', '纨扇相欹绿，香囊独立红。\\n浸淫因重露，狂暴是秋风。\\n逸调无人唱，秋塘每夜空。\\n何繇见周昉，移入画屏中。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4053', 'poem', '唐', '白居易', '早祭风伯，因怀李十一舍人', '远郡虽褊陋，时祀奉朝经。\\n夙兴祭风伯，天气晓冥冥。\\n导骑与从吏，引我出东坰。\\n水雾重如雨，山火高于星。\\n忽忆早朝日，与君趋紫庭。\\n步登龙尾道，却望终南青。\\n一别身向老，所思心未宁。\\n至今想在耳，玉音尚玲玲。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4054', 'poem', '唐', '陈子昂', '酬晖上人秋夜山亭有赠', '皎皎白林秋，微微翠山静。\\n禅居感物变，独坐开轩屏。\\n风泉夜声杂，月露宵光冷。\\n多谢忘机人，尘忧未能整。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4055', 'poem', '唐', '李商隐', '河清与赵氏昆季宴集得拟杜工部', '胜概殊江右，佳名逼渭川。\\n虹收青嶂雨，鸟没夕阳天。\\n客鬓行如此，沧波坐渺然。\\n此中真得地，漂荡钓鱼船。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4056', 'poem', '唐', '元稹', '梦井', '梦上高高原，原上有深井。\\n登高意枯渴，愿见深泉冷。\\n裴回绕井顾，自照泉中影。\\n沉浮落井瓶，井上无悬绠。\\n念此瓶欲沉，荒忙为求请。\\n遍入原上村，村空犬仍猛。\\n还来绕井哭，哭声通复哽。\\n哽噎梦忽惊，觉来房舍静。\\n灯焰碧胧胧，泪光疑冏冏。\\n钟声夜方半，坐卧心难整。\\n忽忆咸阳原，荒田万馀顷。\\n土厚圹亦深，埋魂在深埂。\\n埂深安可越，魂通有时逞。\\n今宵泉下人，化作瓶相憬。\\n感此涕汍澜，汍澜涕沾领。\\n所伤觉梦间，便觉死生境。\\n岂无同穴期，生期谅绵永。\\n又恐前后魂，安能两知省。\\n寻环意无极，坐见天将昞。\\n吟此梦井诗，春朝好光景。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4057', 'poem', '唐', '杜牧', '芭蕉', '芭蕉为雨移，故向窗前种。\\n怜渠点滴声，留得归乡梦。\\n梦远莫归乡，觉来一翻动。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4058', 'poem', '唐', '李白', '金陵凤凰台置酒', '置酒延落景，金陵凤凰台。\\n长波写万古，心与云俱开。\\n借问往昔时，凤凰为谁来。\\n凤凰去已久，正当今日回。\\n明君越羲轩，天老坐三台。\\n豪士无所用，弹弦醉金罍。\\n东风吹山花，安可不尽杯。\\n六帝没幽草，深宫冥绿苔。\\n置酒勿复道，歌钟但相催。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4059', 'poem', '唐', '杜甫', '赤谷', '天寒霜雪繁，游子有所之。\\n岂但岁月暮，重来未有期。\\n晨发赤谷亭，险艰方自兹。\\n乱石无改辙，我车已载脂。\\n山深苦多风，落日童稚饥。\\n悄然村墟迥，烟火何由追。\\n贫病转零落，故乡不可思。\\n常恐死道路，永为高人嗤。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4060', 'poem', '唐', '李端', '杂诗', '主第辞高饮，石家赴宵会。\\n金谷走车来，玉人骑马待。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4061', 'poem', '唐', '杜甫', '九日杨奉先会白水崔明府', '今日潘怀县，同时陆浚仪。\\n坐开桑落酒，来把菊花枝。\\n天宇清霜净，公堂宿雾披。\\n晚酣留客舞，凫舄共差池。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4062', 'poem', '唐', '陆龟蒙', '雁', '南北路何长，中间万弋张。\\n不知烟雾里，几只到衡阳。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4063', 'poem', '唐', '张籍', '与贾岛闲游', '水北原南草色新，雪消风暖不生尘。\\n城中车马应无数，能解闲行有几人。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4064', 'poem', '唐', '李商隐', '代应', '本来银汉是红墙，隔得卢家白玉堂。\\n谁与王昌报消息，尽知三十六鸳鸯。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4065', 'poem', '唐', '杜甫', '草堂即事', '荒村建子月，独树老夫家。\\n雾里江船渡，风前径竹斜。\\n寒鱼依密藻，宿鹭起圆沙。\\n蜀酒禁愁得，无钱何处赊。', '2018-03-09 15:42:24');
INSERT INTO `t_poem` VALUES ('4066', 'poem', '唐', '张窈窕', '春思二首', '门前梅柳烂春辉，闭妾深闺绣舞衣。\\n双燕不知肠欲断，衔泥故故傍人飞。\\n井上梧桐是妾移，夜来花发最高枝。\\n若教不向深闺种，春过门前争得知。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4067', 'poem', '唐', '李商隐', '咸阳', '咸阳宫阙郁嵯峨，六国楼台艳绮罗。\\n自是当时天帝醉，不关秦地有山河。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4068', 'poem', '唐', '白居易', '老病', '昼听笙歌夜醉眠，若非月下即花前。\\n如今老病须知分，不负春来二十年。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4069', 'poem', '唐', '杨玢', '批子弟理旧居状', '四邻侵我我从伊，毕竟须思未有时。\\n试上含元殿基望，秋风秋草正离离。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4070', 'poem', '唐', '翁洮', '冬', '寂寂栖心向杳冥，苦吟寒律句偏清。\\n云凝止水鱼龙蛰，雪点遥峰草木荣。\\n迥夜炉翻埃烬色，天河冰辗辘轳声。\\n归飞未得东风力，魂断三山九万程。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4071', 'poem', '唐', '周昙', '三国门·王表', '王表闻声莫见身，吴中敬事甚君亲。\\n是知邦国将亡灭，不听人臣听鬼神。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4072', 'poem', '唐', '卢仝', '叹昨日三首', '昨日之日不可追，今日之日须臾期。\\n如此如此复如此，壮心死尽生鬓丝。\\n秋风落叶客肠断，不办斗酒开愁眉。\\n贤名圣行甚辛苦，周公孔子徒自欺。\\n天下薄夫苦耽酒，玉川先生也耽酒。\\n薄夫有钱恣张乐，先生无钱养恬漠。\\n有钱无钱俱可怜，百年骤过如流川。\\n平生心事消散尽，天上白日悠悠悬。\\n上帝板板主何物，日车劫劫西向没。\\n自古贤圣无奈何，道行不得皆白骨。\\n白骨土化鬼入泉，生人莫负平生年。\\n何时出得禁酒国，满瓮酿酒曝背眠。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4073', 'poem', '唐', '白居易', '留题开元寺上方', '东寺台阁好，上方风景清。\\n数来犹未厌，长别岂无情。\\n恋水多临坐，辞花剩绕行。\\n最怜新岸柳，手种未全成。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4074', 'poem', '唐', '李商隐', '吴宫', '龙槛沉沉水殿清，禁门深掩断人声。\\n吴王宴罢满宫醉，日暮水漂花出城。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4075', 'poem', '唐', '罗隐', '宫词', '巧画蛾眉独出群，当时人道便承恩。\\n经年不见君王面，落日黄昏空掩门。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4076', 'poem', '唐', '杨炯', '送临津房少府', '岐路三秋别，江津万里长。\\n烟霞驻征盖，弦奏促飞觞。\\n阶树含斜日，池风泛早凉。\\n赠言未终竟，流涕忽沾裳。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4077', 'poem', '唐', '刘禹锡', '寄赠小樊', '花面丫头十三四，春来绰约向人时。\\n终须买取名春草，处处将行步步随。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4078', 'poem', '唐', '白居易', '望亭驿酬别周判官', '何事出长洲，连宵饮不休。\\n醒应难作别，欢渐少于愁。\\n灯火穿村市，笙歌上驿楼。\\n何言五十里，已不属苏州。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4079', 'poem', '唐', '白居易', '和万州杨使君四绝句·江边草', '闻君泽畔伤春草，忆在天门街里时。\\n漠漠凄凄愁满眼，就中惆怅是江蓠。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4080', 'poem', '唐', '杜荀鹤', '赠僧', '利门名路两何凭，百岁风前短焰灯。\\n只恐为僧僧不了，为僧得了总输僧。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4081', 'poem', '唐', '孟云卿', '行行且游猎篇', '少年多武力，勇气冠幽州。\\n何以纵心赏，马啼春草头。\\n迟迟平原上，狐兔奔林丘。\\n猛虎忽前逝，俊鹰连下鞲。\\n俯身逐南北，轻捷固难俦。\\n所发无不中，失之如我雠。\\n岂唯务驰骋，猗尔暴田畴。\\n残杀非不痛，古来良有由。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4082', 'poem', '唐', '司空图', '诗品二十四则·旷达', '生者百岁，相去几何？欢乐苦短，忧愁实多。\\n何如尊酒，日往烟萝。花覆茆檐，疏雨相过。\\n倒酒既尽，杖藜行过。孰不有古，南山峨峨。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4083', 'poem', '唐', '杜甫', '放船', '收帆下急水，卷幔逐回滩。\\n江市戎戎暗，山云淰淰寒。\\n村荒无径入，独鸟怪人看。\\n已泊城楼底，何曾夜色阑。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4084', 'poem', '唐', '刘兼', '对镜', '青镜重磨照白须，白须撚闲意何如。\\n故园迢递千山外，荒郡淹留四载馀。\\n风送竹声侵枕簟，月移花影过庭除。\\n秋霜满领难消释，莫读离骚失意书。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4085', 'poem', '唐', '于武陵', '山上树', '日暖上山路，鸟啼知已春。\\n忽逢幽隐处，如见独醒人。\\n石冷开常晚，风多落亦频。\\n樵夫应不识，岁久伐为薪。', '2018-03-09 15:42:36');
INSERT INTO `t_poem` VALUES ('4086', 'poem', '唐', '韩琮', '牡丹', '桃时杏日不争浓，叶帐阴成始放红。\\n晓艳远分金掌露，暮香深惹玉堂风。\\n名移兰杜千年后，贵擅笙歌百醉中。\\n如梦如仙忽零落，暮霞何处绿屏空。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4087', 'poem', '唐', '白居易', '雪夜小饮赠梦得', '同为懒慢园林客，共对萧条雨雪天。\\n小酌酒巡销永夜，大开口笑送残年。\\n久将时背成遗老，多被人呼作散仙。\\n呼作散仙应有以，曾看东海变桑田。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4088', 'poem', '唐', '杜牧', '夜雨', '九月三十日，雨声如别秋。\\n无端满阶叶，共白几人头。\\n点滴侵寒梦，萧骚著淡愁。\\n渔歌听不唱，蓑湿棹回舟。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4089', 'poem', '唐', '项斯', '远水', '渺渺浸天色，一边生晚光。\\n阔浮萍思远，寒入雁愁长。\\n北极连平地，东流即故乡。\\n扁舟来宿处，仿佛似潇湘。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4090', 'poem', '唐', '薛逢', '惊秋', '露竹风蝉昨夜秋，百年心事付东流。\\n明霜义分成虚话，阜俗文章惜暗投。\\n长笑李斯称溷鼠，每多庄叟喻牺牛。\\n五湖烟水盈归梦，芦荻花中一钓舟。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4091', 'poem', '唐', '刘禹锡', '泰娘歌', '泰娘家本阊门西，门前绿水环金堤。\\n有时妆成好天气，走上皋桥折花戏。\\n风流太守韦尚书，路傍忽见停隼旟。\\n斗量明珠鸟传意，绀幰迎入专城居。\\n长鬟如云衣似雾，锦茵罗荐承轻步。\\n舞学惊鸿水榭春，歌传上客兰堂暮。\\n从郎西入帝城中，贵游簪组香帘栊。\\n低鬟缓视抱明月，纤指破拨生胡风。\\n繁华一旦有消歇，题剑无光履声绝。\\n洛阳旧宅生草莱，杜陵萧萧松柏哀。\\n妆奁虫网厚如茧，博山炉侧倾寒灰。\\n蕲州刺史张公子，白马新到铜驼里。\\n自言买笑掷黄金，月堕云中从此始。\\n安知鵩鸟座隅飞，寂寞旅魂招不归。\\n秦嘉镜有前时结，韩寿香销故箧衣。\\n山城少人江水碧，断雁哀猿风雨夕。\\n朱弦已绝为知音，云鬓未秋私自惜。\\n举目风烟非旧时，梦寻归路多参差。\\n如何将此千行泪，更洒湘江斑竹枝。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4092', 'poem', '唐', '刘禹锡', '西山兰若试茶歌', '山僧后檐茶数丛，春来映竹抽新茸。\\n宛然为客振衣起，自傍芳丛摘鹰觜。\\n斯须炒成满室香，便酌砌下金沙水。\\n骤雨松声入鼎来，白云满碗花徘徊。\\n悠扬喷鼻宿酲散，清峭彻骨烦襟开。\\n阳崖阴岭各殊气，未若竹下莓苔地。\\n炎帝虽尝未解煎，桐君有箓那知味。\\n新芽连拳半未舒，自摘至煎俄顷馀。\\n木兰沾露香微似，瑶草临波色不如。\\n僧言灵味宜幽寂，采采翘英为嘉客。\\n不辞缄封寄郡斋，砖井铜炉损标格。\\n何况蒙山顾渚春，白泥赤印走风尘。\\n欲知花乳清泠味，须是眠云跂石人。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4093', 'poem', '唐', '李白', '与元丹丘方城寺谈玄作', '茫茫大梦中，惟我独先觉。\\n腾转风火来，假合作容貌。\\n灭除昏疑尽，领略入精要。\\n澄虑观此身，因得通寂照。\\n郎悟前后际，始知金仙妙。\\n幸逢禅居人，酌玉坐相召。\\n彼我俱若丧，云山岂殊调。\\n清风生虚空，明月见谈笑。\\n怡然青莲宫，永愿恣游眺。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4094', 'poem', '唐', '齐己', '惊秋', '褰帘听秋信，晚傍竹声归。\\n多故堪伤骨，孤峰好拂衣。\\n梧桐凋绿尽，菡萏堕红稀。\\n却恐吾形影，嫌心与口违。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4095', 'poem', '唐', '李冶', '结素鱼贻友人', '尺素如残雪，结为双鲤鱼。\\n欲知心里事，看取腹中书。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4096', 'poem', '唐', '姚合', '寒食二首（一作张籍诗）', '今朝一百五，出户雨初晴。\\n舞爱双飞蝶，歌闻百啭莺。\\n江深青草岸，花满白云城。\\n为政多孱懦，应无酷吏名。\\n出城烟火少，况复是今朝。\\n闲坐将谁语，临觞只自谣。\\n阶前春藓遍，衣上落花飘。\\n伎乐州人戏，使君心寂寥。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4097', 'poem', '唐', '岑参', '玉关寄长安李主簿', '东去长安万里馀，故人何惜一行书。\\n玉关西望堪肠断，况复明朝是岁除。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4098', 'poem', '唐', '唐彦谦', '野行', '蝶恋晚花终不去，鸥逢春水固难飞。\\n野人心地都无著，伴蝶随鸥亦不归。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4099', 'poem', '唐', '白居易', '对镜', '三分鬓发二分丝，晓镜秋容相对时。\\n去作忙官应太老，退为闲叟未全迟。\\n静中得味何须道，稳处安身更莫疑。\\n若使至今黄绮在，闻吾此语亦分司。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4100', 'poem', '唐', '李商隐', '齐宫词', '永寿兵来夜不扃，金莲无复印中庭。\\n梁台歌管三更罢，犹自风摇九子铃。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4101', 'poem', '唐', '杜牧', '商山麻涧', '云光岚彩四面合，柔柔垂柳十馀家。\\n雉飞鹿过芳草远，牛巷鸡埘春日斜。\\n秀眉老父对樽酒，蒨袖女儿簪野花。\\n征车自念尘土计，惆怅溪边书细沙。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4102', 'poem', '唐', '白居易', '三月三日怀微之', '良时光景长虚掷，壮岁风情已暗销。\\n忽忆同为校书日，每年同醉是今朝。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4103', 'poem', '唐', '崔道融', '峡路', '清猿啼不住，白水下来新。\\n八月莫为客，夜长愁杀人。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4104', 'poem', '唐', '马戴', '远水', '荡漾空沙际，虚明入远天。\\n秋光照不极，鸟影去无边。\\n势引长云断，波轻片雪连。\\n汀洲杳难到，万古覆苍烟。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4105', 'poem', '唐', '司空图', '山中', '凡鸟爱喧人静处，闲云似妒月明时。\\n世间万事非吾事，只愧秋来未有诗。', '2018-03-09 15:42:40');
INSERT INTO `t_poem` VALUES ('4106', 'poem', '唐', '戴叔伦', '春怨', '金鸭香消欲断魂，梨花春雨掩重门。\\n欲知别后相思意，回看罗衣积泪痕。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4107', 'poem', '唐', '元稹', '和乐天重题别东楼', '山容水态使君知，楼上从容万状移。\\n日映文章霞细丽，风驱鳞甲浪参差。\\n鼓催潮户凌晨击，笛赛婆官彻夜吹。\\n唤客潜挥远红袖，卖垆高挂小青旗。\\n賸铺床席春眠处，乍卷帘帷月上时。\\n光景无因将得去，为郎抄在和郎诗。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4108', 'poem', '唐', '白居易', '听歌六绝句·何满子', '世传满子是人名，临就刑时曲始成。\\n一曲四调歌八叠，从头便是断肠声。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4109', 'poem', '唐', '王维', '崔兴宗写真咏', '画君年少时，如今君已老。\\n今时新识人，知君旧时好。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4110', 'poem', '唐', '崔国辅', '杂曲歌辞·小长干曲', '月暗送湖风，相寻路不通。\\n菱歌唱不辍，知在此塘中。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4111', 'poem', '唐', '张籍', '过贾岛野居', '青门坊外住，行坐见南山。\\n此地去人远，知君终日闲。\\n蛙声篱落下，草色户庭间。\\n好是经过处，唯愁暮独还。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4112', 'poem', '唐', '白居易', '僧院花', '欲悟色空为佛事，故栽芳树在僧家。\\n细看便是华严偈，方便风开智慧花。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4113', 'poem', '唐', '周昙', '春秋战国门·田饶', '厨抛败肉士怀饥，仓烂馀粮客未炊。\\n临难欲行求死士，将何恩信致扶危。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4114', 'poem', '唐', '元稹', '新竹', '新篁才解箨，寒色已青葱。\\n冉冉偏凝粉，萧萧渐引风。\\n扶疏多透日，寥落未成丛。\\n惟有团团节，坚贞大小同。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4115', 'poem', '唐', '刘禹锡', '杨柳枝', '巫山巫峡杨柳多，朝云暮雨远相和。\\n因想阳台无限事，为君回唱竹枝歌。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4116', 'poem', '唐', '崔道融', '秋夕', '自怜三十未西游，傍水寻山过却秋。\\n一夜雨声多少事，不思量尽到心头。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4117', 'poem', '唐', '聂夷中', '秋夕', '日往无复见，秋堂暮仍学。\\n玄发不知白，晓人寒铜觉。\\n为材未离群，有玉犹在璞。\\n谁把碧桐枝，刻作云门乐。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4118', 'poem', '唐', '喻凫', '惊秋', '莺啭才间关，蝉鸣旋萧屑。\\n如何两鬓毛，不作千枝雪。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4119', 'poem', '唐', '于武陵', '远水', '悔作望南浦，望中生远愁。\\n因知人易老，为有水东流。\\n欲附故乡信，不逢归客舟。\\n萋萋两岸草，又度一年秋。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4120', 'poem', '唐', '许浑', '村舍二首', '自翦青莎织雨衣，南峰烟火是柴扉。\\n莱妻早报蒸藜熟，童子遥迎种豆归。\\n鱼下碧潭当镜跃，鸟还青嶂拂屏飞。\\n花时未免人来往，欲买严光旧钓矶。\\n尚平多累自归难，一日身闲一日安。\\n山径晓云收猎网，水门凉月挂鱼竿。\\n花间酒气春风暖，竹里棋声暮雨寒。\\n三顷水田秋更熟，北窗谁拂旧尘冠。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4121', 'poem', '唐', '杜甫', '暮春题瀼西新赁草屋五首', '久嗟三峡客，再与暮春期。\\n百舌欲无语，繁花能几时。\\n谷虚云气薄，波乱日华迟。\\n战伐何由定，哀伤不在兹。\\n此邦千树橘，不见比封君。\\n养拙干戈际，全生麋鹿群。\\n畏人江北草，旅食瀼西云。\\n万里巴渝曲，三年实饱闻。\\n彩云阴复白，锦树晓来青。\\n身世双蓬鬓，乾坤一草亭。\\n哀歌时自短，醉舞为谁醒。\\n细雨荷锄立，江猿吟翠屏。\\n壮年学书剑，他日委泥沙。\\n事主非无禄，浮生即有涯。\\n高斋依药饵，绝域改春华。\\n丧乱丹心破，王臣未一家。\\n欲陈济世策，已老尚书郎。\\n未息豺虎斗，空惭鸳鹭行。\\n时危人事急，风逆羽毛伤。\\n落日悲江汉，中宵泪满床。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4122', 'poem', '唐', '韦庄', '途中望雨怀归', '满空寒雨漫霏霏，去路云深锁翠微。\\n牧竖远当烟草立，饥禽闲傍渚田飞。\\n谁家树压红榴折，几处篱悬白菌肥。\\n对此不堪乡外思，荷蓑遥羡钓人归。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4123', 'poem', '唐', '杜甫', '送郑十八虔贬台州司户伤其临老陷贼之故阙为面别情见于诗', '郑公樗散鬓成丝，酒后常称老画师。\\n万里伤心严谴日，百年垂死中兴时。\\n苍惶已就长途往，邂逅无端出饯迟。\\n便与先生应永诀，九重泉路尽交期。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4124', 'poem', '唐', '卢照邻', '入秦川界', '陇阪长无极，苍山望不穷。\\n石径萦疑断，回流映似空。\\n花开绿野雾，莺啭紫岩风。\\n春芳勿遽尽，留赏故人同。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4125', 'poem', '唐', '李白', '戏赠郑溧阳', '陶令日日醉，不知五柳春。\\n素琴本无弦，漉酒用葛巾。\\n清风北窗下，自谓羲皇人。\\n何时到栗里，一见平生亲。', '2018-03-09 15:42:51');
INSERT INTO `t_poem` VALUES ('4126', 'poem', '唐', '白居易', '路上寄银匙与阿龟', '谪宦心都惯，辞乡去不难。\\n缘留龟子住，涕泪一阑干。\\n小子须娇养，邹婆为好看。\\n银匙封寄汝，忆我即加餐。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4127', 'poem', '唐', '白居易', '闲忙', '奔走朝行内，栖迟林墅间。\\n多因病后退，少及健时还。\\n斑白霜侵鬓，苍黄日下山。\\n闲忙俱过日，忙校不如闲。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4128', 'poem', '唐', '李隆基', '端午三殿宴群臣探得神字', '五月符天数，五音调夏钧。\\n旧来传五日，无事不称神。\\n穴枕通灵气，长丝续命人。\\n四时花竞巧，九子粽争新。\\n方殿临华节，圆宫宴雅臣。\\n进对一言重，遒文六义陈。\\n股肱良足咏，风化可还淳。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4129', 'poem', '唐', '李白', '秦女卷衣', '天子居未央，妾侍卷衣裳。\\n顾无紫宫宠，敢拂黄金床。\\n水至亦不去，熊来尚可当。\\n微身奉日月，飘若萤之光。\\n愿君采葑菲，无以下体妨。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4130', 'poem', '唐', '韦应物', '幽居', '贵贱虽异等，出门皆有营。\\n独无外物牵，遂此幽居情。\\n微雨夜来过，不知春草生。\\n青山忽已曙，鸟雀绕舍鸣。\\n时与道人偶，或随樵者行。\\n自当安蹇劣，谁谓薄世荣。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4131', 'poem', '唐', '韦庄', '解维', '又解征帆落照中，暮程还过秣陵东。\\n二年辛苦烟波里，赢得风姿似钓翁。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4132', 'poem', '唐', '李商隐', '题汉祖庙', '乘运应须宅八荒，男儿安在恋池隍。\\n君王自起新丰后，项羽何曾在故乡。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4133', 'poem', '唐', '戴叔伦', '独坐', '白发怀闽峤，丹心恋蓟门。\\n官闲胜道院，宅远类荒村。\\n二月霜花薄，群山雨气昏。\\n东菑春事及，好向野人论。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4134', 'poem', '唐', '顾况', '焙茶坞', '新茶已上焙，旧架忧生醭。\\n旋旋续新烟，呼儿劈寒木。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4135', 'poem', '唐', '聂夷中', '游子行', '萱草生堂阶，游子行天涯。\\n慈亲倚门望，不见萱草花。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4136', 'poem', '唐', '鲍溶', '襄阳怀古', '襄阳太守沈碑意，身后身前几年事。\\n湘江千岁未为陵，水底鱼龙应识字。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4137', 'poem', '唐', '李白', '清溪半夜闻笛', '羌笛梅花引，吴溪陇水情。\\n寒山秋浦月，肠断玉关声。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4138', 'poem', '唐', '德诚', '船子和尚偈', '千尺丝纶直下垂，一波才动万波随。\\n夜静水寒鱼不食，满船空载月明归。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4139', 'poem', '唐', '孟浩然', '送朱大入秦', '游人武陵去，宝剑直千金。\\n分手脱相赠，平生一片心。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4140', 'poem', '唐', '岑参', '登总持阁', '高阁逼诸天，登临近日边。\\n晴开万井树，愁看五陵烟。\\n槛外低秦岭，窗中小渭川。\\n早知清净理，常愿奉金仙。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4141', 'poem', '唐', '白居易', '李都尉古剑', '古剑寒黯黯，铸来几千秋。\\n白光纳日月，紫气排斗牛。\\n有客借一观，爱之不敢求。\\n湛然玉匣中，秋水澄不流。\\n至宝有本性，精刚无与俦。\\n可使寸寸折，不能绕指柔。\\n愿快直士心，将断佞臣头。\\n不愿报小怨，夜半刺私仇。\\n劝君慎所用，无作神兵羞。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4142', 'poem', '唐', '岑参', '献封大夫破播仙凯歌六首', '汉将承恩西破戎，捷书先奏未央宫。\\n天子预开麟阁待，只今谁数贰师功。\\n官军西出过楼兰，营幕傍临月窟寒。\\n蒲海晓霜凝马尾，葱山夜雪扑旌竿。\\n鸣笳叠鼓拥回军，破国平蕃昔未闻。\\n丈夫鹊印摇边月，大将龙旗掣海云。\\n日落辕门鼓角鸣，千群面缚出蕃城。\\n洗兵鱼海云迎阵，秣马龙堆月照营。\\n蕃军遥见汉家营，满谷连山遍哭声。\\n万箭千刀一夜杀，平明流血浸空城。\\n暮雨旌旗湿未干，胡烟白草日光寒。\\n昨夜将军连晓战，蕃军只见马空鞍。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4143', 'poem', '唐', '李咸用', '访友人不遇', '出门无至友，动即到君家。\\n空掩一庭竹，去看何寺花。\\n短僮应捧杖，稚女学擎茶。\\n吟罢留题处，苔阶日影斜。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4144', 'poem', '唐', '西施', '西施诗', '云霞出没群峰外，鸥鸟浮沈一水间。\\n一自越兵齐振地，梦魂不到虎丘山。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4145', 'poem', '唐', '朱庆馀', '塞下曲', '万里去长征，连年惯野营。\\n入群来择马，抛伴去擒生。\\n箭撚雕翎阔，弓盘鹊角轻。\\n问看行近远，西过受降城。', '2018-03-09 15:43:01');
INSERT INTO `t_poem` VALUES ('4146', 'poem', '唐', '白居易', '池上幽境', '袅袅过水桥，微微入林路。\\n幽境深谁知，老身闲独步。\\n行行何所爱，遇物自成趣。\\n平滑青盘石，低密绿阴树。\\n石上一素琴，树下双草屦。\\n此是荣先生，坐禅三乐处。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4147', 'poem', '唐', '秦韬玉', '春雪', '云重寒空思寂寥，玉尘如糁满春朝。\\n片才著地轻轻陷，力不禁风旋旋销。\\n惹砌任他香粉妒，萦丛自学小梅娇。\\n谁家醉卷珠帘看，弦管堂深暖易调。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4148', 'poem', '唐', '王勃', '山扉夜坐', '抱琴开野室，携酒对情人。\\n林塘花月下，别似一家春。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4149', 'poem', '唐', '李嘉祐', '秋朝木芙蓉', '水面芙蓉秋已衰，繁条到是著花时。\\n平明露滴垂红脸，似有朝愁暮落悲。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4150', 'poem', '唐', '韦庄', '独鹤', '夕阳滩上立裴回，红蓼风前雪翅开。\\n应为不知栖宿处，几回飞去又飞来。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4151', 'poem', '唐', '郑谷', '夕阳', '夕阳秋更好，敛敛蕙兰中。\\n极浦明残雨，长天急远鸿。\\n僧窗留半榻，渔舸透疏篷。\\n莫恨清光尽，寒蟾即照空。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4152', 'poem', '唐', '白居易', '经溱洧', '落日驻行骑，沉吟怀古情。\\n郑风变已尽，溱洧至今清。\\n不见士与女，亦无芍药名。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4153', 'poem', '唐', '李山甫', '禅窟寺', '迟日浓花午，闲云小阴天。\\n半岩春洞水，万片晚林烟。\\n惭愧华巅客，来分紫府仙。\\n倚窗山竹夜，风溜响朱絃。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4154', 'poem', '唐', '李白', '送羽林陶将军', '将军出使拥楼船，江上旌旗拂紫烟。\\n万里横戈探虎穴，三杯拔剑舞龙泉。\\n莫道词人无胆气，临行将赠绕朝鞭。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4155', 'poem', '唐', '成彦雄', '松', '大夫名价古今闻，盘屈孤贞更出群。\\n将谓岭头闲得了，夕阳犹挂数枝云。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4156', 'poem', '唐', '杜甫', '凭韦少府班觅松树子', '落落出群非榉柳，青青不朽岂杨梅。\\n欲存老盖千年意，为觅霜根数寸栽。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4157', 'poem', '唐', '李世民', '经破薛举战地', '昔年怀壮气，提戈初仗节。\\n心随朗日高，志与秋霜洁。\\n移锋惊电起，转战长河决。\\n营碎落星沉，阵卷横云裂。\\n一挥氛沴静，再举鲸鲵灭。\\n于兹俯旧原，属目驻华轩。\\n沉沙无故迹，减灶有残痕。\\n浪霞穿水净，峰雾抱莲昏。\\n世途亟流易，人事殊今昔。\\n长想眺前踪，抚躬聊自适。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4158', 'poem', '唐', '孟郊', '百忧', '萱草女儿花，不解壮士忧。\\n壮士心是剑，为君射斗牛。\\n朝思除国雠，暮思除国雠。\\n计尽山河画，意穷草木筹。\\n智士日千虑，愚夫唯四愁。\\n何必在波涛，然后惊沉浮。\\n伯伦心不醉，四皓迹难留。\\n出处各有时，众议徒啾啾。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4159', 'poem', '唐', '李商隐', '木兰', '二月二十二，木兰开坼初。\\n初当新病酒，复自久离居。\\n愁绝更倾国，惊新闻远书。\\n紫丝何日障，油壁几时车。\\n弄粉知伤重，调红或有馀。\\n波痕空映袜，烟态不胜裾。\\n桂岭含芳远，莲塘属意疏。\\n瑶姬与神女，长短定何如。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4160', 'poem', '唐', '白居易', '感芍药花，寄正一上人', '今日阶前红芍药，几花欲老几花新。\\n开时不解比色相，落后始知如幻身。\\n空门此去几多地，欲把残花问上人。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4161', 'poem', '唐', '戴叔伦', '花', '花发炎景中，芳春独能久。\\n因风任开落，向日无先后。\\n若待秋霜来，兰荪共何有。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4162', 'poem', '唐', '吴筠', '舟中夜行', '榜人识江路，挂席从宵征。\\n莫辨洲渚状，但闻风波惊。\\n阴云正飘飖，落月无光晶。\\n岂不畏艰险，所凭在忠诚。\\n何时达遥夜，伫见初日明。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4163', 'poem', '唐', '白居易', '负冬日', '杲杲冬日出，照我屋南隅。\\n负暄闭目坐，和气生肌肤。\\n初似饮醇醪，又如蛰者苏。\\n外融百骸畅，中适一念无。\\n旷然忘所在，心与虚空俱。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4164', 'poem', '唐', '李世民', '登三台言志', '未央初壮汉，阿房昔侈秦。\\n在危犹骋丽，居奢遂役人。\\n岂如家四海，日宇罄朝伦。\\n扇天裁户旧，砌地翦基新。\\n引月擎宵桂，飘云逼曙鳞。\\n露除光炫玉，霜阙映雕银。\\n舞接花梁燕，歌迎鸟路尘。\\n镜池波太液，庄苑丽宜春。\\n作异甘泉日，停非路寝辰。\\n念劳惭逸己，居旷返劳神。\\n所欣成大厦，宏材伫渭滨。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4165', 'poem', '唐', '罗虬', '比红儿诗', '姓字看侵尺五天，芳菲占断百花鲜。\\n马嵬好笑当时事，虚赚明皇幸蜀川。\\n金谷园中花正繁，坠楼从道感深恩。\\n齐奴却是来东市，不为红儿死更冤。\\n陷却平阳为小怜，周师百万战长川。\\n更教乞与红儿貌，举国山川不值钱。\\n一曲都缘张丽华，六宫齐唱后庭花。\\n若教比并红儿貌，枉破当年国与家。\\n乐营门外柳如阴，中有佳人画阁深。\\n若是五陵公子见，买时应不啻千金。\\n青丝高绾石榴裙，肠断当筵酒半醺。\\n置向汉宫图画里，入胡应不数昭君。\\n斜凭栏杆醉态新，敛眸微盻不胜春。\\n当时若遇东昏主，金叶莲花是此人。\\n匼匝千山与万山，碧桃花下景长闲。\\n神仙得似红儿貌，应免刘郎忆世间。\\n越山重叠越溪斜，西子休怜解浣纱。\\n得似红儿今日貌，肯教将去与夫差。\\n诏下人间觅好花，月眉云髻选人家。\\n红儿若向当时见，系臂先封第一纱。\\n锋镝纵横不敢看，泪垂玉箸正汍澜。\\n应缘近似红儿貌，始得深宫奉五官。\\n金缕浓薰百和香，脸红眉黛入时妆。\\n当时便向乔家见，未敢将心在窈娘。\\n通宵甲帐散香尘，汉帝精神礼百神。\\n若见红儿醉中态，也应休忆李夫人。\\n拔得芙蓉出水新，魏家公子信才人。\\n若教瞥见红儿貌，不肯留情付洛神。\\n芳姿不合并常人，云在遥天玉在尘。\\n因事爱思荀奉倩，一生闲坐枉伤神。\\n笔底如风思涌泉，赋中休谩说婵娟。\\n红儿若在东家住，不得登墙尔许年。\\n一抹浓红傍脸斜，妆成不语独攀花。\\n当时若是逢韩寿，未必埋踪在贾家。\\n树袅西风日半沉，地无人迹转伤心。\\n阿娇得似红儿貌，不费长门买赋金。\\n五云高捧紫金堂，花下投壶侍玉皇。\\n从到世人都不识，也应知有杜兰香。\\n戏水源头指旧踪，当时一笑也难逢。\\n红儿若为回桃脸，岂比连催举五烽。\\n虢国夫人照夜玑，若为求得与红儿。\\n醉和香态浓春睡，一树繁花偃绣帏。\\n知有持盈玉叶冠，剪云裁月照人寒。\\n若使红儿风帽戴，直使瑶池会上看。\\n明媚何曾让玉环，破瓜年几百花颜。\\n若教貌向南朝见，定却梅妆似等闲。\\n世事悠悠未足称，肯将闲事更争能。\\n自从命向红儿去，不欲留心在裂缯。\\n自隐新从梦里来，岭云微步下阳台。\\n含情一向春风笑，羞杀凡花尽不开。\\n舍却青娥换玉鞍，古来公子苦无端。\\n莫言一匹追风马，天骥牵来也不看。\\n槛外花低瑞露浓，梦魂惊觉晕春容。\\n凭君细看红儿貌，最称严妆待晓钟。\\n薄罗轻剪越溪纹，鸦翅低垂两鬓分。\\n料得相如偷见面，不应琴里挑文君。\\n南国东邻各一时，后来惟有杜红儿。\\n若教楚国宫人见，羞把腰身并柳枝。\\n照耀金钗簇腻鬟，见时直向画屏间。\\n黄姑阿母能判剖，十斛明珠也是闲。\\n轻小休夸似燕身，生来占断紫宫春。\\n汉皇若遇红儿貌，掌上无因著别人。\\n鹦鹉娥如裛露红，镜前眉样自深宫。\\n稍教得似红儿貌，不嫁南朝沈侍中。\\n拟将心地学安禅，争奈红儿笑靥圆。\\n何物把来堪比并，野塘初绽一枝莲。\\n浸草漂花绕槛香，最怜穿度乐营墙。\\n殷勤留滞缘何事，曾照红儿一面妆。\\n雕阴旧俗骋婵娟，有个红儿赛洛川。\\n常笑世人语虚诞，今朝自见火中莲。\\n渡口诸侬乐未休，竟陵西望路悠悠。\\n石城有个红儿貌，两桨无因迎莫愁。\\n谁向深山识大仙，劝人山上引春泉。\\n定知不及红儿貌，枉却工夫溉玉田。\\n倾国倾城总绝伦，红儿花下认真身。\\n十年东北看燕赵，眼冷何曾见一人。\\n今时自是不谙知，前代由来岂见遗。\\n一笑阳城人便惑，何堪教见杜红儿。\\n京口喧喧百万人，竞传河鼓谢星津。\\n柰花似雪簪云髻，今日夭容是后身。\\n青史书时未是真，可能纤手却强秦。\\n再三为谢齐皇后，要解连环别与人。\\n绣帐鸳鸯对刺纹，博山微暖麝微曛。\\n诗成若有红儿貌，悔道当时月坠云。\\n薄粉轻朱取次施，大都端正亦相宜。\\n只如花下红儿态，不藉城中半额眉。\\n妆成浑欲认前朝，金凤双钗逐步摇。\\n未必慕容宫里伴，舞风歌月胜纤腰。\\n琥珀钗成恩正深，玉儿妖惑荡君心。\\n莫教回首看妆面，始觉曾虚掷万金。\\n自有闲花一面春，脸檀眉黛一时新。\\n殷勤为报梁家妇，休把啼妆赚后人。\\n轻梳小髻号慵来，巧中君心不用媒。\\n可得红儿抛醉眼，汉皇恩泽一时回。\\n千里长江旦暮潮，吴都风俗尚纤腰。\\n周郎若见红儿貌，料得无心念小乔。\\n月落潜奔暗解携，本心谁道独单栖。\\n还缘交甫非良偶，不肯终身作羿妻。\\n汉皇曾识许飞琼，写向人间作画屏。\\n昨日红儿花下见，大都相似更娉婷。\\n魏帝休夸薛夜来，雾绡云縠称身裁。\\n红儿秀发君知否，倚槛繁花带露开。\\n晓月雕梁燕语频，见花难可比他人。\\n年年媚景归何处，长作红儿面上春。\\n逗玉溅盆冬殿开，邀恩先赐夜明苔。\\n红儿若是三千数，多少芳心似死灰。\\n画帘垂地紫金床，暗引羊车驻七香。\\n若见红儿此中住，不劳盐筱洒宫廊。\\n苏小空匀一面妆，便留名字在钱塘。\\n藏鸦门外诸年少，不识红儿未是狂。\\n一首长歌万恨来，惹愁漂泊水难回。\\n崔徽有底多头面，费得微之尔许才。\\n昔年黄阁识奇章，爱说真珠似窈娘。\\n若见红儿深夜态，便应休说绣衣裳。\\n凤折莺离恨转深，此身难负百年心。\\n红儿若向隋朝见，破镜无因更重寻。\\n行绾秾云立暗轩，我来犹爱不成冤。\\n当时若见红儿貌，未必邢相有此言。\\n总似红儿媚态新，莫论千度笑争春。\\n任伊孙武心如铁，不办军前杀此人。\\n暖塘争赴荡舟期，行唱菱歌著艳词。\\n为问东山谢丞相，可能诸妓胜红儿。\\n吴兴皇后欲辞家，泽国重台展曙华。\\n今日红儿貌倾国，恐须真宰别开花。\\n陌上行人歌黍离，三千门客欲何之。\\n若教粗及红儿貌，争取楼前斩爱姬。\\n休话如皋一笑时，金髇中臆锦离披。\\n陋容枉把雕弓射，射尽春禽未展眉。\\n长恨西风送早秋，低眉深恨嫁牵牛。\\n若同人世长相对，争作夫妻得到头。\\n谢娘休漫逞风姿，未必娉婷胜柳枝。\\n闻道只因嘲落絮，何曾得似杜红儿。\\n总传桃叶渡江时，只为王家一首诗。\\n今日红儿自堪赋，不须重唱旧来词。\\n巫山洛浦本无情，总为佳人便得名。\\n今日雕阴有神艳，后来公子莫相轻。\\n几抛云髻恨金墉，泪洗花颜百战中。\\n应有红儿些子貌，却言皇后长深宫。\\n倚槛还应有所思，半开东阁见娇姿。\\n可中得似红儿貌，若遇韩朋好杀伊。\\n晓向妆台与画眉，镜中长欲助娇姿。\\n若教得似红儿貌，走马章台任道迟。\\n练得霜华助翠钿，相期朝谒玉皇前。\\n依稀有似红儿貌，方得吹箫引上天。\\n重门深掩几枝花，未胜红儿莫大夸。\\n王相不能探物理，可能虚上短辕车。\\n前代休怜事可奇，后来还出有光辉。\\n争知昼卧纱窗里，不见神人覆玉衣。\\n化羽尝闻赴九天，只疑尘世是虚传。\\n自从一见红儿貌，始信人间有谪仙。\\n从道长陵小市东，巧将花貌占春风。\\n红儿若是同时见，未必伊先入紫宫。\\n人间难免是深情，命断红儿向此生。\\n不似前时李丞相，枉抛才力为莺莺。\\n凤舞香飘绣幕风，暖穿驰道百花中。\\n还缘有似红儿貌，始道迎将入汉宫。\\n休道将军出世才，尽驱诸妓下歌台。\\n都缘没个红儿貌，致使轻教后阁开。\\n冯媛须知住汉宫，将身只是解当熊。\\n不闻有貌倾人国，争得今朝更似红。\\n能将一笑使人迷，花艳何须上大堤。\\n疏属便同巫峡路，洛川真是武陵溪。\\n辞辇当时意可知，宠深还恐宠先衰。\\n若教得似红儿貌，占却君恩自不疑。\\n三吴时俗重风光，未见红儿一面妆。\\n好写妖娆与教看，便应休更话真娘。\\n波平楚泽浸星辰，台上君王宴早春。\\n毕竟章华会中客，冠缨虚绝为何人。\\n红儿不向汉宫生，便使双成谩得名。\\n疑是麻姑恼尘世，暂教微步下层城。\\n天碧轻纱只六铢，宛如含露透肌肤。\\n便教汉曲争明媚，应没心情更弄珠。\\n共嗟含恨向衡阳，方寸花笺寄沈郎。\\n不似红儿些子貌，当时争得少年狂。\\n浅色桃花亚短墙，不因风送也闻香。\\n凝情尽日君知否，还似红儿淡薄妆。\\n火色樱桃摘得初，仙宫只有世间无。\\n凝情尽日君知否，真似红儿口上朱。\\n宿雨初晴春日长，入帘花气静难忘。\\n凝情尽日君知否，真似红儿舞袖香。\\n初月纤纤映碧池，池波不动独看时。\\n凝情尽日君知否，真似红儿罢舞眉。\\n浓艳浓香雪压枝，袅烟和露晓风吹。\\n红儿被掩妆成后，含笑无人独立时。\\n楼上娇歌袅夜霜，近来休数踏歌娘。\\n红儿漫唱伊州遍，认取轻敲玉韵长。\\n金粟妆成扼臂环，舞腰轻薄瑞云间。\\n红儿生在开元末，羞杀新丰谢阿蛮。\\n君看红儿学醉妆，夸裁宫襭砑裙长。\\n谁能更把闲心力，比并当时武媚娘。\\n栀子同心裛露垂，折来深恐没人知。\\n花前醉客频相问，不赠红儿赠阿谁。\\n云间翡翠一双飞，水上鸳鸯不暂离。\\n写向人间百般态，与君题作比红诗。\\n旧恨长怀不语中，几回偷泣向春风。\\n还缘不及红儿貌，却得生教入楚宫。\\n一舸春深指鄂君，好风从度水成纹。\\n越人若见红儿貌，绣被应羞彻夜薰。\\n花落尘中玉堕泥，香魂应上窈娘堤。\\n欲知此恨无穷处，长倩城乌夜夜啼。', '2018-03-09 15:43:09');
INSERT INTO `t_poem` VALUES ('4166', 'poem', '唐', '牟融', '客中作', '千里云山恋旧游，寒窗凉雨夜悠悠。\\n浮亭花竹频劳梦，别路风烟半是愁。\\n芳草傍人空对酒，流年多病倦登楼。\\n一杯重向樽前醉，莫遣相思累白头。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4167', 'poem', '唐', '罗隐', '归梦', '陆海波涛渐渐深，一回归梦抵千金。\\n路傍草色休多事，墙外莺声肯有心。\\n日晚向隅悲断梗，夜阑浇酒哭知音。\\n贪财败阵谁相悉，鲍叔如今不可寻。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4168', 'poem', '唐', '刘长卿', '送灵澈上人还越中', '禅客无心杖锡还，沃洲深处草堂闲。\\n身随敝屦经残雪，手绽寒衣入旧山。\\n独向青溪依树下，空留白日在人间。\\n那堪别后长相忆，云木苍苍但闭关。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4169', 'poem', '唐', '王勃', '郊兴', '空园歌独酌，春日赋闲居。\\n泽兰侵小径，河柳覆长渠。\\n雨去花光湿，风归叶影疏。\\n山人不惜醉，唯畏绿尊虚。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4170', 'poem', '唐', '许浑', '放猿', '殷勤解金锁，昨夜雨凄凄。\\n山浅忆巫峡，水寒思建溪。\\n远寻红树宿，深向白云啼。\\n好觅来时路，烟萝莫共迷。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4171', 'poem', '唐', '李颀', '题卢五旧居', '物在人亡无见期，闲庭系马不胜悲。\\n窗前绿竹生空地，门外青山如旧时。\\n怅望秋天鸣坠叶，巑岏枯柳宿寒鸱。\\n忆君泪落东流水，岁岁花开知为谁。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4172', 'poem', '唐', '孟浩然', '舟中晓望', '挂席东南望，青山水国遥。\\n舳舻争利涉，来往接风潮。\\n问我今何去，天台访石桥。\\n坐看霞色晓，疑是赤城标。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4173', 'poem', '唐', '韦庄', '观猎', '苑墙东畔欲斜晖，傍苑穿花兔正肥。\\n公子喜逢朝罢日，将军夸换战时衣。\\n鹘翻锦翅云中落，犬带金铃草上飞。\\n直待四郊高鸟尽，掉鞍齐向国门归。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4174', 'poem', '唐', '白居易', '送春', '三月三十日，春归日复暮。\\n惆怅问春风，明朝应不住。\\n送春曲江上，眷眷东西顾。\\n但见扑水花，纷纷不知数。\\n人生似行客，两足无停步。\\n日日进前程，前程几多路。\\n兵刀与水火，尽可违之去。\\n唯有老到来，人间无避处。\\n感时良为已，独倚池南树。\\n今日送春心，心如别亲故。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4175', 'poem', '唐', '常建', '白龙窟泛舟寄天台学道者', '夕映翠山深，馀晖在龙窟。\\n扁舟沧浪意，澹澹花影没。\\n西浮入天色，南望对云阙。\\n因忆莓苔峰，初阳濯玄发。\\n泉萝两幽映，松鹤间清越。\\n碧海莹子神，玉膏泽人骨。\\n忽然为枯木，微兴遂如兀。\\n应寂中有天，明心外无物。\\n环回从所泛，夜静犹不歇。\\n澹然意无限，身与波上月。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4176', 'poem', '唐', '方干', '赠美人四首', '直缘多艺用心劳，心路玲珑格调高。\\n舞袖低徊真蛱蝶，朱唇深浅假樱桃。\\n粉胸半掩疑晴雪，醉眼斜回小样刀。\\n才会雨云须别去，语惭不及琵琶槽。\\n严冬忽作看花日，盛暑翻为见雪时。\\n坐上弄娇声不转，尊前掩笑意难知。\\n含歌媚盼如桃叶，妙舞轻盈似柳枝。\\n年几未多犹怯在，些些私语怕人疑。\\n酒蕴天然自性灵，人间有艺总关情。\\n剥葱十指转筹疾，舞柳细腰随拍轻。\\n常恐胸前春雪释，惟愁座上庆云生。\\n若教梅尉无仙骨，争得仙娥驻玉京。\\n昔日仙人今玉人，深冬相见亦如春。\\n倍酬金价微含笑，才发歌声早动尘。\\n昔岁曾为萧史伴，今朝应作宋家邻。\\n百年别后知谁在，须遣丹青画取真。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4177', 'poem', '唐', '王勃', '春日还郊', '闲情兼嘿语，携杖赴岩泉。\\n草绿萦新带，榆青缀古钱。\\n鱼床侵岸水，鸟路入山烟。\\n还题平子赋，花树满春田。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4178', 'poem', '唐', '杜甫', '月', '四更山吐月，残夜水明楼。\\n尘匣元开镜，风帘自上钩。\\n兔应疑鹤发，蟾亦恋貂裘。\\n斟酌姮娥寡，天寒耐九秋。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4179', 'poem', '唐', '霍总', '采莲女', '舟中采莲女，两两催妆梳。\\n闻早渡江去，日高来起居。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4180', 'poem', '唐', '李中', '酒醒', '睡觉花阴芳草软，不知明月出墙东。\\n杯盘狼藉人何处，聚散空惊似梦中。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4181', 'poem', '唐', '王建', '上阳宫', '上阳花木不曾秋，洛水穿宫处处流。\\n画阁红楼宫女笑，玉箫金管路人愁。\\n幔城入涧橙花发，玉辇登山桂叶稠。\\n曾读列仙王母传，九天未胜此中游。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4182', 'poem', '唐', '白居易', '闻龟儿咏诗', '怜渠已解咏诗章，摇膝支颐学二郎。\\n莫学二郎吟太苦，才年四十鬓如霜。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4183', 'poem', '唐', '鱼玄机', '隔汉江寄子安', '江南江北愁望，相思相忆空吟。\\n鸳鸯暖卧沙浦，鸂鶒闲飞橘林。\\n烟里歌声隐隐，渡头月色沈沈。\\n含情咫尺千里，况听家家远砧。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4184', 'poem', '唐', '徐彦伯', '比干墓', '大位天下宝，维贤国之镇。殷道微而在，受辛纂颓胤。\\n山鸣鬼又哭，地裂川亦震。媟黩皆佞谀，虔刘尽英隽。\\n孤卿帝叔父，特进贞而顺。玉床逾皓洁，铜柱方歊焮，\\n奉国历三朝，观窍明一瞬。季代猖狂主，蓄怒提白刃。\\n之子弥忠谠，愤然更勇进。抚膺誓陨越，知死故不吝。\\n已矣竟剖心，哲妇亦同殉。骊龙暴双骨，太岳摧孤仞。\\n周发次商郊，冤骸悲莫殣。锋剑剿遗孽，报复一何迅。\\n驻罕歌淑灵，命徒封旅榇。自尔衔幽酷，于嗟流景骏。\\n丘坟被宿莽，坛戺缘飞磷。贞观戒北征，维皇念忠信。\\n荒坟护草木，刻桷吹煨烬。代远恩更崇，身颓名益振。\\n帝词书乐石，国馔罗芳衅。伟哉烈士图，奇英千古徇。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4185', 'poem', '唐', '刘商', '观猎三首', '梦非熊虎数年间，驱尽豺狼宇宙闲。\\n传道单于闻校猎，相期不敢过阴山。\\n日隐寒山猎未归，鸣弦落羽雪霏霏。\\n梁园射尽南飞雁，淮楚人惊阳鸟啼。\\n松月东轩许独游，深恩未报复淹留。\\n梁园日暮从公猎，每过青山不举头。', '2018-03-09 15:43:20');
INSERT INTO `t_poem` VALUES ('4186', 'poem', '唐', '陆希声', '阳羡杂咏十九首·桃花谷', '君阳山下足春风，满谷仙桃照水红。\\n何必武陵源上去，涧边好过落花中。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4187', 'poem', '唐', '李太玄', '玉女舞霓裳', '舞势随风散复收，歌声似磬韵还幽。\\n千回赴节填词处，娇眼如波入鬓流。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4188', 'poem', '唐', '司空图', '独坐', '幽径入桑麻，坞西逢一家。\\n编篱薪带茧，补屋草和花。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4189', 'poem', '唐', '顾况', '山中赠客', '山中好处无人别，涧梅伪作山中雪。\\n野客相逢夜不眠，山中童子烧松节。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4190', 'poem', '唐', '张文规', '句', '谁云隼旟吏，长对虎头岩。\\n。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4191', 'poem', '唐', '杜审言', '蓬莱三殿侍宴奉敕咏终南山应制', '北斗挂城边，南山倚殿前。\\n云标金阙迥，树杪玉堂悬。\\n半岭通佳气，中峰绕瑞烟。\\n小臣持献寿，长此戴尧天。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4192', 'poem', '唐', '白居易', '洪州逢熊孺登', '靖安院里辛夷下，醉笑狂吟气最粗。\\n莫问别来多少苦，低头看取白髭须。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4193', 'poem', '唐', '李群玉', '龟', '静养千年寿，重泉自隐居。\\n不应随跛鳖，宁肯滞凡鱼。\\n灵腹唯玄露，芳巢必翠蕖。\\n扬花输蚌蛤，奔月恨蟾蜍。\\n曳尾辞泥后，支床得水初。\\n冠山期不小，铸印事宁虚。\\n有志酬毛宝，无心畏豫且。\\n他时清洛汭，会荐帝尧书。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4194', 'poem', '唐', '骆宾王', '咏怀', '少年识事浅，不知交道难。\\n一言芬若桂，四海臭如兰。\\n宝剑思存楚，金锤许报韩。\\n虚心徒有托，循迹谅无端。\\n太息关山险，吁嗟岁月阑。\\n忘机殊会俗，守拙异怀安。\\n阮籍空长啸，刘琨独未欢。\\n十步庭芳敛，三秋陇月团。\\n槐疏非尽意，松晚夜凌寒。\\n悲调弦中急，穷愁醉里宽。\\n莫将流水引，空向俗人弹。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4195', 'poem', '唐', '秦韬玉', '隋堤', '种柳开河为胜游，堤前常使路人愁。\\n阴埋野色万条思，翠束寒声千里秋。\\n西日至今悲兔苑，东波终不反龙舟。\\n远山应见繁华事，不语青青对水流。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4196', 'poem', '唐', '张籍', '书怀寄王秘书', '白发如今欲满头，从来百事尽应休。\\n只于触目须防病，不拟将心更养愁。\\n下药远求新熟酒，看山多上最高楼。\\n赖君同在京城住，每到花前免独游。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4197', 'poem', '唐', '严武', '寄题杜拾遗锦江野亭', '漫向江头把钓竿，懒眠沙草爱风湍。\\n莫倚善题鹦鹉赋，何须不著鵕鸃冠。\\n腹中书籍幽时晒，肘后医方静处看。\\n兴发会能驰骏马，应须直到使君滩。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4198', 'poem', '唐', '崔涂', '孤雁', '几行归塞尽，念尔独何之。\\n暮雨相呼失，寒塘欲下迟。\\n渚云低暗度，关月冷相随。\\n未必逢矰缴，孤飞自可疑。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4199', 'poem', '唐', '杜甫', '过客相寻', '穷老真无事，江山已定居。\\n地幽忘盥栉，客至罢琴书。\\n挂壁移筐果，呼儿问煮鱼。\\n时闻系舟楫，及此问吾庐。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4200', 'poem', '唐', '崔珏', '岳阳楼晚望', '乾坤千里水云间，钓艇如萍去复还。\\n楼上北风斜卷席，湖中西日倒衔山。\\n怀沙有恨骚人往，鼓瑟无声帝子闲。\\n何事黄昏尚凝睇，数行烟树接荆蛮。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4201', 'poem', '唐', '陈光', '长安新柳', '九陌云初霁，皇衢柳已新。\\n不同天苑景，先得日边春。\\n色浅微含露，丝轻未惹尘。\\n一枝方欲折，归去及兹晨。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4202', 'poem', '唐', '白居易', '闲居', '肺病不饮酒，眼昏不读书。\\n端然无所作，身意闲有馀。\\n鸡栖篱落晚，雪映林木疏。\\n幽独已云极，何必山中居。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4203', 'poem', '唐', '鱼玄机', '江行', '大江横抱武昌斜，鹦鹉洲前户万家。\\n画舸春眠朝未足，梦为蝴蝶也寻花。\\n烟花已入鸬鹚港，画舸犹沿鹦鹉洲。\\n醉卧醒吟都不觉，今朝惊在汉江头。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4204', 'poem', '唐', '贾岛', '句', '晴风吹柳絮，新火起厨烟。\\n。\\n长江风送客，孤馆雨留人。\\n。\\n古岸崩欲尽，平沙长未休。\\n。\\n不如牛与羊，犹得日暮归。\\n。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4205', 'poem', '唐', '贾岛', '旅游', '此心非一事，书札若为传。\\n旧国别多日，故人无少年。\\n空巢霜叶落，疏牖水萤穿。\\n留得林僧宿，中宵坐默然。', '2018-03-09 15:43:31');
INSERT INTO `t_poem` VALUES ('4206', 'poem', '唐', '杜甫', '宿赞公房', '杖锡何来此，秋风已飒然。\\n雨荒深院菊，霜倒半池莲。\\n放逐宁违性，虚空不离禅。\\n相逢成夜宿，陇月向人圆。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4207', 'poem', '唐', '岑参', '北庭贻宗学士道别', '万事不可料，叹君在军中。\\n读书破万卷，何事来从戎。\\n曾逐李轻车，西征出太蒙。\\n荷戈月窟外，擐甲昆仑东。\\n两度皆破胡，朝廷轻战功。\\n十年只一命，万里如飘蓬。\\n容鬓老胡尘，衣裘脆边风。\\n忽来轮台下，相见披心胸。\\n饮酒对春草，弹棋闻夜钟。\\n今且还龟兹，臂上悬角弓。\\n平沙向旅馆，匹马随飞鸿。\\n孤城倚大碛，海气迎边空。\\n四月犹自寒，天山雪濛濛。\\n君有贤主将，何谓泣途穷。\\n时来整六翮，一举凌苍穹。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4208', 'poem', '唐', '杜甫', '独坐二首', '竟日雨冥冥，双崖洗更青。\\n水花寒落岸，山鸟暮过庭。\\n暖老须燕玉，充饥忆楚萍。\\n胡笳在楼上，哀怨不堪听。\\n白狗斜临北，黄牛更在东。\\n峡云常照夜，江月会兼风。\\n晒药安垂老，应门试小童。\\n亦知行不逮，苦恨耳多聋。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4209', 'poem', '唐', '元稹', '酒醒', '饮醉日将尽，醒时夜已阑。\\n暗灯风焰晓，春席水窗寒。\\n未解萦身带，犹倾坠枕冠。\\n呼儿问狼藉，疑是梦中欢。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4210', 'poem', '唐', '杜牧', '自宣城赴官上京', '潇洒江湖十过秋，酒杯无日不淹留。\\n谢公城畔溪惊梦，苏小门前柳拂头。\\n千里云山何处好，几人襟韵一生休。\\n尘冠挂却知闲事，终拟蹉跎访旧游。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4211', 'poem', '唐', '施肩吾', '夜愁曲', '歌者歌未绝，愁人愁转增。\\n空把琅玕枝，强挑无心灯。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4212', 'poem', '唐', '杜牧', '宣州开元寺南楼', '小楼才受一床横，终日看山酒满倾。\\n可惜和风夜来雨，醉中虚度打窗声。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4213', 'poem', '唐', '李贺', '梁公子', '风彩出萧家，本是菖蒲花。\\n南塘莲子熟，洗马走江沙。\\n御笺银沫冷，长簟凤窠斜。\\n种柳营中暗，题书赐馆娃。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4214', 'poem', '唐', '张文姬', '沙上鹭', '沙头一水禽，鼓翼扬清音。\\n只待高风便，非无云汉心。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4215', 'poem', '唐', '皮日休', '惠山听松庵', '千叶莲花旧有香，半山金刹照方塘。\\n殿前日暮高风起，松子声声打石床。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4216', 'poem', '唐', '李商隐', '泪', '永巷长年怨绮罗，离情终日思风波。\\n湘江竹上痕无限，岘首碑前洒几多。\\n人去紫台秋入塞，兵残楚帐夜闻歌。\\n朝来灞水桥边问，未抵青袍送玉珂。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4217', 'poem', '唐', '曹邺', '乐府体', '莲子房房嫩，菖蒲叶叶齐。\\n共结池中根，不厌池中泥。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4218', 'poem', '唐', '刘禹锡', '听旧宫中乐人穆氏唱歌', '曾随织女渡天河，记得云间第一歌。\\n休唱贞元供奉曲，当时朝士已无多。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4219', 'poem', '唐', '韩偓', '病忆', '信知尤物必牵情，一顾难酬觉命轻。\\n曾把禅机销此病，破除才尽又重生。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4220', 'poem', '唐', '刘禹锡', '咏红柿子', '晓连星影出，晚带日光悬。\\n本因遗采掇，翻自保天年。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4221', 'poem', '唐', '李白', '冬夜醉宿龙门，觉起言志', '醉来脱宝剑，旅憩高堂眠。\\n中夜忽惊觉，起立明灯前。\\n开轩聊直望，晓雪河冰壮。\\n哀哀歌苦寒，郁郁独惆怅。\\n傅说版筑臣，李斯鹰犬人。\\n欻起匡社稷，宁复长艰辛。\\n而我胡为者，叹息龙门下。\\n富贵未可期，殷忧向谁写。\\n去去泪满襟，举声梁甫吟。\\n青云当自致，何必求知音。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4222', 'poem', '唐', '佚名', '薛稷书语', '买褚得薛不落节。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4223', 'poem', '唐', '李贺', '杨生青花紫石砚歌', '端州石工巧如神，踏天磨刀割紫云。\\n佣刓抱水含满唇，暗洒苌弘冷血痕。\\n纱帷昼暖墨花春，轻沤漂沫松麝薰。\\n干腻薄重立脚匀，数寸光秋无日昏。\\n圆毫促点声静新，孔砚宽顽何足云。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4224', 'poem', '唐', '李中', '所思', '门掩残花寂寂，帘垂斜月悠悠。\\n纵有一庭萱草，何曾与我忘忧。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4225', 'poem', '唐', '李贺', '帝子歌', '洞庭明月一千里，凉风雁啼天在水。\\n九节菖蒲石上死，湘神弹琴迎帝子。\\n山头老桂吹古香，雌龙怨吟寒水光。\\n沙浦走鱼白石郎，闲取真珠掷龙堂。', '2018-03-09 15:43:36');
INSERT INTO `t_poem` VALUES ('4226', 'poem', '唐', '李世民', '咏小山', '近谷交萦蕊，遥峰对出莲。\\n径细无全磴，松小未含烟。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4227', 'poem', '唐', '韩偓', '两处', '楼上澹山横，楼前沟水清。\\n怜山又怜水，两处总牵情。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4228', 'poem', '唐', '杜牧', '题刘秀才新竹', '数茎幽玉色，晓夕翠烟分。\\n声破寒窗梦，根穿绿藓纹。\\n渐笼当槛日，欲碍入帘云。\\n不是山阴客，何人爱此君。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4229', 'poem', '唐', '杜荀鹤', '夏日题悟空上人院', '三伏闭门披一衲，兼无松竹荫房廊。\\n安禅不必须山水，灭得心中火自凉。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4230', 'poem', '唐', '宋之问', '题张老松树', '岁晚东岩下，周顾何凄恻。\\n日落西山阴，众草起寒色。\\n中有乔松树，使我长叹息。\\n百尺无寸枝，一生自孤直。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4231', 'poem', '唐', '韩偓', '新秋', '一夜清风动扇愁，背时容色入新秋。\\n桃花脸里汪汪泪，忍到更深枕上流。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4232', 'poem', '唐', '黄滔', '二月二日宴中贻同年封先辈渭', '帝尧城里日衔杯，每倚嵇康到玉颓。\\n桂苑五更听榜后，蓬山二月看花开。\\n垂名入甲成龙去，列姓如丁作鹤来。\\n同戴大恩何处报，永言交道契陈雷。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4233', 'poem', '唐', '刘禹锡', '答白刑部闻新蝉', '蝉声未发前，已自感流年。\\n一入凄凉耳，如闻断续弦。\\n晴清依露叶，晚急畏霞天。\\n何事秋卿咏，逢时亦悄然。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4234', 'poem', '唐', '佚名', '杂曲歌辞·水调歌第一', '平沙落日大荒西，陇上明星高复低。\\n孤山几处看烽火，壮士连营候鼓鼙。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4235', 'poem', '唐', '许浑', '姑苏怀古', '宫馆余基辍棹过，黍苗无限独悲歌。\\n荒台麋鹿争新草，空苑岛凫占浅莎。\\n吴岫雨来虚槛冷，楚江风急远帆多。\\n可怜国破忠臣死，日月东流生白波。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4236', 'poem', '唐', '司空图', '漫书五首', '长拟求闲未得闲，又劳行役出秦关。\\n逢人渐觉乡音异，却恨莺声似故山。\\n溪边随事有桑麻，尽日山程十数家。\\n莫怪行人频怅望，杜鹃不是故乡花。\\n海上昔闻麋爱鹤，山中今日鹿憎龟。\\n爱憎止竟须关分，莫把微才望所知。\\n世路快心无好事，恩门嘉话合书绅。\\n神藏鬼伏能千变，亦胜忘机避要津。\\n四翁识势保安闲，须为生灵暂出山。\\n一种老人能算度，磻溪心迹愧商颜。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4237', 'poem', '唐', '李中', '采莲女', '晚凉含笑上兰舟，波底红妆影欲浮。\\n陌上少年休植足，荷香深处不回头。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4238', 'poem', '唐', '汪遵', '比干墓', '国乱时危道不行，忠贤谏死胜谋生。\\n一沉冤骨千年后，垄水虽平恨未平。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4239', 'poem', '唐', '杜甫', '秋风二首', '秋风淅淅吹巫山，上牢下牢修水关。\\n吴樯楚柁牵百丈，暖向神都寒未还。\\n要路何日罢长戟，战自青羌连百蛮。\\n中巴不曾消息好，暝传戍鼓长云间。\\n秋风淅淅吹我衣，东流之外西日微。\\n天清小城捣练急，石古细路行人稀。\\n不知明月为谁好，早晚孤帆他夜归。\\n会将白发倚庭树，故园池台今是非。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4240', 'poem', '唐', '杜甫', '阆水歌', '嘉陵江色何所似，石黛碧玉相因依。\\n正怜日破浪花出，更复春从沙际归。\\n巴童荡桨欹侧过，水鸡衔鱼来去飞。\\n阆中胜事可肠断，阆州城南天下稀。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4241', 'poem', '唐', '李白', '江行寄远', '刳木出吴楚，危槎百馀尺。\\n疾风吹片帆，日暮千里隔。\\n别时酒犹在，已为异乡客。\\n思君不可得，愁见江水碧。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4242', 'poem', '唐', '罗邺', '早发宜陵即事', '霜白山村月落时，一声鸡后又登岐。\\n居人犹自掩关在，行客已愁驱马迟。\\n身事不堪空感激，鬓毛看著欲凋衰。\\n青萍委匣休哮吼，未有恩仇拟报谁。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4243', 'poem', '唐', '张祜', '观徐州李司空猎', '晓出郡城东，分围浅草中。\\n红旗开向日，白马骤迎风。\\n背手抽金镞，翻身控角弓。\\n万人齐指处，一雁落寒空。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4244', 'poem', '唐', '张九龄', '秋夕望月', '清迥江城月，流光万里同。\\n所思如梦里，相望在庭中。\\n皎洁青苔露，萧条黄叶风。\\n含情不得语，频使桂华空。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4245', 'poem', '唐', '卢纶', '送万巨', '把酒留君听琴，难堪岁暮离心。\\n霜叶无风自落，秋云不雨空阴。\\n人愁荒村路细，马怯寒溪水深。\\n望断青山独立，更知何处相寻。', '2018-03-09 15:43:44');
INSERT INTO `t_poem` VALUES ('4246', 'poem', '唐', '岑参', '韦员外家花树歌', '今年花似去年好，去年人到今年老。\\n始知人老不如花，可惜落花君莫扫。\\n君家兄弟不可当，列卿御史尚书郎。\\n朝回花底恒会客，花扑玉缸春酒香。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4247', 'poem', '唐', '温庭筠', '嘲春风', '春风何处好，别殿饶芳草。\\n苒袅转鸾旗，萎蕤吹雉葆。\\n扬芳历九门，澹荡入兰荪。\\n争奈白团扇，时时偷主恩。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4248', 'poem', '唐', '李中', '途中闻子规', '春残杜宇愁，越客思悠悠。\\n雨歇孤村里，花飞远水头。\\n微风声渐咽，高树血应流。\\n因此频回首，家山隔几州。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4249', 'poem', '唐', '白居易', '中秋月', '万里清光不可思，添愁益恨绕天涯，谁人陇外久征戍，\\n何处庭前新别离。失宠故姬归院夜，没蕃老将上楼时。\\n照他几许人肠断，玉兔银蟾远不知。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4250', 'poem', '唐', '李中', '客中寒食', '旅次经寒食，思乡泪湿巾。\\n音书天外断，桃李雨中春。\\n欲饮都无绪，唯吟似有因。\\n输他郊郭外，多少踏青人。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4251', 'poem', '唐', '白居易', '司马宅', '雨径绿芜合，霜园红叶多。\\n萧条司马宅，门巷无人过。\\n唯对大江水，秋风朝夕波。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4252', 'poem', '唐', '杜甫', '长江二首', '众水会涪万，瞿塘争一门。\\n朝宗人共挹，盗贼尔谁尊。\\n孤石隐如马，高萝垂饮猿。\\n归心异波浪，何事即飞翻。\\n浩浩终不息，乃知东极临。\\n众流归海意，万国奉君心。\\n色借潇湘阔，声驱滟滪深。\\n未辞添雾雨，接上遇衣襟。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4253', 'poem', '唐', '郑愔', '春怨', '春朝物候妍，愁妇镜台前。\\n风吹数蝶乱，露洗百花鲜。\\n试出褰罗幌，还来著锦筵。\\n曲中愁夜夜，楼上别年年。\\n不及随萧史，高飞向紫烟。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4254', 'poem', '唐', '胡曾', '咏史诗·嶓冢', '夏禹崩来一万秋，水从嶓冢至今流。\\n当时若诉胼胝苦，更使何人别九州。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4255', 'poem', '唐', '李遐周', '题壁', '燕市人皆去，函关马不归。\\n若逢山下鬼，环上系罗衣。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4256', 'poem', '唐', '齐己', '早莺', '何处经年閟好音，暖风催出啭乔林。\\n羽毛新刷陶潜菊，喉舌初调叔夜琴。\\n藏雨并栖红杏密，避人双入绿杨深。\\n晓来枝上千般语，应共桃花说旧心。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4257', 'poem', '唐', '李山甫', '月', '狡兔顽蟾死复生，度云经汉澹还明。\\n夜长虽耐对君坐，年少不禁随尔行。\\n玉桂影摇乌鹊动，金波寒注鬼神惊。\\n人间半被虚抛掷，唯向孤吟客有情。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4258', 'poem', '唐', '王昌龄', '朝来曲', '月昃鸣珂动，花连绣户春。\\n盘龙玉台镜，唯待画眉人。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4259', 'poem', '唐', '王维', '蓝田山石门精舍', '落日山水好，漾舟信归风。\\n探奇不觉远，因以缘源穷。\\n遥爱云木秀，初疑路不同。\\n安知清流转，偶与前山通。\\n舍舟理轻策，果然惬所适。\\n老僧四五人，逍遥荫松柏。\\n朝梵林未曙，夜禅山更寂。\\n道心及牧童，世事问樵客。\\n暝宿长林下，焚香卧瑶席。\\n涧芳袭人衣，山月映石壁。\\n再寻畏迷误，明发更登历。\\n笑谢桃源人，花红复来觌。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4260', 'poem', '唐', '李商隐', '岳阳楼', '欲为平生一散愁，洞庭湖上岳阳楼。\\n可怜万里堪乘兴，枉是蛟龙解覆舟。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4261', 'poem', '唐', '方干', '衢州别李秀才', '千山红树万山云，把酒相看日又曛。\\n一曲骊歌两行泪，更知何处再逢君。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4262', 'poem', '唐', '徐凝', '观浙江涛', '浙江悠悠海西绿，惊涛日夜两翻覆。\\n钱塘郭里看潮人，直至白头看不足。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4263', 'poem', '唐', '胡曾', '咏史诗·吴宫', '草长黄池千里馀，归来宗庙已丘墟。\\n出师不听忠臣谏，徒耻穷泉见子胥。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4264', 'poem', '唐', '施肩吾', '山石榴花', '深色胭脂碎剪红，巧能攒合是天公。\\n莫言无物堪相比，妖艳西施春驿中。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4265', 'poem', '唐', '李白', '姑孰十咏·谢公宅', '青山日将暝，寂寞谢公宅。\\n竹里无人声，池中虚月白。\\n荒庭衰草遍，废井苍苔积。\\n惟有清风闲，时时起泉石。', '2018-03-09 15:43:51');
INSERT INTO `t_poem` VALUES ('4266', 'poem', '唐', '王勃', '寒夜怀友杂体二首', '北山烟雾始茫茫，南津霜月正苍苍。\\n秋深客思纷无已，复值征鸿中夜起。\\n复閤重楼向浦开，秋风明月度江来。\\n故人故情怀故宴，相望相思不相见。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4267', 'poem', '唐', '朱绛', '春女怨', '独坐纱窗刺绣迟，紫荆花下啭黄鹂。\\n欲知无限伤春意，尽在停针不语时。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4268', 'poem', '唐', '韦应物', '淮上即事，寄广陵亲故', '前舟已眇眇，欲渡谁相待。\\n秋山起暮钟，楚雨连沧海。\\n风波离思满，宿昔容鬓改。\\n独鸟下东南，广陵何处在。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4269', 'poem', '唐', '郑谷', '牡丹', '画堂帘卷张清宴，含香带雾情无限。\\n春风爱惜未放开，柘枝鼓振红英绽。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4270', 'poem', '唐', '韩愈', '题张十一旅舍三咏·井', '贾谊宅中今始见，葛洪山下昔曾窥。\\n寒泉百尺空看影，正是行人渴死时。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4271', 'poem', '唐', '杜牧', '题商山四皓庙一绝', '吕氏强梁嗣子柔，我于天性岂恩雠。\\n南军不袒左边袖，四老安刘是灭刘。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4272', 'poem', '唐', '程贺', '君山', '曾游方外见麻姑，说道君山此本无。\\n云是昆仑山顶石，海风吹落洞庭湖。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4273', 'poem', '唐', '王初', '早春咏雪', '句芒宫树已先开，珠蕊琼花斗剪裁。\\n散作上林今夜雪，送教春色一时来。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4274', 'poem', '唐', '王绩', '独坐', '问君樽酒外，独坐更何须。\\n有客谈名理，无人索地租。\\n三男婚令族，五女嫁贤夫。\\n百年随分了，未羡陟方壶。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4275', 'poem', '唐', '杜甫', '存殁口号二首', '席谦不见近弹棋，毕曜仍传旧小诗。\\n玉局他年无限笑，白杨今日几人悲。\\n郑公粉绘随长夜，曹霸丹青已白头。\\n天下何曾有山水，人间不解重骅骝。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4276', 'poem', '唐', '无名氏', '题童氏画', '林下材华虽可尚，笔端人物更清妍。\\n如何不出深闺里，能以丹青写外边。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4277', 'poem', '唐', '耿湋', '登鹳雀楼', '久客心常醉，高楼日渐低。\\n黄河经海内，华岳镇关西。\\n去远千帆小，来迟独鸟迷。\\n终年不得意，空觉负东溪。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4278', 'poem', '唐', '李敬方', '汴河直进船', '汴水通淮利最多，生人为害亦相和。\\n东南四十三州地，取尽膏脂是此河。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4279', 'poem', '唐', '殷尧藩', '吴宫', '吴王爱歌舞，夜夜醉婵娟。\\n见日吹红烛，和尘扫翠钿。\\n徒令勾践霸，不信子胥贤。\\n莫问长洲草，荒凉无限年。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4280', 'poem', '唐', '胡宿', '采莲女', '碧盖缃葩映素秋，此中兰櫂好销忧。\\n相将共挈青丝笼，一笑同回翠羽舟。\\n日暮鸣榔随下濑，夜深归櫂怯中流。\\n东方自有罗敷婿，不顾江边越鄂州。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4281', 'poem', '唐', '杜甫', '杨监又出画鹰十二扇', '近时冯绍正，能画鸷鸟样。\\n明公出此图，无乃传其状。\\n殊姿各独立，清绝心有向。\\n疾禁千里马，气敌万人将。\\n忆昔骊山宫，冬移含元仗。\\n天寒大羽猎，此物神俱王。\\n当时无凡材，百中皆用壮。\\n粉墨形似间，识者一惆怅。\\n干戈少暇日，真骨老崖嶂。\\n为君除狡兔，会是翻鞲上。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4282', 'poem', '唐', '李百药', '秋晚登古城', '日落征途远，怅然临古城。\\n颓墉寒雀集，荒堞晚乌惊。\\n萧森灌木上，迢递孤烟生。\\n霞景焕余照，露气澄晚清。\\n秋风转摇落，此志安可平！', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4283', 'poem', '唐', '钱起', '送外甥怀素上人归乡侍奉', '释子吾家宝，神清慧有馀。\\n能翻梵王字，妙尽伯英书。\\n远鹤无前侣，孤云寄太虚。\\n狂来轻世界，醉里得真如。\\n飞锡离乡久，宁亲喜腊初。\\n故池残雪满，寒柳霁烟疏。\\n寿酒还尝药，晨餐不荐鱼。\\n遥知禅诵外，健笔赋闲居。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4284', 'poem', '唐', '李颀', '粲公院各赋一物得初荷', '微风和众草，大叶长圆阴。\\n晴露珠共合，夕阳花映深。\\n从来不著水，清净本因心。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4285', 'poem', '唐', '李山甫', '上元怀古二首', '南朝天子爱风流，尽守江山不到头。\\n总是战争收拾得，却因歌舞破除休。\\n尧行道德终无敌，秦把金汤可自由。\\n试问繁华何处有，雨苔烟草古城秋。\\n争帝图王德尽衰，骤兴驰霸亦何为。\\n君臣都是一场笑，家国共成千载悲。\\n排岸远樯森似槊，落波残照赫如旗。\\n今朝城上难回首，不见楼船索战时。', '2018-03-09 15:43:58');
INSERT INTO `t_poem` VALUES ('4286', 'poem', '唐', '黄滔', '别友人', '已喜相逢又怨嗟，十年飘泊在京华。\\n大朝多事还停举，故国经荒未有家。\\n鸟带夕阳投远树，人冲腊雪往边沙。\\n梦魂空系潇湘岸，烟水茫茫芦苇花。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4287', 'poem', '唐', '李峤', '中秋月二首', '盈缺青冥外，东风万古吹。\\n何人种丹桂，不长出轮枝。\\n圆魄上寒空，皆言四海同。\\n安知千里外，不有雨兼风。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4288', 'poem', '唐', '白居易', '题元八溪居', '溪岚漠漠树重重，水槛山窗次第逢。\\n晚叶尚开红踯躅，秋芳初结白芙蓉。\\n声来枕上千年鹤，影落杯中五老峰。\\n更愧殷勤留客意，鱼鲜饭细酒香浓。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4289', 'poem', '唐', '耿湋', '咏宣州笔', '寒竹惭虚受，纤毫任几重。\\n影端缘守直，心劲懒藏锋。\\n落纸惊风起，摇空见露浓。\\n丹青与文事，舍此复何从。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4290', 'poem', '唐', '张蠙', '雨', '半夜西亭雨，离人独启关。\\n桑麻荒旧国，雷电照前山。\\n细滴高槐底，繁声叠漏间。\\n唯应孤镜里，明月长愁颜。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4291', 'poem', '唐', '徐夤', '牡丹花二首', '看遍花无胜此花，剪云披雪蘸丹砂。\\n开当青律二三月，破却长安千万家。\\n天纵秾华刳鄙吝，春教妖艳毒豪奢。\\n不随寒令同时放，倍种双松与辟邪。\\n万万花中第一流，浅霞轻染嫩银瓯。\\n能狂绮陌千金子，也惑朱门万户侯。\\n朝日照开携酒看，暮风吹落绕栏收。\\n诗书满架尘埃扑，尽日无人略举头。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4292', 'poem', '唐', '王昌龄', '潞府客亭寄崔凤童', '萧条郡城闭，旅馆空寒烟。\\n秋月对愁客，山钟摇暮天。\\n新知偶相访，斗酒情依然。\\n一宿阻长会，清风徒满川。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4293', 'poem', '唐', '王驾', '夏雨', '非惟消旱暑，且喜救生民。\\n天地如蒸湿，园林似却春。\\n洗风清枕簟，换夜失埃尘。\\n又作丰年望，田夫笑向人。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4294', 'poem', '唐', '储嗣宗', '吴宫', '荒台荆棘多，忠谏竟如何。\\n细草迷宫巷，闲花误绮罗。\\n前溪徒自绿，子夜不闻歌。\\n怅望清江暮，悠悠东去波。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4295', 'poem', '唐', '李商隐', '回中牡丹为雨所败', '浪笑榴花不及春，先期零落更愁人。\\n玉盘迸泪伤心数，锦瑟惊弦破梦频。\\n万里重阴非旧圃，一年生意属流尘。\\n前溪午罢君回顾，并觉今朝粉态新。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4296', 'poem', '唐', '李白', '赠参寥子', '白鹤飞天书，南荆访高士。\\n五云在岘山，果得参寥子。\\n肮脏辞故园，昂藏入君门。\\n天子分玉帛，百官接话言。\\n毫墨时洒落，探玄有奇作。\\n著论穷天人，千春秘麟阁。\\n长揖不受官，拂衣归林峦。\\n余亦去金马，藤萝同所欢。\\n相思在何处，桂树青云端。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4297', 'poem', '唐', '韩愈', '幽怀', '幽怀不能写，行此春江浔。\\n适与佳节会，士女竞光阴。\\n凝妆耀洲渚，繁吹荡人心。\\n间关林中鸟，亦知和为音。\\n岂无一尊酒，自酌还自吟。\\n但悲时易失，四序迭相侵。\\n我歌君子行，视古犹视今。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4298', 'poem', '唐', '殷琮', '登云梯', '碧落远澄澄，青山路可升。\\n身轻疑易蹋，步独觉难凭。\\n逦迤排将近，回翔势渐登。\\n上宁愁屈曲，高更喜超腾。\\n江树遥分蔼，山岚宛若凝。\\n赤城容许到，敢惮百千层。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4299', 'poem', '唐', '宋鼎', '赠张丞相', '汉上登飞幰，荆南历旧居。\\n已尝临砌橘，更睹跃池鱼。\\n盛德继微渺，深衷能卷舒。\\n义申蓬阁际，情切庙堂初。\\n郡挹文章美，人怀燮理馀。\\n皇恩傥照亮，岂厌承明庐。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4300', 'poem', '唐', '白居易', '纳粟', '有吏夜叩门，高声催纳粟。\\n家人不待晓，场上张灯烛。\\n扬簸净如珠，一车三十斛。\\n犹忧纳不中，鞭责及僮仆。\\n昔余谬从事，内愧才不足。\\n连授四命官，坐尸十年禄。\\n常闻古人语，损益周必复。\\n今日谅甘心，还他太仓谷。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4301', 'poem', '唐', '熊皎', '早梅', '江南近腊时，已亚雪中枝。\\n一夜欲开尽，百花犹未知。\\n人情皆共惜，天意欲教迟。\\n莫讶无浓艳，芳筵正好吹。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4302', 'poem', '唐', '陆龟蒙', '丁香', '江上悠悠人不问，十年云外醉中身。\\n殷勤解却丁香结，纵放繁枝散诞春。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4303', 'poem', '唐', '李贺', '出城', '雪下桂花稀，啼乌被弹归。\\n关水乘驴影，秦风帽带垂。\\n入乡试万里，无印自堪悲。\\n卿卿忍相问，镜中双泪姿。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4304', 'poem', '唐', '李白', '赋得白鹭鸶送宋少府入三峡', '白鹭拳一足，月明秋水寒。\\n人惊远飞去，直向使君滩。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4305', 'poem', '唐', '杜甫', '从韦二明府续处觅绵竹', '华轩蔼蔼他年到，绵竹亭亭出县高。\\n江上舍前无此物，幸分苍翠拂波涛。', '2018-03-09 15:44:10');
INSERT INTO `t_poem` VALUES ('4306', 'poem', '唐', '李建勋', '清明日', '他皆携酒寻芳去，我独关门好静眠。\\n唯有杨花似相觅，因风时复到床前。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4307', 'poem', '唐', '司空曙', '黄子陂', '岸芳春色晓，水影夕阳微。\\n寂寂深烟里，渔舟夜不归。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4308', 'poem', '唐', '蒋维翰', '春女怨', '白玉堂前一树梅，今朝忽见数花开。\\n儿家门户寻常闭，春色因何入得来。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4309', 'poem', '唐', '汤洙', '登云梯', '谢客常游处，层峦枕碧溪。\\n经过殊俗境，登陟象云梯。\\n步步劳山屐，行行蹑涧霓。\\n迥临天路广，俯眺夕阳低。\\n赏咏情弥惬，风尘事已睽。\\n前修如可慕，投足固思齐。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4310', 'poem', '唐', '成彦雄', '杜鹃花', '杜鹃花与鸟，怨艳两何赊。\\n疑是口中血，滴成枝上花。\\n一声寒食夜，数朵野僧家。\\n谢豹出不出，日迟迟又斜。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4311', 'poem', '唐', '常建', '送宇文六', '花映垂杨汉水清，微风林里一枝轻。\\n即今江北还如此，愁杀江南离别情。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4312', 'poem', '唐', '李咸用', '庐山', '非岳不言岳，此山通岳言。\\n高人居乱世，几处满前轩。\\n秀作神仙宅，灵为风雨根。\\n馀阴铺楚甸，一柱表吴门。\\n静得八公侣，雄临九子尊。\\n对犹青熨眼，到必冷凝魂。\\n势受重湖让，形难七泽吞。\\n黑岩藏昼电，紫雾泛朝暾。\\n莲堕宁唯华，玉焚堪小昆。\\n倒松微发罅，飞瀑远成痕。\\n叠见云容衬，棱收雪气昏。\\n裁诗曾困谢，作赋偶无孙。\\n流碍星光撇，惊冲雁阵翻。\\n峰奇寒倚剑，泉曲旋如盆。\\n草短分雏雉，林明露掷猿。\\n秋枫红叶散，春石谷雷奔。\\n月好虎溪路，烟深栗里源。\\n醉吟长易醒，梦去亦销烦。\\n有觉南方重，无疑厚地掀。\\n轻扬闻旧俗，端用镇元元。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4313', 'poem', '唐', '杜牧', '偶题', '道在人间或可传，小还轻变已多年。\\n今来海上升高望，不到蓬莱不是仙。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4314', 'poem', '唐', '杨巨源', '崔娘诗', '清润潘郎玉不如，中庭蕙草雪消初。\\n风流才子多春思，肠断萧娘一纸书。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4315', 'poem', '唐', '贯休', '秋过钱塘江', '巨浸东隅极，山吞大野平。\\n因知吴相恨，不尽海涛声。\\n黑气腾蛟窟，秋云入战城。\\n游人千万里，过此白髭生。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4316', 'poem', '唐', '刘禹锡', '和令狐相公别牡丹', '平章宅里一栏花，临到开时不在家。\\n莫道两京非远别，春明门外即天涯。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4317', 'poem', '唐', '陆凭', '咏浮云', '虚虚复空空，瞬息天地中。\\n假合成此像，吾亦非吾躬。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4318', 'poem', '唐', '杜牧', '遣兴', '镜弄白髭须，如何作老夫。\\n浮生长勿勿，儿小且呜呜。\\n忍过事堪喜，泰来忧胜无。\\n治平心径熟，不遣有穷途。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4319', 'poem', '唐', '吕岩', '题凤翔府天庆观', '得道年来八百秋，不曾飞剑取人头。\\n玉皇未有天符至，且货乌金混世流。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4320', 'poem', '唐', '李商隐', '雨', '摵摵度瓜园，依依傍竹轩。\\n秋池不自冷，风叶共成喧。\\n窗迥有时见，檐高相续翻。\\n侵宵送书雁，应为稻粱恩。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4321', 'poem', '唐', '李商隐', '武夷山', '只得流霞酒一杯，空中箫鼓几时回。\\n武夷洞里生毛竹，老尽曾孙更不来。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4322', 'poem', '唐', '孟浩然', '他乡七夕', '他乡逢七夕，旅馆益羁愁。\\n不见穿针妇，空怀故国楼。\\n绪风初减热，新月始临秋。\\n谁忍窥河汉，迢迢问斗牛。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4323', 'poem', '唐', '章孝标', '送金可纪归新罗', '登唐科第语唐音，望日初生忆故林。\\n鲛室夜眠阴火冷，蜃楼朝泊晓霞深。\\n风高一叶飞鱼背，潮净三山出海心。\\n想把文章合夷乐，蟠桃花里醉人参。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4324', 'poem', '唐', '白居易', '正月十五日夜月', '岁熟人心乐，朝游复夜游。\\n春风来海上，明月在江头。\\n灯火家家市，笙歌处处楼。\\n无妨思帝里，不合厌杭州。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4325', 'poem', '唐', '卢纶', '春日题杜叟山下别业', '白鸟群飞山半晴，渚田相接有泉声。\\n园中晓露青丛合，桥上春风绿野明。\\n云影断来峰影出，林花落尽草花生。\\n今朝醉舞同君乐，始信幽人不爱荣。', '2018-03-09 15:44:22');
INSERT INTO `t_poem` VALUES ('4326', 'poem', '唐', '郎士元', '夜泊湘江', '湘山木落洞庭波，湘水连云秋雁多。\\n寂寞舟中谁借问，月明只自听渔歌。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4327', 'poem', '唐', '李中', '夕阳', '影未沈山水面红，遥天雨过促征鸿。\\n魂销举子不回首，闲照槐花驿路中。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4328', 'poem', '唐', '徐月英', '送人', '惆怅人间万事违，两人同去一人归。\\n生憎平望亭前水，忍照鸳鸯相背飞。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4329', 'poem', '唐', '吴融', '金桥感事', '太行和雪叠晴空，二月郊原尚朔风。\\n饮马早闻临渭北，射雕今欲过山东。\\n百年徒有伊川叹，五利宁无魏绛功？\\n日暮长亭正愁绝，哀笳一曲戍烟中。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4330', 'poem', '唐', '王勃', '羁春', '客心千里倦，春事一朝归。\\n还伤北园里，重见落花飞。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4331', 'poem', '唐', '孟郊', '晓鹤', '晓鹤弹古舌，婆罗门叫音。\\n应吹天上律，不使尘中寻。\\n虚空梦皆断，歆唏安能禁。\\n如开孤月口，似说明星心。\\n既非人间韵，枉作人间禽。\\n不如相将去，碧落窠巢深。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4332', 'poem', '唐', '邵谒', '岁丰', '皇天降丰年，本忧贫士食。\\n贫士无良畴，安能得稼穑。\\n工佣输富家，日落长叹息。\\n为供豪者粮，役尽匹夫力。\\n天地莫施恩，施恩强者得。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4333', 'poem', '唐', '谭用之', '赠索处士', '不将桂子种诸天，长得寻君水石边。\\n玄豹夜寒和雾隐，骊龙春暖抱珠眠。\\n山中宰相陶弘景，洞里真人葛稚川。\\n一度相思一惆怅，水寒烟澹落花前。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4334', 'poem', '唐', '翁绶', '关山月', '裴回汉月满边州，照尽天涯到陇头。\\n影转银河寰海静，光分玉塞古今愁。\\n笳吹远戍孤烽灭，雁下平沙万里秋。\\n况是故园摇落夜，那堪少妇独登楼。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4335', 'poem', '唐', '鱼玄机', '寄国香', '旦夕醉吟身，相思又此春。\\n雨中寄书使，窗下断肠人。\\n山卷珠帘看，愁随芳草新。\\n别来清宴上，几度落梁尘。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4336', 'poem', '唐', '郑谷', '朝谒', '捧日整朝簪，千官一片心。\\n班趋黄道急，殿接紫宸深。\\n威凤回香扆，新莺啭上林。\\n小松含瑞露，春翠易成阴。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4337', 'poem', '唐', '王建', '寄旧山僧', '因依老宿发心初，半学修心半读书。\\n雪后每常同席卧，花时未省两山居。\\n猎人箭底求伤雁，钓户竿头乞活鱼。\\n一向风尘取烦恼，不知衰病日难除。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4338', 'poem', '唐', '钱起', '山花', '山花照坞复烧溪，树树枝枝尽可迷。\\n野客未来枝畔立，流莺已向树边啼。\\n从容只是愁风起，眷恋常须向日西。\\n别有妖妍胜桃李，攀来折去亦成蹊。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4339', 'poem', '唐', '李约', '岁日感怀', '曙气变东风，蟾壶夜漏穷。\\n新春几人老，旧历四时空。\\n身贱悲添岁，家贫喜过冬。\\n称觞惟有感，欢庆在儿童。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4340', 'poem', '唐', '白居易', '湖亭晚归', '尽日湖亭卧，心闲事亦稀。\\n起因残醉醒，坐待晚凉归。\\n松雨飘藤帽，江风透葛衣。\\n柳堤行不厌，沙软絮霏霏。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4341', 'poem', '唐', '韦应物', '寺居独夜，寄崔主簿', '幽人寂不寐，木叶纷纷落。\\n寒雨暗深更，流萤度高阁。\\n坐使青灯晓，还伤夏衣薄。\\n宁知岁方晏，离居更萧索。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4342', 'poem', '唐', '杜甫', '归梦', '道路时通塞，江山日寂寥。\\n偷生唯一老，伐叛已三朝。\\n雨急青枫暮，云深黑水遥。\\n梦归归未得，不用楚辞招。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4343', 'poem', '唐', '杜牧', '自贻', '杜陵萧次君，迁少去官频。\\n寂寞怜吾道，依稀似古人。\\n饰心无彩缋，到骨是风尘。\\n自嫌如匹素，刀尺不由身。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4344', 'poem', '唐', '韦应物', '自巩洛舟行入黄河即事，寄府县僚友', '夹水苍山路向东，东南山豁大河通。\\n寒树依微远天外，夕阳明灭乱流中。\\n孤村几岁临伊岸，一雁初晴下朔风。\\n为报洛桥游宦侣，扁舟不系与心同。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4345', 'poem', '唐', '沈佺期', '游少林寺', '长歌游宝地，徙倚对珠林。\\n雁塔风霜古，龙池岁月深。\\n绀园澄夕霁，碧殿下秋阴。\\n归路烟霞晚，山蝉处处吟。', '2018-03-09 15:44:28');
INSERT INTO `t_poem` VALUES ('4346', 'poem', '唐', '鱼玄机', '卖残牡丹', '临风兴叹落花频，芳意潜消又一春。\\n应为价高人不问，却缘香甚蝶难亲。\\n红英只称生宫里，翠叶那堪染路尘。\\n及至移根上林苑，王孙方恨买无因。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4347', 'poem', '唐', '李端', '宿淮浦忆司空文明', '愁心一倍长离忧，夜思千重恋旧游。\\n秦地故人成远梦，楚天凉雨在孤舟。\\n诸溪近海潮皆应，独树边淮叶尽流。\\n别恨转深何处写，前程唯有一登楼。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4348', 'poem', '唐', '严维', '夏日纳凉', '山阴过野客，镜里接仙郎。\\n盥漱临寒水，褰闱入夏堂。\\n杉松交日影，枕簟上湖光。\\n衮衮承嘉话，清风纳晚凉。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4349', 'poem', '唐', '柳宗元', '柳州峒氓', '郡城南下接通津，异服殊音不可亲。\\n青箬裹盐归峒客，绿荷包饭趁虚人。\\n鹅毛御腊缝山罽，鸡骨占年拜水神。\\n愁向公庭问重译，欲投章甫作文身。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4350', 'poem', '唐', '李端', '瘦马行', '城傍牧马驱未过，一马徘徊起还卧。\\n眼中有泪皮有疮，骨毛焦瘦令人伤。\\n朝朝放在儿童手，谁觉举头看故乡。\\n往时汉地相驰逐，如雨如风过平陆。\\n岂意今朝驱不前，蚊蚋满身泥上腹。\\n路人识是名马儿，畴昔三军不得骑。\\n玉勒金鞍既已远，追奔获兽有谁知。\\n终身枥上食君草，遂与驽骀一时老。\\n倘借长鸣陇上风，犹期一战安西道。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4351', 'poem', '唐', '薛能', '黄河', '何处发昆仑，连乾复浸坤。\\n波浑经雁塞，声振自龙门。\\n岸裂新冲势，滩馀旧落痕。\\n横沟通海上，远色尽山根。\\n勇逗三峰坼，雄标四渎尊。\\n湾中秋景树，阔外夕阳村。\\n沫乱知鱼呴，槎来见鸟蹲。\\n飞沙当白日，凝雾接黄昏。\\n润可资农亩，清能表帝恩。\\n雨吟堪极目，风度想惊魂。\\n显瑞龟曾出，阴灵伯固存。\\n盘涡寒渐急，浅濑暑微温。\\n九曲终柔胜，常流可暗吞。\\n人间无博望，谁复到穷源。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4352', 'poem', '唐', '杜甫', '有感五首', '将帅蒙恩泽，兵戈有岁年。\\n至今劳圣主，可以报皇天。\\n白骨新交战，云台旧拓边。\\n乘槎断消息，无处觅张骞。\\n幽蓟馀蛇豕，乾坤尚虎狼。\\n诸侯春不贡，使者日相望。\\n慎勿吞青海，无劳问越裳。\\n大君先息战，归马华山阳。\\n洛下舟车入，天中贡赋均。\\n日闻红粟腐，寒待翠华春。\\n莫取金汤固，长令宇宙新。\\n不过行俭德，盗贼本王臣。\\n丹桂风霜急，青梧日夜凋。\\n由来强干地，未有不臣朝。\\n受钺亲贤往，卑宫制诏遥。\\n终依古封建，岂独听箫韶。\\n盗灭人还乱，兵残将自疑。\\n登坛名绝假，报主尔何迟。\\n领郡辄无色，之官皆有词。\\n愿闻哀痛诏，端拱问疮痍。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4353', 'poem', '唐', '王建', '水夫谣', '苦哉生长当驿边，官家使我牵驿船。\\n辛苦日多乐日少，水宿沙行如海鸟。\\n逆风上水万斛重，前驿迢迢后淼淼。\\n半夜缘堤雪和雨，受他驱遣还复去。\\n夜寒衣湿披短蓑，臆穿足裂忍痛何！\\n到明辛苦无处说，齐声腾踏牵船歌。\\n一间茅屋何所值，父母之乡去不得。\\n我愿此水作平田，长使水夫不怨天。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4354', 'poem', '唐', '李白', '上元夫人', '上元谁夫人，偏得王母娇。\\n嵯峨三角髻，馀发散垂腰。\\n裘披青毛锦，身著赤霜袍。\\n手提嬴女儿，闲与凤吹箫。\\n眉语两自笑，忽然随风飘。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4355', 'poem', '唐', '张蠙', '长安春望', '明时不敢卧烟霞，又见秦城换物华。\\n残雪未销双凤阙，新春已发五侯家。\\n甘贫只拟长缄酒，忍病犹期强采花。\\n故国别来桑柘尽，十年兵践海西艖。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4356', 'poem', '唐', '龙女', '感怀诗', '海门连洞庭，每去三千里。\\n十载一归来，辛苦潇湘水。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4357', 'poem', '唐', '王建', '长安别', '长安清明好时节，只宜相送不宜别。\\n恶心床上铜片明，照见离人白头发。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4358', 'poem', '唐', '贯休', '杂曲歌辞·古离别', '离恨如旨酒，古今饮皆醉。\\n只恐长江水，尽是儿女泪。\\n伊余非此辈，送人空把臂。\\n他日再相逢，清风动天地。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4359', 'poem', '唐', '李隆基', '幸蜀西至剑门', '剑阁横云峻，銮舆出狩回。\\n翠屏千仞合，丹嶂五丁开。\\n灌木萦旗转，仙云拂马来。\\n乘时方在德，嗟尔勒铭才。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4360', 'poem', '唐', '白居易', '春听琵琶，兼简长孙司户', '四弦不似琵琶声，乱写真珠细撼铃。\\n指底商风悲飒飒，舌头胡语苦醒醒。\\n如言都尉思京国，似诉明妃厌虏庭。\\n迁客共君想劝谏，春肠易断不须听。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4361', 'poem', '唐', '王维', '哭孟浩然（时为殿中侍御史，知南选，至襄阳有作）', '故人不可见，汉水日东流。\\n借问襄阳老，江山空蔡州。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4362', 'poem', '唐', '高适', '送白少府送兵之陇右', '践更登陇首，远别指临洮。\\n为问关山事，何如州县劳。\\n军容随赤羽，树色引青袍。\\n谁断单于臂，今年太白高。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4363', 'poem', '唐', '赵嘏', '浙东赠李副使员外', '妙尽戎机佐上台，少年清苦自霜台。\\n马嘶深竹闲宜贵，花拂朱衣美称才。\\n早入半缘分务重，晚吟多是看山回。\\n名高渐少翻飞伴，几度烟霄独去来。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4364', 'poem', '唐', '武元衡', '长安春望', '宿雨净烟霞，春风绽百花。\\n绿杨中禁路，朱戟五侯家。\\n草色金堤晚，莺声御柳斜。\\n无媒犹未达，应共惜年华。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4365', 'poem', '唐', '卢仝', '喜逢郑三游山', '相逢之处花茸茸，石壁攒峰千万重。\\n他日期君何处好，寒流石上一株松。', '2018-03-09 15:44:36');
INSERT INTO `t_poem` VALUES ('4366', 'poem', '唐', '卢照邻', '还京赠别', '风月清江夜，山水白云朝。\\n万里同为客，三秋契不凋。\\n戏凫分断岸，归骑别高标。\\n一去仙桥道，还望锦城遥。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4367', 'poem', '唐', '白居易', '曲江忆李十一', '李君殁后共谁游，柳岸荷亭两度秋。\\n独绕曲江行一匝，依前还立水边愁。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4368', 'poem', '唐', '温庭筠', '金虎台', '碧草连金虎，青苔蔽石麟。\\n皓齿芳尘起，纤腰玉树春。\\n倚瑟红铅湿，分香翠黛嚬。\\n谁言奉陵寝，相顾复沾巾。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4369', 'poem', '唐', '薛能', '并州寓怀', '人多知遇独难求，人负知音独爱酬。\\n常恐此心无乐处，枉称年少在并州。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4370', 'poem', '唐', '崔颢', '题潼关楼', '客行逢雨霁，歇马上津楼。\\n山势雄三辅，关门扼九州。\\n川从陕路去，河绕华阴流。\\n向晚登临处，风烟万里愁。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4371', 'poem', '唐', '刘义度', '感怀诗', '昨日方髽髻，如今满颔髯。\\n紫阁无心恋，青山有意潜。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4372', 'poem', '唐', '窦巩', '赠王氏小儿', '竹林会里偏怜小，淮水清时最觉贤。\\n莫倚儿童轻岁月，丈人曾共尔同年。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4373', 'poem', '唐', '李峤', '雾', '曹公迷楚泽，汉帝出平城。\\n涿鹿妖氛静，丹山霁色明。\\n类烟飞稍重，方雨散还轻。\\n倘入非熊兆，宁思玄豹情。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4374', 'poem', '唐', '裴迪', '辋川集二十首·文杏馆', '迢迢文杏馆，跻攀日已屡。\\n南岭与北湖，前看复回顾。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4375', 'poem', '唐', '常建', '戏题湖上', '湖上老人坐矶头，湖里桃花水却流。\\n竹竿袅袅波无际，不知何者吞吾钩。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4376', 'poem', '唐', '李远', '咏鸳鸯', '鸳鸯离别伤，人意似鸳鸯。\\n试取鸳鸯看，多应断寸肠。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4377', 'poem', '唐', '司空曙', '关山月', '苍茫明月上，夜久光如积。\\n野幕冷胡霜，关楼宿边客。\\n陇头秋露暗，碛外寒沙白。\\n唯有故乡人，沾裳此闻笛。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4378', 'poem', '唐', '白居易', '香山寺二绝', '空山寂静老夫闲，伴鸟随云往复还。\\n家酝满瓶书满架，半移生计入香山。\\n爱风岩上攀松盖，恋月潭边坐石棱。\\n且共云泉结缘境，他生当作此山僧。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4379', 'poem', '唐', '崔涂', '己亥岁感事', '正闻青犊起葭萌，又报黄巾犯汉营。\\n岂是将皆无上略，直疑天自弃苍生。\\n瓜沙旧戍犹传檄，吴楚新春已废耕。\\n见说圣君能仄席，不知谁是请长缨。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4380', 'poem', '唐', '温庭筠', '杨柳枝', '一尺深红蒙曲尘，天生旧物不如新。\\n合欢桃核终堪恨，里许元来别有人。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4381', 'poem', '唐', '崔道融', '槿花', '槿花不见夕，一日一回新。\\n东风吹桃李，须到明年春。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4382', 'poem', '唐', '刘禹锡', '柳花词三首', '开从绿条上，散逐香风远。\\n故取花落时，悠扬占春晚。\\n轻飞不假风，轻落不委地。\\n撩乱舞晴空，发人无限思。\\n晴天闇闇雪，来送青春暮。\\n无意似多情，千家万家去。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4383', 'poem', '唐', '戴叔伦', '泊雁', '泊雁鸣深渚，收霞落晚川。\\n柝随风敛阵，楼映月低弦。\\n漠漠汀帆转，幽幽岸火然。\\n壑危通细路，沟曲绕平田。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4384', 'poem', '唐', '李山甫', '寄太常王少卿', '别后西风起，新蝉坐卧闻。\\n秋天静如水，远岫碧侵云。\\n雅饮纯和气，清吟冰雪文。\\n想思重回首，梧叶下纷纷。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4385', 'poem', '唐', '贺知章', '唐禅社首乐章·肃和', '黄祇是祗，我其夙夜。\\n夤畏诚絜，匪遑宁舍。\\n礼以琮玉，荐厥茅藉。\\n念兹降康，胡宁克暇。', '2018-03-09 15:44:41');
INSERT INTO `t_poem` VALUES ('4386', 'poem', '唐', '白居易', '除夜', '岁暮纷多思，天涯渺未归。\\n老添新甲子，病减旧容辉。\\n乡国仍留念，功名已息机。\\n明朝四十九，应转悟前非。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4387', 'poem', '唐', '戴叔伦', '登楼望月寄凤翔李少尹', '陌上凉风槐叶凋，夕阳清露湿寒条。\\n登楼望月楚山迥，月到南楼山独遥。\\n心送情人趋凤阙，目随阳雁极烟霄。\\n轩辕不重无名客，此地还能访寂寥。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4388', 'poem', '唐', '李绅', '新楼诗二十首·重台莲', '绿荷舒卷凉风晓，红萼开萦紫莳重。\\n游女汉皋争笑脸，二妃湘浦并愁容。\\n自含秋露贞姿结，不竞春妖冶态秾。\\n终恐玉京仙子识，却将归种碧池峰。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4389', 'poem', '唐', '孟郊', '雪', '忽然太行雪，昨夜飞入来。\\n崚嶒堕庭中，严白何皑皑。\\n奴婢晓开户，四肢冻徘徊。\\n咽言词不成，告诉情状摧。\\n官给未入门，家人尽以灰。\\n意劝莫笑雪，笑雪贫为灾。\\n将暖此残疾，典卖争致杯。\\n教令再举手，夸曜馀生才。\\n强起吐巧词，委曲多新裁。\\n为尔作非夫，忍耻轰暍雷。\\n书之与君子，庶免生嫌猜。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4390', 'poem', '唐', '李暇', '相和歌辞·碧玉歌', '碧玉上宫妓，出入千花林。\\n珠被玳瑁床，感郎情意深。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4391', 'poem', '唐', '司空图', '少仪', '昨日登班缀柏台，更惭起草属微才。\\n锦窠不是寻常锦，兼向丘迟夺得来。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4392', 'poem', '唐', '和凝', '洋川', '华夷图上见洋川，知在青山绿水边。\\n官闲最好游僧舍，江近应须买钓船。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4393', 'poem', '唐', '白居易', '和元八侍御升平新居四绝句·松树', '白金换得青松树，君既先栽我不栽。\\n幸有西风易凭仗，夜深偷送好声来。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4394', 'poem', '唐', '齐己', '蝴蝶', '何处背繁红，迷芳到槛重。\\n分飞还独出，成队偶相逢。\\n远害终防雀，争先不避蜂。\\n桃蹊牵往复，兰径引相从。\\n翠裛丹心冷，香凝粉翅浓。\\n可寻穿树影，难觅宿花踪。\\n日晚来仍急，春残舞未慵。\\n西风旧池馆，犹得采芙蓉。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4395', 'poem', '唐', '于武陵', '孤云', '南北各万里，有云心更闲。\\n因风离海上，伴月到人间。\\n洛浦少高树，长安无旧山。\\n裴回不可驻，漠漠又空还。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4396', 'poem', '唐', '李商隐', '送臻师二首', '昔去灵山非拂席，今来沧海欲求珠。\\n楞伽顶上清凉地，善眼仙人忆我无。\\n苦海迷途去未因，东方过此几微尘。\\n何当百亿莲花上，一一莲花见佛身。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4397', 'poem', '唐', '令狐楚', '游春词', '高楼晓见一花开，便觉春光四面来。\\n暖日晴云知次第，东风不用更相催。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4398', 'poem', '唐', '韩愈', '游城南十六首·出城', '暂出城门蹋青草，远于林下见春山。\\n应须韦杜家家到，只有今朝一日闲。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4399', 'poem', '唐', '林宽', '歌风台', '蒿棘空存百尺基，酒酣曾唱大风词。\\n莫言马上得天下，自古英雄尽解诗。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4400', 'poem', '唐', '李白', '朝下过卢郎中叙旧游', '君登金华省，我入银台门。\\n幸遇圣明主，俱承云雨恩。\\n复此休浣时，闲为畴昔言。\\n却话山海事，宛然林壑存。\\n明湖思晓月，叠嶂忆清猿。\\n何由返初服，田野醉芳樽。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4401', 'poem', '唐', '韦应物', '杂曲歌辞·上皇三台', '不寐倦长更，披衣出户行。\\n月寒秋竹冷，风切夜窗声。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4402', 'poem', '唐', '杜甫', '小园', '由来巫峡水，本自楚人家。\\n客病留因药，春深买为花。\\n秋庭风落果，瀼岸雨颓沙。\\n问俗营寒事，将诗待物华。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4403', 'poem', '唐', '韦承庆', '南行别弟', '澹澹长江水，悠悠远客情。\\n落花相与恨，到地一无声。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4404', 'poem', '唐', '蒋防', '玉卮无当', '美玉常为器，兹焉变漏卮。\\n酒浆悲莫挹，樽俎念空施。\\n符彩功难补，盈虚数已亏。\\n岂惟孤玩好，抑亦类瑕疵。\\n清越音虽在，操持意渐隳。\\n赋形期大匠，良璞勿同斯。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4405', 'poem', '唐', '李隆基', '傀儡吟', '刻木牵丝作老翁，鸡皮鹤发与真同。\\n须臾弄罢寂无事，还似人生一梦中。', '2018-03-09 15:44:45');
INSERT INTO `t_poem` VALUES ('4406', 'poem', '唐', '白居易', '重到城七绝句·见元九', '容貌一日减一日，心情十分无九分。\\n每逢陌路犹嗟叹，何况今朝是见君。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4407', 'poem', '唐', '章孝标', '省试骐骥长鸣', '有马骨堪惊，无人眼暂明。\\n力穷吴坂峻，嘶苦朔风生。\\n逐逐怀良御，萧萧顾乐鸣。\\n瑶池期弄影，天路拟飞声。\\n皎月谁知种，浮云莫问程。\\n盐车今愿脱，千里为君行。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4408', 'poem', '唐', '李端', '听夜雨寄卢纶', '暮雨萧条过凤城，霏霏飒飒重还轻。\\n闻君此夜东林宿，听得荷池几番声。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4409', 'poem', '唐', '李白', '寄崔侍御', '宛溪霜夜听猿愁，去国长为不系舟。\\n独怜一雁飞南海，却羡双溪解北流。\\n高人屡解陈蕃榻，过客难登谢脁楼。\\n此处别离同落叶，朝朝分散敬亭秋。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4410', 'poem', '唐', '李商隐', '华山题王母祠', '莲华峰下锁雕梁，此去瑶池地共长。\\n好为麻姑到东海，劝栽黄竹莫栽桑。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4411', 'poem', '唐', '无名氏', '玉卮无当', '共惜连城宝，翻成无当卮。讵惭君子贵，深讶拙工隳。\\n泛蚁功全少，如虹色不移。可怜珍砾石，何计辨糟醨，\\n江海诚难满，盘筵莫妄施。纵乖斟酌意，犹得奉光仪。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4412', 'poem', '唐', '耿湋', '寒蜂采菊蕊', '游飏下晴空，寻芳到菊丛。\\n带声来蕊上，连影在香中。\\n去住沾馀雾，高低顺过风。\\n终惭异蝴蝶，不与梦魂通。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4413', 'poem', '唐', '徐夤', '酒醒', '酒醒欲得适闲情，骑马那胜策杖行。\\n天暖天寒三月暮，溪南溪北两村名。\\n沙澄浅水鱼知钓，花落平田鹤见耕。\\n望断长安故交远，来书未说九河清。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4414', 'poem', '唐', '胡令能', '王昭君', '胡风似剑锼人骨，汉月如钩钓胃肠。\\n魂梦不知身在路，夜来犹自到昭阳。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4415', 'poem', '唐', '张继', '题严陵钓台', '旧隐人如在，清风亦似秋。\\n客星沈夜壑，钓石俯春流。\\n鸟向乔枝聚，鱼依浅濑游。\\n古来芳饵下，谁是不吞钩。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4416', 'poem', '唐', '胡曾', '咏史诗·湘川', '虞舜南捐万乘君，灵妃挥涕竹成纹。\\n不知精魄游何处，落日潇湘空白云。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4417', 'poem', '唐', '崔道融', '酒醒', '酒醒拨剔残灰火，多少凄凉在此中。\\n炉畔自斟还自醉，打窗深夜雪兼风。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4418', 'poem', '唐', '李世民', '咏弓', '上弦明月半，激箭流星远。\\n落雁带书惊，啼猿映枝转。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4419', 'poem', '唐', '姚合', '闲居遣怀十首', '身外无徭役，开门百事闲。\\n倚松听唳鹤，策杖望秋山。\\n萍任连池绿，苔从匝地斑。\\n料无车马客，何必扫柴关。\\n闲卧销长日，亲朋笑我疏。\\n诗篇随分有，人事度年无。\\n情性僻难改，愁怀酒为除。\\n谁能思此计，空备满床书。\\n白日逍遥过，看山复绕池。\\n展书寻古事，翻卷改新诗。\\n赊酒风前酌，留僧竹里棋。\\n同人笑相问，羡我足闲时。\\n好景时牵目，茅斋兴有馀。\\n远山经雨后，庭树得秋初。\\n道侣怜栽药，高人笑养鱼。\\n优游随本性，甘被弃慵疏。\\n永日厨烟绝，何曾暂废吟。\\n闲时随思缉，小酒恣情斟。\\n看月嫌松密，垂纶爱水深。\\n世间多少事，无事可关心。\\n一生能几日，愁恨也无端。\\n遇酒酕醄饮，逢花烂熳看。\\n青云非失路，白发未相干。\\n以此多携解，将心但自宽。\\n万事徒纷扰，难关枕上身。\\n朗吟销白日，沈醉度青春。\\n演步怜山近，闲眠厌客频。\\n市朝曾不到，长免满衣尘。\\n野性多疏惰，幽栖更称情。\\n独行看影笑，闲坐弄琴声。\\n懒拜腰肢硬，慵趋礼乐生。\\n业文随日遣，不是为求名。\\n生计甘寥落，高名愧自由。\\n惯无身外事，不信世间愁。\\n好酒盈杯酌，闲诗任笔酬。\\n凉风从入户，云水更宜秋。\\n拙直难和洽，从人笑掩关。\\n不能行户外，宁解走尘间。\\n被酒长酣思，无愁可上颜。\\n何言归去事，著处是青山。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4420', 'poem', '唐', '韩愈', '杂诗', '古史散左右，诗书置后前。\\n岂殊蠹书虫，生死文字间。\\n古道自愚蠢，古言自包缠。\\n当今固殊古，谁与为欣欢。\\n独携无言子，共升昆仑颠。\\n长风飘襟裾，遂起飞高圆。\\n下视禹九州，一尘集豪端。\\n遨嬉未云几，下已亿万年。\\n向者夸夺子，万坟厌其巅。\\n惜哉抱所见，白黑未及分。\\n慷慨为悲咤，泪如九河翻。\\n指摘相告语，虽还今谁亲。\\n翩然下大荒，被发骑骐驎。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4421', 'poem', '唐', '无名氏', '日暮山河清', '天高爽气晶，驰景忽西倾。\\n山列千重静，河流一带明。\\n想同金镜澈，宁让玉壶清。\\n纤翳无由出，浮埃不复生。\\n荣纡分汉苑，表里见秦城。\\n逸兴终难系，抽毫仰此情。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4422', 'poem', '唐', '白居易', '折剑头', '拾得折剑头，不知折之由。\\n一握青蛇尾，数寸碧峰头。\\n疑是斩鲸鲵，不然刺蛟虬。\\n缺落泥土中，委弃无人收。\\n我有鄙介性，好刚不好柔。\\n勿轻直折剑，犹胜曲全钩。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4423', 'poem', '唐', '白居易', '山中与元九书，因题书后', '忆昔封书与君夜，金銮殿后欲明天。\\n今夜封书在何处，庐山庵里晚灯前。\\n笼鸟槛猿俱未死，人间相见是何年。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4424', 'poem', '唐', '王维', '敕借岐王九成宫避暑应教', '帝子远辞丹凤阙，天书遥借翠微宫。\\n隔窗云雾生衣上，卷幔山泉入镜中。\\n林下水声喧语笑，岩间树色隐房栊。\\n仙家未必能胜此，何事吹笙向碧空。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4425', 'poem', '唐', '王毂', '杂曲歌辞·苦热行', '祝融南来鞭火龙，火旗焰焰烧天红。\\n日轮当午凝不去，万国如在洪炉中。\\n五岳翠乾云彩灭，阳侯海底愁波竭。\\n何当一夕金风发，为我扫却天下热。', '2018-03-09 15:45:03');
INSERT INTO `t_poem` VALUES ('4426', 'poem', '唐', '韩愈', '落叶送陈羽', '落叶不更息，断蓬无复归。\\n飘飖终自异，邂逅暂相依。\\n悄悄深夜语，悠悠寒月辉。\\n谁云少年别，流泪各沾衣。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4427', 'poem', '唐', '韦庄', '桐庐县作', '钱塘江尽到桐庐，水碧山青画不如。\\n白羽鸟飞严子濑，绿蓑人钓季鹰鱼。\\n潭心倒影时开合，谷口闲云自卷舒。\\n此境只应词客爱，投文空吊木玄虚。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4428', 'poem', '唐', '乔知之', '绿珠篇', '石家金谷重新声，明珠十斛买娉婷。\\n此日可怜君自许，此时可喜得人情。\\n君家闺阁不曾难，常将歌舞借人看。\\n意气雄豪非分理，骄矜势力横相干。\\n辞君去君终不忍，徒劳掩袂伤铅粉。\\n百年离别在高楼，一旦红颜为君尽。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4429', 'poem', '唐', '司空图', '诗品二十四则·高古', '畸人乘真，手把芙蓉。\\n泛彼浩劫，窅然空纵。\\n月出东斗，好风相从。\\n太华夜碧，人闻清钟。\\n虚伫神素，脱然畦封。\\n黄唐在独，落落玄宗。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4430', 'poem', '唐', '权德舆', '浩歌', '杖策出蓬荜，浩歌秋兴长。\\n北风吹荷衣，萧飒景气凉。\\n通逵抵山郭，里巷连湖光。\\n孤云净远峰，绿水溢芳塘。\\n鱼鸟乐天性，杂英互芬芳。\\n我心独何为，万虑萦中肠。\\n履道身未泰，主家谋不臧。\\n心为世教牵，迹寄翰墨场。\\n出处两未定，羁羸空自伤。\\n沈忧不可裁，伫立河之梁。\\n晚归茅檐下，左右陈壶觞。\\n独酌复长谣，放心游八荒。\\n得丧同一域，是非亦何常。\\n胡为苦此生，矻矻徒自强。\\n乃知杯中物，可使忧患忘。\\n因兹谢时辈，栖息无何乡。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4431', 'poem', '唐', '李世民', '春日玄武门宴群臣', '韶光开令序，淑气动芳年。\\n驻辇华林侧，高宴柏梁前。\\n紫庭文珮满，丹墀衮绂连。\\n九夷簉瑶席，五狄列琼筵。\\n娱宾歌湛露，广乐奏钧天。\\n清尊浮绿醑，雅曲韵朱弦。\\n粤余君万国，还惭抚八埏。\\n庶几保贞固，虚己厉求贤。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4432', 'poem', '唐', '江为', '岳阳楼', '倚楼高望极，展转念前途。\\n晚叶红残楚，秋江碧入吴。\\n云中来雁急，天末去帆孤。\\n明月谁同我，悠悠上帝都。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4433', 'poem', '唐', '刘叉', '代牛言', '渴饮颍水流，饿喘吴门月。\\n黄金如可种，我力终不竭。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4434', 'poem', '唐', '杜牧', '独酌', '窗外正风雪，拥炉开酒缸。\\n何如钓船雨，篷底睡秋江。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4435', 'poem', '唐', '王维', '送魏郡李太守赴任', '与君伯氏别，又欲与君离。君行无几日，当复隔山陂。\\n苍茫秦川尽，日落桃林塞。独树临关门，黄河向天外。\\n前经洛阳陌，宛洛故人稀。故人离别尽，淇上转骖騑，\\n企予悲送远，惆怅睢阳路。古木官渡平，秋城邺宫故。\\n想君行县日，其出从如云。遥思魏公子，复忆李将军。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4436', 'poem', '唐', '杜牧', '汉江', '溶溶漾漾白鸥飞，绿净春深好染衣。\\n南去北来人自老，夕阳长送钓船归。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4437', 'poem', '唐', '韦应物', '咏夜', '明从何处去，暗从何处来。\\n但觉年年老，半是此中催。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4438', 'poem', '唐', '乔知之', '苦寒行', '胡天夜清迥，孤云独飘飏，遥裔出雁关，逶迤含晶光。\\n阴陵久裴回，幽都无多阳。初寒冻巨海，杀气流大荒。\\n朔马饮寒冰，行子履胡霜。路有从役倦，卧死黄沙场。\\n羁旅因相依，恸之泪沾裳。由来从军行，赏存不赏亡。\\n亡者诚已矣，徒令存者伤。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4439', 'poem', '唐', '韩偓', '曲江夜思', '鼓声将绝月斜痕，园外闲坊半掩门。\\n池里红莲凝白露，苑中青草伴黄昏。\\n林塘阒寂偏宜夜，烟火稀疏便似村。\\n大抵世间幽独景，最关诗思与离魂。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4440', 'poem', '唐', '韦庄', '听赵秀才弹琴', '满匣冰泉咽又鸣，玉音闲澹入神清。\\n巫山夜雨弦中起，湘水清波指下生。\\n蜂簇野花吟细韵，蝉移高柳迸残声。\\n不须更奏幽兰曲，卓氏门前月正明。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4441', 'poem', '唐', '司空图', '牛头寺', '终南最佳处，禅诵出青霄。\\n群木澄幽寂，疏烟泛泬寥。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4442', 'poem', '唐', '杜牧', '书怀', '满眼青山未得过，镜中无那鬓丝何。\\n只言旋老转无事，欲到中年事更多。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4443', 'poem', '唐', '曹松', '古冢', '代远已难问，累累次古城。\\n民田侵不尽，客路踏还平。\\n作穴蛇分蛰，依冈鹿绕行。\\n唯应风雨夕，鬼火出林明。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4444', 'poem', '唐', '顾况', '梅湾', '白石盘盘磴，清香树树梅。\\n山深不吟赏，辜负委苍苔。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4445', 'poem', '唐', '白居易', '读庄子', '庄生齐物同归一，我道同中有不同。\\n遂性逍遥虽一致，鸾凰终校胜蛇虫。', '2018-03-09 15:45:12');
INSERT INTO `t_poem` VALUES ('4446', 'poem', '唐', '白居易', '昨以拙诗十首寄西川杜相公相公亦以新作…用伸答谢', '诗家律手在成都，权与寻常将相殊。\\n剪截五言兼用钺，陶钧六义别开炉。\\n惊人卷轴须知有，随事文章不道无。\\n篇数虽同光价异，十鱼目换十骊珠。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4447', 'poem', '唐', '杨希道', '咏琴', '久擅龙门质，孤竦峄阳名。\\n齐娥初发弄，赵女正调声。\\n嘉客勿遽反，繁弦曲未成。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4448', 'poem', '唐', '李昌符', '送友人', '举世谁能与事期，解携多是正欢时。\\n人间不遣有名利，陌上始应无别离。\\n晚渡待船愁立久，乱山投店独行迟。\\n我今漂泊还如此，江剑相逢亦未知。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4449', 'poem', '唐', '李煜', '书琵琶背', '侁自肩如削，难胜数缕绦。\\n天香留凤尾，馀暖在檀槽。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4450', 'poem', '唐', '杜牧', '过骊山作', '始皇东游出周鼎，刘项纵观皆引颈。\\n削平天下实辛勤，却为道傍穷百姓。\\n黔首不愚尔益愚，千里函关囚独夫。\\n牧童火入九泉底，烧作灰时犹未枯。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4451', 'poem', '唐', '白居易', '闻新蝉赠刘二十八', '蝉发一声时，槐花带两枝。\\n只应催我老，兼遣报君知。\\n白发生头速，青云入手迟。\\n无过一杯酒，相劝数开眉。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4452', 'poem', '唐', '白居易', '过故洛城', '故城门前春日斜，故城门里无人家。\\n市朝欲认不知处，漠漠野田飞草花。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4453', 'poem', '唐', '徐夤', '蝴蝶三首', '不并难飞茧里蛾，有花芳处定经过。\\n天风相送轻飘去，却笑蜘蛛谩织罗。\\n苒苒双双拂画栏，佳人偷眼再三看。\\n莫欺翼短飞长近，试就花间扑已难。\\n栩栩无因系得他，野园荒径一何多。\\n不闻丝竹谁教舞，应仗流莺为唱歌。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4454', 'poem', '唐', '怀素', '寄衡岳僧', '祝融高座对寒峰，云水昭丘几万重。\\n五月衲衣犹近火，起来白鹤冷青松。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4455', 'poem', '唐', '王维', '临高台送黎拾遗', '相送临高台，川原杳何极。\\n日暮飞鸟还，行人去不息。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4456', 'poem', '唐', '韦应物', '夏景园庐', '群木昼阴静，北窗凉气多。\\n闲居逾时节，夏云已嵯峨。\\n搴叶爱繁绿，缘涧弄惊波。\\n岂为论夙志，对此青山阿。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4457', 'poem', '唐', '李贞白', '咏刺猬', '行似针毡动，卧若栗球圆。\\n莫欺如此大，谁敢便行拳。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4458', 'poem', '唐', '李建勋', '小园', '小园吾所好，栽植忘劳形。\\n晚果经秋赤，寒蔬近社青。\\n竹萝荒引蔓，土井浅生萍。\\n更欲从人劝，凭高置草亭。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4459', 'poem', '唐', '王建', '送人游塞', '初晴天堕丝，晚色上春枝。\\n城下路分处，边头人去时。\\n停车数行日，劝酒问回期。\\n亦是茫茫客，还从此别离。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4460', 'poem', '唐', '李商隐', '龙池', '龙池赐酒敞云屏，羯鼓声高众乐停。\\n夜半宴归宫漏永，薛王沉醉寿王醒。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4461', 'poem', '唐', '岑参', '张仪楼', '传是秦时楼，巍巍至今在。\\n楼南两江水，千古长不改。\\n曾闻昔时人，岁月不相待。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4462', 'poem', '唐', '白居易', '喜罢郡', '五年两郡亦堪嗟，偷出游山走看花。\\n自此光阴为己有，从前日月属官家。\\n樽前免被催迎使，枕上休闻报坐衙。\\n睡到午时欢到夜，回看官职是泥沙。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4463', 'poem', '唐', '韩愈', '游城南十六首·嘲少年', '直把春偿酒，都将命乞花。\\n只知闲信马，不觉误随车。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4464', 'poem', '唐', '司空图', '洛中三首', '秋风团扇未惊心，笑看妆台落叶侵。\\n绣凤不教金缕暗，青楼何处有寒砧。\\n不用频嗟世路难，浮生各自系悲欢。\\n风霜一夜添羁思，罗绮谁家待早寒。\\n燕巢空后谁相伴，鸳被缝来不忍薰。\\n薄命敢辞长滴泪，倡家未必肯留君。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4465', 'poem', '唐', '李贺', '莫种树', '园中莫种树，种树四时愁。\\n独睡南床月，今秋似去秋。', '2018-03-09 15:45:18');
INSERT INTO `t_poem` VALUES ('4466', 'poem', '唐', '王维', '送方尊师归嵩山', '仙官欲往九龙潭，旄节朱幡倚石龛。\\n山压天中半天上，洞穿江底出江南。\\n瀑布杉松常带雨，夕阳苍翠忽成岚。\\n借问迎来双白鹤，已曾衡岳送苏耽。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4467', 'poem', '唐', '武则天', '从驾幸少林寺', '陪銮游禁苑，侍赏出兰闱。\\n云偃攒峰盖，霞低插浪旂。\\n日宫疏涧户，月殿启岩扉。\\n金轮转金地，香阁曳香衣。\\n铎吟轻吹发，幡摇薄雾霏。\\n昔遇焚芝火，山红连野飞。\\n花台无半影，莲塔有全辉。\\n实赖能仁力，攸资善世威。\\n慈缘兴福绪，于此罄归依。\\n风枝不可静，泣血竟何追。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4468', 'poem', '唐', '柳宗元', '戏题阶前芍药', '凡卉与时谢，妍华丽兹晨。\\n欹红醉浓露，窈窕留馀春。\\n孤赏白日暮，暄风动摇频。\\n夜窗蔼芳气，幽卧知相亲。\\n愿致溱洧赠，悠悠南国人。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4469', 'poem', '唐', '司空图', '诗品二十四则·劲健', '行神如空，行气如虹。\\n巫峡千寻，走云连风。\\n饮真茹强，蓄素守中。\\n喻彼行健，是谓存雄。\\n天地与立，神化攸同。\\n期之以实，御之以终。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4470', 'poem', '唐', '李白', '安州应城玉女汤作', '神女殁幽境，汤池流大川。\\n阴阳结炎炭，造化开灵泉。\\n地底烁朱火，沙傍歊素烟。\\n沸珠跃明月，皎镜函空天。\\n气浮兰芳满，色涨桃花然。\\n精览万殊入，潜行七泽连。\\n愈疾功莫尚，变盈道乃全。\\n濯濯气清泚，晞发弄潺湲。\\n散下楚王国，分浇宋玉田。\\n可以奉巡幸，奈何隔穷偏。\\n独随朝宗水，赴海输微涓。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4471', 'poem', '唐', '杜牧', '秋晚江上遣怀', '孤舟天际外，去路望中赊。\\n贫病远行客，梦魂多在家。\\n蝉吟秋色树，鸦噪夕阳沙。\\n不拟彻双鬓，他方掷岁华。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4472', 'poem', '唐', '董思恭', '咏露', '夜色凝仙掌，晨甘下帝庭。\\n不觉九秋至，远向三危零。\\n芦渚花初白，葵园叶尚青。\\n晞阳一洒惠，方愿益沧溟。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4473', 'poem', '唐', '李白', '寒女吟', '昔君布衣时，与妾同辛苦。\\n一拜五官郎，便索邯郸女。\\n妾欲辞君去，君心便相许。\\n妾读蘼芜书，悲歌泪如雨。\\n忆昔嫁君时，曾无一夜乐。\\n不是妾无堪，君家妇难作。\\n起来强歌舞，纵好君嫌恶。\\n下堂辞君去，去后悔遮莫。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4474', 'poem', '唐', '李峤', '剑', '我有昆吾剑，求趋夫子庭。\\n白虹时切玉，紫气夜干星。\\n锷上芙蓉动，匣中霜雪明。\\n倚天持报国，画地取雄名。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4475', 'poem', '唐', '孟郊', '赠别崔纯亮', '食荠肠亦苦，强歌声无欢。\\n出门即有碍，谁谓天地宽。\\n有碍非遐方，长安大道傍。\\n小人智虑险，平地生太行。\\n镜破不改光，兰死不改香。\\n始知君子心，交久道益彰。\\n君心与我怀，离别俱回遑。\\n譬如浸蘖泉，流苦已日长。\\n忍泣目易衰，忍忧形易伤。\\n项籍岂不壮，贾生岂不良。\\n当其失意时，涕泗各沾裳。\\n古人劝加餐，此餐难自强。\\n一饭九祝噎，一嗟十断肠。\\n况是儿女怨，怨气凌彼苍。\\n彼苍若有知，白日下清霜。\\n今朝始惊叹，碧落空茫茫。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4476', 'poem', '唐', '白居易', '好听琴', '本性好丝桐，尘机闻即空。\\n一声来耳里，万事离心中。\\n清畅堪销疾，恬和好养蒙。\\n尤宜听三乐，安慰白头翁。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4477', 'poem', '唐', '杜甫', '所思', '苦忆荆州醉司马，谪官樽俎定常开。\\n九江日落醒何处，一柱观头眠几回。\\n可怜怀抱向人尽，欲问平安无使来。\\n故凭锦水将双泪，好过瞿塘滟滪堆。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4478', 'poem', '唐', '杜甫', '雨二首', '青山澹无姿，白露谁能数。\\n片片水上云，萧萧沙中雨。\\n殊俗状巢居，曾台俯风渚。\\n佳客适万里，沈思情延伫。\\n挂帆远色外，惊浪满吴楚。\\n久阴蛟螭出，寇盗复几许。\\n空山中宵阴，微冷先枕席。\\n回风起清曙，万象萋已碧。\\n落落出岫云，浑浑倚天石。\\n日假何道行，雨含长江白。\\n连樯荆州船，有士荷矛戟。\\n南防草镇惨，沾湿赴远役。\\n群盗下辟山，总戎备强敌。\\n水深云光廓，鸣橹各有适。\\n渔艇息悠悠，夷歌负樵客。\\n留滞一老翁，书时记朝夕。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4479', 'poem', '唐', '齐己', '海棠花', '繁于桃李盛于梅，寒食旬前社后开。\\n半月暄和留艳态，两时风雨免伤摧。\\n人怜格异诗重赋，蝶恋香多夜更来。\\n犹得残红向春暮，牡丹相继发池台。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4480', 'poem', '唐', '戴叔伦', '宿灵岩寺', '马疲盘道峻，投宿入招提。\\n雨急山溪涨，云迷岭树低。\\n凉风来殿角，赤日下天西。\\n偃腹虚檐外，林空鸟恣啼。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4481', 'poem', '唐', '罗隐', '淮南送工部卢员外赴阙', '始从豸角曳长裾，又吐鸡香奏玉除。\\n隋邸旧僚推谢掾，汉廷高议得相如。\\n贵分赤笔升兰署，荣著绯衣从板舆。\\n遥想到时秋欲尽，禁城凉冷露槐疏。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4482', 'poem', '唐', '武则天', '早春夜宴', '九春开上节，千门敞夜扉。\\n兰灯吐新焰，桂魄朗圆辉。\\n送酒惟须满，流杯不用稀。\\n务使霞浆兴，方乘泛洛归。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4483', 'poem', '唐', '杜甫', '寄高三十五詹事', '安稳高詹事，兵戈久索居。\\n时来如宦达，岁晚莫情疏。\\n天上多鸿雁，池中足鲤鱼。\\n相看过半百，不寄一行书。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4484', 'poem', '唐', '李白', '早望海霞边', '四明三千里，朝起赤城霞。\\n日出红光散，分辉照雪崖。\\n一餐咽琼液，五内发金沙。\\n举手何所待，青龙白虎车。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4485', 'poem', '唐', '李贺', '伤心行', '咽咽学楚吟，病骨伤幽素。\\n秋姿白发生，木叶啼风雨。\\n灯青兰膏歇，落照飞蛾舞。\\n古壁生凝尘，羁魂梦中语。', '2018-03-09 15:45:31');
INSERT INTO `t_poem` VALUES ('4486', 'poem', '唐', '李端', '白鹭咏', '迥起来应近，高飞去自遥。\\n映林同落雪，拂水状翻潮。\\n犹有幽人兴，相逢到碧霄。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4487', 'poem', '唐', '李白', '九日登山', '渊明归去来，不与世相逐。\\n为无杯中物，遂偶本州牧。\\n因招白衣人，笑酌黄花菊。\\n我来不得意，虚过重阳时。\\n题舆何俊发，遂结城南期。\\n筑土按响山，俯临宛水湄。\\n胡人叫玉笛，越女弹霜丝。\\n自作英王胄，斯乐不可窥。\\n赤鲤涌琴高，白龟道冯夷。\\n灵仙如仿佛，奠酹遥相知。\\n古来登高人，今复几人在。\\n沧洲违宿诺，明日犹可待。\\n连山似惊波，合沓出溟海。\\n扬袂挥四座，酩酊安所知。\\n齐歌送清扬，起舞乱参差。\\n宾随落叶散，帽逐秋风吹。\\n别后登此台，愿言长相思。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4488', 'poem', '唐', '顾况', '独游青龙寺', '春风入香刹，暇日独游衍。\\n旷然莲花台，作礼月光面。\\n乘兹第八识，出彼超二见。\\n摆落区中缘，无边广弘愿。\\n长廊朝雨毕，古木时禽啭。\\n积翠暧遥原，杂英纷似霰。\\n凤城腾日窟，龙首横天堰。\\n蚁步避危阶，蝇飞响深殿。\\n大通智胜佛，几劫道场现。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4489', 'poem', '唐', '崔道融', '钓鱼', '闲钓江鱼不钓名，瓦瓯斟酒暮山青。\\n醉头倒向芦花里，却笑无端犯客星。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4490', 'poem', '唐', '李颀', '题璿公山池', '远公遁迹庐山岑，开士幽居祇树林。\\n片石孤峰窥色相，清池皓月照禅心。\\n指挥如意天花落，坐卧闲房春草深。\\n此外俗尘都不染，惟馀玄度得相寻。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4491', 'poem', '唐', '崔珏', '水晶枕', '千年积雪万年冰，掌上初擎力不胜。\\n南国旧知何处得，北方寒气此中凝。\\n黄昏转烛萤飞沼，白日褰帘水在簪。\\n蕲簟蜀琴相对好，裁诗乞与涤烦襟。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4492', 'poem', '唐', '李暇', '碧玉歌', '碧玉上官妓，出入千花林。\\n珠被玳瑁床，感郎情意深。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4493', 'poem', '唐', '王绩', '咏怀', '故乡行云是，虚室坐间同。\\n日落西山暮，方知天下空。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4494', 'poem', '唐', '李世民', '度秋', '夏律昨留灰，秋箭今移晷。\\n峨嵋岫初出，洞庭波渐起。\\n桂白发幽岩，菊黄开灞涘。\\n运流方可叹，含毫属微理。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4495', 'poem', '唐', '李商隐', '楚泽', '夕阳归路后，霜野物声干。\\n集鸟翻渔艇，残虹拂马鞍。\\n刘桢元抱病，虞寄数辞官。\\n白袷经年卷，西来及早寒。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4496', 'poem', '唐', '佚名', '郊庙歌辞·祀九宫贵神乐章·豫和', '享申百礼，庆洽百灵。\\n上排阊阖，洞入杳冥。\\n奠玉高坛，燔柴广庭。\\n神之降福，万国咸宁。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4497', 'poem', '唐', '元稹', '远望', '满眼伤心冬景和，一山红树寺边多。\\n仲宣无限思乡泪，漳水东流碧玉波。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4498', 'poem', '唐', '白居易', '阴雨', '润叶濡枝浃四方，浓云来去势何长。\\n旷然寰宇清风满，救旱功高暑气凉。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4499', 'poem', '唐', '王周', '问春', '游丝垂幄雨依依，枝上红香片片飞。\\n把酒问春因底意，为谁来后为谁归。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4500', 'poem', '唐', '油蔚', '赠别营妓卿卿', '怜君无那是多情，枕上相看直到明。\\n日照绿窗人去住，鸦啼红粉泪纵横。\\n愁肠只向金闺断，白发应从玉塞生。\\n为报花时少惆怅，此生终不负卿卿。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4501', 'poem', '唐', '李中', '庭苇', '品格清于竹，诗家景最幽。\\n从栽向池沼，长似在汀洲。\\n玩好招溪叟，栖堪待野鸥。\\n影疏当夕照，花乱正深秋。\\n韵细堪清耳，根牢好系舟。\\n故溪高岸上，冷淡有谁游。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4502', 'poem', '唐', '罗邺', '途中寄友人', '秋庭怅望别君初，折柳分襟十载馀。\\n相见或因中夜梦，寄来多是隔年书。\\n携樽座外花空老，垂钓江头柳渐疏。\\n裁得诗凭千里雁，吟来宁不忆吾庐。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4503', 'poem', '唐', '宋之问', '蓝田山庄', '宦游非吏隐，心事好幽偏。\\n考室先依地，为农且用天。\\n辋川朝伐木，蓝水暮浇田。\\n独与秦山老，相欢春酒前。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4504', 'poem', '唐', '佚名', '神鸡童谣', '生儿不用识文字。斗鸡走马胜读书。贾家小儿年十三，\\n富贵荣华代不如。能令金距期胜负，白罗绣衫随软舆。\\n父死长安千里外，差夫治道挽丧车。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4505', 'poem', '唐', '王维', '辋川集·南垞', '轻舟南垞去，北垞淼难即。\\n隔浦望人家，遥遥不相识。', '2018-03-09 15:45:37');
INSERT INTO `t_poem` VALUES ('4506', 'poem', '唐', '许浑', '湖上', '仿佛欲当三五夕，万蝉清杂乱泉纹。\\n钓鱼船上一尊酒，月出渡头零落云。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4507', 'poem', '唐', '杜甫', '刘九法曹郑瑕邱石门宴集', '秋水清无底，萧然静客心。\\n椽曹乘逸兴，鞍马到荒林。\\n能吏逢聊璧，华筵直一金。\\n晚来横吹好，泓下亦龙吟。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4508', 'poem', '唐', '白居易', '立秋日登乐游园', '独行独语曲江头，回马迟迟上乐游。\\n萧飒凉风与衰鬓，谁教计会一时秋。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4509', 'poem', '唐', '白居易', '送客归京', '水陆四千里，何时归到秦。\\n舟辞三峡雨，马入九衢尘。\\n有酒留行客，无书寄贵人。\\n唯凭远传语，好在曲江春。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4510', 'poem', '唐', '戎昱', '红槿花', '花是深红叶麹尘，不将桃李共争春。\\n今日惊秋自怜客，折来持赠少年人。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4511', 'poem', '唐', '高蟾', '归思', '紫府归期断，芳洲别思迢。\\n黄金作人世，只被岁寒消。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4512', 'poem', '唐', '曹邺', '长城下', '远水犹归壑，征人合忆乡。\\n泣多盈袖血，吟苦满头霜。\\n楚国连天浪，衡门到海荒。\\n何当生燕羽，时得近雕梁。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4513', 'poem', '唐', '张继', '九日巴丘杨公台上宴集', '凄凄霜日上高台，水国秋凉客思哀。\\n万叠银山寒浪起，一行斜字早鸿来。\\n谁家捣练孤城暮，何处题衣远信回。\\n江汉路长身不定，菊花三笑旅怀开。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4514', 'poem', '唐', '周昙', '后汉门·子密', '子密封侯岂所宜，能高德义必无为。\\n当时若缚还彭氏，率土何忧不自归。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4515', 'poem', '唐', '佚名', '郊庙歌辞·褒德庙乐章·进德', '涂山懿戚，妫汭崇姻。\\n祠筵肇启，祭典方申。\\n礼以备物，乐以感神。\\n用隆敦叙，载穆彝伦。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4516', 'poem', '唐', '欧阳玭', '榆溪道上', '初日在斜溪，山云片片低。\\n乡愁梦里失，马色望中迷。\\n涧底凄泉气，岩前遍绿荑。\\n非关秦塞去，无事候晨鸡。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4517', 'poem', '唐', '骆宾王', '尘灰', '洛川流雅韵，秦道擅苛威。\\n听歌梁上动，应律管中飞。\\n光飘神女袜，影落羽人衣。\\n愿言心未翳，终冀效轻微。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4518', 'poem', '唐', '李贺', '河南府试十二月乐词·三月', '东方风来满眼春，花城柳暗愁杀人。\\n复宫深殿竹风起，新翠舞衿净如水。\\n光风转蕙百馀里，暖雾驱云扑天地。\\n军装宫妓扫蛾浅，摇摇锦旗夹城暖。\\n曲水漂香去不归，梨花落尽成秋苑。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4519', 'poem', '唐', '杜甫', '九日寄岑参', '出门复入门，两脚但如旧。\\n所向泥活活，思君令人瘦。\\n沉吟坐西轩，饮食错昏昼。\\n寸步曲江头，难为一相就。\\n吁嗟呼苍生，稼穑不可救。\\n安得诛云师，畴能补天漏。\\n大明韬日月，旷野号禽兽。\\n君子强逶迤，小人困驰骤。\\n维南有崇山，恐与川浸溜。\\n是节东篱菊，纷披为谁秀。\\n岑生多新诗，性亦嗜醇酎。\\n采采黄金花，何由满衣袖。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4520', 'poem', '唐', '杜甫', '送高三十五书记', '崆峒小麦熟，且愿休王师。\\n请公问主将，焉用穷荒为。\\n饥鹰未饱肉，侧翅随人飞。\\n高生跨鞍马，有似幽并儿。\\n脱身簿尉中，始与捶楚辞。\\n借问今何官，触热向武威。\\n答云一书记，所愧国士知。\\n人实不易知，更须慎其仪。\\n十年出幕府，自可持旌麾。\\n此行既特达，足以慰所思。\\n男儿功名遂，亦在老大时。\\n常恨结欢浅，各在天一涯。\\n又如参与商，惨惨中肠悲。\\n惊风吹鸿鹄，不得相追随。\\n黄尘翳沙漠，念子何当归。\\n边城有馀力，早寄从军诗。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4521', 'poem', '唐', '卢照邻', '文翁讲堂', '锦里淹中馆，岷山稷下亭。\\n空梁无燕雀，古壁有丹青。\\n槐落犹疑市，苔深不辨铭。\\n良哉二千石，江汉表遗灵。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4522', 'poem', '唐', '杜牧', '洛中二首', '柳动晴风拂路尘，年年宫阙锁浓春。\\n一从翠辇无巡幸，老却蛾眉几许人。\\n风吹柳带摇晴绿，蝶绕花枝恋暖香。\\n多把芳菲泛春酒，直教愁色对愁肠。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4523', 'poem', '唐', '刘长卿', '喜鲍禅师自龙山至', '故居何日下，春草欲芊芊。\\n犹对山中月，谁听石上泉。\\n猿声知后夜，花发见流年。\\n杖锡闲来往，无心到处禅。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4524', 'poem', '唐', '李白', '待酒不至', '玉壶系青丝，沽酒来何迟。\\n山花向我笑，正好衔杯时。\\n晚酌东窗下，流莺复在兹。\\n春风与醉客，今日乃相宜。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4525', 'poem', '唐', '李德裕', '思平泉树石杂咏一十首·白鹭鹚', '余心怜白鹭，潭上日相依。\\n拂石疑星落，凌风似雪飞。\\n碧沙常独立，清景自忘归。\\n所乐惟烟水，徘徊恋钓矶。', '2018-03-09 15:45:45');
INSERT INTO `t_poem` VALUES ('4526', 'poem', '唐', '卢照邻', '江中望月', '江水向涔阳，澄澄写月光。\\n镜圆珠溜彻，弦满箭波长。\\n沉钩摇兔影，浮桂动丹芳。\\n延照相思夕，千里共沾裳。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4527', 'poem', '唐', '郑锡', '送客之江西', '乘轺奉紫泥，泽国渺天涯。\\n九派春潮满，孤帆暮雨低。\\n草深莺断续，花落水东西。\\n更有高唐处，知君路不迷。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4528', 'poem', '唐', '温庭筠', '南湖', '湖上微风入槛凉，翻翻菱荇满回塘。\\n野船著岸偎春草，水鸟带波飞夕阳。\\n芦叶有声疑雾雨，浪花无际似潇湘。\\n飘然篷艇东归客，尽日相看忆楚乡。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4529', 'poem', '唐', '白居易', '宿灵岩寺上院', '高高白月上青林，客去僧归独夜深。\\n荤血屏除唯对酒，歌钟放散只留琴。\\n更无俗物当人眼，但有泉声洗我心。\\n最爱晓亭东望好，太湖烟水绿沉沉。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4530', 'poem', '唐', '唐彦谦', '玫瑰', '麝炷腾清燎，鲛纱覆绿蒙。\\n宫妆临晓日，锦段落东风。\\n无力春烟里，多愁暮雨中。\\n不知何事意，深浅两般红。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4531', 'poem', '唐', '李白', '夜别张五', '吾多张公子，别酌酣高堂。\\n听歌舞银烛，把酒轻罗裳。\\n横笛弄秋月，琵琶弹陌桑。\\n龙泉解锦带，为尔倾千觞。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4532', 'poem', '唐', '钱起', '送钟评事应宏词下第东归', '芳岁归人嗟转蓬，含情回首灞陵东。\\n蛾眉不入秦台镜，鹢羽还惊宋国风。\\n世事悠扬春梦里，年光寂寞旅愁中。\\n劝君稍尽离筵酒，千里佳期难再同。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4533', 'poem', '唐', '杜甫', '石镜', '蜀王将此镜，送死置空山。\\n冥寞怜香骨，提携近玉颜。\\n众妃无复叹，千骑亦虚还。\\n独有伤心石，埋轮月宇间。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4534', 'poem', '唐', '王昌龄', '九江口作', '漭漭江势阔，雨开浔阳秋。\\n驿门是高岸，望尽黄芦洲。\\n水与五溪合，心期万里游。\\n明时无弃才，谪去随孤舟。\\n鸷鸟立寒木，丈夫佩吴钩。\\n何当报君恩，却系单于头。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4535', 'poem', '唐', '白居易', '感情', '中庭晒服玩，忽见故乡履。\\n昔赠我者谁，东邻婵娟子。\\n因思赠时语，特用结终始。\\n永愿如履綦，双行复双止。\\n自吾谪江郡，漂荡三千里。\\n为感长情人，提携同到此。\\n今朝一惆怅，反覆看未已。\\n人只履犹双，何曾得相似。\\n可嗟复可惜，锦表绣为里。\\n况经梅雨来，色黯花草死。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4536', 'poem', '唐', '刘禹锡', '洞庭秋月行', '洞庭秋月生湖心，层波万顷如熔金。\\n孤轮徐转光不定，游气濛濛隔寒镜。\\n是时白露三秋中，湖平月上天地空。\\n岳阳楼头暮角绝，荡漾已过君山东。\\n山城苍苍夜寂寂，水月逶迤绕城白。\\n荡桨巴童歌竹枝，连樯估客吹羌笛。\\n势高夜久阴力全，金气肃肃开星躔。\\n浮云野马归四裔，遥望星斗当中天。\\n天鸡相呼曙霞出，敛影含光让朝日。\\n日出喧喧人不闲，夜来清景非人间。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4537', 'poem', '唐', '耿玮', '秋日', '返照入闾巷，忧来谁共语？古道少人行，秋风动禾黍。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4538', 'poem', '唐', '白居易', '北窗竹石', '一片瑟瑟石，数竿青青竹。\\n向我如有情，依然看不足。\\n况临北窗下，复近西塘曲。\\n筠风散馀清，苔雨含微绿。\\n有妻亦衰老，无子方茕独。\\n莫掩夜窗扉，共渠相伴宿。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4539', 'poem', '唐', '皇甫冉', '寄权器', '露湿青芜时欲晚，水流黄叶意无穷。\\n节近重阳念归否，眼前篱菊带秋风。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4540', 'poem', '唐', '王昌龄', '卢溪主人', '武陵溪口驻扁舟，溪水随君向北流。\\n行到荆门上三峡，莫将孤月对猿愁。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4541', 'poem', '唐', '李白', '送郄昂谪巴中', '瑶草寒不死，移植沧江滨。\\n东风洒雨露，会入天地春。\\n予若洞庭叶，随波送逐臣。\\n思归未可得，书此谢情人。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4542', 'poem', '唐', '薛涛', '十离诗·鹰离鞲', '爪利如锋眼似铃，平原捉兔称高情。\\n无端窜向青云外，不得君王臂上擎。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4543', 'poem', '唐', '杜荀鹤', '溪岸秋思', '桑柘穷头三四家，挂罾垂钓是生涯。\\n秋风忽起溪滩白，零落岸边芦荻花。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4544', 'poem', '唐', '知业', '句', '接岸桥通何处路，倚栏人是阿谁家。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4545', 'poem', '唐', '白居易', '代迎春花招刘郎中', '幸与松筠相近栽，不随桃李一时开。\\n杏园岂敢妨君去，未有花时且看来。', '2018-03-09 15:45:53');
INSERT INTO `t_poem` VALUES ('4546', 'poem', '唐', '武元衡', '寒食下第', '柳挂九衢丝，花飘万家雪。\\n如何憔悴人，对此芳菲节。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4547', 'poem', '唐', '李郢', '酬友人春暮寄枳花茶', '昨日东风吹枳花，酒醒春晚一瓯茶。\\n如云正护幽人堑，似雪才分野老家。\\n金饼拍成和雨露，玉尘煎出照烟霞。\\n相如病渴今全校，不羡生台白颈鸦。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4548', 'poem', '唐', '张继', '上清词', '紫阳宫女捧丹砂，王母令过汉帝家。\\n春风不肯停仙驭，却向蓬莱看杏花。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4549', 'poem', '唐', '孙元晏', '吴·青盖', '历数将终势已摧，不修君德更堪哀。\\n被他青盖言相误，元是须教入晋来。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4550', 'poem', '唐', '皎然', '秋晚宿破山寺', '秋风落叶满空山，古寺残灯石壁间。\\n昔日经行人去尽，寒云夜夜自飞还。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4551', 'poem', '唐', '白居易', '玩止水', '动者乐流水，静者乐止水。\\n利物不如流，鉴形不如止。\\n凄清早霜降，淅沥微风起。\\n中面红叶开，四隅绿萍委。\\n广狭八九丈，湾环有涯涘。\\n浅深三四尺，洞彻无表里。\\n净分鹤翘足，澄见鱼掉尾。\\n迎眸洗眼尘，隔胸荡心滓。\\n定将禅不别，明与诚相似。\\n清能律贪夫，淡可交君子。\\n岂唯空狎玩，亦取相伦拟。\\n欲识静者心，心源只如此。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4552', 'poem', '唐', '韦庄', '齐安郡', '弥棹齐安郡，孤城百战残。\\n傍村林有虎，带郭县无官。\\n暮角梅花怨，清江桂影寒。\\n黍离缘底事，撩我起长叹。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4553', 'poem', '唐', '温庭筠', '西陵道士茶歌', '乳窦溅溅通石脉，绿尘愁草春江色。\\n涧花入井水味香，山月当人松影直。\\n仙翁白扇霜鸟翎，拂坛夜读黄庭经。\\n疏香皓齿有馀味，更觉鹤心通杳冥。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4554', 'poem', '唐', '张祜', '题杭州孤山寺', '楼台耸碧岑，一径入湖心。\\n不雨山长润，无云水自阴。\\n断桥荒藓涩，空院落花深。\\n犹忆西窗月，钟声在北林。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4555', 'poem', '唐', '李白', '戏赠杜甫（以下见《唐诗纪事》）', '饭颗山头逢杜甫，顶戴笠子日卓午。\\n借问别来太瘦生，总为从前作诗苦。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4556', 'poem', '唐', '白居易', '戏答诸少年', '顾我长年头似雪，饶君壮岁气如云。\\n朱颜今日虽欺我，白发他时不放君。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4557', 'poem', '唐', '白居易', '晚秋闲居', '地僻门深少送迎，披衣闲坐养幽情。\\n秋庭不扫携藤杖，闲蹋梧桐黄叶行。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4558', 'poem', '唐', '白居易', '新栽梅', '池边新栽七株梅，欲到花时点检来。\\n莫怕长洲桃李嫉，今年好为使君开。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4559', 'poem', '唐', '权德舆', '卦名诗', '节变忽惊春，临风骋望频。\\n支颐倦书幌，步履整山巾。\\n时鸟渐成曲，杂芳随意新。\\n曙霞连观阙，绮陌丽咸秦。\\n天地今交泰，云雷背遘屯。\\n中孚谅可乐，书此示家人。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4560', 'poem', '唐', '白居易', '足疾', '足疾无加亦不瘳，绵春历夏复经秋。\\n开颜且酌尊中酒，代步多乘池上舟。\\n幸有眼前衣食在，兼无身后子孙忧。\\n应须学取陶彭泽，但委心形任去留。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4561', 'poem', '唐', '佚名', '改唱', '张公吃酒李公颠，盛六生儿郑九怜。\\n舍下雄鸡失一足，街头小福拉三拳。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4562', 'poem', '唐', '刘方平', '秋夜泛舟', '林塘夜发舟，虫响荻飕飕。\\n万影皆因月，千声各为秋。\\n岁华空复晚，乡思不堪愁。\\n西北浮云外，伊川何处流。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4563', 'poem', '唐', '韩愈', '华山女', '街东街西讲佛经，撞钟吹螺闹宫庭。\\n广张罪福资诱胁，听众狎恰排浮萍。\\n黄衣道士亦讲说，座下寥落如明星。\\n华山女儿家奉道，欲驱异教归仙灵。\\n洗妆拭面著冠帔，白咽红颊长眉青。\\n遂来升座演真诀，观门不许人开扃。\\n不知谁人暗相报，訇然振动如雷霆。\\n扫除众寺人迹绝，骅骝塞路连辎輧。\\n观中人满坐观外，后至无地无由听。\\n抽簪脱钏解环佩，堆金叠玉光青荧。\\n天门贵人传诏召，六宫愿识师颜形。\\n玉皇颔首许归去，乘龙驾鹤来青冥。\\n豪家少年岂知道，来绕百匝脚不停。\\n云窗雾阁事恍惚，重重翠幕深金屏。\\n仙梯难攀俗缘重，浪凭青鸟通丁宁。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4564', 'poem', '唐', '武则天', '石淙', '三山十洞光玄箓，玉峤金峦镇紫微。\\n均露均霜标胜壤，交风交雨列皇畿。\\n万仞高岩藏日色，千寻幽涧浴云衣。\\n且驻欢筵赏仁智，雕鞍薄晚杂尘飞。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4565', 'poem', '唐', '罗隐', '扇上画牡丹', '为爱红芳满砌阶，教人扇上画将来。\\n叶随彩笔参差长，花逐轻风次第开。\\n闲挂几曾停蛱蝶，频摇不怕落莓苔。\\n根生无地如仙桂，疑是姮娥月里栽。', '2018-03-09 15:46:00');
INSERT INTO `t_poem` VALUES ('4566', 'poem', '唐', '王建', '相和歌辞·采桑', '鸟鸣桑叶间，叶绿条复柔。\\n攀看去手近，放下长长钩。\\n黄花盖野田，白马少年游。\\n所念岂回顾，良人在高楼。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4567', 'poem', '唐', '李世民', '冬日临昆明池', '石鲸分玉溜，劫烬隐平沙。\\n柳影冰无叶，梅心冻有花。\\n寒野凝朝雾，霜天散夕霞。\\n欢情犹未极，落景遽西斜。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4568', 'poem', '唐', '李商隐', '寄罗劭兴', '棠棣黄花发，忘忧碧叶齐。\\n人闲微病酒，燕重远兼泥。\\n混沌何由凿，青冥未有梯。\\n高阳旧徒侣，时复一相携。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4569', 'poem', '唐', '杜牧', '春思', '岂君心的的，嗟我泪涓涓。\\n绵羽啼来久，锦鳞书未传。\\n兽垆凝冷焰，罗幕蔽晴烟。\\n自是求佳梦，何须讶昼眠。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4570', 'poem', '唐', '刘长卿', '清明后登城眺望', '风景清明后，云山睥睨前。\\n百花如旧日，万井出新烟。\\n草色无空地，江流合远天。\\n长安在何处，遥指夕阳边。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4571', 'poem', '唐', '王建', '采桑', '鸟鸣桑叶间，绿条复柔柔。\\n攀看去手近，放下长长钩。\\n黄花盖野田，白马少年游。\\n所念岂回顾，良人在高楼。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4572', 'poem', '唐', '李白', '驾去温泉后赠杨山人', '少年落魄楚汉间，风尘萧瑟多苦颜。\\n自言管葛竟谁许，长吁莫错还闭关。\\n一朝君王垂拂拭，剖心输丹雪胸臆。\\n忽蒙白日回景光，直上青云生羽翼。\\n幸陪鸾辇出鸿都，身骑飞龙天马驹。\\n王公大人借颜色，金璋紫绶来相趋。\\n当时结交何纷纷，片言道合惟有君。\\n待吾尽节报明主，然后相携卧白云。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4573', 'poem', '唐', '杜甫', '祠南夕望', '百丈牵江色，孤舟泛日斜。\\n兴来犹杖屦，目断更云沙。\\n山鬼迷春竹，湘娥倚暮花。\\n湖南清绝地，万古一长嗟。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4574', 'poem', '唐', '白居易', '赠内', '生为同室亲，死为同穴尘。\\n他人尚相勉，而况我与君。\\n黔娄固穷士，妻贤忘其贫。\\n冀缺一农夫，妻敬俨如宾。\\n陶潜不营生，翟氏自爨薪。\\n梁鸿不肯仕，孟光甘布裙。\\n君虽不读书，此事耳亦闻。\\n至此千载后，传是何如人。\\n人生未死间，不能忘其身。\\n所须者衣食，不过饱与温。\\n蔬食足充饥，何必膏粱珍。\\n缯絮足御寒，何必锦绣文。\\n君家有贻训，清白遗子孙。\\n我亦贞苦士，与君新结婚。\\n庶保贫与素，偕老同欣欣。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4575', 'poem', '唐', '张籍', '学仙', '楼观开朱门，树木连房廊。\\n中有学仙人，少年休谷粮。\\n高冠如芙蓉，霞月披衣裳。\\n六时朝上清，佩玉纷锵锵。\\n自言天老书，秘覆云锦囊。\\n百年度一人，妄泄有灾殃。\\n每占有仙相，然后传此方。\\n先生坐中堂，弟子跪四厢。\\n金刀截身发，结誓焚灵香。\\n弟子得其诀，清斋入空房。\\n守神保元气，动息随天罡。\\n炉烧丹砂尽，昼夜候火光。\\n药成既服食，计日乘鸾凰。\\n虚空无灵应，终岁安所望。\\n勤劳不能成，疑虑积心肠。\\n虚羸生疾疹，寿命多夭伤。\\n身殁惧人见，夜埋山谷傍。\\n求道慕灵异，不如守寻常。\\n先王知其非，戒之在国章。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4576', 'poem', '唐', '王维', '秋夜独坐怀内弟崔兴宗', '夜静群动息，蟪蛄声悠悠。\\n庭槐北风响，日夕方高秋。\\n思子整羽翰，及时当云浮。\\n吾生将白首，岁晏思沧州。\\n高足在旦暮，肯为南亩俦。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4577', 'poem', '唐', '贯休', '孤云', '将比鹭鸶还恐屈，始思残雪不如多。\\n清风相引去更远，皎洁孤高奈尔何。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4578', 'poem', '唐', '罗邺', '叹别', '北来南去几时休，人在光阴似箭流。\\n直待江山尽无路，始因抛得别离愁。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4579', 'poem', '唐', '元稹', '饮新酒', '闻君新酒熟，况值菊花秋。\\n莫怪平生志，图销尽日愁。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4580', 'poem', '唐', '陈子昂', '春日登金华观', '白玉仙台古，丹丘别望遥。\\n山川乱云日，楼榭入烟霄。\\n鹤舞千年树，虹飞百尺桥。\\n还疑赤松子，天路坐相邀。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4581', 'poem', '唐', '李中', '钟陵禁烟寄从弟', '落絮飞花日又西，踏青无侣草萋萋。\\n交亲书断竟不到，忍听黄昏杜宇啼。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4582', 'poem', '唐', '白居易', '厅前桂', '天台岭上凌霜树，司马厅前委地丛。\\n一种不生明月里，山中犹校胜尘中。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4583', 'poem', '唐', '高力士', '感巫州荠菜', '两京作斤卖，五溪无人采。\\n夷夏虽有殊，气味都不改。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4584', 'poem', '唐', '胡曾', '咏史诗·兰台宫', '迟迟春日满长空，亡国离宫蔓草中。\\n宋玉不忧人事变，从游那赋大王风。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4585', 'poem', '唐', '高适', '送魏八', '更沽淇上酒，还泛驿前舟。\\n为惜故人去，复怜嘶马愁。\\n云山行处合，风雨兴中秋。\\n此路无知己，明珠莫暗投。', '2018-03-09 15:46:12');
INSERT INTO `t_poem` VALUES ('4586', 'poem', '唐', '杜甫', '雨', '始贺天休雨，还嗟地出雷。\\n骤看浮峡过，密作渡江来。\\n牛马行无色，蛟龙斗不开。\\n干戈盛阴气，未必自阳台。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4587', 'poem', '唐', '白居易', '鹤', '人各有所好，物固无常宜。\\n谁谓尔能舞，不如闲立时。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4588', 'poem', '唐', '韦应物', '咏琥珀', '曾为老茯神，本是寒松液。\\n蚊蚋落其中，千年犹可觌。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4589', 'poem', '唐', '李峤', '梅', '大庾敛寒光，南枝独早芳。\\n雪含朝暝色，风引去来香。\\n妆面回青镜，歌尘起画梁。\\n若能遥止渴，何暇泛琼浆。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4590', 'poem', '唐', '白居易', '杂曲歌辞·乐世', '管急丝繁拍渐稠，绿腰宛转曲终头。\\n诚知乐世声声乐，老病人听未免愁。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4591', 'poem', '唐', '冯道', '偶作', '莫为危时便怆神，前程往往有期因。\\n须知海岳归明主，未必乾坤陷吉人。\\n道德几时曾去世，舟车何处不通津。\\n但教方寸无诸恶，狼虎丛中也立身。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4592', 'poem', '唐', '白居易', '早夏游平原回', '夏早日初长，南风草木香。\\n肩舆颇平稳，涧路甚清凉。\\n紫蕨行看采，青梅旋摘尝。\\n疗饥兼解渴，一盏冷云浆。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4593', 'poem', '唐', '黄滔', '归思', '蓟北风烟空汉月，湘南云水半蛮边。\\n寒为旅雁暖还去，秦越离家可十年。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4594', 'poem', '唐', '李隆基', '题梅妃画真', '忆昔娇妃在紫宸，铅华不御得天真。\\n霜绡虽似当时态，争奈娇波不顾人。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4595', 'poem', '唐', '李贺', '听颖师琴歌', '别浦云归桂花渚，蜀国弦中双凤语。\\n芙蓉叶落秋鸾离，越王夜起游天姥。\\n暗佩清臣敲水玉，渡海蛾眉牵白鹿。\\n谁看挟剑赴长桥，谁看浸发题春竹。\\n竺僧前立当吾门，梵宫真相眉棱尊。\\n古琴大轸长八尺，峄阳老树非桐孙。\\n凉馆闻弦惊病客，药囊暂别龙须席。\\n请歌直请卿相歌，奉礼官卑复何益。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4596', 'poem', '唐', '许浑', '学仙二首', '汉武迎仙紫禁秋，玉笙瑶瑟祀昆丘。\\n年年望断无消息，空闭重城十二楼。\\n心期仙诀意无穷，采画云车起寿宫。\\n闻有三山未知处，茂陵松柏满西风。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4597', 'poem', '唐', '戴叔伦', '劝陆三饮酒', '寒郊好天气，劝酒莫辞频。\\n扰扰钟陵市，无穷不醉人。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4598', 'poem', '唐', '韦庄', '虚席', '一闭香闺后，罗衣尽施僧。\\n鼠偷筵上果，蛾扑帐前灯。\\n土蚀钗无凤，尘生镜少菱。\\n有时还影响，花叶曳香缯。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4599', 'poem', '唐', '杜甫', '北邻', '明府岂辞满，藏身方告劳。\\n青钱买野竹，白帻岸江皋。\\n爱酒晋山简，能诗何水曹。\\n时来访老疾，步屟到蓬蒿。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4600', 'poem', '唐', '刘长卿', '松江独宿', '洞庭初下叶，孤客不胜愁。\\n明月天涯夜，青山江上秋。\\n一官成白首，万里寄沧洲。\\n久被浮名系，能无愧海鸥。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4601', 'poem', '唐', '王维', '既蒙宥罪旋复拜官伏感圣恩窃书鄙意兼奉简新…等诸公', '忽蒙汉诏还冠冕，始觉殷王解网罗。\\n日比皇明犹自暗，天齐圣寿未云多。\\n花迎喜气皆知笑，鸟识欢心亦解歌。\\n闻道百城新佩印，还来双阙共鸣珂。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4602', 'poem', '唐', '杜牧', '杏园', '夜来微雨洗芳尘，公子骅骝步贴匀。\\n莫怪杏园憔悴去，满城多少插花人。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4603', 'poem', '唐', '王维', '辋川集·华子冈', '飞鸟去不穷，连山复秋色。\\n上下华子冈，惆怅情何极。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4604', 'poem', '唐', '杜牧', '蔷薇花', '朵朵精神叶叶柔，雨晴香拂醉人头。\\n石家锦幛依然在，闲倚狂风夜不收。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4605', 'poem', '唐', '李白', '九日登巴陵置酒望洞庭水军', '九日天气清，登高无秋云。\\n造化辟川岳，了然楚汉分。\\n长风鼓横波，合沓蹙龙文。\\n忆昔传游豫，楼船壮横汾。\\n今兹讨鲸鲵，旌旆何缤纷。\\n白羽落酒樽，洞庭罗三军。\\n黄花不掇手，战鼓遥相闻。\\n剑舞转颓阳，当时日停曛。\\n酣歌激壮士，可以摧妖氛。\\n龌龊东篱下，渊明不足群。', '2018-03-09 15:46:29');
INSERT INTO `t_poem` VALUES ('4606', 'poem', '唐', '白居易', '新制绫袄成感而有咏', '水波文袄造新成，绫软绵匀温复轻。\\n晨兴好拥向阳坐，晚出宜披踏雪行。\\n鹤氅毳疏无实事，木棉花冷得虚名。\\n宴安往往叹侵夜，卧稳昏昏睡到明。\\n百姓多寒无可救，一身独暖亦何情！\\n心中为念农桑苦，耳里如闻饥冻声。\\n争得大裘长万丈，与君都盖洛阳城！', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4607', 'poem', '唐', '羊士谔', '乾元初，严黄门自京兆少尹贬牧巴郡…十四韵刻于石壁', '石座双峰古，云泉九曲深。\\n寂寥疏凿意，芜没岁时侵。\\n绕席流还壅，浮杯咽复沉。\\n追怀王谢侣，更似会稽岑。\\n谁谓天池翼，相期宅畔吟。\\n光辉轻尺璧，然诺重黄金。\\n几醉东山妓，长悬北阙心。\\n蕙兰留杂佩，桃李想华簪。\\n闭阁余何事，鸣驺亦屡寻。\\n轩裳遵往辙，风景憩中林。\\n横吹多凄调，安歌送好音。\\n初筵方侧弁，故老忽沾襟。\\n盛世当弘济，平生谅所钦。\\n无能愧陈力，惆怅拂瑶琴。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4608', 'poem', '唐', '薛能', '除夜作', '和吹度穹旻，虚徐接建寅。\\n不辞加一岁，唯喜到三春。\\n燎照云烟好，幡悬井邑新。\\n祯祥应北极，调燮验平津。\\n树欲含迟日，山将退旧尘。\\n兰萎残此夜，竹爆和诸邻。\\n祝寿思明圣，驱傩看鬼神。\\n团圆多少辈，眠寝独劳筋。\\n茜旆犹双节，雕盘又五辛。\\n何当平贼后，归作自由身。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4609', 'poem', '唐', '李白', '郢门秋怀', '郢门一为客，巴月三成弦。\\n朔风正摇落，行子愁归旋。\\n杳杳山外日，茫茫江上天。\\n人迷洞庭水，雁度潇湘烟。\\n清旷谐宿好，缁磷及此年。\\n百龄何荡漾，万化相推迁。\\n空谒苍梧帝，徒寻溟海仙。\\n已闻蓬海浅，岂见三桃圆。\\n倚剑增浩叹，扪襟还自怜。\\n终当游五湖，濯足沧浪泉。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4610', 'poem', '唐', '刘希夷', '采桑', '杨柳送行人，青青西入秦。\\n谁家采桑女，楼上不胜春。\\n盈盈灞水曲，步步春芳绿。\\n红脸耀明珠，绛唇含白玉。\\n回首渭桥东，遥怜春色同。\\n青丝娇落日，缃绮弄春风。\\n携笼长叹息，逶迟恋春色。\\n看花若有情，倚树疑无力。\\n薄暮思悠悠，使君南陌头。\\n相逢不相识，归去梦青楼。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4611', 'poem', '唐', '刘禹锡', '聚蚊谣', '沉沉夏夜兰堂开，飞蚊伺暗声如雷。\\n嘈然欻起初骇听，殷殷若自南山来。\\n喧腾鼓舞喜昏黑，昧者不分听者惑。\\n露花滴沥月上天，利觜迎人著不得。\\n我躯七尺尔如芒，我孤尔众能我伤。\\n天生有时不可遏，为尔设幄潜匡床。\\n清商一来秋日晓，羞尔微形饲丹鸟。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4612', 'poem', '唐', '李白', '登邯郸洪波台置酒观发兵', '我把两赤羽，来游燕赵间。\\n天狼正可射，感激无时闲。\\n观兵洪波台，倚剑望玉关。\\n请缨不系越，且向燕然山。\\n风引龙虎旗，歌钟昔追攀。\\n击筑落高月，投壶破愁颜。\\n遥知百战胜，定扫鬼方还。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4613', 'poem', '唐', '杜甫', '浣溪沙', '照日深红暖见鱼，连村绿暗晚藏乌，黄童白叟聚雎盱。\\n麋鹿逢人虽未惯，猿猱闻鼓不须呼，归来说与采桑姑。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4614', 'poem', '唐', '张何', '织鸟', '季春三月里，戴胜下桑来。\\n映日华冠动，迎风绣羽开。\\n候惊蚕事晚，织向女工裁。\\n旅宿依花定，轻飞绕树回。\\n欲过高阁柳，更拂小庭梅。\\n所寄一枝在，宁忧弋者猜。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4615', 'poem', '唐', '白居易', '劝酒十四首·不如来饮酒七首', '莫隐深山去，君应到自嫌。\\n齿伤朝水冷，貌苦夜霜严。\\n渔去风生浦，樵归雪满岩。\\n不如来饮酒，相对醉厌厌。\\n莫作农夫去，君应见自愁。\\n迎春犁瘦地，趁晚喂羸牛。\\n数被官加税，稀逢岁有秋。\\n不如来饮酒，酒伴醉悠悠。\\n莫作商人去，恓惶君未谙。\\n雪霜行塞北，风水宿江南。\\n藏镪百千万，沉舟十二三。\\n不如来饮酒，仰面醉酣酣。\\n莫事长征去，辛勤难具论。\\n何曾画麟阁，只是老辕门。\\n虮虱衣中物，刀枪面上痕。\\n不如来饮酒，合眼醉昏昏。\\n莫学长生去，仙方误杀君。\\n那将薤上露，拟待鹤边云。\\n矻矻皆烧药，累累尽作坟。\\n不如来饮酒，闲坐醉醺醺。\\n莫上青云去，青云足爱憎。\\n自贤夸智慧，相纠斗功能。\\n鱼烂缘吞饵，蛾焦为扑灯。\\n不如来饮酒，任性醉腾腾。\\n莫入红尘去，令人心力劳。\\n相争两蜗角，所得一牛毛。\\n且灭嗔中火，休磨笑里刀。\\n不如来饮酒，稳卧醉陶陶。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4616', 'poem', '唐', '秦韬玉', '边将', '剑光如电马如风，百捷长轻是掌中。\\n无定河边蕃将死，受降城外虏尘空。\\n旗缝雁翅和竿袅，箭撚雕翎逐隼雄。\\n自指燕山最高石，不知谁为勒殊功。', '2018-03-09 15:46:40');
INSERT INTO `t_poem` VALUES ('4617', 'poem', '唐', '贺知章', '唐禅社首乐章·太和', '肃我成命，於昭黄祇，裘冕而祀，陟降在斯。\\n五音克备，八变聿施。缉熙肆靖，厥心匪离。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4618', 'poem', '唐', '韦应物', '淮上遇洛阳李主簿', '结茅临古渡，卧见长淮流。\\n窗里人将老，门前树已秋。\\n寒山独过雁，暮雨远来舟。\\n日夕逢归客，那能忘旧游。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4619', 'poem', '唐', '白居易', '访陈二', '晓垂朱绶带，晚著白纶巾。\\n出去为朝客，归来是野人。\\n两餐聊过日，一榻足容身。\\n此外皆闲事，时时访老陈。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4620', 'poem', '唐', '秦韬玉', '春游', '选胜逢君叙解携，思和芳草远烟迷。\\n小梅香里黄莺啭，垂柳阴中白马嘶。\\n春引美人歌遍熟，风牵公子酒旗低。\\n早知有此关身事，悔不前年住越溪。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4621', 'poem', '唐', '骆宾王', '秋晨同淄川毛司马秋九咏·秋萤', '玉虬分静夜，金萤照晚凉。\\n含辉疑泛月，带火怯凌霜。\\n散彩萦虚牖，飘花绕洞房。\\n下帷如不倦，当解惜馀光。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4622', 'poem', '唐', '齐己', '喜夏雨', '四郊云影合，千里雨声来。\\n尽洗红埃去，并将清气回。\\n潺湲浮楚甸，萧散露荆台。\\n欲赋随车瑞，濡毫渴謏才。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4623', 'poem', '唐', '贯休', '陈情献蜀皇帝', '河北江东处处灾，唯闻全蜀勿尘埃。一瓶一钵垂垂老，\\n千水千山得得来，奈菀幽栖多胜景，巴歈陈贡愧非才。\\n自惭林薮龙钟者，亦得亲登郭隗台。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4624', 'poem', '唐', '杜甫', '野望', '清秋望不极，迢递起曾阴。\\n远水兼天净，孤城隐雾深。\\n叶稀风更落，山迥日初沈。\\n独鹤归何晚，昏鸦已满林。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4625', 'poem', '唐', '罗隐', '夏州胡常侍', '百尺高台勃勃州，大刀长戟汉诸侯。\\n征鸿过尽边云阔，战马闲来塞草秋。\\n国计已推肝胆许，家财不为子孙谋。\\n仍闻陇蜀由多事，深喜将军未白头。', '2018-03-09 15:46:41');
INSERT INTO `t_poem` VALUES ('4626', 'poem', '唐', '李白', '戏赠杜甫（以下见《唐诗纪事》）', '饭颗山头逢杜甫，顶戴笠子日卓午。借问别来太瘦生，总为从前作诗苦。', '2018-03-09 15:46:49');
INSERT INTO `t_poem` VALUES ('4627', 'poem', '宋', '陆游', '恩封渭南伯唐诗人赵嘏为渭南尉当时谓之赵渭', '老向人间久倦游，君恩乞与渭川秋。虚名定作陈惊坐，好句真惭赵倚楼。栈豆十年沾病马，烟波万里著浮鸥。就封他日轻裘去，应过三峰处处留。', '2018-03-09 15:46:49');
INSERT INTO `t_poem` VALUES ('4628', 'poem', '宋', '刘克庄', '唐诗', '瀛洲学士风流远，中叶唐惭贞观唐。灵武拾遗晚羁旅，开元供奉老佯狂。戏苕翡翠非伦拟，撼树蚍蜉不揣量。赖有元和韩十八，骑麟被发共翱翔。', '2018-03-09 15:46:49');
INSERT INTO `t_poem` VALUES ('4629', 'poem', '明', '袁宗道', '同惟长舅读唐诗有感', '数卷陈言逐字新，眼前君是赏音人。家家椟玉谁知赝，处处抽龙总忌真。再舍肉黥居易句，重捐金铸浪仙身。一从马粪卮言出，难洗诗家入骨尘。', '2018-03-09 15:46:49');
INSERT INTO `t_poem` VALUES ('4630', 'poem', '宋', '周南', '读唐诗', '一日勾消两卷诗，趁心抄得是灯时。隔窗一閧狂风雨，无数啁啾冻雀儿。', '2018-03-09 15:46:49');
INSERT INTO `t_poem` VALUES ('4631', 'poem', '宋', '朱继芳', '桃源官罢芸居以唐诗拙作赠别', '自作还相送，唐诗结伴来。归装携此重，笑口为君开。莲上巢难稳，桃头郁垒神。何须千岁上，即此是新陈。', '2018-03-09 15:46:49');
INSERT INTO `t_poem` VALUES ('4632', 'poem', '宋', '戴复古', '伏龙山民宋正甫湖山清隐乃唐诗人陈陶故圃曾', '故人昔住金华峰，面带双溪秋水容。故人今住伏龙山，陈陶故圃茅三间。千载清风徐孺子，门前共此一湖水。百花洲上万垂杨，白鸥群里歌沧浪。故人心事孺子高，故人诗句今陈陶。短衣饭牛不逢尧，何如绣鞍上着锦宫袍。瓦盆对客酌松醪，何如紫霞觞泛碧葡萄。豆萁然火度寒宵，何如玉堂夜照金莲膏。吟成秃笔写芭蕉，何如沉香亭北醉挥毫。再三问君君不对，目送飞鸿楚天外。细读山中招隐篇，超然意与烟霞会。照影湖边双鬓皓，此计知之悔不早。三椽可办愿卜隣，荷锸相随种瑶草。', '2018-03-09 15:46:49');
INSERT INTO `t_poem` VALUES ('4633', 'poem', '唐', '曹唐', '又游仙诗一绝（见《唐诗纪事》）', '靖节先生几代孙，青娥曾接玉郎魂。春风流水还无赖，偷放桃花出洞门。', '2018-03-09 15:46:49');
INSERT INTO `t_poem` VALUES ('4634', 'poem', '唐', '许瀍', '纪梦（一作梦入琼台，本事诗及唐诗纪事并作许浑）', '晚入瑶台露气清，天风飞下步虚声。尘心未尽俗缘在，十里下山空月明。', '2018-03-09 15:46:49');
