INSERT INTO `t_poem` VALUES ('4646', 'poem', '宋', '刘克庄', '唐诗', '瀛洲学士风流远，中叶唐惭贞观唐。灵武拾遗晚羁旅，开元供奉老佯狂。戏苕翡翠非伦拟，撼树蚍蜉不揣量。赖有元和韩十八，骑麟被发共翱翔。', '2018-03-09 15:47:04');
INSERT INTO `t_poem` VALUES ('4647', 'poem', '明', '袁宗道', '同惟长舅读唐诗有感', '数卷陈言逐字新，眼前君是赏音人。家家椟玉谁知赝，处处抽龙总忌真。再舍肉黥居易句，重捐金铸浪仙身。一从马粪卮言出，难洗诗家入骨尘。', '2018-03-09 15:47:04');
INSERT INTO `t_poem` VALUES ('4648', 'poem', '宋', '周南', '读唐诗', '一日勾消两卷诗，趁心抄得是灯时。隔窗一閧狂风雨，无数啁啾冻雀儿。', '2018-03-09 15:47:04');
INSERT INTO `t_poem` VALUES ('4649', 'poem', '宋', '朱继芳', '桃源官罢芸居以唐诗拙作赠别', '自作还相送，唐诗结伴来。归装携此重，笑口为君开。莲上巢难稳，桃头郁垒神。何须千岁上，即此是新陈。', '2018-03-09 15:47:04');
INSERT INTO `t_poem` VALUES ('4650', 'poem', '宋', '戴复古', '伏龙山民宋正甫湖山清隐乃唐诗人陈陶故圃曾', '故人昔住金华峰，面带双溪秋水容。故人今住伏龙山，陈陶故圃茅三间。千载清风徐孺子，门前共此一湖水。百花洲上万垂杨，白鸥群里歌沧浪。故人心事孺子高，故人诗句今陈陶。短衣饭牛不逢尧，何如绣鞍上着锦宫袍。瓦盆对客酌松醪，何如紫霞觞泛碧葡萄。豆萁然火度寒宵，何如玉堂夜照金莲膏。吟成秃笔写芭蕉，何如沉香亭北醉挥毫。再三问君君不对，目送飞鸿楚天外。细读山中招隐篇，超然意与烟霞会。照影湖边双鬓皓，此计知之悔不早。三椽可办愿卜隣，荷锸相随种瑶草。', '2018-03-09 15:47:04');
INSERT INTO `t_poem` VALUES ('4651', 'poem', '唐', '曹唐', '又游仙诗一绝（见《唐诗纪事》）', '靖节先生几代孙，青娥曾接玉郎魂。春风流水还无赖，偷放桃花出洞门。', '2018-03-09 15:47:04');
INSERT INTO `t_poem` VALUES ('4652', 'poem', '唐', '许瀍', '纪梦（一作梦入琼台，本事诗及唐诗纪事并作许浑）', '晚入瑶台露气清，天风飞下步虚声。尘心未尽俗缘在，十里下山空月明。', '2018-03-09 15:47:04');
INSERT INTO `t_poem` VALUES ('4653', 'poem', '唐', '杜甫', '雨', '始贺天休雨，还嗟地出雷。\\n骤看浮峡过，密作渡江来。\\n牛马行无色，蛟龙斗不开。\\n干戈盛阴气，未必自阳台。', '2018-03-09 15:49:04');
INSERT INTO `t_poem` VALUES ('4654', 'poem', '唐', '白居易', '鹤', '人各有所好，物固无常宜。\\n谁谓尔能舞，不如闲立时。', '2018-03-09 15:49:04');
INSERT INTO `t_poem` VALUES ('4655', 'poem', '唐', '韦应物', '咏琥珀', '曾为老茯神，本是寒松液。\\n蚊蚋落其中，千年犹可觌。', '2018-03-09 15:49:04');
INSERT INTO `t_poem` VALUES ('4656', 'poem', '唐', '李峤', '梅', '大庾敛寒光，南枝独早芳。\\n雪含朝暝色，风引去来香。\\n妆面回青镜，歌尘起画梁。\\n若能遥止渴，何暇泛琼浆。', '2018-03-09 15:49:04');
INSERT INTO `t_poem` VALUES ('4657', 'poem', '唐', '白居易', '杂曲歌辞·乐世', '管急丝繁拍渐稠，绿腰宛转曲终头。\\n诚知乐世声声乐，老病人听未免愁。', '2018-03-09 15:49:04');
INSERT INTO `t_poem` VALUES ('4658', 'poem', '唐', '冯道', '偶作', '莫为危时便怆神，前程往往有期因。\\n须知海岳归明主，未必乾坤陷吉人。\\n道德几时曾去世，舟车何处不通津。\\n但教方寸无诸恶，狼虎丛中也立身。', '2018-03-09 15:49:04');
INSERT INTO `t_poem` VALUES ('4659', 'poem', '唐', '白居易', '早夏游平原回', '夏早日初长，南风草木香。\\n肩舆颇平稳，涧路甚清凉。\\n紫蕨行看采，青梅旋摘尝。\\n疗饥兼解渴，一盏冷云浆。', '2018-03-09 15:49:04');
INSERT INTO `t_poem` VALUES ('4660', 'poem', '唐', '黄滔', '归思', '蓟北风烟空汉月，湘南云水半蛮边。\\n寒为旅雁暖还去，秦越离家可十年。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4661', 'poem', '唐', '李隆基', '题梅妃画真', '忆昔娇妃在紫宸，铅华不御得天真。\\n霜绡虽似当时态，争奈娇波不顾人。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4662', 'poem', '唐', '李贺', '听颖师琴歌', '别浦云归桂花渚，蜀国弦中双凤语。\\n芙蓉叶落秋鸾离，越王夜起游天姥。\\n暗佩清臣敲水玉，渡海蛾眉牵白鹿。\\n谁看挟剑赴长桥，谁看浸发题春竹。\\n竺僧前立当吾门，梵宫真相眉棱尊。\\n古琴大轸长八尺，峄阳老树非桐孙。\\n凉馆闻弦惊病客，药囊暂别龙须席。\\n请歌直请卿相歌，奉礼官卑复何益。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4663', 'poem', '唐', '许浑', '学仙二首', '汉武迎仙紫禁秋，玉笙瑶瑟祀昆丘。\\n年年望断无消息，空闭重城十二楼。\\n心期仙诀意无穷，采画云车起寿宫。\\n闻有三山未知处，茂陵松柏满西风。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4664', 'poem', '唐', '戴叔伦', '劝陆三饮酒', '寒郊好天气，劝酒莫辞频。\\n扰扰钟陵市，无穷不醉人。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4665', 'poem', '唐', '韦庄', '虚席', '一闭香闺后，罗衣尽施僧。\\n鼠偷筵上果，蛾扑帐前灯。\\n土蚀钗无凤，尘生镜少菱。\\n有时还影响，花叶曳香缯。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4666', 'poem', '唐', '杜甫', '北邻', '明府岂辞满，藏身方告劳。\\n青钱买野竹，白帻岸江皋。\\n爱酒晋山简，能诗何水曹。\\n时来访老疾，步屟到蓬蒿。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4667', 'poem', '唐', '刘长卿', '松江独宿', '洞庭初下叶，孤客不胜愁。\\n明月天涯夜，青山江上秋。\\n一官成白首，万里寄沧洲。\\n久被浮名系，能无愧海鸥。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4668', 'poem', '唐', '王维', '既蒙宥罪旋复拜官伏感圣恩窃书鄙意兼奉简新…等诸公', '忽蒙汉诏还冠冕，始觉殷王解网罗。\\n日比皇明犹自暗，天齐圣寿未云多。\\n花迎喜气皆知笑，鸟识欢心亦解歌。\\n闻道百城新佩印，还来双阙共鸣珂。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4669', 'poem', '唐', '杜牧', '杏园', '夜来微雨洗芳尘，公子骅骝步贴匀。\\n莫怪杏园憔悴去，满城多少插花人。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4670', 'poem', '唐', '王维', '辋川集·华子冈', '飞鸟去不穷，连山复秋色。\\n上下华子冈，惆怅情何极。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4671', 'poem', '唐', '杜牧', '蔷薇花', '朵朵精神叶叶柔，雨晴香拂醉人头。\\n石家锦幛依然在，闲倚狂风夜不收。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4672', 'poem', '唐', '李白', '九日登巴陵置酒望洞庭水军', '九日天气清，登高无秋云。\\n造化辟川岳，了然楚汉分。\\n长风鼓横波，合沓蹙龙文。\\n忆昔传游豫，楼船壮横汾。\\n今兹讨鲸鲵，旌旆何缤纷。\\n白羽落酒樽，洞庭罗三军。\\n黄花不掇手，战鼓遥相闻。\\n剑舞转颓阳，当时日停曛。\\n酣歌激壮士，可以摧妖氛。\\n龌龊东篱下，渊明不足群。', '2018-03-09 15:49:05');
INSERT INTO `t_poem` VALUES ('4673', 'poem', '唐', '杜荀鹤', '题衡阳隐士山居', '闲居不问世如何，云起山门日已斜。\\n放鹤去寻三岛客，任人来看四时花。\\n松醪腊酝安神酒，布水宵煎觅句茶。\\n毕竟金多也头白，算来争得似君家。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4674', 'poem', '唐', '柳宗元', '首春逢耕者', '南楚春候早，余寒已滋荣。\\n土膏释原野，白蛰竞所营。\\n缀景未及郊，穑人先偶耕。\\n园林幽鸟啭，渚泽新泉清。\\n农事诚素务，羁囚阻平生。\\n故池想芜没，遗亩当榛荆。\\n慕隐既有系，图功遂无成。\\n聊从田父言，款曲陈此情。\\n眷然抚耒耜，回首烟云横。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4675', 'poem', '唐', '刘希夷', '相和歌辞·采桑', '杨柳送行人，青青西入秦。\\n秦家采桑女，楼上不胜春。\\n盈盈灞水曲，步步春芳绿。\\n红脸耀明珠，绛唇含白玉。\\n回首渭桥东，遥怜树色同。\\n青丝娇落日，缃绮弄春风。\\n携笼长叹息，逶迤恋春色。\\n看花若有情，倚树疑无力。\\n薄暮思悠悠，使君南陌头。\\n相逢不相识，归去梦青楼。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4676', 'poem', '唐', '鲍溶', '首夏', '昨日青春去，晚峰尚含妍。\\n虽留有馀态，脉脉防忧煎。\\n幽人惜时节，对此感流年。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4677', 'poem', '唐', '李白', '奔亡道中五首', '苏武天山上，田横海岛边。\\n万重关塞断，何日是归年。\\n亭伯去安在，李陵降未归。\\n愁容变海色，短服改胡衣。\\n谈笑三军却，交游七贵疏。\\n仍留一只箭，未射鲁连书。\\n函谷如玉关，几时可生还。\\n洛阳为易水，嵩岳是燕山。\\n俗变羌胡语，人多沙塞颜。\\n申包惟恸哭，七日鬓毛斑。\\n淼淼望湖水，青青芦叶齐。\\n归心落何处，日没大江西。\\n歇马傍春草，欲行远道迷。\\n谁忍子规鸟，连声向我啼。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4678', 'poem', '唐', '杜牧', '旅情', '窗虚枕簟凉，寝倦忆潇湘。\\n山色几时老，人心终日忙。\\n松风半夜雨，帘月满堂霜。\\n匹马好归去，江头橘正香。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4679', 'poem', '唐', '韦庄', '庭前菊', '为忆长安烂熳开，我今移尔满庭栽。\\n红兰莫笑青青色，曾向龙山泛酒来。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4680', 'poem', '唐', '岑参', '客舍悲秋，有怀两省旧游，呈幕中诸公', '三度为郎便白头，一从出守五经秋。\\n莫言圣主长不用，其那苍生应未休。\\n人间岁月如流水，客舍秋风今又起。\\n不知心事向谁论，江上蝉鸣空满耳。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4681', 'poem', '唐', '权德舆', '诫言', '言之或未行，前哲所不取。\\n方寸虽浩然，因之三缄口。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4682', 'poem', '唐', '韩愈', '奉和虢州刘给事使君三堂新题二十一咏·荷池', '风雨秋池上，高荷盖水繁。\\n未谙鸣摵摵，那似卷翻翻。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4683', 'poem', '唐', '贾岛', '阮籍啸台', '如闻长啸春风里，荆棘丛边访旧踪。\\n地接苏门山近远，荒台突兀抵高峰。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4684', 'poem', '唐', '契盈', '句', '三千里外一条水，十二时中两度潮。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4685', 'poem', '唐', '李贺', '长平箭头歌', '漆灰骨末丹水沙，凄凄古血生铜花。\\n白翎金簳雨中尽，直馀三脊残狼牙。\\n我寻平原乘两马，驿东石田蒿坞下。\\n风长日短星萧萧，黑旗云湿悬空夜。\\n左魂右魄啼肌瘦，酪瓶倒尽将羊炙。\\n虫栖雁病芦笋红，回风送客吹阴火。\\n访古汍澜收断镞，折锋赤璺曾刲肉。\\n南陌东城马上儿，劝我将金换簝竹。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4686', 'poem', '唐', '杜甫', '夕烽', '夕烽来不近，每日报平安。\\n塞上传光小，云边落点残。\\n照秦通警急，过陇自艰难。\\n闻道蓬莱殿，千门立马看。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4687', 'poem', '唐', '佚名', '褰帐', '揉蓝绿色曲尘开，静见三星入坐来。\\n桂影已圆攀折后，子孙长作栋梁材。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4688', 'poem', '唐', '曹唐', '刘晨阮肇游天台', '树入天台石路新，云和草静迥无尘。\\n烟霞不省生前事，水木空疑梦后身。\\n往往鸡鸣岩下月，时时犬吠洞中春。\\n不知此地归何处，须就桃源问主人。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4689', 'poem', '唐', '元稹', '西明寺牡丹', '花向琉璃地上生，光风炫转紫云英。\\n自从天女盘中见，直至今朝眼更明。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4690', 'poem', '唐', '韩愈', '酬王二十舍人雪中见寄', '三日柴门拥不开，阶平庭满白皑皑。\\n今朝蹋作琼瑶迹，为有诗从凤沼来。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4691', 'poem', '唐', '白居易', '戏题木兰花', '紫房日照胭脂拆，素艳风吹腻粉开。\\n怪得独饶脂粉态，木兰曾作女郎来。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4692', 'poem', '唐', '王翰', '春日归思', '杨柳青青杏发花，年光误客转思家。\\n不知湖上菱歌女，几个春舟在若耶。', '2018-03-09 15:49:08');
INSERT INTO `t_poem` VALUES ('4693', 'poem', '唐', '白居易', '病中答招饮者', '顾我镜中悲白发，尽君花下醉青春。\\n不缘眼痛兼身病，可是尊前第二人。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4694', 'poem', '唐', '韦应物', '咏声', '万物自生听，太空恒寂寥。\\n还从静中起，却向静中消。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4695', 'poem', '唐', '杜甫', '巳上人茅斋', '巳公茅屋下，可以赋新诗。\\n枕簟入林僻，茶瓜留客迟。\\n江莲摇白羽，天棘梦青丝。\\n空忝许询辈，难酬支遁词。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4696', 'poem', '唐', '姚合', '送雍陶及第归觐', '献亲冬集书，比橘复何如。\\n此去关山远，相思笑语疏。\\n路寻丹壑断，人近白云居。\\n幽石题名处，凭君亦记余。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4697', 'poem', '唐', '李白', '玉真仙人词', '玉真之仙人，时往太华峰。\\n清晨鸣天鼓，飙欻腾双龙。\\n弄电不辍手，行云本无踪。\\n几时入少室，王母应相逢。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4698', 'poem', '唐', '白居易', '京路', '西来为看秦山雪，东去缘寻洛苑春。\\n来去腾腾两京路，闲行除我更无人。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4699', 'poem', '唐', '王昌龄', '古意', '桃花四面发，桃叶一枝开。\\n欲暮黄鹂啭，伤心玉镜台。\\n清筝向明月，半夜春风来。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4700', 'poem', '唐', '徐夤', '司直巡官无诸移到玫瑰花', '芳菲移自越王台，最似蔷薇好并栽。\\n秾艳尽怜胜彩绘，嘉名谁赠作玫瑰。\\n春藏锦绣风吹拆，天染琼瑶日照开。\\n为报朱衣早邀客，莫教零落委苍苔。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4701', 'poem', '唐', '吴融', '王母庙', '鸾龙一夜降昆丘，遗庙千年枕碧流。\\n赚得武皇心力尽，忍看烟草茂陵秋。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4702', 'poem', '唐', '张籍', '赠姚合', '丹凤城门向晓开，千官相次入朝来。\\n唯君独走冲尘土，下马桥边报直回。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4703', 'poem', '唐', '张九龄', '和黄门卢侍御咏竹', '清切紫庭垂，葳蕤防露枝。\\n色无玄月变，声有惠风吹。\\n高节人相重，虚心世所知。\\n凤凰佳可食，一去一来仪。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4704', 'poem', '唐', '王维', '春日上方即事', '好读高僧传，时看辟谷方。\\n鸠形将刻仗，龟壳用支床。\\n柳色青山映，梨花夕鸟藏。\\n北窗桃李下，闲坐但焚香。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4705', 'poem', '唐', '陆龟蒙', '太湖叟', '细桨轻船卖石归，酒痕狼藉遍苔衣。\\n攻车战舰繁如织，不肯回头问是非。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4706', 'poem', '唐', '武元衡', '登阖闾古城', '登高望远自伤情，柳发花开映古城。\\n全盛已随流水去，黄鹂空啭旧春声。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4707', 'poem', '唐', '齐己', '船窗', '孤舸凭幽窗，清波逼面凉。\\n举头还有碍，低眼即无妨。\\n瞥过沙禽翠，斜分夕照光。\\n何时到山寺，上阁看江乡。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4708', 'poem', '唐', '郑谷', '梅', '江国正寒春信稳，岭头枝上雪飘飘。\\n何言落处堪惆怅，直是开时也寂寥。\\n素艳照尊桃莫比，孤香黏袖李须饶。\\n离人南去肠应断，片片随鞭过楚桥。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4709', 'poem', '唐', '白居易', '听歌六绝句。乐世（一名六么）', '管急弦繁拍渐稠，绿腰宛转曲终头。\\n诚知乐世声声乐，老病人听未免愁。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4710', 'poem', '唐', '李世民', '咏烛二首', '焰听风来动，花开不待春。\\n镇下千行泪，非是为思人。\\n九龙蟠焰动，四照逐花生。\\n即此流高殿，堪持待月明。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4711', 'poem', '唐', '汪遵', '咏北海', '汉臣曾此作缧囚，茹血衣毛十九秋。\\n鹤发半垂龙节在，不闻青史说封侯。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4712', 'poem', '唐', '张籍', '寻仙', '溪头一径入青崖，处处仙居隔杏花。\\n更见峰西幽客说，云中犹有两三家。', '2018-03-09 15:49:18');
INSERT INTO `t_poem` VALUES ('4713', 'poem', '唐', '武则天', '唐明堂乐章·登歌', '礼崇宗祀，志表严禋。\\n笙镛合奏，文物惟新。\\n敬遵茂典，敢择良辰。\\n絜诚斯著，奠谒方申。', '2018-03-09 15:49:28');
INSERT INTO `t_poem` VALUES ('4714', 'poem', '唐', '李义府', '咏乌', '日里飏朝彩，琴中伴夜啼。\\n上林如许树，不借一枝栖。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4715', 'poem', '唐', '杜甫', '春日江村五首', '农务村村急，春流岸岸深。\\n乾坤万里眼，时序百年心。\\n茅屋还堪赋，桃源自可寻。\\n艰难贱生理，飘泊到如今。\\n迢递来三蜀，蹉跎有六年。\\n客身逢故旧，发兴自林泉。\\n过懒从衣结，频游任履穿。\\n藩篱无限景，恣意买江天。\\n种竹交加翠，栽桃烂漫红。\\n经心石镜月，到面雪山风。\\n赤管随王命，银章付老翁。\\n岂知牙齿落，名玷荐贤中。\\n扶病垂朱绂，归休步紫苔。\\n郊扉存晚计，幕府愧群材。\\n燕外晴丝卷，鸥边水叶开。\\n邻家送鱼鳖，问我数能来。\\n群盗哀王粲，中年召贾生。\\n登楼初有作，前席竟为荣。\\n宅入先贤传，才高处士名。\\n异时怀二子，春日复含情。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4716', 'poem', '唐', '储光羲', '采莲词', '浅渚荇花繁，深潭菱叶疏。\\n独往方自得，耻邀淇上姝。\\n广江无术阡，大泽绝方隅。\\n浪中海童语，流下鲛人居。\\n春雁时隐舟，新萍复满湖。\\n采采乘日暮，不思贤与愚。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4717', 'poem', '唐', '李群玉', '桑落洲', '九江寒露夕，微浪北风生。\\n浦屿渔人火，蒹葭凫雁声。\\n颓云晦庐岳，微鼓辨湓城。\\n远忆天边弟，曾从此路行。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4718', 'poem', '唐', '罗隐', '牡丹', '艳多烟重欲开难，红蕊当心一抹檀。\\n公子醉归灯下见，美人朝插镜中看。\\n当庭始觉春风贵，带雨方知国色寒。\\n日晚更将何所似，太真无力凭阑干。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4719', 'poem', '唐', '顾况', '别江南', '江城吹晓角，愁杀远行人。\\n汉将犹防虏，吴官欲向秦。\\n布帆轻白浪，锦带入红尘。\\n将底求名宦，平生但任真。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4720', 'poem', '唐', '白居易', '庾顺之以紫霞绮远赠，以诗答之', '千里故人心郑重，一端香绮紫氛氲。\\n开缄日映晚霞色，满幅风生秋水纹。\\n为褥欲裁怜叶破，制裘将翦惜花分。\\n不如缝作合欢被，寤寐相思如对君。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4721', 'poem', '唐', '张蠙', '新竹', '新鞭暗入庭，初长两三茎。\\n不是他山少，无如此地生。\\n垂梢丛上出，柔叶箨间成。\\n何用高唐峡，风枝扫月明。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4722', 'poem', '唐', '佚名', '杂曲歌辞·采桑', '自古多征战，由来尚甲兵。\\n长驱千里去，一举两蕃平。\\n按剑从沙漠，歌谣满帝京。\\n寄言天下将，须立武功名。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4723', 'poem', '唐', '王勃', '重别薛华', '明月沉珠浦，秋风濯锦川。\\n楼台临绝岸，洲渚亘长天。\\n旅泊成千里，栖遑共百年。\\n穷途唯有泪，还望独潸然。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4724', 'poem', '唐', '皇甫冉', '秋日东郊作', '闲看秋水心无事，卧对寒松手自栽。\\n庐岳高僧留偈别，茅山道士寄书来。\\n燕知社日辞巢去，菊为重阳冒雨开。\\n浅薄将何称献纳，临岐终日自迟回。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4725', 'poem', '唐', '郑锡', '出塞', '关山落叶秋，掩泪望营州。\\n辽海云沙暮，幽燕旌旆愁。\\n战馀能送阵，身老未封侯。\\n去国三千里，归心红粉楼。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4726', 'poem', '唐', '李白', '赠瑕丘王少府', '皎皎鸾凤姿，飘飘神仙气。\\n梅生亦何事，来作南昌尉。\\n清风佐鸣琴，寂寞道为贵。\\n一见过所闻，操持难与群。\\n毫挥鲁邑讼，目送瀛洲云。\\n我隐屠钓下，尔当玉石分。\\n无由接高论，空此仰清芬。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4727', 'poem', '唐', '杜牧', '怀钟陵旧游四首', '一谒征南最少年，虞卿双璧截肪鲜。\\n歌谣千里春长暖，丝管高台月正圆。\\n玉帐军筹罗俊彦，绛帷环珮立神仙。\\n陆公馀德机云在，如我酬恩合执鞭。\\n滕阁中春绮席开，柘枝蛮鼓殷晴雷。\\n垂楼万幕青云合，破浪千帆阵马来。\\n未掘双龙牛斗气，高悬一榻栋梁材。\\n连巴控越知何事，珠翠沉檀处处堆。\\n十顷平湖堤柳合，岸秋兰芷绿纤纤。\\n一声明月采莲女，四面朱楼卷画帘。\\n白鹭烟分光的的，微涟风定翠湉湉。\\n斜辉更落西山影，千步虹桥气象兼。\\n控压平江十万家，秋来江静镜新磨。\\n城头晚鼓雷霆后，桥上游人笑语多。\\n日落汀痕千里色，月当楼午一声歌。\\n昔年行乐秾桃畔，醉与龙沙拣蜀罗。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4728', 'poem', '唐', '杜甫', '远怀舍弟颖、观等', '阳翟空知处，荆南近得书。\\n积年仍远别，多难不安居。\\n江汉春风起，冰霜昨夜除。\\n云天犹错莫，花萼尚萧疏。\\n对酒都疑梦，吟诗正忆渠。\\n旧时元日会，乡党羡吾庐。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4729', 'poem', '唐', '白居易', '立秋夕有怀梦得', '露簟荻竹清，风扇蒲葵轻。\\n一与故人别，再见新蝉鸣。\\n是夕凉飚起，闲境入幽情。\\n回灯见栖鹤，隔竹闻吹笙。\\n夜茶一两杓，秋吟三数声。\\n所思渺千里，云外长洲城。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4730', 'poem', '唐', '高适', '使青夷军入居庸三首', '匹马行将久，征途去转难。\\n不知边地别，只讶客衣单。\\n溪冷泉声苦，山空木叶干。\\n莫言关塞极，云雪尚漫漫。\\n古镇青山口，寒风落日时。\\n岩峦鸟不过，冰雪马堪迟。\\n出塞应无策，还家赖有期。\\n东山足松桂，归去结茅茨。\\n登顿驱征骑，栖迟愧宝刀。\\n远行今若此，微禄果徒劳。\\n绝坂水连下，群峰云共高。\\n自堪成白首，何事一青袍。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4731', 'poem', '唐', '白居易', '问友', '种兰不种艾，兰生艾亦生。\\n根荄相交长，茎叶相附荣。\\n香茎与臭叶，日夜俱长大。\\n锄艾恐伤兰，溉兰恐滋艾。\\n兰亦未能溉，艾亦未能除。\\n沉吟意不决，问君合何如。', '2018-03-09 15:49:29');
INSERT INTO `t_poem` VALUES ('4732', 'poem', '唐', '杜甫', '木皮岭', '首路栗亭西，尚想凤凰村。\\n季冬携童稚，辛苦赴蜀门。\\n南登木皮岭，艰险不易论。\\n汗流被我体，祁寒为之暄。\\n远岫争辅佐，千岩自崩奔。\\n始知五岳外，别有他山尊。\\n仰干塞大明，俯入裂厚坤。\\n再闻虎豹斗，屡跼风水昏。\\n高有废阁道，摧折如短辕。\\n下有冬青林，石上走长根。\\n西崖特秀发，焕若灵芝繁。\\n润聚金碧气，清无沙土痕。\\n忆观昆仑图，目击悬圃存。\\n对此欲何适，默伤垂老魂。', '2018-03-09 15:49:29');
